INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'بسم الله الرحمن الرحيم'),
(2, 1, 2, 'الحمد لله رب العالمين'),
(3, 1, 3, 'الرحمن الرحيم'),
(4, 1, 4, 'مالك يوم الدين'),
(5, 1, 5, 'إياك نعبد وإياك نستعين'),
(6, 1, 6, 'اهدنا الصراط المستقيم'),
(7, 1, 7, 'صراط الذين أنعمت عليهم غير المغضوب عليهم ولا الضالين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'بسم الله الرحمن الرحيم الم'),
(9, 2, 2, 'ذلك الكتاب لا ريب ۛ فيه ۛ هدى للمتقين'),
(10, 2, 3, 'الذين يؤمنون بالغيب ويقيمون الصلاة ومما رزقناهم ينفقون'),
(11, 2, 4, 'والذين يؤمنون بما أنزل إليك وما أنزل من قبلك وبالآخرة هم يوقنون'),
(12, 2, 5, 'أولئك على هدى من ربهم ۖ وأولئك هم المفلحون'),
(13, 2, 6, 'إن الذين كفروا سواء عليهم أأنذرتهم أم لم تنذرهم لا يؤمنون'),
(14, 2, 7, 'ختم الله على قلوبهم وعلى سمعهم ۖ وعلى أبصارهم غشاوة ۖ ولهم عذاب عظيم'),
(15, 2, 8, 'ومن الناس من يقول آمنا بالله وباليوم الآخر وما هم بمؤمنين'),
(16, 2, 9, 'يخادعون الله والذين آمنوا وما يخدعون إلا أنفسهم وما يشعرون'),
(17, 2, 10, 'في قلوبهم مرض فزادهم الله مرضا ۖ ولهم عذاب أليم بما كانوا يكذبون'),
(18, 2, 11, 'وإذا قيل لهم لا تفسدوا في الأرض قالوا إنما نحن مصلحون'),
(19, 2, 12, 'ألا إنهم هم المفسدون ولكن لا يشعرون'),
(20, 2, 13, 'وإذا قيل لهم آمنوا كما آمن الناس قالوا أنؤمن كما آمن السفهاء ۗ ألا إنهم هم السفهاء ولكن لا يعلمون'),
(21, 2, 14, 'وإذا لقوا الذين آمنوا قالوا آمنا وإذا خلوا إلى شياطينهم قالوا إنا معكم إنما نحن مستهزئون'),
(22, 2, 15, 'الله يستهزئ بهم ويمدهم في طغيانهم يعمهون'),
(23, 2, 16, 'أولئك الذين اشتروا الضلالة بالهدى فما ربحت تجارتهم وما كانوا مهتدين'),
(24, 2, 17, 'مثلهم كمثل الذي استوقد نارا فلما أضاءت ما حوله ذهب الله بنورهم وتركهم في ظلمات لا يبصرون'),
(25, 2, 18, 'صم بكم عمي فهم لا يرجعون'),
(26, 2, 19, 'أو كصيب من السماء فيه ظلمات ورعد وبرق يجعلون أصابعهم في آذانهم من الصواعق حذر الموت ۚ والله محيط بالكافرين'),
(27, 2, 20, 'يكاد البرق يخطف أبصارهم ۖ كلما أضاء لهم مشوا فيه وإذا أظلم عليهم قاموا ۚ ولو شاء الله لذهب بسمعهم وأبصارهم ۚ إن الله على كل شيء قدير'),
(28, 2, 21, 'يا أيها الناس اعبدوا ربكم الذي خلقكم والذين من قبلكم لعلكم تتقون'),
(29, 2, 22, 'الذي جعل لكم الأرض فراشا والسماء بناء وأنزل من السماء ماء فأخرج به من الثمرات رزقا لكم ۖ فلا تجعلوا لله أندادا وأنتم تعلمون'),
(30, 2, 23, 'وإن كنتم في ريب مما نزلنا على عبدنا فأتوا بسورة من مثله وادعوا شهداءكم من دون الله إن كنتم صادقين'),
(31, 2, 24, 'فإن لم تفعلوا ولن تفعلوا فاتقوا النار التي وقودها الناس والحجارة ۖ أعدت للكافرين'),
(32, 2, 25, 'وبشر الذين آمنوا وعملوا الصالحات أن لهم جنات تجري من تحتها الأنهار ۖ كلما رزقوا منها من ثمرة رزقا ۙ قالوا هذا الذي رزقنا من قبل ۖ وأتوا به متشابها ۖ ولهم فيها أزواج مطهرة ۖ وهم فيها خالدون'),
(33, 2, 26, 'إن الله لا يستحيي أن يضرب مثلا ما بعوضة فما فوقها ۚ فأما الذين آمنوا فيعلمون أنه الحق من ربهم ۖ وأما الذين كفروا فيقولون ماذا أراد الله بهذا مثلا ۘ يضل به كثيرا ويهدي به كثيرا ۚ وما يضل به إلا الفاسقين'),
(34, 2, 27, 'الذين ينقضون عهد الله من بعد ميثاقه ويقطعون ما أمر الله به أن يوصل ويفسدون في الأرض ۚ أولئك هم الخاسرون'),
(35, 2, 28, 'كيف تكفرون بالله وكنتم أمواتا فأحياكم ۖ ثم يميتكم ثم يحييكم ثم إليه ترجعون'),
(36, 2, 29, 'هو الذي خلق لكم ما في الأرض جميعا ثم استوى إلى السماء فسواهن سبع سماوات ۚ وهو بكل شيء عليم'),
(37, 2, 30, 'وإذ قال ربك للملائكة إني جاعل في الأرض خليفة ۖ قالوا أتجعل فيها من يفسد فيها ويسفك الدماء ونحن نسبح بحمدك ونقدس لك ۖ قال إني أعلم ما لا تعلمون'),
(38, 2, 31, 'وعلم آدم الأسماء كلها ثم عرضهم على الملائكة فقال أنبئوني بأسماء هؤلاء إن كنتم صادقين'),
(39, 2, 32, 'قالوا سبحانك لا علم لنا إلا ما علمتنا ۖ إنك أنت العليم الحكيم'),
(40, 2, 33, 'قال يا آدم أنبئهم بأسمائهم ۖ فلما أنبأهم بأسمائهم قال ألم أقل لكم إني أعلم غيب السماوات والأرض وأعلم ما تبدون وما كنتم تكتمون'),
(41, 2, 34, 'وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس أبى واستكبر وكان من الكافرين'),
(42, 2, 35, 'وقلنا يا آدم اسكن أنت وزوجك الجنة وكلا منها رغدا حيث شئتما ولا تقربا هذه الشجرة فتكونا من الظالمين'),
(43, 2, 36, 'فأزلهما الشيطان عنها فأخرجهما مما كانا فيه ۖ وقلنا اهبطوا بعضكم لبعض عدو ۖ ولكم في الأرض مستقر ومتاع إلى حين'),
(44, 2, 37, 'فتلقى آدم من ربه كلمات فتاب عليه ۚ إنه هو التواب الرحيم'),
(45, 2, 38, 'قلنا اهبطوا منها جميعا ۖ فإما يأتينكم مني هدى فمن تبع هداي فلا خوف عليهم ولا هم يحزنون'),
(46, 2, 39, 'والذين كفروا وكذبوا بآياتنا أولئك أصحاب النار ۖ هم فيها خالدون'),
(47, 2, 40, 'يا بني إسرائيل اذكروا نعمتي التي أنعمت عليكم وأوفوا بعهدي أوف بعهدكم وإياي فارهبون'),
(48, 2, 41, 'وآمنوا بما أنزلت مصدقا لما معكم ولا تكونوا أول كافر به ۖ ولا تشتروا بآياتي ثمنا قليلا وإياي فاتقون'),
(49, 2, 42, 'ولا تلبسوا الحق بالباطل وتكتموا الحق وأنتم تعلمون'),
(50, 2, 43, 'وأقيموا الصلاة وآتوا الزكاة واركعوا مع الراكعين'),
(51, 2, 44, 'أتأمرون الناس بالبر وتنسون أنفسكم وأنتم تتلون الكتاب ۚ أفلا تعقلون'),
(52, 2, 45, 'واستعينوا بالصبر والصلاة ۚ وإنها لكبيرة إلا على الخاشعين'),
(53, 2, 46, 'الذين يظنون أنهم ملاقو ربهم وأنهم إليه راجعون'),
(54, 2, 47, 'يا بني إسرائيل اذكروا نعمتي التي أنعمت عليكم وأني فضلتكم على العالمين'),
(55, 2, 48, 'واتقوا يوما لا تجزي نفس عن نفس شيئا ولا يقبل منها شفاعة ولا يؤخذ منها عدل ولا هم ينصرون'),
(56, 2, 49, 'وإذ نجيناكم من آل فرعون يسومونكم سوء العذاب يذبحون أبناءكم ويستحيون نساءكم ۚ وفي ذلكم بلاء من ربكم عظيم'),
(57, 2, 50, 'وإذ فرقنا بكم البحر فأنجيناكم وأغرقنا آل فرعون وأنتم تنظرون'),
(58, 2, 51, 'وإذ واعدنا موسى أربعين ليلة ثم اتخذتم العجل من بعده وأنتم ظالمون'),
(59, 2, 52, 'ثم عفونا عنكم من بعد ذلك لعلكم تشكرون'),
(60, 2, 53, 'وإذ آتينا موسى الكتاب والفرقان لعلكم تهتدون'),
(61, 2, 54, 'وإذ قال موسى لقومه يا قوم إنكم ظلمتم أنفسكم باتخاذكم العجل فتوبوا إلى بارئكم فاقتلوا أنفسكم ذلكم خير لكم عند بارئكم فتاب عليكم ۚ إنه هو التواب الرحيم'),
(62, 2, 55, 'وإذ قلتم يا موسى لن نؤمن لك حتى نرى الله جهرة فأخذتكم الصاعقة وأنتم تنظرون'),
(63, 2, 56, 'ثم بعثناكم من بعد موتكم لعلكم تشكرون'),
(64, 2, 57, 'وظللنا عليكم الغمام وأنزلنا عليكم المن والسلوى ۖ كلوا من طيبات ما رزقناكم ۖ وما ظلمونا ولكن كانوا أنفسهم يظلمون'),
(65, 2, 58, 'وإذ قلنا ادخلوا هذه القرية فكلوا منها حيث شئتم رغدا وادخلوا الباب سجدا وقولوا حطة نغفر لكم خطاياكم ۚ وسنزيد المحسنين'),
(66, 2, 59, 'فبدل الذين ظلموا قولا غير الذي قيل لهم فأنزلنا على الذين ظلموا رجزا من السماء بما كانوا يفسقون'),
(67, 2, 60, 'وإذ استسقى موسى لقومه فقلنا اضرب بعصاك الحجر ۖ فانفجرت منه اثنتا عشرة عينا ۖ قد علم كل أناس مشربهم ۖ كلوا واشربوا من رزق الله ولا تعثوا في الأرض مفسدين'),
(68, 2, 61, 'وإذ قلتم يا موسى لن نصبر على طعام واحد فادع لنا ربك يخرج لنا مما تنبت الأرض من بقلها وقثائها وفومها وعدسها وبصلها ۖ قال أتستبدلون الذي هو أدنى بالذي هو خير ۚ اهبطوا مصرا فإن لكم ما سألتم ۗ وضربت عليهم الذلة والمسكنة وباءوا بغضب من الله ۗ ذلك بأنهم كانوا يكفرون بآيات الله ويقتلون النبيين بغير الحق ۗ ذلك بما عصوا وكانوا يعتدون'),
(69, 2, 62, 'إن الذين آمنوا والذين هادوا والنصارى والصابئين من آمن بالله واليوم الآخر وعمل صالحا فلهم أجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون'),
(70, 2, 63, 'وإذ أخذنا ميثاقكم ورفعنا فوقكم الطور خذوا ما آتيناكم بقوة واذكروا ما فيه لعلكم تتقون'),
(71, 2, 64, 'ثم توليتم من بعد ذلك ۖ فلولا فضل الله عليكم ورحمته لكنتم من الخاسرين'),
(72, 2, 65, 'ولقد علمتم الذين اعتدوا منكم في السبت فقلنا لهم كونوا قردة خاسئين'),
(73, 2, 66, 'فجعلناها نكالا لما بين يديها وما خلفها وموعظة للمتقين'),
(74, 2, 67, 'وإذ قال موسى لقومه إن الله يأمركم أن تذبحوا بقرة ۖ قالوا أتتخذنا هزوا ۖ قال أعوذ بالله أن أكون من الجاهلين'),
(75, 2, 68, 'قالوا ادع لنا ربك يبين لنا ما هي ۚ قال إنه يقول إنها بقرة لا فارض ولا بكر عوان بين ذلك ۖ فافعلوا ما تؤمرون'),
(76, 2, 69, 'قالوا ادع لنا ربك يبين لنا ما لونها ۚ قال إنه يقول إنها بقرة صفراء فاقع لونها تسر الناظرين'),
(77, 2, 70, 'قالوا ادع لنا ربك يبين لنا ما هي إن البقر تشابه علينا وإنا إن شاء الله لمهتدون'),
(78, 2, 71, 'قال إنه يقول إنها بقرة لا ذلول تثير الأرض ولا تسقي الحرث مسلمة لا شية فيها ۚ قالوا الآن جئت بالحق ۚ فذبحوها وما كادوا يفعلون'),
(79, 2, 72, 'وإذ قتلتم نفسا فادارأتم فيها ۖ والله مخرج ما كنتم تكتمون'),
(80, 2, 73, 'فقلنا اضربوه ببعضها ۚ كذلك يحيي الله الموتى ويريكم آياته لعلكم تعقلون'),
(81, 2, 74, 'ثم قست قلوبكم من بعد ذلك فهي كالحجارة أو أشد قسوة ۚ وإن من الحجارة لما يتفجر منه الأنهار ۚ وإن منها لما يشقق فيخرج منه الماء ۚ وإن منها لما يهبط من خشية الله ۗ وما الله بغافل عما تعملون'),
(82, 2, 75, 'أفتطمعون أن يؤمنوا لكم وقد كان فريق منهم يسمعون كلام الله ثم يحرفونه من بعد ما عقلوه وهم يعلمون'),
(83, 2, 76, 'وإذا لقوا الذين آمنوا قالوا آمنا وإذا خلا بعضهم إلى بعض قالوا أتحدثونهم بما فتح الله عليكم ليحاجوكم به عند ربكم ۚ أفلا تعقلون'),
(84, 2, 77, 'أولا يعلمون أن الله يعلم ما يسرون وما يعلنون'),
(85, 2, 78, 'ومنهم أميون لا يعلمون الكتاب إلا أماني وإن هم إلا يظنون'),
(86, 2, 79, 'فويل للذين يكتبون الكتاب بأيديهم ثم يقولون هذا من عند الله ليشتروا به ثمنا قليلا ۖ فويل لهم مما كتبت أيديهم وويل لهم مما يكسبون'),
(87, 2, 80, 'وقالوا لن تمسنا النار إلا أياما معدودة ۚ قل أتخذتم عند الله عهدا فلن يخلف الله عهده ۖ أم تقولون على الله ما لا تعلمون'),
(88, 2, 81, 'بلى من كسب سيئة وأحاطت به خطيئته فأولئك أصحاب النار ۖ هم فيها خالدون'),
(89, 2, 82, 'والذين آمنوا وعملوا الصالحات أولئك أصحاب الجنة ۖ هم فيها خالدون'),
(90, 2, 83, 'وإذ أخذنا ميثاق بني إسرائيل لا تعبدون إلا الله وبالوالدين إحسانا وذي القربى واليتامى والمساكين وقولوا للناس حسنا وأقيموا الصلاة وآتوا الزكاة ثم توليتم إلا قليلا منكم وأنتم معرضون'),
(91, 2, 84, 'وإذ أخذنا ميثاقكم لا تسفكون دماءكم ولا تخرجون أنفسكم من دياركم ثم أقررتم وأنتم تشهدون'),
(92, 2, 85, 'ثم أنتم هؤلاء تقتلون أنفسكم وتخرجون فريقا منكم من ديارهم تظاهرون عليهم بالإثم والعدوان وإن يأتوكم أسارى تفادوهم وهو محرم عليكم إخراجهم ۚ أفتؤمنون ببعض الكتاب وتكفرون ببعض ۚ فما جزاء من يفعل ذلك منكم إلا خزي في الحياة الدنيا ۖ ويوم القيامة يردون إلى أشد العذاب ۗ وما الله بغافل عما تعملون'),
(93, 2, 86, 'أولئك الذين اشتروا الحياة الدنيا بالآخرة ۖ فلا يخفف عنهم العذاب ولا هم ينصرون'),
(94, 2, 87, 'ولقد آتينا موسى الكتاب وقفينا من بعده بالرسل ۖ وآتينا عيسى ابن مريم البينات وأيدناه بروح القدس ۗ أفكلما جاءكم رسول بما لا تهوى أنفسكم استكبرتم ففريقا كذبتم وفريقا تقتلون'),
(95, 2, 88, 'وقالوا قلوبنا غلف ۚ بل لعنهم الله بكفرهم فقليلا ما يؤمنون'),
(96, 2, 89, 'ولما جاءهم كتاب من عند الله مصدق لما معهم وكانوا من قبل يستفتحون على الذين كفروا فلما جاءهم ما عرفوا كفروا به ۚ فلعنة الله على الكافرين'),
(97, 2, 90, 'بئسما اشتروا به أنفسهم أن يكفروا بما أنزل الله بغيا أن ينزل الله من فضله على من يشاء من عباده ۖ فباءوا بغضب على غضب ۚ وللكافرين عذاب مهين'),
(98, 2, 91, 'وإذا قيل لهم آمنوا بما أنزل الله قالوا نؤمن بما أنزل علينا ويكفرون بما وراءه وهو الحق مصدقا لما معهم ۗ قل فلم تقتلون أنبياء الله من قبل إن كنتم مؤمنين'),
(99, 2, 92, 'ولقد جاءكم موسى بالبينات ثم اتخذتم العجل من بعده وأنتم ظالمون'),
(100, 2, 93, 'وإذ أخذنا ميثاقكم ورفعنا فوقكم الطور خذوا ما آتيناكم بقوة واسمعوا ۖ قالوا سمعنا وعصينا وأشربوا في قلوبهم العجل بكفرهم ۚ قل بئسما يأمركم به إيمانكم إن كنتم مؤمنين'),
(101, 2, 94, 'قل إن كانت لكم الدار الآخرة عند الله خالصة من دون الناس فتمنوا الموت إن كنتم صادقين'),
(102, 2, 95, 'ولن يتمنوه أبدا بما قدمت أيديهم ۗ والله عليم بالظالمين'),
(103, 2, 96, 'ولتجدنهم أحرص الناس على حياة ومن الذين أشركوا ۚ يود أحدهم لو يعمر ألف سنة وما هو بمزحزحه من العذاب أن يعمر ۗ والله بصير بما يعملون'),
(104, 2, 97, 'قل من كان عدوا لجبريل فإنه نزله على قلبك بإذن الله مصدقا لما بين يديه وهدى وبشرى للمؤمنين'),
(105, 2, 98, 'من كان عدوا لله وملائكته ورسله وجبريل وميكال فإن الله عدو للكافرين'),
(106, 2, 99, 'ولقد أنزلنا إليك آيات بينات ۖ وما يكفر بها إلا الفاسقون'),
(107, 2, 100, 'أوكلما عاهدوا عهدا نبذه فريق منهم ۚ بل أكثرهم لا يؤمنون'),
(108, 2, 101, 'ولما جاءهم رسول من عند الله مصدق لما معهم نبذ فريق من الذين أوتوا الكتاب كتاب الله وراء ظهورهم كأنهم لا يعلمون'),
(109, 2, 102, 'واتبعوا ما تتلو الشياطين على ملك سليمان ۖ وما كفر سليمان ولكن الشياطين كفروا يعلمون الناس السحر وما أنزل على الملكين ببابل هاروت وماروت ۚ وما يعلمان من أحد حتى يقولا إنما نحن فتنة فلا تكفر ۖ فيتعلمون منهما ما يفرقون به بين المرء وزوجه ۚ وما هم بضارين به من أحد إلا بإذن الله ۚ ويتعلمون ما يضرهم ولا ينفعهم ۚ ولقد علموا لمن اشتراه ما له في الآخرة من خلاق ۚ ولبئس ما شروا به أنفسهم ۚ لو كانوا يعلمون'),
(110, 2, 103, 'ولو أنهم آمنوا واتقوا لمثوبة من عند الله خير ۖ لو كانوا يعلمون'),
(111, 2, 104, 'يا أيها الذين آمنوا لا تقولوا راعنا وقولوا انظرنا واسمعوا ۗ وللكافرين عذاب أليم'),
(112, 2, 105, 'ما يود الذين كفروا من أهل الكتاب ولا المشركين أن ينزل عليكم من خير من ربكم ۗ والله يختص برحمته من يشاء ۚ والله ذو الفضل العظيم'),
(113, 2, 106, 'ما ننسخ من آية أو ننسها نأت بخير منها أو مثلها ۗ ألم تعلم أن الله على كل شيء قدير'),
(114, 2, 107, 'ألم تعلم أن الله له ملك السماوات والأرض ۗ وما لكم من دون الله من ولي ولا نصير'),
(115, 2, 108, 'أم تريدون أن تسألوا رسولكم كما سئل موسى من قبل ۗ ومن يتبدل الكفر بالإيمان فقد ضل سواء السبيل'),
(116, 2, 109, 'ود كثير من أهل الكتاب لو يردونكم من بعد إيمانكم كفارا حسدا من عند أنفسهم من بعد ما تبين لهم الحق ۖ فاعفوا واصفحوا حتى يأتي الله بأمره ۗ إن الله على كل شيء قدير'),
(117, 2, 110, 'وأقيموا الصلاة وآتوا الزكاة ۚ وما تقدموا لأنفسكم من خير تجدوه عند الله ۗ إن الله بما تعملون بصير'),
(118, 2, 111, 'وقالوا لن يدخل الجنة إلا من كان هودا أو نصارى ۗ تلك أمانيهم ۗ قل هاتوا برهانكم إن كنتم صادقين'),
(119, 2, 112, 'بلى من أسلم وجهه لله وهو محسن فله أجره عند ربه ولا خوف عليهم ولا هم يحزنون'),
(120, 2, 113, 'وقالت اليهود ليست النصارى على شيء وقالت النصارى ليست اليهود على شيء وهم يتلون الكتاب ۗ كذلك قال الذين لا يعلمون مثل قولهم ۚ فالله يحكم بينهم يوم القيامة فيما كانوا فيه يختلفون'),
(121, 2, 114, 'ومن أظلم ممن منع مساجد الله أن يذكر فيها اسمه وسعى في خرابها ۚ أولئك ما كان لهم أن يدخلوها إلا خائفين ۚ لهم في الدنيا خزي ولهم في الآخرة عذاب عظيم'),
(122, 2, 115, 'ولله المشرق والمغرب ۚ فأينما تولوا فثم وجه الله ۚ إن الله واسع عليم'),
(123, 2, 116, 'وقالوا اتخذ الله ولدا ۗ سبحانه ۖ بل له ما في السماوات والأرض ۖ كل له قانتون'),
(124, 2, 117, 'بديع السماوات والأرض ۖ وإذا قضى أمرا فإنما يقول له كن فيكون'),
(125, 2, 118, 'وقال الذين لا يعلمون لولا يكلمنا الله أو تأتينا آية ۗ كذلك قال الذين من قبلهم مثل قولهم ۘ تشابهت قلوبهم ۗ قد بينا الآيات لقوم يوقنون'),
(126, 2, 119, 'إنا أرسلناك بالحق بشيرا ونذيرا ۖ ولا تسأل عن أصحاب الجحيم'),
(127, 2, 120, 'ولن ترضى عنك اليهود ولا النصارى حتى تتبع ملتهم ۗ قل إن هدى الله هو الهدى ۗ ولئن اتبعت أهواءهم بعد الذي جاءك من العلم ۙ ما لك من الله من ولي ولا نصير'),
(128, 2, 121, 'الذين آتيناهم الكتاب يتلونه حق تلاوته أولئك يؤمنون به ۗ ومن يكفر به فأولئك هم الخاسرون'),
(129, 2, 122, 'يا بني إسرائيل اذكروا نعمتي التي أنعمت عليكم وأني فضلتكم على العالمين'),
(130, 2, 123, 'واتقوا يوما لا تجزي نفس عن نفس شيئا ولا يقبل منها عدل ولا تنفعها شفاعة ولا هم ينصرون'),
(131, 2, 124, 'وإذ ابتلى إبراهيم ربه بكلمات فأتمهن ۖ قال إني جاعلك للناس إماما ۖ قال ومن ذريتي ۖ قال لا ينال عهدي الظالمين'),
(132, 2, 125, 'وإذ جعلنا البيت مثابة للناس وأمنا واتخذوا من مقام إبراهيم مصلى ۖ وعهدنا إلى إبراهيم وإسماعيل أن طهرا بيتي للطائفين والعاكفين والركع السجود'),
(133, 2, 126, 'وإذ قال إبراهيم رب اجعل هذا بلدا آمنا وارزق أهله من الثمرات من آمن منهم بالله واليوم الآخر ۖ قال ومن كفر فأمتعه قليلا ثم أضطره إلى عذاب النار ۖ وبئس المصير'),
(134, 2, 127, 'وإذ يرفع إبراهيم القواعد من البيت وإسماعيل ربنا تقبل منا ۖ إنك أنت السميع العليم'),
(135, 2, 128, 'ربنا واجعلنا مسلمين لك ومن ذريتنا أمة مسلمة لك وأرنا مناسكنا وتب علينا ۖ إنك أنت التواب الرحيم'),
(136, 2, 129, 'ربنا وابعث فيهم رسولا منهم يتلو عليهم آياتك ويعلمهم الكتاب والحكمة ويزكيهم ۚ إنك أنت العزيز الحكيم'),
(137, 2, 130, 'ومن يرغب عن ملة إبراهيم إلا من سفه نفسه ۚ ولقد اصطفيناه في الدنيا ۖ وإنه في الآخرة لمن الصالحين'),
(138, 2, 131, 'إذ قال له ربه أسلم ۖ قال أسلمت لرب العالمين'),
(139, 2, 132, 'ووصى بها إبراهيم بنيه ويعقوب يا بني إن الله اصطفى لكم الدين فلا تموتن إلا وأنتم مسلمون'),
(140, 2, 133, 'أم كنتم شهداء إذ حضر يعقوب الموت إذ قال لبنيه ما تعبدون من بعدي قالوا نعبد إلهك وإله آبائك إبراهيم وإسماعيل وإسحاق إلها واحدا ونحن له مسلمون'),
(141, 2, 134, 'تلك أمة قد خلت ۖ لها ما كسبت ولكم ما كسبتم ۖ ولا تسألون عما كانوا يعملون'),
(142, 2, 135, 'وقالوا كونوا هودا أو نصارى تهتدوا ۗ قل بل ملة إبراهيم حنيفا ۖ وما كان من المشركين'),
(143, 2, 136, 'قولوا آمنا بالله وما أنزل إلينا وما أنزل إلى إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط وما أوتي موسى وعيسى وما أوتي النبيون من ربهم لا نفرق بين أحد منهم ونحن له مسلمون'),
(144, 2, 137, 'فإن آمنوا بمثل ما آمنتم به فقد اهتدوا ۖ وإن تولوا فإنما هم في شقاق ۖ فسيكفيكهم الله ۚ وهو السميع العليم'),
(145, 2, 138, 'صبغة الله ۖ ومن أحسن من الله صبغة ۖ ونحن له عابدون'),
(146, 2, 139, 'قل أتحاجوننا في الله وهو ربنا وربكم ولنا أعمالنا ولكم أعمالكم ونحن له مخلصون'),
(147, 2, 140, 'أم تقولون إن إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط كانوا هودا أو نصارى ۗ قل أأنتم أعلم أم الله ۗ ومن أظلم ممن كتم شهادة عنده من الله ۗ وما الله بغافل عما تعملون'),
(148, 2, 141, 'تلك أمة قد خلت ۖ لها ما كسبت ولكم ما كسبتم ۖ ولا تسألون عما كانوا يعملون'),
(149, 2, 142, 'سيقول السفهاء من الناس ما ولاهم عن قبلتهم التي كانوا عليها ۚ قل لله المشرق والمغرب ۚ يهدي من يشاء إلى صراط مستقيم'),
(150, 2, 143, 'وكذلك جعلناكم أمة وسطا لتكونوا شهداء على الناس ويكون الرسول عليكم شهيدا ۗ وما جعلنا القبلة التي كنت عليها إلا لنعلم من يتبع الرسول ممن ينقلب على عقبيه ۚ وإن كانت لكبيرة إلا على الذين هدى الله ۗ وما كان الله ليضيع إيمانكم ۚ إن الله بالناس لرءوف رحيم'),
(151, 2, 144, 'قد نرى تقلب وجهك في السماء ۖ فلنولينك قبلة ترضاها ۚ فول وجهك شطر المسجد الحرام ۚ وحيث ما كنتم فولوا وجوهكم شطره ۗ وإن الذين أوتوا الكتاب ليعلمون أنه الحق من ربهم ۗ وما الله بغافل عما يعملون'),
(152, 2, 145, 'ولئن أتيت الذين أوتوا الكتاب بكل آية ما تبعوا قبلتك ۚ وما أنت بتابع قبلتهم ۚ وما بعضهم بتابع قبلة بعض ۚ ولئن اتبعت أهواءهم من بعد ما جاءك من العلم ۙ إنك إذا لمن الظالمين'),
(153, 2, 146, 'الذين آتيناهم الكتاب يعرفونه كما يعرفون أبناءهم ۖ وإن فريقا منهم ليكتمون الحق وهم يعلمون'),
(154, 2, 147, 'الحق من ربك ۖ فلا تكونن من الممترين'),
(155, 2, 148, 'ولكل وجهة هو موليها ۖ فاستبقوا الخيرات ۚ أين ما تكونوا يأت بكم الله جميعا ۚ إن الله على كل شيء قدير'),
(156, 2, 149, 'ومن حيث خرجت فول وجهك شطر المسجد الحرام ۖ وإنه للحق من ربك ۗ وما الله بغافل عما تعملون'),
(157, 2, 150, 'ومن حيث خرجت فول وجهك شطر المسجد الحرام ۚ وحيث ما كنتم فولوا وجوهكم شطره لئلا يكون للناس عليكم حجة إلا الذين ظلموا منهم فلا تخشوهم واخشوني ولأتم نعمتي عليكم ولعلكم تهتدون'),
(158, 2, 151, 'كما أرسلنا فيكم رسولا منكم يتلو عليكم آياتنا ويزكيكم ويعلمكم الكتاب والحكمة ويعلمكم ما لم تكونوا تعلمون'),
(159, 2, 152, 'فاذكروني أذكركم واشكروا لي ولا تكفرون'),
(160, 2, 153, 'يا أيها الذين آمنوا استعينوا بالصبر والصلاة ۚ إن الله مع الصابرين'),
(161, 2, 154, 'ولا تقولوا لمن يقتل في سبيل الله أموات ۚ بل أحياء ولكن لا تشعرون'),
(162, 2, 155, 'ولنبلونكم بشيء من الخوف والجوع ونقص من الأموال والأنفس والثمرات ۗ وبشر الصابرين'),
(163, 2, 156, 'الذين إذا أصابتهم مصيبة قالوا إنا لله وإنا إليه راجعون'),
(164, 2, 157, 'أولئك عليهم صلوات من ربهم ورحمة ۖ وأولئك هم المهتدون'),
(165, 2, 158, 'إن الصفا والمروة من شعائر الله ۖ فمن حج البيت أو اعتمر فلا جناح عليه أن يطوف بهما ۚ ومن تطوع خيرا فإن الله شاكر عليم'),
(166, 2, 159, 'إن الذين يكتمون ما أنزلنا من البينات والهدى من بعد ما بيناه للناس في الكتاب ۙ أولئك يلعنهم الله ويلعنهم اللاعنون'),
(167, 2, 160, 'إلا الذين تابوا وأصلحوا وبينوا فأولئك أتوب عليهم ۚ وأنا التواب الرحيم'),
(168, 2, 161, 'إن الذين كفروا وماتوا وهم كفار أولئك عليهم لعنة الله والملائكة والناس أجمعين'),
(169, 2, 162, 'خالدين فيها ۖ لا يخفف عنهم العذاب ولا هم ينظرون'),
(170, 2, 163, 'وإلهكم إله واحد ۖ لا إله إلا هو الرحمن الرحيم'),
(171, 2, 164, 'إن في خلق السماوات والأرض واختلاف الليل والنهار والفلك التي تجري في البحر بما ينفع الناس وما أنزل الله من السماء من ماء فأحيا به الأرض بعد موتها وبث فيها من كل دابة وتصريف الرياح والسحاب المسخر بين السماء والأرض لآيات لقوم يعقلون'),
(172, 2, 165, 'ومن الناس من يتخذ من دون الله أندادا يحبونهم كحب الله ۖ والذين آمنوا أشد حبا لله ۗ ولو يرى الذين ظلموا إذ يرون العذاب أن القوة لله جميعا وأن الله شديد العذاب'),
(173, 2, 166, 'إذ تبرأ الذين اتبعوا من الذين اتبعوا ورأوا العذاب وتقطعت بهم الأسباب'),
(174, 2, 167, 'وقال الذين اتبعوا لو أن لنا كرة فنتبرأ منهم كما تبرءوا منا ۗ كذلك يريهم الله أعمالهم حسرات عليهم ۖ وما هم بخارجين من النار'),
(175, 2, 168, 'يا أيها الناس كلوا مما في الأرض حلالا طيبا ولا تتبعوا خطوات الشيطان ۚ إنه لكم عدو مبين'),
(176, 2, 169, 'إنما يأمركم بالسوء والفحشاء وأن تقولوا على الله ما لا تعلمون'),
(177, 2, 170, 'وإذا قيل لهم اتبعوا ما أنزل الله قالوا بل نتبع ما ألفينا عليه آباءنا ۗ أولو كان آباؤهم لا يعقلون شيئا ولا يهتدون'),
(178, 2, 171, 'ومثل الذين كفروا كمثل الذي ينعق بما لا يسمع إلا دعاء ونداء ۚ صم بكم عمي فهم لا يعقلون'),
(179, 2, 172, 'يا أيها الذين آمنوا كلوا من طيبات ما رزقناكم واشكروا لله إن كنتم إياه تعبدون'),
(180, 2, 173, 'إنما حرم عليكم الميتة والدم ولحم الخنزير وما أهل به لغير الله ۖ فمن اضطر غير باغ ولا عاد فلا إثم عليه ۚ إن الله غفور رحيم'),
(181, 2, 174, 'إن الذين يكتمون ما أنزل الله من الكتاب ويشترون به ثمنا قليلا ۙ أولئك ما يأكلون في بطونهم إلا النار ولا يكلمهم الله يوم القيامة ولا يزكيهم ولهم عذاب أليم'),
(182, 2, 175, 'أولئك الذين اشتروا الضلالة بالهدى والعذاب بالمغفرة ۚ فما أصبرهم على النار'),
(183, 2, 176, 'ذلك بأن الله نزل الكتاب بالحق ۗ وإن الذين اختلفوا في الكتاب لفي شقاق بعيد'),
(184, 2, 177, 'ليس البر أن تولوا وجوهكم قبل المشرق والمغرب ولكن البر من آمن بالله واليوم الآخر والملائكة والكتاب والنبيين وآتى المال على حبه ذوي القربى واليتامى والمساكين وابن السبيل والسائلين وفي الرقاب وأقام الصلاة وآتى الزكاة والموفون بعهدهم إذا عاهدوا ۖ والصابرين في البأساء والضراء وحين البأس ۗ أولئك الذين صدقوا ۖ وأولئك هم المتقون'),
(185, 2, 178, 'يا أيها الذين آمنوا كتب عليكم القصاص في القتلى ۖ الحر بالحر والعبد بالعبد والأنثى بالأنثى ۚ فمن عفي له من أخيه شيء فاتباع بالمعروف وأداء إليه بإحسان ۗ ذلك تخفيف من ربكم ورحمة ۗ فمن اعتدى بعد ذلك فله عذاب أليم'),
(186, 2, 179, 'ولكم في القصاص حياة يا أولي الألباب لعلكم تتقون'),
(187, 2, 180, 'كتب عليكم إذا حضر أحدكم الموت إن ترك خيرا الوصية للوالدين والأقربين بالمعروف ۖ حقا على المتقين'),
(188, 2, 181, 'فمن بدله بعد ما سمعه فإنما إثمه على الذين يبدلونه ۚ إن الله سميع عليم'),
(189, 2, 182, 'فمن خاف من موص جنفا أو إثما فأصلح بينهم فلا إثم عليه ۚ إن الله غفور رحيم'),
(190, 2, 183, 'يا أيها الذين آمنوا كتب عليكم الصيام كما كتب على الذين من قبلكم لعلكم تتقون'),
(191, 2, 184, 'أياما معدودات ۚ فمن كان منكم مريضا أو على سفر فعدة من أيام أخر ۚ وعلى الذين يطيقونه فدية طعام مسكين ۖ فمن تطوع خيرا فهو خير له ۚ وأن تصوموا خير لكم ۖ إن كنتم تعلمون'),
(192, 2, 185, 'شهر رمضان الذي أنزل فيه القرآن هدى للناس وبينات من الهدى والفرقان ۚ فمن شهد منكم الشهر فليصمه ۖ ومن كان مريضا أو على سفر فعدة من أيام أخر ۗ يريد الله بكم اليسر ولا يريد بكم العسر ولتكملوا العدة ولتكبروا الله على ما هداكم ولعلكم تشكرون'),
(193, 2, 186, 'وإذا سألك عبادي عني فإني قريب ۖ أجيب دعوة الداع إذا دعان ۖ فليستجيبوا لي وليؤمنوا بي لعلهم يرشدون'),
(194, 2, 187, 'أحل لكم ليلة الصيام الرفث إلى نسائكم ۚ هن لباس لكم وأنتم لباس لهن ۗ علم الله أنكم كنتم تختانون أنفسكم فتاب عليكم وعفا عنكم ۖ فالآن باشروهن وابتغوا ما كتب الله لكم ۚ وكلوا واشربوا حتى يتبين لكم الخيط الأبيض من الخيط الأسود من الفجر ۖ ثم أتموا الصيام إلى الليل ۚ ولا تباشروهن وأنتم عاكفون في المساجد ۗ تلك حدود الله فلا تقربوها ۗ كذلك يبين الله آياته للناس لعلهم يتقون'),
(195, 2, 188, 'ولا تأكلوا أموالكم بينكم بالباطل وتدلوا بها إلى الحكام لتأكلوا فريقا من أموال الناس بالإثم وأنتم تعلمون'),
(196, 2, 189, 'يسألونك عن الأهلة ۖ قل هي مواقيت للناس والحج ۗ وليس البر بأن تأتوا البيوت من ظهورها ولكن البر من اتقى ۗ وأتوا البيوت من أبوابها ۚ واتقوا الله لعلكم تفلحون'),
(197, 2, 190, 'وقاتلوا في سبيل الله الذين يقاتلونكم ولا تعتدوا ۚ إن الله لا يحب المعتدين'),
(198, 2, 191, 'واقتلوهم حيث ثقفتموهم وأخرجوهم من حيث أخرجوكم ۚ والفتنة أشد من القتل ۚ ولا تقاتلوهم عند المسجد الحرام حتى يقاتلوكم فيه ۖ فإن قاتلوكم فاقتلوهم ۗ كذلك جزاء الكافرين'),
(199, 2, 192, 'فإن انتهوا فإن الله غفور رحيم'),
(200, 2, 193, 'وقاتلوهم حتى لا تكون فتنة ويكون الدين لله ۖ فإن انتهوا فلا عدوان إلا على الظالمين'),
(201, 2, 194, 'الشهر الحرام بالشهر الحرام والحرمات قصاص ۚ فمن اعتدى عليكم فاعتدوا عليه بمثل ما اعتدى عليكم ۚ واتقوا الله واعلموا أن الله مع المتقين'),
(202, 2, 195, 'وأنفقوا في سبيل الله ولا تلقوا بأيديكم إلى التهلكة ۛ وأحسنوا ۛ إن الله يحب المحسنين'),
(203, 2, 196, 'وأتموا الحج والعمرة لله ۚ فإن أحصرتم فما استيسر من الهدي ۖ ولا تحلقوا رءوسكم حتى يبلغ الهدي محله ۚ فمن كان منكم مريضا أو به أذى من رأسه ففدية من صيام أو صدقة أو نسك ۚ فإذا أمنتم فمن تمتع بالعمرة إلى الحج فما استيسر من الهدي ۚ فمن لم يجد فصيام ثلاثة أيام في الحج وسبعة إذا رجعتم ۗ تلك عشرة كاملة ۗ ذلك لمن لم يكن أهله حاضري المسجد الحرام ۚ واتقوا الله واعلموا أن الله شديد العقاب'),
(204, 2, 197, 'الحج أشهر معلومات ۚ فمن فرض فيهن الحج فلا رفث ولا فسوق ولا جدال في الحج ۗ وما تفعلوا من خير يعلمه الله ۗ وتزودوا فإن خير الزاد التقوى ۚ واتقون يا أولي الألباب'),
(205, 2, 198, 'ليس عليكم جناح أن تبتغوا فضلا من ربكم ۚ فإذا أفضتم من عرفات فاذكروا الله عند المشعر الحرام ۖ واذكروه كما هداكم وإن كنتم من قبله لمن الضالين'),
(206, 2, 199, 'ثم أفيضوا من حيث أفاض الناس واستغفروا الله ۚ إن الله غفور رحيم'),
(207, 2, 200, 'فإذا قضيتم مناسككم فاذكروا الله كذكركم آباءكم أو أشد ذكرا ۗ فمن الناس من يقول ربنا آتنا في الدنيا وما له في الآخرة من خلاق'),
(208, 2, 201, 'ومنهم من يقول ربنا آتنا في الدنيا حسنة وفي الآخرة حسنة وقنا عذاب النار'),
(209, 2, 202, 'أولئك لهم نصيب مما كسبوا ۚ والله سريع الحساب'),
(210, 2, 203, 'واذكروا الله في أيام معدودات ۚ فمن تعجل في يومين فلا إثم عليه ومن تأخر فلا إثم عليه ۚ لمن اتقى ۗ واتقوا الله واعلموا أنكم إليه تحشرون'),
(211, 2, 204, 'ومن الناس من يعجبك قوله في الحياة الدنيا ويشهد الله على ما في قلبه وهو ألد الخصام'),
(212, 2, 205, 'وإذا تولى سعى في الأرض ليفسد فيها ويهلك الحرث والنسل ۗ والله لا يحب الفساد'),
(213, 2, 206, 'وإذا قيل له اتق الله أخذته العزة بالإثم ۚ فحسبه جهنم ۚ ولبئس المهاد'),
(214, 2, 207, 'ومن الناس من يشري نفسه ابتغاء مرضات الله ۗ والله رءوف بالعباد'),
(215, 2, 208, 'يا أيها الذين آمنوا ادخلوا في السلم كافة ولا تتبعوا خطوات الشيطان ۚ إنه لكم عدو مبين'),
(216, 2, 209, 'فإن زللتم من بعد ما جاءتكم البينات فاعلموا أن الله عزيز حكيم'),
(217, 2, 210, 'هل ينظرون إلا أن يأتيهم الله في ظلل من الغمام والملائكة وقضي الأمر ۚ وإلى الله ترجع الأمور'),
(218, 2, 211, 'سل بني إسرائيل كم آتيناهم من آية بينة ۗ ومن يبدل نعمة الله من بعد ما جاءته فإن الله شديد العقاب'),
(219, 2, 212, 'زين للذين كفروا الحياة الدنيا ويسخرون من الذين آمنوا ۘ والذين اتقوا فوقهم يوم القيامة ۗ والله يرزق من يشاء بغير حساب'),
(220, 2, 213, 'كان الناس أمة واحدة فبعث الله النبيين مبشرين ومنذرين وأنزل معهم الكتاب بالحق ليحكم بين الناس فيما اختلفوا فيه ۚ وما اختلف فيه إلا الذين أوتوه من بعد ما جاءتهم البينات بغيا بينهم ۖ فهدى الله الذين آمنوا لما اختلفوا فيه من الحق بإذنه ۗ والله يهدي من يشاء إلى صراط مستقيم'),
(221, 2, 214, 'أم حسبتم أن تدخلوا الجنة ولما يأتكم مثل الذين خلوا من قبلكم ۖ مستهم البأساء والضراء وزلزلوا حتى يقول الرسول والذين آمنوا معه متى نصر الله ۗ ألا إن نصر الله قريب'),
(222, 2, 215, 'يسألونك ماذا ينفقون ۖ قل ما أنفقتم من خير فللوالدين والأقربين واليتامى والمساكين وابن السبيل ۗ وما تفعلوا من خير فإن الله به عليم'),
(223, 2, 216, 'كتب عليكم القتال وهو كره لكم ۖ وعسى أن تكرهوا شيئا وهو خير لكم ۖ وعسى أن تحبوا شيئا وهو شر لكم ۗ والله يعلم وأنتم لا تعلمون'),
(224, 2, 217, 'يسألونك عن الشهر الحرام قتال فيه ۖ قل قتال فيه كبير ۖ وصد عن سبيل الله وكفر به والمسجد الحرام وإخراج أهله منه أكبر عند الله ۚ والفتنة أكبر من القتل ۗ ولا يزالون يقاتلونكم حتى يردوكم عن دينكم إن استطاعوا ۚ ومن يرتدد منكم عن دينه فيمت وهو كافر فأولئك حبطت أعمالهم في الدنيا والآخرة ۖ وأولئك أصحاب النار ۖ هم فيها خالدون'),
(225, 2, 218, 'إن الذين آمنوا والذين هاجروا وجاهدوا في سبيل الله أولئك يرجون رحمت الله ۚ والله غفور رحيم'),
(226, 2, 219, 'يسألونك عن الخمر والميسر ۖ قل فيهما إثم كبير ومنافع للناس وإثمهما أكبر من نفعهما ۗ ويسألونك ماذا ينفقون قل العفو ۗ كذلك يبين الله لكم الآيات لعلكم تتفكرون'),
(227, 2, 220, 'في الدنيا والآخرة ۗ ويسألونك عن اليتامى ۖ قل إصلاح لهم خير ۖ وإن تخالطوهم فإخوانكم ۚ والله يعلم المفسد من المصلح ۚ ولو شاء الله لأعنتكم ۚ إن الله عزيز حكيم'),
(228, 2, 221, 'ولا تنكحوا المشركات حتى يؤمن ۚ ولأمة مؤمنة خير من مشركة ولو أعجبتكم ۗ ولا تنكحوا المشركين حتى يؤمنوا ۚ ولعبد مؤمن خير من مشرك ولو أعجبكم ۗ أولئك يدعون إلى النار ۖ والله يدعو إلى الجنة والمغفرة بإذنه ۖ ويبين آياته للناس لعلهم يتذكرون'),
(229, 2, 222, 'ويسألونك عن المحيض ۖ قل هو أذى فاعتزلوا النساء في المحيض ۖ ولا تقربوهن حتى يطهرن ۖ فإذا تطهرن فأتوهن من حيث أمركم الله ۚ إن الله يحب التوابين ويحب المتطهرين'),
(230, 2, 223, 'نساؤكم حرث لكم فأتوا حرثكم أنى شئتم ۖ وقدموا لأنفسكم ۚ واتقوا الله واعلموا أنكم ملاقوه ۗ وبشر المؤمنين'),
(231, 2, 224, 'ولا تجعلوا الله عرضة لأيمانكم أن تبروا وتتقوا وتصلحوا بين الناس ۗ والله سميع عليم'),
(232, 2, 225, 'لا يؤاخذكم الله باللغو في أيمانكم ولكن يؤاخذكم بما كسبت قلوبكم ۗ والله غفور حليم'),
(233, 2, 226, 'للذين يؤلون من نسائهم تربص أربعة أشهر ۖ فإن فاءوا فإن الله غفور رحيم'),
(234, 2, 227, 'وإن عزموا الطلاق فإن الله سميع عليم'),
(235, 2, 228, 'والمطلقات يتربصن بأنفسهن ثلاثة قروء ۚ ولا يحل لهن أن يكتمن ما خلق الله في أرحامهن إن كن يؤمن بالله واليوم الآخر ۚ وبعولتهن أحق بردهن في ذلك إن أرادوا إصلاحا ۚ ولهن مثل الذي عليهن بالمعروف ۚ وللرجال عليهن درجة ۗ والله عزيز حكيم'),
(236, 2, 229, 'الطلاق مرتان ۖ فإمساك بمعروف أو تسريح بإحسان ۗ ولا يحل لكم أن تأخذوا مما آتيتموهن شيئا إلا أن يخافا ألا يقيما حدود الله ۖ فإن خفتم ألا يقيما حدود الله فلا جناح عليهما فيما افتدت به ۗ تلك حدود الله فلا تعتدوها ۚ ومن يتعد حدود الله فأولئك هم الظالمون'),
(237, 2, 230, 'فإن طلقها فلا تحل له من بعد حتى تنكح زوجا غيره ۗ فإن طلقها فلا جناح عليهما أن يتراجعا إن ظنا أن يقيما حدود الله ۗ وتلك حدود الله يبينها لقوم يعلمون'),
(238, 2, 231, 'وإذا طلقتم النساء فبلغن أجلهن فأمسكوهن بمعروف أو سرحوهن بمعروف ۚ ولا تمسكوهن ضرارا لتعتدوا ۚ ومن يفعل ذلك فقد ظلم نفسه ۚ ولا تتخذوا آيات الله هزوا ۚ واذكروا نعمت الله عليكم وما أنزل عليكم من الكتاب والحكمة يعظكم به ۚ واتقوا الله واعلموا أن الله بكل شيء عليم'),
(239, 2, 232, 'وإذا طلقتم النساء فبلغن أجلهن فلا تعضلوهن أن ينكحن أزواجهن إذا تراضوا بينهم بالمعروف ۗ ذلك يوعظ به من كان منكم يؤمن بالله واليوم الآخر ۗ ذلكم أزكى لكم وأطهر ۗ والله يعلم وأنتم لا تعلمون'),
(240, 2, 233, 'والوالدات يرضعن أولادهن حولين كاملين ۖ لمن أراد أن يتم الرضاعة ۚ وعلى المولود له رزقهن وكسوتهن بالمعروف ۚ لا تكلف نفس إلا وسعها ۚ لا تضار والدة بولدها ولا مولود له بولده ۚ وعلى الوارث مثل ذلك ۗ فإن أرادا فصالا عن تراض منهما وتشاور فلا جناح عليهما ۗ وإن أردتم أن تسترضعوا أولادكم فلا جناح عليكم إذا سلمتم ما آتيتم بالمعروف ۗ واتقوا الله واعلموا أن الله بما تعملون بصير'),
(241, 2, 234, 'والذين يتوفون منكم ويذرون أزواجا يتربصن بأنفسهن أربعة أشهر وعشرا ۖ فإذا بلغن أجلهن فلا جناح عليكم فيما فعلن في أنفسهن بالمعروف ۗ والله بما تعملون خبير'),
(242, 2, 235, 'ولا جناح عليكم فيما عرضتم به من خطبة النساء أو أكننتم في أنفسكم ۚ علم الله أنكم ستذكرونهن ولكن لا تواعدوهن سرا إلا أن تقولوا قولا معروفا ۚ ولا تعزموا عقدة النكاح حتى يبلغ الكتاب أجله ۚ واعلموا أن الله يعلم ما في أنفسكم فاحذروه ۚ واعلموا أن الله غفور حليم'),
(243, 2, 236, 'لا جناح عليكم إن طلقتم النساء ما لم تمسوهن أو تفرضوا لهن فريضة ۚ ومتعوهن على الموسع قدره وعلى المقتر قدره متاعا بالمعروف ۖ حقا على المحسنين'),
(244, 2, 237, 'وإن طلقتموهن من قبل أن تمسوهن وقد فرضتم لهن فريضة فنصف ما فرضتم إلا أن يعفون أو يعفو الذي بيده عقدة النكاح ۚ وأن تعفوا أقرب للتقوى ۚ ولا تنسوا الفضل بينكم ۚ إن الله بما تعملون بصير'),
(245, 2, 238, 'حافظوا على الصلوات والصلاة الوسطى وقوموا لله قانتين'),
(246, 2, 239, 'فإن خفتم فرجالا أو ركبانا ۖ فإذا أمنتم فاذكروا الله كما علمكم ما لم تكونوا تعلمون'),
(247, 2, 240, 'والذين يتوفون منكم ويذرون أزواجا وصية لأزواجهم متاعا إلى الحول غير إخراج ۚ فإن خرجن فلا جناح عليكم في ما فعلن في أنفسهن من معروف ۗ والله عزيز حكيم'),
(248, 2, 241, 'وللمطلقات متاع بالمعروف ۖ حقا على المتقين'),
(249, 2, 242, 'كذلك يبين الله لكم آياته لعلكم تعقلون'),
(250, 2, 243, 'ألم تر إلى الذين خرجوا من ديارهم وهم ألوف حذر الموت فقال لهم الله موتوا ثم أحياهم ۚ إن الله لذو فضل على الناس ولكن أكثر الناس لا يشكرون'),
(251, 2, 244, 'وقاتلوا في سبيل الله واعلموا أن الله سميع عليم'),
(252, 2, 245, 'من ذا الذي يقرض الله قرضا حسنا فيضاعفه له أضعافا كثيرة ۚ والله يقبض ويبسط وإليه ترجعون'),
(253, 2, 246, 'ألم تر إلى الملإ من بني إسرائيل من بعد موسى إذ قالوا لنبي لهم ابعث لنا ملكا نقاتل في سبيل الله ۖ قال هل عسيتم إن كتب عليكم القتال ألا تقاتلوا ۖ قالوا وما لنا ألا نقاتل في سبيل الله وقد أخرجنا من ديارنا وأبنائنا ۖ فلما كتب عليهم القتال تولوا إلا قليلا منهم ۗ والله عليم بالظالمين'),
(254, 2, 247, 'وقال لهم نبيهم إن الله قد بعث لكم طالوت ملكا ۚ قالوا أنى يكون له الملك علينا ونحن أحق بالملك منه ولم يؤت سعة من المال ۚ قال إن الله اصطفاه عليكم وزاده بسطة في العلم والجسم ۖ والله يؤتي ملكه من يشاء ۚ والله واسع عليم'),
(255, 2, 248, 'وقال لهم نبيهم إن آية ملكه أن يأتيكم التابوت فيه سكينة من ربكم وبقية مما ترك آل موسى وآل هارون تحمله الملائكة ۚ إن في ذلك لآية لكم إن كنتم مؤمنين'),
(256, 2, 249, 'فلما فصل طالوت بالجنود قال إن الله مبتليكم بنهر فمن شرب منه فليس مني ومن لم يطعمه فإنه مني إلا من اغترف غرفة بيده ۚ فشربوا منه إلا قليلا منهم ۚ فلما جاوزه هو والذين آمنوا معه قالوا لا طاقة لنا اليوم بجالوت وجنوده ۚ قال الذين يظنون أنهم ملاقو الله كم من فئة قليلة غلبت فئة كثيرة بإذن الله ۗ والله مع الصابرين'),
(257, 2, 250, 'ولما برزوا لجالوت وجنوده قالوا ربنا أفرغ علينا صبرا وثبت أقدامنا وانصرنا على القوم الكافرين'),
(258, 2, 251, 'فهزموهم بإذن الله وقتل داوود جالوت وآتاه الله الملك والحكمة وعلمه مما يشاء ۗ ولولا دفع الله الناس بعضهم ببعض لفسدت الأرض ولكن الله ذو فضل على العالمين'),
(259, 2, 252, 'تلك آيات الله نتلوها عليك بالحق ۚ وإنك لمن المرسلين'),
(260, 2, 253, 'تلك الرسل فضلنا بعضهم على بعض ۘ منهم من كلم الله ۖ ورفع بعضهم درجات ۚ وآتينا عيسى ابن مريم البينات وأيدناه بروح القدس ۗ ولو شاء الله ما اقتتل الذين من بعدهم من بعد ما جاءتهم البينات ولكن اختلفوا فمنهم من آمن ومنهم من كفر ۚ ولو شاء الله ما اقتتلوا ولكن الله يفعل ما يريد'),
(261, 2, 254, 'يا أيها الذين آمنوا أنفقوا مما رزقناكم من قبل أن يأتي يوم لا بيع فيه ولا خلة ولا شفاعة ۗ والكافرون هم الظالمون'),
(262, 2, 255, 'الله لا إله إلا هو الحي القيوم ۚ لا تأخذه سنة ولا نوم ۚ له ما في السماوات وما في الأرض ۗ من ذا الذي يشفع عنده إلا بإذنه ۚ يعلم ما بين أيديهم وما خلفهم ۖ ولا يحيطون بشيء من علمه إلا بما شاء ۚ وسع كرسيه السماوات والأرض ۖ ولا يئوده حفظهما ۚ وهو العلي العظيم'),
(263, 2, 256, 'لا إكراه في الدين ۖ قد تبين الرشد من الغي ۚ فمن يكفر بالطاغوت ويؤمن بالله فقد استمسك بالعروة الوثقى لا انفصام لها ۗ والله سميع عليم'),
(264, 2, 257, 'الله ولي الذين آمنوا يخرجهم من الظلمات إلى النور ۖ والذين كفروا أولياؤهم الطاغوت يخرجونهم من النور إلى الظلمات ۗ أولئك أصحاب النار ۖ هم فيها خالدون'),
(265, 2, 258, 'ألم تر إلى الذي حاج إبراهيم في ربه أن آتاه الله الملك إذ قال إبراهيم ربي الذي يحيي ويميت قال أنا أحيي وأميت ۖ قال إبراهيم فإن الله يأتي بالشمس من المشرق فأت بها من المغرب فبهت الذي كفر ۗ والله لا يهدي القوم الظالمين'),
(266, 2, 259, 'أو كالذي مر على قرية وهي خاوية على عروشها قال أنى يحيي هذه الله بعد موتها ۖ فأماته الله مائة عام ثم بعثه ۖ قال كم لبثت ۖ قال لبثت يوما أو بعض يوم ۖ قال بل لبثت مائة عام فانظر إلى طعامك وشرابك لم يتسنه ۖ وانظر إلى حمارك ولنجعلك آية للناس ۖ وانظر إلى العظام كيف ننشزها ثم نكسوها لحما ۚ فلما تبين له قال أعلم أن الله على كل شيء قدير'),
(267, 2, 260, 'وإذ قال إبراهيم رب أرني كيف تحيي الموتى ۖ قال أولم تؤمن ۖ قال بلى ولكن ليطمئن قلبي ۖ قال فخذ أربعة من الطير فصرهن إليك ثم اجعل على كل جبل منهن جزءا ثم ادعهن يأتينك سعيا ۚ واعلم أن الله عزيز حكيم'),
(268, 2, 261, 'مثل الذين ينفقون أموالهم في سبيل الله كمثل حبة أنبتت سبع سنابل في كل سنبلة مائة حبة ۗ والله يضاعف لمن يشاء ۗ والله واسع عليم'),
(269, 2, 262, 'الذين ينفقون أموالهم في سبيل الله ثم لا يتبعون ما أنفقوا منا ولا أذى ۙ لهم أجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون'),
(270, 2, 263, 'قول معروف ومغفرة خير من صدقة يتبعها أذى ۗ والله غني حليم'),
(271, 2, 264, 'يا أيها الذين آمنوا لا تبطلوا صدقاتكم بالمن والأذى كالذي ينفق ماله رئاء الناس ولا يؤمن بالله واليوم الآخر ۖ فمثله كمثل صفوان عليه تراب فأصابه وابل فتركه صلدا ۖ لا يقدرون على شيء مما كسبوا ۗ والله لا يهدي القوم الكافرين'),
(272, 2, 265, 'ومثل الذين ينفقون أموالهم ابتغاء مرضات الله وتثبيتا من أنفسهم كمثل جنة بربوة أصابها وابل فآتت أكلها ضعفين فإن لم يصبها وابل فطل ۗ والله بما تعملون بصير'),
(273, 2, 266, 'أيود أحدكم أن تكون له جنة من نخيل وأعناب تجري من تحتها الأنهار له فيها من كل الثمرات وأصابه الكبر وله ذرية ضعفاء فأصابها إعصار فيه نار فاحترقت ۗ كذلك يبين الله لكم الآيات لعلكم تتفكرون'),
(274, 2, 267, 'يا أيها الذين آمنوا أنفقوا من طيبات ما كسبتم ومما أخرجنا لكم من الأرض ۖ ولا تيمموا الخبيث منه تنفقون ولستم بآخذيه إلا أن تغمضوا فيه ۚ واعلموا أن الله غني حميد'),
(275, 2, 268, 'الشيطان يعدكم الفقر ويأمركم بالفحشاء ۖ والله يعدكم مغفرة منه وفضلا ۗ والله واسع عليم'),
(276, 2, 269, 'يؤتي الحكمة من يشاء ۚ ومن يؤت الحكمة فقد أوتي خيرا كثيرا ۗ وما يذكر إلا أولو الألباب'),
(277, 2, 270, 'وما أنفقتم من نفقة أو نذرتم من نذر فإن الله يعلمه ۗ وما للظالمين من أنصار'),
(278, 2, 271, 'إن تبدوا الصدقات فنعما هي ۖ وإن تخفوها وتؤتوها الفقراء فهو خير لكم ۚ ويكفر عنكم من سيئاتكم ۗ والله بما تعملون خبير'),
(279, 2, 272, 'ليس عليك هداهم ولكن الله يهدي من يشاء ۗ وما تنفقوا من خير فلأنفسكم ۚ وما تنفقون إلا ابتغاء وجه الله ۚ وما تنفقوا من خير يوف إليكم وأنتم لا تظلمون'),
(280, 2, 273, 'للفقراء الذين أحصروا في سبيل الله لا يستطيعون ضربا في الأرض يحسبهم الجاهل أغنياء من التعفف تعرفهم بسيماهم لا يسألون الناس إلحافا ۗ وما تنفقوا من خير فإن الله به عليم'),
(281, 2, 274, 'الذين ينفقون أموالهم بالليل والنهار سرا وعلانية فلهم أجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون'),
(282, 2, 275, 'الذين يأكلون الربا لا يقومون إلا كما يقوم الذي يتخبطه الشيطان من المس ۚ ذلك بأنهم قالوا إنما البيع مثل الربا ۗ وأحل الله البيع وحرم الربا ۚ فمن جاءه موعظة من ربه فانتهى فله ما سلف وأمره إلى الله ۖ ومن عاد فأولئك أصحاب النار ۖ هم فيها خالدون'),
(283, 2, 276, 'يمحق الله الربا ويربي الصدقات ۗ والله لا يحب كل كفار أثيم'),
(284, 2, 277, 'إن الذين آمنوا وعملوا الصالحات وأقاموا الصلاة وآتوا الزكاة لهم أجرهم عند ربهم ولا خوف عليهم ولا هم يحزنون'),
(285, 2, 278, 'يا أيها الذين آمنوا اتقوا الله وذروا ما بقي من الربا إن كنتم مؤمنين'),
(286, 2, 279, 'فإن لم تفعلوا فأذنوا بحرب من الله ورسوله ۖ وإن تبتم فلكم رءوس أموالكم لا تظلمون ولا تظلمون'),
(287, 2, 280, 'وإن كان ذو عسرة فنظرة إلى ميسرة ۚ وأن تصدقوا خير لكم ۖ إن كنتم تعلمون'),
(288, 2, 281, 'واتقوا يوما ترجعون فيه إلى الله ۖ ثم توفى كل نفس ما كسبت وهم لا يظلمون'),
(289, 2, 282, 'يا أيها الذين آمنوا إذا تداينتم بدين إلى أجل مسمى فاكتبوه ۚ وليكتب بينكم كاتب بالعدل ۚ ولا يأب كاتب أن يكتب كما علمه الله ۚ فليكتب وليملل الذي عليه الحق وليتق الله ربه ولا يبخس منه شيئا ۚ فإن كان الذي عليه الحق سفيها أو ضعيفا أو لا يستطيع أن يمل هو فليملل وليه بالعدل ۚ واستشهدوا شهيدين من رجالكم ۖ فإن لم يكونا رجلين فرجل وامرأتان ممن ترضون من الشهداء أن تضل إحداهما فتذكر إحداهما الأخرى ۚ ولا يأب الشهداء إذا ما دعوا ۚ ولا تسأموا أن تكتبوه صغيرا أو كبيرا إلى أجله ۚ ذلكم أقسط عند الله وأقوم للشهادة وأدنى ألا ترتابوا ۖ إلا أن تكون تجارة حاضرة تديرونها بينكم فليس عليكم جناح ألا تكتبوها ۗ وأشهدوا إذا تبايعتم ۚ ولا يضار كاتب ولا شهيد ۚ وإن تفعلوا فإنه فسوق بكم ۗ واتقوا الله ۖ ويعلمكم الله ۗ والله بكل شيء عليم'),
(290, 2, 283, 'وإن كنتم على سفر ولم تجدوا كاتبا فرهان مقبوضة ۖ فإن أمن بعضكم بعضا فليؤد الذي اؤتمن أمانته وليتق الله ربه ۗ ولا تكتموا الشهادة ۚ ومن يكتمها فإنه آثم قلبه ۗ والله بما تعملون عليم'),
(291, 2, 284, 'لله ما في السماوات وما في الأرض ۗ وإن تبدوا ما في أنفسكم أو تخفوه يحاسبكم به الله ۖ فيغفر لمن يشاء ويعذب من يشاء ۗ والله على كل شيء قدير'),
(292, 2, 285, 'آمن الرسول بما أنزل إليه من ربه والمؤمنون ۚ كل آمن بالله وملائكته وكتبه ورسله لا نفرق بين أحد من رسله ۚ وقالوا سمعنا وأطعنا ۖ غفرانك ربنا وإليك المصير'),
(293, 2, 286, 'لا يكلف الله نفسا إلا وسعها ۚ لها ما كسبت وعليها ما اكتسبت ۗ ربنا لا تؤاخذنا إن نسينا أو أخطأنا ۚ ربنا ولا تحمل علينا إصرا كما حملته على الذين من قبلنا ۚ ربنا ولا تحملنا ما لا طاقة لنا به ۖ واعف عنا واغفر لنا وارحمنا ۚ أنت مولانا فانصرنا على القوم الكافرين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'بسم الله الرحمن الرحيم الم'),
(295, 3, 2, 'الله لا إله إلا هو الحي القيوم'),
(296, 3, 3, 'نزل عليك الكتاب بالحق مصدقا لما بين يديه وأنزل التوراة والإنجيل'),
(297, 3, 4, 'من قبل هدى للناس وأنزل الفرقان ۗ إن الذين كفروا بآيات الله لهم عذاب شديد ۗ والله عزيز ذو انتقام'),
(298, 3, 5, 'إن الله لا يخفى عليه شيء في الأرض ولا في السماء'),
(299, 3, 6, 'هو الذي يصوركم في الأرحام كيف يشاء ۚ لا إله إلا هو العزيز الحكيم'),
(300, 3, 7, 'هو الذي أنزل عليك الكتاب منه آيات محكمات هن أم الكتاب وأخر متشابهات ۖ فأما الذين في قلوبهم زيغ فيتبعون ما تشابه منه ابتغاء الفتنة وابتغاء تأويله ۗ وما يعلم تأويله إلا الله ۗ والراسخون في العلم يقولون آمنا به كل من عند ربنا ۗ وما يذكر إلا أولو الألباب'),
(301, 3, 8, 'ربنا لا تزغ قلوبنا بعد إذ هديتنا وهب لنا من لدنك رحمة ۚ إنك أنت الوهاب'),
(302, 3, 9, 'ربنا إنك جامع الناس ليوم لا ريب فيه ۚ إن الله لا يخلف الميعاد'),
(303, 3, 10, 'إن الذين كفروا لن تغني عنهم أموالهم ولا أولادهم من الله شيئا ۖ وأولئك هم وقود النار'),
(304, 3, 11, 'كدأب آل فرعون والذين من قبلهم ۚ كذبوا بآياتنا فأخذهم الله بذنوبهم ۗ والله شديد العقاب'),
(305, 3, 12, 'قل للذين كفروا ستغلبون وتحشرون إلى جهنم ۚ وبئس المهاد'),
(306, 3, 13, 'قد كان لكم آية في فئتين التقتا ۖ فئة تقاتل في سبيل الله وأخرى كافرة يرونهم مثليهم رأي العين ۚ والله يؤيد بنصره من يشاء ۗ إن في ذلك لعبرة لأولي الأبصار'),
(307, 3, 14, 'زين للناس حب الشهوات من النساء والبنين والقناطير المقنطرة من الذهب والفضة والخيل المسومة والأنعام والحرث ۗ ذلك متاع الحياة الدنيا ۖ والله عنده حسن المآب'),
(308, 3, 15, 'قل أؤنبئكم بخير من ذلكم ۚ للذين اتقوا عند ربهم جنات تجري من تحتها الأنهار خالدين فيها وأزواج مطهرة ورضوان من الله ۗ والله بصير بالعباد'),
(309, 3, 16, 'الذين يقولون ربنا إننا آمنا فاغفر لنا ذنوبنا وقنا عذاب النار'),
(310, 3, 17, 'الصابرين والصادقين والقانتين والمنفقين والمستغفرين بالأسحار'),
(311, 3, 18, 'شهد الله أنه لا إله إلا هو والملائكة وأولو العلم قائما بالقسط ۚ لا إله إلا هو العزيز الحكيم'),
(312, 3, 19, 'إن الدين عند الله الإسلام ۗ وما اختلف الذين أوتوا الكتاب إلا من بعد ما جاءهم العلم بغيا بينهم ۗ ومن يكفر بآيات الله فإن الله سريع الحساب'),
(313, 3, 20, 'فإن حاجوك فقل أسلمت وجهي لله ومن اتبعن ۗ وقل للذين أوتوا الكتاب والأميين أأسلمتم ۚ فإن أسلموا فقد اهتدوا ۖ وإن تولوا فإنما عليك البلاغ ۗ والله بصير بالعباد'),
(314, 3, 21, 'إن الذين يكفرون بآيات الله ويقتلون النبيين بغير حق ويقتلون الذين يأمرون بالقسط من الناس فبشرهم بعذاب أليم'),
(315, 3, 22, 'أولئك الذين حبطت أعمالهم في الدنيا والآخرة وما لهم من ناصرين'),
(316, 3, 23, 'ألم تر إلى الذين أوتوا نصيبا من الكتاب يدعون إلى كتاب الله ليحكم بينهم ثم يتولى فريق منهم وهم معرضون'),
(317, 3, 24, 'ذلك بأنهم قالوا لن تمسنا النار إلا أياما معدودات ۖ وغرهم في دينهم ما كانوا يفترون'),
(318, 3, 25, 'فكيف إذا جمعناهم ليوم لا ريب فيه ووفيت كل نفس ما كسبت وهم لا يظلمون'),
(319, 3, 26, 'قل اللهم مالك الملك تؤتي الملك من تشاء وتنزع الملك ممن تشاء وتعز من تشاء وتذل من تشاء ۖ بيدك الخير ۖ إنك على كل شيء قدير'),
(320, 3, 27, 'تولج الليل في النهار وتولج النهار في الليل ۖ وتخرج الحي من الميت وتخرج الميت من الحي ۖ وترزق من تشاء بغير حساب'),
(321, 3, 28, 'لا يتخذ المؤمنون الكافرين أولياء من دون المؤمنين ۖ ومن يفعل ذلك فليس من الله في شيء إلا أن تتقوا منهم تقاة ۗ ويحذركم الله نفسه ۗ وإلى الله المصير'),
(322, 3, 29, 'قل إن تخفوا ما في صدوركم أو تبدوه يعلمه الله ۗ ويعلم ما في السماوات وما في الأرض ۗ والله على كل شيء قدير'),
(323, 3, 30, 'يوم تجد كل نفس ما عملت من خير محضرا وما عملت من سوء تود لو أن بينها وبينه أمدا بعيدا ۗ ويحذركم الله نفسه ۗ والله رءوف بالعباد'),
(324, 3, 31, 'قل إن كنتم تحبون الله فاتبعوني يحببكم الله ويغفر لكم ذنوبكم ۗ والله غفور رحيم'),
(325, 3, 32, 'قل أطيعوا الله والرسول ۖ فإن تولوا فإن الله لا يحب الكافرين'),
(326, 3, 33, 'إن الله اصطفى آدم ونوحا وآل إبراهيم وآل عمران على العالمين'),
(327, 3, 34, 'ذرية بعضها من بعض ۗ والله سميع عليم'),
(328, 3, 35, 'إذ قالت امرأت عمران رب إني نذرت لك ما في بطني محررا فتقبل مني ۖ إنك أنت السميع العليم'),
(329, 3, 36, 'فلما وضعتها قالت رب إني وضعتها أنثى والله أعلم بما وضعت وليس الذكر كالأنثى ۖ وإني سميتها مريم وإني أعيذها بك وذريتها من الشيطان الرجيم'),
(330, 3, 37, 'فتقبلها ربها بقبول حسن وأنبتها نباتا حسنا وكفلها زكريا ۖ كلما دخل عليها زكريا المحراب وجد عندها رزقا ۖ قال يا مريم أنى لك هذا ۖ قالت هو من عند الله ۖ إن الله يرزق من يشاء بغير حساب'),
(331, 3, 38, 'هنالك دعا زكريا ربه ۖ قال رب هب لي من لدنك ذرية طيبة ۖ إنك سميع الدعاء'),
(332, 3, 39, 'فنادته الملائكة وهو قائم يصلي في المحراب أن الله يبشرك بيحيى مصدقا بكلمة من الله وسيدا وحصورا ونبيا من الصالحين'),
(333, 3, 40, 'قال رب أنى يكون لي غلام وقد بلغني الكبر وامرأتي عاقر ۖ قال كذلك الله يفعل ما يشاء'),
(334, 3, 41, 'قال رب اجعل لي آية ۖ قال آيتك ألا تكلم الناس ثلاثة أيام إلا رمزا ۗ واذكر ربك كثيرا وسبح بالعشي والإبكار'),
(335, 3, 42, 'وإذ قالت الملائكة يا مريم إن الله اصطفاك وطهرك واصطفاك على نساء العالمين'),
(336, 3, 43, 'يا مريم اقنتي لربك واسجدي واركعي مع الراكعين'),
(337, 3, 44, 'ذلك من أنباء الغيب نوحيه إليك ۚ وما كنت لديهم إذ يلقون أقلامهم أيهم يكفل مريم وما كنت لديهم إذ يختصمون'),
(338, 3, 45, 'إذ قالت الملائكة يا مريم إن الله يبشرك بكلمة منه اسمه المسيح عيسى ابن مريم وجيها في الدنيا والآخرة ومن المقربين'),
(339, 3, 46, 'ويكلم الناس في المهد وكهلا ومن الصالحين'),
(340, 3, 47, 'قالت رب أنى يكون لي ولد ولم يمسسني بشر ۖ قال كذلك الله يخلق ما يشاء ۚ إذا قضى أمرا فإنما يقول له كن فيكون'),
(341, 3, 48, 'ويعلمه الكتاب والحكمة والتوراة والإنجيل'),
(342, 3, 49, 'ورسولا إلى بني إسرائيل أني قد جئتكم بآية من ربكم ۖ أني أخلق لكم من الطين كهيئة الطير فأنفخ فيه فيكون طيرا بإذن الله ۖ وأبرئ الأكمه والأبرص وأحيي الموتى بإذن الله ۖ وأنبئكم بما تأكلون وما تدخرون في بيوتكم ۚ إن في ذلك لآية لكم إن كنتم مؤمنين'),
(343, 3, 50, 'ومصدقا لما بين يدي من التوراة ولأحل لكم بعض الذي حرم عليكم ۚ وجئتكم بآية من ربكم فاتقوا الله وأطيعون'),
(344, 3, 51, 'إن الله ربي وربكم فاعبدوه ۗ هذا صراط مستقيم'),
(345, 3, 52, 'فلما أحس عيسى منهم الكفر قال من أنصاري إلى الله ۖ قال الحواريون نحن أنصار الله آمنا بالله واشهد بأنا مسلمون'),
(346, 3, 53, 'ربنا آمنا بما أنزلت واتبعنا الرسول فاكتبنا مع الشاهدين'),
(347, 3, 54, 'ومكروا ومكر الله ۖ والله خير الماكرين'),
(348, 3, 55, 'إذ قال الله يا عيسى إني متوفيك ورافعك إلي ومطهرك من الذين كفروا وجاعل الذين اتبعوك فوق الذين كفروا إلى يوم القيامة ۖ ثم إلي مرجعكم فأحكم بينكم فيما كنتم فيه تختلفون'),
(349, 3, 56, 'فأما الذين كفروا فأعذبهم عذابا شديدا في الدنيا والآخرة وما لهم من ناصرين'),
(350, 3, 57, 'وأما الذين آمنوا وعملوا الصالحات فيوفيهم أجورهم ۗ والله لا يحب الظالمين'),
(351, 3, 58, 'ذلك نتلوه عليك من الآيات والذكر الحكيم'),
(352, 3, 59, 'إن مثل عيسى عند الله كمثل آدم ۖ خلقه من تراب ثم قال له كن فيكون'),
(353, 3, 60, 'الحق من ربك فلا تكن من الممترين'),
(354, 3, 61, 'فمن حاجك فيه من بعد ما جاءك من العلم فقل تعالوا ندع أبناءنا وأبناءكم ونساءنا ونساءكم وأنفسنا وأنفسكم ثم نبتهل فنجعل لعنت الله على الكاذبين'),
(355, 3, 62, 'إن هذا لهو القصص الحق ۚ وما من إله إلا الله ۚ وإن الله لهو العزيز الحكيم'),
(356, 3, 63, 'فإن تولوا فإن الله عليم بالمفسدين'),
(357, 3, 64, 'قل يا أهل الكتاب تعالوا إلى كلمة سواء بيننا وبينكم ألا نعبد إلا الله ولا نشرك به شيئا ولا يتخذ بعضنا بعضا أربابا من دون الله ۚ فإن تولوا فقولوا اشهدوا بأنا مسلمون'),
(358, 3, 65, 'يا أهل الكتاب لم تحاجون في إبراهيم وما أنزلت التوراة والإنجيل إلا من بعده ۚ أفلا تعقلون'),
(359, 3, 66, 'ها أنتم هؤلاء حاججتم فيما لكم به علم فلم تحاجون فيما ليس لكم به علم ۚ والله يعلم وأنتم لا تعلمون'),
(360, 3, 67, 'ما كان إبراهيم يهوديا ولا نصرانيا ولكن كان حنيفا مسلما وما كان من المشركين'),
(361, 3, 68, 'إن أولى الناس بإبراهيم للذين اتبعوه وهذا النبي والذين آمنوا ۗ والله ولي المؤمنين'),
(362, 3, 69, 'ودت طائفة من أهل الكتاب لو يضلونكم وما يضلون إلا أنفسهم وما يشعرون'),
(363, 3, 70, 'يا أهل الكتاب لم تكفرون بآيات الله وأنتم تشهدون'),
(364, 3, 71, 'يا أهل الكتاب لم تلبسون الحق بالباطل وتكتمون الحق وأنتم تعلمون'),
(365, 3, 72, 'وقالت طائفة من أهل الكتاب آمنوا بالذي أنزل على الذين آمنوا وجه النهار واكفروا آخره لعلهم يرجعون'),
(366, 3, 73, 'ولا تؤمنوا إلا لمن تبع دينكم قل إن الهدى هدى الله أن يؤتى أحد مثل ما أوتيتم أو يحاجوكم عند ربكم ۗ قل إن الفضل بيد الله يؤتيه من يشاء ۗ والله واسع عليم'),
(367, 3, 74, 'يختص برحمته من يشاء ۗ والله ذو الفضل العظيم'),
(368, 3, 75, 'ومن أهل الكتاب من إن تأمنه بقنطار يؤده إليك ومنهم من إن تأمنه بدينار لا يؤده إليك إلا ما دمت عليه قائما ۗ ذلك بأنهم قالوا ليس علينا في الأميين سبيل ويقولون على الله الكذب وهم يعلمون'),
(369, 3, 76, 'بلى من أوفى بعهده واتقى فإن الله يحب المتقين'),
(370, 3, 77, 'إن الذين يشترون بعهد الله وأيمانهم ثمنا قليلا أولئك لا خلاق لهم في الآخرة ولا يكلمهم الله ولا ينظر إليهم يوم القيامة ولا يزكيهم ولهم عذاب أليم'),
(371, 3, 78, 'وإن منهم لفريقا يلوون ألسنتهم بالكتاب لتحسبوه من الكتاب وما هو من الكتاب ويقولون هو من عند الله وما هو من عند الله ويقولون على الله الكذب وهم يعلمون'),
(372, 3, 79, 'ما كان لبشر أن يؤتيه الله الكتاب والحكم والنبوة ثم يقول للناس كونوا عبادا لي من دون الله ولكن كونوا ربانيين بما كنتم تعلمون الكتاب وبما كنتم تدرسون'),
(373, 3, 80, 'ولا يأمركم أن تتخذوا الملائكة والنبيين أربابا ۗ أيأمركم بالكفر بعد إذ أنتم مسلمون'),
(374, 3, 81, 'وإذ أخذ الله ميثاق النبيين لما آتيتكم من كتاب وحكمة ثم جاءكم رسول مصدق لما معكم لتؤمنن به ولتنصرنه ۚ قال أأقررتم وأخذتم على ذلكم إصري ۖ قالوا أقررنا ۚ قال فاشهدوا وأنا معكم من الشاهدين'),
(375, 3, 82, 'فمن تولى بعد ذلك فأولئك هم الفاسقون'),
(376, 3, 83, 'أفغير دين الله يبغون وله أسلم من في السماوات والأرض طوعا وكرها وإليه يرجعون'),
(377, 3, 84, 'قل آمنا بالله وما أنزل علينا وما أنزل على إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط وما أوتي موسى وعيسى والنبيون من ربهم لا نفرق بين أحد منهم ونحن له مسلمون'),
(378, 3, 85, 'ومن يبتغ غير الإسلام دينا فلن يقبل منه وهو في الآخرة من الخاسرين'),
(379, 3, 86, 'كيف يهدي الله قوما كفروا بعد إيمانهم وشهدوا أن الرسول حق وجاءهم البينات ۚ والله لا يهدي القوم الظالمين'),
(380, 3, 87, 'أولئك جزاؤهم أن عليهم لعنة الله والملائكة والناس أجمعين'),
(381, 3, 88, 'خالدين فيها لا يخفف عنهم العذاب ولا هم ينظرون'),
(382, 3, 89, 'إلا الذين تابوا من بعد ذلك وأصلحوا فإن الله غفور رحيم'),
(383, 3, 90, 'إن الذين كفروا بعد إيمانهم ثم ازدادوا كفرا لن تقبل توبتهم وأولئك هم الضالون'),
(384, 3, 91, 'إن الذين كفروا وماتوا وهم كفار فلن يقبل من أحدهم ملء الأرض ذهبا ولو افتدى به ۗ أولئك لهم عذاب أليم وما لهم من ناصرين'),
(385, 3, 92, 'لن تنالوا البر حتى تنفقوا مما تحبون ۚ وما تنفقوا من شيء فإن الله به عليم'),
(386, 3, 93, 'كل الطعام كان حلا لبني إسرائيل إلا ما حرم إسرائيل على نفسه من قبل أن تنزل التوراة ۗ قل فأتوا بالتوراة فاتلوها إن كنتم صادقين'),
(387, 3, 94, 'فمن افترى على الله الكذب من بعد ذلك فأولئك هم الظالمون'),
(388, 3, 95, 'قل صدق الله ۗ فاتبعوا ملة إبراهيم حنيفا وما كان من المشركين'),
(389, 3, 96, 'إن أول بيت وضع للناس للذي ببكة مباركا وهدى للعالمين'),
(390, 3, 97, 'فيه آيات بينات مقام إبراهيم ۖ ومن دخله كان آمنا ۗ ولله على الناس حج البيت من استطاع إليه سبيلا ۚ ومن كفر فإن الله غني عن العالمين'),
(391, 3, 98, 'قل يا أهل الكتاب لم تكفرون بآيات الله والله شهيد على ما تعملون'),
(392, 3, 99, 'قل يا أهل الكتاب لم تصدون عن سبيل الله من آمن تبغونها عوجا وأنتم شهداء ۗ وما الله بغافل عما تعملون'),
(393, 3, 100, 'يا أيها الذين آمنوا إن تطيعوا فريقا من الذين أوتوا الكتاب يردوكم بعد إيمانكم كافرين'),
(394, 3, 101, 'وكيف تكفرون وأنتم تتلى عليكم آيات الله وفيكم رسوله ۗ ومن يعتصم بالله فقد هدي إلى صراط مستقيم'),
(395, 3, 102, 'يا أيها الذين آمنوا اتقوا الله حق تقاته ولا تموتن إلا وأنتم مسلمون'),
(396, 3, 103, 'واعتصموا بحبل الله جميعا ولا تفرقوا ۚ واذكروا نعمت الله عليكم إذ كنتم أعداء فألف بين قلوبكم فأصبحتم بنعمته إخوانا وكنتم على شفا حفرة من النار فأنقذكم منها ۗ كذلك يبين الله لكم آياته لعلكم تهتدون'),
(397, 3, 104, 'ولتكن منكم أمة يدعون إلى الخير ويأمرون بالمعروف وينهون عن المنكر ۚ وأولئك هم المفلحون'),
(398, 3, 105, 'ولا تكونوا كالذين تفرقوا واختلفوا من بعد ما جاءهم البينات ۚ وأولئك لهم عذاب عظيم'),
(399, 3, 106, 'يوم تبيض وجوه وتسود وجوه ۚ فأما الذين اسودت وجوههم أكفرتم بعد إيمانكم فذوقوا العذاب بما كنتم تكفرون'),
(400, 3, 107, 'وأما الذين ابيضت وجوههم ففي رحمة الله هم فيها خالدون'),
(401, 3, 108, 'تلك آيات الله نتلوها عليك بالحق ۗ وما الله يريد ظلما للعالمين'),
(402, 3, 109, 'ولله ما في السماوات وما في الأرض ۚ وإلى الله ترجع الأمور'),
(403, 3, 110, 'كنتم خير أمة أخرجت للناس تأمرون بالمعروف وتنهون عن المنكر وتؤمنون بالله ۗ ولو آمن أهل الكتاب لكان خيرا لهم ۚ منهم المؤمنون وأكثرهم الفاسقون'),
(404, 3, 111, 'لن يضروكم إلا أذى ۖ وإن يقاتلوكم يولوكم الأدبار ثم لا ينصرون'),
(405, 3, 112, 'ضربت عليهم الذلة أين ما ثقفوا إلا بحبل من الله وحبل من الناس وباءوا بغضب من الله وضربت عليهم المسكنة ۚ ذلك بأنهم كانوا يكفرون بآيات الله ويقتلون الأنبياء بغير حق ۚ ذلك بما عصوا وكانوا يعتدون'),
(406, 3, 113, 'ليسوا سواء ۗ من أهل الكتاب أمة قائمة يتلون آيات الله آناء الليل وهم يسجدون'),
(407, 3, 114, 'يؤمنون بالله واليوم الآخر ويأمرون بالمعروف وينهون عن المنكر ويسارعون في الخيرات وأولئك من الصالحين'),
(408, 3, 115, 'وما يفعلوا من خير فلن يكفروه ۗ والله عليم بالمتقين'),
(409, 3, 116, 'إن الذين كفروا لن تغني عنهم أموالهم ولا أولادهم من الله شيئا ۖ وأولئك أصحاب النار ۚ هم فيها خالدون'),
(410, 3, 117, 'مثل ما ينفقون في هذه الحياة الدنيا كمثل ريح فيها صر أصابت حرث قوم ظلموا أنفسهم فأهلكته ۚ وما ظلمهم الله ولكن أنفسهم يظلمون'),
(411, 3, 118, 'يا أيها الذين آمنوا لا تتخذوا بطانة من دونكم لا يألونكم خبالا ودوا ما عنتم قد بدت البغضاء من أفواههم وما تخفي صدورهم أكبر ۚ قد بينا لكم الآيات ۖ إن كنتم تعقلون'),
(412, 3, 119, 'ها أنتم أولاء تحبونهم ولا يحبونكم وتؤمنون بالكتاب كله وإذا لقوكم قالوا آمنا وإذا خلوا عضوا عليكم الأنامل من الغيظ ۚ قل موتوا بغيظكم ۗ إن الله عليم بذات الصدور'),
(413, 3, 120, 'إن تمسسكم حسنة تسؤهم وإن تصبكم سيئة يفرحوا بها ۖ وإن تصبروا وتتقوا لا يضركم كيدهم شيئا ۗ إن الله بما يعملون محيط'),
(414, 3, 121, 'وإذ غدوت من أهلك تبوئ المؤمنين مقاعد للقتال ۗ والله سميع عليم'),
(415, 3, 122, 'إذ همت طائفتان منكم أن تفشلا والله وليهما ۗ وعلى الله فليتوكل المؤمنون'),
(416, 3, 123, 'ولقد نصركم الله ببدر وأنتم أذلة ۖ فاتقوا الله لعلكم تشكرون'),
(417, 3, 124, 'إذ تقول للمؤمنين ألن يكفيكم أن يمدكم ربكم بثلاثة آلاف من الملائكة منزلين'),
(418, 3, 125, 'بلى ۚ إن تصبروا وتتقوا ويأتوكم من فورهم هذا يمددكم ربكم بخمسة آلاف من الملائكة مسومين'),
(419, 3, 126, 'وما جعله الله إلا بشرى لكم ولتطمئن قلوبكم به ۗ وما النصر إلا من عند الله العزيز الحكيم'),
(420, 3, 127, 'ليقطع طرفا من الذين كفروا أو يكبتهم فينقلبوا خائبين'),
(421, 3, 128, 'ليس لك من الأمر شيء أو يتوب عليهم أو يعذبهم فإنهم ظالمون'),
(422, 3, 129, 'ولله ما في السماوات وما في الأرض ۚ يغفر لمن يشاء ويعذب من يشاء ۚ والله غفور رحيم'),
(423, 3, 130, 'يا أيها الذين آمنوا لا تأكلوا الربا أضعافا مضاعفة ۖ واتقوا الله لعلكم تفلحون'),
(424, 3, 131, 'واتقوا النار التي أعدت للكافرين'),
(425, 3, 132, 'وأطيعوا الله والرسول لعلكم ترحمون'),
(426, 3, 133, 'وسارعوا إلى مغفرة من ربكم وجنة عرضها السماوات والأرض أعدت للمتقين'),
(427, 3, 134, 'الذين ينفقون في السراء والضراء والكاظمين الغيظ والعافين عن الناس ۗ والله يحب المحسنين'),
(428, 3, 135, 'والذين إذا فعلوا فاحشة أو ظلموا أنفسهم ذكروا الله فاستغفروا لذنوبهم ومن يغفر الذنوب إلا الله ولم يصروا على ما فعلوا وهم يعلمون'),
(429, 3, 136, 'أولئك جزاؤهم مغفرة من ربهم وجنات تجري من تحتها الأنهار خالدين فيها ۚ ونعم أجر العاملين'),
(430, 3, 137, 'قد خلت من قبلكم سنن فسيروا في الأرض فانظروا كيف كان عاقبة المكذبين'),
(431, 3, 138, 'هذا بيان للناس وهدى وموعظة للمتقين'),
(432, 3, 139, 'ولا تهنوا ولا تحزنوا وأنتم الأعلون إن كنتم مؤمنين'),
(433, 3, 140, 'إن يمسسكم قرح فقد مس القوم قرح مثله ۚ وتلك الأيام نداولها بين الناس وليعلم الله الذين آمنوا ويتخذ منكم شهداء ۗ والله لا يحب الظالمين'),
(434, 3, 141, 'وليمحص الله الذين آمنوا ويمحق الكافرين'),
(435, 3, 142, 'أم حسبتم أن تدخلوا الجنة ولما يعلم الله الذين جاهدوا منكم ويعلم الصابرين'),
(436, 3, 143, 'ولقد كنتم تمنون الموت من قبل أن تلقوه فقد رأيتموه وأنتم تنظرون'),
(437, 3, 144, 'وما محمد إلا رسول قد خلت من قبله الرسل ۚ أفإن مات أو قتل انقلبتم على أعقابكم ۚ ومن ينقلب على عقبيه فلن يضر الله شيئا ۗ وسيجزي الله الشاكرين'),
(438, 3, 145, 'وما كان لنفس أن تموت إلا بإذن الله كتابا مؤجلا ۗ ومن يرد ثواب الدنيا نؤته منها ومن يرد ثواب الآخرة نؤته منها ۚ وسنجزي الشاكرين'),
(439, 3, 146, 'وكأين من نبي قاتل معه ربيون كثير فما وهنوا لما أصابهم في سبيل الله وما ضعفوا وما استكانوا ۗ والله يحب الصابرين'),
(440, 3, 147, 'وما كان قولهم إلا أن قالوا ربنا اغفر لنا ذنوبنا وإسرافنا في أمرنا وثبت أقدامنا وانصرنا على القوم الكافرين'),
(441, 3, 148, 'فآتاهم الله ثواب الدنيا وحسن ثواب الآخرة ۗ والله يحب المحسنين'),
(442, 3, 149, 'يا أيها الذين آمنوا إن تطيعوا الذين كفروا يردوكم على أعقابكم فتنقلبوا خاسرين'),
(443, 3, 150, 'بل الله مولاكم ۖ وهو خير الناصرين'),
(444, 3, 151, 'سنلقي في قلوب الذين كفروا الرعب بما أشركوا بالله ما لم ينزل به سلطانا ۖ ومأواهم النار ۚ وبئس مثوى الظالمين'),
(445, 3, 152, 'ولقد صدقكم الله وعده إذ تحسونهم بإذنه ۖ حتى إذا فشلتم وتنازعتم في الأمر وعصيتم من بعد ما أراكم ما تحبون ۚ منكم من يريد الدنيا ومنكم من يريد الآخرة ۚ ثم صرفكم عنهم ليبتليكم ۖ ولقد عفا عنكم ۗ والله ذو فضل على المؤمنين'),
(446, 3, 153, 'إذ تصعدون ولا تلوون على أحد والرسول يدعوكم في أخراكم فأثابكم غما بغم لكيلا تحزنوا على ما فاتكم ولا ما أصابكم ۗ والله خبير بما تعملون'),
(447, 3, 154, 'ثم أنزل عليكم من بعد الغم أمنة نعاسا يغشى طائفة منكم ۖ وطائفة قد أهمتهم أنفسهم يظنون بالله غير الحق ظن الجاهلية ۖ يقولون هل لنا من الأمر من شيء ۗ قل إن الأمر كله لله ۗ يخفون في أنفسهم ما لا يبدون لك ۖ يقولون لو كان لنا من الأمر شيء ما قتلنا هاهنا ۗ قل لو كنتم في بيوتكم لبرز الذين كتب عليهم القتل إلى مضاجعهم ۖ وليبتلي الله ما في صدوركم وليمحص ما في قلوبكم ۗ والله عليم بذات الصدور'),
(448, 3, 155, 'إن الذين تولوا منكم يوم التقى الجمعان إنما استزلهم الشيطان ببعض ما كسبوا ۖ ولقد عفا الله عنهم ۗ إن الله غفور حليم'),
(449, 3, 156, 'يا أيها الذين آمنوا لا تكونوا كالذين كفروا وقالوا لإخوانهم إذا ضربوا في الأرض أو كانوا غزى لو كانوا عندنا ما ماتوا وما قتلوا ليجعل الله ذلك حسرة في قلوبهم ۗ والله يحيي ويميت ۗ والله بما تعملون بصير'),
(450, 3, 157, 'ولئن قتلتم في سبيل الله أو متم لمغفرة من الله ورحمة خير مما يجمعون'),
(451, 3, 158, 'ولئن متم أو قتلتم لإلى الله تحشرون'),
(452, 3, 159, 'فبما رحمة من الله لنت لهم ۖ ولو كنت فظا غليظ القلب لانفضوا من حولك ۖ فاعف عنهم واستغفر لهم وشاورهم في الأمر ۖ فإذا عزمت فتوكل على الله ۚ إن الله يحب المتوكلين'),
(453, 3, 160, 'إن ينصركم الله فلا غالب لكم ۖ وإن يخذلكم فمن ذا الذي ينصركم من بعده ۗ وعلى الله فليتوكل المؤمنون'),
(454, 3, 161, 'وما كان لنبي أن يغل ۚ ومن يغلل يأت بما غل يوم القيامة ۚ ثم توفى كل نفس ما كسبت وهم لا يظلمون'),
(455, 3, 162, 'أفمن اتبع رضوان الله كمن باء بسخط من الله ومأواه جهنم ۚ وبئس المصير'),
(456, 3, 163, 'هم درجات عند الله ۗ والله بصير بما يعملون'),
(457, 3, 164, 'لقد من الله على المؤمنين إذ بعث فيهم رسولا من أنفسهم يتلو عليهم آياته ويزكيهم ويعلمهم الكتاب والحكمة وإن كانوا من قبل لفي ضلال مبين'),
(458, 3, 165, 'أولما أصابتكم مصيبة قد أصبتم مثليها قلتم أنى هذا ۖ قل هو من عند أنفسكم ۗ إن الله على كل شيء قدير'),
(459, 3, 166, 'وما أصابكم يوم التقى الجمعان فبإذن الله وليعلم المؤمنين'),
(460, 3, 167, 'وليعلم الذين نافقوا ۚ وقيل لهم تعالوا قاتلوا في سبيل الله أو ادفعوا ۖ قالوا لو نعلم قتالا لاتبعناكم ۗ هم للكفر يومئذ أقرب منهم للإيمان ۚ يقولون بأفواههم ما ليس في قلوبهم ۗ والله أعلم بما يكتمون'),
(461, 3, 168, 'الذين قالوا لإخوانهم وقعدوا لو أطاعونا ما قتلوا ۗ قل فادرءوا عن أنفسكم الموت إن كنتم صادقين'),
(462, 3, 169, 'ولا تحسبن الذين قتلوا في سبيل الله أمواتا ۚ بل أحياء عند ربهم يرزقون'),
(463, 3, 170, 'فرحين بما آتاهم الله من فضله ويستبشرون بالذين لم يلحقوا بهم من خلفهم ألا خوف عليهم ولا هم يحزنون'),
(464, 3, 171, 'يستبشرون بنعمة من الله وفضل وأن الله لا يضيع أجر المؤمنين'),
(465, 3, 172, 'الذين استجابوا لله والرسول من بعد ما أصابهم القرح ۚ للذين أحسنوا منهم واتقوا أجر عظيم'),
(466, 3, 173, 'الذين قال لهم الناس إن الناس قد جمعوا لكم فاخشوهم فزادهم إيمانا وقالوا حسبنا الله ونعم الوكيل'),
(467, 3, 174, 'فانقلبوا بنعمة من الله وفضل لم يمسسهم سوء واتبعوا رضوان الله ۗ والله ذو فضل عظيم'),
(468, 3, 175, 'إنما ذلكم الشيطان يخوف أولياءه فلا تخافوهم وخافون إن كنتم مؤمنين'),
(469, 3, 176, 'ولا يحزنك الذين يسارعون في الكفر ۚ إنهم لن يضروا الله شيئا ۗ يريد الله ألا يجعل لهم حظا في الآخرة ۖ ولهم عذاب عظيم'),
(470, 3, 177, 'إن الذين اشتروا الكفر بالإيمان لن يضروا الله شيئا ولهم عذاب أليم'),
(471, 3, 178, 'ولا يحسبن الذين كفروا أنما نملي لهم خير لأنفسهم ۚ إنما نملي لهم ليزدادوا إثما ۚ ولهم عذاب مهين'),
(472, 3, 179, 'ما كان الله ليذر المؤمنين على ما أنتم عليه حتى يميز الخبيث من الطيب ۗ وما كان الله ليطلعكم على الغيب ولكن الله يجتبي من رسله من يشاء ۖ فآمنوا بالله ورسله ۚ وإن تؤمنوا وتتقوا فلكم أجر عظيم'),
(473, 3, 180, 'ولا يحسبن الذين يبخلون بما آتاهم الله من فضله هو خيرا لهم ۖ بل هو شر لهم ۖ سيطوقون ما بخلوا به يوم القيامة ۗ ولله ميراث السماوات والأرض ۗ والله بما تعملون خبير'),
(474, 3, 181, 'لقد سمع الله قول الذين قالوا إن الله فقير ونحن أغنياء ۘ سنكتب ما قالوا وقتلهم الأنبياء بغير حق ونقول ذوقوا عذاب الحريق'),
(475, 3, 182, 'ذلك بما قدمت أيديكم وأن الله ليس بظلام للعبيد'),
(476, 3, 183, 'الذين قالوا إن الله عهد إلينا ألا نؤمن لرسول حتى يأتينا بقربان تأكله النار ۗ قل قد جاءكم رسل من قبلي بالبينات وبالذي قلتم فلم قتلتموهم إن كنتم صادقين'),
(477, 3, 184, 'فإن كذبوك فقد كذب رسل من قبلك جاءوا بالبينات والزبر والكتاب المنير'),
(478, 3, 185, 'كل نفس ذائقة الموت ۗ وإنما توفون أجوركم يوم القيامة ۖ فمن زحزح عن النار وأدخل الجنة فقد فاز ۗ وما الحياة الدنيا إلا متاع الغرور'),
(479, 3, 186, 'لتبلون في أموالكم وأنفسكم ولتسمعن من الذين أوتوا الكتاب من قبلكم ومن الذين أشركوا أذى كثيرا ۚ وإن تصبروا وتتقوا فإن ذلك من عزم الأمور'),
(480, 3, 187, 'وإذ أخذ الله ميثاق الذين أوتوا الكتاب لتبيننه للناس ولا تكتمونه فنبذوه وراء ظهورهم واشتروا به ثمنا قليلا ۖ فبئس ما يشترون'),
(481, 3, 188, 'لا تحسبن الذين يفرحون بما أتوا ويحبون أن يحمدوا بما لم يفعلوا فلا تحسبنهم بمفازة من العذاب ۖ ولهم عذاب أليم'),
(482, 3, 189, 'ولله ملك السماوات والأرض ۗ والله على كل شيء قدير'),
(483, 3, 190, 'إن في خلق السماوات والأرض واختلاف الليل والنهار لآيات لأولي الألباب'),
(484, 3, 191, 'الذين يذكرون الله قياما وقعودا وعلى جنوبهم ويتفكرون في خلق السماوات والأرض ربنا ما خلقت هذا باطلا سبحانك فقنا عذاب النار'),
(485, 3, 192, 'ربنا إنك من تدخل النار فقد أخزيته ۖ وما للظالمين من أنصار'),
(486, 3, 193, 'ربنا إننا سمعنا مناديا ينادي للإيمان أن آمنوا بربكم فآمنا ۚ ربنا فاغفر لنا ذنوبنا وكفر عنا سيئاتنا وتوفنا مع الأبرار'),
(487, 3, 194, 'ربنا وآتنا ما وعدتنا على رسلك ولا تخزنا يوم القيامة ۗ إنك لا تخلف الميعاد'),
(488, 3, 195, 'فاستجاب لهم ربهم أني لا أضيع عمل عامل منكم من ذكر أو أنثى ۖ بعضكم من بعض ۖ فالذين هاجروا وأخرجوا من ديارهم وأوذوا في سبيلي وقاتلوا وقتلوا لأكفرن عنهم سيئاتهم ولأدخلنهم جنات تجري من تحتها الأنهار ثوابا من عند الله ۗ والله عنده حسن الثواب'),
(489, 3, 196, 'لا يغرنك تقلب الذين كفروا في البلاد'),
(490, 3, 197, 'متاع قليل ثم مأواهم جهنم ۚ وبئس المهاد'),
(491, 3, 198, 'لكن الذين اتقوا ربهم لهم جنات تجري من تحتها الأنهار خالدين فيها نزلا من عند الله ۗ وما عند الله خير للأبرار'),
(492, 3, 199, 'وإن من أهل الكتاب لمن يؤمن بالله وما أنزل إليكم وما أنزل إليهم خاشعين لله لا يشترون بآيات الله ثمنا قليلا ۗ أولئك لهم أجرهم عند ربهم ۗ إن الله سريع الحساب'),
(493, 3, 200, 'يا أيها الذين آمنوا اصبروا وصابروا ورابطوا واتقوا الله لعلكم تفلحون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'بسم الله الرحمن الرحيم يا أيها الناس اتقوا ربكم الذي خلقكم من نفس واحدة وخلق منها زوجها وبث منهما رجالا كثيرا ونساء ۚ واتقوا الله الذي تساءلون به والأرحام ۚ إن الله كان عليكم رقيبا'),
(495, 4, 2, 'وآتوا اليتامى أموالهم ۖ ولا تتبدلوا الخبيث بالطيب ۖ ولا تأكلوا أموالهم إلى أموالكم ۚ إنه كان حوبا كبيرا'),
(496, 4, 3, 'وإن خفتم ألا تقسطوا في اليتامى فانكحوا ما طاب لكم من النساء مثنى وثلاث ورباع ۖ فإن خفتم ألا تعدلوا فواحدة أو ما ملكت أيمانكم ۚ ذلك أدنى ألا تعولوا'),
(497, 4, 4, 'وآتوا النساء صدقاتهن نحلة ۚ فإن طبن لكم عن شيء منه نفسا فكلوه هنيئا مريئا'),
(498, 4, 5, 'ولا تؤتوا السفهاء أموالكم التي جعل الله لكم قياما وارزقوهم فيها واكسوهم وقولوا لهم قولا معروفا'),
(499, 4, 6, 'وابتلوا اليتامى حتى إذا بلغوا النكاح فإن آنستم منهم رشدا فادفعوا إليهم أموالهم ۖ ولا تأكلوها إسرافا وبدارا أن يكبروا ۚ ومن كان غنيا فليستعفف ۖ ومن كان فقيرا فليأكل بالمعروف ۚ فإذا دفعتم إليهم أموالهم فأشهدوا عليهم ۚ وكفى بالله حسيبا'),
(500, 4, 7, 'للرجال نصيب مما ترك الوالدان والأقربون وللنساء نصيب مما ترك الوالدان والأقربون مما قل منه أو كثر ۚ نصيبا مفروضا'),
(501, 4, 8, 'وإذا حضر القسمة أولو القربى واليتامى والمساكين فارزقوهم منه وقولوا لهم قولا معروفا'),
(502, 4, 9, 'وليخش الذين لو تركوا من خلفهم ذرية ضعافا خافوا عليهم فليتقوا الله وليقولوا قولا سديدا'),
(503, 4, 10, 'إن الذين يأكلون أموال اليتامى ظلما إنما يأكلون في بطونهم نارا ۖ وسيصلون سعيرا'),
(504, 4, 11, 'يوصيكم الله في أولادكم ۖ للذكر مثل حظ الأنثيين ۚ فإن كن نساء فوق اثنتين فلهن ثلثا ما ترك ۖ وإن كانت واحدة فلها النصف ۚ ولأبويه لكل واحد منهما السدس مما ترك إن كان له ولد ۚ فإن لم يكن له ولد وورثه أبواه فلأمه الثلث ۚ فإن كان له إخوة فلأمه السدس ۚ من بعد وصية يوصي بها أو دين ۗ آباؤكم وأبناؤكم لا تدرون أيهم أقرب لكم نفعا ۚ فريضة من الله ۗ إن الله كان عليما حكيما'),
(505, 4, 12, 'ولكم نصف ما ترك أزواجكم إن لم يكن لهن ولد ۚ فإن كان لهن ولد فلكم الربع مما تركن ۚ من بعد وصية يوصين بها أو دين ۚ ولهن الربع مما تركتم إن لم يكن لكم ولد ۚ فإن كان لكم ولد فلهن الثمن مما تركتم ۚ من بعد وصية توصون بها أو دين ۗ وإن كان رجل يورث كلالة أو امرأة وله أخ أو أخت فلكل واحد منهما السدس ۚ فإن كانوا أكثر من ذلك فهم شركاء في الثلث ۚ من بعد وصية يوصى بها أو دين غير مضار ۚ وصية من الله ۗ والله عليم حليم'),
(506, 4, 13, 'تلك حدود الله ۚ ومن يطع الله ورسوله يدخله جنات تجري من تحتها الأنهار خالدين فيها ۚ وذلك الفوز العظيم'),
(507, 4, 14, 'ومن يعص الله ورسوله ويتعد حدوده يدخله نارا خالدا فيها وله عذاب مهين'),
(508, 4, 15, 'واللاتي يأتين الفاحشة من نسائكم فاستشهدوا عليهن أربعة منكم ۖ فإن شهدوا فأمسكوهن في البيوت حتى يتوفاهن الموت أو يجعل الله لهن سبيلا'),
(509, 4, 16, 'واللذان يأتيانها منكم فآذوهما ۖ فإن تابا وأصلحا فأعرضوا عنهما ۗ إن الله كان توابا رحيما'),
(510, 4, 17, 'إنما التوبة على الله للذين يعملون السوء بجهالة ثم يتوبون من قريب فأولئك يتوب الله عليهم ۗ وكان الله عليما حكيما'),
(511, 4, 18, 'وليست التوبة للذين يعملون السيئات حتى إذا حضر أحدهم الموت قال إني تبت الآن ولا الذين يموتون وهم كفار ۚ أولئك أعتدنا لهم عذابا أليما'),
(512, 4, 19, 'يا أيها الذين آمنوا لا يحل لكم أن ترثوا النساء كرها ۖ ولا تعضلوهن لتذهبوا ببعض ما آتيتموهن إلا أن يأتين بفاحشة مبينة ۚ وعاشروهن بالمعروف ۚ فإن كرهتموهن فعسى أن تكرهوا شيئا ويجعل الله فيه خيرا كثيرا'),
(513, 4, 20, 'وإن أردتم استبدال زوج مكان زوج وآتيتم إحداهن قنطارا فلا تأخذوا منه شيئا ۚ أتأخذونه بهتانا وإثما مبينا'),
(514, 4, 21, 'وكيف تأخذونه وقد أفضى بعضكم إلى بعض وأخذن منكم ميثاقا غليظا'),
(515, 4, 22, 'ولا تنكحوا ما نكح آباؤكم من النساء إلا ما قد سلف ۚ إنه كان فاحشة ومقتا وساء سبيلا'),
(516, 4, 23, 'حرمت عليكم أمهاتكم وبناتكم وأخواتكم وعماتكم وخالاتكم وبنات الأخ وبنات الأخت وأمهاتكم اللاتي أرضعنكم وأخواتكم من الرضاعة وأمهات نسائكم وربائبكم اللاتي في حجوركم من نسائكم اللاتي دخلتم بهن فإن لم تكونوا دخلتم بهن فلا جناح عليكم وحلائل أبنائكم الذين من أصلابكم وأن تجمعوا بين الأختين إلا ما قد سلف ۗ إن الله كان غفورا رحيما'),
(517, 4, 24, 'والمحصنات من النساء إلا ما ملكت أيمانكم ۖ كتاب الله عليكم ۚ وأحل لكم ما وراء ذلكم أن تبتغوا بأموالكم محصنين غير مسافحين ۚ فما استمتعتم به منهن فآتوهن أجورهن فريضة ۚ ولا جناح عليكم فيما تراضيتم به من بعد الفريضة ۚ إن الله كان عليما حكيما'),
(518, 4, 25, 'ومن لم يستطع منكم طولا أن ينكح المحصنات المؤمنات فمن ما ملكت أيمانكم من فتياتكم المؤمنات ۚ والله أعلم بإيمانكم ۚ بعضكم من بعض ۚ فانكحوهن بإذن أهلهن وآتوهن أجورهن بالمعروف محصنات غير مسافحات ولا متخذات أخدان ۚ فإذا أحصن فإن أتين بفاحشة فعليهن نصف ما على المحصنات من العذاب ۚ ذلك لمن خشي العنت منكم ۚ وأن تصبروا خير لكم ۗ والله غفور رحيم'),
(519, 4, 26, 'يريد الله ليبين لكم ويهديكم سنن الذين من قبلكم ويتوب عليكم ۗ والله عليم حكيم'),
(520, 4, 27, 'والله يريد أن يتوب عليكم ويريد الذين يتبعون الشهوات أن تميلوا ميلا عظيما'),
(521, 4, 28, 'يريد الله أن يخفف عنكم ۚ وخلق الإنسان ضعيفا'),
(522, 4, 29, 'يا أيها الذين آمنوا لا تأكلوا أموالكم بينكم بالباطل إلا أن تكون تجارة عن تراض منكم ۚ ولا تقتلوا أنفسكم ۚ إن الله كان بكم رحيما'),
(523, 4, 30, 'ومن يفعل ذلك عدوانا وظلما فسوف نصليه نارا ۚ وكان ذلك على الله يسيرا'),
(524, 4, 31, 'إن تجتنبوا كبائر ما تنهون عنه نكفر عنكم سيئاتكم وندخلكم مدخلا كريما'),
(525, 4, 32, 'ولا تتمنوا ما فضل الله به بعضكم على بعض ۚ للرجال نصيب مما اكتسبوا ۖ وللنساء نصيب مما اكتسبن ۚ واسألوا الله من فضله ۗ إن الله كان بكل شيء عليما'),
(526, 4, 33, 'ولكل جعلنا موالي مما ترك الوالدان والأقربون ۚ والذين عقدت أيمانكم فآتوهم نصيبهم ۚ إن الله كان على كل شيء شهيدا'),
(527, 4, 34, 'الرجال قوامون على النساء بما فضل الله بعضهم على بعض وبما أنفقوا من أموالهم ۚ فالصالحات قانتات حافظات للغيب بما حفظ الله ۚ واللاتي تخافون نشوزهن فعظوهن واهجروهن في المضاجع واضربوهن ۖ فإن أطعنكم فلا تبغوا عليهن سبيلا ۗ إن الله كان عليا كبيرا'),
(528, 4, 35, 'وإن خفتم شقاق بينهما فابعثوا حكما من أهله وحكما من أهلها إن يريدا إصلاحا يوفق الله بينهما ۗ إن الله كان عليما خبيرا'),
(529, 4, 36, 'واعبدوا الله ولا تشركوا به شيئا ۖ وبالوالدين إحسانا وبذي القربى واليتامى والمساكين والجار ذي القربى والجار الجنب والصاحب بالجنب وابن السبيل وما ملكت أيمانكم ۗ إن الله لا يحب من كان مختالا فخورا'),
(530, 4, 37, 'الذين يبخلون ويأمرون الناس بالبخل ويكتمون ما آتاهم الله من فضله ۗ وأعتدنا للكافرين عذابا مهينا'),
(531, 4, 38, 'والذين ينفقون أموالهم رئاء الناس ولا يؤمنون بالله ولا باليوم الآخر ۗ ومن يكن الشيطان له قرينا فساء قرينا'),
(532, 4, 39, 'وماذا عليهم لو آمنوا بالله واليوم الآخر وأنفقوا مما رزقهم الله ۚ وكان الله بهم عليما'),
(533, 4, 40, 'إن الله لا يظلم مثقال ذرة ۖ وإن تك حسنة يضاعفها ويؤت من لدنه أجرا عظيما'),
(534, 4, 41, 'فكيف إذا جئنا من كل أمة بشهيد وجئنا بك على هؤلاء شهيدا'),
(535, 4, 42, 'يومئذ يود الذين كفروا وعصوا الرسول لو تسوى بهم الأرض ولا يكتمون الله حديثا'),
(536, 4, 43, 'يا أيها الذين آمنوا لا تقربوا الصلاة وأنتم سكارى حتى تعلموا ما تقولون ولا جنبا إلا عابري سبيل حتى تغتسلوا ۚ وإن كنتم مرضى أو على سفر أو جاء أحد منكم من الغائط أو لامستم النساء فلم تجدوا ماء فتيمموا صعيدا طيبا فامسحوا بوجوهكم وأيديكم ۗ إن الله كان عفوا غفورا'),
(537, 4, 44, 'ألم تر إلى الذين أوتوا نصيبا من الكتاب يشترون الضلالة ويريدون أن تضلوا السبيل'),
(538, 4, 45, 'والله أعلم بأعدائكم ۚ وكفى بالله وليا وكفى بالله نصيرا'),
(539, 4, 46, 'من الذين هادوا يحرفون الكلم عن مواضعه ويقولون سمعنا وعصينا واسمع غير مسمع وراعنا ليا بألسنتهم وطعنا في الدين ۚ ولو أنهم قالوا سمعنا وأطعنا واسمع وانظرنا لكان خيرا لهم وأقوم ولكن لعنهم الله بكفرهم فلا يؤمنون إلا قليلا'),
(540, 4, 47, 'يا أيها الذين أوتوا الكتاب آمنوا بما نزلنا مصدقا لما معكم من قبل أن نطمس وجوها فنردها على أدبارها أو نلعنهم كما لعنا أصحاب السبت ۚ وكان أمر الله مفعولا'),
(541, 4, 48, 'إن الله لا يغفر أن يشرك به ويغفر ما دون ذلك لمن يشاء ۚ ومن يشرك بالله فقد افترى إثما عظيما'),
(542, 4, 49, 'ألم تر إلى الذين يزكون أنفسهم ۚ بل الله يزكي من يشاء ولا يظلمون فتيلا'),
(543, 4, 50, 'انظر كيف يفترون على الله الكذب ۖ وكفى به إثما مبينا'),
(544, 4, 51, 'ألم تر إلى الذين أوتوا نصيبا من الكتاب يؤمنون بالجبت والطاغوت ويقولون للذين كفروا هؤلاء أهدى من الذين آمنوا سبيلا'),
(545, 4, 52, 'أولئك الذين لعنهم الله ۖ ومن يلعن الله فلن تجد له نصيرا'),
(546, 4, 53, 'أم لهم نصيب من الملك فإذا لا يؤتون الناس نقيرا'),
(547, 4, 54, 'أم يحسدون الناس على ما آتاهم الله من فضله ۖ فقد آتينا آل إبراهيم الكتاب والحكمة وآتيناهم ملكا عظيما'),
(548, 4, 55, 'فمنهم من آمن به ومنهم من صد عنه ۚ وكفى بجهنم سعيرا'),
(549, 4, 56, 'إن الذين كفروا بآياتنا سوف نصليهم نارا كلما نضجت جلودهم بدلناهم جلودا غيرها ليذوقوا العذاب ۗ إن الله كان عزيزا حكيما'),
(550, 4, 57, 'والذين آمنوا وعملوا الصالحات سندخلهم جنات تجري من تحتها الأنهار خالدين فيها أبدا ۖ لهم فيها أزواج مطهرة ۖ وندخلهم ظلا ظليلا'),
(551, 4, 58, 'إن الله يأمركم أن تؤدوا الأمانات إلى أهلها وإذا حكمتم بين الناس أن تحكموا بالعدل ۚ إن الله نعما يعظكم به ۗ إن الله كان سميعا بصيرا'),
(552, 4, 59, 'يا أيها الذين آمنوا أطيعوا الله وأطيعوا الرسول وأولي الأمر منكم ۖ فإن تنازعتم في شيء فردوه إلى الله والرسول إن كنتم تؤمنون بالله واليوم الآخر ۚ ذلك خير وأحسن تأويلا'),
(553, 4, 60, 'ألم تر إلى الذين يزعمون أنهم آمنوا بما أنزل إليك وما أنزل من قبلك يريدون أن يتحاكموا إلى الطاغوت وقد أمروا أن يكفروا به ويريد الشيطان أن يضلهم ضلالا بعيدا'),
(554, 4, 61, 'وإذا قيل لهم تعالوا إلى ما أنزل الله وإلى الرسول رأيت المنافقين يصدون عنك صدودا'),
(555, 4, 62, 'فكيف إذا أصابتهم مصيبة بما قدمت أيديهم ثم جاءوك يحلفون بالله إن أردنا إلا إحسانا وتوفيقا'),
(556, 4, 63, 'أولئك الذين يعلم الله ما في قلوبهم فأعرض عنهم وعظهم وقل لهم في أنفسهم قولا بليغا'),
(557, 4, 64, 'وما أرسلنا من رسول إلا ليطاع بإذن الله ۚ ولو أنهم إذ ظلموا أنفسهم جاءوك فاستغفروا الله واستغفر لهم الرسول لوجدوا الله توابا رحيما'),
(558, 4, 65, 'فلا وربك لا يؤمنون حتى يحكموك فيما شجر بينهم ثم لا يجدوا في أنفسهم حرجا مما قضيت ويسلموا تسليما'),
(559, 4, 66, 'ولو أنا كتبنا عليهم أن اقتلوا أنفسكم أو اخرجوا من دياركم ما فعلوه إلا قليل منهم ۖ ولو أنهم فعلوا ما يوعظون به لكان خيرا لهم وأشد تثبيتا'),
(560, 4, 67, 'وإذا لآتيناهم من لدنا أجرا عظيما'),
(561, 4, 68, 'ولهديناهم صراطا مستقيما'),
(562, 4, 69, 'ومن يطع الله والرسول فأولئك مع الذين أنعم الله عليهم من النبيين والصديقين والشهداء والصالحين ۚ وحسن أولئك رفيقا'),
(563, 4, 70, 'ذلك الفضل من الله ۚ وكفى بالله عليما'),
(564, 4, 71, 'يا أيها الذين آمنوا خذوا حذركم فانفروا ثبات أو انفروا جميعا'),
(565, 4, 72, 'وإن منكم لمن ليبطئن فإن أصابتكم مصيبة قال قد أنعم الله علي إذ لم أكن معهم شهيدا'),
(566, 4, 73, 'ولئن أصابكم فضل من الله ليقولن كأن لم تكن بينكم وبينه مودة يا ليتني كنت معهم فأفوز فوزا عظيما'),
(567, 4, 74, 'فليقاتل في سبيل الله الذين يشرون الحياة الدنيا بالآخرة ۚ ومن يقاتل في سبيل الله فيقتل أو يغلب فسوف نؤتيه أجرا عظيما'),
(568, 4, 75, 'وما لكم لا تقاتلون في سبيل الله والمستضعفين من الرجال والنساء والولدان الذين يقولون ربنا أخرجنا من هذه القرية الظالم أهلها واجعل لنا من لدنك وليا واجعل لنا من لدنك نصيرا'),
(569, 4, 76, 'الذين آمنوا يقاتلون في سبيل الله ۖ والذين كفروا يقاتلون في سبيل الطاغوت فقاتلوا أولياء الشيطان ۖ إن كيد الشيطان كان ضعيفا'),
(570, 4, 77, 'ألم تر إلى الذين قيل لهم كفوا أيديكم وأقيموا الصلاة وآتوا الزكاة فلما كتب عليهم القتال إذا فريق منهم يخشون الناس كخشية الله أو أشد خشية ۚ وقالوا ربنا لم كتبت علينا القتال لولا أخرتنا إلى أجل قريب ۗ قل متاع الدنيا قليل والآخرة خير لمن اتقى ولا تظلمون فتيلا'),
(571, 4, 78, 'أينما تكونوا يدرككم الموت ولو كنتم في بروج مشيدة ۗ وإن تصبهم حسنة يقولوا هذه من عند الله ۖ وإن تصبهم سيئة يقولوا هذه من عندك ۚ قل كل من عند الله ۖ فمال هؤلاء القوم لا يكادون يفقهون حديثا'),
(572, 4, 79, 'ما أصابك من حسنة فمن الله ۖ وما أصابك من سيئة فمن نفسك ۚ وأرسلناك للناس رسولا ۚ وكفى بالله شهيدا'),
(573, 4, 80, 'من يطع الرسول فقد أطاع الله ۖ ومن تولى فما أرسلناك عليهم حفيظا'),
(574, 4, 81, 'ويقولون طاعة فإذا برزوا من عندك بيت طائفة منهم غير الذي تقول ۖ والله يكتب ما يبيتون ۖ فأعرض عنهم وتوكل على الله ۚ وكفى بالله وكيلا'),
(575, 4, 82, 'أفلا يتدبرون القرآن ۚ ولو كان من عند غير الله لوجدوا فيه اختلافا كثيرا'),
(576, 4, 83, 'وإذا جاءهم أمر من الأمن أو الخوف أذاعوا به ۖ ولو ردوه إلى الرسول وإلى أولي الأمر منهم لعلمه الذين يستنبطونه منهم ۗ ولولا فضل الله عليكم ورحمته لاتبعتم الشيطان إلا قليلا'),
(577, 4, 84, 'فقاتل في سبيل الله لا تكلف إلا نفسك ۚ وحرض المؤمنين ۖ عسى الله أن يكف بأس الذين كفروا ۚ والله أشد بأسا وأشد تنكيلا'),
(578, 4, 85, 'من يشفع شفاعة حسنة يكن له نصيب منها ۖ ومن يشفع شفاعة سيئة يكن له كفل منها ۗ وكان الله على كل شيء مقيتا'),
(579, 4, 86, 'وإذا حييتم بتحية فحيوا بأحسن منها أو ردوها ۗ إن الله كان على كل شيء حسيبا'),
(580, 4, 87, 'الله لا إله إلا هو ۚ ليجمعنكم إلى يوم القيامة لا ريب فيه ۗ ومن أصدق من الله حديثا'),
(581, 4, 88, 'فما لكم في المنافقين فئتين والله أركسهم بما كسبوا ۚ أتريدون أن تهدوا من أضل الله ۖ ومن يضلل الله فلن تجد له سبيلا'),
(582, 4, 89, 'ودوا لو تكفرون كما كفروا فتكونون سواء ۖ فلا تتخذوا منهم أولياء حتى يهاجروا في سبيل الله ۚ فإن تولوا فخذوهم واقتلوهم حيث وجدتموهم ۖ ولا تتخذوا منهم وليا ولا نصيرا'),
(583, 4, 90, 'إلا الذين يصلون إلى قوم بينكم وبينهم ميثاق أو جاءوكم حصرت صدورهم أن يقاتلوكم أو يقاتلوا قومهم ۚ ولو شاء الله لسلطهم عليكم فلقاتلوكم ۚ فإن اعتزلوكم فلم يقاتلوكم وألقوا إليكم السلم فما جعل الله لكم عليهم سبيلا'),
(584, 4, 91, 'ستجدون آخرين يريدون أن يأمنوكم ويأمنوا قومهم كل ما ردوا إلى الفتنة أركسوا فيها ۚ فإن لم يعتزلوكم ويلقوا إليكم السلم ويكفوا أيديهم فخذوهم واقتلوهم حيث ثقفتموهم ۚ وأولئكم جعلنا لكم عليهم سلطانا مبينا'),
(585, 4, 92, 'وما كان لمؤمن أن يقتل مؤمنا إلا خطأ ۚ ومن قتل مؤمنا خطأ فتحرير رقبة مؤمنة ودية مسلمة إلى أهله إلا أن يصدقوا ۚ فإن كان من قوم عدو لكم وهو مؤمن فتحرير رقبة مؤمنة ۖ وإن كان من قوم بينكم وبينهم ميثاق فدية مسلمة إلى أهله وتحرير رقبة مؤمنة ۖ فمن لم يجد فصيام شهرين متتابعين توبة من الله ۗ وكان الله عليما حكيما'),
(586, 4, 93, 'ومن يقتل مؤمنا متعمدا فجزاؤه جهنم خالدا فيها وغضب الله عليه ولعنه وأعد له عذابا عظيما'),
(587, 4, 94, 'يا أيها الذين آمنوا إذا ضربتم في سبيل الله فتبينوا ولا تقولوا لمن ألقى إليكم السلام لست مؤمنا تبتغون عرض الحياة الدنيا فعند الله مغانم كثيرة ۚ كذلك كنتم من قبل فمن الله عليكم فتبينوا ۚ إن الله كان بما تعملون خبيرا'),
(588, 4, 95, 'لا يستوي القاعدون من المؤمنين غير أولي الضرر والمجاهدون في سبيل الله بأموالهم وأنفسهم ۚ فضل الله المجاهدين بأموالهم وأنفسهم على القاعدين درجة ۚ وكلا وعد الله الحسنى ۚ وفضل الله المجاهدين على القاعدين أجرا عظيما'),
(589, 4, 96, 'درجات منه ومغفرة ورحمة ۚ وكان الله غفورا رحيما'),
(590, 4, 97, 'إن الذين توفاهم الملائكة ظالمي أنفسهم قالوا فيم كنتم ۖ قالوا كنا مستضعفين في الأرض ۚ قالوا ألم تكن أرض الله واسعة فتهاجروا فيها ۚ فأولئك مأواهم جهنم ۖ وساءت مصيرا'),
(591, 4, 98, 'إلا المستضعفين من الرجال والنساء والولدان لا يستطيعون حيلة ولا يهتدون سبيلا'),
(592, 4, 99, 'فأولئك عسى الله أن يعفو عنهم ۚ وكان الله عفوا غفورا'),
(593, 4, 100, 'ومن يهاجر في سبيل الله يجد في الأرض مراغما كثيرا وسعة ۚ ومن يخرج من بيته مهاجرا إلى الله ورسوله ثم يدركه الموت فقد وقع أجره على الله ۗ وكان الله غفورا رحيما'),
(594, 4, 101, 'وإذا ضربتم في الأرض فليس عليكم جناح أن تقصروا من الصلاة إن خفتم أن يفتنكم الذين كفروا ۚ إن الكافرين كانوا لكم عدوا مبينا'),
(595, 4, 102, 'وإذا كنت فيهم فأقمت لهم الصلاة فلتقم طائفة منهم معك وليأخذوا أسلحتهم فإذا سجدوا فليكونوا من ورائكم ولتأت طائفة أخرى لم يصلوا فليصلوا معك وليأخذوا حذرهم وأسلحتهم ۗ ود الذين كفروا لو تغفلون عن أسلحتكم وأمتعتكم فيميلون عليكم ميلة واحدة ۚ ولا جناح عليكم إن كان بكم أذى من مطر أو كنتم مرضى أن تضعوا أسلحتكم ۖ وخذوا حذركم ۗ إن الله أعد للكافرين عذابا مهينا'),
(596, 4, 103, 'فإذا قضيتم الصلاة فاذكروا الله قياما وقعودا وعلى جنوبكم ۚ فإذا اطمأننتم فأقيموا الصلاة ۚ إن الصلاة كانت على المؤمنين كتابا موقوتا'),
(597, 4, 104, 'ولا تهنوا في ابتغاء القوم ۖ إن تكونوا تألمون فإنهم يألمون كما تألمون ۖ وترجون من الله ما لا يرجون ۗ وكان الله عليما حكيما'),
(598, 4, 105, 'إنا أنزلنا إليك الكتاب بالحق لتحكم بين الناس بما أراك الله ۚ ولا تكن للخائنين خصيما'),
(599, 4, 106, 'واستغفر الله ۖ إن الله كان غفورا رحيما'),
(600, 4, 107, 'ولا تجادل عن الذين يختانون أنفسهم ۚ إن الله لا يحب من كان خوانا أثيما'),
(601, 4, 108, 'يستخفون من الناس ولا يستخفون من الله وهو معهم إذ يبيتون ما لا يرضى من القول ۚ وكان الله بما يعملون محيطا'),
(602, 4, 109, 'ها أنتم هؤلاء جادلتم عنهم في الحياة الدنيا فمن يجادل الله عنهم يوم القيامة أم من يكون عليهم وكيلا'),
(603, 4, 110, 'ومن يعمل سوءا أو يظلم نفسه ثم يستغفر الله يجد الله غفورا رحيما'),
(604, 4, 111, 'ومن يكسب إثما فإنما يكسبه على نفسه ۚ وكان الله عليما حكيما'),
(605, 4, 112, 'ومن يكسب خطيئة أو إثما ثم يرم به بريئا فقد احتمل بهتانا وإثما مبينا'),
(606, 4, 113, 'ولولا فضل الله عليك ورحمته لهمت طائفة منهم أن يضلوك وما يضلون إلا أنفسهم ۖ وما يضرونك من شيء ۚ وأنزل الله عليك الكتاب والحكمة وعلمك ما لم تكن تعلم ۚ وكان فضل الله عليك عظيما'),
(607, 4, 114, 'لا خير في كثير من نجواهم إلا من أمر بصدقة أو معروف أو إصلاح بين الناس ۚ ومن يفعل ذلك ابتغاء مرضات الله فسوف نؤتيه أجرا عظيما'),
(608, 4, 115, 'ومن يشاقق الرسول من بعد ما تبين له الهدى ويتبع غير سبيل المؤمنين نوله ما تولى ونصله جهنم ۖ وساءت مصيرا'),
(609, 4, 116, 'إن الله لا يغفر أن يشرك به ويغفر ما دون ذلك لمن يشاء ۚ ومن يشرك بالله فقد ضل ضلالا بعيدا'),
(610, 4, 117, 'إن يدعون من دونه إلا إناثا وإن يدعون إلا شيطانا مريدا'),
(611, 4, 118, 'لعنه الله ۘ وقال لأتخذن من عبادك نصيبا مفروضا'),
(612, 4, 119, 'ولأضلنهم ولأمنينهم ولآمرنهم فليبتكن آذان الأنعام ولآمرنهم فليغيرن خلق الله ۚ ومن يتخذ الشيطان وليا من دون الله فقد خسر خسرانا مبينا'),
(613, 4, 120, 'يعدهم ويمنيهم ۖ وما يعدهم الشيطان إلا غرورا'),
(614, 4, 121, 'أولئك مأواهم جهنم ولا يجدون عنها محيصا'),
(615, 4, 122, 'والذين آمنوا وعملوا الصالحات سندخلهم جنات تجري من تحتها الأنهار خالدين فيها أبدا ۖ وعد الله حقا ۚ ومن أصدق من الله قيلا'),
(616, 4, 123, 'ليس بأمانيكم ولا أماني أهل الكتاب ۗ من يعمل سوءا يجز به ولا يجد له من دون الله وليا ولا نصيرا'),
(617, 4, 124, 'ومن يعمل من الصالحات من ذكر أو أنثى وهو مؤمن فأولئك يدخلون الجنة ولا يظلمون نقيرا'),
(618, 4, 125, 'ومن أحسن دينا ممن أسلم وجهه لله وهو محسن واتبع ملة إبراهيم حنيفا ۗ واتخذ الله إبراهيم خليلا'),
(619, 4, 126, 'ولله ما في السماوات وما في الأرض ۚ وكان الله بكل شيء محيطا'),
(620, 4, 127, 'ويستفتونك في النساء ۖ قل الله يفتيكم فيهن وما يتلى عليكم في الكتاب في يتامى النساء اللاتي لا تؤتونهن ما كتب لهن وترغبون أن تنكحوهن والمستضعفين من الولدان وأن تقوموا لليتامى بالقسط ۚ وما تفعلوا من خير فإن الله كان به عليما'),
(621, 4, 128, 'وإن امرأة خافت من بعلها نشوزا أو إعراضا فلا جناح عليهما أن يصلحا بينهما صلحا ۚ والصلح خير ۗ وأحضرت الأنفس الشح ۚ وإن تحسنوا وتتقوا فإن الله كان بما تعملون خبيرا'),
(622, 4, 129, 'ولن تستطيعوا أن تعدلوا بين النساء ولو حرصتم ۖ فلا تميلوا كل الميل فتذروها كالمعلقة ۚ وإن تصلحوا وتتقوا فإن الله كان غفورا رحيما'),
(623, 4, 130, 'وإن يتفرقا يغن الله كلا من سعته ۚ وكان الله واسعا حكيما'),
(624, 4, 131, 'ولله ما في السماوات وما في الأرض ۗ ولقد وصينا الذين أوتوا الكتاب من قبلكم وإياكم أن اتقوا الله ۚ وإن تكفروا فإن لله ما في السماوات وما في الأرض ۚ وكان الله غنيا حميدا'),
(625, 4, 132, 'ولله ما في السماوات وما في الأرض ۚ وكفى بالله وكيلا'),
(626, 4, 133, 'إن يشأ يذهبكم أيها الناس ويأت بآخرين ۚ وكان الله على ذلك قديرا'),
(627, 4, 134, 'من كان يريد ثواب الدنيا فعند الله ثواب الدنيا والآخرة ۚ وكان الله سميعا بصيرا'),
(628, 4, 135, 'يا أيها الذين آمنوا كونوا قوامين بالقسط شهداء لله ولو على أنفسكم أو الوالدين والأقربين ۚ إن يكن غنيا أو فقيرا فالله أولى بهما ۖ فلا تتبعوا الهوى أن تعدلوا ۚ وإن تلووا أو تعرضوا فإن الله كان بما تعملون خبيرا'),
(629, 4, 136, 'يا أيها الذين آمنوا آمنوا بالله ورسوله والكتاب الذي نزل على رسوله والكتاب الذي أنزل من قبل ۚ ومن يكفر بالله وملائكته وكتبه ورسله واليوم الآخر فقد ضل ضلالا بعيدا'),
(630, 4, 137, 'إن الذين آمنوا ثم كفروا ثم آمنوا ثم كفروا ثم ازدادوا كفرا لم يكن الله ليغفر لهم ولا ليهديهم سبيلا'),
(631, 4, 138, 'بشر المنافقين بأن لهم عذابا أليما'),
(632, 4, 139, 'الذين يتخذون الكافرين أولياء من دون المؤمنين ۚ أيبتغون عندهم العزة فإن العزة لله جميعا'),
(633, 4, 140, 'وقد نزل عليكم في الكتاب أن إذا سمعتم آيات الله يكفر بها ويستهزأ بها فلا تقعدوا معهم حتى يخوضوا في حديث غيره ۚ إنكم إذا مثلهم ۗ إن الله جامع المنافقين والكافرين في جهنم جميعا'),
(634, 4, 141, 'الذين يتربصون بكم فإن كان لكم فتح من الله قالوا ألم نكن معكم وإن كان للكافرين نصيب قالوا ألم نستحوذ عليكم ونمنعكم من المؤمنين ۚ فالله يحكم بينكم يوم القيامة ۗ ولن يجعل الله للكافرين على المؤمنين سبيلا'),
(635, 4, 142, 'إن المنافقين يخادعون الله وهو خادعهم وإذا قاموا إلى الصلاة قاموا كسالى يراءون الناس ولا يذكرون الله إلا قليلا'),
(636, 4, 143, 'مذبذبين بين ذلك لا إلى هؤلاء ولا إلى هؤلاء ۚ ومن يضلل الله فلن تجد له سبيلا'),
(637, 4, 144, 'يا أيها الذين آمنوا لا تتخذوا الكافرين أولياء من دون المؤمنين ۚ أتريدون أن تجعلوا لله عليكم سلطانا مبينا'),
(638, 4, 145, 'إن المنافقين في الدرك الأسفل من النار ولن تجد لهم نصيرا'),
(639, 4, 146, 'إلا الذين تابوا وأصلحوا واعتصموا بالله وأخلصوا دينهم لله فأولئك مع المؤمنين ۖ وسوف يؤت الله المؤمنين أجرا عظيما'),
(640, 4, 147, 'ما يفعل الله بعذابكم إن شكرتم وآمنتم ۚ وكان الله شاكرا عليما'),
(641, 4, 148, 'لا يحب الله الجهر بالسوء من القول إلا من ظلم ۚ وكان الله سميعا عليما'),
(642, 4, 149, 'إن تبدوا خيرا أو تخفوه أو تعفوا عن سوء فإن الله كان عفوا قديرا'),
(643, 4, 150, 'إن الذين يكفرون بالله ورسله ويريدون أن يفرقوا بين الله ورسله ويقولون نؤمن ببعض ونكفر ببعض ويريدون أن يتخذوا بين ذلك سبيلا'),
(644, 4, 151, 'أولئك هم الكافرون حقا ۚ وأعتدنا للكافرين عذابا مهينا'),
(645, 4, 152, 'والذين آمنوا بالله ورسله ولم يفرقوا بين أحد منهم أولئك سوف يؤتيهم أجورهم ۗ وكان الله غفورا رحيما'),
(646, 4, 153, 'يسألك أهل الكتاب أن تنزل عليهم كتابا من السماء ۚ فقد سألوا موسى أكبر من ذلك فقالوا أرنا الله جهرة فأخذتهم الصاعقة بظلمهم ۚ ثم اتخذوا العجل من بعد ما جاءتهم البينات فعفونا عن ذلك ۚ وآتينا موسى سلطانا مبينا'),
(647, 4, 154, 'ورفعنا فوقهم الطور بميثاقهم وقلنا لهم ادخلوا الباب سجدا وقلنا لهم لا تعدوا في السبت وأخذنا منهم ميثاقا غليظا'),
(648, 4, 155, 'فبما نقضهم ميثاقهم وكفرهم بآيات الله وقتلهم الأنبياء بغير حق وقولهم قلوبنا غلف ۚ بل طبع الله عليها بكفرهم فلا يؤمنون إلا قليلا'),
(649, 4, 156, 'وبكفرهم وقولهم على مريم بهتانا عظيما'),
(650, 4, 157, 'وقولهم إنا قتلنا المسيح عيسى ابن مريم رسول الله وما قتلوه وما صلبوه ولكن شبه لهم ۚ وإن الذين اختلفوا فيه لفي شك منه ۚ ما لهم به من علم إلا اتباع الظن ۚ وما قتلوه يقينا'),
(651, 4, 158, 'بل رفعه الله إليه ۚ وكان الله عزيزا حكيما'),
(652, 4, 159, 'وإن من أهل الكتاب إلا ليؤمنن به قبل موته ۖ ويوم القيامة يكون عليهم شهيدا'),
(653, 4, 160, 'فبظلم من الذين هادوا حرمنا عليهم طيبات أحلت لهم وبصدهم عن سبيل الله كثيرا'),
(654, 4, 161, 'وأخذهم الربا وقد نهوا عنه وأكلهم أموال الناس بالباطل ۚ وأعتدنا للكافرين منهم عذابا أليما'),
(655, 4, 162, 'لكن الراسخون في العلم منهم والمؤمنون يؤمنون بما أنزل إليك وما أنزل من قبلك ۚ والمقيمين الصلاة ۚ والمؤتون الزكاة والمؤمنون بالله واليوم الآخر أولئك سنؤتيهم أجرا عظيما'),
(656, 4, 163, 'إنا أوحينا إليك كما أوحينا إلى نوح والنبيين من بعده ۚ وأوحينا إلى إبراهيم وإسماعيل وإسحاق ويعقوب والأسباط وعيسى وأيوب ويونس وهارون وسليمان ۚ وآتينا داوود زبورا'),
(657, 4, 164, 'ورسلا قد قصصناهم عليك من قبل ورسلا لم نقصصهم عليك ۚ وكلم الله موسى تكليما'),
(658, 4, 165, 'رسلا مبشرين ومنذرين لئلا يكون للناس على الله حجة بعد الرسل ۚ وكان الله عزيزا حكيما'),
(659, 4, 166, 'لكن الله يشهد بما أنزل إليك ۖ أنزله بعلمه ۖ والملائكة يشهدون ۚ وكفى بالله شهيدا'),
(660, 4, 167, 'إن الذين كفروا وصدوا عن سبيل الله قد ضلوا ضلالا بعيدا'),
(661, 4, 168, 'إن الذين كفروا وظلموا لم يكن الله ليغفر لهم ولا ليهديهم طريقا'),
(662, 4, 169, 'إلا طريق جهنم خالدين فيها أبدا ۚ وكان ذلك على الله يسيرا'),
(663, 4, 170, 'يا أيها الناس قد جاءكم الرسول بالحق من ربكم فآمنوا خيرا لكم ۚ وإن تكفروا فإن لله ما في السماوات والأرض ۚ وكان الله عليما حكيما'),
(664, 4, 171, 'يا أهل الكتاب لا تغلوا في دينكم ولا تقولوا على الله إلا الحق ۚ إنما المسيح عيسى ابن مريم رسول الله وكلمته ألقاها إلى مريم وروح منه ۖ فآمنوا بالله ورسله ۖ ولا تقولوا ثلاثة ۚ انتهوا خيرا لكم ۚ إنما الله إله واحد ۖ سبحانه أن يكون له ولد ۘ له ما في السماوات وما في الأرض ۗ وكفى بالله وكيلا'),
(665, 4, 172, 'لن يستنكف المسيح أن يكون عبدا لله ولا الملائكة المقربون ۚ ومن يستنكف عن عبادته ويستكبر فسيحشرهم إليه جميعا'),
(666, 4, 173, 'فأما الذين آمنوا وعملوا الصالحات فيوفيهم أجورهم ويزيدهم من فضله ۖ وأما الذين استنكفوا واستكبروا فيعذبهم عذابا أليما ولا يجدون لهم من دون الله وليا ولا نصيرا'),
(667, 4, 174, 'يا أيها الناس قد جاءكم برهان من ربكم وأنزلنا إليكم نورا مبينا'),
(668, 4, 175, 'فأما الذين آمنوا بالله واعتصموا به فسيدخلهم في رحمة منه وفضل ويهديهم إليه صراطا مستقيما'),
(669, 4, 176, 'يستفتونك قل الله يفتيكم في الكلالة ۚ إن امرؤ هلك ليس له ولد وله أخت فلها نصف ما ترك ۚ وهو يرثها إن لم يكن لها ولد ۚ فإن كانتا اثنتين فلهما الثلثان مما ترك ۚ وإن كانوا إخوة رجالا ونساء فللذكر مثل حظ الأنثيين ۗ يبين الله لكم أن تضلوا ۗ والله بكل شيء عليم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'بسم الله الرحمن الرحيم يا أيها الذين آمنوا أوفوا بالعقود ۚ أحلت لكم بهيمة الأنعام إلا ما يتلى عليكم غير محلي الصيد وأنتم حرم ۗ إن الله يحكم ما يريد'),
(671, 5, 2, 'يا أيها الذين آمنوا لا تحلوا شعائر الله ولا الشهر الحرام ولا الهدي ولا القلائد ولا آمين البيت الحرام يبتغون فضلا من ربهم ورضوانا ۚ وإذا حللتم فاصطادوا ۚ ولا يجرمنكم شنآن قوم أن صدوكم عن المسجد الحرام أن تعتدوا ۘ وتعاونوا على البر والتقوى ۖ ولا تعاونوا على الإثم والعدوان ۚ واتقوا الله ۖ إن الله شديد العقاب'),
(672, 5, 3, 'حرمت عليكم الميتة والدم ولحم الخنزير وما أهل لغير الله به والمنخنقة والموقوذة والمتردية والنطيحة وما أكل السبع إلا ما ذكيتم وما ذبح على النصب وأن تستقسموا بالأزلام ۚ ذلكم فسق ۗ اليوم يئس الذين كفروا من دينكم فلا تخشوهم واخشون ۚ اليوم أكملت لكم دينكم وأتممت عليكم نعمتي ورضيت لكم الإسلام دينا ۚ فمن اضطر في مخمصة غير متجانف لإثم ۙ فإن الله غفور رحيم'),
(673, 5, 4, 'يسألونك ماذا أحل لهم ۖ قل أحل لكم الطيبات ۙ وما علمتم من الجوارح مكلبين تعلمونهن مما علمكم الله ۖ فكلوا مما أمسكن عليكم واذكروا اسم الله عليه ۖ واتقوا الله ۚ إن الله سريع الحساب'),
(674, 5, 5, 'اليوم أحل لكم الطيبات ۖ وطعام الذين أوتوا الكتاب حل لكم وطعامكم حل لهم ۖ والمحصنات من المؤمنات والمحصنات من الذين أوتوا الكتاب من قبلكم إذا آتيتموهن أجورهن محصنين غير مسافحين ولا متخذي أخدان ۗ ومن يكفر بالإيمان فقد حبط عمله وهو في الآخرة من الخاسرين'),
(675, 5, 6, 'يا أيها الذين آمنوا إذا قمتم إلى الصلاة فاغسلوا وجوهكم وأيديكم إلى المرافق وامسحوا برءوسكم وأرجلكم إلى الكعبين ۚ وإن كنتم جنبا فاطهروا ۚ وإن كنتم مرضى أو على سفر أو جاء أحد منكم من الغائط أو لامستم النساء فلم تجدوا ماء فتيمموا صعيدا طيبا فامسحوا بوجوهكم وأيديكم منه ۚ ما يريد الله ليجعل عليكم من حرج ولكن يريد ليطهركم وليتم نعمته عليكم لعلكم تشكرون'),
(676, 5, 7, 'واذكروا نعمة الله عليكم وميثاقه الذي واثقكم به إذ قلتم سمعنا وأطعنا ۖ واتقوا الله ۚ إن الله عليم بذات الصدور'),
(677, 5, 8, 'يا أيها الذين آمنوا كونوا قوامين لله شهداء بالقسط ۖ ولا يجرمنكم شنآن قوم على ألا تعدلوا ۚ اعدلوا هو أقرب للتقوى ۖ واتقوا الله ۚ إن الله خبير بما تعملون'),
(678, 5, 9, 'وعد الله الذين آمنوا وعملوا الصالحات ۙ لهم مغفرة وأجر عظيم'),
(679, 5, 10, 'والذين كفروا وكذبوا بآياتنا أولئك أصحاب الجحيم'),
(680, 5, 11, 'يا أيها الذين آمنوا اذكروا نعمت الله عليكم إذ هم قوم أن يبسطوا إليكم أيديهم فكف أيديهم عنكم ۖ واتقوا الله ۚ وعلى الله فليتوكل المؤمنون'),
(681, 5, 12, 'ولقد أخذ الله ميثاق بني إسرائيل وبعثنا منهم اثني عشر نقيبا ۖ وقال الله إني معكم ۖ لئن أقمتم الصلاة وآتيتم الزكاة وآمنتم برسلي وعزرتموهم وأقرضتم الله قرضا حسنا لأكفرن عنكم سيئاتكم ولأدخلنكم جنات تجري من تحتها الأنهار ۚ فمن كفر بعد ذلك منكم فقد ضل سواء السبيل'),
(682, 5, 13, 'فبما نقضهم ميثاقهم لعناهم وجعلنا قلوبهم قاسية ۖ يحرفون الكلم عن مواضعه ۙ ونسوا حظا مما ذكروا به ۚ ولا تزال تطلع على خائنة منهم إلا قليلا منهم ۖ فاعف عنهم واصفح ۚ إن الله يحب المحسنين'),
(683, 5, 14, 'ومن الذين قالوا إنا نصارى أخذنا ميثاقهم فنسوا حظا مما ذكروا به فأغرينا بينهم العداوة والبغضاء إلى يوم القيامة ۚ وسوف ينبئهم الله بما كانوا يصنعون'),
(684, 5, 15, 'يا أهل الكتاب قد جاءكم رسولنا يبين لكم كثيرا مما كنتم تخفون من الكتاب ويعفو عن كثير ۚ قد جاءكم من الله نور وكتاب مبين'),
(685, 5, 16, 'يهدي به الله من اتبع رضوانه سبل السلام ويخرجهم من الظلمات إلى النور بإذنه ويهديهم إلى صراط مستقيم'),
(686, 5, 17, 'لقد كفر الذين قالوا إن الله هو المسيح ابن مريم ۚ قل فمن يملك من الله شيئا إن أراد أن يهلك المسيح ابن مريم وأمه ومن في الأرض جميعا ۗ ولله ملك السماوات والأرض وما بينهما ۚ يخلق ما يشاء ۚ والله على كل شيء قدير'),
(687, 5, 18, 'وقالت اليهود والنصارى نحن أبناء الله وأحباؤه ۚ قل فلم يعذبكم بذنوبكم ۖ بل أنتم بشر ممن خلق ۚ يغفر لمن يشاء ويعذب من يشاء ۚ ولله ملك السماوات والأرض وما بينهما ۖ وإليه المصير'),
(688, 5, 19, 'يا أهل الكتاب قد جاءكم رسولنا يبين لكم على فترة من الرسل أن تقولوا ما جاءنا من بشير ولا نذير ۖ فقد جاءكم بشير ونذير ۗ والله على كل شيء قدير'),
(689, 5, 20, 'وإذ قال موسى لقومه يا قوم اذكروا نعمة الله عليكم إذ جعل فيكم أنبياء وجعلكم ملوكا وآتاكم ما لم يؤت أحدا من العالمين'),
(690, 5, 21, 'يا قوم ادخلوا الأرض المقدسة التي كتب الله لكم ولا ترتدوا على أدباركم فتنقلبوا خاسرين'),
(691, 5, 22, 'قالوا يا موسى إن فيها قوما جبارين وإنا لن ندخلها حتى يخرجوا منها فإن يخرجوا منها فإنا داخلون'),
(692, 5, 23, 'قال رجلان من الذين يخافون أنعم الله عليهما ادخلوا عليهم الباب فإذا دخلتموه فإنكم غالبون ۚ وعلى الله فتوكلوا إن كنتم مؤمنين'),
(693, 5, 24, 'قالوا يا موسى إنا لن ندخلها أبدا ما داموا فيها ۖ فاذهب أنت وربك فقاتلا إنا هاهنا قاعدون'),
(694, 5, 25, 'قال رب إني لا أملك إلا نفسي وأخي ۖ فافرق بيننا وبين القوم الفاسقين'),
(695, 5, 26, 'قال فإنها محرمة عليهم ۛ أربعين سنة ۛ يتيهون في الأرض ۚ فلا تأس على القوم الفاسقين'),
(696, 5, 27, 'واتل عليهم نبأ ابني آدم بالحق إذ قربا قربانا فتقبل من أحدهما ولم يتقبل من الآخر قال لأقتلنك ۖ قال إنما يتقبل الله من المتقين'),
(697, 5, 28, 'لئن بسطت إلي يدك لتقتلني ما أنا بباسط يدي إليك لأقتلك ۖ إني أخاف الله رب العالمين'),
(698, 5, 29, 'إني أريد أن تبوء بإثمي وإثمك فتكون من أصحاب النار ۚ وذلك جزاء الظالمين'),
(699, 5, 30, 'فطوعت له نفسه قتل أخيه فقتله فأصبح من الخاسرين'),
(700, 5, 31, 'فبعث الله غرابا يبحث في الأرض ليريه كيف يواري سوءة أخيه ۚ قال يا ويلتى أعجزت أن أكون مثل هذا الغراب فأواري سوءة أخي ۖ فأصبح من النادمين'),
(701, 5, 32, 'من أجل ذلك كتبنا على بني إسرائيل أنه من قتل نفسا بغير نفس أو فساد في الأرض فكأنما قتل الناس جميعا ومن أحياها فكأنما أحيا الناس جميعا ۚ ولقد جاءتهم رسلنا بالبينات ثم إن كثيرا منهم بعد ذلك في الأرض لمسرفون'),
(702, 5, 33, 'إنما جزاء الذين يحاربون الله ورسوله ويسعون في الأرض فسادا أن يقتلوا أو يصلبوا أو تقطع أيديهم وأرجلهم من خلاف أو ينفوا من الأرض ۚ ذلك لهم خزي في الدنيا ۖ ولهم في الآخرة عذاب عظيم'),
(703, 5, 34, 'إلا الذين تابوا من قبل أن تقدروا عليهم ۖ فاعلموا أن الله غفور رحيم'),
(704, 5, 35, 'يا أيها الذين آمنوا اتقوا الله وابتغوا إليه الوسيلة وجاهدوا في سبيله لعلكم تفلحون'),
(705, 5, 36, 'إن الذين كفروا لو أن لهم ما في الأرض جميعا ومثله معه ليفتدوا به من عذاب يوم القيامة ما تقبل منهم ۖ ولهم عذاب أليم'),
(706, 5, 37, 'يريدون أن يخرجوا من النار وما هم بخارجين منها ۖ ولهم عذاب مقيم'),
(707, 5, 38, 'والسارق والسارقة فاقطعوا أيديهما جزاء بما كسبا نكالا من الله ۗ والله عزيز حكيم'),
(708, 5, 39, 'فمن تاب من بعد ظلمه وأصلح فإن الله يتوب عليه ۗ إن الله غفور رحيم'),
(709, 5, 40, 'ألم تعلم أن الله له ملك السماوات والأرض يعذب من يشاء ويغفر لمن يشاء ۗ والله على كل شيء قدير'),
(710, 5, 41, 'يا أيها الرسول لا يحزنك الذين يسارعون في الكفر من الذين قالوا آمنا بأفواههم ولم تؤمن قلوبهم ۛ ومن الذين هادوا ۛ سماعون للكذب سماعون لقوم آخرين لم يأتوك ۖ يحرفون الكلم من بعد مواضعه ۖ يقولون إن أوتيتم هذا فخذوه وإن لم تؤتوه فاحذروا ۚ ومن يرد الله فتنته فلن تملك له من الله شيئا ۚ أولئك الذين لم يرد الله أن يطهر قلوبهم ۚ لهم في الدنيا خزي ۖ ولهم في الآخرة عذاب عظيم'),
(711, 5, 42, 'سماعون للكذب أكالون للسحت ۚ فإن جاءوك فاحكم بينهم أو أعرض عنهم ۖ وإن تعرض عنهم فلن يضروك شيئا ۖ وإن حكمت فاحكم بينهم بالقسط ۚ إن الله يحب المقسطين'),
(712, 5, 43, 'وكيف يحكمونك وعندهم التوراة فيها حكم الله ثم يتولون من بعد ذلك ۚ وما أولئك بالمؤمنين'),
(713, 5, 44, 'إنا أنزلنا التوراة فيها هدى ونور ۚ يحكم بها النبيون الذين أسلموا للذين هادوا والربانيون والأحبار بما استحفظوا من كتاب الله وكانوا عليه شهداء ۚ فلا تخشوا الناس واخشون ولا تشتروا بآياتي ثمنا قليلا ۚ ومن لم يحكم بما أنزل الله فأولئك هم الكافرون'),
(714, 5, 45, 'وكتبنا عليهم فيها أن النفس بالنفس والعين بالعين والأنف بالأنف والأذن بالأذن والسن بالسن والجروح قصاص ۚ فمن تصدق به فهو كفارة له ۚ ومن لم يحكم بما أنزل الله فأولئك هم الظالمون'),
(715, 5, 46, 'وقفينا على آثارهم بعيسى ابن مريم مصدقا لما بين يديه من التوراة ۖ وآتيناه الإنجيل فيه هدى ونور ومصدقا لما بين يديه من التوراة وهدى وموعظة للمتقين'),
(716, 5, 47, 'وليحكم أهل الإنجيل بما أنزل الله فيه ۚ ومن لم يحكم بما أنزل الله فأولئك هم الفاسقون'),
(717, 5, 48, 'وأنزلنا إليك الكتاب بالحق مصدقا لما بين يديه من الكتاب ومهيمنا عليه ۖ فاحكم بينهم بما أنزل الله ۖ ولا تتبع أهواءهم عما جاءك من الحق ۚ لكل جعلنا منكم شرعة ومنهاجا ۚ ولو شاء الله لجعلكم أمة واحدة ولكن ليبلوكم في ما آتاكم ۖ فاستبقوا الخيرات ۚ إلى الله مرجعكم جميعا فينبئكم بما كنتم فيه تختلفون'),
(718, 5, 49, 'وأن احكم بينهم بما أنزل الله ولا تتبع أهواءهم واحذرهم أن يفتنوك عن بعض ما أنزل الله إليك ۖ فإن تولوا فاعلم أنما يريد الله أن يصيبهم ببعض ذنوبهم ۗ وإن كثيرا من الناس لفاسقون'),
(719, 5, 50, 'أفحكم الجاهلية يبغون ۚ ومن أحسن من الله حكما لقوم يوقنون'),
(720, 5, 51, 'يا أيها الذين آمنوا لا تتخذوا اليهود والنصارى أولياء ۘ بعضهم أولياء بعض ۚ ومن يتولهم منكم فإنه منهم ۗ إن الله لا يهدي القوم الظالمين'),
(721, 5, 52, 'فترى الذين في قلوبهم مرض يسارعون فيهم يقولون نخشى أن تصيبنا دائرة ۚ فعسى الله أن يأتي بالفتح أو أمر من عنده فيصبحوا على ما أسروا في أنفسهم نادمين'),
(722, 5, 53, 'ويقول الذين آمنوا أهؤلاء الذين أقسموا بالله جهد أيمانهم ۙ إنهم لمعكم ۚ حبطت أعمالهم فأصبحوا خاسرين'),
(723, 5, 54, 'يا أيها الذين آمنوا من يرتد منكم عن دينه فسوف يأتي الله بقوم يحبهم ويحبونه أذلة على المؤمنين أعزة على الكافرين يجاهدون في سبيل الله ولا يخافون لومة لائم ۚ ذلك فضل الله يؤتيه من يشاء ۚ والله واسع عليم'),
(724, 5, 55, 'إنما وليكم الله ورسوله والذين آمنوا الذين يقيمون الصلاة ويؤتون الزكاة وهم راكعون'),
(725, 5, 56, 'ومن يتول الله ورسوله والذين آمنوا فإن حزب الله هم الغالبون'),
(726, 5, 57, 'يا أيها الذين آمنوا لا تتخذوا الذين اتخذوا دينكم هزوا ولعبا من الذين أوتوا الكتاب من قبلكم والكفار أولياء ۚ واتقوا الله إن كنتم مؤمنين'),
(727, 5, 58, 'وإذا ناديتم إلى الصلاة اتخذوها هزوا ولعبا ۚ ذلك بأنهم قوم لا يعقلون'),
(728, 5, 59, 'قل يا أهل الكتاب هل تنقمون منا إلا أن آمنا بالله وما أنزل إلينا وما أنزل من قبل وأن أكثركم فاسقون'),
(729, 5, 60, 'قل هل أنبئكم بشر من ذلك مثوبة عند الله ۚ من لعنه الله وغضب عليه وجعل منهم القردة والخنازير وعبد الطاغوت ۚ أولئك شر مكانا وأضل عن سواء السبيل'),
(730, 5, 61, 'وإذا جاءوكم قالوا آمنا وقد دخلوا بالكفر وهم قد خرجوا به ۚ والله أعلم بما كانوا يكتمون'),
(731, 5, 62, 'وترى كثيرا منهم يسارعون في الإثم والعدوان وأكلهم السحت ۚ لبئس ما كانوا يعملون'),
(732, 5, 63, 'لولا ينهاهم الربانيون والأحبار عن قولهم الإثم وأكلهم السحت ۚ لبئس ما كانوا يصنعون'),
(733, 5, 64, 'وقالت اليهود يد الله مغلولة ۚ غلت أيديهم ولعنوا بما قالوا ۘ بل يداه مبسوطتان ينفق كيف يشاء ۚ وليزيدن كثيرا منهم ما أنزل إليك من ربك طغيانا وكفرا ۚ وألقينا بينهم العداوة والبغضاء إلى يوم القيامة ۚ كلما أوقدوا نارا للحرب أطفأها الله ۚ ويسعون في الأرض فسادا ۚ والله لا يحب المفسدين'),
(734, 5, 65, 'ولو أن أهل الكتاب آمنوا واتقوا لكفرنا عنهم سيئاتهم ولأدخلناهم جنات النعيم'),
(735, 5, 66, 'ولو أنهم أقاموا التوراة والإنجيل وما أنزل إليهم من ربهم لأكلوا من فوقهم ومن تحت أرجلهم ۚ منهم أمة مقتصدة ۖ وكثير منهم ساء ما يعملون'),
(736, 5, 67, 'يا أيها الرسول بلغ ما أنزل إليك من ربك ۖ وإن لم تفعل فما بلغت رسالته ۚ والله يعصمك من الناس ۗ إن الله لا يهدي القوم الكافرين'),
(737, 5, 68, 'قل يا أهل الكتاب لستم على شيء حتى تقيموا التوراة والإنجيل وما أنزل إليكم من ربكم ۗ وليزيدن كثيرا منهم ما أنزل إليك من ربك طغيانا وكفرا ۖ فلا تأس على القوم الكافرين'),
(738, 5, 69, 'إن الذين آمنوا والذين هادوا والصابئون والنصارى من آمن بالله واليوم الآخر وعمل صالحا فلا خوف عليهم ولا هم يحزنون'),
(739, 5, 70, 'لقد أخذنا ميثاق بني إسرائيل وأرسلنا إليهم رسلا ۖ كلما جاءهم رسول بما لا تهوى أنفسهم فريقا كذبوا وفريقا يقتلون'),
(740, 5, 71, 'وحسبوا ألا تكون فتنة فعموا وصموا ثم تاب الله عليهم ثم عموا وصموا كثير منهم ۚ والله بصير بما يعملون'),
(741, 5, 72, 'لقد كفر الذين قالوا إن الله هو المسيح ابن مريم ۖ وقال المسيح يا بني إسرائيل اعبدوا الله ربي وربكم ۖ إنه من يشرك بالله فقد حرم الله عليه الجنة ومأواه النار ۖ وما للظالمين من أنصار'),
(742, 5, 73, 'لقد كفر الذين قالوا إن الله ثالث ثلاثة ۘ وما من إله إلا إله واحد ۚ وإن لم ينتهوا عما يقولون ليمسن الذين كفروا منهم عذاب أليم'),
(743, 5, 74, 'أفلا يتوبون إلى الله ويستغفرونه ۚ والله غفور رحيم'),
(744, 5, 75, 'ما المسيح ابن مريم إلا رسول قد خلت من قبله الرسل وأمه صديقة ۖ كانا يأكلان الطعام ۗ انظر كيف نبين لهم الآيات ثم انظر أنى يؤفكون'),
(745, 5, 76, 'قل أتعبدون من دون الله ما لا يملك لكم ضرا ولا نفعا ۚ والله هو السميع العليم'),
(746, 5, 77, 'قل يا أهل الكتاب لا تغلوا في دينكم غير الحق ولا تتبعوا أهواء قوم قد ضلوا من قبل وأضلوا كثيرا وضلوا عن سواء السبيل'),
(747, 5, 78, 'لعن الذين كفروا من بني إسرائيل على لسان داوود وعيسى ابن مريم ۚ ذلك بما عصوا وكانوا يعتدون'),
(748, 5, 79, 'كانوا لا يتناهون عن منكر فعلوه ۚ لبئس ما كانوا يفعلون'),
(749, 5, 80, 'ترى كثيرا منهم يتولون الذين كفروا ۚ لبئس ما قدمت لهم أنفسهم أن سخط الله عليهم وفي العذاب هم خالدون'),
(750, 5, 81, 'ولو كانوا يؤمنون بالله والنبي وما أنزل إليه ما اتخذوهم أولياء ولكن كثيرا منهم فاسقون'),
(751, 5, 82, 'لتجدن أشد الناس عداوة للذين آمنوا اليهود والذين أشركوا ۖ ولتجدن أقربهم مودة للذين آمنوا الذين قالوا إنا نصارى ۚ ذلك بأن منهم قسيسين ورهبانا وأنهم لا يستكبرون'),
(752, 5, 83, 'وإذا سمعوا ما أنزل إلى الرسول ترى أعينهم تفيض من الدمع مما عرفوا من الحق ۖ يقولون ربنا آمنا فاكتبنا مع الشاهدين'),
(753, 5, 84, 'وما لنا لا نؤمن بالله وما جاءنا من الحق ونطمع أن يدخلنا ربنا مع القوم الصالحين'),
(754, 5, 85, 'فأثابهم الله بما قالوا جنات تجري من تحتها الأنهار خالدين فيها ۚ وذلك جزاء المحسنين'),
(755, 5, 86, 'والذين كفروا وكذبوا بآياتنا أولئك أصحاب الجحيم'),
(756, 5, 87, 'يا أيها الذين آمنوا لا تحرموا طيبات ما أحل الله لكم ولا تعتدوا ۚ إن الله لا يحب المعتدين'),
(757, 5, 88, 'وكلوا مما رزقكم الله حلالا طيبا ۚ واتقوا الله الذي أنتم به مؤمنون'),
(758, 5, 89, 'لا يؤاخذكم الله باللغو في أيمانكم ولكن يؤاخذكم بما عقدتم الأيمان ۖ فكفارته إطعام عشرة مساكين من أوسط ما تطعمون أهليكم أو كسوتهم أو تحرير رقبة ۖ فمن لم يجد فصيام ثلاثة أيام ۚ ذلك كفارة أيمانكم إذا حلفتم ۚ واحفظوا أيمانكم ۚ كذلك يبين الله لكم آياته لعلكم تشكرون'),
(759, 5, 90, 'يا أيها الذين آمنوا إنما الخمر والميسر والأنصاب والأزلام رجس من عمل الشيطان فاجتنبوه لعلكم تفلحون'),
(760, 5, 91, 'إنما يريد الشيطان أن يوقع بينكم العداوة والبغضاء في الخمر والميسر ويصدكم عن ذكر الله وعن الصلاة ۖ فهل أنتم منتهون'),
(761, 5, 92, 'وأطيعوا الله وأطيعوا الرسول واحذروا ۚ فإن توليتم فاعلموا أنما على رسولنا البلاغ المبين'),
(762, 5, 93, 'ليس على الذين آمنوا وعملوا الصالحات جناح فيما طعموا إذا ما اتقوا وآمنوا وعملوا الصالحات ثم اتقوا وآمنوا ثم اتقوا وأحسنوا ۗ والله يحب المحسنين'),
(763, 5, 94, 'يا أيها الذين آمنوا ليبلونكم الله بشيء من الصيد تناله أيديكم ورماحكم ليعلم الله من يخافه بالغيب ۚ فمن اعتدى بعد ذلك فله عذاب أليم'),
(764, 5, 95, 'يا أيها الذين آمنوا لا تقتلوا الصيد وأنتم حرم ۚ ومن قتله منكم متعمدا فجزاء مثل ما قتل من النعم يحكم به ذوا عدل منكم هديا بالغ الكعبة أو كفارة طعام مساكين أو عدل ذلك صياما ليذوق وبال أمره ۗ عفا الله عما سلف ۚ ومن عاد فينتقم الله منه ۗ والله عزيز ذو انتقام'),
(765, 5, 96, 'أحل لكم صيد البحر وطعامه متاعا لكم وللسيارة ۖ وحرم عليكم صيد البر ما دمتم حرما ۗ واتقوا الله الذي إليه تحشرون'),
(766, 5, 97, 'جعل الله الكعبة البيت الحرام قياما للناس والشهر الحرام والهدي والقلائد ۚ ذلك لتعلموا أن الله يعلم ما في السماوات وما في الأرض وأن الله بكل شيء عليم'),
(767, 5, 98, 'اعلموا أن الله شديد العقاب وأن الله غفور رحيم'),
(768, 5, 99, 'ما على الرسول إلا البلاغ ۗ والله يعلم ما تبدون وما تكتمون'),
(769, 5, 100, 'قل لا يستوي الخبيث والطيب ولو أعجبك كثرة الخبيث ۚ فاتقوا الله يا أولي الألباب لعلكم تفلحون'),
(770, 5, 101, 'يا أيها الذين آمنوا لا تسألوا عن أشياء إن تبد لكم تسؤكم وإن تسألوا عنها حين ينزل القرآن تبد لكم عفا الله عنها ۗ والله غفور حليم'),
(771, 5, 102, 'قد سألها قوم من قبلكم ثم أصبحوا بها كافرين'),
(772, 5, 103, 'ما جعل الله من بحيرة ولا سائبة ولا وصيلة ولا حام ۙ ولكن الذين كفروا يفترون على الله الكذب ۖ وأكثرهم لا يعقلون'),
(773, 5, 104, 'وإذا قيل لهم تعالوا إلى ما أنزل الله وإلى الرسول قالوا حسبنا ما وجدنا عليه آباءنا ۚ أولو كان آباؤهم لا يعلمون شيئا ولا يهتدون'),
(774, 5, 105, 'يا أيها الذين آمنوا عليكم أنفسكم ۖ لا يضركم من ضل إذا اهتديتم ۚ إلى الله مرجعكم جميعا فينبئكم بما كنتم تعملون'),
(775, 5, 106, 'يا أيها الذين آمنوا شهادة بينكم إذا حضر أحدكم الموت حين الوصية اثنان ذوا عدل منكم أو آخران من غيركم إن أنتم ضربتم في الأرض فأصابتكم مصيبة الموت ۚ تحبسونهما من بعد الصلاة فيقسمان بالله إن ارتبتم لا نشتري به ثمنا ولو كان ذا قربى ۙ ولا نكتم شهادة الله إنا إذا لمن الآثمين'),
(776, 5, 107, 'فإن عثر على أنهما استحقا إثما فآخران يقومان مقامهما من الذين استحق عليهم الأوليان فيقسمان بالله لشهادتنا أحق من شهادتهما وما اعتدينا إنا إذا لمن الظالمين'),
(777, 5, 108, 'ذلك أدنى أن يأتوا بالشهادة على وجهها أو يخافوا أن ترد أيمان بعد أيمانهم ۗ واتقوا الله واسمعوا ۗ والله لا يهدي القوم الفاسقين'),
(778, 5, 109, 'يوم يجمع الله الرسل فيقول ماذا أجبتم ۖ قالوا لا علم لنا ۖ إنك أنت علام الغيوب'),
(779, 5, 110, 'إذ قال الله يا عيسى ابن مريم اذكر نعمتي عليك وعلى والدتك إذ أيدتك بروح القدس تكلم الناس في المهد وكهلا ۖ وإذ علمتك الكتاب والحكمة والتوراة والإنجيل ۖ وإذ تخلق من الطين كهيئة الطير بإذني فتنفخ فيها فتكون طيرا بإذني ۖ وتبرئ الأكمه والأبرص بإذني ۖ وإذ تخرج الموتى بإذني ۖ وإذ كففت بني إسرائيل عنك إذ جئتهم بالبينات فقال الذين كفروا منهم إن هذا إلا سحر مبين'),
(780, 5, 111, 'وإذ أوحيت إلى الحواريين أن آمنوا بي وبرسولي قالوا آمنا واشهد بأننا مسلمون'),
(781, 5, 112, 'إذ قال الحواريون يا عيسى ابن مريم هل يستطيع ربك أن ينزل علينا مائدة من السماء ۖ قال اتقوا الله إن كنتم مؤمنين'),
(782, 5, 113, 'قالوا نريد أن نأكل منها وتطمئن قلوبنا ونعلم أن قد صدقتنا ونكون عليها من الشاهدين'),
(783, 5, 114, 'قال عيسى ابن مريم اللهم ربنا أنزل علينا مائدة من السماء تكون لنا عيدا لأولنا وآخرنا وآية منك ۖ وارزقنا وأنت خير الرازقين'),
(784, 5, 115, 'قال الله إني منزلها عليكم ۖ فمن يكفر بعد منكم فإني أعذبه عذابا لا أعذبه أحدا من العالمين'),
(785, 5, 116, 'وإذ قال الله يا عيسى ابن مريم أأنت قلت للناس اتخذوني وأمي إلهين من دون الله ۖ قال سبحانك ما يكون لي أن أقول ما ليس لي بحق ۚ إن كنت قلته فقد علمته ۚ تعلم ما في نفسي ولا أعلم ما في نفسك ۚ إنك أنت علام الغيوب'),
(786, 5, 117, 'ما قلت لهم إلا ما أمرتني به أن اعبدوا الله ربي وربكم ۚ وكنت عليهم شهيدا ما دمت فيهم ۖ فلما توفيتني كنت أنت الرقيب عليهم ۚ وأنت على كل شيء شهيد'),
(787, 5, 118, 'إن تعذبهم فإنهم عبادك ۖ وإن تغفر لهم فإنك أنت العزيز الحكيم'),
(788, 5, 119, 'قال الله هذا يوم ينفع الصادقين صدقهم ۚ لهم جنات تجري من تحتها الأنهار خالدين فيها أبدا ۚ رضي الله عنهم ورضوا عنه ۚ ذلك الفوز العظيم'),
(789, 5, 120, 'لله ملك السماوات والأرض وما فيهن ۚ وهو على كل شيء قدير');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'بسم الله الرحمن الرحيم الحمد لله الذي خلق السماوات والأرض وجعل الظلمات والنور ۖ ثم الذين كفروا بربهم يعدلون'),
(791, 6, 2, 'هو الذي خلقكم من طين ثم قضى أجلا ۖ وأجل مسمى عنده ۖ ثم أنتم تمترون'),
(792, 6, 3, 'وهو الله في السماوات وفي الأرض ۖ يعلم سركم وجهركم ويعلم ما تكسبون'),
(793, 6, 4, 'وما تأتيهم من آية من آيات ربهم إلا كانوا عنها معرضين'),
(794, 6, 5, 'فقد كذبوا بالحق لما جاءهم ۖ فسوف يأتيهم أنباء ما كانوا به يستهزئون'),
(795, 6, 6, 'ألم يروا كم أهلكنا من قبلهم من قرن مكناهم في الأرض ما لم نمكن لكم وأرسلنا السماء عليهم مدرارا وجعلنا الأنهار تجري من تحتهم فأهلكناهم بذنوبهم وأنشأنا من بعدهم قرنا آخرين'),
(796, 6, 7, 'ولو نزلنا عليك كتابا في قرطاس فلمسوه بأيديهم لقال الذين كفروا إن هذا إلا سحر مبين'),
(797, 6, 8, 'وقالوا لولا أنزل عليه ملك ۖ ولو أنزلنا ملكا لقضي الأمر ثم لا ينظرون'),
(798, 6, 9, 'ولو جعلناه ملكا لجعلناه رجلا وللبسنا عليهم ما يلبسون'),
(799, 6, 10, 'ولقد استهزئ برسل من قبلك فحاق بالذين سخروا منهم ما كانوا به يستهزئون'),
(800, 6, 11, 'قل سيروا في الأرض ثم انظروا كيف كان عاقبة المكذبين'),
(801, 6, 12, 'قل لمن ما في السماوات والأرض ۖ قل لله ۚ كتب على نفسه الرحمة ۚ ليجمعنكم إلى يوم القيامة لا ريب فيه ۚ الذين خسروا أنفسهم فهم لا يؤمنون'),
(802, 6, 13, 'وله ما سكن في الليل والنهار ۚ وهو السميع العليم'),
(803, 6, 14, 'قل أغير الله أتخذ وليا فاطر السماوات والأرض وهو يطعم ولا يطعم ۗ قل إني أمرت أن أكون أول من أسلم ۖ ولا تكونن من المشركين'),
(804, 6, 15, 'قل إني أخاف إن عصيت ربي عذاب يوم عظيم'),
(805, 6, 16, 'من يصرف عنه يومئذ فقد رحمه ۚ وذلك الفوز المبين'),
(806, 6, 17, 'وإن يمسسك الله بضر فلا كاشف له إلا هو ۖ وإن يمسسك بخير فهو على كل شيء قدير'),
(807, 6, 18, 'وهو القاهر فوق عباده ۚ وهو الحكيم الخبير'),
(808, 6, 19, 'قل أي شيء أكبر شهادة ۖ قل الله ۖ شهيد بيني وبينكم ۚ وأوحي إلي هذا القرآن لأنذركم به ومن بلغ ۚ أئنكم لتشهدون أن مع الله آلهة أخرى ۚ قل لا أشهد ۚ قل إنما هو إله واحد وإنني بريء مما تشركون'),
(809, 6, 20, 'الذين آتيناهم الكتاب يعرفونه كما يعرفون أبناءهم ۘ الذين خسروا أنفسهم فهم لا يؤمنون'),
(810, 6, 21, 'ومن أظلم ممن افترى على الله كذبا أو كذب بآياته ۗ إنه لا يفلح الظالمون'),
(811, 6, 22, 'ويوم نحشرهم جميعا ثم نقول للذين أشركوا أين شركاؤكم الذين كنتم تزعمون'),
(812, 6, 23, 'ثم لم تكن فتنتهم إلا أن قالوا والله ربنا ما كنا مشركين'),
(813, 6, 24, 'انظر كيف كذبوا على أنفسهم ۚ وضل عنهم ما كانوا يفترون'),
(814, 6, 25, 'ومنهم من يستمع إليك ۖ وجعلنا على قلوبهم أكنة أن يفقهوه وفي آذانهم وقرا ۚ وإن يروا كل آية لا يؤمنوا بها ۚ حتى إذا جاءوك يجادلونك يقول الذين كفروا إن هذا إلا أساطير الأولين'),
(815, 6, 26, 'وهم ينهون عنه وينأون عنه ۖ وإن يهلكون إلا أنفسهم وما يشعرون'),
(816, 6, 27, 'ولو ترى إذ وقفوا على النار فقالوا يا ليتنا نرد ولا نكذب بآيات ربنا ونكون من المؤمنين'),
(817, 6, 28, 'بل بدا لهم ما كانوا يخفون من قبل ۖ ولو ردوا لعادوا لما نهوا عنه وإنهم لكاذبون'),
(818, 6, 29, 'وقالوا إن هي إلا حياتنا الدنيا وما نحن بمبعوثين'),
(819, 6, 30, 'ولو ترى إذ وقفوا على ربهم ۚ قال أليس هذا بالحق ۚ قالوا بلى وربنا ۚ قال فذوقوا العذاب بما كنتم تكفرون'),
(820, 6, 31, 'قد خسر الذين كذبوا بلقاء الله ۖ حتى إذا جاءتهم الساعة بغتة قالوا يا حسرتنا على ما فرطنا فيها وهم يحملون أوزارهم على ظهورهم ۚ ألا ساء ما يزرون'),
(821, 6, 32, 'وما الحياة الدنيا إلا لعب ولهو ۖ وللدار الآخرة خير للذين يتقون ۗ أفلا تعقلون'),
(822, 6, 33, 'قد نعلم إنه ليحزنك الذي يقولون ۖ فإنهم لا يكذبونك ولكن الظالمين بآيات الله يجحدون'),
(823, 6, 34, 'ولقد كذبت رسل من قبلك فصبروا على ما كذبوا وأوذوا حتى أتاهم نصرنا ۚ ولا مبدل لكلمات الله ۚ ولقد جاءك من نبإ المرسلين'),
(824, 6, 35, 'وإن كان كبر عليك إعراضهم فإن استطعت أن تبتغي نفقا في الأرض أو سلما في السماء فتأتيهم بآية ۚ ولو شاء الله لجمعهم على الهدى ۚ فلا تكونن من الجاهلين'),
(825, 6, 36, 'إنما يستجيب الذين يسمعون ۘ والموتى يبعثهم الله ثم إليه يرجعون'),
(826, 6, 37, 'وقالوا لولا نزل عليه آية من ربه ۚ قل إن الله قادر على أن ينزل آية ولكن أكثرهم لا يعلمون'),
(827, 6, 38, 'وما من دابة في الأرض ولا طائر يطير بجناحيه إلا أمم أمثالكم ۚ ما فرطنا في الكتاب من شيء ۚ ثم إلى ربهم يحشرون'),
(828, 6, 39, 'والذين كذبوا بآياتنا صم وبكم في الظلمات ۗ من يشإ الله يضلله ومن يشأ يجعله على صراط مستقيم'),
(829, 6, 40, 'قل أرأيتكم إن أتاكم عذاب الله أو أتتكم الساعة أغير الله تدعون إن كنتم صادقين'),
(830, 6, 41, 'بل إياه تدعون فيكشف ما تدعون إليه إن شاء وتنسون ما تشركون'),
(831, 6, 42, 'ولقد أرسلنا إلى أمم من قبلك فأخذناهم بالبأساء والضراء لعلهم يتضرعون'),
(832, 6, 43, 'فلولا إذ جاءهم بأسنا تضرعوا ولكن قست قلوبهم وزين لهم الشيطان ما كانوا يعملون'),
(833, 6, 44, 'فلما نسوا ما ذكروا به فتحنا عليهم أبواب كل شيء حتى إذا فرحوا بما أوتوا أخذناهم بغتة فإذا هم مبلسون'),
(834, 6, 45, 'فقطع دابر القوم الذين ظلموا ۚ والحمد لله رب العالمين'),
(835, 6, 46, 'قل أرأيتم إن أخذ الله سمعكم وأبصاركم وختم على قلوبكم من إله غير الله يأتيكم به ۗ انظر كيف نصرف الآيات ثم هم يصدفون'),
(836, 6, 47, 'قل أرأيتكم إن أتاكم عذاب الله بغتة أو جهرة هل يهلك إلا القوم الظالمون'),
(837, 6, 48, 'وما نرسل المرسلين إلا مبشرين ومنذرين ۖ فمن آمن وأصلح فلا خوف عليهم ولا هم يحزنون'),
(838, 6, 49, 'والذين كذبوا بآياتنا يمسهم العذاب بما كانوا يفسقون'),
(839, 6, 50, 'قل لا أقول لكم عندي خزائن الله ولا أعلم الغيب ولا أقول لكم إني ملك ۖ إن أتبع إلا ما يوحى إلي ۚ قل هل يستوي الأعمى والبصير ۚ أفلا تتفكرون'),
(840, 6, 51, 'وأنذر به الذين يخافون أن يحشروا إلى ربهم ۙ ليس لهم من دونه ولي ولا شفيع لعلهم يتقون'),
(841, 6, 52, 'ولا تطرد الذين يدعون ربهم بالغداة والعشي يريدون وجهه ۖ ما عليك من حسابهم من شيء وما من حسابك عليهم من شيء فتطردهم فتكون من الظالمين'),
(842, 6, 53, 'وكذلك فتنا بعضهم ببعض ليقولوا أهؤلاء من الله عليهم من بيننا ۗ أليس الله بأعلم بالشاكرين'),
(843, 6, 54, 'وإذا جاءك الذين يؤمنون بآياتنا فقل سلام عليكم ۖ كتب ربكم على نفسه الرحمة ۖ أنه من عمل منكم سوءا بجهالة ثم تاب من بعده وأصلح فأنه غفور رحيم'),
(844, 6, 55, 'وكذلك نفصل الآيات ولتستبين سبيل المجرمين'),
(845, 6, 56, 'قل إني نهيت أن أعبد الذين تدعون من دون الله ۚ قل لا أتبع أهواءكم ۙ قد ضللت إذا وما أنا من المهتدين'),
(846, 6, 57, 'قل إني على بينة من ربي وكذبتم به ۚ ما عندي ما تستعجلون به ۚ إن الحكم إلا لله ۖ يقص الحق ۖ وهو خير الفاصلين'),
(847, 6, 58, 'قل لو أن عندي ما تستعجلون به لقضي الأمر بيني وبينكم ۗ والله أعلم بالظالمين'),
(848, 6, 59, 'وعنده مفاتح الغيب لا يعلمها إلا هو ۚ ويعلم ما في البر والبحر ۚ وما تسقط من ورقة إلا يعلمها ولا حبة في ظلمات الأرض ولا رطب ولا يابس إلا في كتاب مبين'),
(849, 6, 60, 'وهو الذي يتوفاكم بالليل ويعلم ما جرحتم بالنهار ثم يبعثكم فيه ليقضى أجل مسمى ۖ ثم إليه مرجعكم ثم ينبئكم بما كنتم تعملون'),
(850, 6, 61, 'وهو القاهر فوق عباده ۖ ويرسل عليكم حفظة حتى إذا جاء أحدكم الموت توفته رسلنا وهم لا يفرطون'),
(851, 6, 62, 'ثم ردوا إلى الله مولاهم الحق ۚ ألا له الحكم وهو أسرع الحاسبين'),
(852, 6, 63, 'قل من ينجيكم من ظلمات البر والبحر تدعونه تضرعا وخفية لئن أنجانا من هذه لنكونن من الشاكرين'),
(853, 6, 64, 'قل الله ينجيكم منها ومن كل كرب ثم أنتم تشركون'),
(854, 6, 65, 'قل هو القادر على أن يبعث عليكم عذابا من فوقكم أو من تحت أرجلكم أو يلبسكم شيعا ويذيق بعضكم بأس بعض ۗ انظر كيف نصرف الآيات لعلهم يفقهون'),
(855, 6, 66, 'وكذب به قومك وهو الحق ۚ قل لست عليكم بوكيل'),
(856, 6, 67, 'لكل نبإ مستقر ۚ وسوف تعلمون'),
(857, 6, 68, 'وإذا رأيت الذين يخوضون في آياتنا فأعرض عنهم حتى يخوضوا في حديث غيره ۚ وإما ينسينك الشيطان فلا تقعد بعد الذكرى مع القوم الظالمين'),
(858, 6, 69, 'وما على الذين يتقون من حسابهم من شيء ولكن ذكرى لعلهم يتقون'),
(859, 6, 70, 'وذر الذين اتخذوا دينهم لعبا ولهوا وغرتهم الحياة الدنيا ۚ وذكر به أن تبسل نفس بما كسبت ليس لها من دون الله ولي ولا شفيع وإن تعدل كل عدل لا يؤخذ منها ۗ أولئك الذين أبسلوا بما كسبوا ۖ لهم شراب من حميم وعذاب أليم بما كانوا يكفرون'),
(860, 6, 71, 'قل أندعو من دون الله ما لا ينفعنا ولا يضرنا ونرد على أعقابنا بعد إذ هدانا الله كالذي استهوته الشياطين في الأرض حيران له أصحاب يدعونه إلى الهدى ائتنا ۗ قل إن هدى الله هو الهدى ۖ وأمرنا لنسلم لرب العالمين'),
(861, 6, 72, 'وأن أقيموا الصلاة واتقوه ۚ وهو الذي إليه تحشرون'),
(862, 6, 73, 'وهو الذي خلق السماوات والأرض بالحق ۖ ويوم يقول كن فيكون ۚ قوله الحق ۚ وله الملك يوم ينفخ في الصور ۚ عالم الغيب والشهادة ۚ وهو الحكيم الخبير'),
(863, 6, 74, 'وإذ قال إبراهيم لأبيه آزر أتتخذ أصناما آلهة ۖ إني أراك وقومك في ضلال مبين'),
(864, 6, 75, 'وكذلك نري إبراهيم ملكوت السماوات والأرض وليكون من الموقنين'),
(865, 6, 76, 'فلما جن عليه الليل رأى كوكبا ۖ قال هذا ربي ۖ فلما أفل قال لا أحب الآفلين'),
(866, 6, 77, 'فلما رأى القمر بازغا قال هذا ربي ۖ فلما أفل قال لئن لم يهدني ربي لأكونن من القوم الضالين'),
(867, 6, 78, 'فلما رأى الشمس بازغة قال هذا ربي هذا أكبر ۖ فلما أفلت قال يا قوم إني بريء مما تشركون'),
(868, 6, 79, 'إني وجهت وجهي للذي فطر السماوات والأرض حنيفا ۖ وما أنا من المشركين'),
(869, 6, 80, 'وحاجه قومه ۚ قال أتحاجوني في الله وقد هدان ۚ ولا أخاف ما تشركون به إلا أن يشاء ربي شيئا ۗ وسع ربي كل شيء علما ۗ أفلا تتذكرون'),
(870, 6, 81, 'وكيف أخاف ما أشركتم ولا تخافون أنكم أشركتم بالله ما لم ينزل به عليكم سلطانا ۚ فأي الفريقين أحق بالأمن ۖ إن كنتم تعلمون'),
(871, 6, 82, 'الذين آمنوا ولم يلبسوا إيمانهم بظلم أولئك لهم الأمن وهم مهتدون'),
(872, 6, 83, 'وتلك حجتنا آتيناها إبراهيم على قومه ۚ نرفع درجات من نشاء ۗ إن ربك حكيم عليم'),
(873, 6, 84, 'ووهبنا له إسحاق ويعقوب ۚ كلا هدينا ۚ ونوحا هدينا من قبل ۖ ومن ذريته داوود وسليمان وأيوب ويوسف وموسى وهارون ۚ وكذلك نجزي المحسنين'),
(874, 6, 85, 'وزكريا ويحيى وعيسى وإلياس ۖ كل من الصالحين'),
(875, 6, 86, 'وإسماعيل واليسع ويونس ولوطا ۚ وكلا فضلنا على العالمين'),
(876, 6, 87, 'ومن آبائهم وذرياتهم وإخوانهم ۖ واجتبيناهم وهديناهم إلى صراط مستقيم'),
(877, 6, 88, 'ذلك هدى الله يهدي به من يشاء من عباده ۚ ولو أشركوا لحبط عنهم ما كانوا يعملون'),
(878, 6, 89, 'أولئك الذين آتيناهم الكتاب والحكم والنبوة ۚ فإن يكفر بها هؤلاء فقد وكلنا بها قوما ليسوا بها بكافرين'),
(879, 6, 90, 'أولئك الذين هدى الله ۖ فبهداهم اقتده ۗ قل لا أسألكم عليه أجرا ۖ إن هو إلا ذكرى للعالمين'),
(880, 6, 91, 'وما قدروا الله حق قدره إذ قالوا ما أنزل الله على بشر من شيء ۗ قل من أنزل الكتاب الذي جاء به موسى نورا وهدى للناس ۖ تجعلونه قراطيس تبدونها وتخفون كثيرا ۖ وعلمتم ما لم تعلموا أنتم ولا آباؤكم ۖ قل الله ۖ ثم ذرهم في خوضهم يلعبون'),
(881, 6, 92, 'وهذا كتاب أنزلناه مبارك مصدق الذي بين يديه ولتنذر أم القرى ومن حولها ۚ والذين يؤمنون بالآخرة يؤمنون به ۖ وهم على صلاتهم يحافظون'),
(882, 6, 93, 'ومن أظلم ممن افترى على الله كذبا أو قال أوحي إلي ولم يوح إليه شيء ومن قال سأنزل مثل ما أنزل الله ۗ ولو ترى إذ الظالمون في غمرات الموت والملائكة باسطو أيديهم أخرجوا أنفسكم ۖ اليوم تجزون عذاب الهون بما كنتم تقولون على الله غير الحق وكنتم عن آياته تستكبرون'),
(883, 6, 94, 'ولقد جئتمونا فرادى كما خلقناكم أول مرة وتركتم ما خولناكم وراء ظهوركم ۖ وما نرى معكم شفعاءكم الذين زعمتم أنهم فيكم شركاء ۚ لقد تقطع بينكم وضل عنكم ما كنتم تزعمون'),
(884, 6, 95, 'إن الله فالق الحب والنوى ۖ يخرج الحي من الميت ومخرج الميت من الحي ۚ ذلكم الله ۖ فأنى تؤفكون'),
(885, 6, 96, 'فالق الإصباح وجعل الليل سكنا والشمس والقمر حسبانا ۚ ذلك تقدير العزيز العليم'),
(886, 6, 97, 'وهو الذي جعل لكم النجوم لتهتدوا بها في ظلمات البر والبحر ۗ قد فصلنا الآيات لقوم يعلمون'),
(887, 6, 98, 'وهو الذي أنشأكم من نفس واحدة فمستقر ومستودع ۗ قد فصلنا الآيات لقوم يفقهون'),
(888, 6, 99, 'وهو الذي أنزل من السماء ماء فأخرجنا به نبات كل شيء فأخرجنا منه خضرا نخرج منه حبا متراكبا ومن النخل من طلعها قنوان دانية وجنات من أعناب والزيتون والرمان مشتبها وغير متشابه ۗ انظروا إلى ثمره إذا أثمر وينعه ۚ إن في ذلكم لآيات لقوم يؤمنون'),
(889, 6, 100, 'وجعلوا لله شركاء الجن وخلقهم ۖ وخرقوا له بنين وبنات بغير علم ۚ سبحانه وتعالى عما يصفون'),
(890, 6, 101, 'بديع السماوات والأرض ۖ أنى يكون له ولد ولم تكن له صاحبة ۖ وخلق كل شيء ۖ وهو بكل شيء عليم'),
(891, 6, 102, 'ذلكم الله ربكم ۖ لا إله إلا هو ۖ خالق كل شيء فاعبدوه ۚ وهو على كل شيء وكيل'),
(892, 6, 103, 'لا تدركه الأبصار وهو يدرك الأبصار ۖ وهو اللطيف الخبير'),
(893, 6, 104, 'قد جاءكم بصائر من ربكم ۖ فمن أبصر فلنفسه ۖ ومن عمي فعليها ۚ وما أنا عليكم بحفيظ'),
(894, 6, 105, 'وكذلك نصرف الآيات وليقولوا درست ولنبينه لقوم يعلمون'),
(895, 6, 106, 'اتبع ما أوحي إليك من ربك ۖ لا إله إلا هو ۖ وأعرض عن المشركين'),
(896, 6, 107, 'ولو شاء الله ما أشركوا ۗ وما جعلناك عليهم حفيظا ۖ وما أنت عليهم بوكيل'),
(897, 6, 108, 'ولا تسبوا الذين يدعون من دون الله فيسبوا الله عدوا بغير علم ۗ كذلك زينا لكل أمة عملهم ثم إلى ربهم مرجعهم فينبئهم بما كانوا يعملون'),
(898, 6, 109, 'وأقسموا بالله جهد أيمانهم لئن جاءتهم آية ليؤمنن بها ۚ قل إنما الآيات عند الله ۖ وما يشعركم أنها إذا جاءت لا يؤمنون'),
(899, 6, 110, 'ونقلب أفئدتهم وأبصارهم كما لم يؤمنوا به أول مرة ونذرهم في طغيانهم يعمهون'),
(900, 6, 111, 'ولو أننا نزلنا إليهم الملائكة وكلمهم الموتى وحشرنا عليهم كل شيء قبلا ما كانوا ليؤمنوا إلا أن يشاء الله ولكن أكثرهم يجهلون'),
(901, 6, 112, 'وكذلك جعلنا لكل نبي عدوا شياطين الإنس والجن يوحي بعضهم إلى بعض زخرف القول غرورا ۚ ولو شاء ربك ما فعلوه ۖ فذرهم وما يفترون'),
(902, 6, 113, 'ولتصغى إليه أفئدة الذين لا يؤمنون بالآخرة وليرضوه وليقترفوا ما هم مقترفون'),
(903, 6, 114, 'أفغير الله أبتغي حكما وهو الذي أنزل إليكم الكتاب مفصلا ۚ والذين آتيناهم الكتاب يعلمون أنه منزل من ربك بالحق ۖ فلا تكونن من الممترين'),
(904, 6, 115, 'وتمت كلمت ربك صدقا وعدلا ۚ لا مبدل لكلماته ۚ وهو السميع العليم'),
(905, 6, 116, 'وإن تطع أكثر من في الأرض يضلوك عن سبيل الله ۚ إن يتبعون إلا الظن وإن هم إلا يخرصون'),
(906, 6, 117, 'إن ربك هو أعلم من يضل عن سبيله ۖ وهو أعلم بالمهتدين'),
(907, 6, 118, 'فكلوا مما ذكر اسم الله عليه إن كنتم بآياته مؤمنين'),
(908, 6, 119, 'وما لكم ألا تأكلوا مما ذكر اسم الله عليه وقد فصل لكم ما حرم عليكم إلا ما اضطررتم إليه ۗ وإن كثيرا ليضلون بأهوائهم بغير علم ۗ إن ربك هو أعلم بالمعتدين'),
(909, 6, 120, 'وذروا ظاهر الإثم وباطنه ۚ إن الذين يكسبون الإثم سيجزون بما كانوا يقترفون'),
(910, 6, 121, 'ولا تأكلوا مما لم يذكر اسم الله عليه وإنه لفسق ۗ وإن الشياطين ليوحون إلى أوليائهم ليجادلوكم ۖ وإن أطعتموهم إنكم لمشركون'),
(911, 6, 122, 'أومن كان ميتا فأحييناه وجعلنا له نورا يمشي به في الناس كمن مثله في الظلمات ليس بخارج منها ۚ كذلك زين للكافرين ما كانوا يعملون'),
(912, 6, 123, 'وكذلك جعلنا في كل قرية أكابر مجرميها ليمكروا فيها ۖ وما يمكرون إلا بأنفسهم وما يشعرون'),
(913, 6, 124, 'وإذا جاءتهم آية قالوا لن نؤمن حتى نؤتى مثل ما أوتي رسل الله ۘ الله أعلم حيث يجعل رسالته ۗ سيصيب الذين أجرموا صغار عند الله وعذاب شديد بما كانوا يمكرون'),
(914, 6, 125, 'فمن يرد الله أن يهديه يشرح صدره للإسلام ۖ ومن يرد أن يضله يجعل صدره ضيقا حرجا كأنما يصعد في السماء ۚ كذلك يجعل الله الرجس على الذين لا يؤمنون'),
(915, 6, 126, 'وهذا صراط ربك مستقيما ۗ قد فصلنا الآيات لقوم يذكرون'),
(916, 6, 127, 'لهم دار السلام عند ربهم ۖ وهو وليهم بما كانوا يعملون'),
(917, 6, 128, 'ويوم يحشرهم جميعا يا معشر الجن قد استكثرتم من الإنس ۖ وقال أولياؤهم من الإنس ربنا استمتع بعضنا ببعض وبلغنا أجلنا الذي أجلت لنا ۚ قال النار مثواكم خالدين فيها إلا ما شاء الله ۗ إن ربك حكيم عليم'),
(918, 6, 129, 'وكذلك نولي بعض الظالمين بعضا بما كانوا يكسبون'),
(919, 6, 130, 'يا معشر الجن والإنس ألم يأتكم رسل منكم يقصون عليكم آياتي وينذرونكم لقاء يومكم هذا ۚ قالوا شهدنا على أنفسنا ۖ وغرتهم الحياة الدنيا وشهدوا على أنفسهم أنهم كانوا كافرين'),
(920, 6, 131, 'ذلك أن لم يكن ربك مهلك القرى بظلم وأهلها غافلون'),
(921, 6, 132, 'ولكل درجات مما عملوا ۚ وما ربك بغافل عما يعملون'),
(922, 6, 133, 'وربك الغني ذو الرحمة ۚ إن يشأ يذهبكم ويستخلف من بعدكم ما يشاء كما أنشأكم من ذرية قوم آخرين'),
(923, 6, 134, 'إن ما توعدون لآت ۖ وما أنتم بمعجزين'),
(924, 6, 135, 'قل يا قوم اعملوا على مكانتكم إني عامل ۖ فسوف تعلمون من تكون له عاقبة الدار ۗ إنه لا يفلح الظالمون'),
(925, 6, 136, 'وجعلوا لله مما ذرأ من الحرث والأنعام نصيبا فقالوا هذا لله بزعمهم وهذا لشركائنا ۖ فما كان لشركائهم فلا يصل إلى الله ۖ وما كان لله فهو يصل إلى شركائهم ۗ ساء ما يحكمون'),
(926, 6, 137, 'وكذلك زين لكثير من المشركين قتل أولادهم شركاؤهم ليردوهم وليلبسوا عليهم دينهم ۖ ولو شاء الله ما فعلوه ۖ فذرهم وما يفترون'),
(927, 6, 138, 'وقالوا هذه أنعام وحرث حجر لا يطعمها إلا من نشاء بزعمهم وأنعام حرمت ظهورها وأنعام لا يذكرون اسم الله عليها افتراء عليه ۚ سيجزيهم بما كانوا يفترون'),
(928, 6, 139, 'وقالوا ما في بطون هذه الأنعام خالصة لذكورنا ومحرم على أزواجنا ۖ وإن يكن ميتة فهم فيه شركاء ۚ سيجزيهم وصفهم ۚ إنه حكيم عليم'),
(929, 6, 140, 'قد خسر الذين قتلوا أولادهم سفها بغير علم وحرموا ما رزقهم الله افتراء على الله ۚ قد ضلوا وما كانوا مهتدين'),
(930, 6, 141, 'وهو الذي أنشأ جنات معروشات وغير معروشات والنخل والزرع مختلفا أكله والزيتون والرمان متشابها وغير متشابه ۚ كلوا من ثمره إذا أثمر وآتوا حقه يوم حصاده ۖ ولا تسرفوا ۚ إنه لا يحب المسرفين'),
(931, 6, 142, 'ومن الأنعام حمولة وفرشا ۚ كلوا مما رزقكم الله ولا تتبعوا خطوات الشيطان ۚ إنه لكم عدو مبين'),
(932, 6, 143, 'ثمانية أزواج ۖ من الضأن اثنين ومن المعز اثنين ۗ قل آلذكرين حرم أم الأنثيين أما اشتملت عليه أرحام الأنثيين ۖ نبئوني بعلم إن كنتم صادقين'),
(933, 6, 144, 'ومن الإبل اثنين ومن البقر اثنين ۗ قل آلذكرين حرم أم الأنثيين أما اشتملت عليه أرحام الأنثيين ۖ أم كنتم شهداء إذ وصاكم الله بهذا ۚ فمن أظلم ممن افترى على الله كذبا ليضل الناس بغير علم ۗ إن الله لا يهدي القوم الظالمين'),
(934, 6, 145, 'قل لا أجد في ما أوحي إلي محرما على طاعم يطعمه إلا أن يكون ميتة أو دما مسفوحا أو لحم خنزير فإنه رجس أو فسقا أهل لغير الله به ۚ فمن اضطر غير باغ ولا عاد فإن ربك غفور رحيم'),
(935, 6, 146, 'وعلى الذين هادوا حرمنا كل ذي ظفر ۖ ومن البقر والغنم حرمنا عليهم شحومهما إلا ما حملت ظهورهما أو الحوايا أو ما اختلط بعظم ۚ ذلك جزيناهم ببغيهم ۖ وإنا لصادقون'),
(936, 6, 147, 'فإن كذبوك فقل ربكم ذو رحمة واسعة ولا يرد بأسه عن القوم المجرمين'),
(937, 6, 148, 'سيقول الذين أشركوا لو شاء الله ما أشركنا ولا آباؤنا ولا حرمنا من شيء ۚ كذلك كذب الذين من قبلهم حتى ذاقوا بأسنا ۗ قل هل عندكم من علم فتخرجوه لنا ۖ إن تتبعون إلا الظن وإن أنتم إلا تخرصون'),
(938, 6, 149, 'قل فلله الحجة البالغة ۖ فلو شاء لهداكم أجمعين'),
(939, 6, 150, 'قل هلم شهداءكم الذين يشهدون أن الله حرم هذا ۖ فإن شهدوا فلا تشهد معهم ۚ ولا تتبع أهواء الذين كذبوا بآياتنا والذين لا يؤمنون بالآخرة وهم بربهم يعدلون'),
(940, 6, 151, 'قل تعالوا أتل ما حرم ربكم عليكم ۖ ألا تشركوا به شيئا ۖ وبالوالدين إحسانا ۖ ولا تقتلوا أولادكم من إملاق ۖ نحن نرزقكم وإياهم ۖ ولا تقربوا الفواحش ما ظهر منها وما بطن ۖ ولا تقتلوا النفس التي حرم الله إلا بالحق ۚ ذلكم وصاكم به لعلكم تعقلون'),
(941, 6, 152, 'ولا تقربوا مال اليتيم إلا بالتي هي أحسن حتى يبلغ أشده ۖ وأوفوا الكيل والميزان بالقسط ۖ لا نكلف نفسا إلا وسعها ۖ وإذا قلتم فاعدلوا ولو كان ذا قربى ۖ وبعهد الله أوفوا ۚ ذلكم وصاكم به لعلكم تذكرون'),
(942, 6, 153, 'وأن هذا صراطي مستقيما فاتبعوه ۖ ولا تتبعوا السبل فتفرق بكم عن سبيله ۚ ذلكم وصاكم به لعلكم تتقون'),
(943, 6, 154, 'ثم آتينا موسى الكتاب تماما على الذي أحسن وتفصيلا لكل شيء وهدى ورحمة لعلهم بلقاء ربهم يؤمنون'),
(944, 6, 155, 'وهذا كتاب أنزلناه مبارك فاتبعوه واتقوا لعلكم ترحمون'),
(945, 6, 156, 'أن تقولوا إنما أنزل الكتاب على طائفتين من قبلنا وإن كنا عن دراستهم لغافلين'),
(946, 6, 157, 'أو تقولوا لو أنا أنزل علينا الكتاب لكنا أهدى منهم ۚ فقد جاءكم بينة من ربكم وهدى ورحمة ۚ فمن أظلم ممن كذب بآيات الله وصدف عنها ۗ سنجزي الذين يصدفون عن آياتنا سوء العذاب بما كانوا يصدفون'),
(947, 6, 158, 'هل ينظرون إلا أن تأتيهم الملائكة أو يأتي ربك أو يأتي بعض آيات ربك ۗ يوم يأتي بعض آيات ربك لا ينفع نفسا إيمانها لم تكن آمنت من قبل أو كسبت في إيمانها خيرا ۗ قل انتظروا إنا منتظرون'),
(948, 6, 159, 'إن الذين فرقوا دينهم وكانوا شيعا لست منهم في شيء ۚ إنما أمرهم إلى الله ثم ينبئهم بما كانوا يفعلون'),
(949, 6, 160, 'من جاء بالحسنة فله عشر أمثالها ۖ ومن جاء بالسيئة فلا يجزى إلا مثلها وهم لا يظلمون'),
(950, 6, 161, 'قل إنني هداني ربي إلى صراط مستقيم دينا قيما ملة إبراهيم حنيفا ۚ وما كان من المشركين'),
(951, 6, 162, 'قل إن صلاتي ونسكي ومحياي ومماتي لله رب العالمين'),
(952, 6, 163, 'لا شريك له ۖ وبذلك أمرت وأنا أول المسلمين'),
(953, 6, 164, 'قل أغير الله أبغي ربا وهو رب كل شيء ۚ ولا تكسب كل نفس إلا عليها ۚ ولا تزر وازرة وزر أخرى ۚ ثم إلى ربكم مرجعكم فينبئكم بما كنتم فيه تختلفون'),
(954, 6, 165, 'وهو الذي جعلكم خلائف الأرض ورفع بعضكم فوق بعض درجات ليبلوكم في ما آتاكم ۗ إن ربك سريع العقاب وإنه لغفور رحيم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'بسم الله الرحمن الرحيم المص'),
(956, 7, 2, 'كتاب أنزل إليك فلا يكن في صدرك حرج منه لتنذر به وذكرى للمؤمنين'),
(957, 7, 3, 'اتبعوا ما أنزل إليكم من ربكم ولا تتبعوا من دونه أولياء ۗ قليلا ما تذكرون'),
(958, 7, 4, 'وكم من قرية أهلكناها فجاءها بأسنا بياتا أو هم قائلون'),
(959, 7, 5, 'فما كان دعواهم إذ جاءهم بأسنا إلا أن قالوا إنا كنا ظالمين'),
(960, 7, 6, 'فلنسألن الذين أرسل إليهم ولنسألن المرسلين'),
(961, 7, 7, 'فلنقصن عليهم بعلم ۖ وما كنا غائبين'),
(962, 7, 8, 'والوزن يومئذ الحق ۚ فمن ثقلت موازينه فأولئك هم المفلحون'),
(963, 7, 9, 'ومن خفت موازينه فأولئك الذين خسروا أنفسهم بما كانوا بآياتنا يظلمون'),
(964, 7, 10, 'ولقد مكناكم في الأرض وجعلنا لكم فيها معايش ۗ قليلا ما تشكرون'),
(965, 7, 11, 'ولقد خلقناكم ثم صورناكم ثم قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس لم يكن من الساجدين'),
(966, 7, 12, 'قال ما منعك ألا تسجد إذ أمرتك ۖ قال أنا خير منه خلقتني من نار وخلقته من طين'),
(967, 7, 13, 'قال فاهبط منها فما يكون لك أن تتكبر فيها فاخرج إنك من الصاغرين'),
(968, 7, 14, 'قال أنظرني إلى يوم يبعثون'),
(969, 7, 15, 'قال إنك من المنظرين'),
(970, 7, 16, 'قال فبما أغويتني لأقعدن لهم صراطك المستقيم'),
(971, 7, 17, 'ثم لآتينهم من بين أيديهم ومن خلفهم وعن أيمانهم وعن شمائلهم ۖ ولا تجد أكثرهم شاكرين'),
(972, 7, 18, 'قال اخرج منها مذءوما مدحورا ۖ لمن تبعك منهم لأملأن جهنم منكم أجمعين'),
(973, 7, 19, 'ويا آدم اسكن أنت وزوجك الجنة فكلا من حيث شئتما ولا تقربا هذه الشجرة فتكونا من الظالمين'),
(974, 7, 20, 'فوسوس لهما الشيطان ليبدي لهما ما ووري عنهما من سوآتهما وقال ما نهاكما ربكما عن هذه الشجرة إلا أن تكونا ملكين أو تكونا من الخالدين'),
(975, 7, 21, 'وقاسمهما إني لكما لمن الناصحين'),
(976, 7, 22, 'فدلاهما بغرور ۚ فلما ذاقا الشجرة بدت لهما سوآتهما وطفقا يخصفان عليهما من ورق الجنة ۖ وناداهما ربهما ألم أنهكما عن تلكما الشجرة وأقل لكما إن الشيطان لكما عدو مبين'),
(977, 7, 23, 'قالا ربنا ظلمنا أنفسنا وإن لم تغفر لنا وترحمنا لنكونن من الخاسرين'),
(978, 7, 24, 'قال اهبطوا بعضكم لبعض عدو ۖ ولكم في الأرض مستقر ومتاع إلى حين'),
(979, 7, 25, 'قال فيها تحيون وفيها تموتون ومنها تخرجون'),
(980, 7, 26, 'يا بني آدم قد أنزلنا عليكم لباسا يواري سوآتكم وريشا ۖ ولباس التقوى ذلك خير ۚ ذلك من آيات الله لعلهم يذكرون'),
(981, 7, 27, 'يا بني آدم لا يفتننكم الشيطان كما أخرج أبويكم من الجنة ينزع عنهما لباسهما ليريهما سوآتهما ۗ إنه يراكم هو وقبيله من حيث لا ترونهم ۗ إنا جعلنا الشياطين أولياء للذين لا يؤمنون'),
(982, 7, 28, 'وإذا فعلوا فاحشة قالوا وجدنا عليها آباءنا والله أمرنا بها ۗ قل إن الله لا يأمر بالفحشاء ۖ أتقولون على الله ما لا تعلمون'),
(983, 7, 29, 'قل أمر ربي بالقسط ۖ وأقيموا وجوهكم عند كل مسجد وادعوه مخلصين له الدين ۚ كما بدأكم تعودون'),
(984, 7, 30, 'فريقا هدى وفريقا حق عليهم الضلالة ۗ إنهم اتخذوا الشياطين أولياء من دون الله ويحسبون أنهم مهتدون'),
(985, 7, 31, 'يا بني آدم خذوا زينتكم عند كل مسجد وكلوا واشربوا ولا تسرفوا ۚ إنه لا يحب المسرفين'),
(986, 7, 32, 'قل من حرم زينة الله التي أخرج لعباده والطيبات من الرزق ۚ قل هي للذين آمنوا في الحياة الدنيا خالصة يوم القيامة ۗ كذلك نفصل الآيات لقوم يعلمون'),
(987, 7, 33, 'قل إنما حرم ربي الفواحش ما ظهر منها وما بطن والإثم والبغي بغير الحق وأن تشركوا بالله ما لم ينزل به سلطانا وأن تقولوا على الله ما لا تعلمون'),
(988, 7, 34, 'ولكل أمة أجل ۖ فإذا جاء أجلهم لا يستأخرون ساعة ۖ ولا يستقدمون'),
(989, 7, 35, 'يا بني آدم إما يأتينكم رسل منكم يقصون عليكم آياتي ۙ فمن اتقى وأصلح فلا خوف عليهم ولا هم يحزنون'),
(990, 7, 36, 'والذين كذبوا بآياتنا واستكبروا عنها أولئك أصحاب النار ۖ هم فيها خالدون'),
(991, 7, 37, 'فمن أظلم ممن افترى على الله كذبا أو كذب بآياته ۚ أولئك ينالهم نصيبهم من الكتاب ۖ حتى إذا جاءتهم رسلنا يتوفونهم قالوا أين ما كنتم تدعون من دون الله ۖ قالوا ضلوا عنا وشهدوا على أنفسهم أنهم كانوا كافرين'),
(992, 7, 38, 'قال ادخلوا في أمم قد خلت من قبلكم من الجن والإنس في النار ۖ كلما دخلت أمة لعنت أختها ۖ حتى إذا اداركوا فيها جميعا قالت أخراهم لأولاهم ربنا هؤلاء أضلونا فآتهم عذابا ضعفا من النار ۖ قال لكل ضعف ولكن لا تعلمون'),
(993, 7, 39, 'وقالت أولاهم لأخراهم فما كان لكم علينا من فضل فذوقوا العذاب بما كنتم تكسبون'),
(994, 7, 40, 'إن الذين كذبوا بآياتنا واستكبروا عنها لا تفتح لهم أبواب السماء ولا يدخلون الجنة حتى يلج الجمل في سم الخياط ۚ وكذلك نجزي المجرمين'),
(995, 7, 41, 'لهم من جهنم مهاد ومن فوقهم غواش ۚ وكذلك نجزي الظالمين'),
(996, 7, 42, 'والذين آمنوا وعملوا الصالحات لا نكلف نفسا إلا وسعها أولئك أصحاب الجنة ۖ هم فيها خالدون'),
(997, 7, 43, 'ونزعنا ما في صدورهم من غل تجري من تحتهم الأنهار ۖ وقالوا الحمد لله الذي هدانا لهذا وما كنا لنهتدي لولا أن هدانا الله ۖ لقد جاءت رسل ربنا بالحق ۖ ونودوا أن تلكم الجنة أورثتموها بما كنتم تعملون'),
(998, 7, 44, 'ونادى أصحاب الجنة أصحاب النار أن قد وجدنا ما وعدنا ربنا حقا فهل وجدتم ما وعد ربكم حقا ۖ قالوا نعم ۚ فأذن مؤذن بينهم أن لعنة الله على الظالمين'),
(999, 7, 45, 'الذين يصدون عن سبيل الله ويبغونها عوجا وهم بالآخرة كافرون'),
(1000, 7, 46, 'وبينهما حجاب ۚ وعلى الأعراف رجال يعرفون كلا بسيماهم ۚ ونادوا أصحاب الجنة أن سلام عليكم ۚ لم يدخلوها وهم يطمعون'),
(1001, 7, 47, 'وإذا صرفت أبصارهم تلقاء أصحاب النار قالوا ربنا لا تجعلنا مع القوم الظالمين'),
(1002, 7, 48, 'ونادى أصحاب الأعراف رجالا يعرفونهم بسيماهم قالوا ما أغنى عنكم جمعكم وما كنتم تستكبرون'),
(1003, 7, 49, 'أهؤلاء الذين أقسمتم لا ينالهم الله برحمة ۚ ادخلوا الجنة لا خوف عليكم ولا أنتم تحزنون'),
(1004, 7, 50, 'ونادى أصحاب النار أصحاب الجنة أن أفيضوا علينا من الماء أو مما رزقكم الله ۚ قالوا إن الله حرمهما على الكافرين'),
(1005, 7, 51, 'الذين اتخذوا دينهم لهوا ولعبا وغرتهم الحياة الدنيا ۚ فاليوم ننساهم كما نسوا لقاء يومهم هذا وما كانوا بآياتنا يجحدون'),
(1006, 7, 52, 'ولقد جئناهم بكتاب فصلناه على علم هدى ورحمة لقوم يؤمنون'),
(1007, 7, 53, 'هل ينظرون إلا تأويله ۚ يوم يأتي تأويله يقول الذين نسوه من قبل قد جاءت رسل ربنا بالحق فهل لنا من شفعاء فيشفعوا لنا أو نرد فنعمل غير الذي كنا نعمل ۚ قد خسروا أنفسهم وضل عنهم ما كانوا يفترون'),
(1008, 7, 54, 'إن ربكم الله الذي خلق السماوات والأرض في ستة أيام ثم استوى على العرش يغشي الليل النهار يطلبه حثيثا والشمس والقمر والنجوم مسخرات بأمره ۗ ألا له الخلق والأمر ۗ تبارك الله رب العالمين'),
(1009, 7, 55, 'ادعوا ربكم تضرعا وخفية ۚ إنه لا يحب المعتدين'),
(1010, 7, 56, 'ولا تفسدوا في الأرض بعد إصلاحها وادعوه خوفا وطمعا ۚ إن رحمت الله قريب من المحسنين'),
(1011, 7, 57, 'وهو الذي يرسل الرياح بشرا بين يدي رحمته ۖ حتى إذا أقلت سحابا ثقالا سقناه لبلد ميت فأنزلنا به الماء فأخرجنا به من كل الثمرات ۚ كذلك نخرج الموتى لعلكم تذكرون'),
(1012, 7, 58, 'والبلد الطيب يخرج نباته بإذن ربه ۖ والذي خبث لا يخرج إلا نكدا ۚ كذلك نصرف الآيات لقوم يشكرون'),
(1013, 7, 59, 'لقد أرسلنا نوحا إلى قومه فقال يا قوم اعبدوا الله ما لكم من إله غيره إني أخاف عليكم عذاب يوم عظيم'),
(1014, 7, 60, 'قال الملأ من قومه إنا لنراك في ضلال مبين'),
(1015, 7, 61, 'قال يا قوم ليس بي ضلالة ولكني رسول من رب العالمين'),
(1016, 7, 62, 'أبلغكم رسالات ربي وأنصح لكم وأعلم من الله ما لا تعلمون'),
(1017, 7, 63, 'أوعجبتم أن جاءكم ذكر من ربكم على رجل منكم لينذركم ولتتقوا ولعلكم ترحمون'),
(1018, 7, 64, 'فكذبوه فأنجيناه والذين معه في الفلك وأغرقنا الذين كذبوا بآياتنا ۚ إنهم كانوا قوما عمين'),
(1019, 7, 65, 'وإلى عاد أخاهم هودا ۗ قال يا قوم اعبدوا الله ما لكم من إله غيره ۚ أفلا تتقون'),
(1020, 7, 66, 'قال الملأ الذين كفروا من قومه إنا لنراك في سفاهة وإنا لنظنك من الكاذبين'),
(1021, 7, 67, 'قال يا قوم ليس بي سفاهة ولكني رسول من رب العالمين'),
(1022, 7, 68, 'أبلغكم رسالات ربي وأنا لكم ناصح أمين'),
(1023, 7, 69, 'أوعجبتم أن جاءكم ذكر من ربكم على رجل منكم لينذركم ۚ واذكروا إذ جعلكم خلفاء من بعد قوم نوح وزادكم في الخلق بسطة ۖ فاذكروا آلاء الله لعلكم تفلحون'),
(1024, 7, 70, 'قالوا أجئتنا لنعبد الله وحده ونذر ما كان يعبد آباؤنا ۖ فأتنا بما تعدنا إن كنت من الصادقين'),
(1025, 7, 71, 'قال قد وقع عليكم من ربكم رجس وغضب ۖ أتجادلونني في أسماء سميتموها أنتم وآباؤكم ما نزل الله بها من سلطان ۚ فانتظروا إني معكم من المنتظرين'),
(1026, 7, 72, 'فأنجيناه والذين معه برحمة منا وقطعنا دابر الذين كذبوا بآياتنا ۖ وما كانوا مؤمنين'),
(1027, 7, 73, 'وإلى ثمود أخاهم صالحا ۗ قال يا قوم اعبدوا الله ما لكم من إله غيره ۖ قد جاءتكم بينة من ربكم ۖ هذه ناقة الله لكم آية ۖ فذروها تأكل في أرض الله ۖ ولا تمسوها بسوء فيأخذكم عذاب أليم'),
(1028, 7, 74, 'واذكروا إذ جعلكم خلفاء من بعد عاد وبوأكم في الأرض تتخذون من سهولها قصورا وتنحتون الجبال بيوتا ۖ فاذكروا آلاء الله ولا تعثوا في الأرض مفسدين'),
(1029, 7, 75, 'قال الملأ الذين استكبروا من قومه للذين استضعفوا لمن آمن منهم أتعلمون أن صالحا مرسل من ربه ۚ قالوا إنا بما أرسل به مؤمنون'),
(1030, 7, 76, 'قال الذين استكبروا إنا بالذي آمنتم به كافرون'),
(1031, 7, 77, 'فعقروا الناقة وعتوا عن أمر ربهم وقالوا يا صالح ائتنا بما تعدنا إن كنت من المرسلين'),
(1032, 7, 78, 'فأخذتهم الرجفة فأصبحوا في دارهم جاثمين'),
(1033, 7, 79, 'فتولى عنهم وقال يا قوم لقد أبلغتكم رسالة ربي ونصحت لكم ولكن لا تحبون الناصحين'),
(1034, 7, 80, 'ولوطا إذ قال لقومه أتأتون الفاحشة ما سبقكم بها من أحد من العالمين'),
(1035, 7, 81, 'إنكم لتأتون الرجال شهوة من دون النساء ۚ بل أنتم قوم مسرفون'),
(1036, 7, 82, 'وما كان جواب قومه إلا أن قالوا أخرجوهم من قريتكم ۖ إنهم أناس يتطهرون'),
(1037, 7, 83, 'فأنجيناه وأهله إلا امرأته كانت من الغابرين'),
(1038, 7, 84, 'وأمطرنا عليهم مطرا ۖ فانظر كيف كان عاقبة المجرمين'),
(1039, 7, 85, 'وإلى مدين أخاهم شعيبا ۗ قال يا قوم اعبدوا الله ما لكم من إله غيره ۖ قد جاءتكم بينة من ربكم ۖ فأوفوا الكيل والميزان ولا تبخسوا الناس أشياءهم ولا تفسدوا في الأرض بعد إصلاحها ۚ ذلكم خير لكم إن كنتم مؤمنين'),
(1040, 7, 86, 'ولا تقعدوا بكل صراط توعدون وتصدون عن سبيل الله من آمن به وتبغونها عوجا ۚ واذكروا إذ كنتم قليلا فكثركم ۖ وانظروا كيف كان عاقبة المفسدين'),
(1041, 7, 87, 'وإن كان طائفة منكم آمنوا بالذي أرسلت به وطائفة لم يؤمنوا فاصبروا حتى يحكم الله بيننا ۚ وهو خير الحاكمين'),
(1042, 7, 88, 'قال الملأ الذين استكبروا من قومه لنخرجنك يا شعيب والذين آمنوا معك من قريتنا أو لتعودن في ملتنا ۚ قال أولو كنا كارهين'),
(1043, 7, 89, 'قد افترينا على الله كذبا إن عدنا في ملتكم بعد إذ نجانا الله منها ۚ وما يكون لنا أن نعود فيها إلا أن يشاء الله ربنا ۚ وسع ربنا كل شيء علما ۚ على الله توكلنا ۚ ربنا افتح بيننا وبين قومنا بالحق وأنت خير الفاتحين'),
(1044, 7, 90, 'وقال الملأ الذين كفروا من قومه لئن اتبعتم شعيبا إنكم إذا لخاسرون'),
(1045, 7, 91, 'فأخذتهم الرجفة فأصبحوا في دارهم جاثمين'),
(1046, 7, 92, 'الذين كذبوا شعيبا كأن لم يغنوا فيها ۚ الذين كذبوا شعيبا كانوا هم الخاسرين'),
(1047, 7, 93, 'فتولى عنهم وقال يا قوم لقد أبلغتكم رسالات ربي ونصحت لكم ۖ فكيف آسى على قوم كافرين'),
(1048, 7, 94, 'وما أرسلنا في قرية من نبي إلا أخذنا أهلها بالبأساء والضراء لعلهم يضرعون'),
(1049, 7, 95, 'ثم بدلنا مكان السيئة الحسنة حتى عفوا وقالوا قد مس آباءنا الضراء والسراء فأخذناهم بغتة وهم لا يشعرون'),
(1050, 7, 96, 'ولو أن أهل القرى آمنوا واتقوا لفتحنا عليهم بركات من السماء والأرض ولكن كذبوا فأخذناهم بما كانوا يكسبون'),
(1051, 7, 97, 'أفأمن أهل القرى أن يأتيهم بأسنا بياتا وهم نائمون'),
(1052, 7, 98, 'أوأمن أهل القرى أن يأتيهم بأسنا ضحى وهم يلعبون'),
(1053, 7, 99, 'أفأمنوا مكر الله ۚ فلا يأمن مكر الله إلا القوم الخاسرون'),
(1054, 7, 100, 'أولم يهد للذين يرثون الأرض من بعد أهلها أن لو نشاء أصبناهم بذنوبهم ۚ ونطبع على قلوبهم فهم لا يسمعون'),
(1055, 7, 101, 'تلك القرى نقص عليك من أنبائها ۚ ولقد جاءتهم رسلهم بالبينات فما كانوا ليؤمنوا بما كذبوا من قبل ۚ كذلك يطبع الله على قلوب الكافرين'),
(1056, 7, 102, 'وما وجدنا لأكثرهم من عهد ۖ وإن وجدنا أكثرهم لفاسقين'),
(1057, 7, 103, 'ثم بعثنا من بعدهم موسى بآياتنا إلى فرعون وملئه فظلموا بها ۖ فانظر كيف كان عاقبة المفسدين'),
(1058, 7, 104, 'وقال موسى يا فرعون إني رسول من رب العالمين'),
(1059, 7, 105, 'حقيق على أن لا أقول على الله إلا الحق ۚ قد جئتكم ببينة من ربكم فأرسل معي بني إسرائيل'),
(1060, 7, 106, 'قال إن كنت جئت بآية فأت بها إن كنت من الصادقين'),
(1061, 7, 107, 'فألقى عصاه فإذا هي ثعبان مبين'),
(1062, 7, 108, 'ونزع يده فإذا هي بيضاء للناظرين'),
(1063, 7, 109, 'قال الملأ من قوم فرعون إن هذا لساحر عليم'),
(1064, 7, 110, 'يريد أن يخرجكم من أرضكم ۖ فماذا تأمرون'),
(1065, 7, 111, 'قالوا أرجه وأخاه وأرسل في المدائن حاشرين'),
(1066, 7, 112, 'يأتوك بكل ساحر عليم'),
(1067, 7, 113, 'وجاء السحرة فرعون قالوا إن لنا لأجرا إن كنا نحن الغالبين'),
(1068, 7, 114, 'قال نعم وإنكم لمن المقربين'),
(1069, 7, 115, 'قالوا يا موسى إما أن تلقي وإما أن نكون نحن الملقين'),
(1070, 7, 116, 'قال ألقوا ۖ فلما ألقوا سحروا أعين الناس واسترهبوهم وجاءوا بسحر عظيم'),
(1071, 7, 117, 'وأوحينا إلى موسى أن ألق عصاك ۖ فإذا هي تلقف ما يأفكون'),
(1072, 7, 118, 'فوقع الحق وبطل ما كانوا يعملون'),
(1073, 7, 119, 'فغلبوا هنالك وانقلبوا صاغرين'),
(1074, 7, 120, 'وألقي السحرة ساجدين'),
(1075, 7, 121, 'قالوا آمنا برب العالمين'),
(1076, 7, 122, 'رب موسى وهارون'),
(1077, 7, 123, 'قال فرعون آمنتم به قبل أن آذن لكم ۖ إن هذا لمكر مكرتموه في المدينة لتخرجوا منها أهلها ۖ فسوف تعلمون'),
(1078, 7, 124, 'لأقطعن أيديكم وأرجلكم من خلاف ثم لأصلبنكم أجمعين'),
(1079, 7, 125, 'قالوا إنا إلى ربنا منقلبون'),
(1080, 7, 126, 'وما تنقم منا إلا أن آمنا بآيات ربنا لما جاءتنا ۚ ربنا أفرغ علينا صبرا وتوفنا مسلمين'),
(1081, 7, 127, 'وقال الملأ من قوم فرعون أتذر موسى وقومه ليفسدوا في الأرض ويذرك وآلهتك ۚ قال سنقتل أبناءهم ونستحيي نساءهم وإنا فوقهم قاهرون'),
(1082, 7, 128, 'قال موسى لقومه استعينوا بالله واصبروا ۖ إن الأرض لله يورثها من يشاء من عباده ۖ والعاقبة للمتقين'),
(1083, 7, 129, 'قالوا أوذينا من قبل أن تأتينا ومن بعد ما جئتنا ۚ قال عسى ربكم أن يهلك عدوكم ويستخلفكم في الأرض فينظر كيف تعملون'),
(1084, 7, 130, 'ولقد أخذنا آل فرعون بالسنين ونقص من الثمرات لعلهم يذكرون'),
(1085, 7, 131, 'فإذا جاءتهم الحسنة قالوا لنا هذه ۖ وإن تصبهم سيئة يطيروا بموسى ومن معه ۗ ألا إنما طائرهم عند الله ولكن أكثرهم لا يعلمون'),
(1086, 7, 132, 'وقالوا مهما تأتنا به من آية لتسحرنا بها فما نحن لك بمؤمنين'),
(1087, 7, 133, 'فأرسلنا عليهم الطوفان والجراد والقمل والضفادع والدم آيات مفصلات فاستكبروا وكانوا قوما مجرمين'),
(1088, 7, 134, 'ولما وقع عليهم الرجز قالوا يا موسى ادع لنا ربك بما عهد عندك ۖ لئن كشفت عنا الرجز لنؤمنن لك ولنرسلن معك بني إسرائيل'),
(1089, 7, 135, 'فلما كشفنا عنهم الرجز إلى أجل هم بالغوه إذا هم ينكثون'),
(1090, 7, 136, 'فانتقمنا منهم فأغرقناهم في اليم بأنهم كذبوا بآياتنا وكانوا عنها غافلين'),
(1091, 7, 137, 'وأورثنا القوم الذين كانوا يستضعفون مشارق الأرض ومغاربها التي باركنا فيها ۖ وتمت كلمت ربك الحسنى على بني إسرائيل بما صبروا ۖ ودمرنا ما كان يصنع فرعون وقومه وما كانوا يعرشون'),
(1092, 7, 138, 'وجاوزنا ببني إسرائيل البحر فأتوا على قوم يعكفون على أصنام لهم ۚ قالوا يا موسى اجعل لنا إلها كما لهم آلهة ۚ قال إنكم قوم تجهلون'),
(1093, 7, 139, 'إن هؤلاء متبر ما هم فيه وباطل ما كانوا يعملون'),
(1094, 7, 140, 'قال أغير الله أبغيكم إلها وهو فضلكم على العالمين'),
(1095, 7, 141, 'وإذ أنجيناكم من آل فرعون يسومونكم سوء العذاب ۖ يقتلون أبناءكم ويستحيون نساءكم ۚ وفي ذلكم بلاء من ربكم عظيم'),
(1096, 7, 142, 'وواعدنا موسى ثلاثين ليلة وأتممناها بعشر فتم ميقات ربه أربعين ليلة ۚ وقال موسى لأخيه هارون اخلفني في قومي وأصلح ولا تتبع سبيل المفسدين'),
(1097, 7, 143, 'ولما جاء موسى لميقاتنا وكلمه ربه قال رب أرني أنظر إليك ۚ قال لن تراني ولكن انظر إلى الجبل فإن استقر مكانه فسوف تراني ۚ فلما تجلى ربه للجبل جعله دكا وخر موسى صعقا ۚ فلما أفاق قال سبحانك تبت إليك وأنا أول المؤمنين'),
(1098, 7, 144, 'قال يا موسى إني اصطفيتك على الناس برسالاتي وبكلامي فخذ ما آتيتك وكن من الشاكرين'),
(1099, 7, 145, 'وكتبنا له في الألواح من كل شيء موعظة وتفصيلا لكل شيء فخذها بقوة وأمر قومك يأخذوا بأحسنها ۚ سأريكم دار الفاسقين'),
(1100, 7, 146, 'سأصرف عن آياتي الذين يتكبرون في الأرض بغير الحق وإن يروا كل آية لا يؤمنوا بها وإن يروا سبيل الرشد لا يتخذوه سبيلا وإن يروا سبيل الغي يتخذوه سبيلا ۚ ذلك بأنهم كذبوا بآياتنا وكانوا عنها غافلين'),
(1101, 7, 147, 'والذين كذبوا بآياتنا ولقاء الآخرة حبطت أعمالهم ۚ هل يجزون إلا ما كانوا يعملون'),
(1102, 7, 148, 'واتخذ قوم موسى من بعده من حليهم عجلا جسدا له خوار ۚ ألم يروا أنه لا يكلمهم ولا يهديهم سبيلا ۘ اتخذوه وكانوا ظالمين'),
(1103, 7, 149, 'ولما سقط في أيديهم ورأوا أنهم قد ضلوا قالوا لئن لم يرحمنا ربنا ويغفر لنا لنكونن من الخاسرين'),
(1104, 7, 150, 'ولما رجع موسى إلى قومه غضبان أسفا قال بئسما خلفتموني من بعدي ۖ أعجلتم أمر ربكم ۖ وألقى الألواح وأخذ برأس أخيه يجره إليه ۚ قال ابن أم إن القوم استضعفوني وكادوا يقتلونني فلا تشمت بي الأعداء ولا تجعلني مع القوم الظالمين'),
(1105, 7, 151, 'قال رب اغفر لي ولأخي وأدخلنا في رحمتك ۖ وأنت أرحم الراحمين'),
(1106, 7, 152, 'إن الذين اتخذوا العجل سينالهم غضب من ربهم وذلة في الحياة الدنيا ۚ وكذلك نجزي المفترين'),
(1107, 7, 153, 'والذين عملوا السيئات ثم تابوا من بعدها وآمنوا إن ربك من بعدها لغفور رحيم'),
(1108, 7, 154, 'ولما سكت عن موسى الغضب أخذ الألواح ۖ وفي نسختها هدى ورحمة للذين هم لربهم يرهبون'),
(1109, 7, 155, 'واختار موسى قومه سبعين رجلا لميقاتنا ۖ فلما أخذتهم الرجفة قال رب لو شئت أهلكتهم من قبل وإياي ۖ أتهلكنا بما فعل السفهاء منا ۖ إن هي إلا فتنتك تضل بها من تشاء وتهدي من تشاء ۖ أنت ولينا فاغفر لنا وارحمنا ۖ وأنت خير الغافرين'),
(1110, 7, 156, 'واكتب لنا في هذه الدنيا حسنة وفي الآخرة إنا هدنا إليك ۚ قال عذابي أصيب به من أشاء ۖ ورحمتي وسعت كل شيء ۚ فسأكتبها للذين يتقون ويؤتون الزكاة والذين هم بآياتنا يؤمنون'),
(1111, 7, 157, 'الذين يتبعون الرسول النبي الأمي الذي يجدونه مكتوبا عندهم في التوراة والإنجيل يأمرهم بالمعروف وينهاهم عن المنكر ويحل لهم الطيبات ويحرم عليهم الخبائث ويضع عنهم إصرهم والأغلال التي كانت عليهم ۚ فالذين آمنوا به وعزروه ونصروه واتبعوا النور الذي أنزل معه ۙ أولئك هم المفلحون'),
(1112, 7, 158, 'قل يا أيها الناس إني رسول الله إليكم جميعا الذي له ملك السماوات والأرض ۖ لا إله إلا هو يحيي ويميت ۖ فآمنوا بالله ورسوله النبي الأمي الذي يؤمن بالله وكلماته واتبعوه لعلكم تهتدون'),
(1113, 7, 159, 'ومن قوم موسى أمة يهدون بالحق وبه يعدلون'),
(1114, 7, 160, 'وقطعناهم اثنتي عشرة أسباطا أمما ۚ وأوحينا إلى موسى إذ استسقاه قومه أن اضرب بعصاك الحجر ۖ فانبجست منه اثنتا عشرة عينا ۖ قد علم كل أناس مشربهم ۚ وظللنا عليهم الغمام وأنزلنا عليهم المن والسلوى ۖ كلوا من طيبات ما رزقناكم ۚ وما ظلمونا ولكن كانوا أنفسهم يظلمون'),
(1115, 7, 161, 'وإذ قيل لهم اسكنوا هذه القرية وكلوا منها حيث شئتم وقولوا حطة وادخلوا الباب سجدا نغفر لكم خطيئاتكم ۚ سنزيد المحسنين'),
(1116, 7, 162, 'فبدل الذين ظلموا منهم قولا غير الذي قيل لهم فأرسلنا عليهم رجزا من السماء بما كانوا يظلمون'),
(1117, 7, 163, 'واسألهم عن القرية التي كانت حاضرة البحر إذ يعدون في السبت إذ تأتيهم حيتانهم يوم سبتهم شرعا ويوم لا يسبتون ۙ لا تأتيهم ۚ كذلك نبلوهم بما كانوا يفسقون'),
(1118, 7, 164, 'وإذ قالت أمة منهم لم تعظون قوما ۙ الله مهلكهم أو معذبهم عذابا شديدا ۖ قالوا معذرة إلى ربكم ولعلهم يتقون'),
(1119, 7, 165, 'فلما نسوا ما ذكروا به أنجينا الذين ينهون عن السوء وأخذنا الذين ظلموا بعذاب بئيس بما كانوا يفسقون'),
(1120, 7, 166, 'فلما عتوا عن ما نهوا عنه قلنا لهم كونوا قردة خاسئين'),
(1121, 7, 167, 'وإذ تأذن ربك ليبعثن عليهم إلى يوم القيامة من يسومهم سوء العذاب ۗ إن ربك لسريع العقاب ۖ وإنه لغفور رحيم'),
(1122, 7, 168, 'وقطعناهم في الأرض أمما ۖ منهم الصالحون ومنهم دون ذلك ۖ وبلوناهم بالحسنات والسيئات لعلهم يرجعون'),
(1123, 7, 169, 'فخلف من بعدهم خلف ورثوا الكتاب يأخذون عرض هذا الأدنى ويقولون سيغفر لنا وإن يأتهم عرض مثله يأخذوه ۚ ألم يؤخذ عليهم ميثاق الكتاب أن لا يقولوا على الله إلا الحق ودرسوا ما فيه ۗ والدار الآخرة خير للذين يتقون ۗ أفلا تعقلون'),
(1124, 7, 170, 'والذين يمسكون بالكتاب وأقاموا الصلاة إنا لا نضيع أجر المصلحين'),
(1125, 7, 171, 'وإذ نتقنا الجبل فوقهم كأنه ظلة وظنوا أنه واقع بهم خذوا ما آتيناكم بقوة واذكروا ما فيه لعلكم تتقون'),
(1126, 7, 172, 'وإذ أخذ ربك من بني آدم من ظهورهم ذريتهم وأشهدهم على أنفسهم ألست بربكم ۖ قالوا بلى ۛ شهدنا ۛ أن تقولوا يوم القيامة إنا كنا عن هذا غافلين'),
(1127, 7, 173, 'أو تقولوا إنما أشرك آباؤنا من قبل وكنا ذرية من بعدهم ۖ أفتهلكنا بما فعل المبطلون'),
(1128, 7, 174, 'وكذلك نفصل الآيات ولعلهم يرجعون'),
(1129, 7, 175, 'واتل عليهم نبأ الذي آتيناه آياتنا فانسلخ منها فأتبعه الشيطان فكان من الغاوين'),
(1130, 7, 176, 'ولو شئنا لرفعناه بها ولكنه أخلد إلى الأرض واتبع هواه ۚ فمثله كمثل الكلب إن تحمل عليه يلهث أو تتركه يلهث ۚ ذلك مثل القوم الذين كذبوا بآياتنا ۚ فاقصص القصص لعلهم يتفكرون'),
(1131, 7, 177, 'ساء مثلا القوم الذين كذبوا بآياتنا وأنفسهم كانوا يظلمون'),
(1132, 7, 178, 'من يهد الله فهو المهتدي ۖ ومن يضلل فأولئك هم الخاسرون'),
(1133, 7, 179, 'ولقد ذرأنا لجهنم كثيرا من الجن والإنس ۖ لهم قلوب لا يفقهون بها ولهم أعين لا يبصرون بها ولهم آذان لا يسمعون بها ۚ أولئك كالأنعام بل هم أضل ۚ أولئك هم الغافلون'),
(1134, 7, 180, 'ولله الأسماء الحسنى فادعوه بها ۖ وذروا الذين يلحدون في أسمائه ۚ سيجزون ما كانوا يعملون'),
(1135, 7, 181, 'وممن خلقنا أمة يهدون بالحق وبه يعدلون'),
(1136, 7, 182, 'والذين كذبوا بآياتنا سنستدرجهم من حيث لا يعلمون'),
(1137, 7, 183, 'وأملي لهم ۚ إن كيدي متين'),
(1138, 7, 184, 'أولم يتفكروا ۗ ما بصاحبهم من جنة ۚ إن هو إلا نذير مبين'),
(1139, 7, 185, 'أولم ينظروا في ملكوت السماوات والأرض وما خلق الله من شيء وأن عسى أن يكون قد اقترب أجلهم ۖ فبأي حديث بعده يؤمنون'),
(1140, 7, 186, 'من يضلل الله فلا هادي له ۚ ويذرهم في طغيانهم يعمهون'),
(1141, 7, 187, 'يسألونك عن الساعة أيان مرساها ۖ قل إنما علمها عند ربي ۖ لا يجليها لوقتها إلا هو ۚ ثقلت في السماوات والأرض ۚ لا تأتيكم إلا بغتة ۗ يسألونك كأنك حفي عنها ۖ قل إنما علمها عند الله ولكن أكثر الناس لا يعلمون'),
(1142, 7, 188, 'قل لا أملك لنفسي نفعا ولا ضرا إلا ما شاء الله ۚ ولو كنت أعلم الغيب لاستكثرت من الخير وما مسني السوء ۚ إن أنا إلا نذير وبشير لقوم يؤمنون'),
(1143, 7, 189, 'هو الذي خلقكم من نفس واحدة وجعل منها زوجها ليسكن إليها ۖ فلما تغشاها حملت حملا خفيفا فمرت به ۖ فلما أثقلت دعوا الله ربهما لئن آتيتنا صالحا لنكونن من الشاكرين'),
(1144, 7, 190, 'فلما آتاهما صالحا جعلا له شركاء فيما آتاهما ۚ فتعالى الله عما يشركون'),
(1145, 7, 191, 'أيشركون ما لا يخلق شيئا وهم يخلقون'),
(1146, 7, 192, 'ولا يستطيعون لهم نصرا ولا أنفسهم ينصرون'),
(1147, 7, 193, 'وإن تدعوهم إلى الهدى لا يتبعوكم ۚ سواء عليكم أدعوتموهم أم أنتم صامتون'),
(1148, 7, 194, 'إن الذين تدعون من دون الله عباد أمثالكم ۖ فادعوهم فليستجيبوا لكم إن كنتم صادقين'),
(1149, 7, 195, 'ألهم أرجل يمشون بها ۖ أم لهم أيد يبطشون بها ۖ أم لهم أعين يبصرون بها ۖ أم لهم آذان يسمعون بها ۗ قل ادعوا شركاءكم ثم كيدون فلا تنظرون'),
(1150, 7, 196, 'إن وليي الله الذي نزل الكتاب ۖ وهو يتولى الصالحين'),
(1151, 7, 197, 'والذين تدعون من دونه لا يستطيعون نصركم ولا أنفسهم ينصرون'),
(1152, 7, 198, 'وإن تدعوهم إلى الهدى لا يسمعوا ۖ وتراهم ينظرون إليك وهم لا يبصرون'),
(1153, 7, 199, 'خذ العفو وأمر بالعرف وأعرض عن الجاهلين'),
(1154, 7, 200, 'وإما ينزغنك من الشيطان نزغ فاستعذ بالله ۚ إنه سميع عليم'),
(1155, 7, 201, 'إن الذين اتقوا إذا مسهم طائف من الشيطان تذكروا فإذا هم مبصرون'),
(1156, 7, 202, 'وإخوانهم يمدونهم في الغي ثم لا يقصرون'),
(1157, 7, 203, 'وإذا لم تأتهم بآية قالوا لولا اجتبيتها ۚ قل إنما أتبع ما يوحى إلي من ربي ۚ هذا بصائر من ربكم وهدى ورحمة لقوم يؤمنون'),
(1158, 7, 204, 'وإذا قرئ القرآن فاستمعوا له وأنصتوا لعلكم ترحمون'),
(1159, 7, 205, 'واذكر ربك في نفسك تضرعا وخيفة ودون الجهر من القول بالغدو والآصال ولا تكن من الغافلين'),
(1160, 7, 206, 'إن الذين عند ربك لا يستكبرون عن عبادته ويسبحونه وله يسجدون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'بسم الله الرحمن الرحيم يسألونك عن الأنفال ۖ قل الأنفال لله والرسول ۖ فاتقوا الله وأصلحوا ذات بينكم ۖ وأطيعوا الله ورسوله إن كنتم مؤمنين'),
(1162, 8, 2, 'إنما المؤمنون الذين إذا ذكر الله وجلت قلوبهم وإذا تليت عليهم آياته زادتهم إيمانا وعلى ربهم يتوكلون'),
(1163, 8, 3, 'الذين يقيمون الصلاة ومما رزقناهم ينفقون'),
(1164, 8, 4, 'أولئك هم المؤمنون حقا ۚ لهم درجات عند ربهم ومغفرة ورزق كريم'),
(1165, 8, 5, 'كما أخرجك ربك من بيتك بالحق وإن فريقا من المؤمنين لكارهون'),
(1166, 8, 6, 'يجادلونك في الحق بعد ما تبين كأنما يساقون إلى الموت وهم ينظرون'),
(1167, 8, 7, 'وإذ يعدكم الله إحدى الطائفتين أنها لكم وتودون أن غير ذات الشوكة تكون لكم ويريد الله أن يحق الحق بكلماته ويقطع دابر الكافرين'),
(1168, 8, 8, 'ليحق الحق ويبطل الباطل ولو كره المجرمون'),
(1169, 8, 9, 'إذ تستغيثون ربكم فاستجاب لكم أني ممدكم بألف من الملائكة مردفين'),
(1170, 8, 10, 'وما جعله الله إلا بشرى ولتطمئن به قلوبكم ۚ وما النصر إلا من عند الله ۚ إن الله عزيز حكيم'),
(1171, 8, 11, 'إذ يغشيكم النعاس أمنة منه وينزل عليكم من السماء ماء ليطهركم به ويذهب عنكم رجز الشيطان وليربط على قلوبكم ويثبت به الأقدام'),
(1172, 8, 12, 'إذ يوحي ربك إلى الملائكة أني معكم فثبتوا الذين آمنوا ۚ سألقي في قلوب الذين كفروا الرعب فاضربوا فوق الأعناق واضربوا منهم كل بنان'),
(1173, 8, 13, 'ذلك بأنهم شاقوا الله ورسوله ۚ ومن يشاقق الله ورسوله فإن الله شديد العقاب'),
(1174, 8, 14, 'ذلكم فذوقوه وأن للكافرين عذاب النار'),
(1175, 8, 15, 'يا أيها الذين آمنوا إذا لقيتم الذين كفروا زحفا فلا تولوهم الأدبار'),
(1176, 8, 16, 'ومن يولهم يومئذ دبره إلا متحرفا لقتال أو متحيزا إلى فئة فقد باء بغضب من الله ومأواه جهنم ۖ وبئس المصير'),
(1177, 8, 17, 'فلم تقتلوهم ولكن الله قتلهم ۚ وما رميت إذ رميت ولكن الله رمى ۚ وليبلي المؤمنين منه بلاء حسنا ۚ إن الله سميع عليم'),
(1178, 8, 18, 'ذلكم وأن الله موهن كيد الكافرين'),
(1179, 8, 19, 'إن تستفتحوا فقد جاءكم الفتح ۖ وإن تنتهوا فهو خير لكم ۖ وإن تعودوا نعد ولن تغني عنكم فئتكم شيئا ولو كثرت وأن الله مع المؤمنين'),
(1180, 8, 20, 'يا أيها الذين آمنوا أطيعوا الله ورسوله ولا تولوا عنه وأنتم تسمعون'),
(1181, 8, 21, 'ولا تكونوا كالذين قالوا سمعنا وهم لا يسمعون'),
(1182, 8, 22, 'إن شر الدواب عند الله الصم البكم الذين لا يعقلون'),
(1183, 8, 23, 'ولو علم الله فيهم خيرا لأسمعهم ۖ ولو أسمعهم لتولوا وهم معرضون'),
(1184, 8, 24, 'يا أيها الذين آمنوا استجيبوا لله وللرسول إذا دعاكم لما يحييكم ۖ واعلموا أن الله يحول بين المرء وقلبه وأنه إليه تحشرون'),
(1185, 8, 25, 'واتقوا فتنة لا تصيبن الذين ظلموا منكم خاصة ۖ واعلموا أن الله شديد العقاب'),
(1186, 8, 26, 'واذكروا إذ أنتم قليل مستضعفون في الأرض تخافون أن يتخطفكم الناس فآواكم وأيدكم بنصره ورزقكم من الطيبات لعلكم تشكرون'),
(1187, 8, 27, 'يا أيها الذين آمنوا لا تخونوا الله والرسول وتخونوا أماناتكم وأنتم تعلمون'),
(1188, 8, 28, 'واعلموا أنما أموالكم وأولادكم فتنة وأن الله عنده أجر عظيم'),
(1189, 8, 29, 'يا أيها الذين آمنوا إن تتقوا الله يجعل لكم فرقانا ويكفر عنكم سيئاتكم ويغفر لكم ۗ والله ذو الفضل العظيم'),
(1190, 8, 30, 'وإذ يمكر بك الذين كفروا ليثبتوك أو يقتلوك أو يخرجوك ۚ ويمكرون ويمكر الله ۖ والله خير الماكرين'),
(1191, 8, 31, 'وإذا تتلى عليهم آياتنا قالوا قد سمعنا لو نشاء لقلنا مثل هذا ۙ إن هذا إلا أساطير الأولين'),
(1192, 8, 32, 'وإذ قالوا اللهم إن كان هذا هو الحق من عندك فأمطر علينا حجارة من السماء أو ائتنا بعذاب أليم'),
(1193, 8, 33, 'وما كان الله ليعذبهم وأنت فيهم ۚ وما كان الله معذبهم وهم يستغفرون'),
(1194, 8, 34, 'وما لهم ألا يعذبهم الله وهم يصدون عن المسجد الحرام وما كانوا أولياءه ۚ إن أولياؤه إلا المتقون ولكن أكثرهم لا يعلمون'),
(1195, 8, 35, 'وما كان صلاتهم عند البيت إلا مكاء وتصدية ۚ فذوقوا العذاب بما كنتم تكفرون'),
(1196, 8, 36, 'إن الذين كفروا ينفقون أموالهم ليصدوا عن سبيل الله ۚ فسينفقونها ثم تكون عليهم حسرة ثم يغلبون ۗ والذين كفروا إلى جهنم يحشرون'),
(1197, 8, 37, 'ليميز الله الخبيث من الطيب ويجعل الخبيث بعضه على بعض فيركمه جميعا فيجعله في جهنم ۚ أولئك هم الخاسرون'),
(1198, 8, 38, 'قل للذين كفروا إن ينتهوا يغفر لهم ما قد سلف وإن يعودوا فقد مضت سنت الأولين'),
(1199, 8, 39, 'وقاتلوهم حتى لا تكون فتنة ويكون الدين كله لله ۚ فإن انتهوا فإن الله بما يعملون بصير'),
(1200, 8, 40, 'وإن تولوا فاعلموا أن الله مولاكم ۚ نعم المولى ونعم النصير'),
(1201, 8, 41, 'واعلموا أنما غنمتم من شيء فأن لله خمسه وللرسول ولذي القربى واليتامى والمساكين وابن السبيل إن كنتم آمنتم بالله وما أنزلنا على عبدنا يوم الفرقان يوم التقى الجمعان ۗ والله على كل شيء قدير'),
(1202, 8, 42, 'إذ أنتم بالعدوة الدنيا وهم بالعدوة القصوى والركب أسفل منكم ۚ ولو تواعدتم لاختلفتم في الميعاد ۙ ولكن ليقضي الله أمرا كان مفعولا ليهلك من هلك عن بينة ويحيى من حي عن بينة ۗ وإن الله لسميع عليم'),
(1203, 8, 43, 'إذ يريكهم الله في منامك قليلا ۖ ولو أراكهم كثيرا لفشلتم ولتنازعتم في الأمر ولكن الله سلم ۗ إنه عليم بذات الصدور'),
(1204, 8, 44, 'وإذ يريكموهم إذ التقيتم في أعينكم قليلا ويقللكم في أعينهم ليقضي الله أمرا كان مفعولا ۗ وإلى الله ترجع الأمور'),
(1205, 8, 45, 'يا أيها الذين آمنوا إذا لقيتم فئة فاثبتوا واذكروا الله كثيرا لعلكم تفلحون'),
(1206, 8, 46, 'وأطيعوا الله ورسوله ولا تنازعوا فتفشلوا وتذهب ريحكم ۖ واصبروا ۚ إن الله مع الصابرين'),
(1207, 8, 47, 'ولا تكونوا كالذين خرجوا من ديارهم بطرا ورئاء الناس ويصدون عن سبيل الله ۚ والله بما يعملون محيط'),
(1208, 8, 48, 'وإذ زين لهم الشيطان أعمالهم وقال لا غالب لكم اليوم من الناس وإني جار لكم ۖ فلما تراءت الفئتان نكص على عقبيه وقال إني بريء منكم إني أرى ما لا ترون إني أخاف الله ۚ والله شديد العقاب'),
(1209, 8, 49, 'إذ يقول المنافقون والذين في قلوبهم مرض غر هؤلاء دينهم ۗ ومن يتوكل على الله فإن الله عزيز حكيم'),
(1210, 8, 50, 'ولو ترى إذ يتوفى الذين كفروا ۙ الملائكة يضربون وجوههم وأدبارهم وذوقوا عذاب الحريق'),
(1211, 8, 51, 'ذلك بما قدمت أيديكم وأن الله ليس بظلام للعبيد'),
(1212, 8, 52, 'كدأب آل فرعون ۙ والذين من قبلهم ۚ كفروا بآيات الله فأخذهم الله بذنوبهم ۗ إن الله قوي شديد العقاب'),
(1213, 8, 53, 'ذلك بأن الله لم يك مغيرا نعمة أنعمها على قوم حتى يغيروا ما بأنفسهم ۙ وأن الله سميع عليم'),
(1214, 8, 54, 'كدأب آل فرعون ۙ والذين من قبلهم ۚ كذبوا بآيات ربهم فأهلكناهم بذنوبهم وأغرقنا آل فرعون ۚ وكل كانوا ظالمين'),
(1215, 8, 55, 'إن شر الدواب عند الله الذين كفروا فهم لا يؤمنون'),
(1216, 8, 56, 'الذين عاهدت منهم ثم ينقضون عهدهم في كل مرة وهم لا يتقون'),
(1217, 8, 57, 'فإما تثقفنهم في الحرب فشرد بهم من خلفهم لعلهم يذكرون'),
(1218, 8, 58, 'وإما تخافن من قوم خيانة فانبذ إليهم على سواء ۚ إن الله لا يحب الخائنين'),
(1219, 8, 59, 'ولا يحسبن الذين كفروا سبقوا ۚ إنهم لا يعجزون'),
(1220, 8, 60, 'وأعدوا لهم ما استطعتم من قوة ومن رباط الخيل ترهبون به عدو الله وعدوكم وآخرين من دونهم لا تعلمونهم الله يعلمهم ۚ وما تنفقوا من شيء في سبيل الله يوف إليكم وأنتم لا تظلمون'),
(1221, 8, 61, 'وإن جنحوا للسلم فاجنح لها وتوكل على الله ۚ إنه هو السميع العليم'),
(1222, 8, 62, 'وإن يريدوا أن يخدعوك فإن حسبك الله ۚ هو الذي أيدك بنصره وبالمؤمنين'),
(1223, 8, 63, 'وألف بين قلوبهم ۚ لو أنفقت ما في الأرض جميعا ما ألفت بين قلوبهم ولكن الله ألف بينهم ۚ إنه عزيز حكيم'),
(1224, 8, 64, 'يا أيها النبي حسبك الله ومن اتبعك من المؤمنين'),
(1225, 8, 65, 'يا أيها النبي حرض المؤمنين على القتال ۚ إن يكن منكم عشرون صابرون يغلبوا مائتين ۚ وإن يكن منكم مائة يغلبوا ألفا من الذين كفروا بأنهم قوم لا يفقهون'),
(1226, 8, 66, 'الآن خفف الله عنكم وعلم أن فيكم ضعفا ۚ فإن يكن منكم مائة صابرة يغلبوا مائتين ۚ وإن يكن منكم ألف يغلبوا ألفين بإذن الله ۗ والله مع الصابرين'),
(1227, 8, 67, 'ما كان لنبي أن يكون له أسرى حتى يثخن في الأرض ۚ تريدون عرض الدنيا والله يريد الآخرة ۗ والله عزيز حكيم'),
(1228, 8, 68, 'لولا كتاب من الله سبق لمسكم فيما أخذتم عذاب عظيم'),
(1229, 8, 69, 'فكلوا مما غنمتم حلالا طيبا ۚ واتقوا الله ۚ إن الله غفور رحيم'),
(1230, 8, 70, 'يا أيها النبي قل لمن في أيديكم من الأسرى إن يعلم الله في قلوبكم خيرا يؤتكم خيرا مما أخذ منكم ويغفر لكم ۗ والله غفور رحيم'),
(1231, 8, 71, 'وإن يريدوا خيانتك فقد خانوا الله من قبل فأمكن منهم ۗ والله عليم حكيم'),
(1232, 8, 72, 'إن الذين آمنوا وهاجروا وجاهدوا بأموالهم وأنفسهم في سبيل الله والذين آووا ونصروا أولئك بعضهم أولياء بعض ۚ والذين آمنوا ولم يهاجروا ما لكم من ولايتهم من شيء حتى يهاجروا ۚ وإن استنصروكم في الدين فعليكم النصر إلا على قوم بينكم وبينهم ميثاق ۗ والله بما تعملون بصير'),
(1233, 8, 73, 'والذين كفروا بعضهم أولياء بعض ۚ إلا تفعلوه تكن فتنة في الأرض وفساد كبير'),
(1234, 8, 74, 'والذين آمنوا وهاجروا وجاهدوا في سبيل الله والذين آووا ونصروا أولئك هم المؤمنون حقا ۚ لهم مغفرة ورزق كريم'),
(1235, 8, 75, 'والذين آمنوا من بعد وهاجروا وجاهدوا معكم فأولئك منكم ۚ وأولو الأرحام بعضهم أولى ببعض في كتاب الله ۗ إن الله بكل شيء عليم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'براءة من الله ورسوله إلى الذين عاهدتم من المشركين'),
(1237, 9, 2, 'فسيحوا في الأرض أربعة أشهر واعلموا أنكم غير معجزي الله ۙ وأن الله مخزي الكافرين'),
(1238, 9, 3, 'وأذان من الله ورسوله إلى الناس يوم الحج الأكبر أن الله بريء من المشركين ۙ ورسوله ۚ فإن تبتم فهو خير لكم ۖ وإن توليتم فاعلموا أنكم غير معجزي الله ۗ وبشر الذين كفروا بعذاب أليم'),
(1239, 9, 4, 'إلا الذين عاهدتم من المشركين ثم لم ينقصوكم شيئا ولم يظاهروا عليكم أحدا فأتموا إليهم عهدهم إلى مدتهم ۚ إن الله يحب المتقين'),
(1240, 9, 5, 'فإذا انسلخ الأشهر الحرم فاقتلوا المشركين حيث وجدتموهم وخذوهم واحصروهم واقعدوا لهم كل مرصد ۚ فإن تابوا وأقاموا الصلاة وآتوا الزكاة فخلوا سبيلهم ۚ إن الله غفور رحيم'),
(1241, 9, 6, 'وإن أحد من المشركين استجارك فأجره حتى يسمع كلام الله ثم أبلغه مأمنه ۚ ذلك بأنهم قوم لا يعلمون'),
(1242, 9, 7, 'كيف يكون للمشركين عهد عند الله وعند رسوله إلا الذين عاهدتم عند المسجد الحرام ۖ فما استقاموا لكم فاستقيموا لهم ۚ إن الله يحب المتقين'),
(1243, 9, 8, 'كيف وإن يظهروا عليكم لا يرقبوا فيكم إلا ولا ذمة ۚ يرضونكم بأفواههم وتأبى قلوبهم وأكثرهم فاسقون'),
(1244, 9, 9, 'اشتروا بآيات الله ثمنا قليلا فصدوا عن سبيله ۚ إنهم ساء ما كانوا يعملون'),
(1245, 9, 10, 'لا يرقبون في مؤمن إلا ولا ذمة ۚ وأولئك هم المعتدون'),
(1246, 9, 11, 'فإن تابوا وأقاموا الصلاة وآتوا الزكاة فإخوانكم في الدين ۗ ونفصل الآيات لقوم يعلمون'),
(1247, 9, 12, 'وإن نكثوا أيمانهم من بعد عهدهم وطعنوا في دينكم فقاتلوا أئمة الكفر ۙ إنهم لا أيمان لهم لعلهم ينتهون'),
(1248, 9, 13, 'ألا تقاتلون قوما نكثوا أيمانهم وهموا بإخراج الرسول وهم بدءوكم أول مرة ۚ أتخشونهم ۚ فالله أحق أن تخشوه إن كنتم مؤمنين'),
(1249, 9, 14, 'قاتلوهم يعذبهم الله بأيديكم ويخزهم وينصركم عليهم ويشف صدور قوم مؤمنين'),
(1250, 9, 15, 'ويذهب غيظ قلوبهم ۗ ويتوب الله على من يشاء ۗ والله عليم حكيم'),
(1251, 9, 16, 'أم حسبتم أن تتركوا ولما يعلم الله الذين جاهدوا منكم ولم يتخذوا من دون الله ولا رسوله ولا المؤمنين وليجة ۚ والله خبير بما تعملون'),
(1252, 9, 17, 'ما كان للمشركين أن يعمروا مساجد الله شاهدين على أنفسهم بالكفر ۚ أولئك حبطت أعمالهم وفي النار هم خالدون'),
(1253, 9, 18, 'إنما يعمر مساجد الله من آمن بالله واليوم الآخر وأقام الصلاة وآتى الزكاة ولم يخش إلا الله ۖ فعسى أولئك أن يكونوا من المهتدين'),
(1254, 9, 19, 'أجعلتم سقاية الحاج وعمارة المسجد الحرام كمن آمن بالله واليوم الآخر وجاهد في سبيل الله ۚ لا يستوون عند الله ۗ والله لا يهدي القوم الظالمين'),
(1255, 9, 20, 'الذين آمنوا وهاجروا وجاهدوا في سبيل الله بأموالهم وأنفسهم أعظم درجة عند الله ۚ وأولئك هم الفائزون'),
(1256, 9, 21, 'يبشرهم ربهم برحمة منه ورضوان وجنات لهم فيها نعيم مقيم'),
(1257, 9, 22, 'خالدين فيها أبدا ۚ إن الله عنده أجر عظيم'),
(1258, 9, 23, 'يا أيها الذين آمنوا لا تتخذوا آباءكم وإخوانكم أولياء إن استحبوا الكفر على الإيمان ۚ ومن يتولهم منكم فأولئك هم الظالمون'),
(1259, 9, 24, 'قل إن كان آباؤكم وأبناؤكم وإخوانكم وأزواجكم وعشيرتكم وأموال اقترفتموها وتجارة تخشون كسادها ومساكن ترضونها أحب إليكم من الله ورسوله وجهاد في سبيله فتربصوا حتى يأتي الله بأمره ۗ والله لا يهدي القوم الفاسقين'),
(1260, 9, 25, 'لقد نصركم الله في مواطن كثيرة ۙ ويوم حنين ۙ إذ أعجبتكم كثرتكم فلم تغن عنكم شيئا وضاقت عليكم الأرض بما رحبت ثم وليتم مدبرين'),
(1261, 9, 26, 'ثم أنزل الله سكينته على رسوله وعلى المؤمنين وأنزل جنودا لم تروها وعذب الذين كفروا ۚ وذلك جزاء الكافرين'),
(1262, 9, 27, 'ثم يتوب الله من بعد ذلك على من يشاء ۗ والله غفور رحيم'),
(1263, 9, 28, 'يا أيها الذين آمنوا إنما المشركون نجس فلا يقربوا المسجد الحرام بعد عامهم هذا ۚ وإن خفتم عيلة فسوف يغنيكم الله من فضله إن شاء ۚ إن الله عليم حكيم'),
(1264, 9, 29, 'قاتلوا الذين لا يؤمنون بالله ولا باليوم الآخر ولا يحرمون ما حرم الله ورسوله ولا يدينون دين الحق من الذين أوتوا الكتاب حتى يعطوا الجزية عن يد وهم صاغرون'),
(1265, 9, 30, 'وقالت اليهود عزير ابن الله وقالت النصارى المسيح ابن الله ۖ ذلك قولهم بأفواههم ۖ يضاهئون قول الذين كفروا من قبل ۚ قاتلهم الله ۚ أنى يؤفكون'),
(1266, 9, 31, 'اتخذوا أحبارهم ورهبانهم أربابا من دون الله والمسيح ابن مريم وما أمروا إلا ليعبدوا إلها واحدا ۖ لا إله إلا هو ۚ سبحانه عما يشركون'),
(1267, 9, 32, 'يريدون أن يطفئوا نور الله بأفواههم ويأبى الله إلا أن يتم نوره ولو كره الكافرون'),
(1268, 9, 33, 'هو الذي أرسل رسوله بالهدى ودين الحق ليظهره على الدين كله ولو كره المشركون'),
(1269, 9, 34, 'يا أيها الذين آمنوا إن كثيرا من الأحبار والرهبان ليأكلون أموال الناس بالباطل ويصدون عن سبيل الله ۗ والذين يكنزون الذهب والفضة ولا ينفقونها في سبيل الله فبشرهم بعذاب أليم'),
(1270, 9, 35, 'يوم يحمى عليها في نار جهنم فتكوى بها جباههم وجنوبهم وظهورهم ۖ هذا ما كنزتم لأنفسكم فذوقوا ما كنتم تكنزون'),
(1271, 9, 36, 'إن عدة الشهور عند الله اثنا عشر شهرا في كتاب الله يوم خلق السماوات والأرض منها أربعة حرم ۚ ذلك الدين القيم ۚ فلا تظلموا فيهن أنفسكم ۚ وقاتلوا المشركين كافة كما يقاتلونكم كافة ۚ واعلموا أن الله مع المتقين'),
(1272, 9, 37, 'إنما النسيء زيادة في الكفر ۖ يضل به الذين كفروا يحلونه عاما ويحرمونه عاما ليواطئوا عدة ما حرم الله فيحلوا ما حرم الله ۚ زين لهم سوء أعمالهم ۗ والله لا يهدي القوم الكافرين'),
(1273, 9, 38, 'يا أيها الذين آمنوا ما لكم إذا قيل لكم انفروا في سبيل الله اثاقلتم إلى الأرض ۚ أرضيتم بالحياة الدنيا من الآخرة ۚ فما متاع الحياة الدنيا في الآخرة إلا قليل'),
(1274, 9, 39, 'إلا تنفروا يعذبكم عذابا أليما ويستبدل قوما غيركم ولا تضروه شيئا ۗ والله على كل شيء قدير'),
(1275, 9, 40, 'إلا تنصروه فقد نصره الله إذ أخرجه الذين كفروا ثاني اثنين إذ هما في الغار إذ يقول لصاحبه لا تحزن إن الله معنا ۖ فأنزل الله سكينته عليه وأيده بجنود لم تروها وجعل كلمة الذين كفروا السفلى ۗ وكلمة الله هي العليا ۗ والله عزيز حكيم'),
(1276, 9, 41, 'انفروا خفافا وثقالا وجاهدوا بأموالكم وأنفسكم في سبيل الله ۚ ذلكم خير لكم إن كنتم تعلمون'),
(1277, 9, 42, 'لو كان عرضا قريبا وسفرا قاصدا لاتبعوك ولكن بعدت عليهم الشقة ۚ وسيحلفون بالله لو استطعنا لخرجنا معكم يهلكون أنفسهم والله يعلم إنهم لكاذبون'),
(1278, 9, 43, 'عفا الله عنك لم أذنت لهم حتى يتبين لك الذين صدقوا وتعلم الكاذبين'),
(1279, 9, 44, 'لا يستأذنك الذين يؤمنون بالله واليوم الآخر أن يجاهدوا بأموالهم وأنفسهم ۗ والله عليم بالمتقين'),
(1280, 9, 45, 'إنما يستأذنك الذين لا يؤمنون بالله واليوم الآخر وارتابت قلوبهم فهم في ريبهم يترددون'),
(1281, 9, 46, 'ولو أرادوا الخروج لأعدوا له عدة ولكن كره الله انبعاثهم فثبطهم وقيل اقعدوا مع القاعدين'),
(1282, 9, 47, 'لو خرجوا فيكم ما زادوكم إلا خبالا ولأوضعوا خلالكم يبغونكم الفتنة وفيكم سماعون لهم ۗ والله عليم بالظالمين'),
(1283, 9, 48, 'لقد ابتغوا الفتنة من قبل وقلبوا لك الأمور حتى جاء الحق وظهر أمر الله وهم كارهون'),
(1284, 9, 49, 'ومنهم من يقول ائذن لي ولا تفتني ۚ ألا في الفتنة سقطوا ۗ وإن جهنم لمحيطة بالكافرين'),
(1285, 9, 50, 'إن تصبك حسنة تسؤهم ۖ وإن تصبك مصيبة يقولوا قد أخذنا أمرنا من قبل ويتولوا وهم فرحون'),
(1286, 9, 51, 'قل لن يصيبنا إلا ما كتب الله لنا هو مولانا ۚ وعلى الله فليتوكل المؤمنون'),
(1287, 9, 52, 'قل هل تربصون بنا إلا إحدى الحسنيين ۖ ونحن نتربص بكم أن يصيبكم الله بعذاب من عنده أو بأيدينا ۖ فتربصوا إنا معكم متربصون'),
(1288, 9, 53, 'قل أنفقوا طوعا أو كرها لن يتقبل منكم ۖ إنكم كنتم قوما فاسقين'),
(1289, 9, 54, 'وما منعهم أن تقبل منهم نفقاتهم إلا أنهم كفروا بالله وبرسوله ولا يأتون الصلاة إلا وهم كسالى ولا ينفقون إلا وهم كارهون'),
(1290, 9, 55, 'فلا تعجبك أموالهم ولا أولادهم ۚ إنما يريد الله ليعذبهم بها في الحياة الدنيا وتزهق أنفسهم وهم كافرون'),
(1291, 9, 56, 'ويحلفون بالله إنهم لمنكم وما هم منكم ولكنهم قوم يفرقون'),
(1292, 9, 57, 'لو يجدون ملجأ أو مغارات أو مدخلا لولوا إليه وهم يجمحون'),
(1293, 9, 58, 'ومنهم من يلمزك في الصدقات فإن أعطوا منها رضوا وإن لم يعطوا منها إذا هم يسخطون'),
(1294, 9, 59, 'ولو أنهم رضوا ما آتاهم الله ورسوله وقالوا حسبنا الله سيؤتينا الله من فضله ورسوله إنا إلى الله راغبون'),
(1295, 9, 60, 'إنما الصدقات للفقراء والمساكين والعاملين عليها والمؤلفة قلوبهم وفي الرقاب والغارمين وفي سبيل الله وابن السبيل ۖ فريضة من الله ۗ والله عليم حكيم'),
(1296, 9, 61, 'ومنهم الذين يؤذون النبي ويقولون هو أذن ۚ قل أذن خير لكم يؤمن بالله ويؤمن للمؤمنين ورحمة للذين آمنوا منكم ۚ والذين يؤذون رسول الله لهم عذاب أليم'),
(1297, 9, 62, 'يحلفون بالله لكم ليرضوكم والله ورسوله أحق أن يرضوه إن كانوا مؤمنين'),
(1298, 9, 63, 'ألم يعلموا أنه من يحادد الله ورسوله فأن له نار جهنم خالدا فيها ۚ ذلك الخزي العظيم'),
(1299, 9, 64, 'يحذر المنافقون أن تنزل عليهم سورة تنبئهم بما في قلوبهم ۚ قل استهزئوا إن الله مخرج ما تحذرون'),
(1300, 9, 65, 'ولئن سألتهم ليقولن إنما كنا نخوض ونلعب ۚ قل أبالله وآياته ورسوله كنتم تستهزئون'),
(1301, 9, 66, 'لا تعتذروا قد كفرتم بعد إيمانكم ۚ إن نعف عن طائفة منكم نعذب طائفة بأنهم كانوا مجرمين'),
(1302, 9, 67, 'المنافقون والمنافقات بعضهم من بعض ۚ يأمرون بالمنكر وينهون عن المعروف ويقبضون أيديهم ۚ نسوا الله فنسيهم ۗ إن المنافقين هم الفاسقون'),
(1303, 9, 68, 'وعد الله المنافقين والمنافقات والكفار نار جهنم خالدين فيها ۚ هي حسبهم ۚ ولعنهم الله ۖ ولهم عذاب مقيم'),
(1304, 9, 69, 'كالذين من قبلكم كانوا أشد منكم قوة وأكثر أموالا وأولادا فاستمتعوا بخلاقهم فاستمتعتم بخلاقكم كما استمتع الذين من قبلكم بخلاقهم وخضتم كالذي خاضوا ۚ أولئك حبطت أعمالهم في الدنيا والآخرة ۖ وأولئك هم الخاسرون'),
(1305, 9, 70, 'ألم يأتهم نبأ الذين من قبلهم قوم نوح وعاد وثمود وقوم إبراهيم وأصحاب مدين والمؤتفكات ۚ أتتهم رسلهم بالبينات ۖ فما كان الله ليظلمهم ولكن كانوا أنفسهم يظلمون'),
(1306, 9, 71, 'والمؤمنون والمؤمنات بعضهم أولياء بعض ۚ يأمرون بالمعروف وينهون عن المنكر ويقيمون الصلاة ويؤتون الزكاة ويطيعون الله ورسوله ۚ أولئك سيرحمهم الله ۗ إن الله عزيز حكيم'),
(1307, 9, 72, 'وعد الله المؤمنين والمؤمنات جنات تجري من تحتها الأنهار خالدين فيها ومساكن طيبة في جنات عدن ۚ ورضوان من الله أكبر ۚ ذلك هو الفوز العظيم'),
(1308, 9, 73, 'يا أيها النبي جاهد الكفار والمنافقين واغلظ عليهم ۚ ومأواهم جهنم ۖ وبئس المصير'),
(1309, 9, 74, 'يحلفون بالله ما قالوا ولقد قالوا كلمة الكفر وكفروا بعد إسلامهم وهموا بما لم ينالوا ۚ وما نقموا إلا أن أغناهم الله ورسوله من فضله ۚ فإن يتوبوا يك خيرا لهم ۖ وإن يتولوا يعذبهم الله عذابا أليما في الدنيا والآخرة ۚ وما لهم في الأرض من ولي ولا نصير'),
(1310, 9, 75, 'ومنهم من عاهد الله لئن آتانا من فضله لنصدقن ولنكونن من الصالحين'),
(1311, 9, 76, 'فلما آتاهم من فضله بخلوا به وتولوا وهم معرضون'),
(1312, 9, 77, 'فأعقبهم نفاقا في قلوبهم إلى يوم يلقونه بما أخلفوا الله ما وعدوه وبما كانوا يكذبون'),
(1313, 9, 78, 'ألم يعلموا أن الله يعلم سرهم ونجواهم وأن الله علام الغيوب'),
(1314, 9, 79, 'الذين يلمزون المطوعين من المؤمنين في الصدقات والذين لا يجدون إلا جهدهم فيسخرون منهم ۙ سخر الله منهم ولهم عذاب أليم'),
(1315, 9, 80, 'استغفر لهم أو لا تستغفر لهم إن تستغفر لهم سبعين مرة فلن يغفر الله لهم ۚ ذلك بأنهم كفروا بالله ورسوله ۗ والله لا يهدي القوم الفاسقين'),
(1316, 9, 81, 'فرح المخلفون بمقعدهم خلاف رسول الله وكرهوا أن يجاهدوا بأموالهم وأنفسهم في سبيل الله وقالوا لا تنفروا في الحر ۗ قل نار جهنم أشد حرا ۚ لو كانوا يفقهون'),
(1317, 9, 82, 'فليضحكوا قليلا وليبكوا كثيرا جزاء بما كانوا يكسبون'),
(1318, 9, 83, 'فإن رجعك الله إلى طائفة منهم فاستأذنوك للخروج فقل لن تخرجوا معي أبدا ولن تقاتلوا معي عدوا ۖ إنكم رضيتم بالقعود أول مرة فاقعدوا مع الخالفين'),
(1319, 9, 84, 'ولا تصل على أحد منهم مات أبدا ولا تقم على قبره ۖ إنهم كفروا بالله ورسوله وماتوا وهم فاسقون'),
(1320, 9, 85, 'ولا تعجبك أموالهم وأولادهم ۚ إنما يريد الله أن يعذبهم بها في الدنيا وتزهق أنفسهم وهم كافرون'),
(1321, 9, 86, 'وإذا أنزلت سورة أن آمنوا بالله وجاهدوا مع رسوله استأذنك أولو الطول منهم وقالوا ذرنا نكن مع القاعدين'),
(1322, 9, 87, 'رضوا بأن يكونوا مع الخوالف وطبع على قلوبهم فهم لا يفقهون'),
(1323, 9, 88, 'لكن الرسول والذين آمنوا معه جاهدوا بأموالهم وأنفسهم ۚ وأولئك لهم الخيرات ۖ وأولئك هم المفلحون'),
(1324, 9, 89, 'أعد الله لهم جنات تجري من تحتها الأنهار خالدين فيها ۚ ذلك الفوز العظيم'),
(1325, 9, 90, 'وجاء المعذرون من الأعراب ليؤذن لهم وقعد الذين كذبوا الله ورسوله ۚ سيصيب الذين كفروا منهم عذاب أليم'),
(1326, 9, 91, 'ليس على الضعفاء ولا على المرضى ولا على الذين لا يجدون ما ينفقون حرج إذا نصحوا لله ورسوله ۚ ما على المحسنين من سبيل ۚ والله غفور رحيم'),
(1327, 9, 92, 'ولا على الذين إذا ما أتوك لتحملهم قلت لا أجد ما أحملكم عليه تولوا وأعينهم تفيض من الدمع حزنا ألا يجدوا ما ينفقون'),
(1328, 9, 93, 'إنما السبيل على الذين يستأذنونك وهم أغنياء ۚ رضوا بأن يكونوا مع الخوالف وطبع الله على قلوبهم فهم لا يعلمون'),
(1329, 9, 94, 'يعتذرون إليكم إذا رجعتم إليهم ۚ قل لا تعتذروا لن نؤمن لكم قد نبأنا الله من أخباركم ۚ وسيرى الله عملكم ورسوله ثم تردون إلى عالم الغيب والشهادة فينبئكم بما كنتم تعملون'),
(1330, 9, 95, 'سيحلفون بالله لكم إذا انقلبتم إليهم لتعرضوا عنهم ۖ فأعرضوا عنهم ۖ إنهم رجس ۖ ومأواهم جهنم جزاء بما كانوا يكسبون'),
(1331, 9, 96, 'يحلفون لكم لترضوا عنهم ۖ فإن ترضوا عنهم فإن الله لا يرضى عن القوم الفاسقين'),
(1332, 9, 97, 'الأعراب أشد كفرا ونفاقا وأجدر ألا يعلموا حدود ما أنزل الله على رسوله ۗ والله عليم حكيم'),
(1333, 9, 98, 'ومن الأعراب من يتخذ ما ينفق مغرما ويتربص بكم الدوائر ۚ عليهم دائرة السوء ۗ والله سميع عليم'),
(1334, 9, 99, 'ومن الأعراب من يؤمن بالله واليوم الآخر ويتخذ ما ينفق قربات عند الله وصلوات الرسول ۚ ألا إنها قربة لهم ۚ سيدخلهم الله في رحمته ۗ إن الله غفور رحيم'),
(1335, 9, 100, 'والسابقون الأولون من المهاجرين والأنصار والذين اتبعوهم بإحسان رضي الله عنهم ورضوا عنه وأعد لهم جنات تجري تحتها الأنهار خالدين فيها أبدا ۚ ذلك الفوز العظيم'),
(1336, 9, 101, 'وممن حولكم من الأعراب منافقون ۖ ومن أهل المدينة ۖ مردوا على النفاق لا تعلمهم ۖ نحن نعلمهم ۚ سنعذبهم مرتين ثم يردون إلى عذاب عظيم'),
(1337, 9, 102, 'وآخرون اعترفوا بذنوبهم خلطوا عملا صالحا وآخر سيئا عسى الله أن يتوب عليهم ۚ إن الله غفور رحيم'),
(1338, 9, 103, 'خذ من أموالهم صدقة تطهرهم وتزكيهم بها وصل عليهم ۖ إن صلاتك سكن لهم ۗ والله سميع عليم'),
(1339, 9, 104, 'ألم يعلموا أن الله هو يقبل التوبة عن عباده ويأخذ الصدقات وأن الله هو التواب الرحيم'),
(1340, 9, 105, 'وقل اعملوا فسيرى الله عملكم ورسوله والمؤمنون ۖ وستردون إلى عالم الغيب والشهادة فينبئكم بما كنتم تعملون'),
(1341, 9, 106, 'وآخرون مرجون لأمر الله إما يعذبهم وإما يتوب عليهم ۗ والله عليم حكيم'),
(1342, 9, 107, 'والذين اتخذوا مسجدا ضرارا وكفرا وتفريقا بين المؤمنين وإرصادا لمن حارب الله ورسوله من قبل ۚ وليحلفن إن أردنا إلا الحسنى ۖ والله يشهد إنهم لكاذبون'),
(1343, 9, 108, 'لا تقم فيه أبدا ۚ لمسجد أسس على التقوى من أول يوم أحق أن تقوم فيه ۚ فيه رجال يحبون أن يتطهروا ۚ والله يحب المطهرين'),
(1344, 9, 109, 'أفمن أسس بنيانه على تقوى من الله ورضوان خير أم من أسس بنيانه على شفا جرف هار فانهار به في نار جهنم ۗ والله لا يهدي القوم الظالمين'),
(1345, 9, 110, 'لا يزال بنيانهم الذي بنوا ريبة في قلوبهم إلا أن تقطع قلوبهم ۗ والله عليم حكيم'),
(1346, 9, 111, 'إن الله اشترى من المؤمنين أنفسهم وأموالهم بأن لهم الجنة ۚ يقاتلون في سبيل الله فيقتلون ويقتلون ۖ وعدا عليه حقا في التوراة والإنجيل والقرآن ۚ ومن أوفى بعهده من الله ۚ فاستبشروا ببيعكم الذي بايعتم به ۚ وذلك هو الفوز العظيم'),
(1347, 9, 112, 'التائبون العابدون الحامدون السائحون الراكعون الساجدون الآمرون بالمعروف والناهون عن المنكر والحافظون لحدود الله ۗ وبشر المؤمنين'),
(1348, 9, 113, 'ما كان للنبي والذين آمنوا أن يستغفروا للمشركين ولو كانوا أولي قربى من بعد ما تبين لهم أنهم أصحاب الجحيم'),
(1349, 9, 114, 'وما كان استغفار إبراهيم لأبيه إلا عن موعدة وعدها إياه فلما تبين له أنه عدو لله تبرأ منه ۚ إن إبراهيم لأواه حليم'),
(1350, 9, 115, 'وما كان الله ليضل قوما بعد إذ هداهم حتى يبين لهم ما يتقون ۚ إن الله بكل شيء عليم'),
(1351, 9, 116, 'إن الله له ملك السماوات والأرض ۖ يحيي ويميت ۚ وما لكم من دون الله من ولي ولا نصير'),
(1352, 9, 117, 'لقد تاب الله على النبي والمهاجرين والأنصار الذين اتبعوه في ساعة العسرة من بعد ما كاد يزيغ قلوب فريق منهم ثم تاب عليهم ۚ إنه بهم رءوف رحيم'),
(1353, 9, 118, 'وعلى الثلاثة الذين خلفوا حتى إذا ضاقت عليهم الأرض بما رحبت وضاقت عليهم أنفسهم وظنوا أن لا ملجأ من الله إلا إليه ثم تاب عليهم ليتوبوا ۚ إن الله هو التواب الرحيم'),
(1354, 9, 119, 'يا أيها الذين آمنوا اتقوا الله وكونوا مع الصادقين'),
(1355, 9, 120, 'ما كان لأهل المدينة ومن حولهم من الأعراب أن يتخلفوا عن رسول الله ولا يرغبوا بأنفسهم عن نفسه ۚ ذلك بأنهم لا يصيبهم ظمأ ولا نصب ولا مخمصة في سبيل الله ولا يطئون موطئا يغيظ الكفار ولا ينالون من عدو نيلا إلا كتب لهم به عمل صالح ۚ إن الله لا يضيع أجر المحسنين'),
(1356, 9, 121, 'ولا ينفقون نفقة صغيرة ولا كبيرة ولا يقطعون واديا إلا كتب لهم ليجزيهم الله أحسن ما كانوا يعملون'),
(1357, 9, 122, 'وما كان المؤمنون لينفروا كافة ۚ فلولا نفر من كل فرقة منهم طائفة ليتفقهوا في الدين ولينذروا قومهم إذا رجعوا إليهم لعلهم يحذرون'),
(1358, 9, 123, 'يا أيها الذين آمنوا قاتلوا الذين يلونكم من الكفار وليجدوا فيكم غلظة ۚ واعلموا أن الله مع المتقين'),
(1359, 9, 124, 'وإذا ما أنزلت سورة فمنهم من يقول أيكم زادته هذه إيمانا ۚ فأما الذين آمنوا فزادتهم إيمانا وهم يستبشرون'),
(1360, 9, 125, 'وأما الذين في قلوبهم مرض فزادتهم رجسا إلى رجسهم وماتوا وهم كافرون'),
(1361, 9, 126, 'أولا يرون أنهم يفتنون في كل عام مرة أو مرتين ثم لا يتوبون ولا هم يذكرون'),
(1362, 9, 127, 'وإذا ما أنزلت سورة نظر بعضهم إلى بعض هل يراكم من أحد ثم انصرفوا ۚ صرف الله قلوبهم بأنهم قوم لا يفقهون'),
(1363, 9, 128, 'لقد جاءكم رسول من أنفسكم عزيز عليه ما عنتم حريص عليكم بالمؤمنين رءوف رحيم'),
(1364, 9, 129, 'فإن تولوا فقل حسبي الله لا إله إلا هو ۖ عليه توكلت ۖ وهو رب العرش العظيم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'بسم الله الرحمن الرحيم الر ۚ تلك آيات الكتاب الحكيم'),
(1366, 10, 2, 'أكان للناس عجبا أن أوحينا إلى رجل منهم أن أنذر الناس وبشر الذين آمنوا أن لهم قدم صدق عند ربهم ۗ قال الكافرون إن هذا لساحر مبين'),
(1367, 10, 3, 'إن ربكم الله الذي خلق السماوات والأرض في ستة أيام ثم استوى على العرش ۖ يدبر الأمر ۖ ما من شفيع إلا من بعد إذنه ۚ ذلكم الله ربكم فاعبدوه ۚ أفلا تذكرون'),
(1368, 10, 4, 'إليه مرجعكم جميعا ۖ وعد الله حقا ۚ إنه يبدأ الخلق ثم يعيده ليجزي الذين آمنوا وعملوا الصالحات بالقسط ۚ والذين كفروا لهم شراب من حميم وعذاب أليم بما كانوا يكفرون'),
(1369, 10, 5, 'هو الذي جعل الشمس ضياء والقمر نورا وقدره منازل لتعلموا عدد السنين والحساب ۚ ما خلق الله ذلك إلا بالحق ۚ يفصل الآيات لقوم يعلمون'),
(1370, 10, 6, 'إن في اختلاف الليل والنهار وما خلق الله في السماوات والأرض لآيات لقوم يتقون'),
(1371, 10, 7, 'إن الذين لا يرجون لقاءنا ورضوا بالحياة الدنيا واطمأنوا بها والذين هم عن آياتنا غافلون'),
(1372, 10, 8, 'أولئك مأواهم النار بما كانوا يكسبون'),
(1373, 10, 9, 'إن الذين آمنوا وعملوا الصالحات يهديهم ربهم بإيمانهم ۖ تجري من تحتهم الأنهار في جنات النعيم'),
(1374, 10, 10, 'دعواهم فيها سبحانك اللهم وتحيتهم فيها سلام ۚ وآخر دعواهم أن الحمد لله رب العالمين'),
(1375, 10, 11, 'ولو يعجل الله للناس الشر استعجالهم بالخير لقضي إليهم أجلهم ۖ فنذر الذين لا يرجون لقاءنا في طغيانهم يعمهون'),
(1376, 10, 12, 'وإذا مس الإنسان الضر دعانا لجنبه أو قاعدا أو قائما فلما كشفنا عنه ضره مر كأن لم يدعنا إلى ضر مسه ۚ كذلك زين للمسرفين ما كانوا يعملون'),
(1377, 10, 13, 'ولقد أهلكنا القرون من قبلكم لما ظلموا ۙ وجاءتهم رسلهم بالبينات وما كانوا ليؤمنوا ۚ كذلك نجزي القوم المجرمين'),
(1378, 10, 14, 'ثم جعلناكم خلائف في الأرض من بعدهم لننظر كيف تعملون'),
(1379, 10, 15, 'وإذا تتلى عليهم آياتنا بينات ۙ قال الذين لا يرجون لقاءنا ائت بقرآن غير هذا أو بدله ۚ قل ما يكون لي أن أبدله من تلقاء نفسي ۖ إن أتبع إلا ما يوحى إلي ۖ إني أخاف إن عصيت ربي عذاب يوم عظيم'),
(1380, 10, 16, 'قل لو شاء الله ما تلوته عليكم ولا أدراكم به ۖ فقد لبثت فيكم عمرا من قبله ۚ أفلا تعقلون'),
(1381, 10, 17, 'فمن أظلم ممن افترى على الله كذبا أو كذب بآياته ۚ إنه لا يفلح المجرمون'),
(1382, 10, 18, 'ويعبدون من دون الله ما لا يضرهم ولا ينفعهم ويقولون هؤلاء شفعاؤنا عند الله ۚ قل أتنبئون الله بما لا يعلم في السماوات ولا في الأرض ۚ سبحانه وتعالى عما يشركون'),
(1383, 10, 19, 'وما كان الناس إلا أمة واحدة فاختلفوا ۚ ولولا كلمة سبقت من ربك لقضي بينهم فيما فيه يختلفون'),
(1384, 10, 20, 'ويقولون لولا أنزل عليه آية من ربه ۖ فقل إنما الغيب لله فانتظروا إني معكم من المنتظرين'),
(1385, 10, 21, 'وإذا أذقنا الناس رحمة من بعد ضراء مستهم إذا لهم مكر في آياتنا ۚ قل الله أسرع مكرا ۚ إن رسلنا يكتبون ما تمكرون'),
(1386, 10, 22, 'هو الذي يسيركم في البر والبحر ۖ حتى إذا كنتم في الفلك وجرين بهم بريح طيبة وفرحوا بها جاءتها ريح عاصف وجاءهم الموج من كل مكان وظنوا أنهم أحيط بهم ۙ دعوا الله مخلصين له الدين لئن أنجيتنا من هذه لنكونن من الشاكرين'),
(1387, 10, 23, 'فلما أنجاهم إذا هم يبغون في الأرض بغير الحق ۗ يا أيها الناس إنما بغيكم على أنفسكم ۖ متاع الحياة الدنيا ۖ ثم إلينا مرجعكم فننبئكم بما كنتم تعملون'),
(1388, 10, 24, 'إنما مثل الحياة الدنيا كماء أنزلناه من السماء فاختلط به نبات الأرض مما يأكل الناس والأنعام حتى إذا أخذت الأرض زخرفها وازينت وظن أهلها أنهم قادرون عليها أتاها أمرنا ليلا أو نهارا فجعلناها حصيدا كأن لم تغن بالأمس ۚ كذلك نفصل الآيات لقوم يتفكرون'),
(1389, 10, 25, 'والله يدعو إلى دار السلام ويهدي من يشاء إلى صراط مستقيم'),
(1390, 10, 26, 'للذين أحسنوا الحسنى وزيادة ۖ ولا يرهق وجوههم قتر ولا ذلة ۚ أولئك أصحاب الجنة ۖ هم فيها خالدون'),
(1391, 10, 27, 'والذين كسبوا السيئات جزاء سيئة بمثلها وترهقهم ذلة ۖ ما لهم من الله من عاصم ۖ كأنما أغشيت وجوههم قطعا من الليل مظلما ۚ أولئك أصحاب النار ۖ هم فيها خالدون'),
(1392, 10, 28, 'ويوم نحشرهم جميعا ثم نقول للذين أشركوا مكانكم أنتم وشركاؤكم ۚ فزيلنا بينهم ۖ وقال شركاؤهم ما كنتم إيانا تعبدون'),
(1393, 10, 29, 'فكفى بالله شهيدا بيننا وبينكم إن كنا عن عبادتكم لغافلين'),
(1394, 10, 30, 'هنالك تبلو كل نفس ما أسلفت ۚ وردوا إلى الله مولاهم الحق ۖ وضل عنهم ما كانوا يفترون'),
(1395, 10, 31, 'قل من يرزقكم من السماء والأرض أمن يملك السمع والأبصار ومن يخرج الحي من الميت ويخرج الميت من الحي ومن يدبر الأمر ۚ فسيقولون الله ۚ فقل أفلا تتقون'),
(1396, 10, 32, 'فذلكم الله ربكم الحق ۖ فماذا بعد الحق إلا الضلال ۖ فأنى تصرفون'),
(1397, 10, 33, 'كذلك حقت كلمت ربك على الذين فسقوا أنهم لا يؤمنون'),
(1398, 10, 34, 'قل هل من شركائكم من يبدأ الخلق ثم يعيده ۚ قل الله يبدأ الخلق ثم يعيده ۖ فأنى تؤفكون'),
(1399, 10, 35, 'قل هل من شركائكم من يهدي إلى الحق ۚ قل الله يهدي للحق ۗ أفمن يهدي إلى الحق أحق أن يتبع أمن لا يهدي إلا أن يهدى ۖ فما لكم كيف تحكمون'),
(1400, 10, 36, 'وما يتبع أكثرهم إلا ظنا ۚ إن الظن لا يغني من الحق شيئا ۚ إن الله عليم بما يفعلون'),
(1401, 10, 37, 'وما كان هذا القرآن أن يفترى من دون الله ولكن تصديق الذي بين يديه وتفصيل الكتاب لا ريب فيه من رب العالمين'),
(1402, 10, 38, 'أم يقولون افتراه ۖ قل فأتوا بسورة مثله وادعوا من استطعتم من دون الله إن كنتم صادقين'),
(1403, 10, 39, 'بل كذبوا بما لم يحيطوا بعلمه ولما يأتهم تأويله ۚ كذلك كذب الذين من قبلهم ۖ فانظر كيف كان عاقبة الظالمين'),
(1404, 10, 40, 'ومنهم من يؤمن به ومنهم من لا يؤمن به ۚ وربك أعلم بالمفسدين'),
(1405, 10, 41, 'وإن كذبوك فقل لي عملي ولكم عملكم ۖ أنتم بريئون مما أعمل وأنا بريء مما تعملون'),
(1406, 10, 42, 'ومنهم من يستمعون إليك ۚ أفأنت تسمع الصم ولو كانوا لا يعقلون'),
(1407, 10, 43, 'ومنهم من ينظر إليك ۚ أفأنت تهدي العمي ولو كانوا لا يبصرون'),
(1408, 10, 44, 'إن الله لا يظلم الناس شيئا ولكن الناس أنفسهم يظلمون'),
(1409, 10, 45, 'ويوم يحشرهم كأن لم يلبثوا إلا ساعة من النهار يتعارفون بينهم ۚ قد خسر الذين كذبوا بلقاء الله وما كانوا مهتدين'),
(1410, 10, 46, 'وإما نرينك بعض الذي نعدهم أو نتوفينك فإلينا مرجعهم ثم الله شهيد على ما يفعلون'),
(1411, 10, 47, 'ولكل أمة رسول ۖ فإذا جاء رسولهم قضي بينهم بالقسط وهم لا يظلمون'),
(1412, 10, 48, 'ويقولون متى هذا الوعد إن كنتم صادقين'),
(1413, 10, 49, 'قل لا أملك لنفسي ضرا ولا نفعا إلا ما شاء الله ۗ لكل أمة أجل ۚ إذا جاء أجلهم فلا يستأخرون ساعة ۖ ولا يستقدمون'),
(1414, 10, 50, 'قل أرأيتم إن أتاكم عذابه بياتا أو نهارا ماذا يستعجل منه المجرمون'),
(1415, 10, 51, 'أثم إذا ما وقع آمنتم به ۚ آلآن وقد كنتم به تستعجلون'),
(1416, 10, 52, 'ثم قيل للذين ظلموا ذوقوا عذاب الخلد هل تجزون إلا بما كنتم تكسبون'),
(1417, 10, 53, 'ويستنبئونك أحق هو ۖ قل إي وربي إنه لحق ۖ وما أنتم بمعجزين'),
(1418, 10, 54, 'ولو أن لكل نفس ظلمت ما في الأرض لافتدت به ۗ وأسروا الندامة لما رأوا العذاب ۖ وقضي بينهم بالقسط ۚ وهم لا يظلمون'),
(1419, 10, 55, 'ألا إن لله ما في السماوات والأرض ۗ ألا إن وعد الله حق ولكن أكثرهم لا يعلمون'),
(1420, 10, 56, 'هو يحيي ويميت وإليه ترجعون'),
(1421, 10, 57, 'يا أيها الناس قد جاءتكم موعظة من ربكم وشفاء لما في الصدور وهدى ورحمة للمؤمنين'),
(1422, 10, 58, 'قل بفضل الله وبرحمته فبذلك فليفرحوا هو خير مما يجمعون'),
(1423, 10, 59, 'قل أرأيتم ما أنزل الله لكم من رزق فجعلتم منه حراما وحلالا قل آلله أذن لكم ۖ أم على الله تفترون'),
(1424, 10, 60, 'وما ظن الذين يفترون على الله الكذب يوم القيامة ۗ إن الله لذو فضل على الناس ولكن أكثرهم لا يشكرون'),
(1425, 10, 61, 'وما تكون في شأن وما تتلو منه من قرآن ولا تعملون من عمل إلا كنا عليكم شهودا إذ تفيضون فيه ۚ وما يعزب عن ربك من مثقال ذرة في الأرض ولا في السماء ولا أصغر من ذلك ولا أكبر إلا في كتاب مبين'),
(1426, 10, 62, 'ألا إن أولياء الله لا خوف عليهم ولا هم يحزنون'),
(1427, 10, 63, 'الذين آمنوا وكانوا يتقون'),
(1428, 10, 64, 'لهم البشرى في الحياة الدنيا وفي الآخرة ۚ لا تبديل لكلمات الله ۚ ذلك هو الفوز العظيم'),
(1429, 10, 65, 'ولا يحزنك قولهم ۘ إن العزة لله جميعا ۚ هو السميع العليم'),
(1430, 10, 66, 'ألا إن لله من في السماوات ومن في الأرض ۗ وما يتبع الذين يدعون من دون الله شركاء ۚ إن يتبعون إلا الظن وإن هم إلا يخرصون'),
(1431, 10, 67, 'هو الذي جعل لكم الليل لتسكنوا فيه والنهار مبصرا ۚ إن في ذلك لآيات لقوم يسمعون'),
(1432, 10, 68, 'قالوا اتخذ الله ولدا ۗ سبحانه ۖ هو الغني ۖ له ما في السماوات وما في الأرض ۚ إن عندكم من سلطان بهذا ۚ أتقولون على الله ما لا تعلمون'),
(1433, 10, 69, 'قل إن الذين يفترون على الله الكذب لا يفلحون'),
(1434, 10, 70, 'متاع في الدنيا ثم إلينا مرجعهم ثم نذيقهم العذاب الشديد بما كانوا يكفرون'),
(1435, 10, 71, 'واتل عليهم نبأ نوح إذ قال لقومه يا قوم إن كان كبر عليكم مقامي وتذكيري بآيات الله فعلى الله توكلت فأجمعوا أمركم وشركاءكم ثم لا يكن أمركم عليكم غمة ثم اقضوا إلي ولا تنظرون'),
(1436, 10, 72, 'فإن توليتم فما سألتكم من أجر ۖ إن أجري إلا على الله ۖ وأمرت أن أكون من المسلمين'),
(1437, 10, 73, 'فكذبوه فنجيناه ومن معه في الفلك وجعلناهم خلائف وأغرقنا الذين كذبوا بآياتنا ۖ فانظر كيف كان عاقبة المنذرين'),
(1438, 10, 74, 'ثم بعثنا من بعده رسلا إلى قومهم فجاءوهم بالبينات فما كانوا ليؤمنوا بما كذبوا به من قبل ۚ كذلك نطبع على قلوب المعتدين'),
(1439, 10, 75, 'ثم بعثنا من بعدهم موسى وهارون إلى فرعون وملئه بآياتنا فاستكبروا وكانوا قوما مجرمين'),
(1440, 10, 76, 'فلما جاءهم الحق من عندنا قالوا إن هذا لسحر مبين'),
(1441, 10, 77, 'قال موسى أتقولون للحق لما جاءكم ۖ أسحر هذا ولا يفلح الساحرون'),
(1442, 10, 78, 'قالوا أجئتنا لتلفتنا عما وجدنا عليه آباءنا وتكون لكما الكبرياء في الأرض وما نحن لكما بمؤمنين'),
(1443, 10, 79, 'وقال فرعون ائتوني بكل ساحر عليم'),
(1444, 10, 80, 'فلما جاء السحرة قال لهم موسى ألقوا ما أنتم ملقون'),
(1445, 10, 81, 'فلما ألقوا قال موسى ما جئتم به السحر ۖ إن الله سيبطله ۖ إن الله لا يصلح عمل المفسدين'),
(1446, 10, 82, 'ويحق الله الحق بكلماته ولو كره المجرمون'),
(1447, 10, 83, 'فما آمن لموسى إلا ذرية من قومه على خوف من فرعون وملئهم أن يفتنهم ۚ وإن فرعون لعال في الأرض وإنه لمن المسرفين'),
(1448, 10, 84, 'وقال موسى يا قوم إن كنتم آمنتم بالله فعليه توكلوا إن كنتم مسلمين'),
(1449, 10, 85, 'فقالوا على الله توكلنا ربنا لا تجعلنا فتنة للقوم الظالمين'),
(1450, 10, 86, 'ونجنا برحمتك من القوم الكافرين'),
(1451, 10, 87, 'وأوحينا إلى موسى وأخيه أن تبوآ لقومكما بمصر بيوتا واجعلوا بيوتكم قبلة وأقيموا الصلاة ۗ وبشر المؤمنين'),
(1452, 10, 88, 'وقال موسى ربنا إنك آتيت فرعون وملأه زينة وأموالا في الحياة الدنيا ربنا ليضلوا عن سبيلك ۖ ربنا اطمس على أموالهم واشدد على قلوبهم فلا يؤمنوا حتى يروا العذاب الأليم'),
(1453, 10, 89, 'قال قد أجيبت دعوتكما فاستقيما ولا تتبعان سبيل الذين لا يعلمون'),
(1454, 10, 90, 'وجاوزنا ببني إسرائيل البحر فأتبعهم فرعون وجنوده بغيا وعدوا ۖ حتى إذا أدركه الغرق قال آمنت أنه لا إله إلا الذي آمنت به بنو إسرائيل وأنا من المسلمين'),
(1455, 10, 91, 'آلآن وقد عصيت قبل وكنت من المفسدين'),
(1456, 10, 92, 'فاليوم ننجيك ببدنك لتكون لمن خلفك آية ۚ وإن كثيرا من الناس عن آياتنا لغافلون'),
(1457, 10, 93, 'ولقد بوأنا بني إسرائيل مبوأ صدق ورزقناهم من الطيبات فما اختلفوا حتى جاءهم العلم ۚ إن ربك يقضي بينهم يوم القيامة فيما كانوا فيه يختلفون'),
(1458, 10, 94, 'فإن كنت في شك مما أنزلنا إليك فاسأل الذين يقرءون الكتاب من قبلك ۚ لقد جاءك الحق من ربك فلا تكونن من الممترين'),
(1459, 10, 95, 'ولا تكونن من الذين كذبوا بآيات الله فتكون من الخاسرين'),
(1460, 10, 96, 'إن الذين حقت عليهم كلمت ربك لا يؤمنون'),
(1461, 10, 97, 'ولو جاءتهم كل آية حتى يروا العذاب الأليم'),
(1462, 10, 98, 'فلولا كانت قرية آمنت فنفعها إيمانها إلا قوم يونس لما آمنوا كشفنا عنهم عذاب الخزي في الحياة الدنيا ومتعناهم إلى حين'),
(1463, 10, 99, 'ولو شاء ربك لآمن من في الأرض كلهم جميعا ۚ أفأنت تكره الناس حتى يكونوا مؤمنين'),
(1464, 10, 100, 'وما كان لنفس أن تؤمن إلا بإذن الله ۚ ويجعل الرجس على الذين لا يعقلون'),
(1465, 10, 101, 'قل انظروا ماذا في السماوات والأرض ۚ وما تغني الآيات والنذر عن قوم لا يؤمنون'),
(1466, 10, 102, 'فهل ينتظرون إلا مثل أيام الذين خلوا من قبلهم ۚ قل فانتظروا إني معكم من المنتظرين'),
(1467, 10, 103, 'ثم ننجي رسلنا والذين آمنوا ۚ كذلك حقا علينا ننج المؤمنين'),
(1468, 10, 104, 'قل يا أيها الناس إن كنتم في شك من ديني فلا أعبد الذين تعبدون من دون الله ولكن أعبد الله الذي يتوفاكم ۖ وأمرت أن أكون من المؤمنين'),
(1469, 10, 105, 'وأن أقم وجهك للدين حنيفا ولا تكونن من المشركين'),
(1470, 10, 106, 'ولا تدع من دون الله ما لا ينفعك ولا يضرك ۖ فإن فعلت فإنك إذا من الظالمين'),
(1471, 10, 107, 'وإن يمسسك الله بضر فلا كاشف له إلا هو ۖ وإن يردك بخير فلا راد لفضله ۚ يصيب به من يشاء من عباده ۚ وهو الغفور الرحيم'),
(1472, 10, 108, 'قل يا أيها الناس قد جاءكم الحق من ربكم ۖ فمن اهتدى فإنما يهتدي لنفسه ۖ ومن ضل فإنما يضل عليها ۖ وما أنا عليكم بوكيل'),
(1473, 10, 109, 'واتبع ما يوحى إليك واصبر حتى يحكم الله ۚ وهو خير الحاكمين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'بسم الله الرحمن الرحيم الر ۚ كتاب أحكمت آياته ثم فصلت من لدن حكيم خبير'),
(1475, 11, 2, 'ألا تعبدوا إلا الله ۚ إنني لكم منه نذير وبشير'),
(1476, 11, 3, 'وأن استغفروا ربكم ثم توبوا إليه يمتعكم متاعا حسنا إلى أجل مسمى ويؤت كل ذي فضل فضله ۖ وإن تولوا فإني أخاف عليكم عذاب يوم كبير'),
(1477, 11, 4, 'إلى الله مرجعكم ۖ وهو على كل شيء قدير'),
(1478, 11, 5, 'ألا إنهم يثنون صدورهم ليستخفوا منه ۚ ألا حين يستغشون ثيابهم يعلم ما يسرون وما يعلنون ۚ إنه عليم بذات الصدور'),
(1479, 11, 6, 'وما من دابة في الأرض إلا على الله رزقها ويعلم مستقرها ومستودعها ۚ كل في كتاب مبين'),
(1480, 11, 7, 'وهو الذي خلق السماوات والأرض في ستة أيام وكان عرشه على الماء ليبلوكم أيكم أحسن عملا ۗ ولئن قلت إنكم مبعوثون من بعد الموت ليقولن الذين كفروا إن هذا إلا سحر مبين'),
(1481, 11, 8, 'ولئن أخرنا عنهم العذاب إلى أمة معدودة ليقولن ما يحبسه ۗ ألا يوم يأتيهم ليس مصروفا عنهم وحاق بهم ما كانوا به يستهزئون'),
(1482, 11, 9, 'ولئن أذقنا الإنسان منا رحمة ثم نزعناها منه إنه ليئوس كفور'),
(1483, 11, 10, 'ولئن أذقناه نعماء بعد ضراء مسته ليقولن ذهب السيئات عني ۚ إنه لفرح فخور'),
(1484, 11, 11, 'إلا الذين صبروا وعملوا الصالحات أولئك لهم مغفرة وأجر كبير'),
(1485, 11, 12, 'فلعلك تارك بعض ما يوحى إليك وضائق به صدرك أن يقولوا لولا أنزل عليه كنز أو جاء معه ملك ۚ إنما أنت نذير ۚ والله على كل شيء وكيل'),
(1486, 11, 13, 'أم يقولون افتراه ۖ قل فأتوا بعشر سور مثله مفتريات وادعوا من استطعتم من دون الله إن كنتم صادقين'),
(1487, 11, 14, 'فإلم يستجيبوا لكم فاعلموا أنما أنزل بعلم الله وأن لا إله إلا هو ۖ فهل أنتم مسلمون'),
(1488, 11, 15, 'من كان يريد الحياة الدنيا وزينتها نوف إليهم أعمالهم فيها وهم فيها لا يبخسون'),
(1489, 11, 16, 'أولئك الذين ليس لهم في الآخرة إلا النار ۖ وحبط ما صنعوا فيها وباطل ما كانوا يعملون'),
(1490, 11, 17, 'أفمن كان على بينة من ربه ويتلوه شاهد منه ومن قبله كتاب موسى إماما ورحمة ۚ أولئك يؤمنون به ۚ ومن يكفر به من الأحزاب فالنار موعده ۚ فلا تك في مرية منه ۚ إنه الحق من ربك ولكن أكثر الناس لا يؤمنون'),
(1491, 11, 18, 'ومن أظلم ممن افترى على الله كذبا ۚ أولئك يعرضون على ربهم ويقول الأشهاد هؤلاء الذين كذبوا على ربهم ۚ ألا لعنة الله على الظالمين'),
(1492, 11, 19, 'الذين يصدون عن سبيل الله ويبغونها عوجا وهم بالآخرة هم كافرون'),
(1493, 11, 20, 'أولئك لم يكونوا معجزين في الأرض وما كان لهم من دون الله من أولياء ۘ يضاعف لهم العذاب ۚ ما كانوا يستطيعون السمع وما كانوا يبصرون'),
(1494, 11, 21, 'أولئك الذين خسروا أنفسهم وضل عنهم ما كانوا يفترون'),
(1495, 11, 22, 'لا جرم أنهم في الآخرة هم الأخسرون'),
(1496, 11, 23, 'إن الذين آمنوا وعملوا الصالحات وأخبتوا إلى ربهم أولئك أصحاب الجنة ۖ هم فيها خالدون'),
(1497, 11, 24, 'مثل الفريقين كالأعمى والأصم والبصير والسميع ۚ هل يستويان مثلا ۚ أفلا تذكرون'),
(1498, 11, 25, 'ولقد أرسلنا نوحا إلى قومه إني لكم نذير مبين'),
(1499, 11, 26, 'أن لا تعبدوا إلا الله ۖ إني أخاف عليكم عذاب يوم أليم'),
(1500, 11, 27, 'فقال الملأ الذين كفروا من قومه ما نراك إلا بشرا مثلنا وما نراك اتبعك إلا الذين هم أراذلنا بادي الرأي وما نرى لكم علينا من فضل بل نظنكم كاذبين'),
(1501, 11, 28, 'قال يا قوم أرأيتم إن كنت على بينة من ربي وآتاني رحمة من عنده فعميت عليكم أنلزمكموها وأنتم لها كارهون'),
(1502, 11, 29, 'ويا قوم لا أسألكم عليه مالا ۖ إن أجري إلا على الله ۚ وما أنا بطارد الذين آمنوا ۚ إنهم ملاقو ربهم ولكني أراكم قوما تجهلون'),
(1503, 11, 30, 'ويا قوم من ينصرني من الله إن طردتهم ۚ أفلا تذكرون'),
(1504, 11, 31, 'ولا أقول لكم عندي خزائن الله ولا أعلم الغيب ولا أقول إني ملك ولا أقول للذين تزدري أعينكم لن يؤتيهم الله خيرا ۖ الله أعلم بما في أنفسهم ۖ إني إذا لمن الظالمين'),
(1505, 11, 32, 'قالوا يا نوح قد جادلتنا فأكثرت جدالنا فأتنا بما تعدنا إن كنت من الصادقين'),
(1506, 11, 33, 'قال إنما يأتيكم به الله إن شاء وما أنتم بمعجزين'),
(1507, 11, 34, 'ولا ينفعكم نصحي إن أردت أن أنصح لكم إن كان الله يريد أن يغويكم ۚ هو ربكم وإليه ترجعون'),
(1508, 11, 35, 'أم يقولون افتراه ۖ قل إن افتريته فعلي إجرامي وأنا بريء مما تجرمون'),
(1509, 11, 36, 'وأوحي إلى نوح أنه لن يؤمن من قومك إلا من قد آمن فلا تبتئس بما كانوا يفعلون'),
(1510, 11, 37, 'واصنع الفلك بأعيننا ووحينا ولا تخاطبني في الذين ظلموا ۚ إنهم مغرقون'),
(1511, 11, 38, 'ويصنع الفلك وكلما مر عليه ملأ من قومه سخروا منه ۚ قال إن تسخروا منا فإنا نسخر منكم كما تسخرون'),
(1512, 11, 39, 'فسوف تعلمون من يأتيه عذاب يخزيه ويحل عليه عذاب مقيم'),
(1513, 11, 40, 'حتى إذا جاء أمرنا وفار التنور قلنا احمل فيها من كل زوجين اثنين وأهلك إلا من سبق عليه القول ومن آمن ۚ وما آمن معه إلا قليل'),
(1514, 11, 41, 'وقال اركبوا فيها بسم الله مجراها ومرساها ۚ إن ربي لغفور رحيم'),
(1515, 11, 42, 'وهي تجري بهم في موج كالجبال ونادى نوح ابنه وكان في معزل يا بني اركب معنا ولا تكن مع الكافرين'),
(1516, 11, 43, 'قال سآوي إلى جبل يعصمني من الماء ۚ قال لا عاصم اليوم من أمر الله إلا من رحم ۚ وحال بينهما الموج فكان من المغرقين'),
(1517, 11, 44, 'وقيل يا أرض ابلعي ماءك ويا سماء أقلعي وغيض الماء وقضي الأمر واستوت على الجودي ۖ وقيل بعدا للقوم الظالمين'),
(1518, 11, 45, 'ونادى نوح ربه فقال رب إن ابني من أهلي وإن وعدك الحق وأنت أحكم الحاكمين'),
(1519, 11, 46, 'قال يا نوح إنه ليس من أهلك ۖ إنه عمل غير صالح ۖ فلا تسألن ما ليس لك به علم ۖ إني أعظك أن تكون من الجاهلين'),
(1520, 11, 47, 'قال رب إني أعوذ بك أن أسألك ما ليس لي به علم ۖ وإلا تغفر لي وترحمني أكن من الخاسرين'),
(1521, 11, 48, 'قيل يا نوح اهبط بسلام منا وبركات عليك وعلى أمم ممن معك ۚ وأمم سنمتعهم ثم يمسهم منا عذاب أليم'),
(1522, 11, 49, 'تلك من أنباء الغيب نوحيها إليك ۖ ما كنت تعلمها أنت ولا قومك من قبل هذا ۖ فاصبر ۖ إن العاقبة للمتقين'),
(1523, 11, 50, 'وإلى عاد أخاهم هودا ۚ قال يا قوم اعبدوا الله ما لكم من إله غيره ۖ إن أنتم إلا مفترون'),
(1524, 11, 51, 'يا قوم لا أسألكم عليه أجرا ۖ إن أجري إلا على الذي فطرني ۚ أفلا تعقلون'),
(1525, 11, 52, 'ويا قوم استغفروا ربكم ثم توبوا إليه يرسل السماء عليكم مدرارا ويزدكم قوة إلى قوتكم ولا تتولوا مجرمين'),
(1526, 11, 53, 'قالوا يا هود ما جئتنا ببينة وما نحن بتاركي آلهتنا عن قولك وما نحن لك بمؤمنين'),
(1527, 11, 54, 'إن نقول إلا اعتراك بعض آلهتنا بسوء ۗ قال إني أشهد الله واشهدوا أني بريء مما تشركون'),
(1528, 11, 55, 'من دونه ۖ فكيدوني جميعا ثم لا تنظرون'),
(1529, 11, 56, 'إني توكلت على الله ربي وربكم ۚ ما من دابة إلا هو آخذ بناصيتها ۚ إن ربي على صراط مستقيم'),
(1530, 11, 57, 'فإن تولوا فقد أبلغتكم ما أرسلت به إليكم ۚ ويستخلف ربي قوما غيركم ولا تضرونه شيئا ۚ إن ربي على كل شيء حفيظ'),
(1531, 11, 58, 'ولما جاء أمرنا نجينا هودا والذين آمنوا معه برحمة منا ونجيناهم من عذاب غليظ'),
(1532, 11, 59, 'وتلك عاد ۖ جحدوا بآيات ربهم وعصوا رسله واتبعوا أمر كل جبار عنيد'),
(1533, 11, 60, 'وأتبعوا في هذه الدنيا لعنة ويوم القيامة ۗ ألا إن عادا كفروا ربهم ۗ ألا بعدا لعاد قوم هود'),
(1534, 11, 61, 'وإلى ثمود أخاهم صالحا ۚ قال يا قوم اعبدوا الله ما لكم من إله غيره ۖ هو أنشأكم من الأرض واستعمركم فيها فاستغفروه ثم توبوا إليه ۚ إن ربي قريب مجيب'),
(1535, 11, 62, 'قالوا يا صالح قد كنت فينا مرجوا قبل هذا ۖ أتنهانا أن نعبد ما يعبد آباؤنا وإننا لفي شك مما تدعونا إليه مريب'),
(1536, 11, 63, 'قال يا قوم أرأيتم إن كنت على بينة من ربي وآتاني منه رحمة فمن ينصرني من الله إن عصيته ۖ فما تزيدونني غير تخسير'),
(1537, 11, 64, 'ويا قوم هذه ناقة الله لكم آية فذروها تأكل في أرض الله ولا تمسوها بسوء فيأخذكم عذاب قريب'),
(1538, 11, 65, 'فعقروها فقال تمتعوا في داركم ثلاثة أيام ۖ ذلك وعد غير مكذوب'),
(1539, 11, 66, 'فلما جاء أمرنا نجينا صالحا والذين آمنوا معه برحمة منا ومن خزي يومئذ ۗ إن ربك هو القوي العزيز'),
(1540, 11, 67, 'وأخذ الذين ظلموا الصيحة فأصبحوا في ديارهم جاثمين'),
(1541, 11, 68, 'كأن لم يغنوا فيها ۗ ألا إن ثمود كفروا ربهم ۗ ألا بعدا لثمود'),
(1542, 11, 69, 'ولقد جاءت رسلنا إبراهيم بالبشرى قالوا سلاما ۖ قال سلام ۖ فما لبث أن جاء بعجل حنيذ'),
(1543, 11, 70, 'فلما رأى أيديهم لا تصل إليه نكرهم وأوجس منهم خيفة ۚ قالوا لا تخف إنا أرسلنا إلى قوم لوط'),
(1544, 11, 71, 'وامرأته قائمة فضحكت فبشرناها بإسحاق ومن وراء إسحاق يعقوب'),
(1545, 11, 72, 'قالت يا ويلتى أألد وأنا عجوز وهذا بعلي شيخا ۖ إن هذا لشيء عجيب'),
(1546, 11, 73, 'قالوا أتعجبين من أمر الله ۖ رحمت الله وبركاته عليكم أهل البيت ۚ إنه حميد مجيد'),
(1547, 11, 74, 'فلما ذهب عن إبراهيم الروع وجاءته البشرى يجادلنا في قوم لوط'),
(1548, 11, 75, 'إن إبراهيم لحليم أواه منيب'),
(1549, 11, 76, 'يا إبراهيم أعرض عن هذا ۖ إنه قد جاء أمر ربك ۖ وإنهم آتيهم عذاب غير مردود'),
(1550, 11, 77, 'ولما جاءت رسلنا لوطا سيء بهم وضاق بهم ذرعا وقال هذا يوم عصيب'),
(1551, 11, 78, 'وجاءه قومه يهرعون إليه ومن قبل كانوا يعملون السيئات ۚ قال يا قوم هؤلاء بناتي هن أطهر لكم ۖ فاتقوا الله ولا تخزون في ضيفي ۖ أليس منكم رجل رشيد'),
(1552, 11, 79, 'قالوا لقد علمت ما لنا في بناتك من حق وإنك لتعلم ما نريد'),
(1553, 11, 80, 'قال لو أن لي بكم قوة أو آوي إلى ركن شديد'),
(1554, 11, 81, 'قالوا يا لوط إنا رسل ربك لن يصلوا إليك ۖ فأسر بأهلك بقطع من الليل ولا يلتفت منكم أحد إلا امرأتك ۖ إنه مصيبها ما أصابهم ۚ إن موعدهم الصبح ۚ أليس الصبح بقريب'),
(1555, 11, 82, 'فلما جاء أمرنا جعلنا عاليها سافلها وأمطرنا عليها حجارة من سجيل منضود'),
(1556, 11, 83, 'مسومة عند ربك ۖ وما هي من الظالمين ببعيد'),
(1557, 11, 84, 'وإلى مدين أخاهم شعيبا ۚ قال يا قوم اعبدوا الله ما لكم من إله غيره ۖ ولا تنقصوا المكيال والميزان ۚ إني أراكم بخير وإني أخاف عليكم عذاب يوم محيط'),
(1558, 11, 85, 'ويا قوم أوفوا المكيال والميزان بالقسط ۖ ولا تبخسوا الناس أشياءهم ولا تعثوا في الأرض مفسدين'),
(1559, 11, 86, 'بقيت الله خير لكم إن كنتم مؤمنين ۚ وما أنا عليكم بحفيظ'),
(1560, 11, 87, 'قالوا يا شعيب أصلاتك تأمرك أن نترك ما يعبد آباؤنا أو أن نفعل في أموالنا ما نشاء ۖ إنك لأنت الحليم الرشيد'),
(1561, 11, 88, 'قال يا قوم أرأيتم إن كنت على بينة من ربي ورزقني منه رزقا حسنا ۚ وما أريد أن أخالفكم إلى ما أنهاكم عنه ۚ إن أريد إلا الإصلاح ما استطعت ۚ وما توفيقي إلا بالله ۚ عليه توكلت وإليه أنيب'),
(1562, 11, 89, 'ويا قوم لا يجرمنكم شقاقي أن يصيبكم مثل ما أصاب قوم نوح أو قوم هود أو قوم صالح ۚ وما قوم لوط منكم ببعيد'),
(1563, 11, 90, 'واستغفروا ربكم ثم توبوا إليه ۚ إن ربي رحيم ودود'),
(1564, 11, 91, 'قالوا يا شعيب ما نفقه كثيرا مما تقول وإنا لنراك فينا ضعيفا ۖ ولولا رهطك لرجمناك ۖ وما أنت علينا بعزيز'),
(1565, 11, 92, 'قال يا قوم أرهطي أعز عليكم من الله واتخذتموه وراءكم ظهريا ۖ إن ربي بما تعملون محيط'),
(1566, 11, 93, 'ويا قوم اعملوا على مكانتكم إني عامل ۖ سوف تعلمون من يأتيه عذاب يخزيه ومن هو كاذب ۖ وارتقبوا إني معكم رقيب'),
(1567, 11, 94, 'ولما جاء أمرنا نجينا شعيبا والذين آمنوا معه برحمة منا وأخذت الذين ظلموا الصيحة فأصبحوا في ديارهم جاثمين'),
(1568, 11, 95, 'كأن لم يغنوا فيها ۗ ألا بعدا لمدين كما بعدت ثمود'),
(1569, 11, 96, 'ولقد أرسلنا موسى بآياتنا وسلطان مبين'),
(1570, 11, 97, 'إلى فرعون وملئه فاتبعوا أمر فرعون ۖ وما أمر فرعون برشيد'),
(1571, 11, 98, 'يقدم قومه يوم القيامة فأوردهم النار ۖ وبئس الورد المورود'),
(1572, 11, 99, 'وأتبعوا في هذه لعنة ويوم القيامة ۚ بئس الرفد المرفود'),
(1573, 11, 100, 'ذلك من أنباء القرى نقصه عليك ۖ منها قائم وحصيد'),
(1574, 11, 101, 'وما ظلمناهم ولكن ظلموا أنفسهم ۖ فما أغنت عنهم آلهتهم التي يدعون من دون الله من شيء لما جاء أمر ربك ۖ وما زادوهم غير تتبيب'),
(1575, 11, 102, 'وكذلك أخذ ربك إذا أخذ القرى وهي ظالمة ۚ إن أخذه أليم شديد'),
(1576, 11, 103, 'إن في ذلك لآية لمن خاف عذاب الآخرة ۚ ذلك يوم مجموع له الناس وذلك يوم مشهود'),
(1577, 11, 104, 'وما نؤخره إلا لأجل معدود'),
(1578, 11, 105, 'يوم يأت لا تكلم نفس إلا بإذنه ۚ فمنهم شقي وسعيد'),
(1579, 11, 106, 'فأما الذين شقوا ففي النار لهم فيها زفير وشهيق'),
(1580, 11, 107, 'خالدين فيها ما دامت السماوات والأرض إلا ما شاء ربك ۚ إن ربك فعال لما يريد'),
(1581, 11, 108, 'وأما الذين سعدوا ففي الجنة خالدين فيها ما دامت السماوات والأرض إلا ما شاء ربك ۖ عطاء غير مجذوذ'),
(1582, 11, 109, 'فلا تك في مرية مما يعبد هؤلاء ۚ ما يعبدون إلا كما يعبد آباؤهم من قبل ۚ وإنا لموفوهم نصيبهم غير منقوص'),
(1583, 11, 110, 'ولقد آتينا موسى الكتاب فاختلف فيه ۚ ولولا كلمة سبقت من ربك لقضي بينهم ۚ وإنهم لفي شك منه مريب'),
(1584, 11, 111, 'وإن كلا لما ليوفينهم ربك أعمالهم ۚ إنه بما يعملون خبير'),
(1585, 11, 112, 'فاستقم كما أمرت ومن تاب معك ولا تطغوا ۚ إنه بما تعملون بصير'),
(1586, 11, 113, 'ولا تركنوا إلى الذين ظلموا فتمسكم النار وما لكم من دون الله من أولياء ثم لا تنصرون'),
(1587, 11, 114, 'وأقم الصلاة طرفي النهار وزلفا من الليل ۚ إن الحسنات يذهبن السيئات ۚ ذلك ذكرى للذاكرين'),
(1588, 11, 115, 'واصبر فإن الله لا يضيع أجر المحسنين'),
(1589, 11, 116, 'فلولا كان من القرون من قبلكم أولو بقية ينهون عن الفساد في الأرض إلا قليلا ممن أنجينا منهم ۗ واتبع الذين ظلموا ما أترفوا فيه وكانوا مجرمين'),
(1590, 11, 117, 'وما كان ربك ليهلك القرى بظلم وأهلها مصلحون'),
(1591, 11, 118, 'ولو شاء ربك لجعل الناس أمة واحدة ۖ ولا يزالون مختلفين'),
(1592, 11, 119, 'إلا من رحم ربك ۚ ولذلك خلقهم ۗ وتمت كلمة ربك لأملأن جهنم من الجنة والناس أجمعين'),
(1593, 11, 120, 'وكلا نقص عليك من أنباء الرسل ما نثبت به فؤادك ۚ وجاءك في هذه الحق وموعظة وذكرى للمؤمنين'),
(1594, 11, 121, 'وقل للذين لا يؤمنون اعملوا على مكانتكم إنا عاملون'),
(1595, 11, 122, 'وانتظروا إنا منتظرون'),
(1596, 11, 123, 'ولله غيب السماوات والأرض وإليه يرجع الأمر كله فاعبده وتوكل عليه ۚ وما ربك بغافل عما تعملون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'بسم الله الرحمن الرحيم الر ۚ تلك آيات الكتاب المبين'),
(1598, 12, 2, 'إنا أنزلناه قرآنا عربيا لعلكم تعقلون'),
(1599, 12, 3, 'نحن نقص عليك أحسن القصص بما أوحينا إليك هذا القرآن وإن كنت من قبله لمن الغافلين'),
(1600, 12, 4, 'إذ قال يوسف لأبيه يا أبت إني رأيت أحد عشر كوكبا والشمس والقمر رأيتهم لي ساجدين'),
(1601, 12, 5, 'قال يا بني لا تقصص رؤياك على إخوتك فيكيدوا لك كيدا ۖ إن الشيطان للإنسان عدو مبين'),
(1602, 12, 6, 'وكذلك يجتبيك ربك ويعلمك من تأويل الأحاديث ويتم نعمته عليك وعلى آل يعقوب كما أتمها على أبويك من قبل إبراهيم وإسحاق ۚ إن ربك عليم حكيم'),
(1603, 12, 7, 'لقد كان في يوسف وإخوته آيات للسائلين'),
(1604, 12, 8, 'إذ قالوا ليوسف وأخوه أحب إلى أبينا منا ونحن عصبة إن أبانا لفي ضلال مبين'),
(1605, 12, 9, 'اقتلوا يوسف أو اطرحوه أرضا يخل لكم وجه أبيكم وتكونوا من بعده قوما صالحين'),
(1606, 12, 10, 'قال قائل منهم لا تقتلوا يوسف وألقوه في غيابت الجب يلتقطه بعض السيارة إن كنتم فاعلين'),
(1607, 12, 11, 'قالوا يا أبانا ما لك لا تأمنا على يوسف وإنا له لناصحون'),
(1608, 12, 12, 'أرسله معنا غدا يرتع ويلعب وإنا له لحافظون'),
(1609, 12, 13, 'قال إني ليحزنني أن تذهبوا به وأخاف أن يأكله الذئب وأنتم عنه غافلون'),
(1610, 12, 14, 'قالوا لئن أكله الذئب ونحن عصبة إنا إذا لخاسرون'),
(1611, 12, 15, 'فلما ذهبوا به وأجمعوا أن يجعلوه في غيابت الجب ۚ وأوحينا إليه لتنبئنهم بأمرهم هذا وهم لا يشعرون'),
(1612, 12, 16, 'وجاءوا أباهم عشاء يبكون'),
(1613, 12, 17, 'قالوا يا أبانا إنا ذهبنا نستبق وتركنا يوسف عند متاعنا فأكله الذئب ۖ وما أنت بمؤمن لنا ولو كنا صادقين'),
(1614, 12, 18, 'وجاءوا على قميصه بدم كذب ۚ قال بل سولت لكم أنفسكم أمرا ۖ فصبر جميل ۖ والله المستعان على ما تصفون'),
(1615, 12, 19, 'وجاءت سيارة فأرسلوا واردهم فأدلى دلوه ۖ قال يا بشرى هذا غلام ۚ وأسروه بضاعة ۚ والله عليم بما يعملون'),
(1616, 12, 20, 'وشروه بثمن بخس دراهم معدودة وكانوا فيه من الزاهدين'),
(1617, 12, 21, 'وقال الذي اشتراه من مصر لامرأته أكرمي مثواه عسى أن ينفعنا أو نتخذه ولدا ۚ وكذلك مكنا ليوسف في الأرض ولنعلمه من تأويل الأحاديث ۚ والله غالب على أمره ولكن أكثر الناس لا يعلمون'),
(1618, 12, 22, 'ولما بلغ أشده آتيناه حكما وعلما ۚ وكذلك نجزي المحسنين'),
(1619, 12, 23, 'وراودته التي هو في بيتها عن نفسه وغلقت الأبواب وقالت هيت لك ۚ قال معاذ الله ۖ إنه ربي أحسن مثواي ۖ إنه لا يفلح الظالمون'),
(1620, 12, 24, 'ولقد همت به ۖ وهم بها لولا أن رأى برهان ربه ۚ كذلك لنصرف عنه السوء والفحشاء ۚ إنه من عبادنا المخلصين'),
(1621, 12, 25, 'واستبقا الباب وقدت قميصه من دبر وألفيا سيدها لدى الباب ۚ قالت ما جزاء من أراد بأهلك سوءا إلا أن يسجن أو عذاب أليم'),
(1622, 12, 26, 'قال هي راودتني عن نفسي ۚ وشهد شاهد من أهلها إن كان قميصه قد من قبل فصدقت وهو من الكاذبين'),
(1623, 12, 27, 'وإن كان قميصه قد من دبر فكذبت وهو من الصادقين'),
(1624, 12, 28, 'فلما رأى قميصه قد من دبر قال إنه من كيدكن ۖ إن كيدكن عظيم'),
(1625, 12, 29, 'يوسف أعرض عن هذا ۚ واستغفري لذنبك ۖ إنك كنت من الخاطئين'),
(1626, 12, 30, 'وقال نسوة في المدينة امرأت العزيز تراود فتاها عن نفسه ۖ قد شغفها حبا ۖ إنا لنراها في ضلال مبين'),
(1627, 12, 31, 'فلما سمعت بمكرهن أرسلت إليهن وأعتدت لهن متكأ وآتت كل واحدة منهن سكينا وقالت اخرج عليهن ۖ فلما رأينه أكبرنه وقطعن أيديهن وقلن حاش لله ما هذا بشرا إن هذا إلا ملك كريم'),
(1628, 12, 32, 'قالت فذلكن الذي لمتنني فيه ۖ ولقد راودته عن نفسه فاستعصم ۖ ولئن لم يفعل ما آمره ليسجنن وليكونا من الصاغرين'),
(1629, 12, 33, 'قال رب السجن أحب إلي مما يدعونني إليه ۖ وإلا تصرف عني كيدهن أصب إليهن وأكن من الجاهلين'),
(1630, 12, 34, 'فاستجاب له ربه فصرف عنه كيدهن ۚ إنه هو السميع العليم'),
(1631, 12, 35, 'ثم بدا لهم من بعد ما رأوا الآيات ليسجننه حتى حين'),
(1632, 12, 36, 'ودخل معه السجن فتيان ۖ قال أحدهما إني أراني أعصر خمرا ۖ وقال الآخر إني أراني أحمل فوق رأسي خبزا تأكل الطير منه ۖ نبئنا بتأويله ۖ إنا نراك من المحسنين'),
(1633, 12, 37, 'قال لا يأتيكما طعام ترزقانه إلا نبأتكما بتأويله قبل أن يأتيكما ۚ ذلكما مما علمني ربي ۚ إني تركت ملة قوم لا يؤمنون بالله وهم بالآخرة هم كافرون'),
(1634, 12, 38, 'واتبعت ملة آبائي إبراهيم وإسحاق ويعقوب ۚ ما كان لنا أن نشرك بالله من شيء ۚ ذلك من فضل الله علينا وعلى الناس ولكن أكثر الناس لا يشكرون'),
(1635, 12, 39, 'يا صاحبي السجن أأرباب متفرقون خير أم الله الواحد القهار'),
(1636, 12, 40, 'ما تعبدون من دونه إلا أسماء سميتموها أنتم وآباؤكم ما أنزل الله بها من سلطان ۚ إن الحكم إلا لله ۚ أمر ألا تعبدوا إلا إياه ۚ ذلك الدين القيم ولكن أكثر الناس لا يعلمون'),
(1637, 12, 41, 'يا صاحبي السجن أما أحدكما فيسقي ربه خمرا ۖ وأما الآخر فيصلب فتأكل الطير من رأسه ۚ قضي الأمر الذي فيه تستفتيان'),
(1638, 12, 42, 'وقال للذي ظن أنه ناج منهما اذكرني عند ربك فأنساه الشيطان ذكر ربه فلبث في السجن بضع سنين'),
(1639, 12, 43, 'وقال الملك إني أرى سبع بقرات سمان يأكلهن سبع عجاف وسبع سنبلات خضر وأخر يابسات ۖ يا أيها الملأ أفتوني في رؤياي إن كنتم للرؤيا تعبرون'),
(1640, 12, 44, 'قالوا أضغاث أحلام ۖ وما نحن بتأويل الأحلام بعالمين'),
(1641, 12, 45, 'وقال الذي نجا منهما وادكر بعد أمة أنا أنبئكم بتأويله فأرسلون'),
(1642, 12, 46, 'يوسف أيها الصديق أفتنا في سبع بقرات سمان يأكلهن سبع عجاف وسبع سنبلات خضر وأخر يابسات لعلي أرجع إلى الناس لعلهم يعلمون'),
(1643, 12, 47, 'قال تزرعون سبع سنين دأبا فما حصدتم فذروه في سنبله إلا قليلا مما تأكلون'),
(1644, 12, 48, 'ثم يأتي من بعد ذلك سبع شداد يأكلن ما قدمتم لهن إلا قليلا مما تحصنون'),
(1645, 12, 49, 'ثم يأتي من بعد ذلك عام فيه يغاث الناس وفيه يعصرون'),
(1646, 12, 50, 'وقال الملك ائتوني به ۖ فلما جاءه الرسول قال ارجع إلى ربك فاسأله ما بال النسوة اللاتي قطعن أيديهن ۚ إن ربي بكيدهن عليم'),
(1647, 12, 51, 'قال ما خطبكن إذ راودتن يوسف عن نفسه ۚ قلن حاش لله ما علمنا عليه من سوء ۚ قالت امرأت العزيز الآن حصحص الحق أنا راودته عن نفسه وإنه لمن الصادقين'),
(1648, 12, 52, 'ذلك ليعلم أني لم أخنه بالغيب وأن الله لا يهدي كيد الخائنين'),
(1649, 12, 53, 'وما أبرئ نفسي ۚ إن النفس لأمارة بالسوء إلا ما رحم ربي ۚ إن ربي غفور رحيم'),
(1650, 12, 54, 'وقال الملك ائتوني به أستخلصه لنفسي ۖ فلما كلمه قال إنك اليوم لدينا مكين أمين'),
(1651, 12, 55, 'قال اجعلني على خزائن الأرض ۖ إني حفيظ عليم'),
(1652, 12, 56, 'وكذلك مكنا ليوسف في الأرض يتبوأ منها حيث يشاء ۚ نصيب برحمتنا من نشاء ۖ ولا نضيع أجر المحسنين'),
(1653, 12, 57, 'ولأجر الآخرة خير للذين آمنوا وكانوا يتقون'),
(1654, 12, 58, 'وجاء إخوة يوسف فدخلوا عليه فعرفهم وهم له منكرون'),
(1655, 12, 59, 'ولما جهزهم بجهازهم قال ائتوني بأخ لكم من أبيكم ۚ ألا ترون أني أوفي الكيل وأنا خير المنزلين'),
(1656, 12, 60, 'فإن لم تأتوني به فلا كيل لكم عندي ولا تقربون'),
(1657, 12, 61, 'قالوا سنراود عنه أباه وإنا لفاعلون'),
(1658, 12, 62, 'وقال لفتيانه اجعلوا بضاعتهم في رحالهم لعلهم يعرفونها إذا انقلبوا إلى أهلهم لعلهم يرجعون'),
(1659, 12, 63, 'فلما رجعوا إلى أبيهم قالوا يا أبانا منع منا الكيل فأرسل معنا أخانا نكتل وإنا له لحافظون'),
(1660, 12, 64, 'قال هل آمنكم عليه إلا كما أمنتكم على أخيه من قبل ۖ فالله خير حافظا ۖ وهو أرحم الراحمين'),
(1661, 12, 65, 'ولما فتحوا متاعهم وجدوا بضاعتهم ردت إليهم ۖ قالوا يا أبانا ما نبغي ۖ هذه بضاعتنا ردت إلينا ۖ ونمير أهلنا ونحفظ أخانا ونزداد كيل بعير ۖ ذلك كيل يسير'),
(1662, 12, 66, 'قال لن أرسله معكم حتى تؤتون موثقا من الله لتأتنني به إلا أن يحاط بكم ۖ فلما آتوه موثقهم قال الله على ما نقول وكيل'),
(1663, 12, 67, 'وقال يا بني لا تدخلوا من باب واحد وادخلوا من أبواب متفرقة ۖ وما أغني عنكم من الله من شيء ۖ إن الحكم إلا لله ۖ عليه توكلت ۖ وعليه فليتوكل المتوكلون'),
(1664, 12, 68, 'ولما دخلوا من حيث أمرهم أبوهم ما كان يغني عنهم من الله من شيء إلا حاجة في نفس يعقوب قضاها ۚ وإنه لذو علم لما علمناه ولكن أكثر الناس لا يعلمون'),
(1665, 12, 69, 'ولما دخلوا على يوسف آوى إليه أخاه ۖ قال إني أنا أخوك فلا تبتئس بما كانوا يعملون'),
(1666, 12, 70, 'فلما جهزهم بجهازهم جعل السقاية في رحل أخيه ثم أذن مؤذن أيتها العير إنكم لسارقون'),
(1667, 12, 71, 'قالوا وأقبلوا عليهم ماذا تفقدون'),
(1668, 12, 72, 'قالوا نفقد صواع الملك ولمن جاء به حمل بعير وأنا به زعيم'),
(1669, 12, 73, 'قالوا تالله لقد علمتم ما جئنا لنفسد في الأرض وما كنا سارقين'),
(1670, 12, 74, 'قالوا فما جزاؤه إن كنتم كاذبين'),
(1671, 12, 75, 'قالوا جزاؤه من وجد في رحله فهو جزاؤه ۚ كذلك نجزي الظالمين'),
(1672, 12, 76, 'فبدأ بأوعيتهم قبل وعاء أخيه ثم استخرجها من وعاء أخيه ۚ كذلك كدنا ليوسف ۖ ما كان ليأخذ أخاه في دين الملك إلا أن يشاء الله ۚ نرفع درجات من نشاء ۗ وفوق كل ذي علم عليم'),
(1673, 12, 77, 'قالوا إن يسرق فقد سرق أخ له من قبل ۚ فأسرها يوسف في نفسه ولم يبدها لهم ۚ قال أنتم شر مكانا ۖ والله أعلم بما تصفون'),
(1674, 12, 78, 'قالوا يا أيها العزيز إن له أبا شيخا كبيرا فخذ أحدنا مكانه ۖ إنا نراك من المحسنين'),
(1675, 12, 79, 'قال معاذ الله أن نأخذ إلا من وجدنا متاعنا عنده إنا إذا لظالمون'),
(1676, 12, 80, 'فلما استيأسوا منه خلصوا نجيا ۖ قال كبيرهم ألم تعلموا أن أباكم قد أخذ عليكم موثقا من الله ومن قبل ما فرطتم في يوسف ۖ فلن أبرح الأرض حتى يأذن لي أبي أو يحكم الله لي ۖ وهو خير الحاكمين'),
(1677, 12, 81, 'ارجعوا إلى أبيكم فقولوا يا أبانا إن ابنك سرق وما شهدنا إلا بما علمنا وما كنا للغيب حافظين'),
(1678, 12, 82, 'واسأل القرية التي كنا فيها والعير التي أقبلنا فيها ۖ وإنا لصادقون'),
(1679, 12, 83, 'قال بل سولت لكم أنفسكم أمرا ۖ فصبر جميل ۖ عسى الله أن يأتيني بهم جميعا ۚ إنه هو العليم الحكيم'),
(1680, 12, 84, 'وتولى عنهم وقال يا أسفى على يوسف وابيضت عيناه من الحزن فهو كظيم'),
(1681, 12, 85, 'قالوا تالله تفتأ تذكر يوسف حتى تكون حرضا أو تكون من الهالكين'),
(1682, 12, 86, 'قال إنما أشكو بثي وحزني إلى الله وأعلم من الله ما لا تعلمون'),
(1683, 12, 87, 'يا بني اذهبوا فتحسسوا من يوسف وأخيه ولا تيأسوا من روح الله ۖ إنه لا ييأس من روح الله إلا القوم الكافرون'),
(1684, 12, 88, 'فلما دخلوا عليه قالوا يا أيها العزيز مسنا وأهلنا الضر وجئنا ببضاعة مزجاة فأوف لنا الكيل وتصدق علينا ۖ إن الله يجزي المتصدقين'),
(1685, 12, 89, 'قال هل علمتم ما فعلتم بيوسف وأخيه إذ أنتم جاهلون'),
(1686, 12, 90, 'قالوا أإنك لأنت يوسف ۖ قال أنا يوسف وهذا أخي ۖ قد من الله علينا ۖ إنه من يتق ويصبر فإن الله لا يضيع أجر المحسنين'),
(1687, 12, 91, 'قالوا تالله لقد آثرك الله علينا وإن كنا لخاطئين'),
(1688, 12, 92, 'قال لا تثريب عليكم اليوم ۖ يغفر الله لكم ۖ وهو أرحم الراحمين'),
(1689, 12, 93, 'اذهبوا بقميصي هذا فألقوه على وجه أبي يأت بصيرا وأتوني بأهلكم أجمعين'),
(1690, 12, 94, 'ولما فصلت العير قال أبوهم إني لأجد ريح يوسف ۖ لولا أن تفندون'),
(1691, 12, 95, 'قالوا تالله إنك لفي ضلالك القديم'),
(1692, 12, 96, 'فلما أن جاء البشير ألقاه على وجهه فارتد بصيرا ۖ قال ألم أقل لكم إني أعلم من الله ما لا تعلمون'),
(1693, 12, 97, 'قالوا يا أبانا استغفر لنا ذنوبنا إنا كنا خاطئين'),
(1694, 12, 98, 'قال سوف أستغفر لكم ربي ۖ إنه هو الغفور الرحيم'),
(1695, 12, 99, 'فلما دخلوا على يوسف آوى إليه أبويه وقال ادخلوا مصر إن شاء الله آمنين'),
(1696, 12, 100, 'ورفع أبويه على العرش وخروا له سجدا ۖ وقال يا أبت هذا تأويل رؤياي من قبل قد جعلها ربي حقا ۖ وقد أحسن بي إذ أخرجني من السجن وجاء بكم من البدو من بعد أن نزغ الشيطان بيني وبين إخوتي ۚ إن ربي لطيف لما يشاء ۚ إنه هو العليم الحكيم'),
(1697, 12, 101, 'رب قد آتيتني من الملك وعلمتني من تأويل الأحاديث ۚ فاطر السماوات والأرض أنت وليي في الدنيا والآخرة ۖ توفني مسلما وألحقني بالصالحين'),
(1698, 12, 102, 'ذلك من أنباء الغيب نوحيه إليك ۖ وما كنت لديهم إذ أجمعوا أمرهم وهم يمكرون'),
(1699, 12, 103, 'وما أكثر الناس ولو حرصت بمؤمنين'),
(1700, 12, 104, 'وما تسألهم عليه من أجر ۚ إن هو إلا ذكر للعالمين'),
(1701, 12, 105, 'وكأين من آية في السماوات والأرض يمرون عليها وهم عنها معرضون'),
(1702, 12, 106, 'وما يؤمن أكثرهم بالله إلا وهم مشركون'),
(1703, 12, 107, 'أفأمنوا أن تأتيهم غاشية من عذاب الله أو تأتيهم الساعة بغتة وهم لا يشعرون'),
(1704, 12, 108, 'قل هذه سبيلي أدعو إلى الله ۚ على بصيرة أنا ومن اتبعني ۖ وسبحان الله وما أنا من المشركين'),
(1705, 12, 109, 'وما أرسلنا من قبلك إلا رجالا نوحي إليهم من أهل القرى ۗ أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم ۗ ولدار الآخرة خير للذين اتقوا ۗ أفلا تعقلون'),
(1706, 12, 110, 'حتى إذا استيأس الرسل وظنوا أنهم قد كذبوا جاءهم نصرنا فنجي من نشاء ۖ ولا يرد بأسنا عن القوم المجرمين'),
(1707, 12, 111, 'لقد كان في قصصهم عبرة لأولي الألباب ۗ ما كان حديثا يفترى ولكن تصديق الذي بين يديه وتفصيل كل شيء وهدى ورحمة لقوم يؤمنون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'بسم الله الرحمن الرحيم المر ۚ تلك آيات الكتاب ۗ والذي أنزل إليك من ربك الحق ولكن أكثر الناس لا يؤمنون'),
(1709, 13, 2, 'الله الذي رفع السماوات بغير عمد ترونها ۖ ثم استوى على العرش ۖ وسخر الشمس والقمر ۖ كل يجري لأجل مسمى ۚ يدبر الأمر يفصل الآيات لعلكم بلقاء ربكم توقنون'),
(1710, 13, 3, 'وهو الذي مد الأرض وجعل فيها رواسي وأنهارا ۖ ومن كل الثمرات جعل فيها زوجين اثنين ۖ يغشي الليل النهار ۚ إن في ذلك لآيات لقوم يتفكرون'),
(1711, 13, 4, 'وفي الأرض قطع متجاورات وجنات من أعناب وزرع ونخيل صنوان وغير صنوان يسقى بماء واحد ونفضل بعضها على بعض في الأكل ۚ إن في ذلك لآيات لقوم يعقلون'),
(1712, 13, 5, 'وإن تعجب فعجب قولهم أإذا كنا ترابا أإنا لفي خلق جديد ۗ أولئك الذين كفروا بربهم ۖ وأولئك الأغلال في أعناقهم ۖ وأولئك أصحاب النار ۖ هم فيها خالدون'),
(1713, 13, 6, 'ويستعجلونك بالسيئة قبل الحسنة وقد خلت من قبلهم المثلات ۗ وإن ربك لذو مغفرة للناس على ظلمهم ۖ وإن ربك لشديد العقاب'),
(1714, 13, 7, 'ويقول الذين كفروا لولا أنزل عليه آية من ربه ۗ إنما أنت منذر ۖ ولكل قوم هاد'),
(1715, 13, 8, 'الله يعلم ما تحمل كل أنثى وما تغيض الأرحام وما تزداد ۖ وكل شيء عنده بمقدار'),
(1716, 13, 9, 'عالم الغيب والشهادة الكبير المتعال'),
(1717, 13, 10, 'سواء منكم من أسر القول ومن جهر به ومن هو مستخف بالليل وسارب بالنهار'),
(1718, 13, 11, 'له معقبات من بين يديه ومن خلفه يحفظونه من أمر الله ۗ إن الله لا يغير ما بقوم حتى يغيروا ما بأنفسهم ۗ وإذا أراد الله بقوم سوءا فلا مرد له ۚ وما لهم من دونه من وال'),
(1719, 13, 12, 'هو الذي يريكم البرق خوفا وطمعا وينشئ السحاب الثقال'),
(1720, 13, 13, 'ويسبح الرعد بحمده والملائكة من خيفته ويرسل الصواعق فيصيب بها من يشاء وهم يجادلون في الله وهو شديد المحال'),
(1721, 13, 14, 'له دعوة الحق ۖ والذين يدعون من دونه لا يستجيبون لهم بشيء إلا كباسط كفيه إلى الماء ليبلغ فاه وما هو ببالغه ۚ وما دعاء الكافرين إلا في ضلال'),
(1722, 13, 15, 'ولله يسجد من في السماوات والأرض طوعا وكرها وظلالهم بالغدو والآصال'),
(1723, 13, 16, 'قل من رب السماوات والأرض قل الله ۚ قل أفاتخذتم من دونه أولياء لا يملكون لأنفسهم نفعا ولا ضرا ۚ قل هل يستوي الأعمى والبصير أم هل تستوي الظلمات والنور ۗ أم جعلوا لله شركاء خلقوا كخلقه فتشابه الخلق عليهم ۚ قل الله خالق كل شيء وهو الواحد القهار'),
(1724, 13, 17, 'أنزل من السماء ماء فسالت أودية بقدرها فاحتمل السيل زبدا رابيا ۚ ومما يوقدون عليه في النار ابتغاء حلية أو متاع زبد مثله ۚ كذلك يضرب الله الحق والباطل ۚ فأما الزبد فيذهب جفاء ۖ وأما ما ينفع الناس فيمكث في الأرض ۚ كذلك يضرب الله الأمثال'),
(1725, 13, 18, 'للذين استجابوا لربهم الحسنى ۚ والذين لم يستجيبوا له لو أن لهم ما في الأرض جميعا ومثله معه لافتدوا به ۚ أولئك لهم سوء الحساب ومأواهم جهنم ۖ وبئس المهاد'),
(1726, 13, 19, 'أفمن يعلم أنما أنزل إليك من ربك الحق كمن هو أعمى ۚ إنما يتذكر أولو الألباب'),
(1727, 13, 20, 'الذين يوفون بعهد الله ولا ينقضون الميثاق'),
(1728, 13, 21, 'والذين يصلون ما أمر الله به أن يوصل ويخشون ربهم ويخافون سوء الحساب'),
(1729, 13, 22, 'والذين صبروا ابتغاء وجه ربهم وأقاموا الصلاة وأنفقوا مما رزقناهم سرا وعلانية ويدرءون بالحسنة السيئة أولئك لهم عقبى الدار'),
(1730, 13, 23, 'جنات عدن يدخلونها ومن صلح من آبائهم وأزواجهم وذرياتهم ۖ والملائكة يدخلون عليهم من كل باب'),
(1731, 13, 24, 'سلام عليكم بما صبرتم ۚ فنعم عقبى الدار'),
(1732, 13, 25, 'والذين ينقضون عهد الله من بعد ميثاقه ويقطعون ما أمر الله به أن يوصل ويفسدون في الأرض ۙ أولئك لهم اللعنة ولهم سوء الدار'),
(1733, 13, 26, 'الله يبسط الرزق لمن يشاء ويقدر ۚ وفرحوا بالحياة الدنيا وما الحياة الدنيا في الآخرة إلا متاع'),
(1734, 13, 27, 'ويقول الذين كفروا لولا أنزل عليه آية من ربه ۗ قل إن الله يضل من يشاء ويهدي إليه من أناب'),
(1735, 13, 28, 'الذين آمنوا وتطمئن قلوبهم بذكر الله ۗ ألا بذكر الله تطمئن القلوب'),
(1736, 13, 29, 'الذين آمنوا وعملوا الصالحات طوبى لهم وحسن مآب'),
(1737, 13, 30, 'كذلك أرسلناك في أمة قد خلت من قبلها أمم لتتلو عليهم الذي أوحينا إليك وهم يكفرون بالرحمن ۚ قل هو ربي لا إله إلا هو عليه توكلت وإليه متاب'),
(1738, 13, 31, 'ولو أن قرآنا سيرت به الجبال أو قطعت به الأرض أو كلم به الموتى ۗ بل لله الأمر جميعا ۗ أفلم ييأس الذين آمنوا أن لو يشاء الله لهدى الناس جميعا ۗ ولا يزال الذين كفروا تصيبهم بما صنعوا قارعة أو تحل قريبا من دارهم حتى يأتي وعد الله ۚ إن الله لا يخلف الميعاد'),
(1739, 13, 32, 'ولقد استهزئ برسل من قبلك فأمليت للذين كفروا ثم أخذتهم ۖ فكيف كان عقاب'),
(1740, 13, 33, 'أفمن هو قائم على كل نفس بما كسبت ۗ وجعلوا لله شركاء قل سموهم ۚ أم تنبئونه بما لا يعلم في الأرض أم بظاهر من القول ۗ بل زين للذين كفروا مكرهم وصدوا عن السبيل ۗ ومن يضلل الله فما له من هاد'),
(1741, 13, 34, 'لهم عذاب في الحياة الدنيا ۖ ولعذاب الآخرة أشق ۖ وما لهم من الله من واق'),
(1742, 13, 35, 'مثل الجنة التي وعد المتقون ۖ تجري من تحتها الأنهار ۖ أكلها دائم وظلها ۚ تلك عقبى الذين اتقوا ۖ وعقبى الكافرين النار'),
(1743, 13, 36, 'والذين آتيناهم الكتاب يفرحون بما أنزل إليك ۖ ومن الأحزاب من ينكر بعضه ۚ قل إنما أمرت أن أعبد الله ولا أشرك به ۚ إليه أدعو وإليه مآب'),
(1744, 13, 37, 'وكذلك أنزلناه حكما عربيا ۚ ولئن اتبعت أهواءهم بعد ما جاءك من العلم ما لك من الله من ولي ولا واق'),
(1745, 13, 38, 'ولقد أرسلنا رسلا من قبلك وجعلنا لهم أزواجا وذرية ۚ وما كان لرسول أن يأتي بآية إلا بإذن الله ۗ لكل أجل كتاب'),
(1746, 13, 39, 'يمحو الله ما يشاء ويثبت ۖ وعنده أم الكتاب'),
(1747, 13, 40, 'وإن ما نرينك بعض الذي نعدهم أو نتوفينك فإنما عليك البلاغ وعلينا الحساب'),
(1748, 13, 41, 'أولم يروا أنا نأتي الأرض ننقصها من أطرافها ۚ والله يحكم لا معقب لحكمه ۚ وهو سريع الحساب'),
(1749, 13, 42, 'وقد مكر الذين من قبلهم فلله المكر جميعا ۖ يعلم ما تكسب كل نفس ۗ وسيعلم الكفار لمن عقبى الدار'),
(1750, 13, 43, 'ويقول الذين كفروا لست مرسلا ۚ قل كفى بالله شهيدا بيني وبينكم ومن عنده علم الكتاب');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'بسم الله الرحمن الرحيم الر ۚ كتاب أنزلناه إليك لتخرج الناس من الظلمات إلى النور بإذن ربهم إلى صراط العزيز الحميد'),
(1752, 14, 2, 'الله الذي له ما في السماوات وما في الأرض ۗ وويل للكافرين من عذاب شديد'),
(1753, 14, 3, 'الذين يستحبون الحياة الدنيا على الآخرة ويصدون عن سبيل الله ويبغونها عوجا ۚ أولئك في ضلال بعيد'),
(1754, 14, 4, 'وما أرسلنا من رسول إلا بلسان قومه ليبين لهم ۖ فيضل الله من يشاء ويهدي من يشاء ۚ وهو العزيز الحكيم'),
(1755, 14, 5, 'ولقد أرسلنا موسى بآياتنا أن أخرج قومك من الظلمات إلى النور وذكرهم بأيام الله ۚ إن في ذلك لآيات لكل صبار شكور'),
(1756, 14, 6, 'وإذ قال موسى لقومه اذكروا نعمة الله عليكم إذ أنجاكم من آل فرعون يسومونكم سوء العذاب ويذبحون أبناءكم ويستحيون نساءكم ۚ وفي ذلكم بلاء من ربكم عظيم'),
(1757, 14, 7, 'وإذ تأذن ربكم لئن شكرتم لأزيدنكم ۖ ولئن كفرتم إن عذابي لشديد'),
(1758, 14, 8, 'وقال موسى إن تكفروا أنتم ومن في الأرض جميعا فإن الله لغني حميد'),
(1759, 14, 9, 'ألم يأتكم نبأ الذين من قبلكم قوم نوح وعاد وثمود ۛ والذين من بعدهم ۛ لا يعلمهم إلا الله ۚ جاءتهم رسلهم بالبينات فردوا أيديهم في أفواههم وقالوا إنا كفرنا بما أرسلتم به وإنا لفي شك مما تدعوننا إليه مريب'),
(1760, 14, 10, 'قالت رسلهم أفي الله شك فاطر السماوات والأرض ۖ يدعوكم ليغفر لكم من ذنوبكم ويؤخركم إلى أجل مسمى ۚ قالوا إن أنتم إلا بشر مثلنا تريدون أن تصدونا عما كان يعبد آباؤنا فأتونا بسلطان مبين'),
(1761, 14, 11, 'قالت لهم رسلهم إن نحن إلا بشر مثلكم ولكن الله يمن على من يشاء من عباده ۖ وما كان لنا أن نأتيكم بسلطان إلا بإذن الله ۚ وعلى الله فليتوكل المؤمنون'),
(1762, 14, 12, 'وما لنا ألا نتوكل على الله وقد هدانا سبلنا ۚ ولنصبرن على ما آذيتمونا ۚ وعلى الله فليتوكل المتوكلون'),
(1763, 14, 13, 'وقال الذين كفروا لرسلهم لنخرجنكم من أرضنا أو لتعودن في ملتنا ۖ فأوحى إليهم ربهم لنهلكن الظالمين'),
(1764, 14, 14, 'ولنسكننكم الأرض من بعدهم ۚ ذلك لمن خاف مقامي وخاف وعيد'),
(1765, 14, 15, 'واستفتحوا وخاب كل جبار عنيد'),
(1766, 14, 16, 'من ورائه جهنم ويسقى من ماء صديد'),
(1767, 14, 17, 'يتجرعه ولا يكاد يسيغه ويأتيه الموت من كل مكان وما هو بميت ۖ ومن ورائه عذاب غليظ'),
(1768, 14, 18, 'مثل الذين كفروا بربهم ۖ أعمالهم كرماد اشتدت به الريح في يوم عاصف ۖ لا يقدرون مما كسبوا على شيء ۚ ذلك هو الضلال البعيد'),
(1769, 14, 19, 'ألم تر أن الله خلق السماوات والأرض بالحق ۚ إن يشأ يذهبكم ويأت بخلق جديد'),
(1770, 14, 20, 'وما ذلك على الله بعزيز'),
(1771, 14, 21, 'وبرزوا لله جميعا فقال الضعفاء للذين استكبروا إنا كنا لكم تبعا فهل أنتم مغنون عنا من عذاب الله من شيء ۚ قالوا لو هدانا الله لهديناكم ۖ سواء علينا أجزعنا أم صبرنا ما لنا من محيص'),
(1772, 14, 22, 'وقال الشيطان لما قضي الأمر إن الله وعدكم وعد الحق ووعدتكم فأخلفتكم ۖ وما كان لي عليكم من سلطان إلا أن دعوتكم فاستجبتم لي ۖ فلا تلوموني ولوموا أنفسكم ۖ ما أنا بمصرخكم وما أنتم بمصرخي ۖ إني كفرت بما أشركتمون من قبل ۗ إن الظالمين لهم عذاب أليم'),
(1773, 14, 23, 'وأدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار خالدين فيها بإذن ربهم ۖ تحيتهم فيها سلام'),
(1774, 14, 24, 'ألم تر كيف ضرب الله مثلا كلمة طيبة كشجرة طيبة أصلها ثابت وفرعها في السماء'),
(1775, 14, 25, 'تؤتي أكلها كل حين بإذن ربها ۗ ويضرب الله الأمثال للناس لعلهم يتذكرون'),
(1776, 14, 26, 'ومثل كلمة خبيثة كشجرة خبيثة اجتثت من فوق الأرض ما لها من قرار'),
(1777, 14, 27, 'يثبت الله الذين آمنوا بالقول الثابت في الحياة الدنيا وفي الآخرة ۖ ويضل الله الظالمين ۚ ويفعل الله ما يشاء'),
(1778, 14, 28, 'ألم تر إلى الذين بدلوا نعمت الله كفرا وأحلوا قومهم دار البوار'),
(1779, 14, 29, 'جهنم يصلونها ۖ وبئس القرار'),
(1780, 14, 30, 'وجعلوا لله أندادا ليضلوا عن سبيله ۗ قل تمتعوا فإن مصيركم إلى النار'),
(1781, 14, 31, 'قل لعبادي الذين آمنوا يقيموا الصلاة وينفقوا مما رزقناهم سرا وعلانية من قبل أن يأتي يوم لا بيع فيه ولا خلال'),
(1782, 14, 32, 'الله الذي خلق السماوات والأرض وأنزل من السماء ماء فأخرج به من الثمرات رزقا لكم ۖ وسخر لكم الفلك لتجري في البحر بأمره ۖ وسخر لكم الأنهار'),
(1783, 14, 33, 'وسخر لكم الشمس والقمر دائبين ۖ وسخر لكم الليل والنهار'),
(1784, 14, 34, 'وآتاكم من كل ما سألتموه ۚ وإن تعدوا نعمت الله لا تحصوها ۗ إن الإنسان لظلوم كفار'),
(1785, 14, 35, 'وإذ قال إبراهيم رب اجعل هذا البلد آمنا واجنبني وبني أن نعبد الأصنام'),
(1786, 14, 36, 'رب إنهن أضللن كثيرا من الناس ۖ فمن تبعني فإنه مني ۖ ومن عصاني فإنك غفور رحيم'),
(1787, 14, 37, 'ربنا إني أسكنت من ذريتي بواد غير ذي زرع عند بيتك المحرم ربنا ليقيموا الصلاة فاجعل أفئدة من الناس تهوي إليهم وارزقهم من الثمرات لعلهم يشكرون'),
(1788, 14, 38, 'ربنا إنك تعلم ما نخفي وما نعلن ۗ وما يخفى على الله من شيء في الأرض ولا في السماء'),
(1789, 14, 39, 'الحمد لله الذي وهب لي على الكبر إسماعيل وإسحاق ۚ إن ربي لسميع الدعاء'),
(1790, 14, 40, 'رب اجعلني مقيم الصلاة ومن ذريتي ۚ ربنا وتقبل دعاء'),
(1791, 14, 41, 'ربنا اغفر لي ولوالدي وللمؤمنين يوم يقوم الحساب'),
(1792, 14, 42, 'ولا تحسبن الله غافلا عما يعمل الظالمون ۚ إنما يؤخرهم ليوم تشخص فيه الأبصار'),
(1793, 14, 43, 'مهطعين مقنعي رءوسهم لا يرتد إليهم طرفهم ۖ وأفئدتهم هواء'),
(1794, 14, 44, 'وأنذر الناس يوم يأتيهم العذاب فيقول الذين ظلموا ربنا أخرنا إلى أجل قريب نجب دعوتك ونتبع الرسل ۗ أولم تكونوا أقسمتم من قبل ما لكم من زوال'),
(1795, 14, 45, 'وسكنتم في مساكن الذين ظلموا أنفسهم وتبين لكم كيف فعلنا بهم وضربنا لكم الأمثال'),
(1796, 14, 46, 'وقد مكروا مكرهم وعند الله مكرهم وإن كان مكرهم لتزول منه الجبال'),
(1797, 14, 47, 'فلا تحسبن الله مخلف وعده رسله ۗ إن الله عزيز ذو انتقام'),
(1798, 14, 48, 'يوم تبدل الأرض غير الأرض والسماوات ۖ وبرزوا لله الواحد القهار'),
(1799, 14, 49, 'وترى المجرمين يومئذ مقرنين في الأصفاد'),
(1800, 14, 50, 'سرابيلهم من قطران وتغشى وجوههم النار'),
(1801, 14, 51, 'ليجزي الله كل نفس ما كسبت ۚ إن الله سريع الحساب'),
(1802, 14, 52, 'هذا بلاغ للناس ولينذروا به وليعلموا أنما هو إله واحد وليذكر أولو الألباب');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'بسم الله الرحمن الرحيم الر ۚ تلك آيات الكتاب وقرآن مبين'),
(1804, 15, 2, 'ربما يود الذين كفروا لو كانوا مسلمين'),
(1805, 15, 3, 'ذرهم يأكلوا ويتمتعوا ويلههم الأمل ۖ فسوف يعلمون'),
(1806, 15, 4, 'وما أهلكنا من قرية إلا ولها كتاب معلوم'),
(1807, 15, 5, 'ما تسبق من أمة أجلها وما يستأخرون'),
(1808, 15, 6, 'وقالوا يا أيها الذي نزل عليه الذكر إنك لمجنون'),
(1809, 15, 7, 'لو ما تأتينا بالملائكة إن كنت من الصادقين'),
(1810, 15, 8, 'ما ننزل الملائكة إلا بالحق وما كانوا إذا منظرين'),
(1811, 15, 9, 'إنا نحن نزلنا الذكر وإنا له لحافظون'),
(1812, 15, 10, 'ولقد أرسلنا من قبلك في شيع الأولين'),
(1813, 15, 11, 'وما يأتيهم من رسول إلا كانوا به يستهزئون'),
(1814, 15, 12, 'كذلك نسلكه في قلوب المجرمين'),
(1815, 15, 13, 'لا يؤمنون به ۖ وقد خلت سنة الأولين'),
(1816, 15, 14, 'ولو فتحنا عليهم بابا من السماء فظلوا فيه يعرجون'),
(1817, 15, 15, 'لقالوا إنما سكرت أبصارنا بل نحن قوم مسحورون'),
(1818, 15, 16, 'ولقد جعلنا في السماء بروجا وزيناها للناظرين'),
(1819, 15, 17, 'وحفظناها من كل شيطان رجيم'),
(1820, 15, 18, 'إلا من استرق السمع فأتبعه شهاب مبين'),
(1821, 15, 19, 'والأرض مددناها وألقينا فيها رواسي وأنبتنا فيها من كل شيء موزون'),
(1822, 15, 20, 'وجعلنا لكم فيها معايش ومن لستم له برازقين'),
(1823, 15, 21, 'وإن من شيء إلا عندنا خزائنه وما ننزله إلا بقدر معلوم'),
(1824, 15, 22, 'وأرسلنا الرياح لواقح فأنزلنا من السماء ماء فأسقيناكموه وما أنتم له بخازنين'),
(1825, 15, 23, 'وإنا لنحن نحيي ونميت ونحن الوارثون'),
(1826, 15, 24, 'ولقد علمنا المستقدمين منكم ولقد علمنا المستأخرين'),
(1827, 15, 25, 'وإن ربك هو يحشرهم ۚ إنه حكيم عليم'),
(1828, 15, 26, 'ولقد خلقنا الإنسان من صلصال من حمإ مسنون'),
(1829, 15, 27, 'والجان خلقناه من قبل من نار السموم'),
(1830, 15, 28, 'وإذ قال ربك للملائكة إني خالق بشرا من صلصال من حمإ مسنون'),
(1831, 15, 29, 'فإذا سويته ونفخت فيه من روحي فقعوا له ساجدين'),
(1832, 15, 30, 'فسجد الملائكة كلهم أجمعون'),
(1833, 15, 31, 'إلا إبليس أبى أن يكون مع الساجدين'),
(1834, 15, 32, 'قال يا إبليس ما لك ألا تكون مع الساجدين'),
(1835, 15, 33, 'قال لم أكن لأسجد لبشر خلقته من صلصال من حمإ مسنون'),
(1836, 15, 34, 'قال فاخرج منها فإنك رجيم'),
(1837, 15, 35, 'وإن عليك اللعنة إلى يوم الدين'),
(1838, 15, 36, 'قال رب فأنظرني إلى يوم يبعثون'),
(1839, 15, 37, 'قال فإنك من المنظرين'),
(1840, 15, 38, 'إلى يوم الوقت المعلوم'),
(1841, 15, 39, 'قال رب بما أغويتني لأزينن لهم في الأرض ولأغوينهم أجمعين'),
(1842, 15, 40, 'إلا عبادك منهم المخلصين'),
(1843, 15, 41, 'قال هذا صراط علي مستقيم'),
(1844, 15, 42, 'إن عبادي ليس لك عليهم سلطان إلا من اتبعك من الغاوين'),
(1845, 15, 43, 'وإن جهنم لموعدهم أجمعين'),
(1846, 15, 44, 'لها سبعة أبواب لكل باب منهم جزء مقسوم'),
(1847, 15, 45, 'إن المتقين في جنات وعيون'),
(1848, 15, 46, 'ادخلوها بسلام آمنين'),
(1849, 15, 47, 'ونزعنا ما في صدورهم من غل إخوانا على سرر متقابلين'),
(1850, 15, 48, 'لا يمسهم فيها نصب وما هم منها بمخرجين'),
(1851, 15, 49, 'نبئ عبادي أني أنا الغفور الرحيم'),
(1852, 15, 50, 'وأن عذابي هو العذاب الأليم'),
(1853, 15, 51, 'ونبئهم عن ضيف إبراهيم'),
(1854, 15, 52, 'إذ دخلوا عليه فقالوا سلاما قال إنا منكم وجلون'),
(1855, 15, 53, 'قالوا لا توجل إنا نبشرك بغلام عليم'),
(1856, 15, 54, 'قال أبشرتموني على أن مسني الكبر فبم تبشرون'),
(1857, 15, 55, 'قالوا بشرناك بالحق فلا تكن من القانطين'),
(1858, 15, 56, 'قال ومن يقنط من رحمة ربه إلا الضالون'),
(1859, 15, 57, 'قال فما خطبكم أيها المرسلون'),
(1860, 15, 58, 'قالوا إنا أرسلنا إلى قوم مجرمين'),
(1861, 15, 59, 'إلا آل لوط إنا لمنجوهم أجمعين'),
(1862, 15, 60, 'إلا امرأته قدرنا ۙ إنها لمن الغابرين'),
(1863, 15, 61, 'فلما جاء آل لوط المرسلون'),
(1864, 15, 62, 'قال إنكم قوم منكرون'),
(1865, 15, 63, 'قالوا بل جئناك بما كانوا فيه يمترون'),
(1866, 15, 64, 'وأتيناك بالحق وإنا لصادقون'),
(1867, 15, 65, 'فأسر بأهلك بقطع من الليل واتبع أدبارهم ولا يلتفت منكم أحد وامضوا حيث تؤمرون'),
(1868, 15, 66, 'وقضينا إليه ذلك الأمر أن دابر هؤلاء مقطوع مصبحين'),
(1869, 15, 67, 'وجاء أهل المدينة يستبشرون'),
(1870, 15, 68, 'قال إن هؤلاء ضيفي فلا تفضحون'),
(1871, 15, 69, 'واتقوا الله ولا تخزون'),
(1872, 15, 70, 'قالوا أولم ننهك عن العالمين'),
(1873, 15, 71, 'قال هؤلاء بناتي إن كنتم فاعلين'),
(1874, 15, 72, 'لعمرك إنهم لفي سكرتهم يعمهون'),
(1875, 15, 73, 'فأخذتهم الصيحة مشرقين'),
(1876, 15, 74, 'فجعلنا عاليها سافلها وأمطرنا عليهم حجارة من سجيل'),
(1877, 15, 75, 'إن في ذلك لآيات للمتوسمين'),
(1878, 15, 76, 'وإنها لبسبيل مقيم'),
(1879, 15, 77, 'إن في ذلك لآية للمؤمنين'),
(1880, 15, 78, 'وإن كان أصحاب الأيكة لظالمين'),
(1881, 15, 79, 'فانتقمنا منهم وإنهما لبإمام مبين'),
(1882, 15, 80, 'ولقد كذب أصحاب الحجر المرسلين'),
(1883, 15, 81, 'وآتيناهم آياتنا فكانوا عنها معرضين'),
(1884, 15, 82, 'وكانوا ينحتون من الجبال بيوتا آمنين'),
(1885, 15, 83, 'فأخذتهم الصيحة مصبحين'),
(1886, 15, 84, 'فما أغنى عنهم ما كانوا يكسبون'),
(1887, 15, 85, 'وما خلقنا السماوات والأرض وما بينهما إلا بالحق ۗ وإن الساعة لآتية ۖ فاصفح الصفح الجميل'),
(1888, 15, 86, 'إن ربك هو الخلاق العليم'),
(1889, 15, 87, 'ولقد آتيناك سبعا من المثاني والقرآن العظيم'),
(1890, 15, 88, 'لا تمدن عينيك إلى ما متعنا به أزواجا منهم ولا تحزن عليهم واخفض جناحك للمؤمنين'),
(1891, 15, 89, 'وقل إني أنا النذير المبين'),
(1892, 15, 90, 'كما أنزلنا على المقتسمين'),
(1893, 15, 91, 'الذين جعلوا القرآن عضين'),
(1894, 15, 92, 'فوربك لنسألنهم أجمعين'),
(1895, 15, 93, 'عما كانوا يعملون'),
(1896, 15, 94, 'فاصدع بما تؤمر وأعرض عن المشركين'),
(1897, 15, 95, 'إنا كفيناك المستهزئين'),
(1898, 15, 96, 'الذين يجعلون مع الله إلها آخر ۚ فسوف يعلمون'),
(1899, 15, 97, 'ولقد نعلم أنك يضيق صدرك بما يقولون'),
(1900, 15, 98, 'فسبح بحمد ربك وكن من الساجدين'),
(1901, 15, 99, 'واعبد ربك حتى يأتيك اليقين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'بسم الله الرحمن الرحيم أتى أمر الله فلا تستعجلوه ۚ سبحانه وتعالى عما يشركون'),
(1903, 16, 2, 'ينزل الملائكة بالروح من أمره على من يشاء من عباده أن أنذروا أنه لا إله إلا أنا فاتقون'),
(1904, 16, 3, 'خلق السماوات والأرض بالحق ۚ تعالى عما يشركون'),
(1905, 16, 4, 'خلق الإنسان من نطفة فإذا هو خصيم مبين'),
(1906, 16, 5, 'والأنعام خلقها ۗ لكم فيها دفء ومنافع ومنها تأكلون'),
(1907, 16, 6, 'ولكم فيها جمال حين تريحون وحين تسرحون'),
(1908, 16, 7, 'وتحمل أثقالكم إلى بلد لم تكونوا بالغيه إلا بشق الأنفس ۚ إن ربكم لرءوف رحيم'),
(1909, 16, 8, 'والخيل والبغال والحمير لتركبوها وزينة ۚ ويخلق ما لا تعلمون'),
(1910, 16, 9, 'وعلى الله قصد السبيل ومنها جائر ۚ ولو شاء لهداكم أجمعين'),
(1911, 16, 10, 'هو الذي أنزل من السماء ماء ۖ لكم منه شراب ومنه شجر فيه تسيمون'),
(1912, 16, 11, 'ينبت لكم به الزرع والزيتون والنخيل والأعناب ومن كل الثمرات ۗ إن في ذلك لآية لقوم يتفكرون'),
(1913, 16, 12, 'وسخر لكم الليل والنهار والشمس والقمر ۖ والنجوم مسخرات بأمره ۗ إن في ذلك لآيات لقوم يعقلون'),
(1914, 16, 13, 'وما ذرأ لكم في الأرض مختلفا ألوانه ۗ إن في ذلك لآية لقوم يذكرون'),
(1915, 16, 14, 'وهو الذي سخر البحر لتأكلوا منه لحما طريا وتستخرجوا منه حلية تلبسونها وترى الفلك مواخر فيه ولتبتغوا من فضله ولعلكم تشكرون'),
(1916, 16, 15, 'وألقى في الأرض رواسي أن تميد بكم وأنهارا وسبلا لعلكم تهتدون'),
(1917, 16, 16, 'وعلامات ۚ وبالنجم هم يهتدون'),
(1918, 16, 17, 'أفمن يخلق كمن لا يخلق ۗ أفلا تذكرون'),
(1919, 16, 18, 'وإن تعدوا نعمة الله لا تحصوها ۗ إن الله لغفور رحيم'),
(1920, 16, 19, 'والله يعلم ما تسرون وما تعلنون'),
(1921, 16, 20, 'والذين يدعون من دون الله لا يخلقون شيئا وهم يخلقون'),
(1922, 16, 21, 'أموات غير أحياء ۖ وما يشعرون أيان يبعثون'),
(1923, 16, 22, 'إلهكم إله واحد ۚ فالذين لا يؤمنون بالآخرة قلوبهم منكرة وهم مستكبرون'),
(1924, 16, 23, 'لا جرم أن الله يعلم ما يسرون وما يعلنون ۚ إنه لا يحب المستكبرين'),
(1925, 16, 24, 'وإذا قيل لهم ماذا أنزل ربكم ۙ قالوا أساطير الأولين'),
(1926, 16, 25, 'ليحملوا أوزارهم كاملة يوم القيامة ۙ ومن أوزار الذين يضلونهم بغير علم ۗ ألا ساء ما يزرون'),
(1927, 16, 26, 'قد مكر الذين من قبلهم فأتى الله بنيانهم من القواعد فخر عليهم السقف من فوقهم وأتاهم العذاب من حيث لا يشعرون'),
(1928, 16, 27, 'ثم يوم القيامة يخزيهم ويقول أين شركائي الذين كنتم تشاقون فيهم ۚ قال الذين أوتوا العلم إن الخزي اليوم والسوء على الكافرين'),
(1929, 16, 28, 'الذين تتوفاهم الملائكة ظالمي أنفسهم ۖ فألقوا السلم ما كنا نعمل من سوء ۚ بلى إن الله عليم بما كنتم تعملون'),
(1930, 16, 29, 'فادخلوا أبواب جهنم خالدين فيها ۖ فلبئس مثوى المتكبرين'),
(1931, 16, 30, 'وقيل للذين اتقوا ماذا أنزل ربكم ۚ قالوا خيرا ۗ للذين أحسنوا في هذه الدنيا حسنة ۚ ولدار الآخرة خير ۚ ولنعم دار المتقين'),
(1932, 16, 31, 'جنات عدن يدخلونها تجري من تحتها الأنهار ۖ لهم فيها ما يشاءون ۚ كذلك يجزي الله المتقين'),
(1933, 16, 32, 'الذين تتوفاهم الملائكة طيبين ۙ يقولون سلام عليكم ادخلوا الجنة بما كنتم تعملون'),
(1934, 16, 33, 'هل ينظرون إلا أن تأتيهم الملائكة أو يأتي أمر ربك ۚ كذلك فعل الذين من قبلهم ۚ وما ظلمهم الله ولكن كانوا أنفسهم يظلمون'),
(1935, 16, 34, 'فأصابهم سيئات ما عملوا وحاق بهم ما كانوا به يستهزئون'),
(1936, 16, 35, 'وقال الذين أشركوا لو شاء الله ما عبدنا من دونه من شيء نحن ولا آباؤنا ولا حرمنا من دونه من شيء ۚ كذلك فعل الذين من قبلهم ۚ فهل على الرسل إلا البلاغ المبين'),
(1937, 16, 36, 'ولقد بعثنا في كل أمة رسولا أن اعبدوا الله واجتنبوا الطاغوت ۖ فمنهم من هدى الله ومنهم من حقت عليه الضلالة ۚ فسيروا في الأرض فانظروا كيف كان عاقبة المكذبين'),
(1938, 16, 37, 'إن تحرص على هداهم فإن الله لا يهدي من يضل ۖ وما لهم من ناصرين'),
(1939, 16, 38, 'وأقسموا بالله جهد أيمانهم ۙ لا يبعث الله من يموت ۚ بلى وعدا عليه حقا ولكن أكثر الناس لا يعلمون'),
(1940, 16, 39, 'ليبين لهم الذي يختلفون فيه وليعلم الذين كفروا أنهم كانوا كاذبين'),
(1941, 16, 40, 'إنما قولنا لشيء إذا أردناه أن نقول له كن فيكون'),
(1942, 16, 41, 'والذين هاجروا في الله من بعد ما ظلموا لنبوئنهم في الدنيا حسنة ۖ ولأجر الآخرة أكبر ۚ لو كانوا يعلمون'),
(1943, 16, 42, 'الذين صبروا وعلى ربهم يتوكلون'),
(1944, 16, 43, 'وما أرسلنا من قبلك إلا رجالا نوحي إليهم ۚ فاسألوا أهل الذكر إن كنتم لا تعلمون'),
(1945, 16, 44, 'بالبينات والزبر ۗ وأنزلنا إليك الذكر لتبين للناس ما نزل إليهم ولعلهم يتفكرون'),
(1946, 16, 45, 'أفأمن الذين مكروا السيئات أن يخسف الله بهم الأرض أو يأتيهم العذاب من حيث لا يشعرون'),
(1947, 16, 46, 'أو يأخذهم في تقلبهم فما هم بمعجزين'),
(1948, 16, 47, 'أو يأخذهم على تخوف فإن ربكم لرءوف رحيم'),
(1949, 16, 48, 'أولم يروا إلى ما خلق الله من شيء يتفيأ ظلاله عن اليمين والشمائل سجدا لله وهم داخرون'),
(1950, 16, 49, 'ولله يسجد ما في السماوات وما في الأرض من دابة والملائكة وهم لا يستكبرون'),
(1951, 16, 50, 'يخافون ربهم من فوقهم ويفعلون ما يؤمرون'),
(1952, 16, 51, 'وقال الله لا تتخذوا إلهين اثنين ۖ إنما هو إله واحد ۖ فإياي فارهبون'),
(1953, 16, 52, 'وله ما في السماوات والأرض وله الدين واصبا ۚ أفغير الله تتقون'),
(1954, 16, 53, 'وما بكم من نعمة فمن الله ۖ ثم إذا مسكم الضر فإليه تجأرون'),
(1955, 16, 54, 'ثم إذا كشف الضر عنكم إذا فريق منكم بربهم يشركون'),
(1956, 16, 55, 'ليكفروا بما آتيناهم ۚ فتمتعوا ۖ فسوف تعلمون'),
(1957, 16, 56, 'ويجعلون لما لا يعلمون نصيبا مما رزقناهم ۗ تالله لتسألن عما كنتم تفترون'),
(1958, 16, 57, 'ويجعلون لله البنات سبحانه ۙ ولهم ما يشتهون'),
(1959, 16, 58, 'وإذا بشر أحدهم بالأنثى ظل وجهه مسودا وهو كظيم'),
(1960, 16, 59, 'يتوارى من القوم من سوء ما بشر به ۚ أيمسكه على هون أم يدسه في التراب ۗ ألا ساء ما يحكمون'),
(1961, 16, 60, 'للذين لا يؤمنون بالآخرة مثل السوء ۖ ولله المثل الأعلى ۚ وهو العزيز الحكيم'),
(1962, 16, 61, 'ولو يؤاخذ الله الناس بظلمهم ما ترك عليها من دابة ولكن يؤخرهم إلى أجل مسمى ۖ فإذا جاء أجلهم لا يستأخرون ساعة ۖ ولا يستقدمون'),
(1963, 16, 62, 'ويجعلون لله ما يكرهون وتصف ألسنتهم الكذب أن لهم الحسنى ۖ لا جرم أن لهم النار وأنهم مفرطون'),
(1964, 16, 63, 'تالله لقد أرسلنا إلى أمم من قبلك فزين لهم الشيطان أعمالهم فهو وليهم اليوم ولهم عذاب أليم'),
(1965, 16, 64, 'وما أنزلنا عليك الكتاب إلا لتبين لهم الذي اختلفوا فيه ۙ وهدى ورحمة لقوم يؤمنون'),
(1966, 16, 65, 'والله أنزل من السماء ماء فأحيا به الأرض بعد موتها ۚ إن في ذلك لآية لقوم يسمعون'),
(1967, 16, 66, 'وإن لكم في الأنعام لعبرة ۖ نسقيكم مما في بطونه من بين فرث ودم لبنا خالصا سائغا للشاربين'),
(1968, 16, 67, 'ومن ثمرات النخيل والأعناب تتخذون منه سكرا ورزقا حسنا ۗ إن في ذلك لآية لقوم يعقلون'),
(1969, 16, 68, 'وأوحى ربك إلى النحل أن اتخذي من الجبال بيوتا ومن الشجر ومما يعرشون'),
(1970, 16, 69, 'ثم كلي من كل الثمرات فاسلكي سبل ربك ذللا ۚ يخرج من بطونها شراب مختلف ألوانه فيه شفاء للناس ۗ إن في ذلك لآية لقوم يتفكرون'),
(1971, 16, 70, 'والله خلقكم ثم يتوفاكم ۚ ومنكم من يرد إلى أرذل العمر لكي لا يعلم بعد علم شيئا ۚ إن الله عليم قدير'),
(1972, 16, 71, 'والله فضل بعضكم على بعض في الرزق ۚ فما الذين فضلوا برادي رزقهم على ما ملكت أيمانهم فهم فيه سواء ۚ أفبنعمة الله يجحدون'),
(1973, 16, 72, 'والله جعل لكم من أنفسكم أزواجا وجعل لكم من أزواجكم بنين وحفدة ورزقكم من الطيبات ۚ أفبالباطل يؤمنون وبنعمت الله هم يكفرون'),
(1974, 16, 73, 'ويعبدون من دون الله ما لا يملك لهم رزقا من السماوات والأرض شيئا ولا يستطيعون'),
(1975, 16, 74, 'فلا تضربوا لله الأمثال ۚ إن الله يعلم وأنتم لا تعلمون'),
(1976, 16, 75, 'ضرب الله مثلا عبدا مملوكا لا يقدر على شيء ومن رزقناه منا رزقا حسنا فهو ينفق منه سرا وجهرا ۖ هل يستوون ۚ الحمد لله ۚ بل أكثرهم لا يعلمون'),
(1977, 16, 76, 'وضرب الله مثلا رجلين أحدهما أبكم لا يقدر على شيء وهو كل على مولاه أينما يوجهه لا يأت بخير ۖ هل يستوي هو ومن يأمر بالعدل ۙ وهو على صراط مستقيم'),
(1978, 16, 77, 'ولله غيب السماوات والأرض ۚ وما أمر الساعة إلا كلمح البصر أو هو أقرب ۚ إن الله على كل شيء قدير'),
(1979, 16, 78, 'والله أخرجكم من بطون أمهاتكم لا تعلمون شيئا وجعل لكم السمع والأبصار والأفئدة ۙ لعلكم تشكرون'),
(1980, 16, 79, 'ألم يروا إلى الطير مسخرات في جو السماء ما يمسكهن إلا الله ۗ إن في ذلك لآيات لقوم يؤمنون'),
(1981, 16, 80, 'والله جعل لكم من بيوتكم سكنا وجعل لكم من جلود الأنعام بيوتا تستخفونها يوم ظعنكم ويوم إقامتكم ۙ ومن أصوافها وأوبارها وأشعارها أثاثا ومتاعا إلى حين'),
(1982, 16, 81, 'والله جعل لكم مما خلق ظلالا وجعل لكم من الجبال أكنانا وجعل لكم سرابيل تقيكم الحر وسرابيل تقيكم بأسكم ۚ كذلك يتم نعمته عليكم لعلكم تسلمون'),
(1983, 16, 82, 'فإن تولوا فإنما عليك البلاغ المبين'),
(1984, 16, 83, 'يعرفون نعمت الله ثم ينكرونها وأكثرهم الكافرون'),
(1985, 16, 84, 'ويوم نبعث من كل أمة شهيدا ثم لا يؤذن للذين كفروا ولا هم يستعتبون'),
(1986, 16, 85, 'وإذا رأى الذين ظلموا العذاب فلا يخفف عنهم ولا هم ينظرون'),
(1987, 16, 86, 'وإذا رأى الذين أشركوا شركاءهم قالوا ربنا هؤلاء شركاؤنا الذين كنا ندعو من دونك ۖ فألقوا إليهم القول إنكم لكاذبون'),
(1988, 16, 87, 'وألقوا إلى الله يومئذ السلم ۖ وضل عنهم ما كانوا يفترون'),
(1989, 16, 88, 'الذين كفروا وصدوا عن سبيل الله زدناهم عذابا فوق العذاب بما كانوا يفسدون'),
(1990, 16, 89, 'ويوم نبعث في كل أمة شهيدا عليهم من أنفسهم ۖ وجئنا بك شهيدا على هؤلاء ۚ ونزلنا عليك الكتاب تبيانا لكل شيء وهدى ورحمة وبشرى للمسلمين'),
(1991, 16, 90, 'إن الله يأمر بالعدل والإحسان وإيتاء ذي القربى وينهى عن الفحشاء والمنكر والبغي ۚ يعظكم لعلكم تذكرون'),
(1992, 16, 91, 'وأوفوا بعهد الله إذا عاهدتم ولا تنقضوا الأيمان بعد توكيدها وقد جعلتم الله عليكم كفيلا ۚ إن الله يعلم ما تفعلون'),
(1993, 16, 92, 'ولا تكونوا كالتي نقضت غزلها من بعد قوة أنكاثا تتخذون أيمانكم دخلا بينكم أن تكون أمة هي أربى من أمة ۚ إنما يبلوكم الله به ۚ وليبينن لكم يوم القيامة ما كنتم فيه تختلفون'),
(1994, 16, 93, 'ولو شاء الله لجعلكم أمة واحدة ولكن يضل من يشاء ويهدي من يشاء ۚ ولتسألن عما كنتم تعملون'),
(1995, 16, 94, 'ولا تتخذوا أيمانكم دخلا بينكم فتزل قدم بعد ثبوتها وتذوقوا السوء بما صددتم عن سبيل الله ۖ ولكم عذاب عظيم'),
(1996, 16, 95, 'ولا تشتروا بعهد الله ثمنا قليلا ۚ إنما عند الله هو خير لكم إن كنتم تعلمون'),
(1997, 16, 96, 'ما عندكم ينفد ۖ وما عند الله باق ۗ ولنجزين الذين صبروا أجرهم بأحسن ما كانوا يعملون'),
(1998, 16, 97, 'من عمل صالحا من ذكر أو أنثى وهو مؤمن فلنحيينه حياة طيبة ۖ ولنجزينهم أجرهم بأحسن ما كانوا يعملون'),
(1999, 16, 98, 'فإذا قرأت القرآن فاستعذ بالله من الشيطان الرجيم'),
(2000, 16, 99, 'إنه ليس له سلطان على الذين آمنوا وعلى ربهم يتوكلون'),
(2001, 16, 100, 'إنما سلطانه على الذين يتولونه والذين هم به مشركون'),
(2002, 16, 101, 'وإذا بدلنا آية مكان آية ۙ والله أعلم بما ينزل قالوا إنما أنت مفتر ۚ بل أكثرهم لا يعلمون'),
(2003, 16, 102, 'قل نزله روح القدس من ربك بالحق ليثبت الذين آمنوا وهدى وبشرى للمسلمين'),
(2004, 16, 103, 'ولقد نعلم أنهم يقولون إنما يعلمه بشر ۗ لسان الذي يلحدون إليه أعجمي وهذا لسان عربي مبين'),
(2005, 16, 104, 'إن الذين لا يؤمنون بآيات الله لا يهديهم الله ولهم عذاب أليم'),
(2006, 16, 105, 'إنما يفتري الكذب الذين لا يؤمنون بآيات الله ۖ وأولئك هم الكاذبون'),
(2007, 16, 106, 'من كفر بالله من بعد إيمانه إلا من أكره وقلبه مطمئن بالإيمان ولكن من شرح بالكفر صدرا فعليهم غضب من الله ولهم عذاب عظيم'),
(2008, 16, 107, 'ذلك بأنهم استحبوا الحياة الدنيا على الآخرة وأن الله لا يهدي القوم الكافرين'),
(2009, 16, 108, 'أولئك الذين طبع الله على قلوبهم وسمعهم وأبصارهم ۖ وأولئك هم الغافلون'),
(2010, 16, 109, 'لا جرم أنهم في الآخرة هم الخاسرون'),
(2011, 16, 110, 'ثم إن ربك للذين هاجروا من بعد ما فتنوا ثم جاهدوا وصبروا إن ربك من بعدها لغفور رحيم'),
(2012, 16, 111, 'يوم تأتي كل نفس تجادل عن نفسها وتوفى كل نفس ما عملت وهم لا يظلمون'),
(2013, 16, 112, 'وضرب الله مثلا قرية كانت آمنة مطمئنة يأتيها رزقها رغدا من كل مكان فكفرت بأنعم الله فأذاقها الله لباس الجوع والخوف بما كانوا يصنعون'),
(2014, 16, 113, 'ولقد جاءهم رسول منهم فكذبوه فأخذهم العذاب وهم ظالمون'),
(2015, 16, 114, 'فكلوا مما رزقكم الله حلالا طيبا واشكروا نعمت الله إن كنتم إياه تعبدون'),
(2016, 16, 115, 'إنما حرم عليكم الميتة والدم ولحم الخنزير وما أهل لغير الله به ۖ فمن اضطر غير باغ ولا عاد فإن الله غفور رحيم'),
(2017, 16, 116, 'ولا تقولوا لما تصف ألسنتكم الكذب هذا حلال وهذا حرام لتفتروا على الله الكذب ۚ إن الذين يفترون على الله الكذب لا يفلحون'),
(2018, 16, 117, 'متاع قليل ولهم عذاب أليم'),
(2019, 16, 118, 'وعلى الذين هادوا حرمنا ما قصصنا عليك من قبل ۖ وما ظلمناهم ولكن كانوا أنفسهم يظلمون'),
(2020, 16, 119, 'ثم إن ربك للذين عملوا السوء بجهالة ثم تابوا من بعد ذلك وأصلحوا إن ربك من بعدها لغفور رحيم'),
(2021, 16, 120, 'إن إبراهيم كان أمة قانتا لله حنيفا ولم يك من المشركين'),
(2022, 16, 121, 'شاكرا لأنعمه ۚ اجتباه وهداه إلى صراط مستقيم'),
(2023, 16, 122, 'وآتيناه في الدنيا حسنة ۖ وإنه في الآخرة لمن الصالحين'),
(2024, 16, 123, 'ثم أوحينا إليك أن اتبع ملة إبراهيم حنيفا ۖ وما كان من المشركين'),
(2025, 16, 124, 'إنما جعل السبت على الذين اختلفوا فيه ۚ وإن ربك ليحكم بينهم يوم القيامة فيما كانوا فيه يختلفون'),
(2026, 16, 125, 'ادع إلى سبيل ربك بالحكمة والموعظة الحسنة ۖ وجادلهم بالتي هي أحسن ۚ إن ربك هو أعلم بمن ضل عن سبيله ۖ وهو أعلم بالمهتدين'),
(2027, 16, 126, 'وإن عاقبتم فعاقبوا بمثل ما عوقبتم به ۖ ولئن صبرتم لهو خير للصابرين'),
(2028, 16, 127, 'واصبر وما صبرك إلا بالله ۚ ولا تحزن عليهم ولا تك في ضيق مما يمكرون'),
(2029, 16, 128, 'إن الله مع الذين اتقوا والذين هم محسنون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'بسم الله الرحمن الرحيم سبحان الذي أسرى بعبده ليلا من المسجد الحرام إلى المسجد الأقصى الذي باركنا حوله لنريه من آياتنا ۚ إنه هو السميع البصير'),
(2031, 17, 2, 'وآتينا موسى الكتاب وجعلناه هدى لبني إسرائيل ألا تتخذوا من دوني وكيلا'),
(2032, 17, 3, 'ذرية من حملنا مع نوح ۚ إنه كان عبدا شكورا'),
(2033, 17, 4, 'وقضينا إلى بني إسرائيل في الكتاب لتفسدن في الأرض مرتين ولتعلن علوا كبيرا'),
(2034, 17, 5, 'فإذا جاء وعد أولاهما بعثنا عليكم عبادا لنا أولي بأس شديد فجاسوا خلال الديار ۚ وكان وعدا مفعولا'),
(2035, 17, 6, 'ثم رددنا لكم الكرة عليهم وأمددناكم بأموال وبنين وجعلناكم أكثر نفيرا'),
(2036, 17, 7, 'إن أحسنتم أحسنتم لأنفسكم ۖ وإن أسأتم فلها ۚ فإذا جاء وعد الآخرة ليسوءوا وجوهكم وليدخلوا المسجد كما دخلوه أول مرة وليتبروا ما علوا تتبيرا'),
(2037, 17, 8, 'عسى ربكم أن يرحمكم ۚ وإن عدتم عدنا ۘ وجعلنا جهنم للكافرين حصيرا'),
(2038, 17, 9, 'إن هذا القرآن يهدي للتي هي أقوم ويبشر المؤمنين الذين يعملون الصالحات أن لهم أجرا كبيرا'),
(2039, 17, 10, 'وأن الذين لا يؤمنون بالآخرة أعتدنا لهم عذابا أليما'),
(2040, 17, 11, 'ويدع الإنسان بالشر دعاءه بالخير ۖ وكان الإنسان عجولا'),
(2041, 17, 12, 'وجعلنا الليل والنهار آيتين ۖ فمحونا آية الليل وجعلنا آية النهار مبصرة لتبتغوا فضلا من ربكم ولتعلموا عدد السنين والحساب ۚ وكل شيء فصلناه تفصيلا'),
(2042, 17, 13, 'وكل إنسان ألزمناه طائره في عنقه ۖ ونخرج له يوم القيامة كتابا يلقاه منشورا'),
(2043, 17, 14, 'اقرأ كتابك كفى بنفسك اليوم عليك حسيبا'),
(2044, 17, 15, 'من اهتدى فإنما يهتدي لنفسه ۖ ومن ضل فإنما يضل عليها ۚ ولا تزر وازرة وزر أخرى ۗ وما كنا معذبين حتى نبعث رسولا'),
(2045, 17, 16, 'وإذا أردنا أن نهلك قرية أمرنا مترفيها ففسقوا فيها فحق عليها القول فدمرناها تدميرا'),
(2046, 17, 17, 'وكم أهلكنا من القرون من بعد نوح ۗ وكفى بربك بذنوب عباده خبيرا بصيرا'),
(2047, 17, 18, 'من كان يريد العاجلة عجلنا له فيها ما نشاء لمن نريد ثم جعلنا له جهنم يصلاها مذموما مدحورا'),
(2048, 17, 19, 'ومن أراد الآخرة وسعى لها سعيها وهو مؤمن فأولئك كان سعيهم مشكورا'),
(2049, 17, 20, 'كلا نمد هؤلاء وهؤلاء من عطاء ربك ۚ وما كان عطاء ربك محظورا'),
(2050, 17, 21, 'انظر كيف فضلنا بعضهم على بعض ۚ وللآخرة أكبر درجات وأكبر تفضيلا'),
(2051, 17, 22, 'لا تجعل مع الله إلها آخر فتقعد مذموما مخذولا'),
(2052, 17, 23, 'وقضى ربك ألا تعبدوا إلا إياه وبالوالدين إحسانا ۚ إما يبلغن عندك الكبر أحدهما أو كلاهما فلا تقل لهما أف ولا تنهرهما وقل لهما قولا كريما'),
(2053, 17, 24, 'واخفض لهما جناح الذل من الرحمة وقل رب ارحمهما كما ربياني صغيرا'),
(2054, 17, 25, 'ربكم أعلم بما في نفوسكم ۚ إن تكونوا صالحين فإنه كان للأوابين غفورا'),
(2055, 17, 26, 'وآت ذا القربى حقه والمسكين وابن السبيل ولا تبذر تبذيرا'),
(2056, 17, 27, 'إن المبذرين كانوا إخوان الشياطين ۖ وكان الشيطان لربه كفورا'),
(2057, 17, 28, 'وإما تعرضن عنهم ابتغاء رحمة من ربك ترجوها فقل لهم قولا ميسورا'),
(2058, 17, 29, 'ولا تجعل يدك مغلولة إلى عنقك ولا تبسطها كل البسط فتقعد ملوما محسورا'),
(2059, 17, 30, 'إن ربك يبسط الرزق لمن يشاء ويقدر ۚ إنه كان بعباده خبيرا بصيرا'),
(2060, 17, 31, 'ولا تقتلوا أولادكم خشية إملاق ۖ نحن نرزقهم وإياكم ۚ إن قتلهم كان خطئا كبيرا'),
(2061, 17, 32, 'ولا تقربوا الزنى ۖ إنه كان فاحشة وساء سبيلا'),
(2062, 17, 33, 'ولا تقتلوا النفس التي حرم الله إلا بالحق ۗ ومن قتل مظلوما فقد جعلنا لوليه سلطانا فلا يسرف في القتل ۖ إنه كان منصورا'),
(2063, 17, 34, 'ولا تقربوا مال اليتيم إلا بالتي هي أحسن حتى يبلغ أشده ۚ وأوفوا بالعهد ۖ إن العهد كان مسئولا'),
(2064, 17, 35, 'وأوفوا الكيل إذا كلتم وزنوا بالقسطاس المستقيم ۚ ذلك خير وأحسن تأويلا'),
(2065, 17, 36, 'ولا تقف ما ليس لك به علم ۚ إن السمع والبصر والفؤاد كل أولئك كان عنه مسئولا'),
(2066, 17, 37, 'ولا تمش في الأرض مرحا ۖ إنك لن تخرق الأرض ولن تبلغ الجبال طولا'),
(2067, 17, 38, 'كل ذلك كان سيئه عند ربك مكروها'),
(2068, 17, 39, 'ذلك مما أوحى إليك ربك من الحكمة ۗ ولا تجعل مع الله إلها آخر فتلقى في جهنم ملوما مدحورا'),
(2069, 17, 40, 'أفأصفاكم ربكم بالبنين واتخذ من الملائكة إناثا ۚ إنكم لتقولون قولا عظيما'),
(2070, 17, 41, 'ولقد صرفنا في هذا القرآن ليذكروا وما يزيدهم إلا نفورا'),
(2071, 17, 42, 'قل لو كان معه آلهة كما يقولون إذا لابتغوا إلى ذي العرش سبيلا'),
(2072, 17, 43, 'سبحانه وتعالى عما يقولون علوا كبيرا'),
(2073, 17, 44, 'تسبح له السماوات السبع والأرض ومن فيهن ۚ وإن من شيء إلا يسبح بحمده ولكن لا تفقهون تسبيحهم ۗ إنه كان حليما غفورا'),
(2074, 17, 45, 'وإذا قرأت القرآن جعلنا بينك وبين الذين لا يؤمنون بالآخرة حجابا مستورا'),
(2075, 17, 46, 'وجعلنا على قلوبهم أكنة أن يفقهوه وفي آذانهم وقرا ۚ وإذا ذكرت ربك في القرآن وحده ولوا على أدبارهم نفورا'),
(2076, 17, 47, 'نحن أعلم بما يستمعون به إذ يستمعون إليك وإذ هم نجوى إذ يقول الظالمون إن تتبعون إلا رجلا مسحورا'),
(2077, 17, 48, 'انظر كيف ضربوا لك الأمثال فضلوا فلا يستطيعون سبيلا'),
(2078, 17, 49, 'وقالوا أإذا كنا عظاما ورفاتا أإنا لمبعوثون خلقا جديدا'),
(2079, 17, 50, 'قل كونوا حجارة أو حديدا'),
(2080, 17, 51, 'أو خلقا مما يكبر في صدوركم ۚ فسيقولون من يعيدنا ۖ قل الذي فطركم أول مرة ۚ فسينغضون إليك رءوسهم ويقولون متى هو ۖ قل عسى أن يكون قريبا'),
(2081, 17, 52, 'يوم يدعوكم فتستجيبون بحمده وتظنون إن لبثتم إلا قليلا'),
(2082, 17, 53, 'وقل لعبادي يقولوا التي هي أحسن ۚ إن الشيطان ينزغ بينهم ۚ إن الشيطان كان للإنسان عدوا مبينا'),
(2083, 17, 54, 'ربكم أعلم بكم ۖ إن يشأ يرحمكم أو إن يشأ يعذبكم ۚ وما أرسلناك عليهم وكيلا'),
(2084, 17, 55, 'وربك أعلم بمن في السماوات والأرض ۗ ولقد فضلنا بعض النبيين على بعض ۖ وآتينا داوود زبورا'),
(2085, 17, 56, 'قل ادعوا الذين زعمتم من دونه فلا يملكون كشف الضر عنكم ولا تحويلا'),
(2086, 17, 57, 'أولئك الذين يدعون يبتغون إلى ربهم الوسيلة أيهم أقرب ويرجون رحمته ويخافون عذابه ۚ إن عذاب ربك كان محذورا'),
(2087, 17, 58, 'وإن من قرية إلا نحن مهلكوها قبل يوم القيامة أو معذبوها عذابا شديدا ۚ كان ذلك في الكتاب مسطورا'),
(2088, 17, 59, 'وما منعنا أن نرسل بالآيات إلا أن كذب بها الأولون ۚ وآتينا ثمود الناقة مبصرة فظلموا بها ۚ وما نرسل بالآيات إلا تخويفا'),
(2089, 17, 60, 'وإذ قلنا لك إن ربك أحاط بالناس ۚ وما جعلنا الرؤيا التي أريناك إلا فتنة للناس والشجرة الملعونة في القرآن ۚ ونخوفهم فما يزيدهم إلا طغيانا كبيرا'),
(2090, 17, 61, 'وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس قال أأسجد لمن خلقت طينا'),
(2091, 17, 62, 'قال أرأيتك هذا الذي كرمت علي لئن أخرتن إلى يوم القيامة لأحتنكن ذريته إلا قليلا'),
(2092, 17, 63, 'قال اذهب فمن تبعك منهم فإن جهنم جزاؤكم جزاء موفورا'),
(2093, 17, 64, 'واستفزز من استطعت منهم بصوتك وأجلب عليهم بخيلك ورجلك وشاركهم في الأموال والأولاد وعدهم ۚ وما يعدهم الشيطان إلا غرورا'),
(2094, 17, 65, 'إن عبادي ليس لك عليهم سلطان ۚ وكفى بربك وكيلا'),
(2095, 17, 66, 'ربكم الذي يزجي لكم الفلك في البحر لتبتغوا من فضله ۚ إنه كان بكم رحيما'),
(2096, 17, 67, 'وإذا مسكم الضر في البحر ضل من تدعون إلا إياه ۖ فلما نجاكم إلى البر أعرضتم ۚ وكان الإنسان كفورا'),
(2097, 17, 68, 'أفأمنتم أن يخسف بكم جانب البر أو يرسل عليكم حاصبا ثم لا تجدوا لكم وكيلا'),
(2098, 17, 69, 'أم أمنتم أن يعيدكم فيه تارة أخرى فيرسل عليكم قاصفا من الريح فيغرقكم بما كفرتم ۙ ثم لا تجدوا لكم علينا به تبيعا'),
(2099, 17, 70, 'ولقد كرمنا بني آدم وحملناهم في البر والبحر ورزقناهم من الطيبات وفضلناهم على كثير ممن خلقنا تفضيلا'),
(2100, 17, 71, 'يوم ندعو كل أناس بإمامهم ۖ فمن أوتي كتابه بيمينه فأولئك يقرءون كتابهم ولا يظلمون فتيلا'),
(2101, 17, 72, 'ومن كان في هذه أعمى فهو في الآخرة أعمى وأضل سبيلا'),
(2102, 17, 73, 'وإن كادوا ليفتنونك عن الذي أوحينا إليك لتفتري علينا غيره ۖ وإذا لاتخذوك خليلا'),
(2103, 17, 74, 'ولولا أن ثبتناك لقد كدت تركن إليهم شيئا قليلا'),
(2104, 17, 75, 'إذا لأذقناك ضعف الحياة وضعف الممات ثم لا تجد لك علينا نصيرا'),
(2105, 17, 76, 'وإن كادوا ليستفزونك من الأرض ليخرجوك منها ۖ وإذا لا يلبثون خلافك إلا قليلا'),
(2106, 17, 77, 'سنة من قد أرسلنا قبلك من رسلنا ۖ ولا تجد لسنتنا تحويلا'),
(2107, 17, 78, 'أقم الصلاة لدلوك الشمس إلى غسق الليل وقرآن الفجر ۖ إن قرآن الفجر كان مشهودا'),
(2108, 17, 79, 'ومن الليل فتهجد به نافلة لك عسى أن يبعثك ربك مقاما محمودا'),
(2109, 17, 80, 'وقل رب أدخلني مدخل صدق وأخرجني مخرج صدق واجعل لي من لدنك سلطانا نصيرا'),
(2110, 17, 81, 'وقل جاء الحق وزهق الباطل ۚ إن الباطل كان زهوقا'),
(2111, 17, 82, 'وننزل من القرآن ما هو شفاء ورحمة للمؤمنين ۙ ولا يزيد الظالمين إلا خسارا'),
(2112, 17, 83, 'وإذا أنعمنا على الإنسان أعرض ونأى بجانبه ۖ وإذا مسه الشر كان يئوسا'),
(2113, 17, 84, 'قل كل يعمل على شاكلته فربكم أعلم بمن هو أهدى سبيلا'),
(2114, 17, 85, 'ويسألونك عن الروح ۖ قل الروح من أمر ربي وما أوتيتم من العلم إلا قليلا'),
(2115, 17, 86, 'ولئن شئنا لنذهبن بالذي أوحينا إليك ثم لا تجد لك به علينا وكيلا'),
(2116, 17, 87, 'إلا رحمة من ربك ۚ إن فضله كان عليك كبيرا'),
(2117, 17, 88, 'قل لئن اجتمعت الإنس والجن على أن يأتوا بمثل هذا القرآن لا يأتون بمثله ولو كان بعضهم لبعض ظهيرا'),
(2118, 17, 89, 'ولقد صرفنا للناس في هذا القرآن من كل مثل فأبى أكثر الناس إلا كفورا'),
(2119, 17, 90, 'وقالوا لن نؤمن لك حتى تفجر لنا من الأرض ينبوعا'),
(2120, 17, 91, 'أو تكون لك جنة من نخيل وعنب فتفجر الأنهار خلالها تفجيرا'),
(2121, 17, 92, 'أو تسقط السماء كما زعمت علينا كسفا أو تأتي بالله والملائكة قبيلا'),
(2122, 17, 93, 'أو يكون لك بيت من زخرف أو ترقى في السماء ولن نؤمن لرقيك حتى تنزل علينا كتابا نقرؤه ۗ قل سبحان ربي هل كنت إلا بشرا رسولا'),
(2123, 17, 94, 'وما منع الناس أن يؤمنوا إذ جاءهم الهدى إلا أن قالوا أبعث الله بشرا رسولا'),
(2124, 17, 95, 'قل لو كان في الأرض ملائكة يمشون مطمئنين لنزلنا عليهم من السماء ملكا رسولا'),
(2125, 17, 96, 'قل كفى بالله شهيدا بيني وبينكم ۚ إنه كان بعباده خبيرا بصيرا'),
(2126, 17, 97, 'ومن يهد الله فهو المهتد ۖ ومن يضلل فلن تجد لهم أولياء من دونه ۖ ونحشرهم يوم القيامة على وجوههم عميا وبكما وصما ۖ مأواهم جهنم ۖ كلما خبت زدناهم سعيرا'),
(2127, 17, 98, 'ذلك جزاؤهم بأنهم كفروا بآياتنا وقالوا أإذا كنا عظاما ورفاتا أإنا لمبعوثون خلقا جديدا'),
(2128, 17, 99, 'أولم يروا أن الله الذي خلق السماوات والأرض قادر على أن يخلق مثلهم وجعل لهم أجلا لا ريب فيه فأبى الظالمون إلا كفورا'),
(2129, 17, 100, 'قل لو أنتم تملكون خزائن رحمة ربي إذا لأمسكتم خشية الإنفاق ۚ وكان الإنسان قتورا'),
(2130, 17, 101, 'ولقد آتينا موسى تسع آيات بينات ۖ فاسأل بني إسرائيل إذ جاءهم فقال له فرعون إني لأظنك يا موسى مسحورا'),
(2131, 17, 102, 'قال لقد علمت ما أنزل هؤلاء إلا رب السماوات والأرض بصائر وإني لأظنك يا فرعون مثبورا'),
(2132, 17, 103, 'فأراد أن يستفزهم من الأرض فأغرقناه ومن معه جميعا'),
(2133, 17, 104, 'وقلنا من بعده لبني إسرائيل اسكنوا الأرض فإذا جاء وعد الآخرة جئنا بكم لفيفا'),
(2134, 17, 105, 'وبالحق أنزلناه وبالحق نزل ۗ وما أرسلناك إلا مبشرا ونذيرا'),
(2135, 17, 106, 'وقرآنا فرقناه لتقرأه على الناس على مكث ونزلناه تنزيلا'),
(2136, 17, 107, 'قل آمنوا به أو لا تؤمنوا ۚ إن الذين أوتوا العلم من قبله إذا يتلى عليهم يخرون للأذقان سجدا'),
(2137, 17, 108, 'ويقولون سبحان ربنا إن كان وعد ربنا لمفعولا'),
(2138, 17, 109, 'ويخرون للأذقان يبكون ويزيدهم خشوعا'),
(2139, 17, 110, 'قل ادعوا الله أو ادعوا الرحمن ۖ أيا ما تدعوا فله الأسماء الحسنى ۚ ولا تجهر بصلاتك ولا تخافت بها وابتغ بين ذلك سبيلا'),
(2140, 17, 111, 'وقل الحمد لله الذي لم يتخذ ولدا ولم يكن له شريك في الملك ولم يكن له ولي من الذل ۖ وكبره تكبيرا');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'بسم الله الرحمن الرحيم الحمد لله الذي أنزل على عبده الكتاب ولم يجعل له عوجا ۜ'),
(2142, 18, 2, 'قيما لينذر بأسا شديدا من لدنه ويبشر المؤمنين الذين يعملون الصالحات أن لهم أجرا حسنا'),
(2143, 18, 3, 'ماكثين فيه أبدا'),
(2144, 18, 4, 'وينذر الذين قالوا اتخذ الله ولدا'),
(2145, 18, 5, 'ما لهم به من علم ولا لآبائهم ۚ كبرت كلمة تخرج من أفواههم ۚ إن يقولون إلا كذبا'),
(2146, 18, 6, 'فلعلك باخع نفسك على آثارهم إن لم يؤمنوا بهذا الحديث أسفا'),
(2147, 18, 7, 'إنا جعلنا ما على الأرض زينة لها لنبلوهم أيهم أحسن عملا'),
(2148, 18, 8, 'وإنا لجاعلون ما عليها صعيدا جرزا'),
(2149, 18, 9, 'أم حسبت أن أصحاب الكهف والرقيم كانوا من آياتنا عجبا'),
(2150, 18, 10, 'إذ أوى الفتية إلى الكهف فقالوا ربنا آتنا من لدنك رحمة وهيئ لنا من أمرنا رشدا'),
(2151, 18, 11, 'فضربنا على آذانهم في الكهف سنين عددا'),
(2152, 18, 12, 'ثم بعثناهم لنعلم أي الحزبين أحصى لما لبثوا أمدا'),
(2153, 18, 13, 'نحن نقص عليك نبأهم بالحق ۚ إنهم فتية آمنوا بربهم وزدناهم هدى'),
(2154, 18, 14, 'وربطنا على قلوبهم إذ قاموا فقالوا ربنا رب السماوات والأرض لن ندعو من دونه إلها ۖ لقد قلنا إذا شططا'),
(2155, 18, 15, 'هؤلاء قومنا اتخذوا من دونه آلهة ۖ لولا يأتون عليهم بسلطان بين ۖ فمن أظلم ممن افترى على الله كذبا'),
(2156, 18, 16, 'وإذ اعتزلتموهم وما يعبدون إلا الله فأووا إلى الكهف ينشر لكم ربكم من رحمته ويهيئ لكم من أمركم مرفقا'),
(2157, 18, 17, 'وترى الشمس إذا طلعت تزاور عن كهفهم ذات اليمين وإذا غربت تقرضهم ذات الشمال وهم في فجوة منه ۚ ذلك من آيات الله ۗ من يهد الله فهو المهتد ۖ ومن يضلل فلن تجد له وليا مرشدا'),
(2158, 18, 18, 'وتحسبهم أيقاظا وهم رقود ۚ ونقلبهم ذات اليمين وذات الشمال ۖ وكلبهم باسط ذراعيه بالوصيد ۚ لو اطلعت عليهم لوليت منهم فرارا ولملئت منهم رعبا'),
(2159, 18, 19, 'وكذلك بعثناهم ليتساءلوا بينهم ۚ قال قائل منهم كم لبثتم ۖ قالوا لبثنا يوما أو بعض يوم ۚ قالوا ربكم أعلم بما لبثتم فابعثوا أحدكم بورقكم هذه إلى المدينة فلينظر أيها أزكى طعاما فليأتكم برزق منه وليتلطف ولا يشعرن بكم أحدا'),
(2160, 18, 20, 'إنهم إن يظهروا عليكم يرجموكم أو يعيدوكم في ملتهم ولن تفلحوا إذا أبدا'),
(2161, 18, 21, 'وكذلك أعثرنا عليهم ليعلموا أن وعد الله حق وأن الساعة لا ريب فيها إذ يتنازعون بينهم أمرهم ۖ فقالوا ابنوا عليهم بنيانا ۖ ربهم أعلم بهم ۚ قال الذين غلبوا على أمرهم لنتخذن عليهم مسجدا'),
(2162, 18, 22, 'سيقولون ثلاثة رابعهم كلبهم ويقولون خمسة سادسهم كلبهم رجما بالغيب ۖ ويقولون سبعة وثامنهم كلبهم ۚ قل ربي أعلم بعدتهم ما يعلمهم إلا قليل ۗ فلا تمار فيهم إلا مراء ظاهرا ولا تستفت فيهم منهم أحدا'),
(2163, 18, 23, 'ولا تقولن لشيء إني فاعل ذلك غدا'),
(2164, 18, 24, 'إلا أن يشاء الله ۚ واذكر ربك إذا نسيت وقل عسى أن يهدين ربي لأقرب من هذا رشدا'),
(2165, 18, 25, 'ولبثوا في كهفهم ثلاث مائة سنين وازدادوا تسعا'),
(2166, 18, 26, 'قل الله أعلم بما لبثوا ۖ له غيب السماوات والأرض ۖ أبصر به وأسمع ۚ ما لهم من دونه من ولي ولا يشرك في حكمه أحدا'),
(2167, 18, 27, 'واتل ما أوحي إليك من كتاب ربك ۖ لا مبدل لكلماته ولن تجد من دونه ملتحدا'),
(2168, 18, 28, 'واصبر نفسك مع الذين يدعون ربهم بالغداة والعشي يريدون وجهه ۖ ولا تعد عيناك عنهم تريد زينة الحياة الدنيا ۖ ولا تطع من أغفلنا قلبه عن ذكرنا واتبع هواه وكان أمره فرطا'),
(2169, 18, 29, 'وقل الحق من ربكم ۖ فمن شاء فليؤمن ومن شاء فليكفر ۚ إنا أعتدنا للظالمين نارا أحاط بهم سرادقها ۚ وإن يستغيثوا يغاثوا بماء كالمهل يشوي الوجوه ۚ بئس الشراب وساءت مرتفقا'),
(2170, 18, 30, 'إن الذين آمنوا وعملوا الصالحات إنا لا نضيع أجر من أحسن عملا'),
(2171, 18, 31, 'أولئك لهم جنات عدن تجري من تحتهم الأنهار يحلون فيها من أساور من ذهب ويلبسون ثيابا خضرا من سندس وإستبرق متكئين فيها على الأرائك ۚ نعم الثواب وحسنت مرتفقا'),
(2172, 18, 32, 'واضرب لهم مثلا رجلين جعلنا لأحدهما جنتين من أعناب وحففناهما بنخل وجعلنا بينهما زرعا'),
(2173, 18, 33, 'كلتا الجنتين آتت أكلها ولم تظلم منه شيئا ۚ وفجرنا خلالهما نهرا'),
(2174, 18, 34, 'وكان له ثمر فقال لصاحبه وهو يحاوره أنا أكثر منك مالا وأعز نفرا'),
(2175, 18, 35, 'ودخل جنته وهو ظالم لنفسه قال ما أظن أن تبيد هذه أبدا'),
(2176, 18, 36, 'وما أظن الساعة قائمة ولئن رددت إلى ربي لأجدن خيرا منها منقلبا'),
(2177, 18, 37, 'قال له صاحبه وهو يحاوره أكفرت بالذي خلقك من تراب ثم من نطفة ثم سواك رجلا'),
(2178, 18, 38, 'لكنا هو الله ربي ولا أشرك بربي أحدا'),
(2179, 18, 39, 'ولولا إذ دخلت جنتك قلت ما شاء الله لا قوة إلا بالله ۚ إن ترن أنا أقل منك مالا وولدا'),
(2180, 18, 40, 'فعسى ربي أن يؤتين خيرا من جنتك ويرسل عليها حسبانا من السماء فتصبح صعيدا زلقا'),
(2181, 18, 41, 'أو يصبح ماؤها غورا فلن تستطيع له طلبا'),
(2182, 18, 42, 'وأحيط بثمره فأصبح يقلب كفيه على ما أنفق فيها وهي خاوية على عروشها ويقول يا ليتني لم أشرك بربي أحدا'),
(2183, 18, 43, 'ولم تكن له فئة ينصرونه من دون الله وما كان منتصرا'),
(2184, 18, 44, 'هنالك الولاية لله الحق ۚ هو خير ثوابا وخير عقبا'),
(2185, 18, 45, 'واضرب لهم مثل الحياة الدنيا كماء أنزلناه من السماء فاختلط به نبات الأرض فأصبح هشيما تذروه الرياح ۗ وكان الله على كل شيء مقتدرا'),
(2186, 18, 46, 'المال والبنون زينة الحياة الدنيا ۖ والباقيات الصالحات خير عند ربك ثوابا وخير أملا'),
(2187, 18, 47, 'ويوم نسير الجبال وترى الأرض بارزة وحشرناهم فلم نغادر منهم أحدا'),
(2188, 18, 48, 'وعرضوا على ربك صفا لقد جئتمونا كما خلقناكم أول مرة ۚ بل زعمتم ألن نجعل لكم موعدا'),
(2189, 18, 49, 'ووضع الكتاب فترى المجرمين مشفقين مما فيه ويقولون يا ويلتنا مال هذا الكتاب لا يغادر صغيرة ولا كبيرة إلا أحصاها ۚ ووجدوا ما عملوا حاضرا ۗ ولا يظلم ربك أحدا'),
(2190, 18, 50, 'وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس كان من الجن ففسق عن أمر ربه ۗ أفتتخذونه وذريته أولياء من دوني وهم لكم عدو ۚ بئس للظالمين بدلا'),
(2191, 18, 51, 'ما أشهدتهم خلق السماوات والأرض ولا خلق أنفسهم وما كنت متخذ المضلين عضدا'),
(2192, 18, 52, 'ويوم يقول نادوا شركائي الذين زعمتم فدعوهم فلم يستجيبوا لهم وجعلنا بينهم موبقا'),
(2193, 18, 53, 'ورأى المجرمون النار فظنوا أنهم مواقعوها ولم يجدوا عنها مصرفا'),
(2194, 18, 54, 'ولقد صرفنا في هذا القرآن للناس من كل مثل ۚ وكان الإنسان أكثر شيء جدلا'),
(2195, 18, 55, 'وما منع الناس أن يؤمنوا إذ جاءهم الهدى ويستغفروا ربهم إلا أن تأتيهم سنة الأولين أو يأتيهم العذاب قبلا'),
(2196, 18, 56, 'وما نرسل المرسلين إلا مبشرين ومنذرين ۚ ويجادل الذين كفروا بالباطل ليدحضوا به الحق ۖ واتخذوا آياتي وما أنذروا هزوا'),
(2197, 18, 57, 'ومن أظلم ممن ذكر بآيات ربه فأعرض عنها ونسي ما قدمت يداه ۚ إنا جعلنا على قلوبهم أكنة أن يفقهوه وفي آذانهم وقرا ۖ وإن تدعهم إلى الهدى فلن يهتدوا إذا أبدا'),
(2198, 18, 58, 'وربك الغفور ذو الرحمة ۖ لو يؤاخذهم بما كسبوا لعجل لهم العذاب ۚ بل لهم موعد لن يجدوا من دونه موئلا'),
(2199, 18, 59, 'وتلك القرى أهلكناهم لما ظلموا وجعلنا لمهلكهم موعدا'),
(2200, 18, 60, 'وإذ قال موسى لفتاه لا أبرح حتى أبلغ مجمع البحرين أو أمضي حقبا'),
(2201, 18, 61, 'فلما بلغا مجمع بينهما نسيا حوتهما فاتخذ سبيله في البحر سربا'),
(2202, 18, 62, 'فلما جاوزا قال لفتاه آتنا غداءنا لقد لقينا من سفرنا هذا نصبا'),
(2203, 18, 63, 'قال أرأيت إذ أوينا إلى الصخرة فإني نسيت الحوت وما أنسانيه إلا الشيطان أن أذكره ۚ واتخذ سبيله في البحر عجبا'),
(2204, 18, 64, 'قال ذلك ما كنا نبغ ۚ فارتدا على آثارهما قصصا'),
(2205, 18, 65, 'فوجدا عبدا من عبادنا آتيناه رحمة من عندنا وعلمناه من لدنا علما'),
(2206, 18, 66, 'قال له موسى هل أتبعك على أن تعلمن مما علمت رشدا'),
(2207, 18, 67, 'قال إنك لن تستطيع معي صبرا'),
(2208, 18, 68, 'وكيف تصبر على ما لم تحط به خبرا'),
(2209, 18, 69, 'قال ستجدني إن شاء الله صابرا ولا أعصي لك أمرا'),
(2210, 18, 70, 'قال فإن اتبعتني فلا تسألني عن شيء حتى أحدث لك منه ذكرا'),
(2211, 18, 71, 'فانطلقا حتى إذا ركبا في السفينة خرقها ۖ قال أخرقتها لتغرق أهلها لقد جئت شيئا إمرا'),
(2212, 18, 72, 'قال ألم أقل إنك لن تستطيع معي صبرا'),
(2213, 18, 73, 'قال لا تؤاخذني بما نسيت ولا ترهقني من أمري عسرا'),
(2214, 18, 74, 'فانطلقا حتى إذا لقيا غلاما فقتله قال أقتلت نفسا زكية بغير نفس لقد جئت شيئا نكرا'),
(2215, 18, 75, 'قال ألم أقل لك إنك لن تستطيع معي صبرا'),
(2216, 18, 76, 'قال إن سألتك عن شيء بعدها فلا تصاحبني ۖ قد بلغت من لدني عذرا'),
(2217, 18, 77, 'فانطلقا حتى إذا أتيا أهل قرية استطعما أهلها فأبوا أن يضيفوهما فوجدا فيها جدارا يريد أن ينقض فأقامه ۖ قال لو شئت لاتخذت عليه أجرا'),
(2218, 18, 78, 'قال هذا فراق بيني وبينك ۚ سأنبئك بتأويل ما لم تستطع عليه صبرا'),
(2219, 18, 79, 'أما السفينة فكانت لمساكين يعملون في البحر فأردت أن أعيبها وكان وراءهم ملك يأخذ كل سفينة غصبا'),
(2220, 18, 80, 'وأما الغلام فكان أبواه مؤمنين فخشينا أن يرهقهما طغيانا وكفرا'),
(2221, 18, 81, 'فأردنا أن يبدلهما ربهما خيرا منه زكاة وأقرب رحما'),
(2222, 18, 82, 'وأما الجدار فكان لغلامين يتيمين في المدينة وكان تحته كنز لهما وكان أبوهما صالحا فأراد ربك أن يبلغا أشدهما ويستخرجا كنزهما رحمة من ربك ۚ وما فعلته عن أمري ۚ ذلك تأويل ما لم تسطع عليه صبرا'),
(2223, 18, 83, 'ويسألونك عن ذي القرنين ۖ قل سأتلو عليكم منه ذكرا'),
(2224, 18, 84, 'إنا مكنا له في الأرض وآتيناه من كل شيء سببا'),
(2225, 18, 85, 'فأتبع سببا'),
(2226, 18, 86, 'حتى إذا بلغ مغرب الشمس وجدها تغرب في عين حمئة ووجد عندها قوما ۗ قلنا يا ذا القرنين إما أن تعذب وإما أن تتخذ فيهم حسنا'),
(2227, 18, 87, 'قال أما من ظلم فسوف نعذبه ثم يرد إلى ربه فيعذبه عذابا نكرا'),
(2228, 18, 88, 'وأما من آمن وعمل صالحا فله جزاء الحسنى ۖ وسنقول له من أمرنا يسرا'),
(2229, 18, 89, 'ثم أتبع سببا'),
(2230, 18, 90, 'حتى إذا بلغ مطلع الشمس وجدها تطلع على قوم لم نجعل لهم من دونها سترا'),
(2231, 18, 91, 'كذلك وقد أحطنا بما لديه خبرا'),
(2232, 18, 92, 'ثم أتبع سببا'),
(2233, 18, 93, 'حتى إذا بلغ بين السدين وجد من دونهما قوما لا يكادون يفقهون قولا'),
(2234, 18, 94, 'قالوا يا ذا القرنين إن يأجوج ومأجوج مفسدون في الأرض فهل نجعل لك خرجا على أن تجعل بيننا وبينهم سدا'),
(2235, 18, 95, 'قال ما مكني فيه ربي خير فأعينوني بقوة أجعل بينكم وبينهم ردما'),
(2236, 18, 96, 'آتوني زبر الحديد ۖ حتى إذا ساوى بين الصدفين قال انفخوا ۖ حتى إذا جعله نارا قال آتوني أفرغ عليه قطرا'),
(2237, 18, 97, 'فما اسطاعوا أن يظهروه وما استطاعوا له نقبا'),
(2238, 18, 98, 'قال هذا رحمة من ربي ۖ فإذا جاء وعد ربي جعله دكاء ۖ وكان وعد ربي حقا'),
(2239, 18, 99, 'وتركنا بعضهم يومئذ يموج في بعض ۖ ونفخ في الصور فجمعناهم جمعا'),
(2240, 18, 100, 'وعرضنا جهنم يومئذ للكافرين عرضا'),
(2241, 18, 101, 'الذين كانت أعينهم في غطاء عن ذكري وكانوا لا يستطيعون سمعا'),
(2242, 18, 102, 'أفحسب الذين كفروا أن يتخذوا عبادي من دوني أولياء ۚ إنا أعتدنا جهنم للكافرين نزلا'),
(2243, 18, 103, 'قل هل ننبئكم بالأخسرين أعمالا'),
(2244, 18, 104, 'الذين ضل سعيهم في الحياة الدنيا وهم يحسبون أنهم يحسنون صنعا'),
(2245, 18, 105, 'أولئك الذين كفروا بآيات ربهم ولقائه فحبطت أعمالهم فلا نقيم لهم يوم القيامة وزنا'),
(2246, 18, 106, 'ذلك جزاؤهم جهنم بما كفروا واتخذوا آياتي ورسلي هزوا'),
(2247, 18, 107, 'إن الذين آمنوا وعملوا الصالحات كانت لهم جنات الفردوس نزلا'),
(2248, 18, 108, 'خالدين فيها لا يبغون عنها حولا'),
(2249, 18, 109, 'قل لو كان البحر مدادا لكلمات ربي لنفد البحر قبل أن تنفد كلمات ربي ولو جئنا بمثله مددا'),
(2250, 18, 110, 'قل إنما أنا بشر مثلكم يوحى إلي أنما إلهكم إله واحد ۖ فمن كان يرجو لقاء ربه فليعمل عملا صالحا ولا يشرك بعبادة ربه أحدا');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'بسم الله الرحمن الرحيم كهيعص'),
(2252, 19, 2, 'ذكر رحمت ربك عبده زكريا'),
(2253, 19, 3, 'إذ نادى ربه نداء خفيا'),
(2254, 19, 4, 'قال رب إني وهن العظم مني واشتعل الرأس شيبا ولم أكن بدعائك رب شقيا'),
(2255, 19, 5, 'وإني خفت الموالي من ورائي وكانت امرأتي عاقرا فهب لي من لدنك وليا'),
(2256, 19, 6, 'يرثني ويرث من آل يعقوب ۖ واجعله رب رضيا'),
(2257, 19, 7, 'يا زكريا إنا نبشرك بغلام اسمه يحيى لم نجعل له من قبل سميا'),
(2258, 19, 8, 'قال رب أنى يكون لي غلام وكانت امرأتي عاقرا وقد بلغت من الكبر عتيا'),
(2259, 19, 9, 'قال كذلك قال ربك هو علي هين وقد خلقتك من قبل ولم تك شيئا'),
(2260, 19, 10, 'قال رب اجعل لي آية ۚ قال آيتك ألا تكلم الناس ثلاث ليال سويا'),
(2261, 19, 11, 'فخرج على قومه من المحراب فأوحى إليهم أن سبحوا بكرة وعشيا'),
(2262, 19, 12, 'يا يحيى خذ الكتاب بقوة ۖ وآتيناه الحكم صبيا'),
(2263, 19, 13, 'وحنانا من لدنا وزكاة ۖ وكان تقيا'),
(2264, 19, 14, 'وبرا بوالديه ولم يكن جبارا عصيا'),
(2265, 19, 15, 'وسلام عليه يوم ولد ويوم يموت ويوم يبعث حيا'),
(2266, 19, 16, 'واذكر في الكتاب مريم إذ انتبذت من أهلها مكانا شرقيا'),
(2267, 19, 17, 'فاتخذت من دونهم حجابا فأرسلنا إليها روحنا فتمثل لها بشرا سويا'),
(2268, 19, 18, 'قالت إني أعوذ بالرحمن منك إن كنت تقيا'),
(2269, 19, 19, 'قال إنما أنا رسول ربك لأهب لك غلاما زكيا'),
(2270, 19, 20, 'قالت أنى يكون لي غلام ولم يمسسني بشر ولم أك بغيا'),
(2271, 19, 21, 'قال كذلك قال ربك هو علي هين ۖ ولنجعله آية للناس ورحمة منا ۚ وكان أمرا مقضيا'),
(2272, 19, 22, 'فحملته فانتبذت به مكانا قصيا'),
(2273, 19, 23, 'فأجاءها المخاض إلى جذع النخلة قالت يا ليتني مت قبل هذا وكنت نسيا منسيا'),
(2274, 19, 24, 'فناداها من تحتها ألا تحزني قد جعل ربك تحتك سريا'),
(2275, 19, 25, 'وهزي إليك بجذع النخلة تساقط عليك رطبا جنيا'),
(2276, 19, 26, 'فكلي واشربي وقري عينا ۖ فإما ترين من البشر أحدا فقولي إني نذرت للرحمن صوما فلن أكلم اليوم إنسيا'),
(2277, 19, 27, 'فأتت به قومها تحمله ۖ قالوا يا مريم لقد جئت شيئا فريا'),
(2278, 19, 28, 'يا أخت هارون ما كان أبوك امرأ سوء وما كانت أمك بغيا'),
(2279, 19, 29, 'فأشارت إليه ۖ قالوا كيف نكلم من كان في المهد صبيا'),
(2280, 19, 30, 'قال إني عبد الله آتاني الكتاب وجعلني نبيا'),
(2281, 19, 31, 'وجعلني مباركا أين ما كنت وأوصاني بالصلاة والزكاة ما دمت حيا'),
(2282, 19, 32, 'وبرا بوالدتي ولم يجعلني جبارا شقيا'),
(2283, 19, 33, 'والسلام علي يوم ولدت ويوم أموت ويوم أبعث حيا'),
(2284, 19, 34, 'ذلك عيسى ابن مريم ۚ قول الحق الذي فيه يمترون'),
(2285, 19, 35, 'ما كان لله أن يتخذ من ولد ۖ سبحانه ۚ إذا قضى أمرا فإنما يقول له كن فيكون'),
(2286, 19, 36, 'وإن الله ربي وربكم فاعبدوه ۚ هذا صراط مستقيم'),
(2287, 19, 37, 'فاختلف الأحزاب من بينهم ۖ فويل للذين كفروا من مشهد يوم عظيم'),
(2288, 19, 38, 'أسمع بهم وأبصر يوم يأتوننا ۖ لكن الظالمون اليوم في ضلال مبين'),
(2289, 19, 39, 'وأنذرهم يوم الحسرة إذ قضي الأمر وهم في غفلة وهم لا يؤمنون'),
(2290, 19, 40, 'إنا نحن نرث الأرض ومن عليها وإلينا يرجعون'),
(2291, 19, 41, 'واذكر في الكتاب إبراهيم ۚ إنه كان صديقا نبيا'),
(2292, 19, 42, 'إذ قال لأبيه يا أبت لم تعبد ما لا يسمع ولا يبصر ولا يغني عنك شيئا'),
(2293, 19, 43, 'يا أبت إني قد جاءني من العلم ما لم يأتك فاتبعني أهدك صراطا سويا'),
(2294, 19, 44, 'يا أبت لا تعبد الشيطان ۖ إن الشيطان كان للرحمن عصيا'),
(2295, 19, 45, 'يا أبت إني أخاف أن يمسك عذاب من الرحمن فتكون للشيطان وليا'),
(2296, 19, 46, 'قال أراغب أنت عن آلهتي يا إبراهيم ۖ لئن لم تنته لأرجمنك ۖ واهجرني مليا'),
(2297, 19, 47, 'قال سلام عليك ۖ سأستغفر لك ربي ۖ إنه كان بي حفيا'),
(2298, 19, 48, 'وأعتزلكم وما تدعون من دون الله وأدعو ربي عسى ألا أكون بدعاء ربي شقيا'),
(2299, 19, 49, 'فلما اعتزلهم وما يعبدون من دون الله وهبنا له إسحاق ويعقوب ۖ وكلا جعلنا نبيا'),
(2300, 19, 50, 'ووهبنا لهم من رحمتنا وجعلنا لهم لسان صدق عليا'),
(2301, 19, 51, 'واذكر في الكتاب موسى ۚ إنه كان مخلصا وكان رسولا نبيا'),
(2302, 19, 52, 'وناديناه من جانب الطور الأيمن وقربناه نجيا'),
(2303, 19, 53, 'ووهبنا له من رحمتنا أخاه هارون نبيا'),
(2304, 19, 54, 'واذكر في الكتاب إسماعيل ۚ إنه كان صادق الوعد وكان رسولا نبيا'),
(2305, 19, 55, 'وكان يأمر أهله بالصلاة والزكاة وكان عند ربه مرضيا'),
(2306, 19, 56, 'واذكر في الكتاب إدريس ۚ إنه كان صديقا نبيا'),
(2307, 19, 57, 'ورفعناه مكانا عليا'),
(2308, 19, 58, 'أولئك الذين أنعم الله عليهم من النبيين من ذرية آدم وممن حملنا مع نوح ومن ذرية إبراهيم وإسرائيل وممن هدينا واجتبينا ۚ إذا تتلى عليهم آيات الرحمن خروا سجدا وبكيا'),
(2309, 19, 59, 'فخلف من بعدهم خلف أضاعوا الصلاة واتبعوا الشهوات ۖ فسوف يلقون غيا'),
(2310, 19, 60, 'إلا من تاب وآمن وعمل صالحا فأولئك يدخلون الجنة ولا يظلمون شيئا'),
(2311, 19, 61, 'جنات عدن التي وعد الرحمن عباده بالغيب ۚ إنه كان وعده مأتيا'),
(2312, 19, 62, 'لا يسمعون فيها لغوا إلا سلاما ۖ ولهم رزقهم فيها بكرة وعشيا'),
(2313, 19, 63, 'تلك الجنة التي نورث من عبادنا من كان تقيا'),
(2314, 19, 64, 'وما نتنزل إلا بأمر ربك ۖ له ما بين أيدينا وما خلفنا وما بين ذلك ۚ وما كان ربك نسيا'),
(2315, 19, 65, 'رب السماوات والأرض وما بينهما فاعبده واصطبر لعبادته ۚ هل تعلم له سميا'),
(2316, 19, 66, 'ويقول الإنسان أإذا ما مت لسوف أخرج حيا'),
(2317, 19, 67, 'أولا يذكر الإنسان أنا خلقناه من قبل ولم يك شيئا'),
(2318, 19, 68, 'فوربك لنحشرنهم والشياطين ثم لنحضرنهم حول جهنم جثيا'),
(2319, 19, 69, 'ثم لننزعن من كل شيعة أيهم أشد على الرحمن عتيا'),
(2320, 19, 70, 'ثم لنحن أعلم بالذين هم أولى بها صليا'),
(2321, 19, 71, 'وإن منكم إلا واردها ۚ كان على ربك حتما مقضيا'),
(2322, 19, 72, 'ثم ننجي الذين اتقوا ونذر الظالمين فيها جثيا'),
(2323, 19, 73, 'وإذا تتلى عليهم آياتنا بينات قال الذين كفروا للذين آمنوا أي الفريقين خير مقاما وأحسن نديا'),
(2324, 19, 74, 'وكم أهلكنا قبلهم من قرن هم أحسن أثاثا ورئيا'),
(2325, 19, 75, 'قل من كان في الضلالة فليمدد له الرحمن مدا ۚ حتى إذا رأوا ما يوعدون إما العذاب وإما الساعة فسيعلمون من هو شر مكانا وأضعف جندا'),
(2326, 19, 76, 'ويزيد الله الذين اهتدوا هدى ۗ والباقيات الصالحات خير عند ربك ثوابا وخير مردا'),
(2327, 19, 77, 'أفرأيت الذي كفر بآياتنا وقال لأوتين مالا وولدا'),
(2328, 19, 78, 'أطلع الغيب أم اتخذ عند الرحمن عهدا'),
(2329, 19, 79, 'كلا ۚ سنكتب ما يقول ونمد له من العذاب مدا'),
(2330, 19, 80, 'ونرثه ما يقول ويأتينا فردا'),
(2331, 19, 81, 'واتخذوا من دون الله آلهة ليكونوا لهم عزا'),
(2332, 19, 82, 'كلا ۚ سيكفرون بعبادتهم ويكونون عليهم ضدا'),
(2333, 19, 83, 'ألم تر أنا أرسلنا الشياطين على الكافرين تؤزهم أزا'),
(2334, 19, 84, 'فلا تعجل عليهم ۖ إنما نعد لهم عدا'),
(2335, 19, 85, 'يوم نحشر المتقين إلى الرحمن وفدا'),
(2336, 19, 86, 'ونسوق المجرمين إلى جهنم وردا'),
(2337, 19, 87, 'لا يملكون الشفاعة إلا من اتخذ عند الرحمن عهدا'),
(2338, 19, 88, 'وقالوا اتخذ الرحمن ولدا'),
(2339, 19, 89, 'لقد جئتم شيئا إدا'),
(2340, 19, 90, 'تكاد السماوات يتفطرن منه وتنشق الأرض وتخر الجبال هدا'),
(2341, 19, 91, 'أن دعوا للرحمن ولدا'),
(2342, 19, 92, 'وما ينبغي للرحمن أن يتخذ ولدا'),
(2343, 19, 93, 'إن كل من في السماوات والأرض إلا آتي الرحمن عبدا'),
(2344, 19, 94, 'لقد أحصاهم وعدهم عدا'),
(2345, 19, 95, 'وكلهم آتيه يوم القيامة فردا'),
(2346, 19, 96, 'إن الذين آمنوا وعملوا الصالحات سيجعل لهم الرحمن ودا'),
(2347, 19, 97, 'فإنما يسرناه بلسانك لتبشر به المتقين وتنذر به قوما لدا'),
(2348, 19, 98, 'وكم أهلكنا قبلهم من قرن هل تحس منهم من أحد أو تسمع لهم ركزا');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'بسم الله الرحمن الرحيم طه'),
(2350, 20, 2, 'ما أنزلنا عليك القرآن لتشقى'),
(2351, 20, 3, 'إلا تذكرة لمن يخشى'),
(2352, 20, 4, 'تنزيلا ممن خلق الأرض والسماوات العلى'),
(2353, 20, 5, 'الرحمن على العرش استوى'),
(2354, 20, 6, 'له ما في السماوات وما في الأرض وما بينهما وما تحت الثرى'),
(2355, 20, 7, 'وإن تجهر بالقول فإنه يعلم السر وأخفى'),
(2356, 20, 8, 'الله لا إله إلا هو ۖ له الأسماء الحسنى'),
(2357, 20, 9, 'وهل أتاك حديث موسى'),
(2358, 20, 10, 'إذ رأى نارا فقال لأهله امكثوا إني آنست نارا لعلي آتيكم منها بقبس أو أجد على النار هدى'),
(2359, 20, 11, 'فلما أتاها نودي يا موسى'),
(2360, 20, 12, 'إني أنا ربك فاخلع نعليك ۖ إنك بالواد المقدس طوى'),
(2361, 20, 13, 'وأنا اخترتك فاستمع لما يوحى'),
(2362, 20, 14, 'إنني أنا الله لا إله إلا أنا فاعبدني وأقم الصلاة لذكري'),
(2363, 20, 15, 'إن الساعة آتية أكاد أخفيها لتجزى كل نفس بما تسعى'),
(2364, 20, 16, 'فلا يصدنك عنها من لا يؤمن بها واتبع هواه فتردى'),
(2365, 20, 17, 'وما تلك بيمينك يا موسى'),
(2366, 20, 18, 'قال هي عصاي أتوكأ عليها وأهش بها على غنمي ولي فيها مآرب أخرى'),
(2367, 20, 19, 'قال ألقها يا موسى'),
(2368, 20, 20, 'فألقاها فإذا هي حية تسعى'),
(2369, 20, 21, 'قال خذها ولا تخف ۖ سنعيدها سيرتها الأولى'),
(2370, 20, 22, 'واضمم يدك إلى جناحك تخرج بيضاء من غير سوء آية أخرى'),
(2371, 20, 23, 'لنريك من آياتنا الكبرى'),
(2372, 20, 24, 'اذهب إلى فرعون إنه طغى'),
(2373, 20, 25, 'قال رب اشرح لي صدري'),
(2374, 20, 26, 'ويسر لي أمري'),
(2375, 20, 27, 'واحلل عقدة من لساني'),
(2376, 20, 28, 'يفقهوا قولي'),
(2377, 20, 29, 'واجعل لي وزيرا من أهلي'),
(2378, 20, 30, 'هارون أخي'),
(2379, 20, 31, 'اشدد به أزري'),
(2380, 20, 32, 'وأشركه في أمري'),
(2381, 20, 33, 'كي نسبحك كثيرا'),
(2382, 20, 34, 'ونذكرك كثيرا'),
(2383, 20, 35, 'إنك كنت بنا بصيرا'),
(2384, 20, 36, 'قال قد أوتيت سؤلك يا موسى'),
(2385, 20, 37, 'ولقد مننا عليك مرة أخرى'),
(2386, 20, 38, 'إذ أوحينا إلى أمك ما يوحى'),
(2387, 20, 39, 'أن اقذفيه في التابوت فاقذفيه في اليم فليلقه اليم بالساحل يأخذه عدو لي وعدو له ۚ وألقيت عليك محبة مني ولتصنع على عيني'),
(2388, 20, 40, 'إذ تمشي أختك فتقول هل أدلكم على من يكفله ۖ فرجعناك إلى أمك كي تقر عينها ولا تحزن ۚ وقتلت نفسا فنجيناك من الغم وفتناك فتونا ۚ فلبثت سنين في أهل مدين ثم جئت على قدر يا موسى'),
(2389, 20, 41, 'واصطنعتك لنفسي'),
(2390, 20, 42, 'اذهب أنت وأخوك بآياتي ولا تنيا في ذكري'),
(2391, 20, 43, 'اذهبا إلى فرعون إنه طغى'),
(2392, 20, 44, 'فقولا له قولا لينا لعله يتذكر أو يخشى'),
(2393, 20, 45, 'قالا ربنا إننا نخاف أن يفرط علينا أو أن يطغى'),
(2394, 20, 46, 'قال لا تخافا ۖ إنني معكما أسمع وأرى'),
(2395, 20, 47, 'فأتياه فقولا إنا رسولا ربك فأرسل معنا بني إسرائيل ولا تعذبهم ۖ قد جئناك بآية من ربك ۖ والسلام على من اتبع الهدى'),
(2396, 20, 48, 'إنا قد أوحي إلينا أن العذاب على من كذب وتولى'),
(2397, 20, 49, 'قال فمن ربكما يا موسى'),
(2398, 20, 50, 'قال ربنا الذي أعطى كل شيء خلقه ثم هدى'),
(2399, 20, 51, 'قال فما بال القرون الأولى'),
(2400, 20, 52, 'قال علمها عند ربي في كتاب ۖ لا يضل ربي ولا ينسى'),
(2401, 20, 53, 'الذي جعل لكم الأرض مهدا وسلك لكم فيها سبلا وأنزل من السماء ماء فأخرجنا به أزواجا من نبات شتى'),
(2402, 20, 54, 'كلوا وارعوا أنعامكم ۗ إن في ذلك لآيات لأولي النهى'),
(2403, 20, 55, 'منها خلقناكم وفيها نعيدكم ومنها نخرجكم تارة أخرى'),
(2404, 20, 56, 'ولقد أريناه آياتنا كلها فكذب وأبى'),
(2405, 20, 57, 'قال أجئتنا لتخرجنا من أرضنا بسحرك يا موسى'),
(2406, 20, 58, 'فلنأتينك بسحر مثله فاجعل بيننا وبينك موعدا لا نخلفه نحن ولا أنت مكانا سوى'),
(2407, 20, 59, 'قال موعدكم يوم الزينة وأن يحشر الناس ضحى'),
(2408, 20, 60, 'فتولى فرعون فجمع كيده ثم أتى'),
(2409, 20, 61, 'قال لهم موسى ويلكم لا تفتروا على الله كذبا فيسحتكم بعذاب ۖ وقد خاب من افترى'),
(2410, 20, 62, 'فتنازعوا أمرهم بينهم وأسروا النجوى'),
(2411, 20, 63, 'قالوا إن هذان لساحران يريدان أن يخرجاكم من أرضكم بسحرهما ويذهبا بطريقتكم المثلى'),
(2412, 20, 64, 'فأجمعوا كيدكم ثم ائتوا صفا ۚ وقد أفلح اليوم من استعلى'),
(2413, 20, 65, 'قالوا يا موسى إما أن تلقي وإما أن نكون أول من ألقى'),
(2414, 20, 66, 'قال بل ألقوا ۖ فإذا حبالهم وعصيهم يخيل إليه من سحرهم أنها تسعى'),
(2415, 20, 67, 'فأوجس في نفسه خيفة موسى'),
(2416, 20, 68, 'قلنا لا تخف إنك أنت الأعلى'),
(2417, 20, 69, 'وألق ما في يمينك تلقف ما صنعوا ۖ إنما صنعوا كيد ساحر ۖ ولا يفلح الساحر حيث أتى'),
(2418, 20, 70, 'فألقي السحرة سجدا قالوا آمنا برب هارون وموسى'),
(2419, 20, 71, 'قال آمنتم له قبل أن آذن لكم ۖ إنه لكبيركم الذي علمكم السحر ۖ فلأقطعن أيديكم وأرجلكم من خلاف ولأصلبنكم في جذوع النخل ولتعلمن أينا أشد عذابا وأبقى'),
(2420, 20, 72, 'قالوا لن نؤثرك على ما جاءنا من البينات والذي فطرنا ۖ فاقض ما أنت قاض ۖ إنما تقضي هذه الحياة الدنيا'),
(2421, 20, 73, 'إنا آمنا بربنا ليغفر لنا خطايانا وما أكرهتنا عليه من السحر ۗ والله خير وأبقى'),
(2422, 20, 74, 'إنه من يأت ربه مجرما فإن له جهنم لا يموت فيها ولا يحيى'),
(2423, 20, 75, 'ومن يأته مؤمنا قد عمل الصالحات فأولئك لهم الدرجات العلى'),
(2424, 20, 76, 'جنات عدن تجري من تحتها الأنهار خالدين فيها ۚ وذلك جزاء من تزكى'),
(2425, 20, 77, 'ولقد أوحينا إلى موسى أن أسر بعبادي فاضرب لهم طريقا في البحر يبسا لا تخاف دركا ولا تخشى'),
(2426, 20, 78, 'فأتبعهم فرعون بجنوده فغشيهم من اليم ما غشيهم'),
(2427, 20, 79, 'وأضل فرعون قومه وما هدى'),
(2428, 20, 80, 'يا بني إسرائيل قد أنجيناكم من عدوكم وواعدناكم جانب الطور الأيمن ونزلنا عليكم المن والسلوى'),
(2429, 20, 81, 'كلوا من طيبات ما رزقناكم ولا تطغوا فيه فيحل عليكم غضبي ۖ ومن يحلل عليه غضبي فقد هوى'),
(2430, 20, 82, 'وإني لغفار لمن تاب وآمن وعمل صالحا ثم اهتدى'),
(2431, 20, 83, 'وما أعجلك عن قومك يا موسى'),
(2432, 20, 84, 'قال هم أولاء على أثري وعجلت إليك رب لترضى'),
(2433, 20, 85, 'قال فإنا قد فتنا قومك من بعدك وأضلهم السامري'),
(2434, 20, 86, 'فرجع موسى إلى قومه غضبان أسفا ۚ قال يا قوم ألم يعدكم ربكم وعدا حسنا ۚ أفطال عليكم العهد أم أردتم أن يحل عليكم غضب من ربكم فأخلفتم موعدي'),
(2435, 20, 87, 'قالوا ما أخلفنا موعدك بملكنا ولكنا حملنا أوزارا من زينة القوم فقذفناها فكذلك ألقى السامري'),
(2436, 20, 88, 'فأخرج لهم عجلا جسدا له خوار فقالوا هذا إلهكم وإله موسى فنسي'),
(2437, 20, 89, 'أفلا يرون ألا يرجع إليهم قولا ولا يملك لهم ضرا ولا نفعا'),
(2438, 20, 90, 'ولقد قال لهم هارون من قبل يا قوم إنما فتنتم به ۖ وإن ربكم الرحمن فاتبعوني وأطيعوا أمري'),
(2439, 20, 91, 'قالوا لن نبرح عليه عاكفين حتى يرجع إلينا موسى'),
(2440, 20, 92, 'قال يا هارون ما منعك إذ رأيتهم ضلوا'),
(2441, 20, 93, 'ألا تتبعن ۖ أفعصيت أمري'),
(2442, 20, 94, 'قال يا ابن أم لا تأخذ بلحيتي ولا برأسي ۖ إني خشيت أن تقول فرقت بين بني إسرائيل ولم ترقب قولي'),
(2443, 20, 95, 'قال فما خطبك يا سامري'),
(2444, 20, 96, 'قال بصرت بما لم يبصروا به فقبضت قبضة من أثر الرسول فنبذتها وكذلك سولت لي نفسي'),
(2445, 20, 97, 'قال فاذهب فإن لك في الحياة أن تقول لا مساس ۖ وإن لك موعدا لن تخلفه ۖ وانظر إلى إلهك الذي ظلت عليه عاكفا ۖ لنحرقنه ثم لننسفنه في اليم نسفا'),
(2446, 20, 98, 'إنما إلهكم الله الذي لا إله إلا هو ۚ وسع كل شيء علما'),
(2447, 20, 99, 'كذلك نقص عليك من أنباء ما قد سبق ۚ وقد آتيناك من لدنا ذكرا'),
(2448, 20, 100, 'من أعرض عنه فإنه يحمل يوم القيامة وزرا'),
(2449, 20, 101, 'خالدين فيه ۖ وساء لهم يوم القيامة حملا'),
(2450, 20, 102, 'يوم ينفخ في الصور ۚ ونحشر المجرمين يومئذ زرقا'),
(2451, 20, 103, 'يتخافتون بينهم إن لبثتم إلا عشرا'),
(2452, 20, 104, 'نحن أعلم بما يقولون إذ يقول أمثلهم طريقة إن لبثتم إلا يوما'),
(2453, 20, 105, 'ويسألونك عن الجبال فقل ينسفها ربي نسفا'),
(2454, 20, 106, 'فيذرها قاعا صفصفا'),
(2455, 20, 107, 'لا ترى فيها عوجا ولا أمتا'),
(2456, 20, 108, 'يومئذ يتبعون الداعي لا عوج له ۖ وخشعت الأصوات للرحمن فلا تسمع إلا همسا'),
(2457, 20, 109, 'يومئذ لا تنفع الشفاعة إلا من أذن له الرحمن ورضي له قولا'),
(2458, 20, 110, 'يعلم ما بين أيديهم وما خلفهم ولا يحيطون به علما'),
(2459, 20, 111, 'وعنت الوجوه للحي القيوم ۖ وقد خاب من حمل ظلما'),
(2460, 20, 112, 'ومن يعمل من الصالحات وهو مؤمن فلا يخاف ظلما ولا هضما'),
(2461, 20, 113, 'وكذلك أنزلناه قرآنا عربيا وصرفنا فيه من الوعيد لعلهم يتقون أو يحدث لهم ذكرا'),
(2462, 20, 114, 'فتعالى الله الملك الحق ۗ ولا تعجل بالقرآن من قبل أن يقضى إليك وحيه ۖ وقل رب زدني علما'),
(2463, 20, 115, 'ولقد عهدنا إلى آدم من قبل فنسي ولم نجد له عزما'),
(2464, 20, 116, 'وإذ قلنا للملائكة اسجدوا لآدم فسجدوا إلا إبليس أبى'),
(2465, 20, 117, 'فقلنا يا آدم إن هذا عدو لك ولزوجك فلا يخرجنكما من الجنة فتشقى'),
(2466, 20, 118, 'إن لك ألا تجوع فيها ولا تعرى'),
(2467, 20, 119, 'وأنك لا تظمأ فيها ولا تضحى'),
(2468, 20, 120, 'فوسوس إليه الشيطان قال يا آدم هل أدلك على شجرة الخلد وملك لا يبلى'),
(2469, 20, 121, 'فأكلا منها فبدت لهما سوآتهما وطفقا يخصفان عليهما من ورق الجنة ۚ وعصى آدم ربه فغوى'),
(2470, 20, 122, 'ثم اجتباه ربه فتاب عليه وهدى'),
(2471, 20, 123, 'قال اهبطا منها جميعا ۖ بعضكم لبعض عدو ۖ فإما يأتينكم مني هدى فمن اتبع هداي فلا يضل ولا يشقى'),
(2472, 20, 124, 'ومن أعرض عن ذكري فإن له معيشة ضنكا ونحشره يوم القيامة أعمى'),
(2473, 20, 125, 'قال رب لم حشرتني أعمى وقد كنت بصيرا'),
(2474, 20, 126, 'قال كذلك أتتك آياتنا فنسيتها ۖ وكذلك اليوم تنسى'),
(2475, 20, 127, 'وكذلك نجزي من أسرف ولم يؤمن بآيات ربه ۚ ولعذاب الآخرة أشد وأبقى'),
(2476, 20, 128, 'أفلم يهد لهم كم أهلكنا قبلهم من القرون يمشون في مساكنهم ۗ إن في ذلك لآيات لأولي النهى'),
(2477, 20, 129, 'ولولا كلمة سبقت من ربك لكان لزاما وأجل مسمى'),
(2478, 20, 130, 'فاصبر على ما يقولون وسبح بحمد ربك قبل طلوع الشمس وقبل غروبها ۖ ومن آناء الليل فسبح وأطراف النهار لعلك ترضى'),
(2479, 20, 131, 'ولا تمدن عينيك إلى ما متعنا به أزواجا منهم زهرة الحياة الدنيا لنفتنهم فيه ۚ ورزق ربك خير وأبقى'),
(2480, 20, 132, 'وأمر أهلك بالصلاة واصطبر عليها ۖ لا نسألك رزقا ۖ نحن نرزقك ۗ والعاقبة للتقوى'),
(2481, 20, 133, 'وقالوا لولا يأتينا بآية من ربه ۚ أولم تأتهم بينة ما في الصحف الأولى'),
(2482, 20, 134, 'ولو أنا أهلكناهم بعذاب من قبله لقالوا ربنا لولا أرسلت إلينا رسولا فنتبع آياتك من قبل أن نذل ونخزى'),
(2483, 20, 135, 'قل كل متربص فتربصوا ۖ فستعلمون من أصحاب الصراط السوي ومن اهتدى');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'بسم الله الرحمن الرحيم اقترب للناس حسابهم وهم في غفلة معرضون'),
(2485, 21, 2, 'ما يأتيهم من ذكر من ربهم محدث إلا استمعوه وهم يلعبون'),
(2486, 21, 3, 'لاهية قلوبهم ۗ وأسروا النجوى الذين ظلموا هل هذا إلا بشر مثلكم ۖ أفتأتون السحر وأنتم تبصرون'),
(2487, 21, 4, 'قال ربي يعلم القول في السماء والأرض ۖ وهو السميع العليم'),
(2488, 21, 5, 'بل قالوا أضغاث أحلام بل افتراه بل هو شاعر فليأتنا بآية كما أرسل الأولون'),
(2489, 21, 6, 'ما آمنت قبلهم من قرية أهلكناها ۖ أفهم يؤمنون'),
(2490, 21, 7, 'وما أرسلنا قبلك إلا رجالا نوحي إليهم ۖ فاسألوا أهل الذكر إن كنتم لا تعلمون'),
(2491, 21, 8, 'وما جعلناهم جسدا لا يأكلون الطعام وما كانوا خالدين'),
(2492, 21, 9, 'ثم صدقناهم الوعد فأنجيناهم ومن نشاء وأهلكنا المسرفين'),
(2493, 21, 10, 'لقد أنزلنا إليكم كتابا فيه ذكركم ۖ أفلا تعقلون'),
(2494, 21, 11, 'وكم قصمنا من قرية كانت ظالمة وأنشأنا بعدها قوما آخرين'),
(2495, 21, 12, 'فلما أحسوا بأسنا إذا هم منها يركضون'),
(2496, 21, 13, 'لا تركضوا وارجعوا إلى ما أترفتم فيه ومساكنكم لعلكم تسألون'),
(2497, 21, 14, 'قالوا يا ويلنا إنا كنا ظالمين'),
(2498, 21, 15, 'فما زالت تلك دعواهم حتى جعلناهم حصيدا خامدين'),
(2499, 21, 16, 'وما خلقنا السماء والأرض وما بينهما لاعبين'),
(2500, 21, 17, 'لو أردنا أن نتخذ لهوا لاتخذناه من لدنا إن كنا فاعلين'),
(2501, 21, 18, 'بل نقذف بالحق على الباطل فيدمغه فإذا هو زاهق ۚ ولكم الويل مما تصفون'),
(2502, 21, 19, 'وله من في السماوات والأرض ۚ ومن عنده لا يستكبرون عن عبادته ولا يستحسرون'),
(2503, 21, 20, 'يسبحون الليل والنهار لا يفترون'),
(2504, 21, 21, 'أم اتخذوا آلهة من الأرض هم ينشرون'),
(2505, 21, 22, 'لو كان فيهما آلهة إلا الله لفسدتا ۚ فسبحان الله رب العرش عما يصفون'),
(2506, 21, 23, 'لا يسأل عما يفعل وهم يسألون'),
(2507, 21, 24, 'أم اتخذوا من دونه آلهة ۖ قل هاتوا برهانكم ۖ هذا ذكر من معي وذكر من قبلي ۗ بل أكثرهم لا يعلمون الحق ۖ فهم معرضون'),
(2508, 21, 25, 'وما أرسلنا من قبلك من رسول إلا نوحي إليه أنه لا إله إلا أنا فاعبدون'),
(2509, 21, 26, 'وقالوا اتخذ الرحمن ولدا ۗ سبحانه ۚ بل عباد مكرمون'),
(2510, 21, 27, 'لا يسبقونه بالقول وهم بأمره يعملون'),
(2511, 21, 28, 'يعلم ما بين أيديهم وما خلفهم ولا يشفعون إلا لمن ارتضى وهم من خشيته مشفقون'),
(2512, 21, 29, 'ومن يقل منهم إني إله من دونه فذلك نجزيه جهنم ۚ كذلك نجزي الظالمين'),
(2513, 21, 30, 'أولم ير الذين كفروا أن السماوات والأرض كانتا رتقا ففتقناهما ۖ وجعلنا من الماء كل شيء حي ۖ أفلا يؤمنون'),
(2514, 21, 31, 'وجعلنا في الأرض رواسي أن تميد بهم وجعلنا فيها فجاجا سبلا لعلهم يهتدون'),
(2515, 21, 32, 'وجعلنا السماء سقفا محفوظا ۖ وهم عن آياتها معرضون'),
(2516, 21, 33, 'وهو الذي خلق الليل والنهار والشمس والقمر ۖ كل في فلك يسبحون'),
(2517, 21, 34, 'وما جعلنا لبشر من قبلك الخلد ۖ أفإن مت فهم الخالدون'),
(2518, 21, 35, 'كل نفس ذائقة الموت ۗ ونبلوكم بالشر والخير فتنة ۖ وإلينا ترجعون'),
(2519, 21, 36, 'وإذا رآك الذين كفروا إن يتخذونك إلا هزوا أهذا الذي يذكر آلهتكم وهم بذكر الرحمن هم كافرون'),
(2520, 21, 37, 'خلق الإنسان من عجل ۚ سأريكم آياتي فلا تستعجلون'),
(2521, 21, 38, 'ويقولون متى هذا الوعد إن كنتم صادقين'),
(2522, 21, 39, 'لو يعلم الذين كفروا حين لا يكفون عن وجوههم النار ولا عن ظهورهم ولا هم ينصرون'),
(2523, 21, 40, 'بل تأتيهم بغتة فتبهتهم فلا يستطيعون ردها ولا هم ينظرون'),
(2524, 21, 41, 'ولقد استهزئ برسل من قبلك فحاق بالذين سخروا منهم ما كانوا به يستهزئون'),
(2525, 21, 42, 'قل من يكلؤكم بالليل والنهار من الرحمن ۗ بل هم عن ذكر ربهم معرضون'),
(2526, 21, 43, 'أم لهم آلهة تمنعهم من دوننا ۚ لا يستطيعون نصر أنفسهم ولا هم منا يصحبون'),
(2527, 21, 44, 'بل متعنا هؤلاء وآباءهم حتى طال عليهم العمر ۗ أفلا يرون أنا نأتي الأرض ننقصها من أطرافها ۚ أفهم الغالبون'),
(2528, 21, 45, 'قل إنما أنذركم بالوحي ۚ ولا يسمع الصم الدعاء إذا ما ينذرون'),
(2529, 21, 46, 'ولئن مستهم نفحة من عذاب ربك ليقولن يا ويلنا إنا كنا ظالمين'),
(2530, 21, 47, 'ونضع الموازين القسط ليوم القيامة فلا تظلم نفس شيئا ۖ وإن كان مثقال حبة من خردل أتينا بها ۗ وكفى بنا حاسبين'),
(2531, 21, 48, 'ولقد آتينا موسى وهارون الفرقان وضياء وذكرا للمتقين'),
(2532, 21, 49, 'الذين يخشون ربهم بالغيب وهم من الساعة مشفقون'),
(2533, 21, 50, 'وهذا ذكر مبارك أنزلناه ۚ أفأنتم له منكرون'),
(2534, 21, 51, 'ولقد آتينا إبراهيم رشده من قبل وكنا به عالمين'),
(2535, 21, 52, 'إذ قال لأبيه وقومه ما هذه التماثيل التي أنتم لها عاكفون'),
(2536, 21, 53, 'قالوا وجدنا آباءنا لها عابدين'),
(2537, 21, 54, 'قال لقد كنتم أنتم وآباؤكم في ضلال مبين'),
(2538, 21, 55, 'قالوا أجئتنا بالحق أم أنت من اللاعبين'),
(2539, 21, 56, 'قال بل ربكم رب السماوات والأرض الذي فطرهن وأنا على ذلكم من الشاهدين'),
(2540, 21, 57, 'وتالله لأكيدن أصنامكم بعد أن تولوا مدبرين'),
(2541, 21, 58, 'فجعلهم جذاذا إلا كبيرا لهم لعلهم إليه يرجعون'),
(2542, 21, 59, 'قالوا من فعل هذا بآلهتنا إنه لمن الظالمين'),
(2543, 21, 60, 'قالوا سمعنا فتى يذكرهم يقال له إبراهيم'),
(2544, 21, 61, 'قالوا فأتوا به على أعين الناس لعلهم يشهدون'),
(2545, 21, 62, 'قالوا أأنت فعلت هذا بآلهتنا يا إبراهيم'),
(2546, 21, 63, 'قال بل فعله كبيرهم هذا فاسألوهم إن كانوا ينطقون'),
(2547, 21, 64, 'فرجعوا إلى أنفسهم فقالوا إنكم أنتم الظالمون'),
(2548, 21, 65, 'ثم نكسوا على رءوسهم لقد علمت ما هؤلاء ينطقون'),
(2549, 21, 66, 'قال أفتعبدون من دون الله ما لا ينفعكم شيئا ولا يضركم'),
(2550, 21, 67, 'أف لكم ولما تعبدون من دون الله ۖ أفلا تعقلون'),
(2551, 21, 68, 'قالوا حرقوه وانصروا آلهتكم إن كنتم فاعلين'),
(2552, 21, 69, 'قلنا يا نار كوني بردا وسلاما على إبراهيم'),
(2553, 21, 70, 'وأرادوا به كيدا فجعلناهم الأخسرين'),
(2554, 21, 71, 'ونجيناه ولوطا إلى الأرض التي باركنا فيها للعالمين'),
(2555, 21, 72, 'ووهبنا له إسحاق ويعقوب نافلة ۖ وكلا جعلنا صالحين'),
(2556, 21, 73, 'وجعلناهم أئمة يهدون بأمرنا وأوحينا إليهم فعل الخيرات وإقام الصلاة وإيتاء الزكاة ۖ وكانوا لنا عابدين'),
(2557, 21, 74, 'ولوطا آتيناه حكما وعلما ونجيناه من القرية التي كانت تعمل الخبائث ۗ إنهم كانوا قوم سوء فاسقين'),
(2558, 21, 75, 'وأدخلناه في رحمتنا ۖ إنه من الصالحين'),
(2559, 21, 76, 'ونوحا إذ نادى من قبل فاستجبنا له فنجيناه وأهله من الكرب العظيم'),
(2560, 21, 77, 'ونصرناه من القوم الذين كذبوا بآياتنا ۚ إنهم كانوا قوم سوء فأغرقناهم أجمعين'),
(2561, 21, 78, 'وداوود وسليمان إذ يحكمان في الحرث إذ نفشت فيه غنم القوم وكنا لحكمهم شاهدين'),
(2562, 21, 79, 'ففهمناها سليمان ۚ وكلا آتينا حكما وعلما ۚ وسخرنا مع داوود الجبال يسبحن والطير ۚ وكنا فاعلين'),
(2563, 21, 80, 'وعلمناه صنعة لبوس لكم لتحصنكم من بأسكم ۖ فهل أنتم شاكرون'),
(2564, 21, 81, 'ولسليمان الريح عاصفة تجري بأمره إلى الأرض التي باركنا فيها ۚ وكنا بكل شيء عالمين'),
(2565, 21, 82, 'ومن الشياطين من يغوصون له ويعملون عملا دون ذلك ۖ وكنا لهم حافظين'),
(2566, 21, 83, 'وأيوب إذ نادى ربه أني مسني الضر وأنت أرحم الراحمين'),
(2567, 21, 84, 'فاستجبنا له فكشفنا ما به من ضر ۖ وآتيناه أهله ومثلهم معهم رحمة من عندنا وذكرى للعابدين'),
(2568, 21, 85, 'وإسماعيل وإدريس وذا الكفل ۖ كل من الصابرين'),
(2569, 21, 86, 'وأدخلناهم في رحمتنا ۖ إنهم من الصالحين'),
(2570, 21, 87, 'وذا النون إذ ذهب مغاضبا فظن أن لن نقدر عليه فنادى في الظلمات أن لا إله إلا أنت سبحانك إني كنت من الظالمين'),
(2571, 21, 88, 'فاستجبنا له ونجيناه من الغم ۚ وكذلك ننجي المؤمنين'),
(2572, 21, 89, 'وزكريا إذ نادى ربه رب لا تذرني فردا وأنت خير الوارثين'),
(2573, 21, 90, 'فاستجبنا له ووهبنا له يحيى وأصلحنا له زوجه ۚ إنهم كانوا يسارعون في الخيرات ويدعوننا رغبا ورهبا ۖ وكانوا لنا خاشعين'),
(2574, 21, 91, 'والتي أحصنت فرجها فنفخنا فيها من روحنا وجعلناها وابنها آية للعالمين'),
(2575, 21, 92, 'إن هذه أمتكم أمة واحدة وأنا ربكم فاعبدون'),
(2576, 21, 93, 'وتقطعوا أمرهم بينهم ۖ كل إلينا راجعون'),
(2577, 21, 94, 'فمن يعمل من الصالحات وهو مؤمن فلا كفران لسعيه وإنا له كاتبون'),
(2578, 21, 95, 'وحرام على قرية أهلكناها أنهم لا يرجعون'),
(2579, 21, 96, 'حتى إذا فتحت يأجوج ومأجوج وهم من كل حدب ينسلون'),
(2580, 21, 97, 'واقترب الوعد الحق فإذا هي شاخصة أبصار الذين كفروا يا ويلنا قد كنا في غفلة من هذا بل كنا ظالمين'),
(2581, 21, 98, 'إنكم وما تعبدون من دون الله حصب جهنم أنتم لها واردون'),
(2582, 21, 99, 'لو كان هؤلاء آلهة ما وردوها ۖ وكل فيها خالدون'),
(2583, 21, 100, 'لهم فيها زفير وهم فيها لا يسمعون'),
(2584, 21, 101, 'إن الذين سبقت لهم منا الحسنى أولئك عنها مبعدون'),
(2585, 21, 102, 'لا يسمعون حسيسها ۖ وهم في ما اشتهت أنفسهم خالدون'),
(2586, 21, 103, 'لا يحزنهم الفزع الأكبر وتتلقاهم الملائكة هذا يومكم الذي كنتم توعدون'),
(2587, 21, 104, 'يوم نطوي السماء كطي السجل للكتب ۚ كما بدأنا أول خلق نعيده ۚ وعدا علينا ۚ إنا كنا فاعلين'),
(2588, 21, 105, 'ولقد كتبنا في الزبور من بعد الذكر أن الأرض يرثها عبادي الصالحون'),
(2589, 21, 106, 'إن في هذا لبلاغا لقوم عابدين'),
(2590, 21, 107, 'وما أرسلناك إلا رحمة للعالمين'),
(2591, 21, 108, 'قل إنما يوحى إلي أنما إلهكم إله واحد ۖ فهل أنتم مسلمون'),
(2592, 21, 109, 'فإن تولوا فقل آذنتكم على سواء ۖ وإن أدري أقريب أم بعيد ما توعدون'),
(2593, 21, 110, 'إنه يعلم الجهر من القول ويعلم ما تكتمون'),
(2594, 21, 111, 'وإن أدري لعله فتنة لكم ومتاع إلى حين'),
(2595, 21, 112, 'قال رب احكم بالحق ۗ وربنا الرحمن المستعان على ما تصفون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'بسم الله الرحمن الرحيم يا أيها الناس اتقوا ربكم ۚ إن زلزلة الساعة شيء عظيم'),
(2597, 22, 2, 'يوم ترونها تذهل كل مرضعة عما أرضعت وتضع كل ذات حمل حملها وترى الناس سكارى وما هم بسكارى ولكن عذاب الله شديد'),
(2598, 22, 3, 'ومن الناس من يجادل في الله بغير علم ويتبع كل شيطان مريد'),
(2599, 22, 4, 'كتب عليه أنه من تولاه فأنه يضله ويهديه إلى عذاب السعير'),
(2600, 22, 5, 'يا أيها الناس إن كنتم في ريب من البعث فإنا خلقناكم من تراب ثم من نطفة ثم من علقة ثم من مضغة مخلقة وغير مخلقة لنبين لكم ۚ ونقر في الأرحام ما نشاء إلى أجل مسمى ثم نخرجكم طفلا ثم لتبلغوا أشدكم ۖ ومنكم من يتوفى ومنكم من يرد إلى أرذل العمر لكيلا يعلم من بعد علم شيئا ۚ وترى الأرض هامدة فإذا أنزلنا عليها الماء اهتزت وربت وأنبتت من كل زوج بهيج'),
(2601, 22, 6, 'ذلك بأن الله هو الحق وأنه يحيي الموتى وأنه على كل شيء قدير'),
(2602, 22, 7, 'وأن الساعة آتية لا ريب فيها وأن الله يبعث من في القبور'),
(2603, 22, 8, 'ومن الناس من يجادل في الله بغير علم ولا هدى ولا كتاب منير'),
(2604, 22, 9, 'ثاني عطفه ليضل عن سبيل الله ۖ له في الدنيا خزي ۖ ونذيقه يوم القيامة عذاب الحريق'),
(2605, 22, 10, 'ذلك بما قدمت يداك وأن الله ليس بظلام للعبيد'),
(2606, 22, 11, 'ومن الناس من يعبد الله على حرف ۖ فإن أصابه خير اطمأن به ۖ وإن أصابته فتنة انقلب على وجهه خسر الدنيا والآخرة ۚ ذلك هو الخسران المبين'),
(2607, 22, 12, 'يدعو من دون الله ما لا يضره وما لا ينفعه ۚ ذلك هو الضلال البعيد'),
(2608, 22, 13, 'يدعو لمن ضره أقرب من نفعه ۚ لبئس المولى ولبئس العشير'),
(2609, 22, 14, 'إن الله يدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار ۚ إن الله يفعل ما يريد'),
(2610, 22, 15, 'من كان يظن أن لن ينصره الله في الدنيا والآخرة فليمدد بسبب إلى السماء ثم ليقطع فلينظر هل يذهبن كيده ما يغيظ'),
(2611, 22, 16, 'وكذلك أنزلناه آيات بينات وأن الله يهدي من يريد'),
(2612, 22, 17, 'إن الذين آمنوا والذين هادوا والصابئين والنصارى والمجوس والذين أشركوا إن الله يفصل بينهم يوم القيامة ۚ إن الله على كل شيء شهيد'),
(2613, 22, 18, 'ألم تر أن الله يسجد له من في السماوات ومن في الأرض والشمس والقمر والنجوم والجبال والشجر والدواب وكثير من الناس ۖ وكثير حق عليه العذاب ۗ ومن يهن الله فما له من مكرم ۚ إن الله يفعل ما يشاء'),
(2614, 22, 19, 'هذان خصمان اختصموا في ربهم ۖ فالذين كفروا قطعت لهم ثياب من نار يصب من فوق رءوسهم الحميم'),
(2615, 22, 20, 'يصهر به ما في بطونهم والجلود'),
(2616, 22, 21, 'ولهم مقامع من حديد'),
(2617, 22, 22, 'كلما أرادوا أن يخرجوا منها من غم أعيدوا فيها وذوقوا عذاب الحريق'),
(2618, 22, 23, 'إن الله يدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار يحلون فيها من أساور من ذهب ولؤلؤا ۖ ولباسهم فيها حرير'),
(2619, 22, 24, 'وهدوا إلى الطيب من القول وهدوا إلى صراط الحميد'),
(2620, 22, 25, 'إن الذين كفروا ويصدون عن سبيل الله والمسجد الحرام الذي جعلناه للناس سواء العاكف فيه والباد ۚ ومن يرد فيه بإلحاد بظلم نذقه من عذاب أليم'),
(2621, 22, 26, 'وإذ بوأنا لإبراهيم مكان البيت أن لا تشرك بي شيئا وطهر بيتي للطائفين والقائمين والركع السجود'),
(2622, 22, 27, 'وأذن في الناس بالحج يأتوك رجالا وعلى كل ضامر يأتين من كل فج عميق'),
(2623, 22, 28, 'ليشهدوا منافع لهم ويذكروا اسم الله في أيام معلومات على ما رزقهم من بهيمة الأنعام ۖ فكلوا منها وأطعموا البائس الفقير'),
(2624, 22, 29, 'ثم ليقضوا تفثهم وليوفوا نذورهم وليطوفوا بالبيت العتيق'),
(2625, 22, 30, 'ذلك ومن يعظم حرمات الله فهو خير له عند ربه ۗ وأحلت لكم الأنعام إلا ما يتلى عليكم ۖ فاجتنبوا الرجس من الأوثان واجتنبوا قول الزور'),
(2626, 22, 31, 'حنفاء لله غير مشركين به ۚ ومن يشرك بالله فكأنما خر من السماء فتخطفه الطير أو تهوي به الريح في مكان سحيق'),
(2627, 22, 32, 'ذلك ومن يعظم شعائر الله فإنها من تقوى القلوب'),
(2628, 22, 33, 'لكم فيها منافع إلى أجل مسمى ثم محلها إلى البيت العتيق'),
(2629, 22, 34, 'ولكل أمة جعلنا منسكا ليذكروا اسم الله على ما رزقهم من بهيمة الأنعام ۗ فإلهكم إله واحد فله أسلموا ۗ وبشر المخبتين'),
(2630, 22, 35, 'الذين إذا ذكر الله وجلت قلوبهم والصابرين على ما أصابهم والمقيمي الصلاة ومما رزقناهم ينفقون'),
(2631, 22, 36, 'والبدن جعلناها لكم من شعائر الله لكم فيها خير ۖ فاذكروا اسم الله عليها صواف ۖ فإذا وجبت جنوبها فكلوا منها وأطعموا القانع والمعتر ۚ كذلك سخرناها لكم لعلكم تشكرون'),
(2632, 22, 37, 'لن ينال الله لحومها ولا دماؤها ولكن يناله التقوى منكم ۚ كذلك سخرها لكم لتكبروا الله على ما هداكم ۗ وبشر المحسنين'),
(2633, 22, 38, 'إن الله يدافع عن الذين آمنوا ۗ إن الله لا يحب كل خوان كفور'),
(2634, 22, 39, 'أذن للذين يقاتلون بأنهم ظلموا ۚ وإن الله على نصرهم لقدير'),
(2635, 22, 40, 'الذين أخرجوا من ديارهم بغير حق إلا أن يقولوا ربنا الله ۗ ولولا دفع الله الناس بعضهم ببعض لهدمت صوامع وبيع وصلوات ومساجد يذكر فيها اسم الله كثيرا ۗ ولينصرن الله من ينصره ۗ إن الله لقوي عزيز'),
(2636, 22, 41, 'الذين إن مكناهم في الأرض أقاموا الصلاة وآتوا الزكاة وأمروا بالمعروف ونهوا عن المنكر ۗ ولله عاقبة الأمور'),
(2637, 22, 42, 'وإن يكذبوك فقد كذبت قبلهم قوم نوح وعاد وثمود'),
(2638, 22, 43, 'وقوم إبراهيم وقوم لوط'),
(2639, 22, 44, 'وأصحاب مدين ۖ وكذب موسى فأمليت للكافرين ثم أخذتهم ۖ فكيف كان نكير'),
(2640, 22, 45, 'فكأين من قرية أهلكناها وهي ظالمة فهي خاوية على عروشها وبئر معطلة وقصر مشيد'),
(2641, 22, 46, 'أفلم يسيروا في الأرض فتكون لهم قلوب يعقلون بها أو آذان يسمعون بها ۖ فإنها لا تعمى الأبصار ولكن تعمى القلوب التي في الصدور'),
(2642, 22, 47, 'ويستعجلونك بالعذاب ولن يخلف الله وعده ۚ وإن يوما عند ربك كألف سنة مما تعدون'),
(2643, 22, 48, 'وكأين من قرية أمليت لها وهي ظالمة ثم أخذتها وإلي المصير'),
(2644, 22, 49, 'قل يا أيها الناس إنما أنا لكم نذير مبين'),
(2645, 22, 50, 'فالذين آمنوا وعملوا الصالحات لهم مغفرة ورزق كريم'),
(2646, 22, 51, 'والذين سعوا في آياتنا معاجزين أولئك أصحاب الجحيم'),
(2647, 22, 52, 'وما أرسلنا من قبلك من رسول ولا نبي إلا إذا تمنى ألقى الشيطان في أمنيته فينسخ الله ما يلقي الشيطان ثم يحكم الله آياته ۗ والله عليم حكيم'),
(2648, 22, 53, 'ليجعل ما يلقي الشيطان فتنة للذين في قلوبهم مرض والقاسية قلوبهم ۗ وإن الظالمين لفي شقاق بعيد'),
(2649, 22, 54, 'وليعلم الذين أوتوا العلم أنه الحق من ربك فيؤمنوا به فتخبت له قلوبهم ۗ وإن الله لهاد الذين آمنوا إلى صراط مستقيم'),
(2650, 22, 55, 'ولا يزال الذين كفروا في مرية منه حتى تأتيهم الساعة بغتة أو يأتيهم عذاب يوم عقيم'),
(2651, 22, 56, 'الملك يومئذ لله يحكم بينهم ۚ فالذين آمنوا وعملوا الصالحات في جنات النعيم'),
(2652, 22, 57, 'والذين كفروا وكذبوا بآياتنا فأولئك لهم عذاب مهين'),
(2653, 22, 58, 'والذين هاجروا في سبيل الله ثم قتلوا أو ماتوا ليرزقنهم الله رزقا حسنا ۚ وإن الله لهو خير الرازقين'),
(2654, 22, 59, 'ليدخلنهم مدخلا يرضونه ۗ وإن الله لعليم حليم'),
(2655, 22, 60, 'ذلك ومن عاقب بمثل ما عوقب به ثم بغي عليه لينصرنه الله ۗ إن الله لعفو غفور'),
(2656, 22, 61, 'ذلك بأن الله يولج الليل في النهار ويولج النهار في الليل وأن الله سميع بصير'),
(2657, 22, 62, 'ذلك بأن الله هو الحق وأن ما يدعون من دونه هو الباطل وأن الله هو العلي الكبير'),
(2658, 22, 63, 'ألم تر أن الله أنزل من السماء ماء فتصبح الأرض مخضرة ۗ إن الله لطيف خبير'),
(2659, 22, 64, 'له ما في السماوات وما في الأرض ۗ وإن الله لهو الغني الحميد'),
(2660, 22, 65, 'ألم تر أن الله سخر لكم ما في الأرض والفلك تجري في البحر بأمره ويمسك السماء أن تقع على الأرض إلا بإذنه ۗ إن الله بالناس لرءوف رحيم'),
(2661, 22, 66, 'وهو الذي أحياكم ثم يميتكم ثم يحييكم ۗ إن الإنسان لكفور'),
(2662, 22, 67, 'لكل أمة جعلنا منسكا هم ناسكوه ۖ فلا ينازعنك في الأمر ۚ وادع إلى ربك ۖ إنك لعلى هدى مستقيم'),
(2663, 22, 68, 'وإن جادلوك فقل الله أعلم بما تعملون'),
(2664, 22, 69, 'الله يحكم بينكم يوم القيامة فيما كنتم فيه تختلفون'),
(2665, 22, 70, 'ألم تعلم أن الله يعلم ما في السماء والأرض ۗ إن ذلك في كتاب ۚ إن ذلك على الله يسير'),
(2666, 22, 71, 'ويعبدون من دون الله ما لم ينزل به سلطانا وما ليس لهم به علم ۗ وما للظالمين من نصير'),
(2667, 22, 72, 'وإذا تتلى عليهم آياتنا بينات تعرف في وجوه الذين كفروا المنكر ۖ يكادون يسطون بالذين يتلون عليهم آياتنا ۗ قل أفأنبئكم بشر من ذلكم ۗ النار وعدها الله الذين كفروا ۖ وبئس المصير'),
(2668, 22, 73, 'يا أيها الناس ضرب مثل فاستمعوا له ۚ إن الذين تدعون من دون الله لن يخلقوا ذبابا ولو اجتمعوا له ۖ وإن يسلبهم الذباب شيئا لا يستنقذوه منه ۚ ضعف الطالب والمطلوب'),
(2669, 22, 74, 'ما قدروا الله حق قدره ۗ إن الله لقوي عزيز'),
(2670, 22, 75, 'الله يصطفي من الملائكة رسلا ومن الناس ۚ إن الله سميع بصير'),
(2671, 22, 76, 'يعلم ما بين أيديهم وما خلفهم ۗ وإلى الله ترجع الأمور'),
(2672, 22, 77, 'يا أيها الذين آمنوا اركعوا واسجدوا واعبدوا ربكم وافعلوا الخير لعلكم تفلحون'),
(2673, 22, 78, 'وجاهدوا في الله حق جهاده ۚ هو اجتباكم وما جعل عليكم في الدين من حرج ۚ ملة أبيكم إبراهيم ۚ هو سماكم المسلمين من قبل وفي هذا ليكون الرسول شهيدا عليكم وتكونوا شهداء على الناس ۚ فأقيموا الصلاة وآتوا الزكاة واعتصموا بالله هو مولاكم ۖ فنعم المولى ونعم النصير');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'بسم الله الرحمن الرحيم قد أفلح المؤمنون'),
(2675, 23, 2, 'الذين هم في صلاتهم خاشعون'),
(2676, 23, 3, 'والذين هم عن اللغو معرضون'),
(2677, 23, 4, 'والذين هم للزكاة فاعلون'),
(2678, 23, 5, 'والذين هم لفروجهم حافظون'),
(2679, 23, 6, 'إلا على أزواجهم أو ما ملكت أيمانهم فإنهم غير ملومين'),
(2680, 23, 7, 'فمن ابتغى وراء ذلك فأولئك هم العادون'),
(2681, 23, 8, 'والذين هم لأماناتهم وعهدهم راعون'),
(2682, 23, 9, 'والذين هم على صلواتهم يحافظون'),
(2683, 23, 10, 'أولئك هم الوارثون'),
(2684, 23, 11, 'الذين يرثون الفردوس هم فيها خالدون'),
(2685, 23, 12, 'ولقد خلقنا الإنسان من سلالة من طين'),
(2686, 23, 13, 'ثم جعلناه نطفة في قرار مكين'),
(2687, 23, 14, 'ثم خلقنا النطفة علقة فخلقنا العلقة مضغة فخلقنا المضغة عظاما فكسونا العظام لحما ثم أنشأناه خلقا آخر ۚ فتبارك الله أحسن الخالقين'),
(2688, 23, 15, 'ثم إنكم بعد ذلك لميتون'),
(2689, 23, 16, 'ثم إنكم يوم القيامة تبعثون'),
(2690, 23, 17, 'ولقد خلقنا فوقكم سبع طرائق وما كنا عن الخلق غافلين'),
(2691, 23, 18, 'وأنزلنا من السماء ماء بقدر فأسكناه في الأرض ۖ وإنا على ذهاب به لقادرون'),
(2692, 23, 19, 'فأنشأنا لكم به جنات من نخيل وأعناب لكم فيها فواكه كثيرة ومنها تأكلون'),
(2693, 23, 20, 'وشجرة تخرج من طور سيناء تنبت بالدهن وصبغ للآكلين'),
(2694, 23, 21, 'وإن لكم في الأنعام لعبرة ۖ نسقيكم مما في بطونها ولكم فيها منافع كثيرة ومنها تأكلون'),
(2695, 23, 22, 'وعليها وعلى الفلك تحملون'),
(2696, 23, 23, 'ولقد أرسلنا نوحا إلى قومه فقال يا قوم اعبدوا الله ما لكم من إله غيره ۖ أفلا تتقون'),
(2697, 23, 24, 'فقال الملأ الذين كفروا من قومه ما هذا إلا بشر مثلكم يريد أن يتفضل عليكم ولو شاء الله لأنزل ملائكة ما سمعنا بهذا في آبائنا الأولين'),
(2698, 23, 25, 'إن هو إلا رجل به جنة فتربصوا به حتى حين'),
(2699, 23, 26, 'قال رب انصرني بما كذبون'),
(2700, 23, 27, 'فأوحينا إليه أن اصنع الفلك بأعيننا ووحينا فإذا جاء أمرنا وفار التنور ۙ فاسلك فيها من كل زوجين اثنين وأهلك إلا من سبق عليه القول منهم ۖ ولا تخاطبني في الذين ظلموا ۖ إنهم مغرقون'),
(2701, 23, 28, 'فإذا استويت أنت ومن معك على الفلك فقل الحمد لله الذي نجانا من القوم الظالمين'),
(2702, 23, 29, 'وقل رب أنزلني منزلا مباركا وأنت خير المنزلين'),
(2703, 23, 30, 'إن في ذلك لآيات وإن كنا لمبتلين'),
(2704, 23, 31, 'ثم أنشأنا من بعدهم قرنا آخرين'),
(2705, 23, 32, 'فأرسلنا فيهم رسولا منهم أن اعبدوا الله ما لكم من إله غيره ۖ أفلا تتقون'),
(2706, 23, 33, 'وقال الملأ من قومه الذين كفروا وكذبوا بلقاء الآخرة وأترفناهم في الحياة الدنيا ما هذا إلا بشر مثلكم يأكل مما تأكلون منه ويشرب مما تشربون'),
(2707, 23, 34, 'ولئن أطعتم بشرا مثلكم إنكم إذا لخاسرون'),
(2708, 23, 35, 'أيعدكم أنكم إذا متم وكنتم ترابا وعظاما أنكم مخرجون'),
(2709, 23, 36, 'هيهات هيهات لما توعدون'),
(2710, 23, 37, 'إن هي إلا حياتنا الدنيا نموت ونحيا وما نحن بمبعوثين'),
(2711, 23, 38, 'إن هو إلا رجل افترى على الله كذبا وما نحن له بمؤمنين'),
(2712, 23, 39, 'قال رب انصرني بما كذبون'),
(2713, 23, 40, 'قال عما قليل ليصبحن نادمين'),
(2714, 23, 41, 'فأخذتهم الصيحة بالحق فجعلناهم غثاء ۚ فبعدا للقوم الظالمين'),
(2715, 23, 42, 'ثم أنشأنا من بعدهم قرونا آخرين'),
(2716, 23, 43, 'ما تسبق من أمة أجلها وما يستأخرون'),
(2717, 23, 44, 'ثم أرسلنا رسلنا تترى ۖ كل ما جاء أمة رسولها كذبوه ۚ فأتبعنا بعضهم بعضا وجعلناهم أحاديث ۚ فبعدا لقوم لا يؤمنون'),
(2718, 23, 45, 'ثم أرسلنا موسى وأخاه هارون بآياتنا وسلطان مبين'),
(2719, 23, 46, 'إلى فرعون وملئه فاستكبروا وكانوا قوما عالين'),
(2720, 23, 47, 'فقالوا أنؤمن لبشرين مثلنا وقومهما لنا عابدون'),
(2721, 23, 48, 'فكذبوهما فكانوا من المهلكين'),
(2722, 23, 49, 'ولقد آتينا موسى الكتاب لعلهم يهتدون'),
(2723, 23, 50, 'وجعلنا ابن مريم وأمه آية وآويناهما إلى ربوة ذات قرار ومعين'),
(2724, 23, 51, 'يا أيها الرسل كلوا من الطيبات واعملوا صالحا ۖ إني بما تعملون عليم'),
(2725, 23, 52, 'وإن هذه أمتكم أمة واحدة وأنا ربكم فاتقون'),
(2726, 23, 53, 'فتقطعوا أمرهم بينهم زبرا ۖ كل حزب بما لديهم فرحون'),
(2727, 23, 54, 'فذرهم في غمرتهم حتى حين'),
(2728, 23, 55, 'أيحسبون أنما نمدهم به من مال وبنين'),
(2729, 23, 56, 'نسارع لهم في الخيرات ۚ بل لا يشعرون'),
(2730, 23, 57, 'إن الذين هم من خشية ربهم مشفقون'),
(2731, 23, 58, 'والذين هم بآيات ربهم يؤمنون'),
(2732, 23, 59, 'والذين هم بربهم لا يشركون'),
(2733, 23, 60, 'والذين يؤتون ما آتوا وقلوبهم وجلة أنهم إلى ربهم راجعون'),
(2734, 23, 61, 'أولئك يسارعون في الخيرات وهم لها سابقون'),
(2735, 23, 62, 'ولا نكلف نفسا إلا وسعها ۖ ولدينا كتاب ينطق بالحق ۚ وهم لا يظلمون'),
(2736, 23, 63, 'بل قلوبهم في غمرة من هذا ولهم أعمال من دون ذلك هم لها عاملون'),
(2737, 23, 64, 'حتى إذا أخذنا مترفيهم بالعذاب إذا هم يجأرون'),
(2738, 23, 65, 'لا تجأروا اليوم ۖ إنكم منا لا تنصرون'),
(2739, 23, 66, 'قد كانت آياتي تتلى عليكم فكنتم على أعقابكم تنكصون'),
(2740, 23, 67, 'مستكبرين به سامرا تهجرون'),
(2741, 23, 68, 'أفلم يدبروا القول أم جاءهم ما لم يأت آباءهم الأولين'),
(2742, 23, 69, 'أم لم يعرفوا رسولهم فهم له منكرون'),
(2743, 23, 70, 'أم يقولون به جنة ۚ بل جاءهم بالحق وأكثرهم للحق كارهون'),
(2744, 23, 71, 'ولو اتبع الحق أهواءهم لفسدت السماوات والأرض ومن فيهن ۚ بل أتيناهم بذكرهم فهم عن ذكرهم معرضون'),
(2745, 23, 72, 'أم تسألهم خرجا فخراج ربك خير ۖ وهو خير الرازقين'),
(2746, 23, 73, 'وإنك لتدعوهم إلى صراط مستقيم'),
(2747, 23, 74, 'وإن الذين لا يؤمنون بالآخرة عن الصراط لناكبون'),
(2748, 23, 75, 'ولو رحمناهم وكشفنا ما بهم من ضر للجوا في طغيانهم يعمهون'),
(2749, 23, 76, 'ولقد أخذناهم بالعذاب فما استكانوا لربهم وما يتضرعون'),
(2750, 23, 77, 'حتى إذا فتحنا عليهم بابا ذا عذاب شديد إذا هم فيه مبلسون'),
(2751, 23, 78, 'وهو الذي أنشأ لكم السمع والأبصار والأفئدة ۚ قليلا ما تشكرون'),
(2752, 23, 79, 'وهو الذي ذرأكم في الأرض وإليه تحشرون'),
(2753, 23, 80, 'وهو الذي يحيي ويميت وله اختلاف الليل والنهار ۚ أفلا تعقلون'),
(2754, 23, 81, 'بل قالوا مثل ما قال الأولون'),
(2755, 23, 82, 'قالوا أإذا متنا وكنا ترابا وعظاما أإنا لمبعوثون'),
(2756, 23, 83, 'لقد وعدنا نحن وآباؤنا هذا من قبل إن هذا إلا أساطير الأولين'),
(2757, 23, 84, 'قل لمن الأرض ومن فيها إن كنتم تعلمون'),
(2758, 23, 85, 'سيقولون لله ۚ قل أفلا تذكرون'),
(2759, 23, 86, 'قل من رب السماوات السبع ورب العرش العظيم'),
(2760, 23, 87, 'سيقولون لله ۚ قل أفلا تتقون'),
(2761, 23, 88, 'قل من بيده ملكوت كل شيء وهو يجير ولا يجار عليه إن كنتم تعلمون'),
(2762, 23, 89, 'سيقولون لله ۚ قل فأنى تسحرون'),
(2763, 23, 90, 'بل أتيناهم بالحق وإنهم لكاذبون'),
(2764, 23, 91, 'ما اتخذ الله من ولد وما كان معه من إله ۚ إذا لذهب كل إله بما خلق ولعلا بعضهم على بعض ۚ سبحان الله عما يصفون'),
(2765, 23, 92, 'عالم الغيب والشهادة فتعالى عما يشركون'),
(2766, 23, 93, 'قل رب إما تريني ما يوعدون'),
(2767, 23, 94, 'رب فلا تجعلني في القوم الظالمين'),
(2768, 23, 95, 'وإنا على أن نريك ما نعدهم لقادرون'),
(2769, 23, 96, 'ادفع بالتي هي أحسن السيئة ۚ نحن أعلم بما يصفون'),
(2770, 23, 97, 'وقل رب أعوذ بك من همزات الشياطين'),
(2771, 23, 98, 'وأعوذ بك رب أن يحضرون'),
(2772, 23, 99, 'حتى إذا جاء أحدهم الموت قال رب ارجعون'),
(2773, 23, 100, 'لعلي أعمل صالحا فيما تركت ۚ كلا ۚ إنها كلمة هو قائلها ۖ ومن ورائهم برزخ إلى يوم يبعثون'),
(2774, 23, 101, 'فإذا نفخ في الصور فلا أنساب بينهم يومئذ ولا يتساءلون'),
(2775, 23, 102, 'فمن ثقلت موازينه فأولئك هم المفلحون'),
(2776, 23, 103, 'ومن خفت موازينه فأولئك الذين خسروا أنفسهم في جهنم خالدون'),
(2777, 23, 104, 'تلفح وجوههم النار وهم فيها كالحون'),
(2778, 23, 105, 'ألم تكن آياتي تتلى عليكم فكنتم بها تكذبون'),
(2779, 23, 106, 'قالوا ربنا غلبت علينا شقوتنا وكنا قوما ضالين'),
(2780, 23, 107, 'ربنا أخرجنا منها فإن عدنا فإنا ظالمون'),
(2781, 23, 108, 'قال اخسئوا فيها ولا تكلمون'),
(2782, 23, 109, 'إنه كان فريق من عبادي يقولون ربنا آمنا فاغفر لنا وارحمنا وأنت خير الراحمين'),
(2783, 23, 110, 'فاتخذتموهم سخريا حتى أنسوكم ذكري وكنتم منهم تضحكون'),
(2784, 23, 111, 'إني جزيتهم اليوم بما صبروا أنهم هم الفائزون'),
(2785, 23, 112, 'قال كم لبثتم في الأرض عدد سنين'),
(2786, 23, 113, 'قالوا لبثنا يوما أو بعض يوم فاسأل العادين'),
(2787, 23, 114, 'قال إن لبثتم إلا قليلا ۖ لو أنكم كنتم تعلمون'),
(2788, 23, 115, 'أفحسبتم أنما خلقناكم عبثا وأنكم إلينا لا ترجعون'),
(2789, 23, 116, 'فتعالى الله الملك الحق ۖ لا إله إلا هو رب العرش الكريم'),
(2790, 23, 117, 'ومن يدع مع الله إلها آخر لا برهان له به فإنما حسابه عند ربه ۚ إنه لا يفلح الكافرون'),
(2791, 23, 118, 'وقل رب اغفر وارحم وأنت خير الراحمين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'بسم الله الرحمن الرحيم سورة أنزلناها وفرضناها وأنزلنا فيها آيات بينات لعلكم تذكرون'),
(2793, 24, 2, 'الزانية والزاني فاجلدوا كل واحد منهما مائة جلدة ۖ ولا تأخذكم بهما رأفة في دين الله إن كنتم تؤمنون بالله واليوم الآخر ۖ وليشهد عذابهما طائفة من المؤمنين'),
(2794, 24, 3, 'الزاني لا ينكح إلا زانية أو مشركة والزانية لا ينكحها إلا زان أو مشرك ۚ وحرم ذلك على المؤمنين'),
(2795, 24, 4, 'والذين يرمون المحصنات ثم لم يأتوا بأربعة شهداء فاجلدوهم ثمانين جلدة ولا تقبلوا لهم شهادة أبدا ۚ وأولئك هم الفاسقون'),
(2796, 24, 5, 'إلا الذين تابوا من بعد ذلك وأصلحوا فإن الله غفور رحيم'),
(2797, 24, 6, 'والذين يرمون أزواجهم ولم يكن لهم شهداء إلا أنفسهم فشهادة أحدهم أربع شهادات بالله ۙ إنه لمن الصادقين'),
(2798, 24, 7, 'والخامسة أن لعنت الله عليه إن كان من الكاذبين'),
(2799, 24, 8, 'ويدرأ عنها العذاب أن تشهد أربع شهادات بالله ۙ إنه لمن الكاذبين'),
(2800, 24, 9, 'والخامسة أن غضب الله عليها إن كان من الصادقين'),
(2801, 24, 10, 'ولولا فضل الله عليكم ورحمته وأن الله تواب حكيم'),
(2802, 24, 11, 'إن الذين جاءوا بالإفك عصبة منكم ۚ لا تحسبوه شرا لكم ۖ بل هو خير لكم ۚ لكل امرئ منهم ما اكتسب من الإثم ۚ والذي تولى كبره منهم له عذاب عظيم'),
(2803, 24, 12, 'لولا إذ سمعتموه ظن المؤمنون والمؤمنات بأنفسهم خيرا وقالوا هذا إفك مبين'),
(2804, 24, 13, 'لولا جاءوا عليه بأربعة شهداء ۚ فإذ لم يأتوا بالشهداء فأولئك عند الله هم الكاذبون'),
(2805, 24, 14, 'ولولا فضل الله عليكم ورحمته في الدنيا والآخرة لمسكم في ما أفضتم فيه عذاب عظيم'),
(2806, 24, 15, 'إذ تلقونه بألسنتكم وتقولون بأفواهكم ما ليس لكم به علم وتحسبونه هينا وهو عند الله عظيم'),
(2807, 24, 16, 'ولولا إذ سمعتموه قلتم ما يكون لنا أن نتكلم بهذا سبحانك هذا بهتان عظيم'),
(2808, 24, 17, 'يعظكم الله أن تعودوا لمثله أبدا إن كنتم مؤمنين'),
(2809, 24, 18, 'ويبين الله لكم الآيات ۚ والله عليم حكيم'),
(2810, 24, 19, 'إن الذين يحبون أن تشيع الفاحشة في الذين آمنوا لهم عذاب أليم في الدنيا والآخرة ۚ والله يعلم وأنتم لا تعلمون'),
(2811, 24, 20, 'ولولا فضل الله عليكم ورحمته وأن الله رءوف رحيم'),
(2812, 24, 21, 'يا أيها الذين آمنوا لا تتبعوا خطوات الشيطان ۚ ومن يتبع خطوات الشيطان فإنه يأمر بالفحشاء والمنكر ۚ ولولا فضل الله عليكم ورحمته ما زكى منكم من أحد أبدا ولكن الله يزكي من يشاء ۗ والله سميع عليم'),
(2813, 24, 22, 'ولا يأتل أولو الفضل منكم والسعة أن يؤتوا أولي القربى والمساكين والمهاجرين في سبيل الله ۖ وليعفوا وليصفحوا ۗ ألا تحبون أن يغفر الله لكم ۗ والله غفور رحيم'),
(2814, 24, 23, 'إن الذين يرمون المحصنات الغافلات المؤمنات لعنوا في الدنيا والآخرة ولهم عذاب عظيم'),
(2815, 24, 24, 'يوم تشهد عليهم ألسنتهم وأيديهم وأرجلهم بما كانوا يعملون'),
(2816, 24, 25, 'يومئذ يوفيهم الله دينهم الحق ويعلمون أن الله هو الحق المبين'),
(2817, 24, 26, 'الخبيثات للخبيثين والخبيثون للخبيثات ۖ والطيبات للطيبين والطيبون للطيبات ۚ أولئك مبرءون مما يقولون ۖ لهم مغفرة ورزق كريم'),
(2818, 24, 27, 'يا أيها الذين آمنوا لا تدخلوا بيوتا غير بيوتكم حتى تستأنسوا وتسلموا على أهلها ۚ ذلكم خير لكم لعلكم تذكرون'),
(2819, 24, 28, 'فإن لم تجدوا فيها أحدا فلا تدخلوها حتى يؤذن لكم ۖ وإن قيل لكم ارجعوا فارجعوا ۖ هو أزكى لكم ۚ والله بما تعملون عليم'),
(2820, 24, 29, 'ليس عليكم جناح أن تدخلوا بيوتا غير مسكونة فيها متاع لكم ۚ والله يعلم ما تبدون وما تكتمون'),
(2821, 24, 30, 'قل للمؤمنين يغضوا من أبصارهم ويحفظوا فروجهم ۚ ذلك أزكى لهم ۗ إن الله خبير بما يصنعون'),
(2822, 24, 31, 'وقل للمؤمنات يغضضن من أبصارهن ويحفظن فروجهن ولا يبدين زينتهن إلا ما ظهر منها ۖ وليضربن بخمرهن على جيوبهن ۖ ولا يبدين زينتهن إلا لبعولتهن أو آبائهن أو آباء بعولتهن أو أبنائهن أو أبناء بعولتهن أو إخوانهن أو بني إخوانهن أو بني أخواتهن أو نسائهن أو ما ملكت أيمانهن أو التابعين غير أولي الإربة من الرجال أو الطفل الذين لم يظهروا على عورات النساء ۖ ولا يضربن بأرجلهن ليعلم ما يخفين من زينتهن ۚ وتوبوا إلى الله جميعا أيه المؤمنون لعلكم تفلحون'),
(2823, 24, 32, 'وأنكحوا الأيامى منكم والصالحين من عبادكم وإمائكم ۚ إن يكونوا فقراء يغنهم الله من فضله ۗ والله واسع عليم'),
(2824, 24, 33, 'وليستعفف الذين لا يجدون نكاحا حتى يغنيهم الله من فضله ۗ والذين يبتغون الكتاب مما ملكت أيمانكم فكاتبوهم إن علمتم فيهم خيرا ۖ وآتوهم من مال الله الذي آتاكم ۚ ولا تكرهوا فتياتكم على البغاء إن أردن تحصنا لتبتغوا عرض الحياة الدنيا ۚ ومن يكرههن فإن الله من بعد إكراههن غفور رحيم'),
(2825, 24, 34, 'ولقد أنزلنا إليكم آيات مبينات ومثلا من الذين خلوا من قبلكم وموعظة للمتقين'),
(2826, 24, 35, 'الله نور السماوات والأرض ۚ مثل نوره كمشكاة فيها مصباح ۖ المصباح في زجاجة ۖ الزجاجة كأنها كوكب دري يوقد من شجرة مباركة زيتونة لا شرقية ولا غربية يكاد زيتها يضيء ولو لم تمسسه نار ۚ نور على نور ۗ يهدي الله لنوره من يشاء ۚ ويضرب الله الأمثال للناس ۗ والله بكل شيء عليم'),
(2827, 24, 36, 'في بيوت أذن الله أن ترفع ويذكر فيها اسمه يسبح له فيها بالغدو والآصال'),
(2828, 24, 37, 'رجال لا تلهيهم تجارة ولا بيع عن ذكر الله وإقام الصلاة وإيتاء الزكاة ۙ يخافون يوما تتقلب فيه القلوب والأبصار'),
(2829, 24, 38, 'ليجزيهم الله أحسن ما عملوا ويزيدهم من فضله ۗ والله يرزق من يشاء بغير حساب'),
(2830, 24, 39, 'والذين كفروا أعمالهم كسراب بقيعة يحسبه الظمآن ماء حتى إذا جاءه لم يجده شيئا ووجد الله عنده فوفاه حسابه ۗ والله سريع الحساب'),
(2831, 24, 40, 'أو كظلمات في بحر لجي يغشاه موج من فوقه موج من فوقه سحاب ۚ ظلمات بعضها فوق بعض إذا أخرج يده لم يكد يراها ۗ ومن لم يجعل الله له نورا فما له من نور'),
(2832, 24, 41, 'ألم تر أن الله يسبح له من في السماوات والأرض والطير صافات ۖ كل قد علم صلاته وتسبيحه ۗ والله عليم بما يفعلون'),
(2833, 24, 42, 'ولله ملك السماوات والأرض ۖ وإلى الله المصير'),
(2834, 24, 43, 'ألم تر أن الله يزجي سحابا ثم يؤلف بينه ثم يجعله ركاما فترى الودق يخرج من خلاله وينزل من السماء من جبال فيها من برد فيصيب به من يشاء ويصرفه عن من يشاء ۖ يكاد سنا برقه يذهب بالأبصار'),
(2835, 24, 44, 'يقلب الله الليل والنهار ۚ إن في ذلك لعبرة لأولي الأبصار'),
(2836, 24, 45, 'والله خلق كل دابة من ماء ۖ فمنهم من يمشي على بطنه ومنهم من يمشي على رجلين ومنهم من يمشي على أربع ۚ يخلق الله ما يشاء ۚ إن الله على كل شيء قدير'),
(2837, 24, 46, 'لقد أنزلنا آيات مبينات ۚ والله يهدي من يشاء إلى صراط مستقيم'),
(2838, 24, 47, 'ويقولون آمنا بالله وبالرسول وأطعنا ثم يتولى فريق منهم من بعد ذلك ۚ وما أولئك بالمؤمنين'),
(2839, 24, 48, 'وإذا دعوا إلى الله ورسوله ليحكم بينهم إذا فريق منهم معرضون'),
(2840, 24, 49, 'وإن يكن لهم الحق يأتوا إليه مذعنين'),
(2841, 24, 50, 'أفي قلوبهم مرض أم ارتابوا أم يخافون أن يحيف الله عليهم ورسوله ۚ بل أولئك هم الظالمون'),
(2842, 24, 51, 'إنما كان قول المؤمنين إذا دعوا إلى الله ورسوله ليحكم بينهم أن يقولوا سمعنا وأطعنا ۚ وأولئك هم المفلحون'),
(2843, 24, 52, 'ومن يطع الله ورسوله ويخش الله ويتقه فأولئك هم الفائزون'),
(2844, 24, 53, 'وأقسموا بالله جهد أيمانهم لئن أمرتهم ليخرجن ۖ قل لا تقسموا ۖ طاعة معروفة ۚ إن الله خبير بما تعملون'),
(2845, 24, 54, 'قل أطيعوا الله وأطيعوا الرسول ۖ فإن تولوا فإنما عليه ما حمل وعليكم ما حملتم ۖ وإن تطيعوه تهتدوا ۚ وما على الرسول إلا البلاغ المبين'),
(2846, 24, 55, 'وعد الله الذين آمنوا منكم وعملوا الصالحات ليستخلفنهم في الأرض كما استخلف الذين من قبلهم وليمكنن لهم دينهم الذي ارتضى لهم وليبدلنهم من بعد خوفهم أمنا ۚ يعبدونني لا يشركون بي شيئا ۚ ومن كفر بعد ذلك فأولئك هم الفاسقون'),
(2847, 24, 56, 'وأقيموا الصلاة وآتوا الزكاة وأطيعوا الرسول لعلكم ترحمون'),
(2848, 24, 57, 'لا تحسبن الذين كفروا معجزين في الأرض ۚ ومأواهم النار ۖ ولبئس المصير'),
(2849, 24, 58, 'يا أيها الذين آمنوا ليستأذنكم الذين ملكت أيمانكم والذين لم يبلغوا الحلم منكم ثلاث مرات ۚ من قبل صلاة الفجر وحين تضعون ثيابكم من الظهيرة ومن بعد صلاة العشاء ۚ ثلاث عورات لكم ۚ ليس عليكم ولا عليهم جناح بعدهن ۚ طوافون عليكم بعضكم على بعض ۚ كذلك يبين الله لكم الآيات ۗ والله عليم حكيم'),
(2850, 24, 59, 'وإذا بلغ الأطفال منكم الحلم فليستأذنوا كما استأذن الذين من قبلهم ۚ كذلك يبين الله لكم آياته ۗ والله عليم حكيم'),
(2851, 24, 60, 'والقواعد من النساء اللاتي لا يرجون نكاحا فليس عليهن جناح أن يضعن ثيابهن غير متبرجات بزينة ۖ وأن يستعففن خير لهن ۗ والله سميع عليم'),
(2852, 24, 61, 'ليس على الأعمى حرج ولا على الأعرج حرج ولا على المريض حرج ولا على أنفسكم أن تأكلوا من بيوتكم أو بيوت آبائكم أو بيوت أمهاتكم أو بيوت إخوانكم أو بيوت أخواتكم أو بيوت أعمامكم أو بيوت عماتكم أو بيوت أخوالكم أو بيوت خالاتكم أو ما ملكتم مفاتحه أو صديقكم ۚ ليس عليكم جناح أن تأكلوا جميعا أو أشتاتا ۚ فإذا دخلتم بيوتا فسلموا على أنفسكم تحية من عند الله مباركة طيبة ۚ كذلك يبين الله لكم الآيات لعلكم تعقلون'),
(2853, 24, 62, 'إنما المؤمنون الذين آمنوا بالله ورسوله وإذا كانوا معه على أمر جامع لم يذهبوا حتى يستأذنوه ۚ إن الذين يستأذنونك أولئك الذين يؤمنون بالله ورسوله ۚ فإذا استأذنوك لبعض شأنهم فأذن لمن شئت منهم واستغفر لهم الله ۚ إن الله غفور رحيم'),
(2854, 24, 63, 'لا تجعلوا دعاء الرسول بينكم كدعاء بعضكم بعضا ۚ قد يعلم الله الذين يتسللون منكم لواذا ۚ فليحذر الذين يخالفون عن أمره أن تصيبهم فتنة أو يصيبهم عذاب أليم'),
(2855, 24, 64, 'ألا إن لله ما في السماوات والأرض ۖ قد يعلم ما أنتم عليه ويوم يرجعون إليه فينبئهم بما عملوا ۗ والله بكل شيء عليم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'بسم الله الرحمن الرحيم تبارك الذي نزل الفرقان على عبده ليكون للعالمين نذيرا'),
(2857, 25, 2, 'الذي له ملك السماوات والأرض ولم يتخذ ولدا ولم يكن له شريك في الملك وخلق كل شيء فقدره تقديرا'),
(2858, 25, 3, 'واتخذوا من دونه آلهة لا يخلقون شيئا وهم يخلقون ولا يملكون لأنفسهم ضرا ولا نفعا ولا يملكون موتا ولا حياة ولا نشورا'),
(2859, 25, 4, 'وقال الذين كفروا إن هذا إلا إفك افتراه وأعانه عليه قوم آخرون ۖ فقد جاءوا ظلما وزورا'),
(2860, 25, 5, 'وقالوا أساطير الأولين اكتتبها فهي تملى عليه بكرة وأصيلا'),
(2861, 25, 6, 'قل أنزله الذي يعلم السر في السماوات والأرض ۚ إنه كان غفورا رحيما'),
(2862, 25, 7, 'وقالوا مال هذا الرسول يأكل الطعام ويمشي في الأسواق ۙ لولا أنزل إليه ملك فيكون معه نذيرا'),
(2863, 25, 8, 'أو يلقى إليه كنز أو تكون له جنة يأكل منها ۚ وقال الظالمون إن تتبعون إلا رجلا مسحورا'),
(2864, 25, 9, 'انظر كيف ضربوا لك الأمثال فضلوا فلا يستطيعون سبيلا'),
(2865, 25, 10, 'تبارك الذي إن شاء جعل لك خيرا من ذلك جنات تجري من تحتها الأنهار ويجعل لك قصورا'),
(2866, 25, 11, 'بل كذبوا بالساعة ۖ وأعتدنا لمن كذب بالساعة سعيرا'),
(2867, 25, 12, 'إذا رأتهم من مكان بعيد سمعوا لها تغيظا وزفيرا'),
(2868, 25, 13, 'وإذا ألقوا منها مكانا ضيقا مقرنين دعوا هنالك ثبورا'),
(2869, 25, 14, 'لا تدعوا اليوم ثبورا واحدا وادعوا ثبورا كثيرا'),
(2870, 25, 15, 'قل أذلك خير أم جنة الخلد التي وعد المتقون ۚ كانت لهم جزاء ومصيرا'),
(2871, 25, 16, 'لهم فيها ما يشاءون خالدين ۚ كان على ربك وعدا مسئولا'),
(2872, 25, 17, 'ويوم يحشرهم وما يعبدون من دون الله فيقول أأنتم أضللتم عبادي هؤلاء أم هم ضلوا السبيل'),
(2873, 25, 18, 'قالوا سبحانك ما كان ينبغي لنا أن نتخذ من دونك من أولياء ولكن متعتهم وآباءهم حتى نسوا الذكر وكانوا قوما بورا'),
(2874, 25, 19, 'فقد كذبوكم بما تقولون فما تستطيعون صرفا ولا نصرا ۚ ومن يظلم منكم نذقه عذابا كبيرا'),
(2875, 25, 20, 'وما أرسلنا قبلك من المرسلين إلا إنهم ليأكلون الطعام ويمشون في الأسواق ۗ وجعلنا بعضكم لبعض فتنة أتصبرون ۗ وكان ربك بصيرا'),
(2876, 25, 21, 'وقال الذين لا يرجون لقاءنا لولا أنزل علينا الملائكة أو نرى ربنا ۗ لقد استكبروا في أنفسهم وعتوا عتوا كبيرا'),
(2877, 25, 22, 'يوم يرون الملائكة لا بشرى يومئذ للمجرمين ويقولون حجرا محجورا'),
(2878, 25, 23, 'وقدمنا إلى ما عملوا من عمل فجعلناه هباء منثورا'),
(2879, 25, 24, 'أصحاب الجنة يومئذ خير مستقرا وأحسن مقيلا'),
(2880, 25, 25, 'ويوم تشقق السماء بالغمام ونزل الملائكة تنزيلا'),
(2881, 25, 26, 'الملك يومئذ الحق للرحمن ۚ وكان يوما على الكافرين عسيرا'),
(2882, 25, 27, 'ويوم يعض الظالم على يديه يقول يا ليتني اتخذت مع الرسول سبيلا'),
(2883, 25, 28, 'يا ويلتى ليتني لم أتخذ فلانا خليلا'),
(2884, 25, 29, 'لقد أضلني عن الذكر بعد إذ جاءني ۗ وكان الشيطان للإنسان خذولا'),
(2885, 25, 30, 'وقال الرسول يا رب إن قومي اتخذوا هذا القرآن مهجورا'),
(2886, 25, 31, 'وكذلك جعلنا لكل نبي عدوا من المجرمين ۗ وكفى بربك هاديا ونصيرا'),
(2887, 25, 32, 'وقال الذين كفروا لولا نزل عليه القرآن جملة واحدة ۚ كذلك لنثبت به فؤادك ۖ ورتلناه ترتيلا'),
(2888, 25, 33, 'ولا يأتونك بمثل إلا جئناك بالحق وأحسن تفسيرا'),
(2889, 25, 34, 'الذين يحشرون على وجوههم إلى جهنم أولئك شر مكانا وأضل سبيلا'),
(2890, 25, 35, 'ولقد آتينا موسى الكتاب وجعلنا معه أخاه هارون وزيرا'),
(2891, 25, 36, 'فقلنا اذهبا إلى القوم الذين كذبوا بآياتنا فدمرناهم تدميرا'),
(2892, 25, 37, 'وقوم نوح لما كذبوا الرسل أغرقناهم وجعلناهم للناس آية ۖ وأعتدنا للظالمين عذابا أليما'),
(2893, 25, 38, 'وعادا وثمود وأصحاب الرس وقرونا بين ذلك كثيرا'),
(2894, 25, 39, 'وكلا ضربنا له الأمثال ۖ وكلا تبرنا تتبيرا'),
(2895, 25, 40, 'ولقد أتوا على القرية التي أمطرت مطر السوء ۚ أفلم يكونوا يرونها ۚ بل كانوا لا يرجون نشورا'),
(2896, 25, 41, 'وإذا رأوك إن يتخذونك إلا هزوا أهذا الذي بعث الله رسولا'),
(2897, 25, 42, 'إن كاد ليضلنا عن آلهتنا لولا أن صبرنا عليها ۚ وسوف يعلمون حين يرون العذاب من أضل سبيلا'),
(2898, 25, 43, 'أرأيت من اتخذ إلهه هواه أفأنت تكون عليه وكيلا'),
(2899, 25, 44, 'أم تحسب أن أكثرهم يسمعون أو يعقلون ۚ إن هم إلا كالأنعام ۖ بل هم أضل سبيلا'),
(2900, 25, 45, 'ألم تر إلى ربك كيف مد الظل ولو شاء لجعله ساكنا ثم جعلنا الشمس عليه دليلا'),
(2901, 25, 46, 'ثم قبضناه إلينا قبضا يسيرا'),
(2902, 25, 47, 'وهو الذي جعل لكم الليل لباسا والنوم سباتا وجعل النهار نشورا'),
(2903, 25, 48, 'وهو الذي أرسل الرياح بشرا بين يدي رحمته ۚ وأنزلنا من السماء ماء طهورا'),
(2904, 25, 49, 'لنحيي به بلدة ميتا ونسقيه مما خلقنا أنعاما وأناسي كثيرا'),
(2905, 25, 50, 'ولقد صرفناه بينهم ليذكروا فأبى أكثر الناس إلا كفورا'),
(2906, 25, 51, 'ولو شئنا لبعثنا في كل قرية نذيرا'),
(2907, 25, 52, 'فلا تطع الكافرين وجاهدهم به جهادا كبيرا'),
(2908, 25, 53, 'وهو الذي مرج البحرين هذا عذب فرات وهذا ملح أجاج وجعل بينهما برزخا وحجرا محجورا'),
(2909, 25, 54, 'وهو الذي خلق من الماء بشرا فجعله نسبا وصهرا ۗ وكان ربك قديرا'),
(2910, 25, 55, 'ويعبدون من دون الله ما لا ينفعهم ولا يضرهم ۗ وكان الكافر على ربه ظهيرا'),
(2911, 25, 56, 'وما أرسلناك إلا مبشرا ونذيرا'),
(2912, 25, 57, 'قل ما أسألكم عليه من أجر إلا من شاء أن يتخذ إلى ربه سبيلا'),
(2913, 25, 58, 'وتوكل على الحي الذي لا يموت وسبح بحمده ۚ وكفى به بذنوب عباده خبيرا'),
(2914, 25, 59, 'الذي خلق السماوات والأرض وما بينهما في ستة أيام ثم استوى على العرش ۚ الرحمن فاسأل به خبيرا'),
(2915, 25, 60, 'وإذا قيل لهم اسجدوا للرحمن قالوا وما الرحمن أنسجد لما تأمرنا وزادهم نفورا'),
(2916, 25, 61, 'تبارك الذي جعل في السماء بروجا وجعل فيها سراجا وقمرا منيرا'),
(2917, 25, 62, 'وهو الذي جعل الليل والنهار خلفة لمن أراد أن يذكر أو أراد شكورا'),
(2918, 25, 63, 'وعباد الرحمن الذين يمشون على الأرض هونا وإذا خاطبهم الجاهلون قالوا سلاما'),
(2919, 25, 64, 'والذين يبيتون لربهم سجدا وقياما'),
(2920, 25, 65, 'والذين يقولون ربنا اصرف عنا عذاب جهنم ۖ إن عذابها كان غراما'),
(2921, 25, 66, 'إنها ساءت مستقرا ومقاما'),
(2922, 25, 67, 'والذين إذا أنفقوا لم يسرفوا ولم يقتروا وكان بين ذلك قواما'),
(2923, 25, 68, 'والذين لا يدعون مع الله إلها آخر ولا يقتلون النفس التي حرم الله إلا بالحق ولا يزنون ۚ ومن يفعل ذلك يلق أثاما'),
(2924, 25, 69, 'يضاعف له العذاب يوم القيامة ويخلد فيه مهانا'),
(2925, 25, 70, 'إلا من تاب وآمن وعمل عملا صالحا فأولئك يبدل الله سيئاتهم حسنات ۗ وكان الله غفورا رحيما'),
(2926, 25, 71, 'ومن تاب وعمل صالحا فإنه يتوب إلى الله متابا'),
(2927, 25, 72, 'والذين لا يشهدون الزور وإذا مروا باللغو مروا كراما'),
(2928, 25, 73, 'والذين إذا ذكروا بآيات ربهم لم يخروا عليها صما وعميانا'),
(2929, 25, 74, 'والذين يقولون ربنا هب لنا من أزواجنا وذرياتنا قرة أعين واجعلنا للمتقين إماما'),
(2930, 25, 75, 'أولئك يجزون الغرفة بما صبروا ويلقون فيها تحية وسلاما'),
(2931, 25, 76, 'خالدين فيها ۚ حسنت مستقرا ومقاما'),
(2932, 25, 77, 'قل ما يعبأ بكم ربي لولا دعاؤكم ۖ فقد كذبتم فسوف يكون لزاما');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'بسم الله الرحمن الرحيم طسم'),
(2934, 26, 2, 'تلك آيات الكتاب المبين'),
(2935, 26, 3, 'لعلك باخع نفسك ألا يكونوا مؤمنين'),
(2936, 26, 4, 'إن نشأ ننزل عليهم من السماء آية فظلت أعناقهم لها خاضعين'),
(2937, 26, 5, 'وما يأتيهم من ذكر من الرحمن محدث إلا كانوا عنه معرضين'),
(2938, 26, 6, 'فقد كذبوا فسيأتيهم أنباء ما كانوا به يستهزئون'),
(2939, 26, 7, 'أولم يروا إلى الأرض كم أنبتنا فيها من كل زوج كريم'),
(2940, 26, 8, 'إن في ذلك لآية ۖ وما كان أكثرهم مؤمنين'),
(2941, 26, 9, 'وإن ربك لهو العزيز الرحيم'),
(2942, 26, 10, 'وإذ نادى ربك موسى أن ائت القوم الظالمين'),
(2943, 26, 11, 'قوم فرعون ۚ ألا يتقون'),
(2944, 26, 12, 'قال رب إني أخاف أن يكذبون'),
(2945, 26, 13, 'ويضيق صدري ولا ينطلق لساني فأرسل إلى هارون'),
(2946, 26, 14, 'ولهم علي ذنب فأخاف أن يقتلون'),
(2947, 26, 15, 'قال كلا ۖ فاذهبا بآياتنا ۖ إنا معكم مستمعون'),
(2948, 26, 16, 'فأتيا فرعون فقولا إنا رسول رب العالمين'),
(2949, 26, 17, 'أن أرسل معنا بني إسرائيل'),
(2950, 26, 18, 'قال ألم نربك فينا وليدا ولبثت فينا من عمرك سنين'),
(2951, 26, 19, 'وفعلت فعلتك التي فعلت وأنت من الكافرين'),
(2952, 26, 20, 'قال فعلتها إذا وأنا من الضالين'),
(2953, 26, 21, 'ففررت منكم لما خفتكم فوهب لي ربي حكما وجعلني من المرسلين'),
(2954, 26, 22, 'وتلك نعمة تمنها علي أن عبدت بني إسرائيل'),
(2955, 26, 23, 'قال فرعون وما رب العالمين'),
(2956, 26, 24, 'قال رب السماوات والأرض وما بينهما ۖ إن كنتم موقنين'),
(2957, 26, 25, 'قال لمن حوله ألا تستمعون'),
(2958, 26, 26, 'قال ربكم ورب آبائكم الأولين'),
(2959, 26, 27, 'قال إن رسولكم الذي أرسل إليكم لمجنون'),
(2960, 26, 28, 'قال رب المشرق والمغرب وما بينهما ۖ إن كنتم تعقلون'),
(2961, 26, 29, 'قال لئن اتخذت إلها غيري لأجعلنك من المسجونين'),
(2962, 26, 30, 'قال أولو جئتك بشيء مبين'),
(2963, 26, 31, 'قال فأت به إن كنت من الصادقين'),
(2964, 26, 32, 'فألقى عصاه فإذا هي ثعبان مبين'),
(2965, 26, 33, 'ونزع يده فإذا هي بيضاء للناظرين'),
(2966, 26, 34, 'قال للملإ حوله إن هذا لساحر عليم'),
(2967, 26, 35, 'يريد أن يخرجكم من أرضكم بسحره فماذا تأمرون'),
(2968, 26, 36, 'قالوا أرجه وأخاه وابعث في المدائن حاشرين'),
(2969, 26, 37, 'يأتوك بكل سحار عليم'),
(2970, 26, 38, 'فجمع السحرة لميقات يوم معلوم'),
(2971, 26, 39, 'وقيل للناس هل أنتم مجتمعون'),
(2972, 26, 40, 'لعلنا نتبع السحرة إن كانوا هم الغالبين'),
(2973, 26, 41, 'فلما جاء السحرة قالوا لفرعون أئن لنا لأجرا إن كنا نحن الغالبين'),
(2974, 26, 42, 'قال نعم وإنكم إذا لمن المقربين'),
(2975, 26, 43, 'قال لهم موسى ألقوا ما أنتم ملقون'),
(2976, 26, 44, 'فألقوا حبالهم وعصيهم وقالوا بعزة فرعون إنا لنحن الغالبون'),
(2977, 26, 45, 'فألقى موسى عصاه فإذا هي تلقف ما يأفكون'),
(2978, 26, 46, 'فألقي السحرة ساجدين'),
(2979, 26, 47, 'قالوا آمنا برب العالمين'),
(2980, 26, 48, 'رب موسى وهارون'),
(2981, 26, 49, 'قال آمنتم له قبل أن آذن لكم ۖ إنه لكبيركم الذي علمكم السحر فلسوف تعلمون ۚ لأقطعن أيديكم وأرجلكم من خلاف ولأصلبنكم أجمعين'),
(2982, 26, 50, 'قالوا لا ضير ۖ إنا إلى ربنا منقلبون'),
(2983, 26, 51, 'إنا نطمع أن يغفر لنا ربنا خطايانا أن كنا أول المؤمنين'),
(2984, 26, 52, 'وأوحينا إلى موسى أن أسر بعبادي إنكم متبعون'),
(2985, 26, 53, 'فأرسل فرعون في المدائن حاشرين'),
(2986, 26, 54, 'إن هؤلاء لشرذمة قليلون'),
(2987, 26, 55, 'وإنهم لنا لغائظون'),
(2988, 26, 56, 'وإنا لجميع حاذرون'),
(2989, 26, 57, 'فأخرجناهم من جنات وعيون'),
(2990, 26, 58, 'وكنوز ومقام كريم'),
(2991, 26, 59, 'كذلك وأورثناها بني إسرائيل'),
(2992, 26, 60, 'فأتبعوهم مشرقين'),
(2993, 26, 61, 'فلما تراءى الجمعان قال أصحاب موسى إنا لمدركون'),
(2994, 26, 62, 'قال كلا ۖ إن معي ربي سيهدين'),
(2995, 26, 63, 'فأوحينا إلى موسى أن اضرب بعصاك البحر ۖ فانفلق فكان كل فرق كالطود العظيم'),
(2996, 26, 64, 'وأزلفنا ثم الآخرين'),
(2997, 26, 65, 'وأنجينا موسى ومن معه أجمعين'),
(2998, 26, 66, 'ثم أغرقنا الآخرين'),
(2999, 26, 67, 'إن في ذلك لآية ۖ وما كان أكثرهم مؤمنين'),
(3000, 26, 68, 'وإن ربك لهو العزيز الرحيم'),
(3001, 26, 69, 'واتل عليهم نبأ إبراهيم'),
(3002, 26, 70, 'إذ قال لأبيه وقومه ما تعبدون'),
(3003, 26, 71, 'قالوا نعبد أصناما فنظل لها عاكفين'),
(3004, 26, 72, 'قال هل يسمعونكم إذ تدعون'),
(3005, 26, 73, 'أو ينفعونكم أو يضرون'),
(3006, 26, 74, 'قالوا بل وجدنا آباءنا كذلك يفعلون'),
(3007, 26, 75, 'قال أفرأيتم ما كنتم تعبدون'),
(3008, 26, 76, 'أنتم وآباؤكم الأقدمون'),
(3009, 26, 77, 'فإنهم عدو لي إلا رب العالمين'),
(3010, 26, 78, 'الذي خلقني فهو يهدين'),
(3011, 26, 79, 'والذي هو يطعمني ويسقين'),
(3012, 26, 80, 'وإذا مرضت فهو يشفين'),
(3013, 26, 81, 'والذي يميتني ثم يحيين'),
(3014, 26, 82, 'والذي أطمع أن يغفر لي خطيئتي يوم الدين'),
(3015, 26, 83, 'رب هب لي حكما وألحقني بالصالحين'),
(3016, 26, 84, 'واجعل لي لسان صدق في الآخرين'),
(3017, 26, 85, 'واجعلني من ورثة جنة النعيم'),
(3018, 26, 86, 'واغفر لأبي إنه كان من الضالين'),
(3019, 26, 87, 'ولا تخزني يوم يبعثون'),
(3020, 26, 88, 'يوم لا ينفع مال ولا بنون'),
(3021, 26, 89, 'إلا من أتى الله بقلب سليم'),
(3022, 26, 90, 'وأزلفت الجنة للمتقين'),
(3023, 26, 91, 'وبرزت الجحيم للغاوين'),
(3024, 26, 92, 'وقيل لهم أين ما كنتم تعبدون'),
(3025, 26, 93, 'من دون الله هل ينصرونكم أو ينتصرون'),
(3026, 26, 94, 'فكبكبوا فيها هم والغاوون'),
(3027, 26, 95, 'وجنود إبليس أجمعون'),
(3028, 26, 96, 'قالوا وهم فيها يختصمون'),
(3029, 26, 97, 'تالله إن كنا لفي ضلال مبين'),
(3030, 26, 98, 'إذ نسويكم برب العالمين'),
(3031, 26, 99, 'وما أضلنا إلا المجرمون'),
(3032, 26, 100, 'فما لنا من شافعين'),
(3033, 26, 101, 'ولا صديق حميم'),
(3034, 26, 102, 'فلو أن لنا كرة فنكون من المؤمنين'),
(3035, 26, 103, 'إن في ذلك لآية ۖ وما كان أكثرهم مؤمنين'),
(3036, 26, 104, 'وإن ربك لهو العزيز الرحيم'),
(3037, 26, 105, 'كذبت قوم نوح المرسلين'),
(3038, 26, 106, 'إذ قال لهم أخوهم نوح ألا تتقون'),
(3039, 26, 107, 'إني لكم رسول أمين'),
(3040, 26, 108, 'فاتقوا الله وأطيعون'),
(3041, 26, 109, 'وما أسألكم عليه من أجر ۖ إن أجري إلا على رب العالمين'),
(3042, 26, 110, 'فاتقوا الله وأطيعون'),
(3043, 26, 111, 'قالوا أنؤمن لك واتبعك الأرذلون'),
(3044, 26, 112, 'قال وما علمي بما كانوا يعملون'),
(3045, 26, 113, 'إن حسابهم إلا على ربي ۖ لو تشعرون'),
(3046, 26, 114, 'وما أنا بطارد المؤمنين'),
(3047, 26, 115, 'إن أنا إلا نذير مبين'),
(3048, 26, 116, 'قالوا لئن لم تنته يا نوح لتكونن من المرجومين'),
(3049, 26, 117, 'قال رب إن قومي كذبون'),
(3050, 26, 118, 'فافتح بيني وبينهم فتحا ونجني ومن معي من المؤمنين'),
(3051, 26, 119, 'فأنجيناه ومن معه في الفلك المشحون'),
(3052, 26, 120, 'ثم أغرقنا بعد الباقين'),
(3053, 26, 121, 'إن في ذلك لآية ۖ وما كان أكثرهم مؤمنين'),
(3054, 26, 122, 'وإن ربك لهو العزيز الرحيم'),
(3055, 26, 123, 'كذبت عاد المرسلين'),
(3056, 26, 124, 'إذ قال لهم أخوهم هود ألا تتقون'),
(3057, 26, 125, 'إني لكم رسول أمين'),
(3058, 26, 126, 'فاتقوا الله وأطيعون'),
(3059, 26, 127, 'وما أسألكم عليه من أجر ۖ إن أجري إلا على رب العالمين'),
(3060, 26, 128, 'أتبنون بكل ريع آية تعبثون'),
(3061, 26, 129, 'وتتخذون مصانع لعلكم تخلدون'),
(3062, 26, 130, 'وإذا بطشتم بطشتم جبارين'),
(3063, 26, 131, 'فاتقوا الله وأطيعون'),
(3064, 26, 132, 'واتقوا الذي أمدكم بما تعلمون'),
(3065, 26, 133, 'أمدكم بأنعام وبنين'),
(3066, 26, 134, 'وجنات وعيون'),
(3067, 26, 135, 'إني أخاف عليكم عذاب يوم عظيم'),
(3068, 26, 136, 'قالوا سواء علينا أوعظت أم لم تكن من الواعظين'),
(3069, 26, 137, 'إن هذا إلا خلق الأولين'),
(3070, 26, 138, 'وما نحن بمعذبين'),
(3071, 26, 139, 'فكذبوه فأهلكناهم ۗ إن في ذلك لآية ۖ وما كان أكثرهم مؤمنين'),
(3072, 26, 140, 'وإن ربك لهو العزيز الرحيم'),
(3073, 26, 141, 'كذبت ثمود المرسلين'),
(3074, 26, 142, 'إذ قال لهم أخوهم صالح ألا تتقون'),
(3075, 26, 143, 'إني لكم رسول أمين'),
(3076, 26, 144, 'فاتقوا الله وأطيعون'),
(3077, 26, 145, 'وما أسألكم عليه من أجر ۖ إن أجري إلا على رب العالمين'),
(3078, 26, 146, 'أتتركون في ما هاهنا آمنين'),
(3079, 26, 147, 'في جنات وعيون'),
(3080, 26, 148, 'وزروع ونخل طلعها هضيم'),
(3081, 26, 149, 'وتنحتون من الجبال بيوتا فارهين'),
(3082, 26, 150, 'فاتقوا الله وأطيعون'),
(3083, 26, 151, 'ولا تطيعوا أمر المسرفين'),
(3084, 26, 152, 'الذين يفسدون في الأرض ولا يصلحون'),
(3085, 26, 153, 'قالوا إنما أنت من المسحرين'),
(3086, 26, 154, 'ما أنت إلا بشر مثلنا فأت بآية إن كنت من الصادقين'),
(3087, 26, 155, 'قال هذه ناقة لها شرب ولكم شرب يوم معلوم'),
(3088, 26, 156, 'ولا تمسوها بسوء فيأخذكم عذاب يوم عظيم'),
(3089, 26, 157, 'فعقروها فأصبحوا نادمين'),
(3090, 26, 158, 'فأخذهم العذاب ۗ إن في ذلك لآية ۖ وما كان أكثرهم مؤمنين'),
(3091, 26, 159, 'وإن ربك لهو العزيز الرحيم'),
(3092, 26, 160, 'كذبت قوم لوط المرسلين'),
(3093, 26, 161, 'إذ قال لهم أخوهم لوط ألا تتقون'),
(3094, 26, 162, 'إني لكم رسول أمين'),
(3095, 26, 163, 'فاتقوا الله وأطيعون'),
(3096, 26, 164, 'وما أسألكم عليه من أجر ۖ إن أجري إلا على رب العالمين'),
(3097, 26, 165, 'أتأتون الذكران من العالمين'),
(3098, 26, 166, 'وتذرون ما خلق لكم ربكم من أزواجكم ۚ بل أنتم قوم عادون'),
(3099, 26, 167, 'قالوا لئن لم تنته يا لوط لتكونن من المخرجين'),
(3100, 26, 168, 'قال إني لعملكم من القالين'),
(3101, 26, 169, 'رب نجني وأهلي مما يعملون'),
(3102, 26, 170, 'فنجيناه وأهله أجمعين'),
(3103, 26, 171, 'إلا عجوزا في الغابرين'),
(3104, 26, 172, 'ثم دمرنا الآخرين'),
(3105, 26, 173, 'وأمطرنا عليهم مطرا ۖ فساء مطر المنذرين'),
(3106, 26, 174, 'إن في ذلك لآية ۖ وما كان أكثرهم مؤمنين'),
(3107, 26, 175, 'وإن ربك لهو العزيز الرحيم'),
(3108, 26, 176, 'كذب أصحاب الأيكة المرسلين'),
(3109, 26, 177, 'إذ قال لهم شعيب ألا تتقون'),
(3110, 26, 178, 'إني لكم رسول أمين'),
(3111, 26, 179, 'فاتقوا الله وأطيعون'),
(3112, 26, 180, 'وما أسألكم عليه من أجر ۖ إن أجري إلا على رب العالمين'),
(3113, 26, 181, 'أوفوا الكيل ولا تكونوا من المخسرين'),
(3114, 26, 182, 'وزنوا بالقسطاس المستقيم'),
(3115, 26, 183, 'ولا تبخسوا الناس أشياءهم ولا تعثوا في الأرض مفسدين'),
(3116, 26, 184, 'واتقوا الذي خلقكم والجبلة الأولين'),
(3117, 26, 185, 'قالوا إنما أنت من المسحرين'),
(3118, 26, 186, 'وما أنت إلا بشر مثلنا وإن نظنك لمن الكاذبين'),
(3119, 26, 187, 'فأسقط علينا كسفا من السماء إن كنت من الصادقين'),
(3120, 26, 188, 'قال ربي أعلم بما تعملون'),
(3121, 26, 189, 'فكذبوه فأخذهم عذاب يوم الظلة ۚ إنه كان عذاب يوم عظيم'),
(3122, 26, 190, 'إن في ذلك لآية ۖ وما كان أكثرهم مؤمنين'),
(3123, 26, 191, 'وإن ربك لهو العزيز الرحيم'),
(3124, 26, 192, 'وإنه لتنزيل رب العالمين'),
(3125, 26, 193, 'نزل به الروح الأمين'),
(3126, 26, 194, 'على قلبك لتكون من المنذرين'),
(3127, 26, 195, 'بلسان عربي مبين'),
(3128, 26, 196, 'وإنه لفي زبر الأولين'),
(3129, 26, 197, 'أولم يكن لهم آية أن يعلمه علماء بني إسرائيل'),
(3130, 26, 198, 'ولو نزلناه على بعض الأعجمين'),
(3131, 26, 199, 'فقرأه عليهم ما كانوا به مؤمنين'),
(3132, 26, 200, 'كذلك سلكناه في قلوب المجرمين'),
(3133, 26, 201, 'لا يؤمنون به حتى يروا العذاب الأليم'),
(3134, 26, 202, 'فيأتيهم بغتة وهم لا يشعرون'),
(3135, 26, 203, 'فيقولوا هل نحن منظرون'),
(3136, 26, 204, 'أفبعذابنا يستعجلون'),
(3137, 26, 205, 'أفرأيت إن متعناهم سنين'),
(3138, 26, 206, 'ثم جاءهم ما كانوا يوعدون'),
(3139, 26, 207, 'ما أغنى عنهم ما كانوا يمتعون'),
(3140, 26, 208, 'وما أهلكنا من قرية إلا لها منذرون'),
(3141, 26, 209, 'ذكرى وما كنا ظالمين'),
(3142, 26, 210, 'وما تنزلت به الشياطين'),
(3143, 26, 211, 'وما ينبغي لهم وما يستطيعون'),
(3144, 26, 212, 'إنهم عن السمع لمعزولون'),
(3145, 26, 213, 'فلا تدع مع الله إلها آخر فتكون من المعذبين'),
(3146, 26, 214, 'وأنذر عشيرتك الأقربين'),
(3147, 26, 215, 'واخفض جناحك لمن اتبعك من المؤمنين'),
(3148, 26, 216, 'فإن عصوك فقل إني بريء مما تعملون'),
(3149, 26, 217, 'وتوكل على العزيز الرحيم'),
(3150, 26, 218, 'الذي يراك حين تقوم'),
(3151, 26, 219, 'وتقلبك في الساجدين'),
(3152, 26, 220, 'إنه هو السميع العليم'),
(3153, 26, 221, 'هل أنبئكم على من تنزل الشياطين'),
(3154, 26, 222, 'تنزل على كل أفاك أثيم'),
(3155, 26, 223, 'يلقون السمع وأكثرهم كاذبون'),
(3156, 26, 224, 'والشعراء يتبعهم الغاوون'),
(3157, 26, 225, 'ألم تر أنهم في كل واد يهيمون'),
(3158, 26, 226, 'وأنهم يقولون ما لا يفعلون'),
(3159, 26, 227, 'إلا الذين آمنوا وعملوا الصالحات وذكروا الله كثيرا وانتصروا من بعد ما ظلموا ۗ وسيعلم الذين ظلموا أي منقلب ينقلبون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'بسم الله الرحمن الرحيم طس ۚ تلك آيات القرآن وكتاب مبين'),
(3161, 27, 2, 'هدى وبشرى للمؤمنين'),
(3162, 27, 3, 'الذين يقيمون الصلاة ويؤتون الزكاة وهم بالآخرة هم يوقنون'),
(3163, 27, 4, 'إن الذين لا يؤمنون بالآخرة زينا لهم أعمالهم فهم يعمهون'),
(3164, 27, 5, 'أولئك الذين لهم سوء العذاب وهم في الآخرة هم الأخسرون'),
(3165, 27, 6, 'وإنك لتلقى القرآن من لدن حكيم عليم'),
(3166, 27, 7, 'إذ قال موسى لأهله إني آنست نارا سآتيكم منها بخبر أو آتيكم بشهاب قبس لعلكم تصطلون'),
(3167, 27, 8, 'فلما جاءها نودي أن بورك من في النار ومن حولها وسبحان الله رب العالمين'),
(3168, 27, 9, 'يا موسى إنه أنا الله العزيز الحكيم'),
(3169, 27, 10, 'وألق عصاك ۚ فلما رآها تهتز كأنها جان ولى مدبرا ولم يعقب ۚ يا موسى لا تخف إني لا يخاف لدي المرسلون'),
(3170, 27, 11, 'إلا من ظلم ثم بدل حسنا بعد سوء فإني غفور رحيم'),
(3171, 27, 12, 'وأدخل يدك في جيبك تخرج بيضاء من غير سوء ۖ في تسع آيات إلى فرعون وقومه ۚ إنهم كانوا قوما فاسقين'),
(3172, 27, 13, 'فلما جاءتهم آياتنا مبصرة قالوا هذا سحر مبين'),
(3173, 27, 14, 'وجحدوا بها واستيقنتها أنفسهم ظلما وعلوا ۚ فانظر كيف كان عاقبة المفسدين'),
(3174, 27, 15, 'ولقد آتينا داوود وسليمان علما ۖ وقالا الحمد لله الذي فضلنا على كثير من عباده المؤمنين'),
(3175, 27, 16, 'وورث سليمان داوود ۖ وقال يا أيها الناس علمنا منطق الطير وأوتينا من كل شيء ۖ إن هذا لهو الفضل المبين'),
(3176, 27, 17, 'وحشر لسليمان جنوده من الجن والإنس والطير فهم يوزعون'),
(3177, 27, 18, 'حتى إذا أتوا على واد النمل قالت نملة يا أيها النمل ادخلوا مساكنكم لا يحطمنكم سليمان وجنوده وهم لا يشعرون'),
(3178, 27, 19, 'فتبسم ضاحكا من قولها وقال رب أوزعني أن أشكر نعمتك التي أنعمت علي وعلى والدي وأن أعمل صالحا ترضاه وأدخلني برحمتك في عبادك الصالحين'),
(3179, 27, 20, 'وتفقد الطير فقال ما لي لا أرى الهدهد أم كان من الغائبين'),
(3180, 27, 21, 'لأعذبنه عذابا شديدا أو لأذبحنه أو ليأتيني بسلطان مبين'),
(3181, 27, 22, 'فمكث غير بعيد فقال أحطت بما لم تحط به وجئتك من سبإ بنبإ يقين'),
(3182, 27, 23, 'إني وجدت امرأة تملكهم وأوتيت من كل شيء ولها عرش عظيم'),
(3183, 27, 24, 'وجدتها وقومها يسجدون للشمس من دون الله وزين لهم الشيطان أعمالهم فصدهم عن السبيل فهم لا يهتدون'),
(3184, 27, 25, 'ألا يسجدوا لله الذي يخرج الخبء في السماوات والأرض ويعلم ما تخفون وما تعلنون'),
(3185, 27, 26, 'الله لا إله إلا هو رب العرش العظيم'),
(3186, 27, 27, 'قال سننظر أصدقت أم كنت من الكاذبين'),
(3187, 27, 28, 'اذهب بكتابي هذا فألقه إليهم ثم تول عنهم فانظر ماذا يرجعون'),
(3188, 27, 29, 'قالت يا أيها الملأ إني ألقي إلي كتاب كريم'),
(3189, 27, 30, 'إنه من سليمان وإنه بسم الله الرحمن الرحيم'),
(3190, 27, 31, 'ألا تعلوا علي وأتوني مسلمين'),
(3191, 27, 32, 'قالت يا أيها الملأ أفتوني في أمري ما كنت قاطعة أمرا حتى تشهدون'),
(3192, 27, 33, 'قالوا نحن أولو قوة وأولو بأس شديد والأمر إليك فانظري ماذا تأمرين'),
(3193, 27, 34, 'قالت إن الملوك إذا دخلوا قرية أفسدوها وجعلوا أعزة أهلها أذلة ۖ وكذلك يفعلون'),
(3194, 27, 35, 'وإني مرسلة إليهم بهدية فناظرة بم يرجع المرسلون'),
(3195, 27, 36, 'فلما جاء سليمان قال أتمدونن بمال فما آتاني الله خير مما آتاكم بل أنتم بهديتكم تفرحون'),
(3196, 27, 37, 'ارجع إليهم فلنأتينهم بجنود لا قبل لهم بها ولنخرجنهم منها أذلة وهم صاغرون'),
(3197, 27, 38, 'قال يا أيها الملأ أيكم يأتيني بعرشها قبل أن يأتوني مسلمين'),
(3198, 27, 39, 'قال عفريت من الجن أنا آتيك به قبل أن تقوم من مقامك ۖ وإني عليه لقوي أمين'),
(3199, 27, 40, 'قال الذي عنده علم من الكتاب أنا آتيك به قبل أن يرتد إليك طرفك ۚ فلما رآه مستقرا عنده قال هذا من فضل ربي ليبلوني أأشكر أم أكفر ۖ ومن شكر فإنما يشكر لنفسه ۖ ومن كفر فإن ربي غني كريم'),
(3200, 27, 41, 'قال نكروا لها عرشها ننظر أتهتدي أم تكون من الذين لا يهتدون'),
(3201, 27, 42, 'فلما جاءت قيل أهكذا عرشك ۖ قالت كأنه هو ۚ وأوتينا العلم من قبلها وكنا مسلمين'),
(3202, 27, 43, 'وصدها ما كانت تعبد من دون الله ۖ إنها كانت من قوم كافرين'),
(3203, 27, 44, 'قيل لها ادخلي الصرح ۖ فلما رأته حسبته لجة وكشفت عن ساقيها ۚ قال إنه صرح ممرد من قوارير ۗ قالت رب إني ظلمت نفسي وأسلمت مع سليمان لله رب العالمين'),
(3204, 27, 45, 'ولقد أرسلنا إلى ثمود أخاهم صالحا أن اعبدوا الله فإذا هم فريقان يختصمون'),
(3205, 27, 46, 'قال يا قوم لم تستعجلون بالسيئة قبل الحسنة ۖ لولا تستغفرون الله لعلكم ترحمون'),
(3206, 27, 47, 'قالوا اطيرنا بك وبمن معك ۚ قال طائركم عند الله ۖ بل أنتم قوم تفتنون'),
(3207, 27, 48, 'وكان في المدينة تسعة رهط يفسدون في الأرض ولا يصلحون'),
(3208, 27, 49, 'قالوا تقاسموا بالله لنبيتنه وأهله ثم لنقولن لوليه ما شهدنا مهلك أهله وإنا لصادقون'),
(3209, 27, 50, 'ومكروا مكرا ومكرنا مكرا وهم لا يشعرون'),
(3210, 27, 51, 'فانظر كيف كان عاقبة مكرهم أنا دمرناهم وقومهم أجمعين'),
(3211, 27, 52, 'فتلك بيوتهم خاوية بما ظلموا ۗ إن في ذلك لآية لقوم يعلمون'),
(3212, 27, 53, 'وأنجينا الذين آمنوا وكانوا يتقون'),
(3213, 27, 54, 'ولوطا إذ قال لقومه أتأتون الفاحشة وأنتم تبصرون'),
(3214, 27, 55, 'أئنكم لتأتون الرجال شهوة من دون النساء ۚ بل أنتم قوم تجهلون'),
(3215, 27, 56, 'فما كان جواب قومه إلا أن قالوا أخرجوا آل لوط من قريتكم ۖ إنهم أناس يتطهرون'),
(3216, 27, 57, 'فأنجيناه وأهله إلا امرأته قدرناها من الغابرين'),
(3217, 27, 58, 'وأمطرنا عليهم مطرا ۖ فساء مطر المنذرين'),
(3218, 27, 59, 'قل الحمد لله وسلام على عباده الذين اصطفى ۗ آلله خير أما يشركون'),
(3219, 27, 60, 'أمن خلق السماوات والأرض وأنزل لكم من السماء ماء فأنبتنا به حدائق ذات بهجة ما كان لكم أن تنبتوا شجرها ۗ أإله مع الله ۚ بل هم قوم يعدلون'),
(3220, 27, 61, 'أمن جعل الأرض قرارا وجعل خلالها أنهارا وجعل لها رواسي وجعل بين البحرين حاجزا ۗ أإله مع الله ۚ بل أكثرهم لا يعلمون'),
(3221, 27, 62, 'أمن يجيب المضطر إذا دعاه ويكشف السوء ويجعلكم خلفاء الأرض ۗ أإله مع الله ۚ قليلا ما تذكرون'),
(3222, 27, 63, 'أمن يهديكم في ظلمات البر والبحر ومن يرسل الرياح بشرا بين يدي رحمته ۗ أإله مع الله ۚ تعالى الله عما يشركون'),
(3223, 27, 64, 'أمن يبدأ الخلق ثم يعيده ومن يرزقكم من السماء والأرض ۗ أإله مع الله ۚ قل هاتوا برهانكم إن كنتم صادقين'),
(3224, 27, 65, 'قل لا يعلم من في السماوات والأرض الغيب إلا الله ۚ وما يشعرون أيان يبعثون'),
(3225, 27, 66, 'بل ادارك علمهم في الآخرة ۚ بل هم في شك منها ۖ بل هم منها عمون'),
(3226, 27, 67, 'وقال الذين كفروا أإذا كنا ترابا وآباؤنا أئنا لمخرجون'),
(3227, 27, 68, 'لقد وعدنا هذا نحن وآباؤنا من قبل إن هذا إلا أساطير الأولين'),
(3228, 27, 69, 'قل سيروا في الأرض فانظروا كيف كان عاقبة المجرمين'),
(3229, 27, 70, 'ولا تحزن عليهم ولا تكن في ضيق مما يمكرون'),
(3230, 27, 71, 'ويقولون متى هذا الوعد إن كنتم صادقين'),
(3231, 27, 72, 'قل عسى أن يكون ردف لكم بعض الذي تستعجلون'),
(3232, 27, 73, 'وإن ربك لذو فضل على الناس ولكن أكثرهم لا يشكرون'),
(3233, 27, 74, 'وإن ربك ليعلم ما تكن صدورهم وما يعلنون'),
(3234, 27, 75, 'وما من غائبة في السماء والأرض إلا في كتاب مبين'),
(3235, 27, 76, 'إن هذا القرآن يقص على بني إسرائيل أكثر الذي هم فيه يختلفون'),
(3236, 27, 77, 'وإنه لهدى ورحمة للمؤمنين'),
(3237, 27, 78, 'إن ربك يقضي بينهم بحكمه ۚ وهو العزيز العليم'),
(3238, 27, 79, 'فتوكل على الله ۖ إنك على الحق المبين'),
(3239, 27, 80, 'إنك لا تسمع الموتى ولا تسمع الصم الدعاء إذا ولوا مدبرين'),
(3240, 27, 81, 'وما أنت بهادي العمي عن ضلالتهم ۖ إن تسمع إلا من يؤمن بآياتنا فهم مسلمون'),
(3241, 27, 82, 'وإذا وقع القول عليهم أخرجنا لهم دابة من الأرض تكلمهم أن الناس كانوا بآياتنا لا يوقنون'),
(3242, 27, 83, 'ويوم نحشر من كل أمة فوجا ممن يكذب بآياتنا فهم يوزعون'),
(3243, 27, 84, 'حتى إذا جاءوا قال أكذبتم بآياتي ولم تحيطوا بها علما أماذا كنتم تعملون'),
(3244, 27, 85, 'ووقع القول عليهم بما ظلموا فهم لا ينطقون'),
(3245, 27, 86, 'ألم يروا أنا جعلنا الليل ليسكنوا فيه والنهار مبصرا ۚ إن في ذلك لآيات لقوم يؤمنون'),
(3246, 27, 87, 'ويوم ينفخ في الصور ففزع من في السماوات ومن في الأرض إلا من شاء الله ۚ وكل أتوه داخرين'),
(3247, 27, 88, 'وترى الجبال تحسبها جامدة وهي تمر مر السحاب ۚ صنع الله الذي أتقن كل شيء ۚ إنه خبير بما تفعلون'),
(3248, 27, 89, 'من جاء بالحسنة فله خير منها وهم من فزع يومئذ آمنون'),
(3249, 27, 90, 'ومن جاء بالسيئة فكبت وجوههم في النار هل تجزون إلا ما كنتم تعملون'),
(3250, 27, 91, 'إنما أمرت أن أعبد رب هذه البلدة الذي حرمها وله كل شيء ۖ وأمرت أن أكون من المسلمين'),
(3251, 27, 92, 'وأن أتلو القرآن ۖ فمن اهتدى فإنما يهتدي لنفسه ۖ ومن ضل فقل إنما أنا من المنذرين'),
(3252, 27, 93, 'وقل الحمد لله سيريكم آياته فتعرفونها ۚ وما ربك بغافل عما تعملون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'بسم الله الرحمن الرحيم طسم'),
(3254, 28, 2, 'تلك آيات الكتاب المبين'),
(3255, 28, 3, 'نتلو عليك من نبإ موسى وفرعون بالحق لقوم يؤمنون'),
(3256, 28, 4, 'إن فرعون علا في الأرض وجعل أهلها شيعا يستضعف طائفة منهم يذبح أبناءهم ويستحيي نساءهم ۚ إنه كان من المفسدين'),
(3257, 28, 5, 'ونريد أن نمن على الذين استضعفوا في الأرض ونجعلهم أئمة ونجعلهم الوارثين'),
(3258, 28, 6, 'ونمكن لهم في الأرض ونري فرعون وهامان وجنودهما منهم ما كانوا يحذرون'),
(3259, 28, 7, 'وأوحينا إلى أم موسى أن أرضعيه ۖ فإذا خفت عليه فألقيه في اليم ولا تخافي ولا تحزني ۖ إنا رادوه إليك وجاعلوه من المرسلين'),
(3260, 28, 8, 'فالتقطه آل فرعون ليكون لهم عدوا وحزنا ۗ إن فرعون وهامان وجنودهما كانوا خاطئين'),
(3261, 28, 9, 'وقالت امرأت فرعون قرت عين لي ولك ۖ لا تقتلوه عسى أن ينفعنا أو نتخذه ولدا وهم لا يشعرون'),
(3262, 28, 10, 'وأصبح فؤاد أم موسى فارغا ۖ إن كادت لتبدي به لولا أن ربطنا على قلبها لتكون من المؤمنين'),
(3263, 28, 11, 'وقالت لأخته قصيه ۖ فبصرت به عن جنب وهم لا يشعرون'),
(3264, 28, 12, 'وحرمنا عليه المراضع من قبل فقالت هل أدلكم على أهل بيت يكفلونه لكم وهم له ناصحون'),
(3265, 28, 13, 'فرددناه إلى أمه كي تقر عينها ولا تحزن ولتعلم أن وعد الله حق ولكن أكثرهم لا يعلمون'),
(3266, 28, 14, 'ولما بلغ أشده واستوى آتيناه حكما وعلما ۚ وكذلك نجزي المحسنين'),
(3267, 28, 15, 'ودخل المدينة على حين غفلة من أهلها فوجد فيها رجلين يقتتلان هذا من شيعته وهذا من عدوه ۖ فاستغاثه الذي من شيعته على الذي من عدوه فوكزه موسى فقضى عليه ۖ قال هذا من عمل الشيطان ۖ إنه عدو مضل مبين'),
(3268, 28, 16, 'قال رب إني ظلمت نفسي فاغفر لي فغفر له ۚ إنه هو الغفور الرحيم'),
(3269, 28, 17, 'قال رب بما أنعمت علي فلن أكون ظهيرا للمجرمين'),
(3270, 28, 18, 'فأصبح في المدينة خائفا يترقب فإذا الذي استنصره بالأمس يستصرخه ۚ قال له موسى إنك لغوي مبين'),
(3271, 28, 19, 'فلما أن أراد أن يبطش بالذي هو عدو لهما قال يا موسى أتريد أن تقتلني كما قتلت نفسا بالأمس ۖ إن تريد إلا أن تكون جبارا في الأرض وما تريد أن تكون من المصلحين'),
(3272, 28, 20, 'وجاء رجل من أقصى المدينة يسعى قال يا موسى إن الملأ يأتمرون بك ليقتلوك فاخرج إني لك من الناصحين'),
(3273, 28, 21, 'فخرج منها خائفا يترقب ۖ قال رب نجني من القوم الظالمين'),
(3274, 28, 22, 'ولما توجه تلقاء مدين قال عسى ربي أن يهديني سواء السبيل'),
(3275, 28, 23, 'ولما ورد ماء مدين وجد عليه أمة من الناس يسقون ووجد من دونهم امرأتين تذودان ۖ قال ما خطبكما ۖ قالتا لا نسقي حتى يصدر الرعاء ۖ وأبونا شيخ كبير'),
(3276, 28, 24, 'فسقى لهما ثم تولى إلى الظل فقال رب إني لما أنزلت إلي من خير فقير'),
(3277, 28, 25, 'فجاءته إحداهما تمشي على استحياء قالت إن أبي يدعوك ليجزيك أجر ما سقيت لنا ۚ فلما جاءه وقص عليه القصص قال لا تخف ۖ نجوت من القوم الظالمين'),
(3278, 28, 26, 'قالت إحداهما يا أبت استأجره ۖ إن خير من استأجرت القوي الأمين'),
(3279, 28, 27, 'قال إني أريد أن أنكحك إحدى ابنتي هاتين على أن تأجرني ثماني حجج ۖ فإن أتممت عشرا فمن عندك ۖ وما أريد أن أشق عليك ۚ ستجدني إن شاء الله من الصالحين'),
(3280, 28, 28, 'قال ذلك بيني وبينك ۖ أيما الأجلين قضيت فلا عدوان علي ۖ والله على ما نقول وكيل'),
(3281, 28, 29, 'فلما قضى موسى الأجل وسار بأهله آنس من جانب الطور نارا قال لأهله امكثوا إني آنست نارا لعلي آتيكم منها بخبر أو جذوة من النار لعلكم تصطلون'),
(3282, 28, 30, 'فلما أتاها نودي من شاطئ الواد الأيمن في البقعة المباركة من الشجرة أن يا موسى إني أنا الله رب العالمين'),
(3283, 28, 31, 'وأن ألق عصاك ۖ فلما رآها تهتز كأنها جان ولى مدبرا ولم يعقب ۚ يا موسى أقبل ولا تخف ۖ إنك من الآمنين'),
(3284, 28, 32, 'اسلك يدك في جيبك تخرج بيضاء من غير سوء واضمم إليك جناحك من الرهب ۖ فذانك برهانان من ربك إلى فرعون وملئه ۚ إنهم كانوا قوما فاسقين'),
(3285, 28, 33, 'قال رب إني قتلت منهم نفسا فأخاف أن يقتلون'),
(3286, 28, 34, 'وأخي هارون هو أفصح مني لسانا فأرسله معي ردءا يصدقني ۖ إني أخاف أن يكذبون'),
(3287, 28, 35, 'قال سنشد عضدك بأخيك ونجعل لكما سلطانا فلا يصلون إليكما ۚ بآياتنا أنتما ومن اتبعكما الغالبون'),
(3288, 28, 36, 'فلما جاءهم موسى بآياتنا بينات قالوا ما هذا إلا سحر مفترى وما سمعنا بهذا في آبائنا الأولين'),
(3289, 28, 37, 'وقال موسى ربي أعلم بمن جاء بالهدى من عنده ومن تكون له عاقبة الدار ۖ إنه لا يفلح الظالمون'),
(3290, 28, 38, 'وقال فرعون يا أيها الملأ ما علمت لكم من إله غيري فأوقد لي يا هامان على الطين فاجعل لي صرحا لعلي أطلع إلى إله موسى وإني لأظنه من الكاذبين'),
(3291, 28, 39, 'واستكبر هو وجنوده في الأرض بغير الحق وظنوا أنهم إلينا لا يرجعون'),
(3292, 28, 40, 'فأخذناه وجنوده فنبذناهم في اليم ۖ فانظر كيف كان عاقبة الظالمين'),
(3293, 28, 41, 'وجعلناهم أئمة يدعون إلى النار ۖ ويوم القيامة لا ينصرون'),
(3294, 28, 42, 'وأتبعناهم في هذه الدنيا لعنة ۖ ويوم القيامة هم من المقبوحين'),
(3295, 28, 43, 'ولقد آتينا موسى الكتاب من بعد ما أهلكنا القرون الأولى بصائر للناس وهدى ورحمة لعلهم يتذكرون'),
(3296, 28, 44, 'وما كنت بجانب الغربي إذ قضينا إلى موسى الأمر وما كنت من الشاهدين'),
(3297, 28, 45, 'ولكنا أنشأنا قرونا فتطاول عليهم العمر ۚ وما كنت ثاويا في أهل مدين تتلو عليهم آياتنا ولكنا كنا مرسلين'),
(3298, 28, 46, 'وما كنت بجانب الطور إذ نادينا ولكن رحمة من ربك لتنذر قوما ما أتاهم من نذير من قبلك لعلهم يتذكرون'),
(3299, 28, 47, 'ولولا أن تصيبهم مصيبة بما قدمت أيديهم فيقولوا ربنا لولا أرسلت إلينا رسولا فنتبع آياتك ونكون من المؤمنين'),
(3300, 28, 48, 'فلما جاءهم الحق من عندنا قالوا لولا أوتي مثل ما أوتي موسى ۚ أولم يكفروا بما أوتي موسى من قبل ۖ قالوا سحران تظاهرا وقالوا إنا بكل كافرون'),
(3301, 28, 49, 'قل فأتوا بكتاب من عند الله هو أهدى منهما أتبعه إن كنتم صادقين'),
(3302, 28, 50, 'فإن لم يستجيبوا لك فاعلم أنما يتبعون أهواءهم ۚ ومن أضل ممن اتبع هواه بغير هدى من الله ۚ إن الله لا يهدي القوم الظالمين'),
(3303, 28, 51, 'ولقد وصلنا لهم القول لعلهم يتذكرون'),
(3304, 28, 52, 'الذين آتيناهم الكتاب من قبله هم به يؤمنون'),
(3305, 28, 53, 'وإذا يتلى عليهم قالوا آمنا به إنه الحق من ربنا إنا كنا من قبله مسلمين'),
(3306, 28, 54, 'أولئك يؤتون أجرهم مرتين بما صبروا ويدرءون بالحسنة السيئة ومما رزقناهم ينفقون'),
(3307, 28, 55, 'وإذا سمعوا اللغو أعرضوا عنه وقالوا لنا أعمالنا ولكم أعمالكم سلام عليكم لا نبتغي الجاهلين'),
(3308, 28, 56, 'إنك لا تهدي من أحببت ولكن الله يهدي من يشاء ۚ وهو أعلم بالمهتدين'),
(3309, 28, 57, 'وقالوا إن نتبع الهدى معك نتخطف من أرضنا ۚ أولم نمكن لهم حرما آمنا يجبى إليه ثمرات كل شيء رزقا من لدنا ولكن أكثرهم لا يعلمون'),
(3310, 28, 58, 'وكم أهلكنا من قرية بطرت معيشتها ۖ فتلك مساكنهم لم تسكن من بعدهم إلا قليلا ۖ وكنا نحن الوارثين'),
(3311, 28, 59, 'وما كان ربك مهلك القرى حتى يبعث في أمها رسولا يتلو عليهم آياتنا ۚ وما كنا مهلكي القرى إلا وأهلها ظالمون'),
(3312, 28, 60, 'وما أوتيتم من شيء فمتاع الحياة الدنيا وزينتها ۚ وما عند الله خير وأبقى ۚ أفلا تعقلون'),
(3313, 28, 61, 'أفمن وعدناه وعدا حسنا فهو لاقيه كمن متعناه متاع الحياة الدنيا ثم هو يوم القيامة من المحضرين'),
(3314, 28, 62, 'ويوم يناديهم فيقول أين شركائي الذين كنتم تزعمون'),
(3315, 28, 63, 'قال الذين حق عليهم القول ربنا هؤلاء الذين أغوينا أغويناهم كما غوينا ۖ تبرأنا إليك ۖ ما كانوا إيانا يعبدون'),
(3316, 28, 64, 'وقيل ادعوا شركاءكم فدعوهم فلم يستجيبوا لهم ورأوا العذاب ۚ لو أنهم كانوا يهتدون'),
(3317, 28, 65, 'ويوم يناديهم فيقول ماذا أجبتم المرسلين'),
(3318, 28, 66, 'فعميت عليهم الأنباء يومئذ فهم لا يتساءلون'),
(3319, 28, 67, 'فأما من تاب وآمن وعمل صالحا فعسى أن يكون من المفلحين'),
(3320, 28, 68, 'وربك يخلق ما يشاء ويختار ۗ ما كان لهم الخيرة ۚ سبحان الله وتعالى عما يشركون'),
(3321, 28, 69, 'وربك يعلم ما تكن صدورهم وما يعلنون'),
(3322, 28, 70, 'وهو الله لا إله إلا هو ۖ له الحمد في الأولى والآخرة ۖ وله الحكم وإليه ترجعون'),
(3323, 28, 71, 'قل أرأيتم إن جعل الله عليكم الليل سرمدا إلى يوم القيامة من إله غير الله يأتيكم بضياء ۖ أفلا تسمعون'),
(3324, 28, 72, 'قل أرأيتم إن جعل الله عليكم النهار سرمدا إلى يوم القيامة من إله غير الله يأتيكم بليل تسكنون فيه ۖ أفلا تبصرون'),
(3325, 28, 73, 'ومن رحمته جعل لكم الليل والنهار لتسكنوا فيه ولتبتغوا من فضله ولعلكم تشكرون'),
(3326, 28, 74, 'ويوم يناديهم فيقول أين شركائي الذين كنتم تزعمون'),
(3327, 28, 75, 'ونزعنا من كل أمة شهيدا فقلنا هاتوا برهانكم فعلموا أن الحق لله وضل عنهم ما كانوا يفترون'),
(3328, 28, 76, 'إن قارون كان من قوم موسى فبغى عليهم ۖ وآتيناه من الكنوز ما إن مفاتحه لتنوء بالعصبة أولي القوة إذ قال له قومه لا تفرح ۖ إن الله لا يحب الفرحين'),
(3329, 28, 77, 'وابتغ فيما آتاك الله الدار الآخرة ۖ ولا تنس نصيبك من الدنيا ۖ وأحسن كما أحسن الله إليك ۖ ولا تبغ الفساد في الأرض ۖ إن الله لا يحب المفسدين'),
(3330, 28, 78, 'قال إنما أوتيته على علم عندي ۚ أولم يعلم أن الله قد أهلك من قبله من القرون من هو أشد منه قوة وأكثر جمعا ۚ ولا يسأل عن ذنوبهم المجرمون'),
(3331, 28, 79, 'فخرج على قومه في زينته ۖ قال الذين يريدون الحياة الدنيا يا ليت لنا مثل ما أوتي قارون إنه لذو حظ عظيم'),
(3332, 28, 80, 'وقال الذين أوتوا العلم ويلكم ثواب الله خير لمن آمن وعمل صالحا ولا يلقاها إلا الصابرون'),
(3333, 28, 81, 'فخسفنا به وبداره الأرض فما كان له من فئة ينصرونه من دون الله وما كان من المنتصرين'),
(3334, 28, 82, 'وأصبح الذين تمنوا مكانه بالأمس يقولون ويكأن الله يبسط الرزق لمن يشاء من عباده ويقدر ۖ لولا أن من الله علينا لخسف بنا ۖ ويكأنه لا يفلح الكافرون'),
(3335, 28, 83, 'تلك الدار الآخرة نجعلها للذين لا يريدون علوا في الأرض ولا فسادا ۚ والعاقبة للمتقين'),
(3336, 28, 84, 'من جاء بالحسنة فله خير منها ۖ ومن جاء بالسيئة فلا يجزى الذين عملوا السيئات إلا ما كانوا يعملون'),
(3337, 28, 85, 'إن الذي فرض عليك القرآن لرادك إلى معاد ۚ قل ربي أعلم من جاء بالهدى ومن هو في ضلال مبين'),
(3338, 28, 86, 'وما كنت ترجو أن يلقى إليك الكتاب إلا رحمة من ربك ۖ فلا تكونن ظهيرا للكافرين'),
(3339, 28, 87, 'ولا يصدنك عن آيات الله بعد إذ أنزلت إليك ۖ وادع إلى ربك ۖ ولا تكونن من المشركين'),
(3340, 28, 88, 'ولا تدع مع الله إلها آخر ۘ لا إله إلا هو ۚ كل شيء هالك إلا وجهه ۚ له الحكم وإليه ترجعون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'بسم الله الرحمن الرحيم الم'),
(3342, 29, 2, 'أحسب الناس أن يتركوا أن يقولوا آمنا وهم لا يفتنون'),
(3343, 29, 3, 'ولقد فتنا الذين من قبلهم ۖ فليعلمن الله الذين صدقوا وليعلمن الكاذبين'),
(3344, 29, 4, 'أم حسب الذين يعملون السيئات أن يسبقونا ۚ ساء ما يحكمون'),
(3345, 29, 5, 'من كان يرجو لقاء الله فإن أجل الله لآت ۚ وهو السميع العليم'),
(3346, 29, 6, 'ومن جاهد فإنما يجاهد لنفسه ۚ إن الله لغني عن العالمين'),
(3347, 29, 7, 'والذين آمنوا وعملوا الصالحات لنكفرن عنهم سيئاتهم ولنجزينهم أحسن الذي كانوا يعملون'),
(3348, 29, 8, 'ووصينا الإنسان بوالديه حسنا ۖ وإن جاهداك لتشرك بي ما ليس لك به علم فلا تطعهما ۚ إلي مرجعكم فأنبئكم بما كنتم تعملون'),
(3349, 29, 9, 'والذين آمنوا وعملوا الصالحات لندخلنهم في الصالحين'),
(3350, 29, 10, 'ومن الناس من يقول آمنا بالله فإذا أوذي في الله جعل فتنة الناس كعذاب الله ولئن جاء نصر من ربك ليقولن إنا كنا معكم ۚ أوليس الله بأعلم بما في صدور العالمين'),
(3351, 29, 11, 'وليعلمن الله الذين آمنوا وليعلمن المنافقين'),
(3352, 29, 12, 'وقال الذين كفروا للذين آمنوا اتبعوا سبيلنا ولنحمل خطاياكم وما هم بحاملين من خطاياهم من شيء ۖ إنهم لكاذبون'),
(3353, 29, 13, 'وليحملن أثقالهم وأثقالا مع أثقالهم ۖ وليسألن يوم القيامة عما كانوا يفترون'),
(3354, 29, 14, 'ولقد أرسلنا نوحا إلى قومه فلبث فيهم ألف سنة إلا خمسين عاما فأخذهم الطوفان وهم ظالمون'),
(3355, 29, 15, 'فأنجيناه وأصحاب السفينة وجعلناها آية للعالمين'),
(3356, 29, 16, 'وإبراهيم إذ قال لقومه اعبدوا الله واتقوه ۖ ذلكم خير لكم إن كنتم تعلمون'),
(3357, 29, 17, 'إنما تعبدون من دون الله أوثانا وتخلقون إفكا ۚ إن الذين تعبدون من دون الله لا يملكون لكم رزقا فابتغوا عند الله الرزق واعبدوه واشكروا له ۖ إليه ترجعون'),
(3358, 29, 18, 'وإن تكذبوا فقد كذب أمم من قبلكم ۖ وما على الرسول إلا البلاغ المبين'),
(3359, 29, 19, 'أولم يروا كيف يبدئ الله الخلق ثم يعيده ۚ إن ذلك على الله يسير'),
(3360, 29, 20, 'قل سيروا في الأرض فانظروا كيف بدأ الخلق ۚ ثم الله ينشئ النشأة الآخرة ۚ إن الله على كل شيء قدير'),
(3361, 29, 21, 'يعذب من يشاء ويرحم من يشاء ۖ وإليه تقلبون'),
(3362, 29, 22, 'وما أنتم بمعجزين في الأرض ولا في السماء ۖ وما لكم من دون الله من ولي ولا نصير'),
(3363, 29, 23, 'والذين كفروا بآيات الله ولقائه أولئك يئسوا من رحمتي وأولئك لهم عذاب أليم'),
(3364, 29, 24, 'فما كان جواب قومه إلا أن قالوا اقتلوه أو حرقوه فأنجاه الله من النار ۚ إن في ذلك لآيات لقوم يؤمنون'),
(3365, 29, 25, 'وقال إنما اتخذتم من دون الله أوثانا مودة بينكم في الحياة الدنيا ۖ ثم يوم القيامة يكفر بعضكم ببعض ويلعن بعضكم بعضا ومأواكم النار وما لكم من ناصرين'),
(3366, 29, 26, 'فآمن له لوط ۘ وقال إني مهاجر إلى ربي ۖ إنه هو العزيز الحكيم'),
(3367, 29, 27, 'ووهبنا له إسحاق ويعقوب وجعلنا في ذريته النبوة والكتاب وآتيناه أجره في الدنيا ۖ وإنه في الآخرة لمن الصالحين'),
(3368, 29, 28, 'ولوطا إذ قال لقومه إنكم لتأتون الفاحشة ما سبقكم بها من أحد من العالمين'),
(3369, 29, 29, 'أئنكم لتأتون الرجال وتقطعون السبيل وتأتون في ناديكم المنكر ۖ فما كان جواب قومه إلا أن قالوا ائتنا بعذاب الله إن كنت من الصادقين'),
(3370, 29, 30, 'قال رب انصرني على القوم المفسدين'),
(3371, 29, 31, 'ولما جاءت رسلنا إبراهيم بالبشرى قالوا إنا مهلكو أهل هذه القرية ۖ إن أهلها كانوا ظالمين'),
(3372, 29, 32, 'قال إن فيها لوطا ۚ قالوا نحن أعلم بمن فيها ۖ لننجينه وأهله إلا امرأته كانت من الغابرين'),
(3373, 29, 33, 'ولما أن جاءت رسلنا لوطا سيء بهم وضاق بهم ذرعا وقالوا لا تخف ولا تحزن ۖ إنا منجوك وأهلك إلا امرأتك كانت من الغابرين'),
(3374, 29, 34, 'إنا منزلون على أهل هذه القرية رجزا من السماء بما كانوا يفسقون'),
(3375, 29, 35, 'ولقد تركنا منها آية بينة لقوم يعقلون'),
(3376, 29, 36, 'وإلى مدين أخاهم شعيبا فقال يا قوم اعبدوا الله وارجوا اليوم الآخر ولا تعثوا في الأرض مفسدين'),
(3377, 29, 37, 'فكذبوه فأخذتهم الرجفة فأصبحوا في دارهم جاثمين'),
(3378, 29, 38, 'وعادا وثمود وقد تبين لكم من مساكنهم ۖ وزين لهم الشيطان أعمالهم فصدهم عن السبيل وكانوا مستبصرين'),
(3379, 29, 39, 'وقارون وفرعون وهامان ۖ ولقد جاءهم موسى بالبينات فاستكبروا في الأرض وما كانوا سابقين'),
(3380, 29, 40, 'فكلا أخذنا بذنبه ۖ فمنهم من أرسلنا عليه حاصبا ومنهم من أخذته الصيحة ومنهم من خسفنا به الأرض ومنهم من أغرقنا ۚ وما كان الله ليظلمهم ولكن كانوا أنفسهم يظلمون'),
(3381, 29, 41, 'مثل الذين اتخذوا من دون الله أولياء كمثل العنكبوت اتخذت بيتا ۖ وإن أوهن البيوت لبيت العنكبوت ۖ لو كانوا يعلمون'),
(3382, 29, 42, 'إن الله يعلم ما يدعون من دونه من شيء ۚ وهو العزيز الحكيم'),
(3383, 29, 43, 'وتلك الأمثال نضربها للناس ۖ وما يعقلها إلا العالمون'),
(3384, 29, 44, 'خلق الله السماوات والأرض بالحق ۚ إن في ذلك لآية للمؤمنين'),
(3385, 29, 45, 'اتل ما أوحي إليك من الكتاب وأقم الصلاة ۖ إن الصلاة تنهى عن الفحشاء والمنكر ۗ ولذكر الله أكبر ۗ والله يعلم ما تصنعون'),
(3386, 29, 46, 'ولا تجادلوا أهل الكتاب إلا بالتي هي أحسن إلا الذين ظلموا منهم ۖ وقولوا آمنا بالذي أنزل إلينا وأنزل إليكم وإلهنا وإلهكم واحد ونحن له مسلمون'),
(3387, 29, 47, 'وكذلك أنزلنا إليك الكتاب ۚ فالذين آتيناهم الكتاب يؤمنون به ۖ ومن هؤلاء من يؤمن به ۚ وما يجحد بآياتنا إلا الكافرون'),
(3388, 29, 48, 'وما كنت تتلو من قبله من كتاب ولا تخطه بيمينك ۖ إذا لارتاب المبطلون'),
(3389, 29, 49, 'بل هو آيات بينات في صدور الذين أوتوا العلم ۚ وما يجحد بآياتنا إلا الظالمون'),
(3390, 29, 50, 'وقالوا لولا أنزل عليه آيات من ربه ۖ قل إنما الآيات عند الله وإنما أنا نذير مبين'),
(3391, 29, 51, 'أولم يكفهم أنا أنزلنا عليك الكتاب يتلى عليهم ۚ إن في ذلك لرحمة وذكرى لقوم يؤمنون'),
(3392, 29, 52, 'قل كفى بالله بيني وبينكم شهيدا ۖ يعلم ما في السماوات والأرض ۗ والذين آمنوا بالباطل وكفروا بالله أولئك هم الخاسرون'),
(3393, 29, 53, 'ويستعجلونك بالعذاب ۚ ولولا أجل مسمى لجاءهم العذاب وليأتينهم بغتة وهم لا يشعرون'),
(3394, 29, 54, 'يستعجلونك بالعذاب وإن جهنم لمحيطة بالكافرين'),
(3395, 29, 55, 'يوم يغشاهم العذاب من فوقهم ومن تحت أرجلهم ويقول ذوقوا ما كنتم تعملون'),
(3396, 29, 56, 'يا عبادي الذين آمنوا إن أرضي واسعة فإياي فاعبدون'),
(3397, 29, 57, 'كل نفس ذائقة الموت ۖ ثم إلينا ترجعون'),
(3398, 29, 58, 'والذين آمنوا وعملوا الصالحات لنبوئنهم من الجنة غرفا تجري من تحتها الأنهار خالدين فيها ۚ نعم أجر العاملين'),
(3399, 29, 59, 'الذين صبروا وعلى ربهم يتوكلون'),
(3400, 29, 60, 'وكأين من دابة لا تحمل رزقها الله يرزقها وإياكم ۚ وهو السميع العليم'),
(3401, 29, 61, 'ولئن سألتهم من خلق السماوات والأرض وسخر الشمس والقمر ليقولن الله ۖ فأنى يؤفكون'),
(3402, 29, 62, 'الله يبسط الرزق لمن يشاء من عباده ويقدر له ۚ إن الله بكل شيء عليم'),
(3403, 29, 63, 'ولئن سألتهم من نزل من السماء ماء فأحيا به الأرض من بعد موتها ليقولن الله ۚ قل الحمد لله ۚ بل أكثرهم لا يعقلون'),
(3404, 29, 64, 'وما هذه الحياة الدنيا إلا لهو ولعب ۚ وإن الدار الآخرة لهي الحيوان ۚ لو كانوا يعلمون'),
(3405, 29, 65, 'فإذا ركبوا في الفلك دعوا الله مخلصين له الدين فلما نجاهم إلى البر إذا هم يشركون'),
(3406, 29, 66, 'ليكفروا بما آتيناهم وليتمتعوا ۖ فسوف يعلمون'),
(3407, 29, 67, 'أولم يروا أنا جعلنا حرما آمنا ويتخطف الناس من حولهم ۚ أفبالباطل يؤمنون وبنعمة الله يكفرون'),
(3408, 29, 68, 'ومن أظلم ممن افترى على الله كذبا أو كذب بالحق لما جاءه ۚ أليس في جهنم مثوى للكافرين'),
(3409, 29, 69, 'والذين جاهدوا فينا لنهدينهم سبلنا ۚ وإن الله لمع المحسنين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'بسم الله الرحمن الرحيم الم'),
(3411, 30, 2, 'غلبت الروم'),
(3412, 30, 3, 'في أدنى الأرض وهم من بعد غلبهم سيغلبون'),
(3413, 30, 4, 'في بضع سنين ۗ لله الأمر من قبل ومن بعد ۚ ويومئذ يفرح المؤمنون'),
(3414, 30, 5, 'بنصر الله ۚ ينصر من يشاء ۖ وهو العزيز الرحيم'),
(3415, 30, 6, 'وعد الله ۖ لا يخلف الله وعده ولكن أكثر الناس لا يعلمون'),
(3416, 30, 7, 'يعلمون ظاهرا من الحياة الدنيا وهم عن الآخرة هم غافلون'),
(3417, 30, 8, 'أولم يتفكروا في أنفسهم ۗ ما خلق الله السماوات والأرض وما بينهما إلا بالحق وأجل مسمى ۗ وإن كثيرا من الناس بلقاء ربهم لكافرون'),
(3418, 30, 9, 'أولم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم ۚ كانوا أشد منهم قوة وأثاروا الأرض وعمروها أكثر مما عمروها وجاءتهم رسلهم بالبينات ۖ فما كان الله ليظلمهم ولكن كانوا أنفسهم يظلمون'),
(3419, 30, 10, 'ثم كان عاقبة الذين أساءوا السوأى أن كذبوا بآيات الله وكانوا بها يستهزئون'),
(3420, 30, 11, 'الله يبدأ الخلق ثم يعيده ثم إليه ترجعون'),
(3421, 30, 12, 'ويوم تقوم الساعة يبلس المجرمون'),
(3422, 30, 13, 'ولم يكن لهم من شركائهم شفعاء وكانوا بشركائهم كافرين'),
(3423, 30, 14, 'ويوم تقوم الساعة يومئذ يتفرقون'),
(3424, 30, 15, 'فأما الذين آمنوا وعملوا الصالحات فهم في روضة يحبرون'),
(3425, 30, 16, 'وأما الذين كفروا وكذبوا بآياتنا ولقاء الآخرة فأولئك في العذاب محضرون'),
(3426, 30, 17, 'فسبحان الله حين تمسون وحين تصبحون'),
(3427, 30, 18, 'وله الحمد في السماوات والأرض وعشيا وحين تظهرون'),
(3428, 30, 19, 'يخرج الحي من الميت ويخرج الميت من الحي ويحيي الأرض بعد موتها ۚ وكذلك تخرجون'),
(3429, 30, 20, 'ومن آياته أن خلقكم من تراب ثم إذا أنتم بشر تنتشرون'),
(3430, 30, 21, 'ومن آياته أن خلق لكم من أنفسكم أزواجا لتسكنوا إليها وجعل بينكم مودة ورحمة ۚ إن في ذلك لآيات لقوم يتفكرون'),
(3431, 30, 22, 'ومن آياته خلق السماوات والأرض واختلاف ألسنتكم وألوانكم ۚ إن في ذلك لآيات للعالمين'),
(3432, 30, 23, 'ومن آياته منامكم بالليل والنهار وابتغاؤكم من فضله ۚ إن في ذلك لآيات لقوم يسمعون'),
(3433, 30, 24, 'ومن آياته يريكم البرق خوفا وطمعا وينزل من السماء ماء فيحيي به الأرض بعد موتها ۚ إن في ذلك لآيات لقوم يعقلون'),
(3434, 30, 25, 'ومن آياته أن تقوم السماء والأرض بأمره ۚ ثم إذا دعاكم دعوة من الأرض إذا أنتم تخرجون'),
(3435, 30, 26, 'وله من في السماوات والأرض ۖ كل له قانتون'),
(3436, 30, 27, 'وهو الذي يبدأ الخلق ثم يعيده وهو أهون عليه ۚ وله المثل الأعلى في السماوات والأرض ۚ وهو العزيز الحكيم'),
(3437, 30, 28, 'ضرب لكم مثلا من أنفسكم ۖ هل لكم من ما ملكت أيمانكم من شركاء في ما رزقناكم فأنتم فيه سواء تخافونهم كخيفتكم أنفسكم ۚ كذلك نفصل الآيات لقوم يعقلون'),
(3438, 30, 29, 'بل اتبع الذين ظلموا أهواءهم بغير علم ۖ فمن يهدي من أضل الله ۖ وما لهم من ناصرين'),
(3439, 30, 30, 'فأقم وجهك للدين حنيفا ۚ فطرت الله التي فطر الناس عليها ۚ لا تبديل لخلق الله ۚ ذلك الدين القيم ولكن أكثر الناس لا يعلمون'),
(3440, 30, 31, 'منيبين إليه واتقوه وأقيموا الصلاة ولا تكونوا من المشركين'),
(3441, 30, 32, 'من الذين فرقوا دينهم وكانوا شيعا ۖ كل حزب بما لديهم فرحون'),
(3442, 30, 33, 'وإذا مس الناس ضر دعوا ربهم منيبين إليه ثم إذا أذاقهم منه رحمة إذا فريق منهم بربهم يشركون'),
(3443, 30, 34, 'ليكفروا بما آتيناهم ۚ فتمتعوا فسوف تعلمون'),
(3444, 30, 35, 'أم أنزلنا عليهم سلطانا فهو يتكلم بما كانوا به يشركون'),
(3445, 30, 36, 'وإذا أذقنا الناس رحمة فرحوا بها ۖ وإن تصبهم سيئة بما قدمت أيديهم إذا هم يقنطون'),
(3446, 30, 37, 'أولم يروا أن الله يبسط الرزق لمن يشاء ويقدر ۚ إن في ذلك لآيات لقوم يؤمنون'),
(3447, 30, 38, 'فآت ذا القربى حقه والمسكين وابن السبيل ۚ ذلك خير للذين يريدون وجه الله ۖ وأولئك هم المفلحون'),
(3448, 30, 39, 'وما آتيتم من ربا ليربو في أموال الناس فلا يربو عند الله ۖ وما آتيتم من زكاة تريدون وجه الله فأولئك هم المضعفون'),
(3449, 30, 40, 'الله الذي خلقكم ثم رزقكم ثم يميتكم ثم يحييكم ۖ هل من شركائكم من يفعل من ذلكم من شيء ۚ سبحانه وتعالى عما يشركون'),
(3450, 30, 41, 'ظهر الفساد في البر والبحر بما كسبت أيدي الناس ليذيقهم بعض الذي عملوا لعلهم يرجعون'),
(3451, 30, 42, 'قل سيروا في الأرض فانظروا كيف كان عاقبة الذين من قبل ۚ كان أكثرهم مشركين'),
(3452, 30, 43, 'فأقم وجهك للدين القيم من قبل أن يأتي يوم لا مرد له من الله ۖ يومئذ يصدعون'),
(3453, 30, 44, 'من كفر فعليه كفره ۖ ومن عمل صالحا فلأنفسهم يمهدون'),
(3454, 30, 45, 'ليجزي الذين آمنوا وعملوا الصالحات من فضله ۚ إنه لا يحب الكافرين'),
(3455, 30, 46, 'ومن آياته أن يرسل الرياح مبشرات وليذيقكم من رحمته ولتجري الفلك بأمره ولتبتغوا من فضله ولعلكم تشكرون'),
(3456, 30, 47, 'ولقد أرسلنا من قبلك رسلا إلى قومهم فجاءوهم بالبينات فانتقمنا من الذين أجرموا ۖ وكان حقا علينا نصر المؤمنين'),
(3457, 30, 48, 'الله الذي يرسل الرياح فتثير سحابا فيبسطه في السماء كيف يشاء ويجعله كسفا فترى الودق يخرج من خلاله ۖ فإذا أصاب به من يشاء من عباده إذا هم يستبشرون'),
(3458, 30, 49, 'وإن كانوا من قبل أن ينزل عليهم من قبله لمبلسين'),
(3459, 30, 50, 'فانظر إلى آثار رحمت الله كيف يحيي الأرض بعد موتها ۚ إن ذلك لمحيي الموتى ۖ وهو على كل شيء قدير'),
(3460, 30, 51, 'ولئن أرسلنا ريحا فرأوه مصفرا لظلوا من بعده يكفرون'),
(3461, 30, 52, 'فإنك لا تسمع الموتى ولا تسمع الصم الدعاء إذا ولوا مدبرين'),
(3462, 30, 53, 'وما أنت بهاد العمي عن ضلالتهم ۖ إن تسمع إلا من يؤمن بآياتنا فهم مسلمون'),
(3463, 30, 54, 'الله الذي خلقكم من ضعف ثم جعل من بعد ضعف قوة ثم جعل من بعد قوة ضعفا وشيبة ۚ يخلق ما يشاء ۖ وهو العليم القدير'),
(3464, 30, 55, 'ويوم تقوم الساعة يقسم المجرمون ما لبثوا غير ساعة ۚ كذلك كانوا يؤفكون'),
(3465, 30, 56, 'وقال الذين أوتوا العلم والإيمان لقد لبثتم في كتاب الله إلى يوم البعث ۖ فهذا يوم البعث ولكنكم كنتم لا تعلمون'),
(3466, 30, 57, 'فيومئذ لا ينفع الذين ظلموا معذرتهم ولا هم يستعتبون'),
(3467, 30, 58, 'ولقد ضربنا للناس في هذا القرآن من كل مثل ۚ ولئن جئتهم بآية ليقولن الذين كفروا إن أنتم إلا مبطلون'),
(3468, 30, 59, 'كذلك يطبع الله على قلوب الذين لا يعلمون'),
(3469, 30, 60, 'فاصبر إن وعد الله حق ۖ ولا يستخفنك الذين لا يوقنون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'بسم الله الرحمن الرحيم الم'),
(3471, 31, 2, 'تلك آيات الكتاب الحكيم'),
(3472, 31, 3, 'هدى ورحمة للمحسنين'),
(3473, 31, 4, 'الذين يقيمون الصلاة ويؤتون الزكاة وهم بالآخرة هم يوقنون'),
(3474, 31, 5, 'أولئك على هدى من ربهم ۖ وأولئك هم المفلحون'),
(3475, 31, 6, 'ومن الناس من يشتري لهو الحديث ليضل عن سبيل الله بغير علم ويتخذها هزوا ۚ أولئك لهم عذاب مهين'),
(3476, 31, 7, 'وإذا تتلى عليه آياتنا ولى مستكبرا كأن لم يسمعها كأن في أذنيه وقرا ۖ فبشره بعذاب أليم'),
(3477, 31, 8, 'إن الذين آمنوا وعملوا الصالحات لهم جنات النعيم'),
(3478, 31, 9, 'خالدين فيها ۖ وعد الله حقا ۚ وهو العزيز الحكيم'),
(3479, 31, 10, 'خلق السماوات بغير عمد ترونها ۖ وألقى في الأرض رواسي أن تميد بكم وبث فيها من كل دابة ۚ وأنزلنا من السماء ماء فأنبتنا فيها من كل زوج كريم'),
(3480, 31, 11, 'هذا خلق الله فأروني ماذا خلق الذين من دونه ۚ بل الظالمون في ضلال مبين'),
(3481, 31, 12, 'ولقد آتينا لقمان الحكمة أن اشكر لله ۚ ومن يشكر فإنما يشكر لنفسه ۖ ومن كفر فإن الله غني حميد'),
(3482, 31, 13, 'وإذ قال لقمان لابنه وهو يعظه يا بني لا تشرك بالله ۖ إن الشرك لظلم عظيم'),
(3483, 31, 14, 'ووصينا الإنسان بوالديه حملته أمه وهنا على وهن وفصاله في عامين أن اشكر لي ولوالديك إلي المصير'),
(3484, 31, 15, 'وإن جاهداك على أن تشرك بي ما ليس لك به علم فلا تطعهما ۖ وصاحبهما في الدنيا معروفا ۖ واتبع سبيل من أناب إلي ۚ ثم إلي مرجعكم فأنبئكم بما كنتم تعملون'),
(3485, 31, 16, 'يا بني إنها إن تك مثقال حبة من خردل فتكن في صخرة أو في السماوات أو في الأرض يأت بها الله ۚ إن الله لطيف خبير'),
(3486, 31, 17, 'يا بني أقم الصلاة وأمر بالمعروف وانه عن المنكر واصبر على ما أصابك ۖ إن ذلك من عزم الأمور'),
(3487, 31, 18, 'ولا تصعر خدك للناس ولا تمش في الأرض مرحا ۖ إن الله لا يحب كل مختال فخور'),
(3488, 31, 19, 'واقصد في مشيك واغضض من صوتك ۚ إن أنكر الأصوات لصوت الحمير'),
(3489, 31, 20, 'ألم تروا أن الله سخر لكم ما في السماوات وما في الأرض وأسبغ عليكم نعمه ظاهرة وباطنة ۗ ومن الناس من يجادل في الله بغير علم ولا هدى ولا كتاب منير'),
(3490, 31, 21, 'وإذا قيل لهم اتبعوا ما أنزل الله قالوا بل نتبع ما وجدنا عليه آباءنا ۚ أولو كان الشيطان يدعوهم إلى عذاب السعير'),
(3491, 31, 22, 'ومن يسلم وجهه إلى الله وهو محسن فقد استمسك بالعروة الوثقى ۗ وإلى الله عاقبة الأمور'),
(3492, 31, 23, 'ومن كفر فلا يحزنك كفره ۚ إلينا مرجعهم فننبئهم بما عملوا ۚ إن الله عليم بذات الصدور'),
(3493, 31, 24, 'نمتعهم قليلا ثم نضطرهم إلى عذاب غليظ'),
(3494, 31, 25, 'ولئن سألتهم من خلق السماوات والأرض ليقولن الله ۚ قل الحمد لله ۚ بل أكثرهم لا يعلمون'),
(3495, 31, 26, 'لله ما في السماوات والأرض ۚ إن الله هو الغني الحميد'),
(3496, 31, 27, 'ولو أنما في الأرض من شجرة أقلام والبحر يمده من بعده سبعة أبحر ما نفدت كلمات الله ۗ إن الله عزيز حكيم'),
(3497, 31, 28, 'ما خلقكم ولا بعثكم إلا كنفس واحدة ۗ إن الله سميع بصير'),
(3498, 31, 29, 'ألم تر أن الله يولج الليل في النهار ويولج النهار في الليل وسخر الشمس والقمر كل يجري إلى أجل مسمى وأن الله بما تعملون خبير'),
(3499, 31, 30, 'ذلك بأن الله هو الحق وأن ما يدعون من دونه الباطل وأن الله هو العلي الكبير'),
(3500, 31, 31, 'ألم تر أن الفلك تجري في البحر بنعمت الله ليريكم من آياته ۚ إن في ذلك لآيات لكل صبار شكور'),
(3501, 31, 32, 'وإذا غشيهم موج كالظلل دعوا الله مخلصين له الدين فلما نجاهم إلى البر فمنهم مقتصد ۚ وما يجحد بآياتنا إلا كل ختار كفور'),
(3502, 31, 33, 'يا أيها الناس اتقوا ربكم واخشوا يوما لا يجزي والد عن ولده ولا مولود هو جاز عن والده شيئا ۚ إن وعد الله حق ۖ فلا تغرنكم الحياة الدنيا ولا يغرنكم بالله الغرور'),
(3503, 31, 34, 'إن الله عنده علم الساعة وينزل الغيث ويعلم ما في الأرحام ۖ وما تدري نفس ماذا تكسب غدا ۖ وما تدري نفس بأي أرض تموت ۚ إن الله عليم خبير');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'بسم الله الرحمن الرحيم الم'),
(3505, 32, 2, 'تنزيل الكتاب لا ريب فيه من رب العالمين'),
(3506, 32, 3, 'أم يقولون افتراه ۚ بل هو الحق من ربك لتنذر قوما ما أتاهم من نذير من قبلك لعلهم يهتدون'),
(3507, 32, 4, 'الله الذي خلق السماوات والأرض وما بينهما في ستة أيام ثم استوى على العرش ۖ ما لكم من دونه من ولي ولا شفيع ۚ أفلا تتذكرون'),
(3508, 32, 5, 'يدبر الأمر من السماء إلى الأرض ثم يعرج إليه في يوم كان مقداره ألف سنة مما تعدون'),
(3509, 32, 6, 'ذلك عالم الغيب والشهادة العزيز الرحيم'),
(3510, 32, 7, 'الذي أحسن كل شيء خلقه ۖ وبدأ خلق الإنسان من طين'),
(3511, 32, 8, 'ثم جعل نسله من سلالة من ماء مهين'),
(3512, 32, 9, 'ثم سواه ونفخ فيه من روحه ۖ وجعل لكم السمع والأبصار والأفئدة ۚ قليلا ما تشكرون'),
(3513, 32, 10, 'وقالوا أإذا ضللنا في الأرض أإنا لفي خلق جديد ۚ بل هم بلقاء ربهم كافرون'),
(3514, 32, 11, 'قل يتوفاكم ملك الموت الذي وكل بكم ثم إلى ربكم ترجعون'),
(3515, 32, 12, 'ولو ترى إذ المجرمون ناكسو رءوسهم عند ربهم ربنا أبصرنا وسمعنا فارجعنا نعمل صالحا إنا موقنون'),
(3516, 32, 13, 'ولو شئنا لآتينا كل نفس هداها ولكن حق القول مني لأملأن جهنم من الجنة والناس أجمعين'),
(3517, 32, 14, 'فذوقوا بما نسيتم لقاء يومكم هذا إنا نسيناكم ۖ وذوقوا عذاب الخلد بما كنتم تعملون'),
(3518, 32, 15, 'إنما يؤمن بآياتنا الذين إذا ذكروا بها خروا سجدا وسبحوا بحمد ربهم وهم لا يستكبرون'),
(3519, 32, 16, 'تتجافى جنوبهم عن المضاجع يدعون ربهم خوفا وطمعا ومما رزقناهم ينفقون'),
(3520, 32, 17, 'فلا تعلم نفس ما أخفي لهم من قرة أعين جزاء بما كانوا يعملون'),
(3521, 32, 18, 'أفمن كان مؤمنا كمن كان فاسقا ۚ لا يستوون'),
(3522, 32, 19, 'أما الذين آمنوا وعملوا الصالحات فلهم جنات المأوى نزلا بما كانوا يعملون'),
(3523, 32, 20, 'وأما الذين فسقوا فمأواهم النار ۖ كلما أرادوا أن يخرجوا منها أعيدوا فيها وقيل لهم ذوقوا عذاب النار الذي كنتم به تكذبون'),
(3524, 32, 21, 'ولنذيقنهم من العذاب الأدنى دون العذاب الأكبر لعلهم يرجعون'),
(3525, 32, 22, 'ومن أظلم ممن ذكر بآيات ربه ثم أعرض عنها ۚ إنا من المجرمين منتقمون'),
(3526, 32, 23, 'ولقد آتينا موسى الكتاب فلا تكن في مرية من لقائه ۖ وجعلناه هدى لبني إسرائيل'),
(3527, 32, 24, 'وجعلنا منهم أئمة يهدون بأمرنا لما صبروا ۖ وكانوا بآياتنا يوقنون'),
(3528, 32, 25, 'إن ربك هو يفصل بينهم يوم القيامة فيما كانوا فيه يختلفون'),
(3529, 32, 26, 'أولم يهد لهم كم أهلكنا من قبلهم من القرون يمشون في مساكنهم ۚ إن في ذلك لآيات ۖ أفلا يسمعون'),
(3530, 32, 27, 'أولم يروا أنا نسوق الماء إلى الأرض الجرز فنخرج به زرعا تأكل منه أنعامهم وأنفسهم ۖ أفلا يبصرون'),
(3531, 32, 28, 'ويقولون متى هذا الفتح إن كنتم صادقين'),
(3532, 32, 29, 'قل يوم الفتح لا ينفع الذين كفروا إيمانهم ولا هم ينظرون'),
(3533, 32, 30, 'فأعرض عنهم وانتظر إنهم منتظرون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'بسم الله الرحمن الرحيم يا أيها النبي اتق الله ولا تطع الكافرين والمنافقين ۗ إن الله كان عليما حكيما'),
(3535, 33, 2, 'واتبع ما يوحى إليك من ربك ۚ إن الله كان بما تعملون خبيرا'),
(3536, 33, 3, 'وتوكل على الله ۚ وكفى بالله وكيلا'),
(3537, 33, 4, 'ما جعل الله لرجل من قلبين في جوفه ۚ وما جعل أزواجكم اللائي تظاهرون منهن أمهاتكم ۚ وما جعل أدعياءكم أبناءكم ۚ ذلكم قولكم بأفواهكم ۖ والله يقول الحق وهو يهدي السبيل'),
(3538, 33, 5, 'ادعوهم لآبائهم هو أقسط عند الله ۚ فإن لم تعلموا آباءهم فإخوانكم في الدين ومواليكم ۚ وليس عليكم جناح فيما أخطأتم به ولكن ما تعمدت قلوبكم ۚ وكان الله غفورا رحيما'),
(3539, 33, 6, 'النبي أولى بالمؤمنين من أنفسهم ۖ وأزواجه أمهاتهم ۗ وأولو الأرحام بعضهم أولى ببعض في كتاب الله من المؤمنين والمهاجرين إلا أن تفعلوا إلى أوليائكم معروفا ۚ كان ذلك في الكتاب مسطورا'),
(3540, 33, 7, 'وإذ أخذنا من النبيين ميثاقهم ومنك ومن نوح وإبراهيم وموسى وعيسى ابن مريم ۖ وأخذنا منهم ميثاقا غليظا'),
(3541, 33, 8, 'ليسأل الصادقين عن صدقهم ۚ وأعد للكافرين عذابا أليما'),
(3542, 33, 9, 'يا أيها الذين آمنوا اذكروا نعمة الله عليكم إذ جاءتكم جنود فأرسلنا عليهم ريحا وجنودا لم تروها ۚ وكان الله بما تعملون بصيرا'),
(3543, 33, 10, 'إذ جاءوكم من فوقكم ومن أسفل منكم وإذ زاغت الأبصار وبلغت القلوب الحناجر وتظنون بالله الظنونا'),
(3544, 33, 11, 'هنالك ابتلي المؤمنون وزلزلوا زلزالا شديدا'),
(3545, 33, 12, 'وإذ يقول المنافقون والذين في قلوبهم مرض ما وعدنا الله ورسوله إلا غرورا'),
(3546, 33, 13, 'وإذ قالت طائفة منهم يا أهل يثرب لا مقام لكم فارجعوا ۚ ويستأذن فريق منهم النبي يقولون إن بيوتنا عورة وما هي بعورة ۖ إن يريدون إلا فرارا'),
(3547, 33, 14, 'ولو دخلت عليهم من أقطارها ثم سئلوا الفتنة لآتوها وما تلبثوا بها إلا يسيرا'),
(3548, 33, 15, 'ولقد كانوا عاهدوا الله من قبل لا يولون الأدبار ۚ وكان عهد الله مسئولا'),
(3549, 33, 16, 'قل لن ينفعكم الفرار إن فررتم من الموت أو القتل وإذا لا تمتعون إلا قليلا'),
(3550, 33, 17, 'قل من ذا الذي يعصمكم من الله إن أراد بكم سوءا أو أراد بكم رحمة ۚ ولا يجدون لهم من دون الله وليا ولا نصيرا'),
(3551, 33, 18, 'قد يعلم الله المعوقين منكم والقائلين لإخوانهم هلم إلينا ۖ ولا يأتون البأس إلا قليلا'),
(3552, 33, 19, 'أشحة عليكم ۖ فإذا جاء الخوف رأيتهم ينظرون إليك تدور أعينهم كالذي يغشى عليه من الموت ۖ فإذا ذهب الخوف سلقوكم بألسنة حداد أشحة على الخير ۚ أولئك لم يؤمنوا فأحبط الله أعمالهم ۚ وكان ذلك على الله يسيرا'),
(3553, 33, 20, 'يحسبون الأحزاب لم يذهبوا ۖ وإن يأت الأحزاب يودوا لو أنهم بادون في الأعراب يسألون عن أنبائكم ۖ ولو كانوا فيكم ما قاتلوا إلا قليلا'),
(3554, 33, 21, 'لقد كان لكم في رسول الله أسوة حسنة لمن كان يرجو الله واليوم الآخر وذكر الله كثيرا'),
(3555, 33, 22, 'ولما رأى المؤمنون الأحزاب قالوا هذا ما وعدنا الله ورسوله وصدق الله ورسوله ۚ وما زادهم إلا إيمانا وتسليما'),
(3556, 33, 23, 'من المؤمنين رجال صدقوا ما عاهدوا الله عليه ۖ فمنهم من قضى نحبه ومنهم من ينتظر ۖ وما بدلوا تبديلا'),
(3557, 33, 24, 'ليجزي الله الصادقين بصدقهم ويعذب المنافقين إن شاء أو يتوب عليهم ۚ إن الله كان غفورا رحيما'),
(3558, 33, 25, 'ورد الله الذين كفروا بغيظهم لم ينالوا خيرا ۚ وكفى الله المؤمنين القتال ۚ وكان الله قويا عزيزا'),
(3559, 33, 26, 'وأنزل الذين ظاهروهم من أهل الكتاب من صياصيهم وقذف في قلوبهم الرعب فريقا تقتلون وتأسرون فريقا'),
(3560, 33, 27, 'وأورثكم أرضهم وديارهم وأموالهم وأرضا لم تطئوها ۚ وكان الله على كل شيء قديرا'),
(3561, 33, 28, 'يا أيها النبي قل لأزواجك إن كنتن تردن الحياة الدنيا وزينتها فتعالين أمتعكن وأسرحكن سراحا جميلا'),
(3562, 33, 29, 'وإن كنتن تردن الله ورسوله والدار الآخرة فإن الله أعد للمحسنات منكن أجرا عظيما'),
(3563, 33, 30, 'يا نساء النبي من يأت منكن بفاحشة مبينة يضاعف لها العذاب ضعفين ۚ وكان ذلك على الله يسيرا'),
(3564, 33, 31, 'ومن يقنت منكن لله ورسوله وتعمل صالحا نؤتها أجرها مرتين وأعتدنا لها رزقا كريما'),
(3565, 33, 32, 'يا نساء النبي لستن كأحد من النساء ۚ إن اتقيتن فلا تخضعن بالقول فيطمع الذي في قلبه مرض وقلن قولا معروفا'),
(3566, 33, 33, 'وقرن في بيوتكن ولا تبرجن تبرج الجاهلية الأولى ۖ وأقمن الصلاة وآتين الزكاة وأطعن الله ورسوله ۚ إنما يريد الله ليذهب عنكم الرجس أهل البيت ويطهركم تطهيرا'),
(3567, 33, 34, 'واذكرن ما يتلى في بيوتكن من آيات الله والحكمة ۚ إن الله كان لطيفا خبيرا'),
(3568, 33, 35, 'إن المسلمين والمسلمات والمؤمنين والمؤمنات والقانتين والقانتات والصادقين والصادقات والصابرين والصابرات والخاشعين والخاشعات والمتصدقين والمتصدقات والصائمين والصائمات والحافظين فروجهم والحافظات والذاكرين الله كثيرا والذاكرات أعد الله لهم مغفرة وأجرا عظيما'),
(3569, 33, 36, 'وما كان لمؤمن ولا مؤمنة إذا قضى الله ورسوله أمرا أن يكون لهم الخيرة من أمرهم ۗ ومن يعص الله ورسوله فقد ضل ضلالا مبينا'),
(3570, 33, 37, 'وإذ تقول للذي أنعم الله عليه وأنعمت عليه أمسك عليك زوجك واتق الله وتخفي في نفسك ما الله مبديه وتخشى الناس والله أحق أن تخشاه ۖ فلما قضى زيد منها وطرا زوجناكها لكي لا يكون على المؤمنين حرج في أزواج أدعيائهم إذا قضوا منهن وطرا ۚ وكان أمر الله مفعولا'),
(3571, 33, 38, 'ما كان على النبي من حرج فيما فرض الله له ۖ سنة الله في الذين خلوا من قبل ۚ وكان أمر الله قدرا مقدورا'),
(3572, 33, 39, 'الذين يبلغون رسالات الله ويخشونه ولا يخشون أحدا إلا الله ۗ وكفى بالله حسيبا'),
(3573, 33, 40, 'ما كان محمد أبا أحد من رجالكم ولكن رسول الله وخاتم النبيين ۗ وكان الله بكل شيء عليما'),
(3574, 33, 41, 'يا أيها الذين آمنوا اذكروا الله ذكرا كثيرا'),
(3575, 33, 42, 'وسبحوه بكرة وأصيلا'),
(3576, 33, 43, 'هو الذي يصلي عليكم وملائكته ليخرجكم من الظلمات إلى النور ۚ وكان بالمؤمنين رحيما'),
(3577, 33, 44, 'تحيتهم يوم يلقونه سلام ۚ وأعد لهم أجرا كريما'),
(3578, 33, 45, 'يا أيها النبي إنا أرسلناك شاهدا ومبشرا ونذيرا'),
(3579, 33, 46, 'وداعيا إلى الله بإذنه وسراجا منيرا'),
(3580, 33, 47, 'وبشر المؤمنين بأن لهم من الله فضلا كبيرا'),
(3581, 33, 48, 'ولا تطع الكافرين والمنافقين ودع أذاهم وتوكل على الله ۚ وكفى بالله وكيلا'),
(3582, 33, 49, 'يا أيها الذين آمنوا إذا نكحتم المؤمنات ثم طلقتموهن من قبل أن تمسوهن فما لكم عليهن من عدة تعتدونها ۖ فمتعوهن وسرحوهن سراحا جميلا'),
(3583, 33, 50, 'يا أيها النبي إنا أحللنا لك أزواجك اللاتي آتيت أجورهن وما ملكت يمينك مما أفاء الله عليك وبنات عمك وبنات عماتك وبنات خالك وبنات خالاتك اللاتي هاجرن معك وامرأة مؤمنة إن وهبت نفسها للنبي إن أراد النبي أن يستنكحها خالصة لك من دون المؤمنين ۗ قد علمنا ما فرضنا عليهم في أزواجهم وما ملكت أيمانهم لكيلا يكون عليك حرج ۗ وكان الله غفورا رحيما'),
(3584, 33, 51, 'ترجي من تشاء منهن وتؤوي إليك من تشاء ۖ ومن ابتغيت ممن عزلت فلا جناح عليك ۚ ذلك أدنى أن تقر أعينهن ولا يحزن ويرضين بما آتيتهن كلهن ۚ والله يعلم ما في قلوبكم ۚ وكان الله عليما حليما'),
(3585, 33, 52, 'لا يحل لك النساء من بعد ولا أن تبدل بهن من أزواج ولو أعجبك حسنهن إلا ما ملكت يمينك ۗ وكان الله على كل شيء رقيبا'),
(3586, 33, 53, 'يا أيها الذين آمنوا لا تدخلوا بيوت النبي إلا أن يؤذن لكم إلى طعام غير ناظرين إناه ولكن إذا دعيتم فادخلوا فإذا طعمتم فانتشروا ولا مستأنسين لحديث ۚ إن ذلكم كان يؤذي النبي فيستحيي منكم ۖ والله لا يستحيي من الحق ۚ وإذا سألتموهن متاعا فاسألوهن من وراء حجاب ۚ ذلكم أطهر لقلوبكم وقلوبهن ۚ وما كان لكم أن تؤذوا رسول الله ولا أن تنكحوا أزواجه من بعده أبدا ۚ إن ذلكم كان عند الله عظيما'),
(3587, 33, 54, 'إن تبدوا شيئا أو تخفوه فإن الله كان بكل شيء عليما'),
(3588, 33, 55, 'لا جناح عليهن في آبائهن ولا أبنائهن ولا إخوانهن ولا أبناء إخوانهن ولا أبناء أخواتهن ولا نسائهن ولا ما ملكت أيمانهن ۗ واتقين الله ۚ إن الله كان على كل شيء شهيدا'),
(3589, 33, 56, 'إن الله وملائكته يصلون على النبي ۚ يا أيها الذين آمنوا صلوا عليه وسلموا تسليما'),
(3590, 33, 57, 'إن الذين يؤذون الله ورسوله لعنهم الله في الدنيا والآخرة وأعد لهم عذابا مهينا'),
(3591, 33, 58, 'والذين يؤذون المؤمنين والمؤمنات بغير ما اكتسبوا فقد احتملوا بهتانا وإثما مبينا'),
(3592, 33, 59, 'يا أيها النبي قل لأزواجك وبناتك ونساء المؤمنين يدنين عليهن من جلابيبهن ۚ ذلك أدنى أن يعرفن فلا يؤذين ۗ وكان الله غفورا رحيما'),
(3593, 33, 60, 'لئن لم ينته المنافقون والذين في قلوبهم مرض والمرجفون في المدينة لنغرينك بهم ثم لا يجاورونك فيها إلا قليلا'),
(3594, 33, 61, 'ملعونين ۖ أينما ثقفوا أخذوا وقتلوا تقتيلا'),
(3595, 33, 62, 'سنة الله في الذين خلوا من قبل ۖ ولن تجد لسنة الله تبديلا'),
(3596, 33, 63, 'يسألك الناس عن الساعة ۖ قل إنما علمها عند الله ۚ وما يدريك لعل الساعة تكون قريبا'),
(3597, 33, 64, 'إن الله لعن الكافرين وأعد لهم سعيرا'),
(3598, 33, 65, 'خالدين فيها أبدا ۖ لا يجدون وليا ولا نصيرا'),
(3599, 33, 66, 'يوم تقلب وجوههم في النار يقولون يا ليتنا أطعنا الله وأطعنا الرسولا'),
(3600, 33, 67, 'وقالوا ربنا إنا أطعنا سادتنا وكبراءنا فأضلونا السبيلا'),
(3601, 33, 68, 'ربنا آتهم ضعفين من العذاب والعنهم لعنا كبيرا'),
(3602, 33, 69, 'يا أيها الذين آمنوا لا تكونوا كالذين آذوا موسى فبرأه الله مما قالوا ۚ وكان عند الله وجيها'),
(3603, 33, 70, 'يا أيها الذين آمنوا اتقوا الله وقولوا قولا سديدا'),
(3604, 33, 71, 'يصلح لكم أعمالكم ويغفر لكم ذنوبكم ۗ ومن يطع الله ورسوله فقد فاز فوزا عظيما'),
(3605, 33, 72, 'إنا عرضنا الأمانة على السماوات والأرض والجبال فأبين أن يحملنها وأشفقن منها وحملها الإنسان ۖ إنه كان ظلوما جهولا'),
(3606, 33, 73, 'ليعذب الله المنافقين والمنافقات والمشركين والمشركات ويتوب الله على المؤمنين والمؤمنات ۗ وكان الله غفورا رحيما');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'بسم الله الرحمن الرحيم الحمد لله الذي له ما في السماوات وما في الأرض وله الحمد في الآخرة ۚ وهو الحكيم الخبير'),
(3608, 34, 2, 'يعلم ما يلج في الأرض وما يخرج منها وما ينزل من السماء وما يعرج فيها ۚ وهو الرحيم الغفور'),
(3609, 34, 3, 'وقال الذين كفروا لا تأتينا الساعة ۖ قل بلى وربي لتأتينكم عالم الغيب ۖ لا يعزب عنه مثقال ذرة في السماوات ولا في الأرض ولا أصغر من ذلك ولا أكبر إلا في كتاب مبين'),
(3610, 34, 4, 'ليجزي الذين آمنوا وعملوا الصالحات ۚ أولئك لهم مغفرة ورزق كريم'),
(3611, 34, 5, 'والذين سعوا في آياتنا معاجزين أولئك لهم عذاب من رجز أليم'),
(3612, 34, 6, 'ويرى الذين أوتوا العلم الذي أنزل إليك من ربك هو الحق ويهدي إلى صراط العزيز الحميد'),
(3613, 34, 7, 'وقال الذين كفروا هل ندلكم على رجل ينبئكم إذا مزقتم كل ممزق إنكم لفي خلق جديد'),
(3614, 34, 8, 'أفترى على الله كذبا أم به جنة ۗ بل الذين لا يؤمنون بالآخرة في العذاب والضلال البعيد'),
(3615, 34, 9, 'أفلم يروا إلى ما بين أيديهم وما خلفهم من السماء والأرض ۚ إن نشأ نخسف بهم الأرض أو نسقط عليهم كسفا من السماء ۚ إن في ذلك لآية لكل عبد منيب'),
(3616, 34, 10, 'ولقد آتينا داوود منا فضلا ۖ يا جبال أوبي معه والطير ۖ وألنا له الحديد'),
(3617, 34, 11, 'أن اعمل سابغات وقدر في السرد ۖ واعملوا صالحا ۖ إني بما تعملون بصير'),
(3618, 34, 12, 'ولسليمان الريح غدوها شهر ورواحها شهر ۖ وأسلنا له عين القطر ۖ ومن الجن من يعمل بين يديه بإذن ربه ۖ ومن يزغ منهم عن أمرنا نذقه من عذاب السعير'),
(3619, 34, 13, 'يعملون له ما يشاء من محاريب وتماثيل وجفان كالجواب وقدور راسيات ۚ اعملوا آل داوود شكرا ۚ وقليل من عبادي الشكور'),
(3620, 34, 14, 'فلما قضينا عليه الموت ما دلهم على موته إلا دابة الأرض تأكل منسأته ۖ فلما خر تبينت الجن أن لو كانوا يعلمون الغيب ما لبثوا في العذاب المهين'),
(3621, 34, 15, 'لقد كان لسبإ في مسكنهم آية ۖ جنتان عن يمين وشمال ۖ كلوا من رزق ربكم واشكروا له ۚ بلدة طيبة ورب غفور'),
(3622, 34, 16, 'فأعرضوا فأرسلنا عليهم سيل العرم وبدلناهم بجنتيهم جنتين ذواتي أكل خمط وأثل وشيء من سدر قليل'),
(3623, 34, 17, 'ذلك جزيناهم بما كفروا ۖ وهل نجازي إلا الكفور'),
(3624, 34, 18, 'وجعلنا بينهم وبين القرى التي باركنا فيها قرى ظاهرة وقدرنا فيها السير ۖ سيروا فيها ليالي وأياما آمنين'),
(3625, 34, 19, 'فقالوا ربنا باعد بين أسفارنا وظلموا أنفسهم فجعلناهم أحاديث ومزقناهم كل ممزق ۚ إن في ذلك لآيات لكل صبار شكور'),
(3626, 34, 20, 'ولقد صدق عليهم إبليس ظنه فاتبعوه إلا فريقا من المؤمنين'),
(3627, 34, 21, 'وما كان له عليهم من سلطان إلا لنعلم من يؤمن بالآخرة ممن هو منها في شك ۗ وربك على كل شيء حفيظ'),
(3628, 34, 22, 'قل ادعوا الذين زعمتم من دون الله ۖ لا يملكون مثقال ذرة في السماوات ولا في الأرض وما لهم فيهما من شرك وما له منهم من ظهير'),
(3629, 34, 23, 'ولا تنفع الشفاعة عنده إلا لمن أذن له ۚ حتى إذا فزع عن قلوبهم قالوا ماذا قال ربكم ۖ قالوا الحق ۖ وهو العلي الكبير'),
(3630, 34, 24, 'قل من يرزقكم من السماوات والأرض ۖ قل الله ۖ وإنا أو إياكم لعلى هدى أو في ضلال مبين'),
(3631, 34, 25, 'قل لا تسألون عما أجرمنا ولا نسأل عما تعملون'),
(3632, 34, 26, 'قل يجمع بيننا ربنا ثم يفتح بيننا بالحق وهو الفتاح العليم'),
(3633, 34, 27, 'قل أروني الذين ألحقتم به شركاء ۖ كلا ۚ بل هو الله العزيز الحكيم'),
(3634, 34, 28, 'وما أرسلناك إلا كافة للناس بشيرا ونذيرا ولكن أكثر الناس لا يعلمون'),
(3635, 34, 29, 'ويقولون متى هذا الوعد إن كنتم صادقين'),
(3636, 34, 30, 'قل لكم ميعاد يوم لا تستأخرون عنه ساعة ولا تستقدمون'),
(3637, 34, 31, 'وقال الذين كفروا لن نؤمن بهذا القرآن ولا بالذي بين يديه ۗ ولو ترى إذ الظالمون موقوفون عند ربهم يرجع بعضهم إلى بعض القول يقول الذين استضعفوا للذين استكبروا لولا أنتم لكنا مؤمنين'),
(3638, 34, 32, 'قال الذين استكبروا للذين استضعفوا أنحن صددناكم عن الهدى بعد إذ جاءكم ۖ بل كنتم مجرمين'),
(3639, 34, 33, 'وقال الذين استضعفوا للذين استكبروا بل مكر الليل والنهار إذ تأمروننا أن نكفر بالله ونجعل له أندادا ۚ وأسروا الندامة لما رأوا العذاب وجعلنا الأغلال في أعناق الذين كفروا ۚ هل يجزون إلا ما كانوا يعملون'),
(3640, 34, 34, 'وما أرسلنا في قرية من نذير إلا قال مترفوها إنا بما أرسلتم به كافرون'),
(3641, 34, 35, 'وقالوا نحن أكثر أموالا وأولادا وما نحن بمعذبين'),
(3642, 34, 36, 'قل إن ربي يبسط الرزق لمن يشاء ويقدر ولكن أكثر الناس لا يعلمون'),
(3643, 34, 37, 'وما أموالكم ولا أولادكم بالتي تقربكم عندنا زلفى إلا من آمن وعمل صالحا فأولئك لهم جزاء الضعف بما عملوا وهم في الغرفات آمنون'),
(3644, 34, 38, 'والذين يسعون في آياتنا معاجزين أولئك في العذاب محضرون'),
(3645, 34, 39, 'قل إن ربي يبسط الرزق لمن يشاء من عباده ويقدر له ۚ وما أنفقتم من شيء فهو يخلفه ۖ وهو خير الرازقين'),
(3646, 34, 40, 'ويوم يحشرهم جميعا ثم يقول للملائكة أهؤلاء إياكم كانوا يعبدون'),
(3647, 34, 41, 'قالوا سبحانك أنت ولينا من دونهم ۖ بل كانوا يعبدون الجن ۖ أكثرهم بهم مؤمنون'),
(3648, 34, 42, 'فاليوم لا يملك بعضكم لبعض نفعا ولا ضرا ونقول للذين ظلموا ذوقوا عذاب النار التي كنتم بها تكذبون'),
(3649, 34, 43, 'وإذا تتلى عليهم آياتنا بينات قالوا ما هذا إلا رجل يريد أن يصدكم عما كان يعبد آباؤكم وقالوا ما هذا إلا إفك مفترى ۚ وقال الذين كفروا للحق لما جاءهم إن هذا إلا سحر مبين'),
(3650, 34, 44, 'وما آتيناهم من كتب يدرسونها ۖ وما أرسلنا إليهم قبلك من نذير'),
(3651, 34, 45, 'وكذب الذين من قبلهم وما بلغوا معشار ما آتيناهم فكذبوا رسلي ۖ فكيف كان نكير'),
(3652, 34, 46, 'قل إنما أعظكم بواحدة ۖ أن تقوموا لله مثنى وفرادى ثم تتفكروا ۚ ما بصاحبكم من جنة ۚ إن هو إلا نذير لكم بين يدي عذاب شديد'),
(3653, 34, 47, 'قل ما سألتكم من أجر فهو لكم ۖ إن أجري إلا على الله ۖ وهو على كل شيء شهيد'),
(3654, 34, 48, 'قل إن ربي يقذف بالحق علام الغيوب'),
(3655, 34, 49, 'قل جاء الحق وما يبدئ الباطل وما يعيد'),
(3656, 34, 50, 'قل إن ضللت فإنما أضل على نفسي ۖ وإن اهتديت فبما يوحي إلي ربي ۚ إنه سميع قريب'),
(3657, 34, 51, 'ولو ترى إذ فزعوا فلا فوت وأخذوا من مكان قريب'),
(3658, 34, 52, 'وقالوا آمنا به وأنى لهم التناوش من مكان بعيد'),
(3659, 34, 53, 'وقد كفروا به من قبل ۖ ويقذفون بالغيب من مكان بعيد'),
(3660, 34, 54, 'وحيل بينهم وبين ما يشتهون كما فعل بأشياعهم من قبل ۚ إنهم كانوا في شك مريب');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'بسم الله الرحمن الرحيم الحمد لله فاطر السماوات والأرض جاعل الملائكة رسلا أولي أجنحة مثنى وثلاث ورباع ۚ يزيد في الخلق ما يشاء ۚ إن الله على كل شيء قدير'),
(3662, 35, 2, 'ما يفتح الله للناس من رحمة فلا ممسك لها ۖ وما يمسك فلا مرسل له من بعده ۚ وهو العزيز الحكيم'),
(3663, 35, 3, 'يا أيها الناس اذكروا نعمت الله عليكم ۚ هل من خالق غير الله يرزقكم من السماء والأرض ۚ لا إله إلا هو ۖ فأنى تؤفكون'),
(3664, 35, 4, 'وإن يكذبوك فقد كذبت رسل من قبلك ۚ وإلى الله ترجع الأمور'),
(3665, 35, 5, 'يا أيها الناس إن وعد الله حق ۖ فلا تغرنكم الحياة الدنيا ۖ ولا يغرنكم بالله الغرور'),
(3666, 35, 6, 'إن الشيطان لكم عدو فاتخذوه عدوا ۚ إنما يدعو حزبه ليكونوا من أصحاب السعير'),
(3667, 35, 7, 'الذين كفروا لهم عذاب شديد ۖ والذين آمنوا وعملوا الصالحات لهم مغفرة وأجر كبير'),
(3668, 35, 8, 'أفمن زين له سوء عمله فرآه حسنا ۖ فإن الله يضل من يشاء ويهدي من يشاء ۖ فلا تذهب نفسك عليهم حسرات ۚ إن الله عليم بما يصنعون'),
(3669, 35, 9, 'والله الذي أرسل الرياح فتثير سحابا فسقناه إلى بلد ميت فأحيينا به الأرض بعد موتها ۚ كذلك النشور'),
(3670, 35, 10, 'من كان يريد العزة فلله العزة جميعا ۚ إليه يصعد الكلم الطيب والعمل الصالح يرفعه ۚ والذين يمكرون السيئات لهم عذاب شديد ۖ ومكر أولئك هو يبور'),
(3671, 35, 11, 'والله خلقكم من تراب ثم من نطفة ثم جعلكم أزواجا ۚ وما تحمل من أنثى ولا تضع إلا بعلمه ۚ وما يعمر من معمر ولا ينقص من عمره إلا في كتاب ۚ إن ذلك على الله يسير'),
(3672, 35, 12, 'وما يستوي البحران هذا عذب فرات سائغ شرابه وهذا ملح أجاج ۖ ومن كل تأكلون لحما طريا وتستخرجون حلية تلبسونها ۖ وترى الفلك فيه مواخر لتبتغوا من فضله ولعلكم تشكرون'),
(3673, 35, 13, 'يولج الليل في النهار ويولج النهار في الليل وسخر الشمس والقمر كل يجري لأجل مسمى ۚ ذلكم الله ربكم له الملك ۚ والذين تدعون من دونه ما يملكون من قطمير'),
(3674, 35, 14, 'إن تدعوهم لا يسمعوا دعاءكم ولو سمعوا ما استجابوا لكم ۖ ويوم القيامة يكفرون بشرككم ۚ ولا ينبئك مثل خبير'),
(3675, 35, 15, 'يا أيها الناس أنتم الفقراء إلى الله ۖ والله هو الغني الحميد'),
(3676, 35, 16, 'إن يشأ يذهبكم ويأت بخلق جديد'),
(3677, 35, 17, 'وما ذلك على الله بعزيز'),
(3678, 35, 18, 'ولا تزر وازرة وزر أخرى ۚ وإن تدع مثقلة إلى حملها لا يحمل منه شيء ولو كان ذا قربى ۗ إنما تنذر الذين يخشون ربهم بالغيب وأقاموا الصلاة ۚ ومن تزكى فإنما يتزكى لنفسه ۚ وإلى الله المصير'),
(3679, 35, 19, 'وما يستوي الأعمى والبصير'),
(3680, 35, 20, 'ولا الظلمات ولا النور'),
(3681, 35, 21, 'ولا الظل ولا الحرور'),
(3682, 35, 22, 'وما يستوي الأحياء ولا الأموات ۚ إن الله يسمع من يشاء ۖ وما أنت بمسمع من في القبور'),
(3683, 35, 23, 'إن أنت إلا نذير'),
(3684, 35, 24, 'إنا أرسلناك بالحق بشيرا ونذيرا ۚ وإن من أمة إلا خلا فيها نذير'),
(3685, 35, 25, 'وإن يكذبوك فقد كذب الذين من قبلهم جاءتهم رسلهم بالبينات وبالزبر وبالكتاب المنير'),
(3686, 35, 26, 'ثم أخذت الذين كفروا ۖ فكيف كان نكير'),
(3687, 35, 27, 'ألم تر أن الله أنزل من السماء ماء فأخرجنا به ثمرات مختلفا ألوانها ۚ ومن الجبال جدد بيض وحمر مختلف ألوانها وغرابيب سود'),
(3688, 35, 28, 'ومن الناس والدواب والأنعام مختلف ألوانه كذلك ۗ إنما يخشى الله من عباده العلماء ۗ إن الله عزيز غفور'),
(3689, 35, 29, 'إن الذين يتلون كتاب الله وأقاموا الصلاة وأنفقوا مما رزقناهم سرا وعلانية يرجون تجارة لن تبور'),
(3690, 35, 30, 'ليوفيهم أجورهم ويزيدهم من فضله ۚ إنه غفور شكور'),
(3691, 35, 31, 'والذي أوحينا إليك من الكتاب هو الحق مصدقا لما بين يديه ۗ إن الله بعباده لخبير بصير'),
(3692, 35, 32, 'ثم أورثنا الكتاب الذين اصطفينا من عبادنا ۖ فمنهم ظالم لنفسه ومنهم مقتصد ومنهم سابق بالخيرات بإذن الله ۚ ذلك هو الفضل الكبير'),
(3693, 35, 33, 'جنات عدن يدخلونها يحلون فيها من أساور من ذهب ولؤلؤا ۖ ولباسهم فيها حرير'),
(3694, 35, 34, 'وقالوا الحمد لله الذي أذهب عنا الحزن ۖ إن ربنا لغفور شكور'),
(3695, 35, 35, 'الذي أحلنا دار المقامة من فضله لا يمسنا فيها نصب ولا يمسنا فيها لغوب'),
(3696, 35, 36, 'والذين كفروا لهم نار جهنم لا يقضى عليهم فيموتوا ولا يخفف عنهم من عذابها ۚ كذلك نجزي كل كفور'),
(3697, 35, 37, 'وهم يصطرخون فيها ربنا أخرجنا نعمل صالحا غير الذي كنا نعمل ۚ أولم نعمركم ما يتذكر فيه من تذكر وجاءكم النذير ۖ فذوقوا فما للظالمين من نصير'),
(3698, 35, 38, 'إن الله عالم غيب السماوات والأرض ۚ إنه عليم بذات الصدور'),
(3699, 35, 39, 'هو الذي جعلكم خلائف في الأرض ۚ فمن كفر فعليه كفره ۖ ولا يزيد الكافرين كفرهم عند ربهم إلا مقتا ۖ ولا يزيد الكافرين كفرهم إلا خسارا'),
(3700, 35, 40, 'قل أرأيتم شركاءكم الذين تدعون من دون الله أروني ماذا خلقوا من الأرض أم لهم شرك في السماوات أم آتيناهم كتابا فهم على بينت منه ۚ بل إن يعد الظالمون بعضهم بعضا إلا غرورا'),
(3701, 35, 41, 'إن الله يمسك السماوات والأرض أن تزولا ۚ ولئن زالتا إن أمسكهما من أحد من بعده ۚ إنه كان حليما غفورا'),
(3702, 35, 42, 'وأقسموا بالله جهد أيمانهم لئن جاءهم نذير ليكونن أهدى من إحدى الأمم ۖ فلما جاءهم نذير ما زادهم إلا نفورا'),
(3703, 35, 43, 'استكبارا في الأرض ومكر السيئ ۚ ولا يحيق المكر السيئ إلا بأهله ۚ فهل ينظرون إلا سنت الأولين ۚ فلن تجد لسنت الله تبديلا ۖ ولن تجد لسنت الله تحويلا'),
(3704, 35, 44, 'أولم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم وكانوا أشد منهم قوة ۚ وما كان الله ليعجزه من شيء في السماوات ولا في الأرض ۚ إنه كان عليما قديرا'),
(3705, 35, 45, 'ولو يؤاخذ الله الناس بما كسبوا ما ترك على ظهرها من دابة ولكن يؤخرهم إلى أجل مسمى ۖ فإذا جاء أجلهم فإن الله كان بعباده بصيرا');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'بسم الله الرحمن الرحيم يس'),
(3707, 36, 2, 'والقرآن الحكيم'),
(3708, 36, 3, 'إنك لمن المرسلين'),
(3709, 36, 4, 'على صراط مستقيم'),
(3710, 36, 5, 'تنزيل العزيز الرحيم'),
(3711, 36, 6, 'لتنذر قوما ما أنذر آباؤهم فهم غافلون'),
(3712, 36, 7, 'لقد حق القول على أكثرهم فهم لا يؤمنون'),
(3713, 36, 8, 'إنا جعلنا في أعناقهم أغلالا فهي إلى الأذقان فهم مقمحون'),
(3714, 36, 9, 'وجعلنا من بين أيديهم سدا ومن خلفهم سدا فأغشيناهم فهم لا يبصرون'),
(3715, 36, 10, 'وسواء عليهم أأنذرتهم أم لم تنذرهم لا يؤمنون'),
(3716, 36, 11, 'إنما تنذر من اتبع الذكر وخشي الرحمن بالغيب ۖ فبشره بمغفرة وأجر كريم'),
(3717, 36, 12, 'إنا نحن نحيي الموتى ونكتب ما قدموا وآثارهم ۚ وكل شيء أحصيناه في إمام مبين'),
(3718, 36, 13, 'واضرب لهم مثلا أصحاب القرية إذ جاءها المرسلون'),
(3719, 36, 14, 'إذ أرسلنا إليهم اثنين فكذبوهما فعززنا بثالث فقالوا إنا إليكم مرسلون'),
(3720, 36, 15, 'قالوا ما أنتم إلا بشر مثلنا وما أنزل الرحمن من شيء إن أنتم إلا تكذبون'),
(3721, 36, 16, 'قالوا ربنا يعلم إنا إليكم لمرسلون'),
(3722, 36, 17, 'وما علينا إلا البلاغ المبين'),
(3723, 36, 18, 'قالوا إنا تطيرنا بكم ۖ لئن لم تنتهوا لنرجمنكم وليمسنكم منا عذاب أليم'),
(3724, 36, 19, 'قالوا طائركم معكم ۚ أئن ذكرتم ۚ بل أنتم قوم مسرفون'),
(3725, 36, 20, 'وجاء من أقصى المدينة رجل يسعى قال يا قوم اتبعوا المرسلين'),
(3726, 36, 21, 'اتبعوا من لا يسألكم أجرا وهم مهتدون'),
(3727, 36, 22, 'وما لي لا أعبد الذي فطرني وإليه ترجعون'),
(3728, 36, 23, 'أأتخذ من دونه آلهة إن يردن الرحمن بضر لا تغن عني شفاعتهم شيئا ولا ينقذون'),
(3729, 36, 24, 'إني إذا لفي ضلال مبين'),
(3730, 36, 25, 'إني آمنت بربكم فاسمعون'),
(3731, 36, 26, 'قيل ادخل الجنة ۖ قال يا ليت قومي يعلمون'),
(3732, 36, 27, 'بما غفر لي ربي وجعلني من المكرمين'),
(3733, 36, 28, 'وما أنزلنا على قومه من بعده من جند من السماء وما كنا منزلين'),
(3734, 36, 29, 'إن كانت إلا صيحة واحدة فإذا هم خامدون'),
(3735, 36, 30, 'يا حسرة على العباد ۚ ما يأتيهم من رسول إلا كانوا به يستهزئون'),
(3736, 36, 31, 'ألم يروا كم أهلكنا قبلهم من القرون أنهم إليهم لا يرجعون'),
(3737, 36, 32, 'وإن كل لما جميع لدينا محضرون'),
(3738, 36, 33, 'وآية لهم الأرض الميتة أحييناها وأخرجنا منها حبا فمنه يأكلون'),
(3739, 36, 34, 'وجعلنا فيها جنات من نخيل وأعناب وفجرنا فيها من العيون'),
(3740, 36, 35, 'ليأكلوا من ثمره وما عملته أيديهم ۖ أفلا يشكرون'),
(3741, 36, 36, 'سبحان الذي خلق الأزواج كلها مما تنبت الأرض ومن أنفسهم ومما لا يعلمون'),
(3742, 36, 37, 'وآية لهم الليل نسلخ منه النهار فإذا هم مظلمون'),
(3743, 36, 38, 'والشمس تجري لمستقر لها ۚ ذلك تقدير العزيز العليم'),
(3744, 36, 39, 'والقمر قدرناه منازل حتى عاد كالعرجون القديم'),
(3745, 36, 40, 'لا الشمس ينبغي لها أن تدرك القمر ولا الليل سابق النهار ۚ وكل في فلك يسبحون'),
(3746, 36, 41, 'وآية لهم أنا حملنا ذريتهم في الفلك المشحون'),
(3747, 36, 42, 'وخلقنا لهم من مثله ما يركبون'),
(3748, 36, 43, 'وإن نشأ نغرقهم فلا صريخ لهم ولا هم ينقذون'),
(3749, 36, 44, 'إلا رحمة منا ومتاعا إلى حين'),
(3750, 36, 45, 'وإذا قيل لهم اتقوا ما بين أيديكم وما خلفكم لعلكم ترحمون'),
(3751, 36, 46, 'وما تأتيهم من آية من آيات ربهم إلا كانوا عنها معرضين'),
(3752, 36, 47, 'وإذا قيل لهم أنفقوا مما رزقكم الله قال الذين كفروا للذين آمنوا أنطعم من لو يشاء الله أطعمه إن أنتم إلا في ضلال مبين'),
(3753, 36, 48, 'ويقولون متى هذا الوعد إن كنتم صادقين'),
(3754, 36, 49, 'ما ينظرون إلا صيحة واحدة تأخذهم وهم يخصمون'),
(3755, 36, 50, 'فلا يستطيعون توصية ولا إلى أهلهم يرجعون'),
(3756, 36, 51, 'ونفخ في الصور فإذا هم من الأجداث إلى ربهم ينسلون'),
(3757, 36, 52, 'قالوا يا ويلنا من بعثنا من مرقدنا ۜ ۗ هذا ما وعد الرحمن وصدق المرسلون'),
(3758, 36, 53, 'إن كانت إلا صيحة واحدة فإذا هم جميع لدينا محضرون'),
(3759, 36, 54, 'فاليوم لا تظلم نفس شيئا ولا تجزون إلا ما كنتم تعملون'),
(3760, 36, 55, 'إن أصحاب الجنة اليوم في شغل فاكهون'),
(3761, 36, 56, 'هم وأزواجهم في ظلال على الأرائك متكئون'),
(3762, 36, 57, 'لهم فيها فاكهة ولهم ما يدعون'),
(3763, 36, 58, 'سلام قولا من رب رحيم'),
(3764, 36, 59, 'وامتازوا اليوم أيها المجرمون'),
(3765, 36, 60, 'ألم أعهد إليكم يا بني آدم أن لا تعبدوا الشيطان ۖ إنه لكم عدو مبين'),
(3766, 36, 61, 'وأن اعبدوني ۚ هذا صراط مستقيم'),
(3767, 36, 62, 'ولقد أضل منكم جبلا كثيرا ۖ أفلم تكونوا تعقلون'),
(3768, 36, 63, 'هذه جهنم التي كنتم توعدون'),
(3769, 36, 64, 'اصلوها اليوم بما كنتم تكفرون'),
(3770, 36, 65, 'اليوم نختم على أفواههم وتكلمنا أيديهم وتشهد أرجلهم بما كانوا يكسبون'),
(3771, 36, 66, 'ولو نشاء لطمسنا على أعينهم فاستبقوا الصراط فأنى يبصرون'),
(3772, 36, 67, 'ولو نشاء لمسخناهم على مكانتهم فما استطاعوا مضيا ولا يرجعون'),
(3773, 36, 68, 'ومن نعمره ننكسه في الخلق ۖ أفلا يعقلون'),
(3774, 36, 69, 'وما علمناه الشعر وما ينبغي له ۚ إن هو إلا ذكر وقرآن مبين'),
(3775, 36, 70, 'لينذر من كان حيا ويحق القول على الكافرين'),
(3776, 36, 71, 'أولم يروا أنا خلقنا لهم مما عملت أيدينا أنعاما فهم لها مالكون'),
(3777, 36, 72, 'وذللناها لهم فمنها ركوبهم ومنها يأكلون'),
(3778, 36, 73, 'ولهم فيها منافع ومشارب ۖ أفلا يشكرون'),
(3779, 36, 74, 'واتخذوا من دون الله آلهة لعلهم ينصرون'),
(3780, 36, 75, 'لا يستطيعون نصرهم وهم لهم جند محضرون'),
(3781, 36, 76, 'فلا يحزنك قولهم ۘ إنا نعلم ما يسرون وما يعلنون'),
(3782, 36, 77, 'أولم ير الإنسان أنا خلقناه من نطفة فإذا هو خصيم مبين'),
(3783, 36, 78, 'وضرب لنا مثلا ونسي خلقه ۖ قال من يحيي العظام وهي رميم'),
(3784, 36, 79, 'قل يحييها الذي أنشأها أول مرة ۖ وهو بكل خلق عليم'),
(3785, 36, 80, 'الذي جعل لكم من الشجر الأخضر نارا فإذا أنتم منه توقدون'),
(3786, 36, 81, 'أوليس الذي خلق السماوات والأرض بقادر على أن يخلق مثلهم ۚ بلى وهو الخلاق العليم'),
(3787, 36, 82, 'إنما أمره إذا أراد شيئا أن يقول له كن فيكون'),
(3788, 36, 83, 'فسبحان الذي بيده ملكوت كل شيء وإليه ترجعون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'بسم الله الرحمن الرحيم والصافات صفا'),
(3790, 37, 2, 'فالزاجرات زجرا'),
(3791, 37, 3, 'فالتاليات ذكرا'),
(3792, 37, 4, 'إن إلهكم لواحد'),
(3793, 37, 5, 'رب السماوات والأرض وما بينهما ورب المشارق'),
(3794, 37, 6, 'إنا زينا السماء الدنيا بزينة الكواكب'),
(3795, 37, 7, 'وحفظا من كل شيطان مارد'),
(3796, 37, 8, 'لا يسمعون إلى الملإ الأعلى ويقذفون من كل جانب'),
(3797, 37, 9, 'دحورا ۖ ولهم عذاب واصب'),
(3798, 37, 10, 'إلا من خطف الخطفة فأتبعه شهاب ثاقب'),
(3799, 37, 11, 'فاستفتهم أهم أشد خلقا أم من خلقنا ۚ إنا خلقناهم من طين لازب'),
(3800, 37, 12, 'بل عجبت ويسخرون'),
(3801, 37, 13, 'وإذا ذكروا لا يذكرون'),
(3802, 37, 14, 'وإذا رأوا آية يستسخرون'),
(3803, 37, 15, 'وقالوا إن هذا إلا سحر مبين'),
(3804, 37, 16, 'أإذا متنا وكنا ترابا وعظاما أإنا لمبعوثون'),
(3805, 37, 17, 'أوآباؤنا الأولون'),
(3806, 37, 18, 'قل نعم وأنتم داخرون'),
(3807, 37, 19, 'فإنما هي زجرة واحدة فإذا هم ينظرون'),
(3808, 37, 20, 'وقالوا يا ويلنا هذا يوم الدين'),
(3809, 37, 21, 'هذا يوم الفصل الذي كنتم به تكذبون'),
(3810, 37, 22, 'احشروا الذين ظلموا وأزواجهم وما كانوا يعبدون'),
(3811, 37, 23, 'من دون الله فاهدوهم إلى صراط الجحيم'),
(3812, 37, 24, 'وقفوهم ۖ إنهم مسئولون'),
(3813, 37, 25, 'ما لكم لا تناصرون'),
(3814, 37, 26, 'بل هم اليوم مستسلمون'),
(3815, 37, 27, 'وأقبل بعضهم على بعض يتساءلون'),
(3816, 37, 28, 'قالوا إنكم كنتم تأتوننا عن اليمين'),
(3817, 37, 29, 'قالوا بل لم تكونوا مؤمنين'),
(3818, 37, 30, 'وما كان لنا عليكم من سلطان ۖ بل كنتم قوما طاغين'),
(3819, 37, 31, 'فحق علينا قول ربنا ۖ إنا لذائقون'),
(3820, 37, 32, 'فأغويناكم إنا كنا غاوين'),
(3821, 37, 33, 'فإنهم يومئذ في العذاب مشتركون'),
(3822, 37, 34, 'إنا كذلك نفعل بالمجرمين'),
(3823, 37, 35, 'إنهم كانوا إذا قيل لهم لا إله إلا الله يستكبرون'),
(3824, 37, 36, 'ويقولون أئنا لتاركو آلهتنا لشاعر مجنون'),
(3825, 37, 37, 'بل جاء بالحق وصدق المرسلين'),
(3826, 37, 38, 'إنكم لذائقو العذاب الأليم'),
(3827, 37, 39, 'وما تجزون إلا ما كنتم تعملون'),
(3828, 37, 40, 'إلا عباد الله المخلصين'),
(3829, 37, 41, 'أولئك لهم رزق معلوم'),
(3830, 37, 42, 'فواكه ۖ وهم مكرمون'),
(3831, 37, 43, 'في جنات النعيم'),
(3832, 37, 44, 'على سرر متقابلين'),
(3833, 37, 45, 'يطاف عليهم بكأس من معين'),
(3834, 37, 46, 'بيضاء لذة للشاربين'),
(3835, 37, 47, 'لا فيها غول ولا هم عنها ينزفون'),
(3836, 37, 48, 'وعندهم قاصرات الطرف عين'),
(3837, 37, 49, 'كأنهن بيض مكنون'),
(3838, 37, 50, 'فأقبل بعضهم على بعض يتساءلون'),
(3839, 37, 51, 'قال قائل منهم إني كان لي قرين'),
(3840, 37, 52, 'يقول أإنك لمن المصدقين'),
(3841, 37, 53, 'أإذا متنا وكنا ترابا وعظاما أإنا لمدينون'),
(3842, 37, 54, 'قال هل أنتم مطلعون'),
(3843, 37, 55, 'فاطلع فرآه في سواء الجحيم'),
(3844, 37, 56, 'قال تالله إن كدت لتردين'),
(3845, 37, 57, 'ولولا نعمة ربي لكنت من المحضرين'),
(3846, 37, 58, 'أفما نحن بميتين'),
(3847, 37, 59, 'إلا موتتنا الأولى وما نحن بمعذبين'),
(3848, 37, 60, 'إن هذا لهو الفوز العظيم'),
(3849, 37, 61, 'لمثل هذا فليعمل العاملون'),
(3850, 37, 62, 'أذلك خير نزلا أم شجرة الزقوم'),
(3851, 37, 63, 'إنا جعلناها فتنة للظالمين'),
(3852, 37, 64, 'إنها شجرة تخرج في أصل الجحيم'),
(3853, 37, 65, 'طلعها كأنه رءوس الشياطين'),
(3854, 37, 66, 'فإنهم لآكلون منها فمالئون منها البطون'),
(3855, 37, 67, 'ثم إن لهم عليها لشوبا من حميم'),
(3856, 37, 68, 'ثم إن مرجعهم لإلى الجحيم'),
(3857, 37, 69, 'إنهم ألفوا آباءهم ضالين'),
(3858, 37, 70, 'فهم على آثارهم يهرعون'),
(3859, 37, 71, 'ولقد ضل قبلهم أكثر الأولين'),
(3860, 37, 72, 'ولقد أرسلنا فيهم منذرين'),
(3861, 37, 73, 'فانظر كيف كان عاقبة المنذرين'),
(3862, 37, 74, 'إلا عباد الله المخلصين'),
(3863, 37, 75, 'ولقد نادانا نوح فلنعم المجيبون'),
(3864, 37, 76, 'ونجيناه وأهله من الكرب العظيم'),
(3865, 37, 77, 'وجعلنا ذريته هم الباقين'),
(3866, 37, 78, 'وتركنا عليه في الآخرين'),
(3867, 37, 79, 'سلام على نوح في العالمين'),
(3868, 37, 80, 'إنا كذلك نجزي المحسنين'),
(3869, 37, 81, 'إنه من عبادنا المؤمنين'),
(3870, 37, 82, 'ثم أغرقنا الآخرين'),
(3871, 37, 83, 'وإن من شيعته لإبراهيم'),
(3872, 37, 84, 'إذ جاء ربه بقلب سليم'),
(3873, 37, 85, 'إذ قال لأبيه وقومه ماذا تعبدون'),
(3874, 37, 86, 'أئفكا آلهة دون الله تريدون'),
(3875, 37, 87, 'فما ظنكم برب العالمين'),
(3876, 37, 88, 'فنظر نظرة في النجوم'),
(3877, 37, 89, 'فقال إني سقيم'),
(3878, 37, 90, 'فتولوا عنه مدبرين'),
(3879, 37, 91, 'فراغ إلى آلهتهم فقال ألا تأكلون'),
(3880, 37, 92, 'ما لكم لا تنطقون'),
(3881, 37, 93, 'فراغ عليهم ضربا باليمين'),
(3882, 37, 94, 'فأقبلوا إليه يزفون'),
(3883, 37, 95, 'قال أتعبدون ما تنحتون'),
(3884, 37, 96, 'والله خلقكم وما تعملون'),
(3885, 37, 97, 'قالوا ابنوا له بنيانا فألقوه في الجحيم'),
(3886, 37, 98, 'فأرادوا به كيدا فجعلناهم الأسفلين'),
(3887, 37, 99, 'وقال إني ذاهب إلى ربي سيهدين'),
(3888, 37, 100, 'رب هب لي من الصالحين'),
(3889, 37, 101, 'فبشرناه بغلام حليم'),
(3890, 37, 102, 'فلما بلغ معه السعي قال يا بني إني أرى في المنام أني أذبحك فانظر ماذا ترى ۚ قال يا أبت افعل ما تؤمر ۖ ستجدني إن شاء الله من الصابرين'),
(3891, 37, 103, 'فلما أسلما وتله للجبين'),
(3892, 37, 104, 'وناديناه أن يا إبراهيم'),
(3893, 37, 105, 'قد صدقت الرؤيا ۚ إنا كذلك نجزي المحسنين'),
(3894, 37, 106, 'إن هذا لهو البلاء المبين'),
(3895, 37, 107, 'وفديناه بذبح عظيم'),
(3896, 37, 108, 'وتركنا عليه في الآخرين'),
(3897, 37, 109, 'سلام على إبراهيم'),
(3898, 37, 110, 'كذلك نجزي المحسنين'),
(3899, 37, 111, 'إنه من عبادنا المؤمنين'),
(3900, 37, 112, 'وبشرناه بإسحاق نبيا من الصالحين'),
(3901, 37, 113, 'وباركنا عليه وعلى إسحاق ۚ ومن ذريتهما محسن وظالم لنفسه مبين'),
(3902, 37, 114, 'ولقد مننا على موسى وهارون'),
(3903, 37, 115, 'ونجيناهما وقومهما من الكرب العظيم'),
(3904, 37, 116, 'ونصرناهم فكانوا هم الغالبين'),
(3905, 37, 117, 'وآتيناهما الكتاب المستبين'),
(3906, 37, 118, 'وهديناهما الصراط المستقيم'),
(3907, 37, 119, 'وتركنا عليهما في الآخرين'),
(3908, 37, 120, 'سلام على موسى وهارون'),
(3909, 37, 121, 'إنا كذلك نجزي المحسنين'),
(3910, 37, 122, 'إنهما من عبادنا المؤمنين'),
(3911, 37, 123, 'وإن إلياس لمن المرسلين'),
(3912, 37, 124, 'إذ قال لقومه ألا تتقون'),
(3913, 37, 125, 'أتدعون بعلا وتذرون أحسن الخالقين'),
(3914, 37, 126, 'الله ربكم ورب آبائكم الأولين'),
(3915, 37, 127, 'فكذبوه فإنهم لمحضرون'),
(3916, 37, 128, 'إلا عباد الله المخلصين'),
(3917, 37, 129, 'وتركنا عليه في الآخرين'),
(3918, 37, 130, 'سلام على إل ياسين'),
(3919, 37, 131, 'إنا كذلك نجزي المحسنين'),
(3920, 37, 132, 'إنه من عبادنا المؤمنين'),
(3921, 37, 133, 'وإن لوطا لمن المرسلين'),
(3922, 37, 134, 'إذ نجيناه وأهله أجمعين'),
(3923, 37, 135, 'إلا عجوزا في الغابرين'),
(3924, 37, 136, 'ثم دمرنا الآخرين'),
(3925, 37, 137, 'وإنكم لتمرون عليهم مصبحين'),
(3926, 37, 138, 'وبالليل ۗ أفلا تعقلون'),
(3927, 37, 139, 'وإن يونس لمن المرسلين'),
(3928, 37, 140, 'إذ أبق إلى الفلك المشحون'),
(3929, 37, 141, 'فساهم فكان من المدحضين'),
(3930, 37, 142, 'فالتقمه الحوت وهو مليم'),
(3931, 37, 143, 'فلولا أنه كان من المسبحين'),
(3932, 37, 144, 'للبث في بطنه إلى يوم يبعثون'),
(3933, 37, 145, 'فنبذناه بالعراء وهو سقيم'),
(3934, 37, 146, 'وأنبتنا عليه شجرة من يقطين'),
(3935, 37, 147, 'وأرسلناه إلى مائة ألف أو يزيدون'),
(3936, 37, 148, 'فآمنوا فمتعناهم إلى حين'),
(3937, 37, 149, 'فاستفتهم ألربك البنات ولهم البنون'),
(3938, 37, 150, 'أم خلقنا الملائكة إناثا وهم شاهدون'),
(3939, 37, 151, 'ألا إنهم من إفكهم ليقولون'),
(3940, 37, 152, 'ولد الله وإنهم لكاذبون'),
(3941, 37, 153, 'أصطفى البنات على البنين'),
(3942, 37, 154, 'ما لكم كيف تحكمون'),
(3943, 37, 155, 'أفلا تذكرون'),
(3944, 37, 156, 'أم لكم سلطان مبين'),
(3945, 37, 157, 'فأتوا بكتابكم إن كنتم صادقين'),
(3946, 37, 158, 'وجعلوا بينه وبين الجنة نسبا ۚ ولقد علمت الجنة إنهم لمحضرون'),
(3947, 37, 159, 'سبحان الله عما يصفون'),
(3948, 37, 160, 'إلا عباد الله المخلصين'),
(3949, 37, 161, 'فإنكم وما تعبدون'),
(3950, 37, 162, 'ما أنتم عليه بفاتنين'),
(3951, 37, 163, 'إلا من هو صال الجحيم'),
(3952, 37, 164, 'وما منا إلا له مقام معلوم'),
(3953, 37, 165, 'وإنا لنحن الصافون'),
(3954, 37, 166, 'وإنا لنحن المسبحون'),
(3955, 37, 167, 'وإن كانوا ليقولون'),
(3956, 37, 168, 'لو أن عندنا ذكرا من الأولين'),
(3957, 37, 169, 'لكنا عباد الله المخلصين'),
(3958, 37, 170, 'فكفروا به ۖ فسوف يعلمون'),
(3959, 37, 171, 'ولقد سبقت كلمتنا لعبادنا المرسلين'),
(3960, 37, 172, 'إنهم لهم المنصورون'),
(3961, 37, 173, 'وإن جندنا لهم الغالبون'),
(3962, 37, 174, 'فتول عنهم حتى حين'),
(3963, 37, 175, 'وأبصرهم فسوف يبصرون'),
(3964, 37, 176, 'أفبعذابنا يستعجلون'),
(3965, 37, 177, 'فإذا نزل بساحتهم فساء صباح المنذرين'),
(3966, 37, 178, 'وتول عنهم حتى حين'),
(3967, 37, 179, 'وأبصر فسوف يبصرون'),
(3968, 37, 180, 'سبحان ربك رب العزة عما يصفون'),
(3969, 37, 181, 'وسلام على المرسلين'),
(3970, 37, 182, 'والحمد لله رب العالمين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'بسم الله الرحمن الرحيم ص ۚ والقرآن ذي الذكر'),
(3972, 38, 2, 'بل الذين كفروا في عزة وشقاق'),
(3973, 38, 3, 'كم أهلكنا من قبلهم من قرن فنادوا ولات حين مناص'),
(3974, 38, 4, 'وعجبوا أن جاءهم منذر منهم ۖ وقال الكافرون هذا ساحر كذاب'),
(3975, 38, 5, 'أجعل الآلهة إلها واحدا ۖ إن هذا لشيء عجاب'),
(3976, 38, 6, 'وانطلق الملأ منهم أن امشوا واصبروا على آلهتكم ۖ إن هذا لشيء يراد'),
(3977, 38, 7, 'ما سمعنا بهذا في الملة الآخرة إن هذا إلا اختلاق'),
(3978, 38, 8, 'أأنزل عليه الذكر من بيننا ۚ بل هم في شك من ذكري ۖ بل لما يذوقوا عذاب'),
(3979, 38, 9, 'أم عندهم خزائن رحمة ربك العزيز الوهاب'),
(3980, 38, 10, 'أم لهم ملك السماوات والأرض وما بينهما ۖ فليرتقوا في الأسباب'),
(3981, 38, 11, 'جند ما هنالك مهزوم من الأحزاب'),
(3982, 38, 12, 'كذبت قبلهم قوم نوح وعاد وفرعون ذو الأوتاد'),
(3983, 38, 13, 'وثمود وقوم لوط وأصحاب الأيكة ۚ أولئك الأحزاب'),
(3984, 38, 14, 'إن كل إلا كذب الرسل فحق عقاب'),
(3985, 38, 15, 'وما ينظر هؤلاء إلا صيحة واحدة ما لها من فواق'),
(3986, 38, 16, 'وقالوا ربنا عجل لنا قطنا قبل يوم الحساب'),
(3987, 38, 17, 'اصبر على ما يقولون واذكر عبدنا داوود ذا الأيد ۖ إنه أواب'),
(3988, 38, 18, 'إنا سخرنا الجبال معه يسبحن بالعشي والإشراق'),
(3989, 38, 19, 'والطير محشورة ۖ كل له أواب'),
(3990, 38, 20, 'وشددنا ملكه وآتيناه الحكمة وفصل الخطاب'),
(3991, 38, 21, 'وهل أتاك نبأ الخصم إذ تسوروا المحراب'),
(3992, 38, 22, 'إذ دخلوا على داوود ففزع منهم ۖ قالوا لا تخف ۖ خصمان بغى بعضنا على بعض فاحكم بيننا بالحق ولا تشطط واهدنا إلى سواء الصراط'),
(3993, 38, 23, 'إن هذا أخي له تسع وتسعون نعجة ولي نعجة واحدة فقال أكفلنيها وعزني في الخطاب'),
(3994, 38, 24, 'قال لقد ظلمك بسؤال نعجتك إلى نعاجه ۖ وإن كثيرا من الخلطاء ليبغي بعضهم على بعض إلا الذين آمنوا وعملوا الصالحات وقليل ما هم ۗ وظن داوود أنما فتناه فاستغفر ربه وخر راكعا وأناب'),
(3995, 38, 25, 'فغفرنا له ذلك ۖ وإن له عندنا لزلفى وحسن مآب'),
(3996, 38, 26, 'يا داوود إنا جعلناك خليفة في الأرض فاحكم بين الناس بالحق ولا تتبع الهوى فيضلك عن سبيل الله ۚ إن الذين يضلون عن سبيل الله لهم عذاب شديد بما نسوا يوم الحساب'),
(3997, 38, 27, 'وما خلقنا السماء والأرض وما بينهما باطلا ۚ ذلك ظن الذين كفروا ۚ فويل للذين كفروا من النار'),
(3998, 38, 28, 'أم نجعل الذين آمنوا وعملوا الصالحات كالمفسدين في الأرض أم نجعل المتقين كالفجار'),
(3999, 38, 29, 'كتاب أنزلناه إليك مبارك ليدبروا آياته وليتذكر أولو الألباب'),
(4000, 38, 30, 'ووهبنا لداوود سليمان ۚ نعم العبد ۖ إنه أواب'),
(4001, 38, 31, 'إذ عرض عليه بالعشي الصافنات الجياد'),
(4002, 38, 32, 'فقال إني أحببت حب الخير عن ذكر ربي حتى توارت بالحجاب'),
(4003, 38, 33, 'ردوها علي ۖ فطفق مسحا بالسوق والأعناق'),
(4004, 38, 34, 'ولقد فتنا سليمان وألقينا على كرسيه جسدا ثم أناب'),
(4005, 38, 35, 'قال رب اغفر لي وهب لي ملكا لا ينبغي لأحد من بعدي ۖ إنك أنت الوهاب'),
(4006, 38, 36, 'فسخرنا له الريح تجري بأمره رخاء حيث أصاب'),
(4007, 38, 37, 'والشياطين كل بناء وغواص'),
(4008, 38, 38, 'وآخرين مقرنين في الأصفاد'),
(4009, 38, 39, 'هذا عطاؤنا فامنن أو أمسك بغير حساب'),
(4010, 38, 40, 'وإن له عندنا لزلفى وحسن مآب'),
(4011, 38, 41, 'واذكر عبدنا أيوب إذ نادى ربه أني مسني الشيطان بنصب وعذاب'),
(4012, 38, 42, 'اركض برجلك ۖ هذا مغتسل بارد وشراب'),
(4013, 38, 43, 'ووهبنا له أهله ومثلهم معهم رحمة منا وذكرى لأولي الألباب'),
(4014, 38, 44, 'وخذ بيدك ضغثا فاضرب به ولا تحنث ۗ إنا وجدناه صابرا ۚ نعم العبد ۖ إنه أواب'),
(4015, 38, 45, 'واذكر عبادنا إبراهيم وإسحاق ويعقوب أولي الأيدي والأبصار'),
(4016, 38, 46, 'إنا أخلصناهم بخالصة ذكرى الدار'),
(4017, 38, 47, 'وإنهم عندنا لمن المصطفين الأخيار'),
(4018, 38, 48, 'واذكر إسماعيل واليسع وذا الكفل ۖ وكل من الأخيار'),
(4019, 38, 49, 'هذا ذكر ۚ وإن للمتقين لحسن مآب'),
(4020, 38, 50, 'جنات عدن مفتحة لهم الأبواب'),
(4021, 38, 51, 'متكئين فيها يدعون فيها بفاكهة كثيرة وشراب'),
(4022, 38, 52, 'وعندهم قاصرات الطرف أتراب'),
(4023, 38, 53, 'هذا ما توعدون ليوم الحساب'),
(4024, 38, 54, 'إن هذا لرزقنا ما له من نفاد'),
(4025, 38, 55, 'هذا ۚ وإن للطاغين لشر مآب'),
(4026, 38, 56, 'جهنم يصلونها فبئس المهاد'),
(4027, 38, 57, 'هذا فليذوقوه حميم وغساق'),
(4028, 38, 58, 'وآخر من شكله أزواج'),
(4029, 38, 59, 'هذا فوج مقتحم معكم ۖ لا مرحبا بهم ۚ إنهم صالو النار'),
(4030, 38, 60, 'قالوا بل أنتم لا مرحبا بكم ۖ أنتم قدمتموه لنا ۖ فبئس القرار'),
(4031, 38, 61, 'قالوا ربنا من قدم لنا هذا فزده عذابا ضعفا في النار'),
(4032, 38, 62, 'وقالوا ما لنا لا نرى رجالا كنا نعدهم من الأشرار'),
(4033, 38, 63, 'أتخذناهم سخريا أم زاغت عنهم الأبصار'),
(4034, 38, 64, 'إن ذلك لحق تخاصم أهل النار'),
(4035, 38, 65, 'قل إنما أنا منذر ۖ وما من إله إلا الله الواحد القهار'),
(4036, 38, 66, 'رب السماوات والأرض وما بينهما العزيز الغفار'),
(4037, 38, 67, 'قل هو نبأ عظيم'),
(4038, 38, 68, 'أنتم عنه معرضون'),
(4039, 38, 69, 'ما كان لي من علم بالملإ الأعلى إذ يختصمون'),
(4040, 38, 70, 'إن يوحى إلي إلا أنما أنا نذير مبين'),
(4041, 38, 71, 'إذ قال ربك للملائكة إني خالق بشرا من طين'),
(4042, 38, 72, 'فإذا سويته ونفخت فيه من روحي فقعوا له ساجدين'),
(4043, 38, 73, 'فسجد الملائكة كلهم أجمعون'),
(4044, 38, 74, 'إلا إبليس استكبر وكان من الكافرين'),
(4045, 38, 75, 'قال يا إبليس ما منعك أن تسجد لما خلقت بيدي ۖ أستكبرت أم كنت من العالين'),
(4046, 38, 76, 'قال أنا خير منه ۖ خلقتني من نار وخلقته من طين'),
(4047, 38, 77, 'قال فاخرج منها فإنك رجيم'),
(4048, 38, 78, 'وإن عليك لعنتي إلى يوم الدين'),
(4049, 38, 79, 'قال رب فأنظرني إلى يوم يبعثون'),
(4050, 38, 80, 'قال فإنك من المنظرين'),
(4051, 38, 81, 'إلى يوم الوقت المعلوم'),
(4052, 38, 82, 'قال فبعزتك لأغوينهم أجمعين'),
(4053, 38, 83, 'إلا عبادك منهم المخلصين'),
(4054, 38, 84, 'قال فالحق والحق أقول'),
(4055, 38, 85, 'لأملأن جهنم منك وممن تبعك منهم أجمعين'),
(4056, 38, 86, 'قل ما أسألكم عليه من أجر وما أنا من المتكلفين'),
(4057, 38, 87, 'إن هو إلا ذكر للعالمين'),
(4058, 38, 88, 'ولتعلمن نبأه بعد حين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'بسم الله الرحمن الرحيم تنزيل الكتاب من الله العزيز الحكيم'),
(4060, 39, 2, 'إنا أنزلنا إليك الكتاب بالحق فاعبد الله مخلصا له الدين'),
(4061, 39, 3, 'ألا لله الدين الخالص ۚ والذين اتخذوا من دونه أولياء ما نعبدهم إلا ليقربونا إلى الله زلفى إن الله يحكم بينهم في ما هم فيه يختلفون ۗ إن الله لا يهدي من هو كاذب كفار'),
(4062, 39, 4, 'لو أراد الله أن يتخذ ولدا لاصطفى مما يخلق ما يشاء ۚ سبحانه ۖ هو الله الواحد القهار'),
(4063, 39, 5, 'خلق السماوات والأرض بالحق ۖ يكور الليل على النهار ويكور النهار على الليل ۖ وسخر الشمس والقمر ۖ كل يجري لأجل مسمى ۗ ألا هو العزيز الغفار'),
(4064, 39, 6, 'خلقكم من نفس واحدة ثم جعل منها زوجها وأنزل لكم من الأنعام ثمانية أزواج ۚ يخلقكم في بطون أمهاتكم خلقا من بعد خلق في ظلمات ثلاث ۚ ذلكم الله ربكم له الملك ۖ لا إله إلا هو ۖ فأنى تصرفون'),
(4065, 39, 7, 'إن تكفروا فإن الله غني عنكم ۖ ولا يرضى لعباده الكفر ۖ وإن تشكروا يرضه لكم ۗ ولا تزر وازرة وزر أخرى ۗ ثم إلى ربكم مرجعكم فينبئكم بما كنتم تعملون ۚ إنه عليم بذات الصدور'),
(4066, 39, 8, 'وإذا مس الإنسان ضر دعا ربه منيبا إليه ثم إذا خوله نعمة منه نسي ما كان يدعو إليه من قبل وجعل لله أندادا ليضل عن سبيله ۚ قل تمتع بكفرك قليلا ۖ إنك من أصحاب النار'),
(4067, 39, 9, 'أمن هو قانت آناء الليل ساجدا وقائما يحذر الآخرة ويرجو رحمة ربه ۗ قل هل يستوي الذين يعلمون والذين لا يعلمون ۗ إنما يتذكر أولو الألباب'),
(4068, 39, 10, 'قل يا عباد الذين آمنوا اتقوا ربكم ۚ للذين أحسنوا في هذه الدنيا حسنة ۗ وأرض الله واسعة ۗ إنما يوفى الصابرون أجرهم بغير حساب'),
(4069, 39, 11, 'قل إني أمرت أن أعبد الله مخلصا له الدين'),
(4070, 39, 12, 'وأمرت لأن أكون أول المسلمين'),
(4071, 39, 13, 'قل إني أخاف إن عصيت ربي عذاب يوم عظيم'),
(4072, 39, 14, 'قل الله أعبد مخلصا له ديني'),
(4073, 39, 15, 'فاعبدوا ما شئتم من دونه ۗ قل إن الخاسرين الذين خسروا أنفسهم وأهليهم يوم القيامة ۗ ألا ذلك هو الخسران المبين'),
(4074, 39, 16, 'لهم من فوقهم ظلل من النار ومن تحتهم ظلل ۚ ذلك يخوف الله به عباده ۚ يا عباد فاتقون'),
(4075, 39, 17, 'والذين اجتنبوا الطاغوت أن يعبدوها وأنابوا إلى الله لهم البشرى ۚ فبشر عباد'),
(4076, 39, 18, 'الذين يستمعون القول فيتبعون أحسنه ۚ أولئك الذين هداهم الله ۖ وأولئك هم أولو الألباب'),
(4077, 39, 19, 'أفمن حق عليه كلمة العذاب أفأنت تنقذ من في النار'),
(4078, 39, 20, 'لكن الذين اتقوا ربهم لهم غرف من فوقها غرف مبنية تجري من تحتها الأنهار ۖ وعد الله ۖ لا يخلف الله الميعاد'),
(4079, 39, 21, 'ألم تر أن الله أنزل من السماء ماء فسلكه ينابيع في الأرض ثم يخرج به زرعا مختلفا ألوانه ثم يهيج فتراه مصفرا ثم يجعله حطاما ۚ إن في ذلك لذكرى لأولي الألباب'),
(4080, 39, 22, 'أفمن شرح الله صدره للإسلام فهو على نور من ربه ۚ فويل للقاسية قلوبهم من ذكر الله ۚ أولئك في ضلال مبين'),
(4081, 39, 23, 'الله نزل أحسن الحديث كتابا متشابها مثاني تقشعر منه جلود الذين يخشون ربهم ثم تلين جلودهم وقلوبهم إلى ذكر الله ۚ ذلك هدى الله يهدي به من يشاء ۚ ومن يضلل الله فما له من هاد'),
(4082, 39, 24, 'أفمن يتقي بوجهه سوء العذاب يوم القيامة ۚ وقيل للظالمين ذوقوا ما كنتم تكسبون'),
(4083, 39, 25, 'كذب الذين من قبلهم فأتاهم العذاب من حيث لا يشعرون'),
(4084, 39, 26, 'فأذاقهم الله الخزي في الحياة الدنيا ۖ ولعذاب الآخرة أكبر ۚ لو كانوا يعلمون'),
(4085, 39, 27, 'ولقد ضربنا للناس في هذا القرآن من كل مثل لعلهم يتذكرون'),
(4086, 39, 28, 'قرآنا عربيا غير ذي عوج لعلهم يتقون'),
(4087, 39, 29, 'ضرب الله مثلا رجلا فيه شركاء متشاكسون ورجلا سلما لرجل هل يستويان مثلا ۚ الحمد لله ۚ بل أكثرهم لا يعلمون'),
(4088, 39, 30, 'إنك ميت وإنهم ميتون'),
(4089, 39, 31, 'ثم إنكم يوم القيامة عند ربكم تختصمون'),
(4090, 39, 32, 'فمن أظلم ممن كذب على الله وكذب بالصدق إذ جاءه ۚ أليس في جهنم مثوى للكافرين'),
(4091, 39, 33, 'والذي جاء بالصدق وصدق به ۙ أولئك هم المتقون'),
(4092, 39, 34, 'لهم ما يشاءون عند ربهم ۚ ذلك جزاء المحسنين'),
(4093, 39, 35, 'ليكفر الله عنهم أسوأ الذي عملوا ويجزيهم أجرهم بأحسن الذي كانوا يعملون'),
(4094, 39, 36, 'أليس الله بكاف عبده ۖ ويخوفونك بالذين من دونه ۚ ومن يضلل الله فما له من هاد'),
(4095, 39, 37, 'ومن يهد الله فما له من مضل ۗ أليس الله بعزيز ذي انتقام'),
(4096, 39, 38, 'ولئن سألتهم من خلق السماوات والأرض ليقولن الله ۚ قل أفرأيتم ما تدعون من دون الله إن أرادني الله بضر هل هن كاشفات ضره أو أرادني برحمة هل هن ممسكات رحمته ۚ قل حسبي الله ۖ عليه يتوكل المتوكلون'),
(4097, 39, 39, 'قل يا قوم اعملوا على مكانتكم إني عامل ۖ فسوف تعلمون'),
(4098, 39, 40, 'من يأتيه عذاب يخزيه ويحل عليه عذاب مقيم'),
(4099, 39, 41, 'إنا أنزلنا عليك الكتاب للناس بالحق ۖ فمن اهتدى فلنفسه ۖ ومن ضل فإنما يضل عليها ۖ وما أنت عليهم بوكيل'),
(4100, 39, 42, 'الله يتوفى الأنفس حين موتها والتي لم تمت في منامها ۖ فيمسك التي قضى عليها الموت ويرسل الأخرى إلى أجل مسمى ۚ إن في ذلك لآيات لقوم يتفكرون'),
(4101, 39, 43, 'أم اتخذوا من دون الله شفعاء ۚ قل أولو كانوا لا يملكون شيئا ولا يعقلون'),
(4102, 39, 44, 'قل لله الشفاعة جميعا ۖ له ملك السماوات والأرض ۖ ثم إليه ترجعون'),
(4103, 39, 45, 'وإذا ذكر الله وحده اشمأزت قلوب الذين لا يؤمنون بالآخرة ۖ وإذا ذكر الذين من دونه إذا هم يستبشرون'),
(4104, 39, 46, 'قل اللهم فاطر السماوات والأرض عالم الغيب والشهادة أنت تحكم بين عبادك في ما كانوا فيه يختلفون'),
(4105, 39, 47, 'ولو أن للذين ظلموا ما في الأرض جميعا ومثله معه لافتدوا به من سوء العذاب يوم القيامة ۚ وبدا لهم من الله ما لم يكونوا يحتسبون'),
(4106, 39, 48, 'وبدا لهم سيئات ما كسبوا وحاق بهم ما كانوا به يستهزئون'),
(4107, 39, 49, 'فإذا مس الإنسان ضر دعانا ثم إذا خولناه نعمة منا قال إنما أوتيته على علم ۚ بل هي فتنة ولكن أكثرهم لا يعلمون'),
(4108, 39, 50, 'قد قالها الذين من قبلهم فما أغنى عنهم ما كانوا يكسبون'),
(4109, 39, 51, 'فأصابهم سيئات ما كسبوا ۚ والذين ظلموا من هؤلاء سيصيبهم سيئات ما كسبوا وما هم بمعجزين'),
(4110, 39, 52, 'أولم يعلموا أن الله يبسط الرزق لمن يشاء ويقدر ۚ إن في ذلك لآيات لقوم يؤمنون'),
(4111, 39, 53, 'قل يا عبادي الذين أسرفوا على أنفسهم لا تقنطوا من رحمة الله ۚ إن الله يغفر الذنوب جميعا ۚ إنه هو الغفور الرحيم'),
(4112, 39, 54, 'وأنيبوا إلى ربكم وأسلموا له من قبل أن يأتيكم العذاب ثم لا تنصرون'),
(4113, 39, 55, 'واتبعوا أحسن ما أنزل إليكم من ربكم من قبل أن يأتيكم العذاب بغتة وأنتم لا تشعرون'),
(4114, 39, 56, 'أن تقول نفس يا حسرتى على ما فرطت في جنب الله وإن كنت لمن الساخرين'),
(4115, 39, 57, 'أو تقول لو أن الله هداني لكنت من المتقين'),
(4116, 39, 58, 'أو تقول حين ترى العذاب لو أن لي كرة فأكون من المحسنين'),
(4117, 39, 59, 'بلى قد جاءتك آياتي فكذبت بها واستكبرت وكنت من الكافرين'),
(4118, 39, 60, 'ويوم القيامة ترى الذين كذبوا على الله وجوههم مسودة ۚ أليس في جهنم مثوى للمتكبرين'),
(4119, 39, 61, 'وينجي الله الذين اتقوا بمفازتهم لا يمسهم السوء ولا هم يحزنون'),
(4120, 39, 62, 'الله خالق كل شيء ۖ وهو على كل شيء وكيل'),
(4121, 39, 63, 'له مقاليد السماوات والأرض ۗ والذين كفروا بآيات الله أولئك هم الخاسرون'),
(4122, 39, 64, 'قل أفغير الله تأمروني أعبد أيها الجاهلون'),
(4123, 39, 65, 'ولقد أوحي إليك وإلى الذين من قبلك لئن أشركت ليحبطن عملك ولتكونن من الخاسرين'),
(4124, 39, 66, 'بل الله فاعبد وكن من الشاكرين'),
(4125, 39, 67, 'وما قدروا الله حق قدره والأرض جميعا قبضته يوم القيامة والسماوات مطويات بيمينه ۚ سبحانه وتعالى عما يشركون'),
(4126, 39, 68, 'ونفخ في الصور فصعق من في السماوات ومن في الأرض إلا من شاء الله ۖ ثم نفخ فيه أخرى فإذا هم قيام ينظرون'),
(4127, 39, 69, 'وأشرقت الأرض بنور ربها ووضع الكتاب وجيء بالنبيين والشهداء وقضي بينهم بالحق وهم لا يظلمون'),
(4128, 39, 70, 'ووفيت كل نفس ما عملت وهو أعلم بما يفعلون'),
(4129, 39, 71, 'وسيق الذين كفروا إلى جهنم زمرا ۖ حتى إذا جاءوها فتحت أبوابها وقال لهم خزنتها ألم يأتكم رسل منكم يتلون عليكم آيات ربكم وينذرونكم لقاء يومكم هذا ۚ قالوا بلى ولكن حقت كلمة العذاب على الكافرين'),
(4130, 39, 72, 'قيل ادخلوا أبواب جهنم خالدين فيها ۖ فبئس مثوى المتكبرين'),
(4131, 39, 73, 'وسيق الذين اتقوا ربهم إلى الجنة زمرا ۖ حتى إذا جاءوها وفتحت أبوابها وقال لهم خزنتها سلام عليكم طبتم فادخلوها خالدين'),
(4132, 39, 74, 'وقالوا الحمد لله الذي صدقنا وعده وأورثنا الأرض نتبوأ من الجنة حيث نشاء ۖ فنعم أجر العاملين'),
(4133, 39, 75, 'وترى الملائكة حافين من حول العرش يسبحون بحمد ربهم ۖ وقضي بينهم بالحق وقيل الحمد لله رب العالمين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'بسم الله الرحمن الرحيم حم'),
(4135, 40, 2, 'تنزيل الكتاب من الله العزيز العليم'),
(4136, 40, 3, 'غافر الذنب وقابل التوب شديد العقاب ذي الطول ۖ لا إله إلا هو ۖ إليه المصير'),
(4137, 40, 4, 'ما يجادل في آيات الله إلا الذين كفروا فلا يغررك تقلبهم في البلاد'),
(4138, 40, 5, 'كذبت قبلهم قوم نوح والأحزاب من بعدهم ۖ وهمت كل أمة برسولهم ليأخذوه ۖ وجادلوا بالباطل ليدحضوا به الحق فأخذتهم ۖ فكيف كان عقاب'),
(4139, 40, 6, 'وكذلك حقت كلمت ربك على الذين كفروا أنهم أصحاب النار'),
(4140, 40, 7, 'الذين يحملون العرش ومن حوله يسبحون بحمد ربهم ويؤمنون به ويستغفرون للذين آمنوا ربنا وسعت كل شيء رحمة وعلما فاغفر للذين تابوا واتبعوا سبيلك وقهم عذاب الجحيم'),
(4141, 40, 8, 'ربنا وأدخلهم جنات عدن التي وعدتهم ومن صلح من آبائهم وأزواجهم وذرياتهم ۚ إنك أنت العزيز الحكيم'),
(4142, 40, 9, 'وقهم السيئات ۚ ومن تق السيئات يومئذ فقد رحمته ۚ وذلك هو الفوز العظيم'),
(4143, 40, 10, 'إن الذين كفروا ينادون لمقت الله أكبر من مقتكم أنفسكم إذ تدعون إلى الإيمان فتكفرون'),
(4144, 40, 11, 'قالوا ربنا أمتنا اثنتين وأحييتنا اثنتين فاعترفنا بذنوبنا فهل إلى خروج من سبيل'),
(4145, 40, 12, 'ذلكم بأنه إذا دعي الله وحده كفرتم ۖ وإن يشرك به تؤمنوا ۚ فالحكم لله العلي الكبير'),
(4146, 40, 13, 'هو الذي يريكم آياته وينزل لكم من السماء رزقا ۚ وما يتذكر إلا من ينيب'),
(4147, 40, 14, 'فادعوا الله مخلصين له الدين ولو كره الكافرون'),
(4148, 40, 15, 'رفيع الدرجات ذو العرش يلقي الروح من أمره على من يشاء من عباده لينذر يوم التلاق'),
(4149, 40, 16, 'يوم هم بارزون ۖ لا يخفى على الله منهم شيء ۚ لمن الملك اليوم ۖ لله الواحد القهار'),
(4150, 40, 17, 'اليوم تجزى كل نفس بما كسبت ۚ لا ظلم اليوم ۚ إن الله سريع الحساب'),
(4151, 40, 18, 'وأنذرهم يوم الآزفة إذ القلوب لدى الحناجر كاظمين ۚ ما للظالمين من حميم ولا شفيع يطاع'),
(4152, 40, 19, 'يعلم خائنة الأعين وما تخفي الصدور'),
(4153, 40, 20, 'والله يقضي بالحق ۖ والذين يدعون من دونه لا يقضون بشيء ۗ إن الله هو السميع البصير'),
(4154, 40, 21, 'أولم يسيروا في الأرض فينظروا كيف كان عاقبة الذين كانوا من قبلهم ۚ كانوا هم أشد منهم قوة وآثارا في الأرض فأخذهم الله بذنوبهم وما كان لهم من الله من واق'),
(4155, 40, 22, 'ذلك بأنهم كانت تأتيهم رسلهم بالبينات فكفروا فأخذهم الله ۚ إنه قوي شديد العقاب'),
(4156, 40, 23, 'ولقد أرسلنا موسى بآياتنا وسلطان مبين'),
(4157, 40, 24, 'إلى فرعون وهامان وقارون فقالوا ساحر كذاب'),
(4158, 40, 25, 'فلما جاءهم بالحق من عندنا قالوا اقتلوا أبناء الذين آمنوا معه واستحيوا نساءهم ۚ وما كيد الكافرين إلا في ضلال'),
(4159, 40, 26, 'وقال فرعون ذروني أقتل موسى وليدع ربه ۖ إني أخاف أن يبدل دينكم أو أن يظهر في الأرض الفساد'),
(4160, 40, 27, 'وقال موسى إني عذت بربي وربكم من كل متكبر لا يؤمن بيوم الحساب'),
(4161, 40, 28, 'وقال رجل مؤمن من آل فرعون يكتم إيمانه أتقتلون رجلا أن يقول ربي الله وقد جاءكم بالبينات من ربكم ۖ وإن يك كاذبا فعليه كذبه ۖ وإن يك صادقا يصبكم بعض الذي يعدكم ۖ إن الله لا يهدي من هو مسرف كذاب'),
(4162, 40, 29, 'يا قوم لكم الملك اليوم ظاهرين في الأرض فمن ينصرنا من بأس الله إن جاءنا ۚ قال فرعون ما أريكم إلا ما أرى وما أهديكم إلا سبيل الرشاد'),
(4163, 40, 30, 'وقال الذي آمن يا قوم إني أخاف عليكم مثل يوم الأحزاب'),
(4164, 40, 31, 'مثل دأب قوم نوح وعاد وثمود والذين من بعدهم ۚ وما الله يريد ظلما للعباد'),
(4165, 40, 32, 'ويا قوم إني أخاف عليكم يوم التناد'),
(4166, 40, 33, 'يوم تولون مدبرين ما لكم من الله من عاصم ۗ ومن يضلل الله فما له من هاد'),
(4167, 40, 34, 'ولقد جاءكم يوسف من قبل بالبينات فما زلتم في شك مما جاءكم به ۖ حتى إذا هلك قلتم لن يبعث الله من بعده رسولا ۚ كذلك يضل الله من هو مسرف مرتاب'),
(4168, 40, 35, 'الذين يجادلون في آيات الله بغير سلطان أتاهم ۖ كبر مقتا عند الله وعند الذين آمنوا ۚ كذلك يطبع الله على كل قلب متكبر جبار'),
(4169, 40, 36, 'وقال فرعون يا هامان ابن لي صرحا لعلي أبلغ الأسباب'),
(4170, 40, 37, 'أسباب السماوات فأطلع إلى إله موسى وإني لأظنه كاذبا ۚ وكذلك زين لفرعون سوء عمله وصد عن السبيل ۚ وما كيد فرعون إلا في تباب'),
(4171, 40, 38, 'وقال الذي آمن يا قوم اتبعون أهدكم سبيل الرشاد'),
(4172, 40, 39, 'يا قوم إنما هذه الحياة الدنيا متاع وإن الآخرة هي دار القرار'),
(4173, 40, 40, 'من عمل سيئة فلا يجزى إلا مثلها ۖ ومن عمل صالحا من ذكر أو أنثى وهو مؤمن فأولئك يدخلون الجنة يرزقون فيها بغير حساب'),
(4174, 40, 41, 'ويا قوم ما لي أدعوكم إلى النجاة وتدعونني إلى النار'),
(4175, 40, 42, 'تدعونني لأكفر بالله وأشرك به ما ليس لي به علم وأنا أدعوكم إلى العزيز الغفار'),
(4176, 40, 43, 'لا جرم أنما تدعونني إليه ليس له دعوة في الدنيا ولا في الآخرة وأن مردنا إلى الله وأن المسرفين هم أصحاب النار'),
(4177, 40, 44, 'فستذكرون ما أقول لكم ۚ وأفوض أمري إلى الله ۚ إن الله بصير بالعباد'),
(4178, 40, 45, 'فوقاه الله سيئات ما مكروا ۖ وحاق بآل فرعون سوء العذاب'),
(4179, 40, 46, 'النار يعرضون عليها غدوا وعشيا ۖ ويوم تقوم الساعة أدخلوا آل فرعون أشد العذاب'),
(4180, 40, 47, 'وإذ يتحاجون في النار فيقول الضعفاء للذين استكبروا إنا كنا لكم تبعا فهل أنتم مغنون عنا نصيبا من النار'),
(4181, 40, 48, 'قال الذين استكبروا إنا كل فيها إن الله قد حكم بين العباد'),
(4182, 40, 49, 'وقال الذين في النار لخزنة جهنم ادعوا ربكم يخفف عنا يوما من العذاب'),
(4183, 40, 50, 'قالوا أولم تك تأتيكم رسلكم بالبينات ۖ قالوا بلى ۚ قالوا فادعوا ۗ وما دعاء الكافرين إلا في ضلال'),
(4184, 40, 51, 'إنا لننصر رسلنا والذين آمنوا في الحياة الدنيا ويوم يقوم الأشهاد'),
(4185, 40, 52, 'يوم لا ينفع الظالمين معذرتهم ۖ ولهم اللعنة ولهم سوء الدار'),
(4186, 40, 53, 'ولقد آتينا موسى الهدى وأورثنا بني إسرائيل الكتاب'),
(4187, 40, 54, 'هدى وذكرى لأولي الألباب'),
(4188, 40, 55, 'فاصبر إن وعد الله حق واستغفر لذنبك وسبح بحمد ربك بالعشي والإبكار'),
(4189, 40, 56, 'إن الذين يجادلون في آيات الله بغير سلطان أتاهم ۙ إن في صدورهم إلا كبر ما هم ببالغيه ۚ فاستعذ بالله ۖ إنه هو السميع البصير'),
(4190, 40, 57, 'لخلق السماوات والأرض أكبر من خلق الناس ولكن أكثر الناس لا يعلمون'),
(4191, 40, 58, 'وما يستوي الأعمى والبصير والذين آمنوا وعملوا الصالحات ولا المسيء ۚ قليلا ما تتذكرون'),
(4192, 40, 59, 'إن الساعة لآتية لا ريب فيها ولكن أكثر الناس لا يؤمنون'),
(4193, 40, 60, 'وقال ربكم ادعوني أستجب لكم ۚ إن الذين يستكبرون عن عبادتي سيدخلون جهنم داخرين'),
(4194, 40, 61, 'الله الذي جعل لكم الليل لتسكنوا فيه والنهار مبصرا ۚ إن الله لذو فضل على الناس ولكن أكثر الناس لا يشكرون'),
(4195, 40, 62, 'ذلكم الله ربكم خالق كل شيء لا إله إلا هو ۖ فأنى تؤفكون'),
(4196, 40, 63, 'كذلك يؤفك الذين كانوا بآيات الله يجحدون'),
(4197, 40, 64, 'الله الذي جعل لكم الأرض قرارا والسماء بناء وصوركم فأحسن صوركم ورزقكم من الطيبات ۚ ذلكم الله ربكم ۖ فتبارك الله رب العالمين'),
(4198, 40, 65, 'هو الحي لا إله إلا هو فادعوه مخلصين له الدين ۗ الحمد لله رب العالمين'),
(4199, 40, 66, 'قل إني نهيت أن أعبد الذين تدعون من دون الله لما جاءني البينات من ربي وأمرت أن أسلم لرب العالمين'),
(4200, 40, 67, 'هو الذي خلقكم من تراب ثم من نطفة ثم من علقة ثم يخرجكم طفلا ثم لتبلغوا أشدكم ثم لتكونوا شيوخا ۚ ومنكم من يتوفى من قبل ۖ ولتبلغوا أجلا مسمى ولعلكم تعقلون'),
(4201, 40, 68, 'هو الذي يحيي ويميت ۖ فإذا قضى أمرا فإنما يقول له كن فيكون'),
(4202, 40, 69, 'ألم تر إلى الذين يجادلون في آيات الله أنى يصرفون'),
(4203, 40, 70, 'الذين كذبوا بالكتاب وبما أرسلنا به رسلنا ۖ فسوف يعلمون'),
(4204, 40, 71, 'إذ الأغلال في أعناقهم والسلاسل يسحبون'),
(4205, 40, 72, 'في الحميم ثم في النار يسجرون'),
(4206, 40, 73, 'ثم قيل لهم أين ما كنتم تشركون'),
(4207, 40, 74, 'من دون الله ۖ قالوا ضلوا عنا بل لم نكن ندعو من قبل شيئا ۚ كذلك يضل الله الكافرين'),
(4208, 40, 75, 'ذلكم بما كنتم تفرحون في الأرض بغير الحق وبما كنتم تمرحون'),
(4209, 40, 76, 'ادخلوا أبواب جهنم خالدين فيها ۖ فبئس مثوى المتكبرين'),
(4210, 40, 77, 'فاصبر إن وعد الله حق ۚ فإما نرينك بعض الذي نعدهم أو نتوفينك فإلينا يرجعون'),
(4211, 40, 78, 'ولقد أرسلنا رسلا من قبلك منهم من قصصنا عليك ومنهم من لم نقصص عليك ۗ وما كان لرسول أن يأتي بآية إلا بإذن الله ۚ فإذا جاء أمر الله قضي بالحق وخسر هنالك المبطلون'),
(4212, 40, 79, 'الله الذي جعل لكم الأنعام لتركبوا منها ومنها تأكلون'),
(4213, 40, 80, 'ولكم فيها منافع ولتبلغوا عليها حاجة في صدوركم وعليها وعلى الفلك تحملون'),
(4214, 40, 81, 'ويريكم آياته فأي آيات الله تنكرون'),
(4215, 40, 82, 'أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم ۚ كانوا أكثر منهم وأشد قوة وآثارا في الأرض فما أغنى عنهم ما كانوا يكسبون'),
(4216, 40, 83, 'فلما جاءتهم رسلهم بالبينات فرحوا بما عندهم من العلم وحاق بهم ما كانوا به يستهزئون'),
(4217, 40, 84, 'فلما رأوا بأسنا قالوا آمنا بالله وحده وكفرنا بما كنا به مشركين'),
(4218, 40, 85, 'فلم يك ينفعهم إيمانهم لما رأوا بأسنا ۖ سنت الله التي قد خلت في عباده ۖ وخسر هنالك الكافرون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'بسم الله الرحمن الرحيم حم'),
(4220, 41, 2, 'تنزيل من الرحمن الرحيم'),
(4221, 41, 3, 'كتاب فصلت آياته قرآنا عربيا لقوم يعلمون'),
(4222, 41, 4, 'بشيرا ونذيرا فأعرض أكثرهم فهم لا يسمعون'),
(4223, 41, 5, 'وقالوا قلوبنا في أكنة مما تدعونا إليه وفي آذاننا وقر ومن بيننا وبينك حجاب فاعمل إننا عاملون'),
(4224, 41, 6, 'قل إنما أنا بشر مثلكم يوحى إلي أنما إلهكم إله واحد فاستقيموا إليه واستغفروه ۗ وويل للمشركين'),
(4225, 41, 7, 'الذين لا يؤتون الزكاة وهم بالآخرة هم كافرون'),
(4226, 41, 8, 'إن الذين آمنوا وعملوا الصالحات لهم أجر غير ممنون'),
(4227, 41, 9, 'قل أئنكم لتكفرون بالذي خلق الأرض في يومين وتجعلون له أندادا ۚ ذلك رب العالمين'),
(4228, 41, 10, 'وجعل فيها رواسي من فوقها وبارك فيها وقدر فيها أقواتها في أربعة أيام سواء للسائلين'),
(4229, 41, 11, 'ثم استوى إلى السماء وهي دخان فقال لها وللأرض ائتيا طوعا أو كرها قالتا أتينا طائعين'),
(4230, 41, 12, 'فقضاهن سبع سماوات في يومين وأوحى في كل سماء أمرها ۚ وزينا السماء الدنيا بمصابيح وحفظا ۚ ذلك تقدير العزيز العليم'),
(4231, 41, 13, 'فإن أعرضوا فقل أنذرتكم صاعقة مثل صاعقة عاد وثمود'),
(4232, 41, 14, 'إذ جاءتهم الرسل من بين أيديهم ومن خلفهم ألا تعبدوا إلا الله ۖ قالوا لو شاء ربنا لأنزل ملائكة فإنا بما أرسلتم به كافرون'),
(4233, 41, 15, 'فأما عاد فاستكبروا في الأرض بغير الحق وقالوا من أشد منا قوة ۖ أولم يروا أن الله الذي خلقهم هو أشد منهم قوة ۖ وكانوا بآياتنا يجحدون'),
(4234, 41, 16, 'فأرسلنا عليهم ريحا صرصرا في أيام نحسات لنذيقهم عذاب الخزي في الحياة الدنيا ۖ ولعذاب الآخرة أخزى ۖ وهم لا ينصرون'),
(4235, 41, 17, 'وأما ثمود فهديناهم فاستحبوا العمى على الهدى فأخذتهم صاعقة العذاب الهون بما كانوا يكسبون'),
(4236, 41, 18, 'ونجينا الذين آمنوا وكانوا يتقون'),
(4237, 41, 19, 'ويوم يحشر أعداء الله إلى النار فهم يوزعون'),
(4238, 41, 20, 'حتى إذا ما جاءوها شهد عليهم سمعهم وأبصارهم وجلودهم بما كانوا يعملون'),
(4239, 41, 21, 'وقالوا لجلودهم لم شهدتم علينا ۖ قالوا أنطقنا الله الذي أنطق كل شيء وهو خلقكم أول مرة وإليه ترجعون'),
(4240, 41, 22, 'وما كنتم تستترون أن يشهد عليكم سمعكم ولا أبصاركم ولا جلودكم ولكن ظننتم أن الله لا يعلم كثيرا مما تعملون'),
(4241, 41, 23, 'وذلكم ظنكم الذي ظننتم بربكم أرداكم فأصبحتم من الخاسرين'),
(4242, 41, 24, 'فإن يصبروا فالنار مثوى لهم ۖ وإن يستعتبوا فما هم من المعتبين'),
(4243, 41, 25, 'وقيضنا لهم قرناء فزينوا لهم ما بين أيديهم وما خلفهم وحق عليهم القول في أمم قد خلت من قبلهم من الجن والإنس ۖ إنهم كانوا خاسرين'),
(4244, 41, 26, 'وقال الذين كفروا لا تسمعوا لهذا القرآن والغوا فيه لعلكم تغلبون'),
(4245, 41, 27, 'فلنذيقن الذين كفروا عذابا شديدا ولنجزينهم أسوأ الذي كانوا يعملون'),
(4246, 41, 28, 'ذلك جزاء أعداء الله النار ۖ لهم فيها دار الخلد ۖ جزاء بما كانوا بآياتنا يجحدون'),
(4247, 41, 29, 'وقال الذين كفروا ربنا أرنا اللذين أضلانا من الجن والإنس نجعلهما تحت أقدامنا ليكونا من الأسفلين'),
(4248, 41, 30, 'إن الذين قالوا ربنا الله ثم استقاموا تتنزل عليهم الملائكة ألا تخافوا ولا تحزنوا وأبشروا بالجنة التي كنتم توعدون'),
(4249, 41, 31, 'نحن أولياؤكم في الحياة الدنيا وفي الآخرة ۖ ولكم فيها ما تشتهي أنفسكم ولكم فيها ما تدعون'),
(4250, 41, 32, 'نزلا من غفور رحيم'),
(4251, 41, 33, 'ومن أحسن قولا ممن دعا إلى الله وعمل صالحا وقال إنني من المسلمين'),
(4252, 41, 34, 'ولا تستوي الحسنة ولا السيئة ۚ ادفع بالتي هي أحسن فإذا الذي بينك وبينه عداوة كأنه ولي حميم'),
(4253, 41, 35, 'وما يلقاها إلا الذين صبروا وما يلقاها إلا ذو حظ عظيم'),
(4254, 41, 36, 'وإما ينزغنك من الشيطان نزغ فاستعذ بالله ۖ إنه هو السميع العليم'),
(4255, 41, 37, 'ومن آياته الليل والنهار والشمس والقمر ۚ لا تسجدوا للشمس ولا للقمر واسجدوا لله الذي خلقهن إن كنتم إياه تعبدون'),
(4256, 41, 38, 'فإن استكبروا فالذين عند ربك يسبحون له بالليل والنهار وهم لا يسأمون'),
(4257, 41, 39, 'ومن آياته أنك ترى الأرض خاشعة فإذا أنزلنا عليها الماء اهتزت وربت ۚ إن الذي أحياها لمحيي الموتى ۚ إنه على كل شيء قدير'),
(4258, 41, 40, 'إن الذين يلحدون في آياتنا لا يخفون علينا ۗ أفمن يلقى في النار خير أم من يأتي آمنا يوم القيامة ۚ اعملوا ما شئتم ۖ إنه بما تعملون بصير'),
(4259, 41, 41, 'إن الذين كفروا بالذكر لما جاءهم ۖ وإنه لكتاب عزيز'),
(4260, 41, 42, 'لا يأتيه الباطل من بين يديه ولا من خلفه ۖ تنزيل من حكيم حميد'),
(4261, 41, 43, 'ما يقال لك إلا ما قد قيل للرسل من قبلك ۚ إن ربك لذو مغفرة وذو عقاب أليم'),
(4262, 41, 44, 'ولو جعلناه قرآنا أعجميا لقالوا لولا فصلت آياته ۖ أأعجمي وعربي ۗ قل هو للذين آمنوا هدى وشفاء ۖ والذين لا يؤمنون في آذانهم وقر وهو عليهم عمى ۚ أولئك ينادون من مكان بعيد'),
(4263, 41, 45, 'ولقد آتينا موسى الكتاب فاختلف فيه ۗ ولولا كلمة سبقت من ربك لقضي بينهم ۚ وإنهم لفي شك منه مريب'),
(4264, 41, 46, 'من عمل صالحا فلنفسه ۖ ومن أساء فعليها ۗ وما ربك بظلام للعبيد'),
(4265, 41, 47, 'إليه يرد علم الساعة ۚ وما تخرج من ثمرات من أكمامها وما تحمل من أنثى ولا تضع إلا بعلمه ۚ ويوم يناديهم أين شركائي قالوا آذناك ما منا من شهيد'),
(4266, 41, 48, 'وضل عنهم ما كانوا يدعون من قبل ۖ وظنوا ما لهم من محيص'),
(4267, 41, 49, 'لا يسأم الإنسان من دعاء الخير وإن مسه الشر فيئوس قنوط'),
(4268, 41, 50, 'ولئن أذقناه رحمة منا من بعد ضراء مسته ليقولن هذا لي وما أظن الساعة قائمة ولئن رجعت إلى ربي إن لي عنده للحسنى ۚ فلننبئن الذين كفروا بما عملوا ولنذيقنهم من عذاب غليظ'),
(4269, 41, 51, 'وإذا أنعمنا على الإنسان أعرض ونأى بجانبه وإذا مسه الشر فذو دعاء عريض'),
(4270, 41, 52, 'قل أرأيتم إن كان من عند الله ثم كفرتم به من أضل ممن هو في شقاق بعيد'),
(4271, 41, 53, 'سنريهم آياتنا في الآفاق وفي أنفسهم حتى يتبين لهم أنه الحق ۗ أولم يكف بربك أنه على كل شيء شهيد'),
(4272, 41, 54, 'ألا إنهم في مرية من لقاء ربهم ۗ ألا إنه بكل شيء محيط');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'بسم الله الرحمن الرحيم حم'),
(4274, 42, 2, 'عسق'),
(4275, 42, 3, 'كذلك يوحي إليك وإلى الذين من قبلك الله العزيز الحكيم'),
(4276, 42, 4, 'له ما في السماوات وما في الأرض ۖ وهو العلي العظيم'),
(4277, 42, 5, 'تكاد السماوات يتفطرن من فوقهن ۚ والملائكة يسبحون بحمد ربهم ويستغفرون لمن في الأرض ۗ ألا إن الله هو الغفور الرحيم'),
(4278, 42, 6, 'والذين اتخذوا من دونه أولياء الله حفيظ عليهم وما أنت عليهم بوكيل'),
(4279, 42, 7, 'وكذلك أوحينا إليك قرآنا عربيا لتنذر أم القرى ومن حولها وتنذر يوم الجمع لا ريب فيه ۚ فريق في الجنة وفريق في السعير'),
(4280, 42, 8, 'ولو شاء الله لجعلهم أمة واحدة ولكن يدخل من يشاء في رحمته ۚ والظالمون ما لهم من ولي ولا نصير'),
(4281, 42, 9, 'أم اتخذوا من دونه أولياء ۖ فالله هو الولي وهو يحيي الموتى وهو على كل شيء قدير'),
(4282, 42, 10, 'وما اختلفتم فيه من شيء فحكمه إلى الله ۚ ذلكم الله ربي عليه توكلت وإليه أنيب'),
(4283, 42, 11, 'فاطر السماوات والأرض ۚ جعل لكم من أنفسكم أزواجا ومن الأنعام أزواجا ۖ يذرؤكم فيه ۚ ليس كمثله شيء ۖ وهو السميع البصير'),
(4284, 42, 12, 'له مقاليد السماوات والأرض ۖ يبسط الرزق لمن يشاء ويقدر ۚ إنه بكل شيء عليم'),
(4285, 42, 13, 'شرع لكم من الدين ما وصى به نوحا والذي أوحينا إليك وما وصينا به إبراهيم وموسى وعيسى ۖ أن أقيموا الدين ولا تتفرقوا فيه ۚ كبر على المشركين ما تدعوهم إليه ۚ الله يجتبي إليه من يشاء ويهدي إليه من ينيب'),
(4286, 42, 14, 'وما تفرقوا إلا من بعد ما جاءهم العلم بغيا بينهم ۚ ولولا كلمة سبقت من ربك إلى أجل مسمى لقضي بينهم ۚ وإن الذين أورثوا الكتاب من بعدهم لفي شك منه مريب'),
(4287, 42, 15, 'فلذلك فادع ۖ واستقم كما أمرت ۖ ولا تتبع أهواءهم ۖ وقل آمنت بما أنزل الله من كتاب ۖ وأمرت لأعدل بينكم ۖ الله ربنا وربكم ۖ لنا أعمالنا ولكم أعمالكم ۖ لا حجة بيننا وبينكم ۖ الله يجمع بيننا ۖ وإليه المصير'),
(4288, 42, 16, 'والذين يحاجون في الله من بعد ما استجيب له حجتهم داحضة عند ربهم وعليهم غضب ولهم عذاب شديد'),
(4289, 42, 17, 'الله الذي أنزل الكتاب بالحق والميزان ۗ وما يدريك لعل الساعة قريب'),
(4290, 42, 18, 'يستعجل بها الذين لا يؤمنون بها ۖ والذين آمنوا مشفقون منها ويعلمون أنها الحق ۗ ألا إن الذين يمارون في الساعة لفي ضلال بعيد'),
(4291, 42, 19, 'الله لطيف بعباده يرزق من يشاء ۖ وهو القوي العزيز'),
(4292, 42, 20, 'من كان يريد حرث الآخرة نزد له في حرثه ۖ ومن كان يريد حرث الدنيا نؤته منها وما له في الآخرة من نصيب'),
(4293, 42, 21, 'أم لهم شركاء شرعوا لهم من الدين ما لم يأذن به الله ۚ ولولا كلمة الفصل لقضي بينهم ۗ وإن الظالمين لهم عذاب أليم'),
(4294, 42, 22, 'ترى الظالمين مشفقين مما كسبوا وهو واقع بهم ۗ والذين آمنوا وعملوا الصالحات في روضات الجنات ۖ لهم ما يشاءون عند ربهم ۚ ذلك هو الفضل الكبير'),
(4295, 42, 23, 'ذلك الذي يبشر الله عباده الذين آمنوا وعملوا الصالحات ۗ قل لا أسألكم عليه أجرا إلا المودة في القربى ۗ ومن يقترف حسنة نزد له فيها حسنا ۚ إن الله غفور شكور'),
(4296, 42, 24, 'أم يقولون افترى على الله كذبا ۖ فإن يشإ الله يختم على قلبك ۗ ويمح الله الباطل ويحق الحق بكلماته ۚ إنه عليم بذات الصدور'),
(4297, 42, 25, 'وهو الذي يقبل التوبة عن عباده ويعفو عن السيئات ويعلم ما تفعلون'),
(4298, 42, 26, 'ويستجيب الذين آمنوا وعملوا الصالحات ويزيدهم من فضله ۚ والكافرون لهم عذاب شديد'),
(4299, 42, 27, 'ولو بسط الله الرزق لعباده لبغوا في الأرض ولكن ينزل بقدر ما يشاء ۚ إنه بعباده خبير بصير'),
(4300, 42, 28, 'وهو الذي ينزل الغيث من بعد ما قنطوا وينشر رحمته ۚ وهو الولي الحميد'),
(4301, 42, 29, 'ومن آياته خلق السماوات والأرض وما بث فيهما من دابة ۚ وهو على جمعهم إذا يشاء قدير'),
(4302, 42, 30, 'وما أصابكم من مصيبة فبما كسبت أيديكم ويعفو عن كثير'),
(4303, 42, 31, 'وما أنتم بمعجزين في الأرض ۖ وما لكم من دون الله من ولي ولا نصير'),
(4304, 42, 32, 'ومن آياته الجوار في البحر كالأعلام'),
(4305, 42, 33, 'إن يشأ يسكن الريح فيظللن رواكد على ظهره ۚ إن في ذلك لآيات لكل صبار شكور'),
(4306, 42, 34, 'أو يوبقهن بما كسبوا ويعف عن كثير'),
(4307, 42, 35, 'ويعلم الذين يجادلون في آياتنا ما لهم من محيص'),
(4308, 42, 36, 'فما أوتيتم من شيء فمتاع الحياة الدنيا ۖ وما عند الله خير وأبقى للذين آمنوا وعلى ربهم يتوكلون'),
(4309, 42, 37, 'والذين يجتنبون كبائر الإثم والفواحش وإذا ما غضبوا هم يغفرون'),
(4310, 42, 38, 'والذين استجابوا لربهم وأقاموا الصلاة وأمرهم شورى بينهم ومما رزقناهم ينفقون'),
(4311, 42, 39, 'والذين إذا أصابهم البغي هم ينتصرون'),
(4312, 42, 40, 'وجزاء سيئة سيئة مثلها ۖ فمن عفا وأصلح فأجره على الله ۚ إنه لا يحب الظالمين'),
(4313, 42, 41, 'ولمن انتصر بعد ظلمه فأولئك ما عليهم من سبيل'),
(4314, 42, 42, 'إنما السبيل على الذين يظلمون الناس ويبغون في الأرض بغير الحق ۚ أولئك لهم عذاب أليم'),
(4315, 42, 43, 'ولمن صبر وغفر إن ذلك لمن عزم الأمور'),
(4316, 42, 44, 'ومن يضلل الله فما له من ولي من بعده ۗ وترى الظالمين لما رأوا العذاب يقولون هل إلى مرد من سبيل'),
(4317, 42, 45, 'وتراهم يعرضون عليها خاشعين من الذل ينظرون من طرف خفي ۗ وقال الذين آمنوا إن الخاسرين الذين خسروا أنفسهم وأهليهم يوم القيامة ۗ ألا إن الظالمين في عذاب مقيم'),
(4318, 42, 46, 'وما كان لهم من أولياء ينصرونهم من دون الله ۗ ومن يضلل الله فما له من سبيل'),
(4319, 42, 47, 'استجيبوا لربكم من قبل أن يأتي يوم لا مرد له من الله ۚ ما لكم من ملجإ يومئذ وما لكم من نكير'),
(4320, 42, 48, 'فإن أعرضوا فما أرسلناك عليهم حفيظا ۖ إن عليك إلا البلاغ ۗ وإنا إذا أذقنا الإنسان منا رحمة فرح بها ۖ وإن تصبهم سيئة بما قدمت أيديهم فإن الإنسان كفور'),
(4321, 42, 49, 'لله ملك السماوات والأرض ۚ يخلق ما يشاء ۚ يهب لمن يشاء إناثا ويهب لمن يشاء الذكور'),
(4322, 42, 50, 'أو يزوجهم ذكرانا وإناثا ۖ ويجعل من يشاء عقيما ۚ إنه عليم قدير'),
(4323, 42, 51, 'وما كان لبشر أن يكلمه الله إلا وحيا أو من وراء حجاب أو يرسل رسولا فيوحي بإذنه ما يشاء ۚ إنه علي حكيم'),
(4324, 42, 52, 'وكذلك أوحينا إليك روحا من أمرنا ۚ ما كنت تدري ما الكتاب ولا الإيمان ولكن جعلناه نورا نهدي به من نشاء من عبادنا ۚ وإنك لتهدي إلى صراط مستقيم'),
(4325, 42, 53, 'صراط الله الذي له ما في السماوات وما في الأرض ۗ ألا إلى الله تصير الأمور');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'بسم الله الرحمن الرحيم حم'),
(4327, 43, 2, 'والكتاب المبين'),
(4328, 43, 3, 'إنا جعلناه قرآنا عربيا لعلكم تعقلون'),
(4329, 43, 4, 'وإنه في أم الكتاب لدينا لعلي حكيم'),
(4330, 43, 5, 'أفنضرب عنكم الذكر صفحا أن كنتم قوما مسرفين'),
(4331, 43, 6, 'وكم أرسلنا من نبي في الأولين'),
(4332, 43, 7, 'وما يأتيهم من نبي إلا كانوا به يستهزئون'),
(4333, 43, 8, 'فأهلكنا أشد منهم بطشا ومضى مثل الأولين'),
(4334, 43, 9, 'ولئن سألتهم من خلق السماوات والأرض ليقولن خلقهن العزيز العليم'),
(4335, 43, 10, 'الذي جعل لكم الأرض مهدا وجعل لكم فيها سبلا لعلكم تهتدون'),
(4336, 43, 11, 'والذي نزل من السماء ماء بقدر فأنشرنا به بلدة ميتا ۚ كذلك تخرجون'),
(4337, 43, 12, 'والذي خلق الأزواج كلها وجعل لكم من الفلك والأنعام ما تركبون'),
(4338, 43, 13, 'لتستووا على ظهوره ثم تذكروا نعمة ربكم إذا استويتم عليه وتقولوا سبحان الذي سخر لنا هذا وما كنا له مقرنين'),
(4339, 43, 14, 'وإنا إلى ربنا لمنقلبون'),
(4340, 43, 15, 'وجعلوا له من عباده جزءا ۚ إن الإنسان لكفور مبين'),
(4341, 43, 16, 'أم اتخذ مما يخلق بنات وأصفاكم بالبنين'),
(4342, 43, 17, 'وإذا بشر أحدهم بما ضرب للرحمن مثلا ظل وجهه مسودا وهو كظيم'),
(4343, 43, 18, 'أومن ينشأ في الحلية وهو في الخصام غير مبين'),
(4344, 43, 19, 'وجعلوا الملائكة الذين هم عباد الرحمن إناثا ۚ أشهدوا خلقهم ۚ ستكتب شهادتهم ويسألون'),
(4345, 43, 20, 'وقالوا لو شاء الرحمن ما عبدناهم ۗ ما لهم بذلك من علم ۖ إن هم إلا يخرصون'),
(4346, 43, 21, 'أم آتيناهم كتابا من قبله فهم به مستمسكون'),
(4347, 43, 22, 'بل قالوا إنا وجدنا آباءنا على أمة وإنا على آثارهم مهتدون'),
(4348, 43, 23, 'وكذلك ما أرسلنا من قبلك في قرية من نذير إلا قال مترفوها إنا وجدنا آباءنا على أمة وإنا على آثارهم مقتدون'),
(4349, 43, 24, 'قال أولو جئتكم بأهدى مما وجدتم عليه آباءكم ۖ قالوا إنا بما أرسلتم به كافرون'),
(4350, 43, 25, 'فانتقمنا منهم ۖ فانظر كيف كان عاقبة المكذبين'),
(4351, 43, 26, 'وإذ قال إبراهيم لأبيه وقومه إنني براء مما تعبدون'),
(4352, 43, 27, 'إلا الذي فطرني فإنه سيهدين'),
(4353, 43, 28, 'وجعلها كلمة باقية في عقبه لعلهم يرجعون'),
(4354, 43, 29, 'بل متعت هؤلاء وآباءهم حتى جاءهم الحق ورسول مبين'),
(4355, 43, 30, 'ولما جاءهم الحق قالوا هذا سحر وإنا به كافرون'),
(4356, 43, 31, 'وقالوا لولا نزل هذا القرآن على رجل من القريتين عظيم'),
(4357, 43, 32, 'أهم يقسمون رحمت ربك ۚ نحن قسمنا بينهم معيشتهم في الحياة الدنيا ۚ ورفعنا بعضهم فوق بعض درجات ليتخذ بعضهم بعضا سخريا ۗ ورحمت ربك خير مما يجمعون'),
(4358, 43, 33, 'ولولا أن يكون الناس أمة واحدة لجعلنا لمن يكفر بالرحمن لبيوتهم سقفا من فضة ومعارج عليها يظهرون'),
(4359, 43, 34, 'ولبيوتهم أبوابا وسررا عليها يتكئون'),
(4360, 43, 35, 'وزخرفا ۚ وإن كل ذلك لما متاع الحياة الدنيا ۚ والآخرة عند ربك للمتقين'),
(4361, 43, 36, 'ومن يعش عن ذكر الرحمن نقيض له شيطانا فهو له قرين'),
(4362, 43, 37, 'وإنهم ليصدونهم عن السبيل ويحسبون أنهم مهتدون'),
(4363, 43, 38, 'حتى إذا جاءنا قال يا ليت بيني وبينك بعد المشرقين فبئس القرين'),
(4364, 43, 39, 'ولن ينفعكم اليوم إذ ظلمتم أنكم في العذاب مشتركون'),
(4365, 43, 40, 'أفأنت تسمع الصم أو تهدي العمي ومن كان في ضلال مبين'),
(4366, 43, 41, 'فإما نذهبن بك فإنا منهم منتقمون'),
(4367, 43, 42, 'أو نرينك الذي وعدناهم فإنا عليهم مقتدرون'),
(4368, 43, 43, 'فاستمسك بالذي أوحي إليك ۖ إنك على صراط مستقيم'),
(4369, 43, 44, 'وإنه لذكر لك ولقومك ۖ وسوف تسألون'),
(4370, 43, 45, 'واسأل من أرسلنا من قبلك من رسلنا أجعلنا من دون الرحمن آلهة يعبدون'),
(4371, 43, 46, 'ولقد أرسلنا موسى بآياتنا إلى فرعون وملئه فقال إني رسول رب العالمين'),
(4372, 43, 47, 'فلما جاءهم بآياتنا إذا هم منها يضحكون'),
(4373, 43, 48, 'وما نريهم من آية إلا هي أكبر من أختها ۖ وأخذناهم بالعذاب لعلهم يرجعون'),
(4374, 43, 49, 'وقالوا يا أيه الساحر ادع لنا ربك بما عهد عندك إننا لمهتدون'),
(4375, 43, 50, 'فلما كشفنا عنهم العذاب إذا هم ينكثون'),
(4376, 43, 51, 'ونادى فرعون في قومه قال يا قوم أليس لي ملك مصر وهذه الأنهار تجري من تحتي ۖ أفلا تبصرون'),
(4377, 43, 52, 'أم أنا خير من هذا الذي هو مهين ولا يكاد يبين'),
(4378, 43, 53, 'فلولا ألقي عليه أسورة من ذهب أو جاء معه الملائكة مقترنين'),
(4379, 43, 54, 'فاستخف قومه فأطاعوه ۚ إنهم كانوا قوما فاسقين'),
(4380, 43, 55, 'فلما آسفونا انتقمنا منهم فأغرقناهم أجمعين'),
(4381, 43, 56, 'فجعلناهم سلفا ومثلا للآخرين'),
(4382, 43, 57, 'ولما ضرب ابن مريم مثلا إذا قومك منه يصدون'),
(4383, 43, 58, 'وقالوا أآلهتنا خير أم هو ۚ ما ضربوه لك إلا جدلا ۚ بل هم قوم خصمون'),
(4384, 43, 59, 'إن هو إلا عبد أنعمنا عليه وجعلناه مثلا لبني إسرائيل'),
(4385, 43, 60, 'ولو نشاء لجعلنا منكم ملائكة في الأرض يخلفون'),
(4386, 43, 61, 'وإنه لعلم للساعة فلا تمترن بها واتبعون ۚ هذا صراط مستقيم'),
(4387, 43, 62, 'ولا يصدنكم الشيطان ۖ إنه لكم عدو مبين'),
(4388, 43, 63, 'ولما جاء عيسى بالبينات قال قد جئتكم بالحكمة ولأبين لكم بعض الذي تختلفون فيه ۖ فاتقوا الله وأطيعون'),
(4389, 43, 64, 'إن الله هو ربي وربكم فاعبدوه ۚ هذا صراط مستقيم'),
(4390, 43, 65, 'فاختلف الأحزاب من بينهم ۖ فويل للذين ظلموا من عذاب يوم أليم'),
(4391, 43, 66, 'هل ينظرون إلا الساعة أن تأتيهم بغتة وهم لا يشعرون'),
(4392, 43, 67, 'الأخلاء يومئذ بعضهم لبعض عدو إلا المتقين'),
(4393, 43, 68, 'يا عباد لا خوف عليكم اليوم ولا أنتم تحزنون'),
(4394, 43, 69, 'الذين آمنوا بآياتنا وكانوا مسلمين'),
(4395, 43, 70, 'ادخلوا الجنة أنتم وأزواجكم تحبرون'),
(4396, 43, 71, 'يطاف عليهم بصحاف من ذهب وأكواب ۖ وفيها ما تشتهيه الأنفس وتلذ الأعين ۖ وأنتم فيها خالدون'),
(4397, 43, 72, 'وتلك الجنة التي أورثتموها بما كنتم تعملون'),
(4398, 43, 73, 'لكم فيها فاكهة كثيرة منها تأكلون'),
(4399, 43, 74, 'إن المجرمين في عذاب جهنم خالدون'),
(4400, 43, 75, 'لا يفتر عنهم وهم فيه مبلسون'),
(4401, 43, 76, 'وما ظلمناهم ولكن كانوا هم الظالمين'),
(4402, 43, 77, 'ونادوا يا مالك ليقض علينا ربك ۖ قال إنكم ماكثون'),
(4403, 43, 78, 'لقد جئناكم بالحق ولكن أكثركم للحق كارهون'),
(4404, 43, 79, 'أم أبرموا أمرا فإنا مبرمون'),
(4405, 43, 80, 'أم يحسبون أنا لا نسمع سرهم ونجواهم ۚ بلى ورسلنا لديهم يكتبون'),
(4406, 43, 81, 'قل إن كان للرحمن ولد فأنا أول العابدين'),
(4407, 43, 82, 'سبحان رب السماوات والأرض رب العرش عما يصفون'),
(4408, 43, 83, 'فذرهم يخوضوا ويلعبوا حتى يلاقوا يومهم الذي يوعدون'),
(4409, 43, 84, 'وهو الذي في السماء إله وفي الأرض إله ۚ وهو الحكيم العليم'),
(4410, 43, 85, 'وتبارك الذي له ملك السماوات والأرض وما بينهما وعنده علم الساعة وإليه ترجعون'),
(4411, 43, 86, 'ولا يملك الذين يدعون من دونه الشفاعة إلا من شهد بالحق وهم يعلمون'),
(4412, 43, 87, 'ولئن سألتهم من خلقهم ليقولن الله ۖ فأنى يؤفكون'),
(4413, 43, 88, 'وقيله يا رب إن هؤلاء قوم لا يؤمنون'),
(4414, 43, 89, 'فاصفح عنهم وقل سلام ۚ فسوف يعلمون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'بسم الله الرحمن الرحيم حم'),
(4416, 44, 2, 'والكتاب المبين'),
(4417, 44, 3, 'إنا أنزلناه في ليلة مباركة ۚ إنا كنا منذرين'),
(4418, 44, 4, 'فيها يفرق كل أمر حكيم'),
(4419, 44, 5, 'أمرا من عندنا ۚ إنا كنا مرسلين'),
(4420, 44, 6, 'رحمة من ربك ۚ إنه هو السميع العليم'),
(4421, 44, 7, 'رب السماوات والأرض وما بينهما ۖ إن كنتم موقنين'),
(4422, 44, 8, 'لا إله إلا هو يحيي ويميت ۖ ربكم ورب آبائكم الأولين'),
(4423, 44, 9, 'بل هم في شك يلعبون'),
(4424, 44, 10, 'فارتقب يوم تأتي السماء بدخان مبين'),
(4425, 44, 11, 'يغشى الناس ۖ هذا عذاب أليم'),
(4426, 44, 12, 'ربنا اكشف عنا العذاب إنا مؤمنون'),
(4427, 44, 13, 'أنى لهم الذكرى وقد جاءهم رسول مبين'),
(4428, 44, 14, 'ثم تولوا عنه وقالوا معلم مجنون'),
(4429, 44, 15, 'إنا كاشفو العذاب قليلا ۚ إنكم عائدون'),
(4430, 44, 16, 'يوم نبطش البطشة الكبرى إنا منتقمون'),
(4431, 44, 17, 'ولقد فتنا قبلهم قوم فرعون وجاءهم رسول كريم'),
(4432, 44, 18, 'أن أدوا إلي عباد الله ۖ إني لكم رسول أمين'),
(4433, 44, 19, 'وأن لا تعلوا على الله ۖ إني آتيكم بسلطان مبين'),
(4434, 44, 20, 'وإني عذت بربي وربكم أن ترجمون'),
(4435, 44, 21, 'وإن لم تؤمنوا لي فاعتزلون'),
(4436, 44, 22, 'فدعا ربه أن هؤلاء قوم مجرمون'),
(4437, 44, 23, 'فأسر بعبادي ليلا إنكم متبعون'),
(4438, 44, 24, 'واترك البحر رهوا ۖ إنهم جند مغرقون'),
(4439, 44, 25, 'كم تركوا من جنات وعيون'),
(4440, 44, 26, 'وزروع ومقام كريم'),
(4441, 44, 27, 'ونعمة كانوا فيها فاكهين'),
(4442, 44, 28, 'كذلك ۖ وأورثناها قوما آخرين'),
(4443, 44, 29, 'فما بكت عليهم السماء والأرض وما كانوا منظرين'),
(4444, 44, 30, 'ولقد نجينا بني إسرائيل من العذاب المهين'),
(4445, 44, 31, 'من فرعون ۚ إنه كان عاليا من المسرفين'),
(4446, 44, 32, 'ولقد اخترناهم على علم على العالمين'),
(4447, 44, 33, 'وآتيناهم من الآيات ما فيه بلاء مبين'),
(4448, 44, 34, 'إن هؤلاء ليقولون'),
(4449, 44, 35, 'إن هي إلا موتتنا الأولى وما نحن بمنشرين'),
(4450, 44, 36, 'فأتوا بآبائنا إن كنتم صادقين'),
(4451, 44, 37, 'أهم خير أم قوم تبع والذين من قبلهم ۚ أهلكناهم ۖ إنهم كانوا مجرمين'),
(4452, 44, 38, 'وما خلقنا السماوات والأرض وما بينهما لاعبين'),
(4453, 44, 39, 'ما خلقناهما إلا بالحق ولكن أكثرهم لا يعلمون'),
(4454, 44, 40, 'إن يوم الفصل ميقاتهم أجمعين'),
(4455, 44, 41, 'يوم لا يغني مولى عن مولى شيئا ولا هم ينصرون'),
(4456, 44, 42, 'إلا من رحم الله ۚ إنه هو العزيز الرحيم'),
(4457, 44, 43, 'إن شجرت الزقوم'),
(4458, 44, 44, 'طعام الأثيم'),
(4459, 44, 45, 'كالمهل يغلي في البطون'),
(4460, 44, 46, 'كغلي الحميم'),
(4461, 44, 47, 'خذوه فاعتلوه إلى سواء الجحيم'),
(4462, 44, 48, 'ثم صبوا فوق رأسه من عذاب الحميم'),
(4463, 44, 49, 'ذق إنك أنت العزيز الكريم'),
(4464, 44, 50, 'إن هذا ما كنتم به تمترون'),
(4465, 44, 51, 'إن المتقين في مقام أمين'),
(4466, 44, 52, 'في جنات وعيون'),
(4467, 44, 53, 'يلبسون من سندس وإستبرق متقابلين'),
(4468, 44, 54, 'كذلك وزوجناهم بحور عين'),
(4469, 44, 55, 'يدعون فيها بكل فاكهة آمنين'),
(4470, 44, 56, 'لا يذوقون فيها الموت إلا الموتة الأولى ۖ ووقاهم عذاب الجحيم'),
(4471, 44, 57, 'فضلا من ربك ۚ ذلك هو الفوز العظيم'),
(4472, 44, 58, 'فإنما يسرناه بلسانك لعلهم يتذكرون'),
(4473, 44, 59, 'فارتقب إنهم مرتقبون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'بسم الله الرحمن الرحيم حم'),
(4475, 45, 2, 'تنزيل الكتاب من الله العزيز الحكيم'),
(4476, 45, 3, 'إن في السماوات والأرض لآيات للمؤمنين'),
(4477, 45, 4, 'وفي خلقكم وما يبث من دابة آيات لقوم يوقنون'),
(4478, 45, 5, 'واختلاف الليل والنهار وما أنزل الله من السماء من رزق فأحيا به الأرض بعد موتها وتصريف الرياح آيات لقوم يعقلون'),
(4479, 45, 6, 'تلك آيات الله نتلوها عليك بالحق ۖ فبأي حديث بعد الله وآياته يؤمنون'),
(4480, 45, 7, 'ويل لكل أفاك أثيم'),
(4481, 45, 8, 'يسمع آيات الله تتلى عليه ثم يصر مستكبرا كأن لم يسمعها ۖ فبشره بعذاب أليم'),
(4482, 45, 9, 'وإذا علم من آياتنا شيئا اتخذها هزوا ۚ أولئك لهم عذاب مهين'),
(4483, 45, 10, 'من ورائهم جهنم ۖ ولا يغني عنهم ما كسبوا شيئا ولا ما اتخذوا من دون الله أولياء ۖ ولهم عذاب عظيم'),
(4484, 45, 11, 'هذا هدى ۖ والذين كفروا بآيات ربهم لهم عذاب من رجز أليم'),
(4485, 45, 12, 'الله الذي سخر لكم البحر لتجري الفلك فيه بأمره ولتبتغوا من فضله ولعلكم تشكرون'),
(4486, 45, 13, 'وسخر لكم ما في السماوات وما في الأرض جميعا منه ۚ إن في ذلك لآيات لقوم يتفكرون'),
(4487, 45, 14, 'قل للذين آمنوا يغفروا للذين لا يرجون أيام الله ليجزي قوما بما كانوا يكسبون'),
(4488, 45, 15, 'من عمل صالحا فلنفسه ۖ ومن أساء فعليها ۖ ثم إلى ربكم ترجعون'),
(4489, 45, 16, 'ولقد آتينا بني إسرائيل الكتاب والحكم والنبوة ورزقناهم من الطيبات وفضلناهم على العالمين'),
(4490, 45, 17, 'وآتيناهم بينات من الأمر ۖ فما اختلفوا إلا من بعد ما جاءهم العلم بغيا بينهم ۚ إن ربك يقضي بينهم يوم القيامة فيما كانوا فيه يختلفون'),
(4491, 45, 18, 'ثم جعلناك على شريعة من الأمر فاتبعها ولا تتبع أهواء الذين لا يعلمون'),
(4492, 45, 19, 'إنهم لن يغنوا عنك من الله شيئا ۚ وإن الظالمين بعضهم أولياء بعض ۖ والله ولي المتقين'),
(4493, 45, 20, 'هذا بصائر للناس وهدى ورحمة لقوم يوقنون'),
(4494, 45, 21, 'أم حسب الذين اجترحوا السيئات أن نجعلهم كالذين آمنوا وعملوا الصالحات سواء محياهم ومماتهم ۚ ساء ما يحكمون'),
(4495, 45, 22, 'وخلق الله السماوات والأرض بالحق ولتجزى كل نفس بما كسبت وهم لا يظلمون'),
(4496, 45, 23, 'أفرأيت من اتخذ إلهه هواه وأضله الله على علم وختم على سمعه وقلبه وجعل على بصره غشاوة فمن يهديه من بعد الله ۚ أفلا تذكرون'),
(4497, 45, 24, 'وقالوا ما هي إلا حياتنا الدنيا نموت ونحيا وما يهلكنا إلا الدهر ۚ وما لهم بذلك من علم ۖ إن هم إلا يظنون'),
(4498, 45, 25, 'وإذا تتلى عليهم آياتنا بينات ما كان حجتهم إلا أن قالوا ائتوا بآبائنا إن كنتم صادقين'),
(4499, 45, 26, 'قل الله يحييكم ثم يميتكم ثم يجمعكم إلى يوم القيامة لا ريب فيه ولكن أكثر الناس لا يعلمون'),
(4500, 45, 27, 'ولله ملك السماوات والأرض ۚ ويوم تقوم الساعة يومئذ يخسر المبطلون'),
(4501, 45, 28, 'وترى كل أمة جاثية ۚ كل أمة تدعى إلى كتابها اليوم تجزون ما كنتم تعملون'),
(4502, 45, 29, 'هذا كتابنا ينطق عليكم بالحق ۚ إنا كنا نستنسخ ما كنتم تعملون'),
(4503, 45, 30, 'فأما الذين آمنوا وعملوا الصالحات فيدخلهم ربهم في رحمته ۚ ذلك هو الفوز المبين'),
(4504, 45, 31, 'وأما الذين كفروا أفلم تكن آياتي تتلى عليكم فاستكبرتم وكنتم قوما مجرمين'),
(4505, 45, 32, 'وإذا قيل إن وعد الله حق والساعة لا ريب فيها قلتم ما ندري ما الساعة إن نظن إلا ظنا وما نحن بمستيقنين'),
(4506, 45, 33, 'وبدا لهم سيئات ما عملوا وحاق بهم ما كانوا به يستهزئون'),
(4507, 45, 34, 'وقيل اليوم ننساكم كما نسيتم لقاء يومكم هذا ومأواكم النار وما لكم من ناصرين'),
(4508, 45, 35, 'ذلكم بأنكم اتخذتم آيات الله هزوا وغرتكم الحياة الدنيا ۚ فاليوم لا يخرجون منها ولا هم يستعتبون'),
(4509, 45, 36, 'فلله الحمد رب السماوات ورب الأرض رب العالمين'),
(4510, 45, 37, 'وله الكبرياء في السماوات والأرض ۖ وهو العزيز الحكيم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'بسم الله الرحمن الرحيم حم'),
(4512, 46, 2, 'تنزيل الكتاب من الله العزيز الحكيم'),
(4513, 46, 3, 'ما خلقنا السماوات والأرض وما بينهما إلا بالحق وأجل مسمى ۚ والذين كفروا عما أنذروا معرضون'),
(4514, 46, 4, 'قل أرأيتم ما تدعون من دون الله أروني ماذا خلقوا من الأرض أم لهم شرك في السماوات ۖ ائتوني بكتاب من قبل هذا أو أثارة من علم إن كنتم صادقين'),
(4515, 46, 5, 'ومن أضل ممن يدعو من دون الله من لا يستجيب له إلى يوم القيامة وهم عن دعائهم غافلون'),
(4516, 46, 6, 'وإذا حشر الناس كانوا لهم أعداء وكانوا بعبادتهم كافرين'),
(4517, 46, 7, 'وإذا تتلى عليهم آياتنا بينات قال الذين كفروا للحق لما جاءهم هذا سحر مبين'),
(4518, 46, 8, 'أم يقولون افتراه ۖ قل إن افتريته فلا تملكون لي من الله شيئا ۖ هو أعلم بما تفيضون فيه ۖ كفى به شهيدا بيني وبينكم ۖ وهو الغفور الرحيم'),
(4519, 46, 9, 'قل ما كنت بدعا من الرسل وما أدري ما يفعل بي ولا بكم ۖ إن أتبع إلا ما يوحى إلي وما أنا إلا نذير مبين'),
(4520, 46, 10, 'قل أرأيتم إن كان من عند الله وكفرتم به وشهد شاهد من بني إسرائيل على مثله فآمن واستكبرتم ۖ إن الله لا يهدي القوم الظالمين'),
(4521, 46, 11, 'وقال الذين كفروا للذين آمنوا لو كان خيرا ما سبقونا إليه ۚ وإذ لم يهتدوا به فسيقولون هذا إفك قديم'),
(4522, 46, 12, 'ومن قبله كتاب موسى إماما ورحمة ۚ وهذا كتاب مصدق لسانا عربيا لينذر الذين ظلموا وبشرى للمحسنين'),
(4523, 46, 13, 'إن الذين قالوا ربنا الله ثم استقاموا فلا خوف عليهم ولا هم يحزنون'),
(4524, 46, 14, 'أولئك أصحاب الجنة خالدين فيها جزاء بما كانوا يعملون'),
(4525, 46, 15, 'ووصينا الإنسان بوالديه إحسانا ۖ حملته أمه كرها ووضعته كرها ۖ وحمله وفصاله ثلاثون شهرا ۚ حتى إذا بلغ أشده وبلغ أربعين سنة قال رب أوزعني أن أشكر نعمتك التي أنعمت علي وعلى والدي وأن أعمل صالحا ترضاه وأصلح لي في ذريتي ۖ إني تبت إليك وإني من المسلمين'),
(4526, 46, 16, 'أولئك الذين نتقبل عنهم أحسن ما عملوا ونتجاوز عن سيئاتهم في أصحاب الجنة ۖ وعد الصدق الذي كانوا يوعدون'),
(4527, 46, 17, 'والذي قال لوالديه أف لكما أتعدانني أن أخرج وقد خلت القرون من قبلي وهما يستغيثان الله ويلك آمن إن وعد الله حق فيقول ما هذا إلا أساطير الأولين'),
(4528, 46, 18, 'أولئك الذين حق عليهم القول في أمم قد خلت من قبلهم من الجن والإنس ۖ إنهم كانوا خاسرين'),
(4529, 46, 19, 'ولكل درجات مما عملوا ۖ وليوفيهم أعمالهم وهم لا يظلمون'),
(4530, 46, 20, 'ويوم يعرض الذين كفروا على النار أذهبتم طيباتكم في حياتكم الدنيا واستمتعتم بها فاليوم تجزون عذاب الهون بما كنتم تستكبرون في الأرض بغير الحق وبما كنتم تفسقون'),
(4531, 46, 21, 'واذكر أخا عاد إذ أنذر قومه بالأحقاف وقد خلت النذر من بين يديه ومن خلفه ألا تعبدوا إلا الله إني أخاف عليكم عذاب يوم عظيم'),
(4532, 46, 22, 'قالوا أجئتنا لتأفكنا عن آلهتنا فأتنا بما تعدنا إن كنت من الصادقين'),
(4533, 46, 23, 'قال إنما العلم عند الله وأبلغكم ما أرسلت به ولكني أراكم قوما تجهلون'),
(4534, 46, 24, 'فلما رأوه عارضا مستقبل أوديتهم قالوا هذا عارض ممطرنا ۚ بل هو ما استعجلتم به ۖ ريح فيها عذاب أليم'),
(4535, 46, 25, 'تدمر كل شيء بأمر ربها فأصبحوا لا يرى إلا مساكنهم ۚ كذلك نجزي القوم المجرمين'),
(4536, 46, 26, 'ولقد مكناهم فيما إن مكناكم فيه وجعلنا لهم سمعا وأبصارا وأفئدة فما أغنى عنهم سمعهم ولا أبصارهم ولا أفئدتهم من شيء إذ كانوا يجحدون بآيات الله وحاق بهم ما كانوا به يستهزئون'),
(4537, 46, 27, 'ولقد أهلكنا ما حولكم من القرى وصرفنا الآيات لعلهم يرجعون'),
(4538, 46, 28, 'فلولا نصرهم الذين اتخذوا من دون الله قربانا آلهة ۖ بل ضلوا عنهم ۚ وذلك إفكهم وما كانوا يفترون'),
(4539, 46, 29, 'وإذ صرفنا إليك نفرا من الجن يستمعون القرآن فلما حضروه قالوا أنصتوا ۖ فلما قضي ولوا إلى قومهم منذرين'),
(4540, 46, 30, 'قالوا يا قومنا إنا سمعنا كتابا أنزل من بعد موسى مصدقا لما بين يديه يهدي إلى الحق وإلى طريق مستقيم'),
(4541, 46, 31, 'يا قومنا أجيبوا داعي الله وآمنوا به يغفر لكم من ذنوبكم ويجركم من عذاب أليم'),
(4542, 46, 32, 'ومن لا يجب داعي الله فليس بمعجز في الأرض وليس له من دونه أولياء ۚ أولئك في ضلال مبين'),
(4543, 46, 33, 'أولم يروا أن الله الذي خلق السماوات والأرض ولم يعي بخلقهن بقادر على أن يحيي الموتى ۚ بلى إنه على كل شيء قدير'),
(4544, 46, 34, 'ويوم يعرض الذين كفروا على النار أليس هذا بالحق ۖ قالوا بلى وربنا ۚ قال فذوقوا العذاب بما كنتم تكفرون'),
(4545, 46, 35, 'فاصبر كما صبر أولو العزم من الرسل ولا تستعجل لهم ۚ كأنهم يوم يرون ما يوعدون لم يلبثوا إلا ساعة من نهار ۚ بلاغ ۚ فهل يهلك إلا القوم الفاسقون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'بسم الله الرحمن الرحيم الذين كفروا وصدوا عن سبيل الله أضل أعمالهم'),
(4547, 47, 2, 'والذين آمنوا وعملوا الصالحات وآمنوا بما نزل على محمد وهو الحق من ربهم ۙ كفر عنهم سيئاتهم وأصلح بالهم'),
(4548, 47, 3, 'ذلك بأن الذين كفروا اتبعوا الباطل وأن الذين آمنوا اتبعوا الحق من ربهم ۚ كذلك يضرب الله للناس أمثالهم'),
(4549, 47, 4, 'فإذا لقيتم الذين كفروا فضرب الرقاب حتى إذا أثخنتموهم فشدوا الوثاق فإما منا بعد وإما فداء حتى تضع الحرب أوزارها ۚ ذلك ولو يشاء الله لانتصر منهم ولكن ليبلو بعضكم ببعض ۗ والذين قتلوا في سبيل الله فلن يضل أعمالهم'),
(4550, 47, 5, 'سيهديهم ويصلح بالهم'),
(4551, 47, 6, 'ويدخلهم الجنة عرفها لهم'),
(4552, 47, 7, 'يا أيها الذين آمنوا إن تنصروا الله ينصركم ويثبت أقدامكم'),
(4553, 47, 8, 'والذين كفروا فتعسا لهم وأضل أعمالهم'),
(4554, 47, 9, 'ذلك بأنهم كرهوا ما أنزل الله فأحبط أعمالهم'),
(4555, 47, 10, 'أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم ۚ دمر الله عليهم ۖ وللكافرين أمثالها'),
(4556, 47, 11, 'ذلك بأن الله مولى الذين آمنوا وأن الكافرين لا مولى لهم'),
(4557, 47, 12, 'إن الله يدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار ۖ والذين كفروا يتمتعون ويأكلون كما تأكل الأنعام والنار مثوى لهم'),
(4558, 47, 13, 'وكأين من قرية هي أشد قوة من قريتك التي أخرجتك أهلكناهم فلا ناصر لهم'),
(4559, 47, 14, 'أفمن كان على بينة من ربه كمن زين له سوء عمله واتبعوا أهواءهم'),
(4560, 47, 15, 'مثل الجنة التي وعد المتقون ۖ فيها أنهار من ماء غير آسن وأنهار من لبن لم يتغير طعمه وأنهار من خمر لذة للشاربين وأنهار من عسل مصفى ۖ ولهم فيها من كل الثمرات ومغفرة من ربهم ۖ كمن هو خالد في النار وسقوا ماء حميما فقطع أمعاءهم'),
(4561, 47, 16, 'ومنهم من يستمع إليك حتى إذا خرجوا من عندك قالوا للذين أوتوا العلم ماذا قال آنفا ۚ أولئك الذين طبع الله على قلوبهم واتبعوا أهواءهم'),
(4562, 47, 17, 'والذين اهتدوا زادهم هدى وآتاهم تقواهم'),
(4563, 47, 18, 'فهل ينظرون إلا الساعة أن تأتيهم بغتة ۖ فقد جاء أشراطها ۚ فأنى لهم إذا جاءتهم ذكراهم'),
(4564, 47, 19, 'فاعلم أنه لا إله إلا الله واستغفر لذنبك وللمؤمنين والمؤمنات ۗ والله يعلم متقلبكم ومثواكم'),
(4565, 47, 20, 'ويقول الذين آمنوا لولا نزلت سورة ۖ فإذا أنزلت سورة محكمة وذكر فيها القتال ۙ رأيت الذين في قلوبهم مرض ينظرون إليك نظر المغشي عليه من الموت ۖ فأولى لهم'),
(4566, 47, 21, 'طاعة وقول معروف ۚ فإذا عزم الأمر فلو صدقوا الله لكان خيرا لهم'),
(4567, 47, 22, 'فهل عسيتم إن توليتم أن تفسدوا في الأرض وتقطعوا أرحامكم'),
(4568, 47, 23, 'أولئك الذين لعنهم الله فأصمهم وأعمى أبصارهم'),
(4569, 47, 24, 'أفلا يتدبرون القرآن أم على قلوب أقفالها'),
(4570, 47, 25, 'إن الذين ارتدوا على أدبارهم من بعد ما تبين لهم الهدى ۙ الشيطان سول لهم وأملى لهم'),
(4571, 47, 26, 'ذلك بأنهم قالوا للذين كرهوا ما نزل الله سنطيعكم في بعض الأمر ۖ والله يعلم إسرارهم'),
(4572, 47, 27, 'فكيف إذا توفتهم الملائكة يضربون وجوههم وأدبارهم'),
(4573, 47, 28, 'ذلك بأنهم اتبعوا ما أسخط الله وكرهوا رضوانه فأحبط أعمالهم'),
(4574, 47, 29, 'أم حسب الذين في قلوبهم مرض أن لن يخرج الله أضغانهم'),
(4575, 47, 30, 'ولو نشاء لأريناكهم فلعرفتهم بسيماهم ۚ ولتعرفنهم في لحن القول ۚ والله يعلم أعمالكم'),
(4576, 47, 31, 'ولنبلونكم حتى نعلم المجاهدين منكم والصابرين ونبلو أخباركم'),
(4577, 47, 32, 'إن الذين كفروا وصدوا عن سبيل الله وشاقوا الرسول من بعد ما تبين لهم الهدى لن يضروا الله شيئا وسيحبط أعمالهم'),
(4578, 47, 33, 'يا أيها الذين آمنوا أطيعوا الله وأطيعوا الرسول ولا تبطلوا أعمالكم'),
(4579, 47, 34, 'إن الذين كفروا وصدوا عن سبيل الله ثم ماتوا وهم كفار فلن يغفر الله لهم'),
(4580, 47, 35, 'فلا تهنوا وتدعوا إلى السلم وأنتم الأعلون والله معكم ولن يتركم أعمالكم'),
(4581, 47, 36, 'إنما الحياة الدنيا لعب ولهو ۚ وإن تؤمنوا وتتقوا يؤتكم أجوركم ولا يسألكم أموالكم'),
(4582, 47, 37, 'إن يسألكموها فيحفكم تبخلوا ويخرج أضغانكم'),
(4583, 47, 38, 'ها أنتم هؤلاء تدعون لتنفقوا في سبيل الله فمنكم من يبخل ۖ ومن يبخل فإنما يبخل عن نفسه ۚ والله الغني وأنتم الفقراء ۚ وإن تتولوا يستبدل قوما غيركم ثم لا يكونوا أمثالكم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'بسم الله الرحمن الرحيم إنا فتحنا لك فتحا مبينا'),
(4585, 48, 2, 'ليغفر لك الله ما تقدم من ذنبك وما تأخر ويتم نعمته عليك ويهديك صراطا مستقيما'),
(4586, 48, 3, 'وينصرك الله نصرا عزيزا'),
(4587, 48, 4, 'هو الذي أنزل السكينة في قلوب المؤمنين ليزدادوا إيمانا مع إيمانهم ۗ ولله جنود السماوات والأرض ۚ وكان الله عليما حكيما'),
(4588, 48, 5, 'ليدخل المؤمنين والمؤمنات جنات تجري من تحتها الأنهار خالدين فيها ويكفر عنهم سيئاتهم ۚ وكان ذلك عند الله فوزا عظيما'),
(4589, 48, 6, 'ويعذب المنافقين والمنافقات والمشركين والمشركات الظانين بالله ظن السوء ۚ عليهم دائرة السوء ۖ وغضب الله عليهم ولعنهم وأعد لهم جهنم ۖ وساءت مصيرا'),
(4590, 48, 7, 'ولله جنود السماوات والأرض ۚ وكان الله عزيزا حكيما'),
(4591, 48, 8, 'إنا أرسلناك شاهدا ومبشرا ونذيرا'),
(4592, 48, 9, 'لتؤمنوا بالله ورسوله وتعزروه وتوقروه وتسبحوه بكرة وأصيلا'),
(4593, 48, 10, 'إن الذين يبايعونك إنما يبايعون الله يد الله فوق أيديهم ۚ فمن نكث فإنما ينكث على نفسه ۖ ومن أوفى بما عاهد عليه الله فسيؤتيه أجرا عظيما'),
(4594, 48, 11, 'سيقول لك المخلفون من الأعراب شغلتنا أموالنا وأهلونا فاستغفر لنا ۚ يقولون بألسنتهم ما ليس في قلوبهم ۚ قل فمن يملك لكم من الله شيئا إن أراد بكم ضرا أو أراد بكم نفعا ۚ بل كان الله بما تعملون خبيرا'),
(4595, 48, 12, 'بل ظننتم أن لن ينقلب الرسول والمؤمنون إلى أهليهم أبدا وزين ذلك في قلوبكم وظننتم ظن السوء وكنتم قوما بورا'),
(4596, 48, 13, 'ومن لم يؤمن بالله ورسوله فإنا أعتدنا للكافرين سعيرا'),
(4597, 48, 14, 'ولله ملك السماوات والأرض ۚ يغفر لمن يشاء ويعذب من يشاء ۚ وكان الله غفورا رحيما'),
(4598, 48, 15, 'سيقول المخلفون إذا انطلقتم إلى مغانم لتأخذوها ذرونا نتبعكم ۖ يريدون أن يبدلوا كلام الله ۚ قل لن تتبعونا كذلكم قال الله من قبل ۖ فسيقولون بل تحسدوننا ۚ بل كانوا لا يفقهون إلا قليلا'),
(4599, 48, 16, 'قل للمخلفين من الأعراب ستدعون إلى قوم أولي بأس شديد تقاتلونهم أو يسلمون ۖ فإن تطيعوا يؤتكم الله أجرا حسنا ۖ وإن تتولوا كما توليتم من قبل يعذبكم عذابا أليما'),
(4600, 48, 17, 'ليس على الأعمى حرج ولا على الأعرج حرج ولا على المريض حرج ۗ ومن يطع الله ورسوله يدخله جنات تجري من تحتها الأنهار ۖ ومن يتول يعذبه عذابا أليما'),
(4601, 48, 18, 'لقد رضي الله عن المؤمنين إذ يبايعونك تحت الشجرة فعلم ما في قلوبهم فأنزل السكينة عليهم وأثابهم فتحا قريبا'),
(4602, 48, 19, 'ومغانم كثيرة يأخذونها ۗ وكان الله عزيزا حكيما'),
(4603, 48, 20, 'وعدكم الله مغانم كثيرة تأخذونها فعجل لكم هذه وكف أيدي الناس عنكم ولتكون آية للمؤمنين ويهديكم صراطا مستقيما'),
(4604, 48, 21, 'وأخرى لم تقدروا عليها قد أحاط الله بها ۚ وكان الله على كل شيء قديرا'),
(4605, 48, 22, 'ولو قاتلكم الذين كفروا لولوا الأدبار ثم لا يجدون وليا ولا نصيرا'),
(4606, 48, 23, 'سنة الله التي قد خلت من قبل ۖ ولن تجد لسنة الله تبديلا'),
(4607, 48, 24, 'وهو الذي كف أيديهم عنكم وأيديكم عنهم ببطن مكة من بعد أن أظفركم عليهم ۚ وكان الله بما تعملون بصيرا'),
(4608, 48, 25, 'هم الذين كفروا وصدوكم عن المسجد الحرام والهدي معكوفا أن يبلغ محله ۚ ولولا رجال مؤمنون ونساء مؤمنات لم تعلموهم أن تطئوهم فتصيبكم منهم معرة بغير علم ۖ ليدخل الله في رحمته من يشاء ۚ لو تزيلوا لعذبنا الذين كفروا منهم عذابا أليما'),
(4609, 48, 26, 'إذ جعل الذين كفروا في قلوبهم الحمية حمية الجاهلية فأنزل الله سكينته على رسوله وعلى المؤمنين وألزمهم كلمة التقوى وكانوا أحق بها وأهلها ۚ وكان الله بكل شيء عليما'),
(4610, 48, 27, 'لقد صدق الله رسوله الرؤيا بالحق ۖ لتدخلن المسجد الحرام إن شاء الله آمنين محلقين رءوسكم ومقصرين لا تخافون ۖ فعلم ما لم تعلموا فجعل من دون ذلك فتحا قريبا'),
(4611, 48, 28, 'هو الذي أرسل رسوله بالهدى ودين الحق ليظهره على الدين كله ۚ وكفى بالله شهيدا'),
(4612, 48, 29, 'محمد رسول الله ۚ والذين معه أشداء على الكفار رحماء بينهم ۖ تراهم ركعا سجدا يبتغون فضلا من الله ورضوانا ۖ سيماهم في وجوههم من أثر السجود ۚ ذلك مثلهم في التوراة ۚ ومثلهم في الإنجيل كزرع أخرج شطأه فآزره فاستغلظ فاستوى على سوقه يعجب الزراع ليغيظ بهم الكفار ۗ وعد الله الذين آمنوا وعملوا الصالحات منهم مغفرة وأجرا عظيما');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'بسم الله الرحمن الرحيم يا أيها الذين آمنوا لا تقدموا بين يدي الله ورسوله ۖ واتقوا الله ۚ إن الله سميع عليم'),
(4614, 49, 2, 'يا أيها الذين آمنوا لا ترفعوا أصواتكم فوق صوت النبي ولا تجهروا له بالقول كجهر بعضكم لبعض أن تحبط أعمالكم وأنتم لا تشعرون'),
(4615, 49, 3, 'إن الذين يغضون أصواتهم عند رسول الله أولئك الذين امتحن الله قلوبهم للتقوى ۚ لهم مغفرة وأجر عظيم'),
(4616, 49, 4, 'إن الذين ينادونك من وراء الحجرات أكثرهم لا يعقلون'),
(4617, 49, 5, 'ولو أنهم صبروا حتى تخرج إليهم لكان خيرا لهم ۚ والله غفور رحيم'),
(4618, 49, 6, 'يا أيها الذين آمنوا إن جاءكم فاسق بنبإ فتبينوا أن تصيبوا قوما بجهالة فتصبحوا على ما فعلتم نادمين'),
(4619, 49, 7, 'واعلموا أن فيكم رسول الله ۚ لو يطيعكم في كثير من الأمر لعنتم ولكن الله حبب إليكم الإيمان وزينه في قلوبكم وكره إليكم الكفر والفسوق والعصيان ۚ أولئك هم الراشدون'),
(4620, 49, 8, 'فضلا من الله ونعمة ۚ والله عليم حكيم'),
(4621, 49, 9, 'وإن طائفتان من المؤمنين اقتتلوا فأصلحوا بينهما ۖ فإن بغت إحداهما على الأخرى فقاتلوا التي تبغي حتى تفيء إلى أمر الله ۚ فإن فاءت فأصلحوا بينهما بالعدل وأقسطوا ۖ إن الله يحب المقسطين'),
(4622, 49, 10, 'إنما المؤمنون إخوة فأصلحوا بين أخويكم ۚ واتقوا الله لعلكم ترحمون'),
(4623, 49, 11, 'يا أيها الذين آمنوا لا يسخر قوم من قوم عسى أن يكونوا خيرا منهم ولا نساء من نساء عسى أن يكن خيرا منهن ۖ ولا تلمزوا أنفسكم ولا تنابزوا بالألقاب ۖ بئس الاسم الفسوق بعد الإيمان ۚ ومن لم يتب فأولئك هم الظالمون'),
(4624, 49, 12, 'يا أيها الذين آمنوا اجتنبوا كثيرا من الظن إن بعض الظن إثم ۖ ولا تجسسوا ولا يغتب بعضكم بعضا ۚ أيحب أحدكم أن يأكل لحم أخيه ميتا فكرهتموه ۚ واتقوا الله ۚ إن الله تواب رحيم'),
(4625, 49, 13, 'يا أيها الناس إنا خلقناكم من ذكر وأنثى وجعلناكم شعوبا وقبائل لتعارفوا ۚ إن أكرمكم عند الله أتقاكم ۚ إن الله عليم خبير'),
(4626, 49, 14, 'قالت الأعراب آمنا ۖ قل لم تؤمنوا ولكن قولوا أسلمنا ولما يدخل الإيمان في قلوبكم ۖ وإن تطيعوا الله ورسوله لا يلتكم من أعمالكم شيئا ۚ إن الله غفور رحيم'),
(4627, 49, 15, 'إنما المؤمنون الذين آمنوا بالله ورسوله ثم لم يرتابوا وجاهدوا بأموالهم وأنفسهم في سبيل الله ۚ أولئك هم الصادقون'),
(4628, 49, 16, 'قل أتعلمون الله بدينكم والله يعلم ما في السماوات وما في الأرض ۚ والله بكل شيء عليم'),
(4629, 49, 17, 'يمنون عليك أن أسلموا ۖ قل لا تمنوا علي إسلامكم ۖ بل الله يمن عليكم أن هداكم للإيمان إن كنتم صادقين'),
(4630, 49, 18, 'إن الله يعلم غيب السماوات والأرض ۚ والله بصير بما تعملون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'بسم الله الرحمن الرحيم ق ۚ والقرآن المجيد'),
(4632, 50, 2, 'بل عجبوا أن جاءهم منذر منهم فقال الكافرون هذا شيء عجيب'),
(4633, 50, 3, 'أإذا متنا وكنا ترابا ۖ ذلك رجع بعيد'),
(4634, 50, 4, 'قد علمنا ما تنقص الأرض منهم ۖ وعندنا كتاب حفيظ'),
(4635, 50, 5, 'بل كذبوا بالحق لما جاءهم فهم في أمر مريج'),
(4636, 50, 6, 'أفلم ينظروا إلى السماء فوقهم كيف بنيناها وزيناها وما لها من فروج'),
(4637, 50, 7, 'والأرض مددناها وألقينا فيها رواسي وأنبتنا فيها من كل زوج بهيج'),
(4638, 50, 8, 'تبصرة وذكرى لكل عبد منيب'),
(4639, 50, 9, 'ونزلنا من السماء ماء مباركا فأنبتنا به جنات وحب الحصيد'),
(4640, 50, 10, 'والنخل باسقات لها طلع نضيد'),
(4641, 50, 11, 'رزقا للعباد ۖ وأحيينا به بلدة ميتا ۚ كذلك الخروج'),
(4642, 50, 12, 'كذبت قبلهم قوم نوح وأصحاب الرس وثمود'),
(4643, 50, 13, 'وعاد وفرعون وإخوان لوط'),
(4644, 50, 14, 'وأصحاب الأيكة وقوم تبع ۚ كل كذب الرسل فحق وعيد'),
(4645, 50, 15, 'أفعيينا بالخلق الأول ۚ بل هم في لبس من خلق جديد'),
(4646, 50, 16, 'ولقد خلقنا الإنسان ونعلم ما توسوس به نفسه ۖ ونحن أقرب إليه من حبل الوريد'),
(4647, 50, 17, 'إذ يتلقى المتلقيان عن اليمين وعن الشمال قعيد'),
(4648, 50, 18, 'ما يلفظ من قول إلا لديه رقيب عتيد'),
(4649, 50, 19, 'وجاءت سكرة الموت بالحق ۖ ذلك ما كنت منه تحيد'),
(4650, 50, 20, 'ونفخ في الصور ۚ ذلك يوم الوعيد'),
(4651, 50, 21, 'وجاءت كل نفس معها سائق وشهيد'),
(4652, 50, 22, 'لقد كنت في غفلة من هذا فكشفنا عنك غطاءك فبصرك اليوم حديد'),
(4653, 50, 23, 'وقال قرينه هذا ما لدي عتيد'),
(4654, 50, 24, 'ألقيا في جهنم كل كفار عنيد'),
(4655, 50, 25, 'مناع للخير معتد مريب'),
(4656, 50, 26, 'الذي جعل مع الله إلها آخر فألقياه في العذاب الشديد'),
(4657, 50, 27, 'قال قرينه ربنا ما أطغيته ولكن كان في ضلال بعيد'),
(4658, 50, 28, 'قال لا تختصموا لدي وقد قدمت إليكم بالوعيد'),
(4659, 50, 29, 'ما يبدل القول لدي وما أنا بظلام للعبيد'),
(4660, 50, 30, 'يوم نقول لجهنم هل امتلأت وتقول هل من مزيد'),
(4661, 50, 31, 'وأزلفت الجنة للمتقين غير بعيد'),
(4662, 50, 32, 'هذا ما توعدون لكل أواب حفيظ'),
(4663, 50, 33, 'من خشي الرحمن بالغيب وجاء بقلب منيب'),
(4664, 50, 34, 'ادخلوها بسلام ۖ ذلك يوم الخلود'),
(4665, 50, 35, 'لهم ما يشاءون فيها ولدينا مزيد'),
(4666, 50, 36, 'وكم أهلكنا قبلهم من قرن هم أشد منهم بطشا فنقبوا في البلاد هل من محيص'),
(4667, 50, 37, 'إن في ذلك لذكرى لمن كان له قلب أو ألقى السمع وهو شهيد'),
(4668, 50, 38, 'ولقد خلقنا السماوات والأرض وما بينهما في ستة أيام وما مسنا من لغوب'),
(4669, 50, 39, 'فاصبر على ما يقولون وسبح بحمد ربك قبل طلوع الشمس وقبل الغروب'),
(4670, 50, 40, 'ومن الليل فسبحه وأدبار السجود'),
(4671, 50, 41, 'واستمع يوم يناد المناد من مكان قريب'),
(4672, 50, 42, 'يوم يسمعون الصيحة بالحق ۚ ذلك يوم الخروج'),
(4673, 50, 43, 'إنا نحن نحيي ونميت وإلينا المصير'),
(4674, 50, 44, 'يوم تشقق الأرض عنهم سراعا ۚ ذلك حشر علينا يسير'),
(4675, 50, 45, 'نحن أعلم بما يقولون ۖ وما أنت عليهم بجبار ۖ فذكر بالقرآن من يخاف وعيد');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'بسم الله الرحمن الرحيم والذاريات ذروا'),
(4677, 51, 2, 'فالحاملات وقرا'),
(4678, 51, 3, 'فالجاريات يسرا'),
(4679, 51, 4, 'فالمقسمات أمرا'),
(4680, 51, 5, 'إنما توعدون لصادق'),
(4681, 51, 6, 'وإن الدين لواقع'),
(4682, 51, 7, 'والسماء ذات الحبك'),
(4683, 51, 8, 'إنكم لفي قول مختلف'),
(4684, 51, 9, 'يؤفك عنه من أفك'),
(4685, 51, 10, 'قتل الخراصون'),
(4686, 51, 11, 'الذين هم في غمرة ساهون'),
(4687, 51, 12, 'يسألون أيان يوم الدين'),
(4688, 51, 13, 'يوم هم على النار يفتنون'),
(4689, 51, 14, 'ذوقوا فتنتكم هذا الذي كنتم به تستعجلون'),
(4690, 51, 15, 'إن المتقين في جنات وعيون'),
(4691, 51, 16, 'آخذين ما آتاهم ربهم ۚ إنهم كانوا قبل ذلك محسنين'),
(4692, 51, 17, 'كانوا قليلا من الليل ما يهجعون'),
(4693, 51, 18, 'وبالأسحار هم يستغفرون'),
(4694, 51, 19, 'وفي أموالهم حق للسائل والمحروم'),
(4695, 51, 20, 'وفي الأرض آيات للموقنين'),
(4696, 51, 21, 'وفي أنفسكم ۚ أفلا تبصرون'),
(4697, 51, 22, 'وفي السماء رزقكم وما توعدون'),
(4698, 51, 23, 'فورب السماء والأرض إنه لحق مثل ما أنكم تنطقون'),
(4699, 51, 24, 'هل أتاك حديث ضيف إبراهيم المكرمين'),
(4700, 51, 25, 'إذ دخلوا عليه فقالوا سلاما ۖ قال سلام قوم منكرون'),
(4701, 51, 26, 'فراغ إلى أهله فجاء بعجل سمين'),
(4702, 51, 27, 'فقربه إليهم قال ألا تأكلون'),
(4703, 51, 28, 'فأوجس منهم خيفة ۖ قالوا لا تخف ۖ وبشروه بغلام عليم'),
(4704, 51, 29, 'فأقبلت امرأته في صرة فصكت وجهها وقالت عجوز عقيم'),
(4705, 51, 30, 'قالوا كذلك قال ربك ۖ إنه هو الحكيم العليم'),
(4706, 51, 31, 'قال فما خطبكم أيها المرسلون'),
(4707, 51, 32, 'قالوا إنا أرسلنا إلى قوم مجرمين'),
(4708, 51, 33, 'لنرسل عليهم حجارة من طين'),
(4709, 51, 34, 'مسومة عند ربك للمسرفين'),
(4710, 51, 35, 'فأخرجنا من كان فيها من المؤمنين'),
(4711, 51, 36, 'فما وجدنا فيها غير بيت من المسلمين'),
(4712, 51, 37, 'وتركنا فيها آية للذين يخافون العذاب الأليم'),
(4713, 51, 38, 'وفي موسى إذ أرسلناه إلى فرعون بسلطان مبين'),
(4714, 51, 39, 'فتولى بركنه وقال ساحر أو مجنون'),
(4715, 51, 40, 'فأخذناه وجنوده فنبذناهم في اليم وهو مليم'),
(4716, 51, 41, 'وفي عاد إذ أرسلنا عليهم الريح العقيم'),
(4717, 51, 42, 'ما تذر من شيء أتت عليه إلا جعلته كالرميم'),
(4718, 51, 43, 'وفي ثمود إذ قيل لهم تمتعوا حتى حين'),
(4719, 51, 44, 'فعتوا عن أمر ربهم فأخذتهم الصاعقة وهم ينظرون'),
(4720, 51, 45, 'فما استطاعوا من قيام وما كانوا منتصرين'),
(4721, 51, 46, 'وقوم نوح من قبل ۖ إنهم كانوا قوما فاسقين'),
(4722, 51, 47, 'والسماء بنيناها بأيد وإنا لموسعون'),
(4723, 51, 48, 'والأرض فرشناها فنعم الماهدون'),
(4724, 51, 49, 'ومن كل شيء خلقنا زوجين لعلكم تذكرون'),
(4725, 51, 50, 'ففروا إلى الله ۖ إني لكم منه نذير مبين'),
(4726, 51, 51, 'ولا تجعلوا مع الله إلها آخر ۖ إني لكم منه نذير مبين'),
(4727, 51, 52, 'كذلك ما أتى الذين من قبلهم من رسول إلا قالوا ساحر أو مجنون'),
(4728, 51, 53, 'أتواصوا به ۚ بل هم قوم طاغون'),
(4729, 51, 54, 'فتول عنهم فما أنت بملوم'),
(4730, 51, 55, 'وذكر فإن الذكرى تنفع المؤمنين'),
(4731, 51, 56, 'وما خلقت الجن والإنس إلا ليعبدون'),
(4732, 51, 57, 'ما أريد منهم من رزق وما أريد أن يطعمون'),
(4733, 51, 58, 'إن الله هو الرزاق ذو القوة المتين'),
(4734, 51, 59, 'فإن للذين ظلموا ذنوبا مثل ذنوب أصحابهم فلا يستعجلون'),
(4735, 51, 60, 'فويل للذين كفروا من يومهم الذي يوعدون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'بسم الله الرحمن الرحيم والطور'),
(4737, 52, 2, 'وكتاب مسطور'),
(4738, 52, 3, 'في رق منشور'),
(4739, 52, 4, 'والبيت المعمور'),
(4740, 52, 5, 'والسقف المرفوع'),
(4741, 52, 6, 'والبحر المسجور'),
(4742, 52, 7, 'إن عذاب ربك لواقع'),
(4743, 52, 8, 'ما له من دافع'),
(4744, 52, 9, 'يوم تمور السماء مورا'),
(4745, 52, 10, 'وتسير الجبال سيرا'),
(4746, 52, 11, 'فويل يومئذ للمكذبين'),
(4747, 52, 12, 'الذين هم في خوض يلعبون'),
(4748, 52, 13, 'يوم يدعون إلى نار جهنم دعا'),
(4749, 52, 14, 'هذه النار التي كنتم بها تكذبون'),
(4750, 52, 15, 'أفسحر هذا أم أنتم لا تبصرون'),
(4751, 52, 16, 'اصلوها فاصبروا أو لا تصبروا سواء عليكم ۖ إنما تجزون ما كنتم تعملون'),
(4752, 52, 17, 'إن المتقين في جنات ونعيم'),
(4753, 52, 18, 'فاكهين بما آتاهم ربهم ووقاهم ربهم عذاب الجحيم'),
(4754, 52, 19, 'كلوا واشربوا هنيئا بما كنتم تعملون'),
(4755, 52, 20, 'متكئين على سرر مصفوفة ۖ وزوجناهم بحور عين'),
(4756, 52, 21, 'والذين آمنوا واتبعتهم ذريتهم بإيمان ألحقنا بهم ذريتهم وما ألتناهم من عملهم من شيء ۚ كل امرئ بما كسب رهين'),
(4757, 52, 22, 'وأمددناهم بفاكهة ولحم مما يشتهون'),
(4758, 52, 23, 'يتنازعون فيها كأسا لا لغو فيها ولا تأثيم'),
(4759, 52, 24, 'ويطوف عليهم غلمان لهم كأنهم لؤلؤ مكنون'),
(4760, 52, 25, 'وأقبل بعضهم على بعض يتساءلون'),
(4761, 52, 26, 'قالوا إنا كنا قبل في أهلنا مشفقين'),
(4762, 52, 27, 'فمن الله علينا ووقانا عذاب السموم'),
(4763, 52, 28, 'إنا كنا من قبل ندعوه ۖ إنه هو البر الرحيم'),
(4764, 52, 29, 'فذكر فما أنت بنعمت ربك بكاهن ولا مجنون'),
(4765, 52, 30, 'أم يقولون شاعر نتربص به ريب المنون'),
(4766, 52, 31, 'قل تربصوا فإني معكم من المتربصين'),
(4767, 52, 32, 'أم تأمرهم أحلامهم بهذا ۚ أم هم قوم طاغون'),
(4768, 52, 33, 'أم يقولون تقوله ۚ بل لا يؤمنون'),
(4769, 52, 34, 'فليأتوا بحديث مثله إن كانوا صادقين'),
(4770, 52, 35, 'أم خلقوا من غير شيء أم هم الخالقون'),
(4771, 52, 36, 'أم خلقوا السماوات والأرض ۚ بل لا يوقنون'),
(4772, 52, 37, 'أم عندهم خزائن ربك أم هم المصيطرون'),
(4773, 52, 38, 'أم لهم سلم يستمعون فيه ۖ فليأت مستمعهم بسلطان مبين'),
(4774, 52, 39, 'أم له البنات ولكم البنون'),
(4775, 52, 40, 'أم تسألهم أجرا فهم من مغرم مثقلون'),
(4776, 52, 41, 'أم عندهم الغيب فهم يكتبون'),
(4777, 52, 42, 'أم يريدون كيدا ۖ فالذين كفروا هم المكيدون'),
(4778, 52, 43, 'أم لهم إله غير الله ۚ سبحان الله عما يشركون'),
(4779, 52, 44, 'وإن يروا كسفا من السماء ساقطا يقولوا سحاب مركوم'),
(4780, 52, 45, 'فذرهم حتى يلاقوا يومهم الذي فيه يصعقون'),
(4781, 52, 46, 'يوم لا يغني عنهم كيدهم شيئا ولا هم ينصرون'),
(4782, 52, 47, 'وإن للذين ظلموا عذابا دون ذلك ولكن أكثرهم لا يعلمون'),
(4783, 52, 48, 'واصبر لحكم ربك فإنك بأعيننا ۖ وسبح بحمد ربك حين تقوم'),
(4784, 52, 49, 'ومن الليل فسبحه وإدبار النجوم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'بسم الله الرحمن الرحيم والنجم إذا هوى'),
(4786, 53, 2, 'ما ضل صاحبكم وما غوى'),
(4787, 53, 3, 'وما ينطق عن الهوى'),
(4788, 53, 4, 'إن هو إلا وحي يوحى'),
(4789, 53, 5, 'علمه شديد القوى'),
(4790, 53, 6, 'ذو مرة فاستوى'),
(4791, 53, 7, 'وهو بالأفق الأعلى'),
(4792, 53, 8, 'ثم دنا فتدلى'),
(4793, 53, 9, 'فكان قاب قوسين أو أدنى'),
(4794, 53, 10, 'فأوحى إلى عبده ما أوحى'),
(4795, 53, 11, 'ما كذب الفؤاد ما رأى'),
(4796, 53, 12, 'أفتمارونه على ما يرى'),
(4797, 53, 13, 'ولقد رآه نزلة أخرى'),
(4798, 53, 14, 'عند سدرة المنتهى'),
(4799, 53, 15, 'عندها جنة المأوى'),
(4800, 53, 16, 'إذ يغشى السدرة ما يغشى'),
(4801, 53, 17, 'ما زاغ البصر وما طغى'),
(4802, 53, 18, 'لقد رأى من آيات ربه الكبرى'),
(4803, 53, 19, 'أفرأيتم اللات والعزى'),
(4804, 53, 20, 'ومناة الثالثة الأخرى'),
(4805, 53, 21, 'ألكم الذكر وله الأنثى'),
(4806, 53, 22, 'تلك إذا قسمة ضيزى'),
(4807, 53, 23, 'إن هي إلا أسماء سميتموها أنتم وآباؤكم ما أنزل الله بها من سلطان ۚ إن يتبعون إلا الظن وما تهوى الأنفس ۖ ولقد جاءهم من ربهم الهدى'),
(4808, 53, 24, 'أم للإنسان ما تمنى'),
(4809, 53, 25, 'فلله الآخرة والأولى'),
(4810, 53, 26, 'وكم من ملك في السماوات لا تغني شفاعتهم شيئا إلا من بعد أن يأذن الله لمن يشاء ويرضى'),
(4811, 53, 27, 'إن الذين لا يؤمنون بالآخرة ليسمون الملائكة تسمية الأنثى'),
(4812, 53, 28, 'وما لهم به من علم ۖ إن يتبعون إلا الظن ۖ وإن الظن لا يغني من الحق شيئا'),
(4813, 53, 29, 'فأعرض عن من تولى عن ذكرنا ولم يرد إلا الحياة الدنيا'),
(4814, 53, 30, 'ذلك مبلغهم من العلم ۚ إن ربك هو أعلم بمن ضل عن سبيله وهو أعلم بمن اهتدى'),
(4815, 53, 31, 'ولله ما في السماوات وما في الأرض ليجزي الذين أساءوا بما عملوا ويجزي الذين أحسنوا بالحسنى'),
(4816, 53, 32, 'الذين يجتنبون كبائر الإثم والفواحش إلا اللمم ۚ إن ربك واسع المغفرة ۚ هو أعلم بكم إذ أنشأكم من الأرض وإذ أنتم أجنة في بطون أمهاتكم ۖ فلا تزكوا أنفسكم ۖ هو أعلم بمن اتقى'),
(4817, 53, 33, 'أفرأيت الذي تولى'),
(4818, 53, 34, 'وأعطى قليلا وأكدى'),
(4819, 53, 35, 'أعنده علم الغيب فهو يرى'),
(4820, 53, 36, 'أم لم ينبأ بما في صحف موسى'),
(4821, 53, 37, 'وإبراهيم الذي وفى'),
(4822, 53, 38, 'ألا تزر وازرة وزر أخرى'),
(4823, 53, 39, 'وأن ليس للإنسان إلا ما سعى'),
(4824, 53, 40, 'وأن سعيه سوف يرى'),
(4825, 53, 41, 'ثم يجزاه الجزاء الأوفى'),
(4826, 53, 42, 'وأن إلى ربك المنتهى'),
(4827, 53, 43, 'وأنه هو أضحك وأبكى'),
(4828, 53, 44, 'وأنه هو أمات وأحيا'),
(4829, 53, 45, 'وأنه خلق الزوجين الذكر والأنثى'),
(4830, 53, 46, 'من نطفة إذا تمنى'),
(4831, 53, 47, 'وأن عليه النشأة الأخرى'),
(4832, 53, 48, 'وأنه هو أغنى وأقنى'),
(4833, 53, 49, 'وأنه هو رب الشعرى'),
(4834, 53, 50, 'وأنه أهلك عادا الأولى'),
(4835, 53, 51, 'وثمود فما أبقى'),
(4836, 53, 52, 'وقوم نوح من قبل ۖ إنهم كانوا هم أظلم وأطغى'),
(4837, 53, 53, 'والمؤتفكة أهوى'),
(4838, 53, 54, 'فغشاها ما غشى'),
(4839, 53, 55, 'فبأي آلاء ربك تتمارى'),
(4840, 53, 56, 'هذا نذير من النذر الأولى'),
(4841, 53, 57, 'أزفت الآزفة'),
(4842, 53, 58, 'ليس لها من دون الله كاشفة'),
(4843, 53, 59, 'أفمن هذا الحديث تعجبون'),
(4844, 53, 60, 'وتضحكون ولا تبكون'),
(4845, 53, 61, 'وأنتم سامدون'),
(4846, 53, 62, 'فاسجدوا لله واعبدوا');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'بسم الله الرحمن الرحيم اقتربت الساعة وانشق القمر'),
(4848, 54, 2, 'وإن يروا آية يعرضوا ويقولوا سحر مستمر'),
(4849, 54, 3, 'وكذبوا واتبعوا أهواءهم ۚ وكل أمر مستقر'),
(4850, 54, 4, 'ولقد جاءهم من الأنباء ما فيه مزدجر'),
(4851, 54, 5, 'حكمة بالغة ۖ فما تغن النذر'),
(4852, 54, 6, 'فتول عنهم ۘ يوم يدع الداع إلى شيء نكر'),
(4853, 54, 7, 'خشعا أبصارهم يخرجون من الأجداث كأنهم جراد منتشر'),
(4854, 54, 8, 'مهطعين إلى الداع ۖ يقول الكافرون هذا يوم عسر'),
(4855, 54, 9, 'كذبت قبلهم قوم نوح فكذبوا عبدنا وقالوا مجنون وازدجر'),
(4856, 54, 10, 'فدعا ربه أني مغلوب فانتصر'),
(4857, 54, 11, 'ففتحنا أبواب السماء بماء منهمر'),
(4858, 54, 12, 'وفجرنا الأرض عيونا فالتقى الماء على أمر قد قدر'),
(4859, 54, 13, 'وحملناه على ذات ألواح ودسر'),
(4860, 54, 14, 'تجري بأعيننا جزاء لمن كان كفر'),
(4861, 54, 15, 'ولقد تركناها آية فهل من مدكر'),
(4862, 54, 16, 'فكيف كان عذابي ونذر'),
(4863, 54, 17, 'ولقد يسرنا القرآن للذكر فهل من مدكر'),
(4864, 54, 18, 'كذبت عاد فكيف كان عذابي ونذر'),
(4865, 54, 19, 'إنا أرسلنا عليهم ريحا صرصرا في يوم نحس مستمر'),
(4866, 54, 20, 'تنزع الناس كأنهم أعجاز نخل منقعر'),
(4867, 54, 21, 'فكيف كان عذابي ونذر'),
(4868, 54, 22, 'ولقد يسرنا القرآن للذكر فهل من مدكر'),
(4869, 54, 23, 'كذبت ثمود بالنذر'),
(4870, 54, 24, 'فقالوا أبشرا منا واحدا نتبعه إنا إذا لفي ضلال وسعر'),
(4871, 54, 25, 'أألقي الذكر عليه من بيننا بل هو كذاب أشر'),
(4872, 54, 26, 'سيعلمون غدا من الكذاب الأشر'),
(4873, 54, 27, 'إنا مرسلو الناقة فتنة لهم فارتقبهم واصطبر'),
(4874, 54, 28, 'ونبئهم أن الماء قسمة بينهم ۖ كل شرب محتضر'),
(4875, 54, 29, 'فنادوا صاحبهم فتعاطى فعقر'),
(4876, 54, 30, 'فكيف كان عذابي ونذر'),
(4877, 54, 31, 'إنا أرسلنا عليهم صيحة واحدة فكانوا كهشيم المحتظر'),
(4878, 54, 32, 'ولقد يسرنا القرآن للذكر فهل من مدكر'),
(4879, 54, 33, 'كذبت قوم لوط بالنذر'),
(4880, 54, 34, 'إنا أرسلنا عليهم حاصبا إلا آل لوط ۖ نجيناهم بسحر'),
(4881, 54, 35, 'نعمة من عندنا ۚ كذلك نجزي من شكر'),
(4882, 54, 36, 'ولقد أنذرهم بطشتنا فتماروا بالنذر'),
(4883, 54, 37, 'ولقد راودوه عن ضيفه فطمسنا أعينهم فذوقوا عذابي ونذر'),
(4884, 54, 38, 'ولقد صبحهم بكرة عذاب مستقر'),
(4885, 54, 39, 'فذوقوا عذابي ونذر'),
(4886, 54, 40, 'ولقد يسرنا القرآن للذكر فهل من مدكر'),
(4887, 54, 41, 'ولقد جاء آل فرعون النذر'),
(4888, 54, 42, 'كذبوا بآياتنا كلها فأخذناهم أخذ عزيز مقتدر'),
(4889, 54, 43, 'أكفاركم خير من أولئكم أم لكم براءة في الزبر'),
(4890, 54, 44, 'أم يقولون نحن جميع منتصر'),
(4891, 54, 45, 'سيهزم الجمع ويولون الدبر'),
(4892, 54, 46, 'بل الساعة موعدهم والساعة أدهى وأمر'),
(4893, 54, 47, 'إن المجرمين في ضلال وسعر'),
(4894, 54, 48, 'يوم يسحبون في النار على وجوههم ذوقوا مس سقر'),
(4895, 54, 49, 'إنا كل شيء خلقناه بقدر'),
(4896, 54, 50, 'وما أمرنا إلا واحدة كلمح بالبصر'),
(4897, 54, 51, 'ولقد أهلكنا أشياعكم فهل من مدكر'),
(4898, 54, 52, 'وكل شيء فعلوه في الزبر'),
(4899, 54, 53, 'وكل صغير وكبير مستطر'),
(4900, 54, 54, 'إن المتقين في جنات ونهر'),
(4901, 54, 55, 'في مقعد صدق عند مليك مقتدر');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'بسم الله الرحمن الرحيم الرحمن'),
(4903, 55, 2, 'علم القرآن'),
(4904, 55, 3, 'خلق الإنسان'),
(4905, 55, 4, 'علمه البيان'),
(4906, 55, 5, 'الشمس والقمر بحسبان'),
(4907, 55, 6, 'والنجم والشجر يسجدان'),
(4908, 55, 7, 'والسماء رفعها ووضع الميزان'),
(4909, 55, 8, 'ألا تطغوا في الميزان'),
(4910, 55, 9, 'وأقيموا الوزن بالقسط ولا تخسروا الميزان'),
(4911, 55, 10, 'والأرض وضعها للأنام'),
(4912, 55, 11, 'فيها فاكهة والنخل ذات الأكمام'),
(4913, 55, 12, 'والحب ذو العصف والريحان'),
(4914, 55, 13, 'فبأي آلاء ربكما تكذبان'),
(4915, 55, 14, 'خلق الإنسان من صلصال كالفخار'),
(4916, 55, 15, 'وخلق الجان من مارج من نار'),
(4917, 55, 16, 'فبأي آلاء ربكما تكذبان'),
(4918, 55, 17, 'رب المشرقين ورب المغربين'),
(4919, 55, 18, 'فبأي آلاء ربكما تكذبان'),
(4920, 55, 19, 'مرج البحرين يلتقيان'),
(4921, 55, 20, 'بينهما برزخ لا يبغيان'),
(4922, 55, 21, 'فبأي آلاء ربكما تكذبان'),
(4923, 55, 22, 'يخرج منهما اللؤلؤ والمرجان'),
(4924, 55, 23, 'فبأي آلاء ربكما تكذبان'),
(4925, 55, 24, 'وله الجوار المنشآت في البحر كالأعلام'),
(4926, 55, 25, 'فبأي آلاء ربكما تكذبان'),
(4927, 55, 26, 'كل من عليها فان'),
(4928, 55, 27, 'ويبقى وجه ربك ذو الجلال والإكرام'),
(4929, 55, 28, 'فبأي آلاء ربكما تكذبان'),
(4930, 55, 29, 'يسأله من في السماوات والأرض ۚ كل يوم هو في شأن'),
(4931, 55, 30, 'فبأي آلاء ربكما تكذبان'),
(4932, 55, 31, 'سنفرغ لكم أيه الثقلان'),
(4933, 55, 32, 'فبأي آلاء ربكما تكذبان'),
(4934, 55, 33, 'يا معشر الجن والإنس إن استطعتم أن تنفذوا من أقطار السماوات والأرض فانفذوا ۚ لا تنفذون إلا بسلطان'),
(4935, 55, 34, 'فبأي آلاء ربكما تكذبان'),
(4936, 55, 35, 'يرسل عليكما شواظ من نار ونحاس فلا تنتصران'),
(4937, 55, 36, 'فبأي آلاء ربكما تكذبان'),
(4938, 55, 37, 'فإذا انشقت السماء فكانت وردة كالدهان'),
(4939, 55, 38, 'فبأي آلاء ربكما تكذبان'),
(4940, 55, 39, 'فيومئذ لا يسأل عن ذنبه إنس ولا جان'),
(4941, 55, 40, 'فبأي آلاء ربكما تكذبان'),
(4942, 55, 41, 'يعرف المجرمون بسيماهم فيؤخذ بالنواصي والأقدام'),
(4943, 55, 42, 'فبأي آلاء ربكما تكذبان'),
(4944, 55, 43, 'هذه جهنم التي يكذب بها المجرمون'),
(4945, 55, 44, 'يطوفون بينها وبين حميم آن'),
(4946, 55, 45, 'فبأي آلاء ربكما تكذبان'),
(4947, 55, 46, 'ولمن خاف مقام ربه جنتان'),
(4948, 55, 47, 'فبأي آلاء ربكما تكذبان'),
(4949, 55, 48, 'ذواتا أفنان'),
(4950, 55, 49, 'فبأي آلاء ربكما تكذبان'),
(4951, 55, 50, 'فيهما عينان تجريان'),
(4952, 55, 51, 'فبأي آلاء ربكما تكذبان'),
(4953, 55, 52, 'فيهما من كل فاكهة زوجان'),
(4954, 55, 53, 'فبأي آلاء ربكما تكذبان'),
(4955, 55, 54, 'متكئين على فرش بطائنها من إستبرق ۚ وجنى الجنتين دان'),
(4956, 55, 55, 'فبأي آلاء ربكما تكذبان'),
(4957, 55, 56, 'فيهن قاصرات الطرف لم يطمثهن إنس قبلهم ولا جان'),
(4958, 55, 57, 'فبأي آلاء ربكما تكذبان'),
(4959, 55, 58, 'كأنهن الياقوت والمرجان'),
(4960, 55, 59, 'فبأي آلاء ربكما تكذبان'),
(4961, 55, 60, 'هل جزاء الإحسان إلا الإحسان'),
(4962, 55, 61, 'فبأي آلاء ربكما تكذبان'),
(4963, 55, 62, 'ومن دونهما جنتان'),
(4964, 55, 63, 'فبأي آلاء ربكما تكذبان'),
(4965, 55, 64, 'مدهامتان'),
(4966, 55, 65, 'فبأي آلاء ربكما تكذبان'),
(4967, 55, 66, 'فيهما عينان نضاختان'),
(4968, 55, 67, 'فبأي آلاء ربكما تكذبان'),
(4969, 55, 68, 'فيهما فاكهة ونخل ورمان'),
(4970, 55, 69, 'فبأي آلاء ربكما تكذبان'),
(4971, 55, 70, 'فيهن خيرات حسان'),
(4972, 55, 71, 'فبأي آلاء ربكما تكذبان'),
(4973, 55, 72, 'حور مقصورات في الخيام'),
(4974, 55, 73, 'فبأي آلاء ربكما تكذبان'),
(4975, 55, 74, 'لم يطمثهن إنس قبلهم ولا جان'),
(4976, 55, 75, 'فبأي آلاء ربكما تكذبان'),
(4977, 55, 76, 'متكئين على رفرف خضر وعبقري حسان'),
(4978, 55, 77, 'فبأي آلاء ربكما تكذبان'),
(4979, 55, 78, 'تبارك اسم ربك ذي الجلال والإكرام');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'بسم الله الرحمن الرحيم إذا وقعت الواقعة'),
(4981, 56, 2, 'ليس لوقعتها كاذبة'),
(4982, 56, 3, 'خافضة رافعة'),
(4983, 56, 4, 'إذا رجت الأرض رجا'),
(4984, 56, 5, 'وبست الجبال بسا'),
(4985, 56, 6, 'فكانت هباء منبثا'),
(4986, 56, 7, 'وكنتم أزواجا ثلاثة'),
(4987, 56, 8, 'فأصحاب الميمنة ما أصحاب الميمنة'),
(4988, 56, 9, 'وأصحاب المشأمة ما أصحاب المشأمة'),
(4989, 56, 10, 'والسابقون السابقون'),
(4990, 56, 11, 'أولئك المقربون'),
(4991, 56, 12, 'في جنات النعيم'),
(4992, 56, 13, 'ثلة من الأولين'),
(4993, 56, 14, 'وقليل من الآخرين'),
(4994, 56, 15, 'على سرر موضونة'),
(4995, 56, 16, 'متكئين عليها متقابلين'),
(4996, 56, 17, 'يطوف عليهم ولدان مخلدون'),
(4997, 56, 18, 'بأكواب وأباريق وكأس من معين'),
(4998, 56, 19, 'لا يصدعون عنها ولا ينزفون'),
(4999, 56, 20, 'وفاكهة مما يتخيرون'),
(5000, 56, 21, 'ولحم طير مما يشتهون'),
(5001, 56, 22, 'وحور عين'),
(5002, 56, 23, 'كأمثال اللؤلؤ المكنون'),
(5003, 56, 24, 'جزاء بما كانوا يعملون'),
(5004, 56, 25, 'لا يسمعون فيها لغوا ولا تأثيما'),
(5005, 56, 26, 'إلا قيلا سلاما سلاما'),
(5006, 56, 27, 'وأصحاب اليمين ما أصحاب اليمين'),
(5007, 56, 28, 'في سدر مخضود'),
(5008, 56, 29, 'وطلح منضود'),
(5009, 56, 30, 'وظل ممدود'),
(5010, 56, 31, 'وماء مسكوب'),
(5011, 56, 32, 'وفاكهة كثيرة'),
(5012, 56, 33, 'لا مقطوعة ولا ممنوعة'),
(5013, 56, 34, 'وفرش مرفوعة'),
(5014, 56, 35, 'إنا أنشأناهن إنشاء'),
(5015, 56, 36, 'فجعلناهن أبكارا'),
(5016, 56, 37, 'عربا أترابا'),
(5017, 56, 38, 'لأصحاب اليمين'),
(5018, 56, 39, 'ثلة من الأولين'),
(5019, 56, 40, 'وثلة من الآخرين'),
(5020, 56, 41, 'وأصحاب الشمال ما أصحاب الشمال'),
(5021, 56, 42, 'في سموم وحميم'),
(5022, 56, 43, 'وظل من يحموم'),
(5023, 56, 44, 'لا بارد ولا كريم'),
(5024, 56, 45, 'إنهم كانوا قبل ذلك مترفين'),
(5025, 56, 46, 'وكانوا يصرون على الحنث العظيم'),
(5026, 56, 47, 'وكانوا يقولون أئذا متنا وكنا ترابا وعظاما أإنا لمبعوثون'),
(5027, 56, 48, 'أوآباؤنا الأولون'),
(5028, 56, 49, 'قل إن الأولين والآخرين'),
(5029, 56, 50, 'لمجموعون إلى ميقات يوم معلوم'),
(5030, 56, 51, 'ثم إنكم أيها الضالون المكذبون'),
(5031, 56, 52, 'لآكلون من شجر من زقوم'),
(5032, 56, 53, 'فمالئون منها البطون'),
(5033, 56, 54, 'فشاربون عليه من الحميم'),
(5034, 56, 55, 'فشاربون شرب الهيم'),
(5035, 56, 56, 'هذا نزلهم يوم الدين'),
(5036, 56, 57, 'نحن خلقناكم فلولا تصدقون'),
(5037, 56, 58, 'أفرأيتم ما تمنون'),
(5038, 56, 59, 'أأنتم تخلقونه أم نحن الخالقون'),
(5039, 56, 60, 'نحن قدرنا بينكم الموت وما نحن بمسبوقين'),
(5040, 56, 61, 'على أن نبدل أمثالكم وننشئكم في ما لا تعلمون'),
(5041, 56, 62, 'ولقد علمتم النشأة الأولى فلولا تذكرون'),
(5042, 56, 63, 'أفرأيتم ما تحرثون'),
(5043, 56, 64, 'أأنتم تزرعونه أم نحن الزارعون'),
(5044, 56, 65, 'لو نشاء لجعلناه حطاما فظلتم تفكهون'),
(5045, 56, 66, 'إنا لمغرمون'),
(5046, 56, 67, 'بل نحن محرومون'),
(5047, 56, 68, 'أفرأيتم الماء الذي تشربون'),
(5048, 56, 69, 'أأنتم أنزلتموه من المزن أم نحن المنزلون'),
(5049, 56, 70, 'لو نشاء جعلناه أجاجا فلولا تشكرون'),
(5050, 56, 71, 'أفرأيتم النار التي تورون'),
(5051, 56, 72, 'أأنتم أنشأتم شجرتها أم نحن المنشئون'),
(5052, 56, 73, 'نحن جعلناها تذكرة ومتاعا للمقوين'),
(5053, 56, 74, 'فسبح باسم ربك العظيم'),
(5054, 56, 75, 'فلا أقسم بمواقع النجوم'),
(5055, 56, 76, 'وإنه لقسم لو تعلمون عظيم'),
(5056, 56, 77, 'إنه لقرآن كريم'),
(5057, 56, 78, 'في كتاب مكنون'),
(5058, 56, 79, 'لا يمسه إلا المطهرون'),
(5059, 56, 80, 'تنزيل من رب العالمين'),
(5060, 56, 81, 'أفبهذا الحديث أنتم مدهنون'),
(5061, 56, 82, 'وتجعلون رزقكم أنكم تكذبون'),
(5062, 56, 83, 'فلولا إذا بلغت الحلقوم'),
(5063, 56, 84, 'وأنتم حينئذ تنظرون'),
(5064, 56, 85, 'ونحن أقرب إليه منكم ولكن لا تبصرون'),
(5065, 56, 86, 'فلولا إن كنتم غير مدينين'),
(5066, 56, 87, 'ترجعونها إن كنتم صادقين'),
(5067, 56, 88, 'فأما إن كان من المقربين'),
(5068, 56, 89, 'فروح وريحان وجنت نعيم'),
(5069, 56, 90, 'وأما إن كان من أصحاب اليمين'),
(5070, 56, 91, 'فسلام لك من أصحاب اليمين'),
(5071, 56, 92, 'وأما إن كان من المكذبين الضالين'),
(5072, 56, 93, 'فنزل من حميم'),
(5073, 56, 94, 'وتصلية جحيم'),
(5074, 56, 95, 'إن هذا لهو حق اليقين'),
(5075, 56, 96, 'فسبح باسم ربك العظيم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'بسم الله الرحمن الرحيم سبح لله ما في السماوات والأرض ۖ وهو العزيز الحكيم'),
(5077, 57, 2, 'له ملك السماوات والأرض ۖ يحيي ويميت ۖ وهو على كل شيء قدير'),
(5078, 57, 3, 'هو الأول والآخر والظاهر والباطن ۖ وهو بكل شيء عليم'),
(5079, 57, 4, 'هو الذي خلق السماوات والأرض في ستة أيام ثم استوى على العرش ۚ يعلم ما يلج في الأرض وما يخرج منها وما ينزل من السماء وما يعرج فيها ۖ وهو معكم أين ما كنتم ۚ والله بما تعملون بصير'),
(5080, 57, 5, 'له ملك السماوات والأرض ۚ وإلى الله ترجع الأمور'),
(5081, 57, 6, 'يولج الليل في النهار ويولج النهار في الليل ۚ وهو عليم بذات الصدور'),
(5082, 57, 7, 'آمنوا بالله ورسوله وأنفقوا مما جعلكم مستخلفين فيه ۖ فالذين آمنوا منكم وأنفقوا لهم أجر كبير'),
(5083, 57, 8, 'وما لكم لا تؤمنون بالله ۙ والرسول يدعوكم لتؤمنوا بربكم وقد أخذ ميثاقكم إن كنتم مؤمنين'),
(5084, 57, 9, 'هو الذي ينزل على عبده آيات بينات ليخرجكم من الظلمات إلى النور ۚ وإن الله بكم لرءوف رحيم'),
(5085, 57, 10, 'وما لكم ألا تنفقوا في سبيل الله ولله ميراث السماوات والأرض ۚ لا يستوي منكم من أنفق من قبل الفتح وقاتل ۚ أولئك أعظم درجة من الذين أنفقوا من بعد وقاتلوا ۚ وكلا وعد الله الحسنى ۚ والله بما تعملون خبير'),
(5086, 57, 11, 'من ذا الذي يقرض الله قرضا حسنا فيضاعفه له وله أجر كريم'),
(5087, 57, 12, 'يوم ترى المؤمنين والمؤمنات يسعى نورهم بين أيديهم وبأيمانهم بشراكم اليوم جنات تجري من تحتها الأنهار خالدين فيها ۚ ذلك هو الفوز العظيم'),
(5088, 57, 13, 'يوم يقول المنافقون والمنافقات للذين آمنوا انظرونا نقتبس من نوركم قيل ارجعوا وراءكم فالتمسوا نورا فضرب بينهم بسور له باب باطنه فيه الرحمة وظاهره من قبله العذاب'),
(5089, 57, 14, 'ينادونهم ألم نكن معكم ۖ قالوا بلى ولكنكم فتنتم أنفسكم وتربصتم وارتبتم وغرتكم الأماني حتى جاء أمر الله وغركم بالله الغرور'),
(5090, 57, 15, 'فاليوم لا يؤخذ منكم فدية ولا من الذين كفروا ۚ مأواكم النار ۖ هي مولاكم ۖ وبئس المصير'),
(5091, 57, 16, 'ألم يأن للذين آمنوا أن تخشع قلوبهم لذكر الله وما نزل من الحق ولا يكونوا كالذين أوتوا الكتاب من قبل فطال عليهم الأمد فقست قلوبهم ۖ وكثير منهم فاسقون'),
(5092, 57, 17, 'اعلموا أن الله يحيي الأرض بعد موتها ۚ قد بينا لكم الآيات لعلكم تعقلون'),
(5093, 57, 18, 'إن المصدقين والمصدقات وأقرضوا الله قرضا حسنا يضاعف لهم ولهم أجر كريم'),
(5094, 57, 19, 'والذين آمنوا بالله ورسله أولئك هم الصديقون ۖ والشهداء عند ربهم لهم أجرهم ونورهم ۖ والذين كفروا وكذبوا بآياتنا أولئك أصحاب الجحيم'),
(5095, 57, 20, 'اعلموا أنما الحياة الدنيا لعب ولهو وزينة وتفاخر بينكم وتكاثر في الأموال والأولاد ۖ كمثل غيث أعجب الكفار نباته ثم يهيج فتراه مصفرا ثم يكون حطاما ۖ وفي الآخرة عذاب شديد ومغفرة من الله ورضوان ۚ وما الحياة الدنيا إلا متاع الغرور'),
(5096, 57, 21, 'سابقوا إلى مغفرة من ربكم وجنة عرضها كعرض السماء والأرض أعدت للذين آمنوا بالله ورسله ۚ ذلك فضل الله يؤتيه من يشاء ۚ والله ذو الفضل العظيم'),
(5097, 57, 22, 'ما أصاب من مصيبة في الأرض ولا في أنفسكم إلا في كتاب من قبل أن نبرأها ۚ إن ذلك على الله يسير'),
(5098, 57, 23, 'لكيلا تأسوا على ما فاتكم ولا تفرحوا بما آتاكم ۗ والله لا يحب كل مختال فخور'),
(5099, 57, 24, 'الذين يبخلون ويأمرون الناس بالبخل ۗ ومن يتول فإن الله هو الغني الحميد'),
(5100, 57, 25, 'لقد أرسلنا رسلنا بالبينات وأنزلنا معهم الكتاب والميزان ليقوم الناس بالقسط ۖ وأنزلنا الحديد فيه بأس شديد ومنافع للناس وليعلم الله من ينصره ورسله بالغيب ۚ إن الله قوي عزيز'),
(5101, 57, 26, 'ولقد أرسلنا نوحا وإبراهيم وجعلنا في ذريتهما النبوة والكتاب ۖ فمنهم مهتد ۖ وكثير منهم فاسقون'),
(5102, 57, 27, 'ثم قفينا على آثارهم برسلنا وقفينا بعيسى ابن مريم وآتيناه الإنجيل وجعلنا في قلوب الذين اتبعوه رأفة ورحمة ورهبانية ابتدعوها ما كتبناها عليهم إلا ابتغاء رضوان الله فما رعوها حق رعايتها ۖ فآتينا الذين آمنوا منهم أجرهم ۖ وكثير منهم فاسقون'),
(5103, 57, 28, 'يا أيها الذين آمنوا اتقوا الله وآمنوا برسوله يؤتكم كفلين من رحمته ويجعل لكم نورا تمشون به ويغفر لكم ۚ والله غفور رحيم'),
(5104, 57, 29, 'لئلا يعلم أهل الكتاب ألا يقدرون على شيء من فضل الله ۙ وأن الفضل بيد الله يؤتيه من يشاء ۚ والله ذو الفضل العظيم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'بسم الله الرحمن الرحيم قد سمع الله قول التي تجادلك في زوجها وتشتكي إلى الله والله يسمع تحاوركما ۚ إن الله سميع بصير'),
(5106, 58, 2, 'الذين يظاهرون منكم من نسائهم ما هن أمهاتهم ۖ إن أمهاتهم إلا اللائي ولدنهم ۚ وإنهم ليقولون منكرا من القول وزورا ۚ وإن الله لعفو غفور'),
(5107, 58, 3, 'والذين يظاهرون من نسائهم ثم يعودون لما قالوا فتحرير رقبة من قبل أن يتماسا ۚ ذلكم توعظون به ۚ والله بما تعملون خبير'),
(5108, 58, 4, 'فمن لم يجد فصيام شهرين متتابعين من قبل أن يتماسا ۖ فمن لم يستطع فإطعام ستين مسكينا ۚ ذلك لتؤمنوا بالله ورسوله ۚ وتلك حدود الله ۗ وللكافرين عذاب أليم'),
(5109, 58, 5, 'إن الذين يحادون الله ورسوله كبتوا كما كبت الذين من قبلهم ۚ وقد أنزلنا آيات بينات ۚ وللكافرين عذاب مهين'),
(5110, 58, 6, 'يوم يبعثهم الله جميعا فينبئهم بما عملوا ۚ أحصاه الله ونسوه ۚ والله على كل شيء شهيد'),
(5111, 58, 7, 'ألم تر أن الله يعلم ما في السماوات وما في الأرض ۖ ما يكون من نجوى ثلاثة إلا هو رابعهم ولا خمسة إلا هو سادسهم ولا أدنى من ذلك ولا أكثر إلا هو معهم أين ما كانوا ۖ ثم ينبئهم بما عملوا يوم القيامة ۚ إن الله بكل شيء عليم'),
(5112, 58, 8, 'ألم تر إلى الذين نهوا عن النجوى ثم يعودون لما نهوا عنه ويتناجون بالإثم والعدوان ومعصيت الرسول وإذا جاءوك حيوك بما لم يحيك به الله ويقولون في أنفسهم لولا يعذبنا الله بما نقول ۚ حسبهم جهنم يصلونها ۖ فبئس المصير'),
(5113, 58, 9, 'يا أيها الذين آمنوا إذا تناجيتم فلا تتناجوا بالإثم والعدوان ومعصيت الرسول وتناجوا بالبر والتقوى ۖ واتقوا الله الذي إليه تحشرون'),
(5114, 58, 10, 'إنما النجوى من الشيطان ليحزن الذين آمنوا وليس بضارهم شيئا إلا بإذن الله ۚ وعلى الله فليتوكل المؤمنون'),
(5115, 58, 11, 'يا أيها الذين آمنوا إذا قيل لكم تفسحوا في المجالس فافسحوا يفسح الله لكم ۖ وإذا قيل انشزوا فانشزوا يرفع الله الذين آمنوا منكم والذين أوتوا العلم درجات ۚ والله بما تعملون خبير'),
(5116, 58, 12, 'يا أيها الذين آمنوا إذا ناجيتم الرسول فقدموا بين يدي نجواكم صدقة ۚ ذلك خير لكم وأطهر ۚ فإن لم تجدوا فإن الله غفور رحيم'),
(5117, 58, 13, 'أأشفقتم أن تقدموا بين يدي نجواكم صدقات ۚ فإذ لم تفعلوا وتاب الله عليكم فأقيموا الصلاة وآتوا الزكاة وأطيعوا الله ورسوله ۚ والله خبير بما تعملون'),
(5118, 58, 14, 'ألم تر إلى الذين تولوا قوما غضب الله عليهم ما هم منكم ولا منهم ويحلفون على الكذب وهم يعلمون'),
(5119, 58, 15, 'أعد الله لهم عذابا شديدا ۖ إنهم ساء ما كانوا يعملون'),
(5120, 58, 16, 'اتخذوا أيمانهم جنة فصدوا عن سبيل الله فلهم عذاب مهين'),
(5121, 58, 17, 'لن تغني عنهم أموالهم ولا أولادهم من الله شيئا ۚ أولئك أصحاب النار ۖ هم فيها خالدون'),
(5122, 58, 18, 'يوم يبعثهم الله جميعا فيحلفون له كما يحلفون لكم ۖ ويحسبون أنهم على شيء ۚ ألا إنهم هم الكاذبون'),
(5123, 58, 19, 'استحوذ عليهم الشيطان فأنساهم ذكر الله ۚ أولئك حزب الشيطان ۚ ألا إن حزب الشيطان هم الخاسرون'),
(5124, 58, 20, 'إن الذين يحادون الله ورسوله أولئك في الأذلين'),
(5125, 58, 21, 'كتب الله لأغلبن أنا ورسلي ۚ إن الله قوي عزيز'),
(5126, 58, 22, 'لا تجد قوما يؤمنون بالله واليوم الآخر يوادون من حاد الله ورسوله ولو كانوا آباءهم أو أبناءهم أو إخوانهم أو عشيرتهم ۚ أولئك كتب في قلوبهم الإيمان وأيدهم بروح منه ۖ ويدخلهم جنات تجري من تحتها الأنهار خالدين فيها ۚ رضي الله عنهم ورضوا عنه ۚ أولئك حزب الله ۚ ألا إن حزب الله هم المفلحون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'بسم الله الرحمن الرحيم سبح لله ما في السماوات وما في الأرض ۖ وهو العزيز الحكيم'),
(5128, 59, 2, 'هو الذي أخرج الذين كفروا من أهل الكتاب من ديارهم لأول الحشر ۚ ما ظننتم أن يخرجوا ۖ وظنوا أنهم مانعتهم حصونهم من الله فأتاهم الله من حيث لم يحتسبوا ۖ وقذف في قلوبهم الرعب ۚ يخربون بيوتهم بأيديهم وأيدي المؤمنين فاعتبروا يا أولي الأبصار'),
(5129, 59, 3, 'ولولا أن كتب الله عليهم الجلاء لعذبهم في الدنيا ۖ ولهم في الآخرة عذاب النار'),
(5130, 59, 4, 'ذلك بأنهم شاقوا الله ورسوله ۖ ومن يشاق الله فإن الله شديد العقاب'),
(5131, 59, 5, 'ما قطعتم من لينة أو تركتموها قائمة على أصولها فبإذن الله وليخزي الفاسقين'),
(5132, 59, 6, 'وما أفاء الله على رسوله منهم فما أوجفتم عليه من خيل ولا ركاب ولكن الله يسلط رسله على من يشاء ۚ والله على كل شيء قدير'),
(5133, 59, 7, 'ما أفاء الله على رسوله من أهل القرى فلله وللرسول ولذي القربى واليتامى والمساكين وابن السبيل كي لا يكون دولة بين الأغنياء منكم ۚ وما آتاكم الرسول فخذوه وما نهاكم عنه فانتهوا ۚ واتقوا الله ۖ إن الله شديد العقاب'),
(5134, 59, 8, 'للفقراء المهاجرين الذين أخرجوا من ديارهم وأموالهم يبتغون فضلا من الله ورضوانا وينصرون الله ورسوله ۚ أولئك هم الصادقون'),
(5135, 59, 9, 'والذين تبوءوا الدار والإيمان من قبلهم يحبون من هاجر إليهم ولا يجدون في صدورهم حاجة مما أوتوا ويؤثرون على أنفسهم ولو كان بهم خصاصة ۚ ومن يوق شح نفسه فأولئك هم المفلحون'),
(5136, 59, 10, 'والذين جاءوا من بعدهم يقولون ربنا اغفر لنا ولإخواننا الذين سبقونا بالإيمان ولا تجعل في قلوبنا غلا للذين آمنوا ربنا إنك رءوف رحيم'),
(5137, 59, 11, 'ألم تر إلى الذين نافقوا يقولون لإخوانهم الذين كفروا من أهل الكتاب لئن أخرجتم لنخرجن معكم ولا نطيع فيكم أحدا أبدا وإن قوتلتم لننصرنكم والله يشهد إنهم لكاذبون'),
(5138, 59, 12, 'لئن أخرجوا لا يخرجون معهم ولئن قوتلوا لا ينصرونهم ولئن نصروهم ليولن الأدبار ثم لا ينصرون'),
(5139, 59, 13, 'لأنتم أشد رهبة في صدورهم من الله ۚ ذلك بأنهم قوم لا يفقهون'),
(5140, 59, 14, 'لا يقاتلونكم جميعا إلا في قرى محصنة أو من وراء جدر ۚ بأسهم بينهم شديد ۚ تحسبهم جميعا وقلوبهم شتى ۚ ذلك بأنهم قوم لا يعقلون'),
(5141, 59, 15, 'كمثل الذين من قبلهم قريبا ۖ ذاقوا وبال أمرهم ولهم عذاب أليم'),
(5142, 59, 16, 'كمثل الشيطان إذ قال للإنسان اكفر فلما كفر قال إني بريء منك إني أخاف الله رب العالمين'),
(5143, 59, 17, 'فكان عاقبتهما أنهما في النار خالدين فيها ۚ وذلك جزاء الظالمين'),
(5144, 59, 18, 'يا أيها الذين آمنوا اتقوا الله ولتنظر نفس ما قدمت لغد ۖ واتقوا الله ۚ إن الله خبير بما تعملون'),
(5145, 59, 19, 'ولا تكونوا كالذين نسوا الله فأنساهم أنفسهم ۚ أولئك هم الفاسقون'),
(5146, 59, 20, 'لا يستوي أصحاب النار وأصحاب الجنة ۚ أصحاب الجنة هم الفائزون'),
(5147, 59, 21, 'لو أنزلنا هذا القرآن على جبل لرأيته خاشعا متصدعا من خشية الله ۚ وتلك الأمثال نضربها للناس لعلهم يتفكرون'),
(5148, 59, 22, 'هو الله الذي لا إله إلا هو ۖ عالم الغيب والشهادة ۖ هو الرحمن الرحيم'),
(5149, 59, 23, 'هو الله الذي لا إله إلا هو الملك القدوس السلام المؤمن المهيمن العزيز الجبار المتكبر ۚ سبحان الله عما يشركون'),
(5150, 59, 24, 'هو الله الخالق البارئ المصور ۖ له الأسماء الحسنى ۚ يسبح له ما في السماوات والأرض ۖ وهو العزيز الحكيم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'بسم الله الرحمن الرحيم يا أيها الذين آمنوا لا تتخذوا عدوي وعدوكم أولياء تلقون إليهم بالمودة وقد كفروا بما جاءكم من الحق يخرجون الرسول وإياكم ۙ أن تؤمنوا بالله ربكم إن كنتم خرجتم جهادا في سبيلي وابتغاء مرضاتي ۚ تسرون إليهم بالمودة وأنا أعلم بما أخفيتم وما أعلنتم ۚ ومن يفعله منكم فقد ضل سواء السبيل'),
(5152, 60, 2, 'إن يثقفوكم يكونوا لكم أعداء ويبسطوا إليكم أيديهم وألسنتهم بالسوء وودوا لو تكفرون'),
(5153, 60, 3, 'لن تنفعكم أرحامكم ولا أولادكم ۚ يوم القيامة يفصل بينكم ۚ والله بما تعملون بصير'),
(5154, 60, 4, 'قد كانت لكم أسوة حسنة في إبراهيم والذين معه إذ قالوا لقومهم إنا برآء منكم ومما تعبدون من دون الله كفرنا بكم وبدا بيننا وبينكم العداوة والبغضاء أبدا حتى تؤمنوا بالله وحده إلا قول إبراهيم لأبيه لأستغفرن لك وما أملك لك من الله من شيء ۖ ربنا عليك توكلنا وإليك أنبنا وإليك المصير'),
(5155, 60, 5, 'ربنا لا تجعلنا فتنة للذين كفروا واغفر لنا ربنا ۖ إنك أنت العزيز الحكيم'),
(5156, 60, 6, 'لقد كان لكم فيهم أسوة حسنة لمن كان يرجو الله واليوم الآخر ۚ ومن يتول فإن الله هو الغني الحميد'),
(5157, 60, 7, 'عسى الله أن يجعل بينكم وبين الذين عاديتم منهم مودة ۚ والله قدير ۚ والله غفور رحيم'),
(5158, 60, 8, 'لا ينهاكم الله عن الذين لم يقاتلوكم في الدين ولم يخرجوكم من دياركم أن تبروهم وتقسطوا إليهم ۚ إن الله يحب المقسطين'),
(5159, 60, 9, 'إنما ينهاكم الله عن الذين قاتلوكم في الدين وأخرجوكم من دياركم وظاهروا على إخراجكم أن تولوهم ۚ ومن يتولهم فأولئك هم الظالمون'),
(5160, 60, 10, 'يا أيها الذين آمنوا إذا جاءكم المؤمنات مهاجرات فامتحنوهن ۖ الله أعلم بإيمانهن ۖ فإن علمتموهن مؤمنات فلا ترجعوهن إلى الكفار ۖ لا هن حل لهم ولا هم يحلون لهن ۖ وآتوهم ما أنفقوا ۚ ولا جناح عليكم أن تنكحوهن إذا آتيتموهن أجورهن ۚ ولا تمسكوا بعصم الكوافر واسألوا ما أنفقتم وليسألوا ما أنفقوا ۚ ذلكم حكم الله ۖ يحكم بينكم ۚ والله عليم حكيم'),
(5161, 60, 11, 'وإن فاتكم شيء من أزواجكم إلى الكفار فعاقبتم فآتوا الذين ذهبت أزواجهم مثل ما أنفقوا ۚ واتقوا الله الذي أنتم به مؤمنون'),
(5162, 60, 12, 'يا أيها النبي إذا جاءك المؤمنات يبايعنك على أن لا يشركن بالله شيئا ولا يسرقن ولا يزنين ولا يقتلن أولادهن ولا يأتين ببهتان يفترينه بين أيديهن وأرجلهن ولا يعصينك في معروف ۙ فبايعهن واستغفر لهن الله ۖ إن الله غفور رحيم'),
(5163, 60, 13, 'يا أيها الذين آمنوا لا تتولوا قوما غضب الله عليهم قد يئسوا من الآخرة كما يئس الكفار من أصحاب القبور');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'بسم الله الرحمن الرحيم سبح لله ما في السماوات وما في الأرض ۖ وهو العزيز الحكيم'),
(5165, 61, 2, 'يا أيها الذين آمنوا لم تقولون ما لا تفعلون'),
(5166, 61, 3, 'كبر مقتا عند الله أن تقولوا ما لا تفعلون'),
(5167, 61, 4, 'إن الله يحب الذين يقاتلون في سبيله صفا كأنهم بنيان مرصوص'),
(5168, 61, 5, 'وإذ قال موسى لقومه يا قوم لم تؤذونني وقد تعلمون أني رسول الله إليكم ۖ فلما زاغوا أزاغ الله قلوبهم ۚ والله لا يهدي القوم الفاسقين'),
(5169, 61, 6, 'وإذ قال عيسى ابن مريم يا بني إسرائيل إني رسول الله إليكم مصدقا لما بين يدي من التوراة ومبشرا برسول يأتي من بعدي اسمه أحمد ۖ فلما جاءهم بالبينات قالوا هذا سحر مبين'),
(5170, 61, 7, 'ومن أظلم ممن افترى على الله الكذب وهو يدعى إلى الإسلام ۚ والله لا يهدي القوم الظالمين'),
(5171, 61, 8, 'يريدون ليطفئوا نور الله بأفواههم والله متم نوره ولو كره الكافرون'),
(5172, 61, 9, 'هو الذي أرسل رسوله بالهدى ودين الحق ليظهره على الدين كله ولو كره المشركون'),
(5173, 61, 10, 'يا أيها الذين آمنوا هل أدلكم على تجارة تنجيكم من عذاب أليم'),
(5174, 61, 11, 'تؤمنون بالله ورسوله وتجاهدون في سبيل الله بأموالكم وأنفسكم ۚ ذلكم خير لكم إن كنتم تعلمون'),
(5175, 61, 12, 'يغفر لكم ذنوبكم ويدخلكم جنات تجري من تحتها الأنهار ومساكن طيبة في جنات عدن ۚ ذلك الفوز العظيم'),
(5176, 61, 13, 'وأخرى تحبونها ۖ نصر من الله وفتح قريب ۗ وبشر المؤمنين'),
(5177, 61, 14, 'يا أيها الذين آمنوا كونوا أنصار الله كما قال عيسى ابن مريم للحواريين من أنصاري إلى الله ۖ قال الحواريون نحن أنصار الله ۖ فآمنت طائفة من بني إسرائيل وكفرت طائفة ۖ فأيدنا الذين آمنوا على عدوهم فأصبحوا ظاهرين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'بسم الله الرحمن الرحيم يسبح لله ما في السماوات وما في الأرض الملك القدوس العزيز الحكيم'),
(5179, 62, 2, 'هو الذي بعث في الأميين رسولا منهم يتلو عليهم آياته ويزكيهم ويعلمهم الكتاب والحكمة وإن كانوا من قبل لفي ضلال مبين'),
(5180, 62, 3, 'وآخرين منهم لما يلحقوا بهم ۚ وهو العزيز الحكيم'),
(5181, 62, 4, 'ذلك فضل الله يؤتيه من يشاء ۚ والله ذو الفضل العظيم'),
(5182, 62, 5, 'مثل الذين حملوا التوراة ثم لم يحملوها كمثل الحمار يحمل أسفارا ۚ بئس مثل القوم الذين كذبوا بآيات الله ۚ والله لا يهدي القوم الظالمين'),
(5183, 62, 6, 'قل يا أيها الذين هادوا إن زعمتم أنكم أولياء لله من دون الناس فتمنوا الموت إن كنتم صادقين'),
(5184, 62, 7, 'ولا يتمنونه أبدا بما قدمت أيديهم ۚ والله عليم بالظالمين'),
(5185, 62, 8, 'قل إن الموت الذي تفرون منه فإنه ملاقيكم ۖ ثم تردون إلى عالم الغيب والشهادة فينبئكم بما كنتم تعملون'),
(5186, 62, 9, 'يا أيها الذين آمنوا إذا نودي للصلاة من يوم الجمعة فاسعوا إلى ذكر الله وذروا البيع ۚ ذلكم خير لكم إن كنتم تعلمون'),
(5187, 62, 10, 'فإذا قضيت الصلاة فانتشروا في الأرض وابتغوا من فضل الله واذكروا الله كثيرا لعلكم تفلحون'),
(5188, 62, 11, 'وإذا رأوا تجارة أو لهوا انفضوا إليها وتركوك قائما ۚ قل ما عند الله خير من اللهو ومن التجارة ۚ والله خير الرازقين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'بسم الله الرحمن الرحيم إذا جاءك المنافقون قالوا نشهد إنك لرسول الله ۗ والله يعلم إنك لرسوله والله يشهد إن المنافقين لكاذبون'),
(5190, 63, 2, 'اتخذوا أيمانهم جنة فصدوا عن سبيل الله ۚ إنهم ساء ما كانوا يعملون'),
(5191, 63, 3, 'ذلك بأنهم آمنوا ثم كفروا فطبع على قلوبهم فهم لا يفقهون'),
(5192, 63, 4, 'وإذا رأيتهم تعجبك أجسامهم ۖ وإن يقولوا تسمع لقولهم ۖ كأنهم خشب مسندة ۖ يحسبون كل صيحة عليهم ۚ هم العدو فاحذرهم ۚ قاتلهم الله ۖ أنى يؤفكون'),
(5193, 63, 5, 'وإذا قيل لهم تعالوا يستغفر لكم رسول الله لووا رءوسهم ورأيتهم يصدون وهم مستكبرون'),
(5194, 63, 6, 'سواء عليهم أستغفرت لهم أم لم تستغفر لهم لن يغفر الله لهم ۚ إن الله لا يهدي القوم الفاسقين'),
(5195, 63, 7, 'هم الذين يقولون لا تنفقوا على من عند رسول الله حتى ينفضوا ۗ ولله خزائن السماوات والأرض ولكن المنافقين لا يفقهون'),
(5196, 63, 8, 'يقولون لئن رجعنا إلى المدينة ليخرجن الأعز منها الأذل ۚ ولله العزة ولرسوله وللمؤمنين ولكن المنافقين لا يعلمون'),
(5197, 63, 9, 'يا أيها الذين آمنوا لا تلهكم أموالكم ولا أولادكم عن ذكر الله ۚ ومن يفعل ذلك فأولئك هم الخاسرون'),
(5198, 63, 10, 'وأنفقوا من ما رزقناكم من قبل أن يأتي أحدكم الموت فيقول رب لولا أخرتني إلى أجل قريب فأصدق وأكن من الصالحين'),
(5199, 63, 11, 'ولن يؤخر الله نفسا إذا جاء أجلها ۚ والله خبير بما تعملون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'بسم الله الرحمن الرحيم يسبح لله ما في السماوات وما في الأرض ۖ له الملك وله الحمد ۖ وهو على كل شيء قدير'),
(5201, 64, 2, 'هو الذي خلقكم فمنكم كافر ومنكم مؤمن ۚ والله بما تعملون بصير'),
(5202, 64, 3, 'خلق السماوات والأرض بالحق وصوركم فأحسن صوركم ۖ وإليه المصير'),
(5203, 64, 4, 'يعلم ما في السماوات والأرض ويعلم ما تسرون وما تعلنون ۚ والله عليم بذات الصدور'),
(5204, 64, 5, 'ألم يأتكم نبأ الذين كفروا من قبل فذاقوا وبال أمرهم ولهم عذاب أليم'),
(5205, 64, 6, 'ذلك بأنه كانت تأتيهم رسلهم بالبينات فقالوا أبشر يهدوننا فكفروا وتولوا ۚ واستغنى الله ۚ والله غني حميد'),
(5206, 64, 7, 'زعم الذين كفروا أن لن يبعثوا ۚ قل بلى وربي لتبعثن ثم لتنبؤن بما عملتم ۚ وذلك على الله يسير'),
(5207, 64, 8, 'فآمنوا بالله ورسوله والنور الذي أنزلنا ۚ والله بما تعملون خبير'),
(5208, 64, 9, 'يوم يجمعكم ليوم الجمع ۖ ذلك يوم التغابن ۗ ومن يؤمن بالله ويعمل صالحا يكفر عنه سيئاته ويدخله جنات تجري من تحتها الأنهار خالدين فيها أبدا ۚ ذلك الفوز العظيم'),
(5209, 64, 10, 'والذين كفروا وكذبوا بآياتنا أولئك أصحاب النار خالدين فيها ۖ وبئس المصير'),
(5210, 64, 11, 'ما أصاب من مصيبة إلا بإذن الله ۗ ومن يؤمن بالله يهد قلبه ۚ والله بكل شيء عليم'),
(5211, 64, 12, 'وأطيعوا الله وأطيعوا الرسول ۚ فإن توليتم فإنما على رسولنا البلاغ المبين'),
(5212, 64, 13, 'الله لا إله إلا هو ۚ وعلى الله فليتوكل المؤمنون'),
(5213, 64, 14, 'يا أيها الذين آمنوا إن من أزواجكم وأولادكم عدوا لكم فاحذروهم ۚ وإن تعفوا وتصفحوا وتغفروا فإن الله غفور رحيم'),
(5214, 64, 15, 'إنما أموالكم وأولادكم فتنة ۚ والله عنده أجر عظيم'),
(5215, 64, 16, 'فاتقوا الله ما استطعتم واسمعوا وأطيعوا وأنفقوا خيرا لأنفسكم ۗ ومن يوق شح نفسه فأولئك هم المفلحون'),
(5216, 64, 17, 'إن تقرضوا الله قرضا حسنا يضاعفه لكم ويغفر لكم ۚ والله شكور حليم'),
(5217, 64, 18, 'عالم الغيب والشهادة العزيز الحكيم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'بسم الله الرحمن الرحيم يا أيها النبي إذا طلقتم النساء فطلقوهن لعدتهن وأحصوا العدة ۖ واتقوا الله ربكم ۖ لا تخرجوهن من بيوتهن ولا يخرجن إلا أن يأتين بفاحشة مبينة ۚ وتلك حدود الله ۚ ومن يتعد حدود الله فقد ظلم نفسه ۚ لا تدري لعل الله يحدث بعد ذلك أمرا'),
(5219, 65, 2, 'فإذا بلغن أجلهن فأمسكوهن بمعروف أو فارقوهن بمعروف وأشهدوا ذوي عدل منكم وأقيموا الشهادة لله ۚ ذلكم يوعظ به من كان يؤمن بالله واليوم الآخر ۚ ومن يتق الله يجعل له مخرجا'),
(5220, 65, 3, 'ويرزقه من حيث لا يحتسب ۚ ومن يتوكل على الله فهو حسبه ۚ إن الله بالغ أمره ۚ قد جعل الله لكل شيء قدرا'),
(5221, 65, 4, 'واللائي يئسن من المحيض من نسائكم إن ارتبتم فعدتهن ثلاثة أشهر واللائي لم يحضن ۚ وأولات الأحمال أجلهن أن يضعن حملهن ۚ ومن يتق الله يجعل له من أمره يسرا'),
(5222, 65, 5, 'ذلك أمر الله أنزله إليكم ۚ ومن يتق الله يكفر عنه سيئاته ويعظم له أجرا'),
(5223, 65, 6, 'أسكنوهن من حيث سكنتم من وجدكم ولا تضاروهن لتضيقوا عليهن ۚ وإن كن أولات حمل فأنفقوا عليهن حتى يضعن حملهن ۚ فإن أرضعن لكم فآتوهن أجورهن ۖ وأتمروا بينكم بمعروف ۖ وإن تعاسرتم فسترضع له أخرى'),
(5224, 65, 7, 'لينفق ذو سعة من سعته ۖ ومن قدر عليه رزقه فلينفق مما آتاه الله ۚ لا يكلف الله نفسا إلا ما آتاها ۚ سيجعل الله بعد عسر يسرا'),
(5225, 65, 8, 'وكأين من قرية عتت عن أمر ربها ورسله فحاسبناها حسابا شديدا وعذبناها عذابا نكرا'),
(5226, 65, 9, 'فذاقت وبال أمرها وكان عاقبة أمرها خسرا'),
(5227, 65, 10, 'أعد الله لهم عذابا شديدا ۖ فاتقوا الله يا أولي الألباب الذين آمنوا ۚ قد أنزل الله إليكم ذكرا'),
(5228, 65, 11, 'رسولا يتلو عليكم آيات الله مبينات ليخرج الذين آمنوا وعملوا الصالحات من الظلمات إلى النور ۚ ومن يؤمن بالله ويعمل صالحا يدخله جنات تجري من تحتها الأنهار خالدين فيها أبدا ۖ قد أحسن الله له رزقا'),
(5229, 65, 12, 'الله الذي خلق سبع سماوات ومن الأرض مثلهن يتنزل الأمر بينهن لتعلموا أن الله على كل شيء قدير وأن الله قد أحاط بكل شيء علما');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'بسم الله الرحمن الرحيم يا أيها النبي لم تحرم ما أحل الله لك ۖ تبتغي مرضات أزواجك ۚ والله غفور رحيم'),
(5231, 66, 2, 'قد فرض الله لكم تحلة أيمانكم ۚ والله مولاكم ۖ وهو العليم الحكيم'),
(5232, 66, 3, 'وإذ أسر النبي إلى بعض أزواجه حديثا فلما نبأت به وأظهره الله عليه عرف بعضه وأعرض عن بعض ۖ فلما نبأها به قالت من أنبأك هذا ۖ قال نبأني العليم الخبير'),
(5233, 66, 4, 'إن تتوبا إلى الله فقد صغت قلوبكما ۖ وإن تظاهرا عليه فإن الله هو مولاه وجبريل وصالح المؤمنين ۖ والملائكة بعد ذلك ظهير'),
(5234, 66, 5, 'عسى ربه إن طلقكن أن يبدله أزواجا خيرا منكن مسلمات مؤمنات قانتات تائبات عابدات سائحات ثيبات وأبكارا'),
(5235, 66, 6, 'يا أيها الذين آمنوا قوا أنفسكم وأهليكم نارا وقودها الناس والحجارة عليها ملائكة غلاظ شداد لا يعصون الله ما أمرهم ويفعلون ما يؤمرون'),
(5236, 66, 7, 'يا أيها الذين كفروا لا تعتذروا اليوم ۖ إنما تجزون ما كنتم تعملون'),
(5237, 66, 8, 'يا أيها الذين آمنوا توبوا إلى الله توبة نصوحا عسى ربكم أن يكفر عنكم سيئاتكم ويدخلكم جنات تجري من تحتها الأنهار يوم لا يخزي الله النبي والذين آمنوا معه ۖ نورهم يسعى بين أيديهم وبأيمانهم يقولون ربنا أتمم لنا نورنا واغفر لنا ۖ إنك على كل شيء قدير'),
(5238, 66, 9, 'يا أيها النبي جاهد الكفار والمنافقين واغلظ عليهم ۚ ومأواهم جهنم ۖ وبئس المصير'),
(5239, 66, 10, 'ضرب الله مثلا للذين كفروا امرأت نوح وامرأت لوط ۖ كانتا تحت عبدين من عبادنا صالحين فخانتاهما فلم يغنيا عنهما من الله شيئا وقيل ادخلا النار مع الداخلين'),
(5240, 66, 11, 'وضرب الله مثلا للذين آمنوا امرأت فرعون إذ قالت رب ابن لي عندك بيتا في الجنة ونجني من فرعون وعمله ونجني من القوم الظالمين'),
(5241, 66, 12, 'ومريم ابنت عمران التي أحصنت فرجها فنفخنا فيه من روحنا وصدقت بكلمات ربها وكتبه وكانت من القانتين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'بسم الله الرحمن الرحيم تبارك الذي بيده الملك وهو على كل شيء قدير'),
(5243, 67, 2, 'الذي خلق الموت والحياة ليبلوكم أيكم أحسن عملا ۚ وهو العزيز الغفور'),
(5244, 67, 3, 'الذي خلق سبع سماوات طباقا ۖ ما ترى في خلق الرحمن من تفاوت ۖ فارجع البصر هل ترى من فطور'),
(5245, 67, 4, 'ثم ارجع البصر كرتين ينقلب إليك البصر خاسئا وهو حسير'),
(5246, 67, 5, 'ولقد زينا السماء الدنيا بمصابيح وجعلناها رجوما للشياطين ۖ وأعتدنا لهم عذاب السعير'),
(5247, 67, 6, 'وللذين كفروا بربهم عذاب جهنم ۖ وبئس المصير'),
(5248, 67, 7, 'إذا ألقوا فيها سمعوا لها شهيقا وهي تفور'),
(5249, 67, 8, 'تكاد تميز من الغيظ ۖ كلما ألقي فيها فوج سألهم خزنتها ألم يأتكم نذير'),
(5250, 67, 9, 'قالوا بلى قد جاءنا نذير فكذبنا وقلنا ما نزل الله من شيء إن أنتم إلا في ضلال كبير'),
(5251, 67, 10, 'وقالوا لو كنا نسمع أو نعقل ما كنا في أصحاب السعير'),
(5252, 67, 11, 'فاعترفوا بذنبهم فسحقا لأصحاب السعير'),
(5253, 67, 12, 'إن الذين يخشون ربهم بالغيب لهم مغفرة وأجر كبير'),
(5254, 67, 13, 'وأسروا قولكم أو اجهروا به ۖ إنه عليم بذات الصدور'),
(5255, 67, 14, 'ألا يعلم من خلق وهو اللطيف الخبير'),
(5256, 67, 15, 'هو الذي جعل لكم الأرض ذلولا فامشوا في مناكبها وكلوا من رزقه ۖ وإليه النشور'),
(5257, 67, 16, 'أأمنتم من في السماء أن يخسف بكم الأرض فإذا هي تمور'),
(5258, 67, 17, 'أم أمنتم من في السماء أن يرسل عليكم حاصبا ۖ فستعلمون كيف نذير'),
(5259, 67, 18, 'ولقد كذب الذين من قبلهم فكيف كان نكير'),
(5260, 67, 19, 'أولم يروا إلى الطير فوقهم صافات ويقبضن ۚ ما يمسكهن إلا الرحمن ۚ إنه بكل شيء بصير'),
(5261, 67, 20, 'أمن هذا الذي هو جند لكم ينصركم من دون الرحمن ۚ إن الكافرون إلا في غرور'),
(5262, 67, 21, 'أمن هذا الذي يرزقكم إن أمسك رزقه ۚ بل لجوا في عتو ونفور'),
(5263, 67, 22, 'أفمن يمشي مكبا على وجهه أهدى أمن يمشي سويا على صراط مستقيم'),
(5264, 67, 23, 'قل هو الذي أنشأكم وجعل لكم السمع والأبصار والأفئدة ۖ قليلا ما تشكرون'),
(5265, 67, 24, 'قل هو الذي ذرأكم في الأرض وإليه تحشرون'),
(5266, 67, 25, 'ويقولون متى هذا الوعد إن كنتم صادقين'),
(5267, 67, 26, 'قل إنما العلم عند الله وإنما أنا نذير مبين'),
(5268, 67, 27, 'فلما رأوه زلفة سيئت وجوه الذين كفروا وقيل هذا الذي كنتم به تدعون'),
(5269, 67, 28, 'قل أرأيتم إن أهلكني الله ومن معي أو رحمنا فمن يجير الكافرين من عذاب أليم'),
(5270, 67, 29, 'قل هو الرحمن آمنا به وعليه توكلنا ۖ فستعلمون من هو في ضلال مبين'),
(5271, 67, 30, 'قل أرأيتم إن أصبح ماؤكم غورا فمن يأتيكم بماء معين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'بسم الله الرحمن الرحيم ن ۚ والقلم وما يسطرون'),
(5273, 68, 2, 'ما أنت بنعمة ربك بمجنون'),
(5274, 68, 3, 'وإن لك لأجرا غير ممنون'),
(5275, 68, 4, 'وإنك لعلى خلق عظيم'),
(5276, 68, 5, 'فستبصر ويبصرون'),
(5277, 68, 6, 'بأييكم المفتون'),
(5278, 68, 7, 'إن ربك هو أعلم بمن ضل عن سبيله وهو أعلم بالمهتدين'),
(5279, 68, 8, 'فلا تطع المكذبين'),
(5280, 68, 9, 'ودوا لو تدهن فيدهنون'),
(5281, 68, 10, 'ولا تطع كل حلاف مهين'),
(5282, 68, 11, 'هماز مشاء بنميم'),
(5283, 68, 12, 'مناع للخير معتد أثيم'),
(5284, 68, 13, 'عتل بعد ذلك زنيم'),
(5285, 68, 14, 'أن كان ذا مال وبنين'),
(5286, 68, 15, 'إذا تتلى عليه آياتنا قال أساطير الأولين'),
(5287, 68, 16, 'سنسمه على الخرطوم'),
(5288, 68, 17, 'إنا بلوناهم كما بلونا أصحاب الجنة إذ أقسموا ليصرمنها مصبحين'),
(5289, 68, 18, 'ولا يستثنون'),
(5290, 68, 19, 'فطاف عليها طائف من ربك وهم نائمون'),
(5291, 68, 20, 'فأصبحت كالصريم'),
(5292, 68, 21, 'فتنادوا مصبحين'),
(5293, 68, 22, 'أن اغدوا على حرثكم إن كنتم صارمين'),
(5294, 68, 23, 'فانطلقوا وهم يتخافتون'),
(5295, 68, 24, 'أن لا يدخلنها اليوم عليكم مسكين'),
(5296, 68, 25, 'وغدوا على حرد قادرين'),
(5297, 68, 26, 'فلما رأوها قالوا إنا لضالون'),
(5298, 68, 27, 'بل نحن محرومون'),
(5299, 68, 28, 'قال أوسطهم ألم أقل لكم لولا تسبحون'),
(5300, 68, 29, 'قالوا سبحان ربنا إنا كنا ظالمين'),
(5301, 68, 30, 'فأقبل بعضهم على بعض يتلاومون'),
(5302, 68, 31, 'قالوا يا ويلنا إنا كنا طاغين'),
(5303, 68, 32, 'عسى ربنا أن يبدلنا خيرا منها إنا إلى ربنا راغبون'),
(5304, 68, 33, 'كذلك العذاب ۖ ولعذاب الآخرة أكبر ۚ لو كانوا يعلمون'),
(5305, 68, 34, 'إن للمتقين عند ربهم جنات النعيم'),
(5306, 68, 35, 'أفنجعل المسلمين كالمجرمين'),
(5307, 68, 36, 'ما لكم كيف تحكمون'),
(5308, 68, 37, 'أم لكم كتاب فيه تدرسون'),
(5309, 68, 38, 'إن لكم فيه لما تخيرون'),
(5310, 68, 39, 'أم لكم أيمان علينا بالغة إلى يوم القيامة ۙ إن لكم لما تحكمون'),
(5311, 68, 40, 'سلهم أيهم بذلك زعيم'),
(5312, 68, 41, 'أم لهم شركاء فليأتوا بشركائهم إن كانوا صادقين'),
(5313, 68, 42, 'يوم يكشف عن ساق ويدعون إلى السجود فلا يستطيعون'),
(5314, 68, 43, 'خاشعة أبصارهم ترهقهم ذلة ۖ وقد كانوا يدعون إلى السجود وهم سالمون'),
(5315, 68, 44, 'فذرني ومن يكذب بهذا الحديث ۖ سنستدرجهم من حيث لا يعلمون'),
(5316, 68, 45, 'وأملي لهم ۚ إن كيدي متين'),
(5317, 68, 46, 'أم تسألهم أجرا فهم من مغرم مثقلون'),
(5318, 68, 47, 'أم عندهم الغيب فهم يكتبون'),
(5319, 68, 48, 'فاصبر لحكم ربك ولا تكن كصاحب الحوت إذ نادى وهو مكظوم'),
(5320, 68, 49, 'لولا أن تداركه نعمة من ربه لنبذ بالعراء وهو مذموم'),
(5321, 68, 50, 'فاجتباه ربه فجعله من الصالحين'),
(5322, 68, 51, 'وإن يكاد الذين كفروا ليزلقونك بأبصارهم لما سمعوا الذكر ويقولون إنه لمجنون'),
(5323, 68, 52, 'وما هو إلا ذكر للعالمين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'بسم الله الرحمن الرحيم الحاقة'),
(5325, 69, 2, 'ما الحاقة'),
(5326, 69, 3, 'وما أدراك ما الحاقة'),
(5327, 69, 4, 'كذبت ثمود وعاد بالقارعة'),
(5328, 69, 5, 'فأما ثمود فأهلكوا بالطاغية'),
(5329, 69, 6, 'وأما عاد فأهلكوا بريح صرصر عاتية'),
(5330, 69, 7, 'سخرها عليهم سبع ليال وثمانية أيام حسوما فترى القوم فيها صرعى كأنهم أعجاز نخل خاوية'),
(5331, 69, 8, 'فهل ترى لهم من باقية'),
(5332, 69, 9, 'وجاء فرعون ومن قبله والمؤتفكات بالخاطئة'),
(5333, 69, 10, 'فعصوا رسول ربهم فأخذهم أخذة رابية'),
(5334, 69, 11, 'إنا لما طغى الماء حملناكم في الجارية'),
(5335, 69, 12, 'لنجعلها لكم تذكرة وتعيها أذن واعية'),
(5336, 69, 13, 'فإذا نفخ في الصور نفخة واحدة'),
(5337, 69, 14, 'وحملت الأرض والجبال فدكتا دكة واحدة'),
(5338, 69, 15, 'فيومئذ وقعت الواقعة'),
(5339, 69, 16, 'وانشقت السماء فهي يومئذ واهية'),
(5340, 69, 17, 'والملك على أرجائها ۚ ويحمل عرش ربك فوقهم يومئذ ثمانية'),
(5341, 69, 18, 'يومئذ تعرضون لا تخفى منكم خافية'),
(5342, 69, 19, 'فأما من أوتي كتابه بيمينه فيقول هاؤم اقرءوا كتابيه'),
(5343, 69, 20, 'إني ظننت أني ملاق حسابيه'),
(5344, 69, 21, 'فهو في عيشة راضية'),
(5345, 69, 22, 'في جنة عالية'),
(5346, 69, 23, 'قطوفها دانية'),
(5347, 69, 24, 'كلوا واشربوا هنيئا بما أسلفتم في الأيام الخالية'),
(5348, 69, 25, 'وأما من أوتي كتابه بشماله فيقول يا ليتني لم أوت كتابيه'),
(5349, 69, 26, 'ولم أدر ما حسابيه'),
(5350, 69, 27, 'يا ليتها كانت القاضية'),
(5351, 69, 28, 'ما أغنى عني ماليه ۜ'),
(5352, 69, 29, 'هلك عني سلطانيه'),
(5353, 69, 30, 'خذوه فغلوه'),
(5354, 69, 31, 'ثم الجحيم صلوه'),
(5355, 69, 32, 'ثم في سلسلة ذرعها سبعون ذراعا فاسلكوه'),
(5356, 69, 33, 'إنه كان لا يؤمن بالله العظيم'),
(5357, 69, 34, 'ولا يحض على طعام المسكين'),
(5358, 69, 35, 'فليس له اليوم هاهنا حميم'),
(5359, 69, 36, 'ولا طعام إلا من غسلين'),
(5360, 69, 37, 'لا يأكله إلا الخاطئون'),
(5361, 69, 38, 'فلا أقسم بما تبصرون'),
(5362, 69, 39, 'وما لا تبصرون'),
(5363, 69, 40, 'إنه لقول رسول كريم'),
(5364, 69, 41, 'وما هو بقول شاعر ۚ قليلا ما تؤمنون'),
(5365, 69, 42, 'ولا بقول كاهن ۚ قليلا ما تذكرون'),
(5366, 69, 43, 'تنزيل من رب العالمين'),
(5367, 69, 44, 'ولو تقول علينا بعض الأقاويل'),
(5368, 69, 45, 'لأخذنا منه باليمين'),
(5369, 69, 46, 'ثم لقطعنا منه الوتين'),
(5370, 69, 47, 'فما منكم من أحد عنه حاجزين'),
(5371, 69, 48, 'وإنه لتذكرة للمتقين'),
(5372, 69, 49, 'وإنا لنعلم أن منكم مكذبين'),
(5373, 69, 50, 'وإنه لحسرة على الكافرين'),
(5374, 69, 51, 'وإنه لحق اليقين'),
(5375, 69, 52, 'فسبح باسم ربك العظيم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'بسم الله الرحمن الرحيم سأل سائل بعذاب واقع'),
(5377, 70, 2, 'للكافرين ليس له دافع'),
(5378, 70, 3, 'من الله ذي المعارج'),
(5379, 70, 4, 'تعرج الملائكة والروح إليه في يوم كان مقداره خمسين ألف سنة'),
(5380, 70, 5, 'فاصبر صبرا جميلا'),
(5381, 70, 6, 'إنهم يرونه بعيدا'),
(5382, 70, 7, 'ونراه قريبا'),
(5383, 70, 8, 'يوم تكون السماء كالمهل'),
(5384, 70, 9, 'وتكون الجبال كالعهن'),
(5385, 70, 10, 'ولا يسأل حميم حميما'),
(5386, 70, 11, 'يبصرونهم ۚ يود المجرم لو يفتدي من عذاب يومئذ ببنيه'),
(5387, 70, 12, 'وصاحبته وأخيه'),
(5388, 70, 13, 'وفصيلته التي تؤويه'),
(5389, 70, 14, 'ومن في الأرض جميعا ثم ينجيه'),
(5390, 70, 15, 'كلا ۖ إنها لظى'),
(5391, 70, 16, 'نزاعة للشوى'),
(5392, 70, 17, 'تدعو من أدبر وتولى'),
(5393, 70, 18, 'وجمع فأوعى'),
(5394, 70, 19, 'إن الإنسان خلق هلوعا'),
(5395, 70, 20, 'إذا مسه الشر جزوعا'),
(5396, 70, 21, 'وإذا مسه الخير منوعا'),
(5397, 70, 22, 'إلا المصلين'),
(5398, 70, 23, 'الذين هم على صلاتهم دائمون'),
(5399, 70, 24, 'والذين في أموالهم حق معلوم'),
(5400, 70, 25, 'للسائل والمحروم'),
(5401, 70, 26, 'والذين يصدقون بيوم الدين'),
(5402, 70, 27, 'والذين هم من عذاب ربهم مشفقون'),
(5403, 70, 28, 'إن عذاب ربهم غير مأمون'),
(5404, 70, 29, 'والذين هم لفروجهم حافظون'),
(5405, 70, 30, 'إلا على أزواجهم أو ما ملكت أيمانهم فإنهم غير ملومين'),
(5406, 70, 31, 'فمن ابتغى وراء ذلك فأولئك هم العادون'),
(5407, 70, 32, 'والذين هم لأماناتهم وعهدهم راعون'),
(5408, 70, 33, 'والذين هم بشهاداتهم قائمون'),
(5409, 70, 34, 'والذين هم على صلاتهم يحافظون'),
(5410, 70, 35, 'أولئك في جنات مكرمون'),
(5411, 70, 36, 'فمال الذين كفروا قبلك مهطعين'),
(5412, 70, 37, 'عن اليمين وعن الشمال عزين'),
(5413, 70, 38, 'أيطمع كل امرئ منهم أن يدخل جنة نعيم'),
(5414, 70, 39, 'كلا ۖ إنا خلقناهم مما يعلمون'),
(5415, 70, 40, 'فلا أقسم برب المشارق والمغارب إنا لقادرون'),
(5416, 70, 41, 'على أن نبدل خيرا منهم وما نحن بمسبوقين'),
(5417, 70, 42, 'فذرهم يخوضوا ويلعبوا حتى يلاقوا يومهم الذي يوعدون'),
(5418, 70, 43, 'يوم يخرجون من الأجداث سراعا كأنهم إلى نصب يوفضون'),
(5419, 70, 44, 'خاشعة أبصارهم ترهقهم ذلة ۚ ذلك اليوم الذي كانوا يوعدون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'بسم الله الرحمن الرحيم إنا أرسلنا نوحا إلى قومه أن أنذر قومك من قبل أن يأتيهم عذاب أليم'),
(5421, 71, 2, 'قال يا قوم إني لكم نذير مبين'),
(5422, 71, 3, 'أن اعبدوا الله واتقوه وأطيعون'),
(5423, 71, 4, 'يغفر لكم من ذنوبكم ويؤخركم إلى أجل مسمى ۚ إن أجل الله إذا جاء لا يؤخر ۖ لو كنتم تعلمون'),
(5424, 71, 5, 'قال رب إني دعوت قومي ليلا ونهارا'),
(5425, 71, 6, 'فلم يزدهم دعائي إلا فرارا'),
(5426, 71, 7, 'وإني كلما دعوتهم لتغفر لهم جعلوا أصابعهم في آذانهم واستغشوا ثيابهم وأصروا واستكبروا استكبارا'),
(5427, 71, 8, 'ثم إني دعوتهم جهارا'),
(5428, 71, 9, 'ثم إني أعلنت لهم وأسررت لهم إسرارا'),
(5429, 71, 10, 'فقلت استغفروا ربكم إنه كان غفارا'),
(5430, 71, 11, 'يرسل السماء عليكم مدرارا'),
(5431, 71, 12, 'ويمددكم بأموال وبنين ويجعل لكم جنات ويجعل لكم أنهارا'),
(5432, 71, 13, 'ما لكم لا ترجون لله وقارا'),
(5433, 71, 14, 'وقد خلقكم أطوارا'),
(5434, 71, 15, 'ألم تروا كيف خلق الله سبع سماوات طباقا'),
(5435, 71, 16, 'وجعل القمر فيهن نورا وجعل الشمس سراجا'),
(5436, 71, 17, 'والله أنبتكم من الأرض نباتا'),
(5437, 71, 18, 'ثم يعيدكم فيها ويخرجكم إخراجا'),
(5438, 71, 19, 'والله جعل لكم الأرض بساطا'),
(5439, 71, 20, 'لتسلكوا منها سبلا فجاجا'),
(5440, 71, 21, 'قال نوح رب إنهم عصوني واتبعوا من لم يزده ماله وولده إلا خسارا'),
(5441, 71, 22, 'ومكروا مكرا كبارا'),
(5442, 71, 23, 'وقالوا لا تذرن آلهتكم ولا تذرن ودا ولا سواعا ولا يغوث ويعوق ونسرا'),
(5443, 71, 24, 'وقد أضلوا كثيرا ۖ ولا تزد الظالمين إلا ضلالا'),
(5444, 71, 25, 'مما خطيئاتهم أغرقوا فأدخلوا نارا فلم يجدوا لهم من دون الله أنصارا'),
(5445, 71, 26, 'وقال نوح رب لا تذر على الأرض من الكافرين ديارا'),
(5446, 71, 27, 'إنك إن تذرهم يضلوا عبادك ولا يلدوا إلا فاجرا كفارا'),
(5447, 71, 28, 'رب اغفر لي ولوالدي ولمن دخل بيتي مؤمنا وللمؤمنين والمؤمنات ولا تزد الظالمين إلا تبارا');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'بسم الله الرحمن الرحيم قل أوحي إلي أنه استمع نفر من الجن فقالوا إنا سمعنا قرآنا عجبا'),
(5449, 72, 2, 'يهدي إلى الرشد فآمنا به ۖ ولن نشرك بربنا أحدا'),
(5450, 72, 3, 'وأنه تعالى جد ربنا ما اتخذ صاحبة ولا ولدا'),
(5451, 72, 4, 'وأنه كان يقول سفيهنا على الله شططا'),
(5452, 72, 5, 'وأنا ظننا أن لن تقول الإنس والجن على الله كذبا'),
(5453, 72, 6, 'وأنه كان رجال من الإنس يعوذون برجال من الجن فزادوهم رهقا'),
(5454, 72, 7, 'وأنهم ظنوا كما ظننتم أن لن يبعث الله أحدا'),
(5455, 72, 8, 'وأنا لمسنا السماء فوجدناها ملئت حرسا شديدا وشهبا'),
(5456, 72, 9, 'وأنا كنا نقعد منها مقاعد للسمع ۖ فمن يستمع الآن يجد له شهابا رصدا'),
(5457, 72, 10, 'وأنا لا ندري أشر أريد بمن في الأرض أم أراد بهم ربهم رشدا'),
(5458, 72, 11, 'وأنا منا الصالحون ومنا دون ذلك ۖ كنا طرائق قددا'),
(5459, 72, 12, 'وأنا ظننا أن لن نعجز الله في الأرض ولن نعجزه هربا'),
(5460, 72, 13, 'وأنا لما سمعنا الهدى آمنا به ۖ فمن يؤمن بربه فلا يخاف بخسا ولا رهقا'),
(5461, 72, 14, 'وأنا منا المسلمون ومنا القاسطون ۖ فمن أسلم فأولئك تحروا رشدا'),
(5462, 72, 15, 'وأما القاسطون فكانوا لجهنم حطبا'),
(5463, 72, 16, 'وأن لو استقاموا على الطريقة لأسقيناهم ماء غدقا'),
(5464, 72, 17, 'لنفتنهم فيه ۚ ومن يعرض عن ذكر ربه يسلكه عذابا صعدا'),
(5465, 72, 18, 'وأن المساجد لله فلا تدعوا مع الله أحدا'),
(5466, 72, 19, 'وأنه لما قام عبد الله يدعوه كادوا يكونون عليه لبدا'),
(5467, 72, 20, 'قل إنما أدعو ربي ولا أشرك به أحدا'),
(5468, 72, 21, 'قل إني لا أملك لكم ضرا ولا رشدا'),
(5469, 72, 22, 'قل إني لن يجيرني من الله أحد ولن أجد من دونه ملتحدا'),
(5470, 72, 23, 'إلا بلاغا من الله ورسالاته ۚ ومن يعص الله ورسوله فإن له نار جهنم خالدين فيها أبدا'),
(5471, 72, 24, 'حتى إذا رأوا ما يوعدون فسيعلمون من أضعف ناصرا وأقل عددا'),
(5472, 72, 25, 'قل إن أدري أقريب ما توعدون أم يجعل له ربي أمدا'),
(5473, 72, 26, 'عالم الغيب فلا يظهر على غيبه أحدا'),
(5474, 72, 27, 'إلا من ارتضى من رسول فإنه يسلك من بين يديه ومن خلفه رصدا'),
(5475, 72, 28, 'ليعلم أن قد أبلغوا رسالات ربهم وأحاط بما لديهم وأحصى كل شيء عددا');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'بسم الله الرحمن الرحيم يا أيها المزمل'),
(5477, 73, 2, 'قم الليل إلا قليلا'),
(5478, 73, 3, 'نصفه أو انقص منه قليلا'),
(5479, 73, 4, 'أو زد عليه ورتل القرآن ترتيلا'),
(5480, 73, 5, 'إنا سنلقي عليك قولا ثقيلا'),
(5481, 73, 6, 'إن ناشئة الليل هي أشد وطئا وأقوم قيلا'),
(5482, 73, 7, 'إن لك في النهار سبحا طويلا'),
(5483, 73, 8, 'واذكر اسم ربك وتبتل إليه تبتيلا'),
(5484, 73, 9, 'رب المشرق والمغرب لا إله إلا هو فاتخذه وكيلا'),
(5485, 73, 10, 'واصبر على ما يقولون واهجرهم هجرا جميلا'),
(5486, 73, 11, 'وذرني والمكذبين أولي النعمة ومهلهم قليلا'),
(5487, 73, 12, 'إن لدينا أنكالا وجحيما'),
(5488, 73, 13, 'وطعاما ذا غصة وعذابا أليما'),
(5489, 73, 14, 'يوم ترجف الأرض والجبال وكانت الجبال كثيبا مهيلا'),
(5490, 73, 15, 'إنا أرسلنا إليكم رسولا شاهدا عليكم كما أرسلنا إلى فرعون رسولا'),
(5491, 73, 16, 'فعصى فرعون الرسول فأخذناه أخذا وبيلا'),
(5492, 73, 17, 'فكيف تتقون إن كفرتم يوما يجعل الولدان شيبا'),
(5493, 73, 18, 'السماء منفطر به ۚ كان وعده مفعولا'),
(5494, 73, 19, 'إن هذه تذكرة ۖ فمن شاء اتخذ إلى ربه سبيلا'),
(5495, 73, 20, 'إن ربك يعلم أنك تقوم أدنى من ثلثي الليل ونصفه وثلثه وطائفة من الذين معك ۚ والله يقدر الليل والنهار ۚ علم أن لن تحصوه فتاب عليكم ۖ فاقرءوا ما تيسر من القرآن ۚ علم أن سيكون منكم مرضى ۙ وآخرون يضربون في الأرض يبتغون من فضل الله ۙ وآخرون يقاتلون في سبيل الله ۖ فاقرءوا ما تيسر منه ۚ وأقيموا الصلاة وآتوا الزكاة وأقرضوا الله قرضا حسنا ۚ وما تقدموا لأنفسكم من خير تجدوه عند الله هو خيرا وأعظم أجرا ۚ واستغفروا الله ۖ إن الله غفور رحيم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'بسم الله الرحمن الرحيم يا أيها المدثر'),
(5497, 74, 2, 'قم فأنذر'),
(5498, 74, 3, 'وربك فكبر'),
(5499, 74, 4, 'وثيابك فطهر'),
(5500, 74, 5, 'والرجز فاهجر'),
(5501, 74, 6, 'ولا تمنن تستكثر'),
(5502, 74, 7, 'ولربك فاصبر'),
(5503, 74, 8, 'فإذا نقر في الناقور'),
(5504, 74, 9, 'فذلك يومئذ يوم عسير'),
(5505, 74, 10, 'على الكافرين غير يسير'),
(5506, 74, 11, 'ذرني ومن خلقت وحيدا'),
(5507, 74, 12, 'وجعلت له مالا ممدودا'),
(5508, 74, 13, 'وبنين شهودا'),
(5509, 74, 14, 'ومهدت له تمهيدا'),
(5510, 74, 15, 'ثم يطمع أن أزيد'),
(5511, 74, 16, 'كلا ۖ إنه كان لآياتنا عنيدا'),
(5512, 74, 17, 'سأرهقه صعودا'),
(5513, 74, 18, 'إنه فكر وقدر'),
(5514, 74, 19, 'فقتل كيف قدر'),
(5515, 74, 20, 'ثم قتل كيف قدر'),
(5516, 74, 21, 'ثم نظر'),
(5517, 74, 22, 'ثم عبس وبسر'),
(5518, 74, 23, 'ثم أدبر واستكبر'),
(5519, 74, 24, 'فقال إن هذا إلا سحر يؤثر'),
(5520, 74, 25, 'إن هذا إلا قول البشر'),
(5521, 74, 26, 'سأصليه سقر'),
(5522, 74, 27, 'وما أدراك ما سقر'),
(5523, 74, 28, 'لا تبقي ولا تذر'),
(5524, 74, 29, 'لواحة للبشر'),
(5525, 74, 30, 'عليها تسعة عشر'),
(5526, 74, 31, 'وما جعلنا أصحاب النار إلا ملائكة ۙ وما جعلنا عدتهم إلا فتنة للذين كفروا ليستيقن الذين أوتوا الكتاب ويزداد الذين آمنوا إيمانا ۙ ولا يرتاب الذين أوتوا الكتاب والمؤمنون ۙ وليقول الذين في قلوبهم مرض والكافرون ماذا أراد الله بهذا مثلا ۚ كذلك يضل الله من يشاء ويهدي من يشاء ۚ وما يعلم جنود ربك إلا هو ۚ وما هي إلا ذكرى للبشر'),
(5527, 74, 32, 'كلا والقمر'),
(5528, 74, 33, 'والليل إذ أدبر'),
(5529, 74, 34, 'والصبح إذا أسفر'),
(5530, 74, 35, 'إنها لإحدى الكبر'),
(5531, 74, 36, 'نذيرا للبشر'),
(5532, 74, 37, 'لمن شاء منكم أن يتقدم أو يتأخر'),
(5533, 74, 38, 'كل نفس بما كسبت رهينة'),
(5534, 74, 39, 'إلا أصحاب اليمين'),
(5535, 74, 40, 'في جنات يتساءلون'),
(5536, 74, 41, 'عن المجرمين'),
(5537, 74, 42, 'ما سلككم في سقر'),
(5538, 74, 43, 'قالوا لم نك من المصلين'),
(5539, 74, 44, 'ولم نك نطعم المسكين'),
(5540, 74, 45, 'وكنا نخوض مع الخائضين'),
(5541, 74, 46, 'وكنا نكذب بيوم الدين'),
(5542, 74, 47, 'حتى أتانا اليقين'),
(5543, 74, 48, 'فما تنفعهم شفاعة الشافعين'),
(5544, 74, 49, 'فما لهم عن التذكرة معرضين'),
(5545, 74, 50, 'كأنهم حمر مستنفرة'),
(5546, 74, 51, 'فرت من قسورة'),
(5547, 74, 52, 'بل يريد كل امرئ منهم أن يؤتى صحفا منشرة'),
(5548, 74, 53, 'كلا ۖ بل لا يخافون الآخرة'),
(5549, 74, 54, 'كلا إنه تذكرة'),
(5550, 74, 55, 'فمن شاء ذكره'),
(5551, 74, 56, 'وما يذكرون إلا أن يشاء الله ۚ هو أهل التقوى وأهل المغفرة');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'بسم الله الرحمن الرحيم لا أقسم بيوم القيامة'),
(5553, 75, 2, 'ولا أقسم بالنفس اللوامة'),
(5554, 75, 3, 'أيحسب الإنسان ألن نجمع عظامه'),
(5555, 75, 4, 'بلى قادرين على أن نسوي بنانه'),
(5556, 75, 5, 'بل يريد الإنسان ليفجر أمامه'),
(5557, 75, 6, 'يسأل أيان يوم القيامة'),
(5558, 75, 7, 'فإذا برق البصر'),
(5559, 75, 8, 'وخسف القمر'),
(5560, 75, 9, 'وجمع الشمس والقمر'),
(5561, 75, 10, 'يقول الإنسان يومئذ أين المفر'),
(5562, 75, 11, 'كلا لا وزر'),
(5563, 75, 12, 'إلى ربك يومئذ المستقر'),
(5564, 75, 13, 'ينبأ الإنسان يومئذ بما قدم وأخر'),
(5565, 75, 14, 'بل الإنسان على نفسه بصيرة'),
(5566, 75, 15, 'ولو ألقى معاذيره'),
(5567, 75, 16, 'لا تحرك به لسانك لتعجل به'),
(5568, 75, 17, 'إن علينا جمعه وقرآنه'),
(5569, 75, 18, 'فإذا قرأناه فاتبع قرآنه'),
(5570, 75, 19, 'ثم إن علينا بيانه'),
(5571, 75, 20, 'كلا بل تحبون العاجلة'),
(5572, 75, 21, 'وتذرون الآخرة'),
(5573, 75, 22, 'وجوه يومئذ ناضرة'),
(5574, 75, 23, 'إلى ربها ناظرة'),
(5575, 75, 24, 'ووجوه يومئذ باسرة'),
(5576, 75, 25, 'تظن أن يفعل بها فاقرة'),
(5577, 75, 26, 'كلا إذا بلغت التراقي'),
(5578, 75, 27, 'وقيل من ۜ راق'),
(5579, 75, 28, 'وظن أنه الفراق'),
(5580, 75, 29, 'والتفت الساق بالساق'),
(5581, 75, 30, 'إلى ربك يومئذ المساق'),
(5582, 75, 31, 'فلا صدق ولا صلى'),
(5583, 75, 32, 'ولكن كذب وتولى'),
(5584, 75, 33, 'ثم ذهب إلى أهله يتمطى'),
(5585, 75, 34, 'أولى لك فأولى'),
(5586, 75, 35, 'ثم أولى لك فأولى'),
(5587, 75, 36, 'أيحسب الإنسان أن يترك سدى'),
(5588, 75, 37, 'ألم يك نطفة من مني يمنى'),
(5589, 75, 38, 'ثم كان علقة فخلق فسوى'),
(5590, 75, 39, 'فجعل منه الزوجين الذكر والأنثى'),
(5591, 75, 40, 'أليس ذلك بقادر على أن يحيي الموتى');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'بسم الله الرحمن الرحيم هل أتى على الإنسان حين من الدهر لم يكن شيئا مذكورا'),
(5593, 76, 2, 'إنا خلقنا الإنسان من نطفة أمشاج نبتليه فجعلناه سميعا بصيرا'),
(5594, 76, 3, 'إنا هديناه السبيل إما شاكرا وإما كفورا'),
(5595, 76, 4, 'إنا أعتدنا للكافرين سلاسل وأغلالا وسعيرا'),
(5596, 76, 5, 'إن الأبرار يشربون من كأس كان مزاجها كافورا'),
(5597, 76, 6, 'عينا يشرب بها عباد الله يفجرونها تفجيرا'),
(5598, 76, 7, 'يوفون بالنذر ويخافون يوما كان شره مستطيرا'),
(5599, 76, 8, 'ويطعمون الطعام على حبه مسكينا ويتيما وأسيرا'),
(5600, 76, 9, 'إنما نطعمكم لوجه الله لا نريد منكم جزاء ولا شكورا'),
(5601, 76, 10, 'إنا نخاف من ربنا يوما عبوسا قمطريرا'),
(5602, 76, 11, 'فوقاهم الله شر ذلك اليوم ولقاهم نضرة وسرورا'),
(5603, 76, 12, 'وجزاهم بما صبروا جنة وحريرا'),
(5604, 76, 13, 'متكئين فيها على الأرائك ۖ لا يرون فيها شمسا ولا زمهريرا'),
(5605, 76, 14, 'ودانية عليهم ظلالها وذللت قطوفها تذليلا'),
(5606, 76, 15, 'ويطاف عليهم بآنية من فضة وأكواب كانت قواريرا'),
(5607, 76, 16, 'قوارير من فضة قدروها تقديرا'),
(5608, 76, 17, 'ويسقون فيها كأسا كان مزاجها زنجبيلا'),
(5609, 76, 18, 'عينا فيها تسمى سلسبيلا'),
(5610, 76, 19, 'ويطوف عليهم ولدان مخلدون إذا رأيتهم حسبتهم لؤلؤا منثورا'),
(5611, 76, 20, 'وإذا رأيت ثم رأيت نعيما وملكا كبيرا'),
(5612, 76, 21, 'عاليهم ثياب سندس خضر وإستبرق ۖ وحلوا أساور من فضة وسقاهم ربهم شرابا طهورا'),
(5613, 76, 22, 'إن هذا كان لكم جزاء وكان سعيكم مشكورا'),
(5614, 76, 23, 'إنا نحن نزلنا عليك القرآن تنزيلا'),
(5615, 76, 24, 'فاصبر لحكم ربك ولا تطع منهم آثما أو كفورا'),
(5616, 76, 25, 'واذكر اسم ربك بكرة وأصيلا'),
(5617, 76, 26, 'ومن الليل فاسجد له وسبحه ليلا طويلا'),
(5618, 76, 27, 'إن هؤلاء يحبون العاجلة ويذرون وراءهم يوما ثقيلا'),
(5619, 76, 28, 'نحن خلقناهم وشددنا أسرهم ۖ وإذا شئنا بدلنا أمثالهم تبديلا'),
(5620, 76, 29, 'إن هذه تذكرة ۖ فمن شاء اتخذ إلى ربه سبيلا'),
(5621, 76, 30, 'وما تشاءون إلا أن يشاء الله ۚ إن الله كان عليما حكيما'),
(5622, 76, 31, 'يدخل من يشاء في رحمته ۚ والظالمين أعد لهم عذابا أليما');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'بسم الله الرحمن الرحيم والمرسلات عرفا'),
(5624, 77, 2, 'فالعاصفات عصفا'),
(5625, 77, 3, 'والناشرات نشرا'),
(5626, 77, 4, 'فالفارقات فرقا'),
(5627, 77, 5, 'فالملقيات ذكرا'),
(5628, 77, 6, 'عذرا أو نذرا'),
(5629, 77, 7, 'إنما توعدون لواقع'),
(5630, 77, 8, 'فإذا النجوم طمست'),
(5631, 77, 9, 'وإذا السماء فرجت'),
(5632, 77, 10, 'وإذا الجبال نسفت'),
(5633, 77, 11, 'وإذا الرسل أقتت'),
(5634, 77, 12, 'لأي يوم أجلت'),
(5635, 77, 13, 'ليوم الفصل'),
(5636, 77, 14, 'وما أدراك ما يوم الفصل'),
(5637, 77, 15, 'ويل يومئذ للمكذبين'),
(5638, 77, 16, 'ألم نهلك الأولين'),
(5639, 77, 17, 'ثم نتبعهم الآخرين'),
(5640, 77, 18, 'كذلك نفعل بالمجرمين'),
(5641, 77, 19, 'ويل يومئذ للمكذبين'),
(5642, 77, 20, 'ألم نخلقكم من ماء مهين'),
(5643, 77, 21, 'فجعلناه في قرار مكين'),
(5644, 77, 22, 'إلى قدر معلوم'),
(5645, 77, 23, 'فقدرنا فنعم القادرون'),
(5646, 77, 24, 'ويل يومئذ للمكذبين'),
(5647, 77, 25, 'ألم نجعل الأرض كفاتا'),
(5648, 77, 26, 'أحياء وأمواتا'),
(5649, 77, 27, 'وجعلنا فيها رواسي شامخات وأسقيناكم ماء فراتا'),
(5650, 77, 28, 'ويل يومئذ للمكذبين'),
(5651, 77, 29, 'انطلقوا إلى ما كنتم به تكذبون'),
(5652, 77, 30, 'انطلقوا إلى ظل ذي ثلاث شعب'),
(5653, 77, 31, 'لا ظليل ولا يغني من اللهب'),
(5654, 77, 32, 'إنها ترمي بشرر كالقصر'),
(5655, 77, 33, 'كأنه جمالت صفر'),
(5656, 77, 34, 'ويل يومئذ للمكذبين'),
(5657, 77, 35, 'هذا يوم لا ينطقون'),
(5658, 77, 36, 'ولا يؤذن لهم فيعتذرون'),
(5659, 77, 37, 'ويل يومئذ للمكذبين'),
(5660, 77, 38, 'هذا يوم الفصل ۖ جمعناكم والأولين'),
(5661, 77, 39, 'فإن كان لكم كيد فكيدون'),
(5662, 77, 40, 'ويل يومئذ للمكذبين'),
(5663, 77, 41, 'إن المتقين في ظلال وعيون'),
(5664, 77, 42, 'وفواكه مما يشتهون'),
(5665, 77, 43, 'كلوا واشربوا هنيئا بما كنتم تعملون'),
(5666, 77, 44, 'إنا كذلك نجزي المحسنين'),
(5667, 77, 45, 'ويل يومئذ للمكذبين'),
(5668, 77, 46, 'كلوا وتمتعوا قليلا إنكم مجرمون'),
(5669, 77, 47, 'ويل يومئذ للمكذبين'),
(5670, 77, 48, 'وإذا قيل لهم اركعوا لا يركعون'),
(5671, 77, 49, 'ويل يومئذ للمكذبين'),
(5672, 77, 50, 'فبأي حديث بعده يؤمنون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'بسم الله الرحمن الرحيم عم يتساءلون'),
(5674, 78, 2, 'عن النبإ العظيم'),
(5675, 78, 3, 'الذي هم فيه مختلفون'),
(5676, 78, 4, 'كلا سيعلمون'),
(5677, 78, 5, 'ثم كلا سيعلمون'),
(5678, 78, 6, 'ألم نجعل الأرض مهادا'),
(5679, 78, 7, 'والجبال أوتادا'),
(5680, 78, 8, 'وخلقناكم أزواجا'),
(5681, 78, 9, 'وجعلنا نومكم سباتا'),
(5682, 78, 10, 'وجعلنا الليل لباسا'),
(5683, 78, 11, 'وجعلنا النهار معاشا'),
(5684, 78, 12, 'وبنينا فوقكم سبعا شدادا'),
(5685, 78, 13, 'وجعلنا سراجا وهاجا'),
(5686, 78, 14, 'وأنزلنا من المعصرات ماء ثجاجا'),
(5687, 78, 15, 'لنخرج به حبا ونباتا'),
(5688, 78, 16, 'وجنات ألفافا'),
(5689, 78, 17, 'إن يوم الفصل كان ميقاتا'),
(5690, 78, 18, 'يوم ينفخ في الصور فتأتون أفواجا'),
(5691, 78, 19, 'وفتحت السماء فكانت أبوابا'),
(5692, 78, 20, 'وسيرت الجبال فكانت سرابا'),
(5693, 78, 21, 'إن جهنم كانت مرصادا'),
(5694, 78, 22, 'للطاغين مآبا'),
(5695, 78, 23, 'لابثين فيها أحقابا'),
(5696, 78, 24, 'لا يذوقون فيها بردا ولا شرابا'),
(5697, 78, 25, 'إلا حميما وغساقا'),
(5698, 78, 26, 'جزاء وفاقا'),
(5699, 78, 27, 'إنهم كانوا لا يرجون حسابا'),
(5700, 78, 28, 'وكذبوا بآياتنا كذابا'),
(5701, 78, 29, 'وكل شيء أحصيناه كتابا'),
(5702, 78, 30, 'فذوقوا فلن نزيدكم إلا عذابا'),
(5703, 78, 31, 'إن للمتقين مفازا'),
(5704, 78, 32, 'حدائق وأعنابا'),
(5705, 78, 33, 'وكواعب أترابا'),
(5706, 78, 34, 'وكأسا دهاقا'),
(5707, 78, 35, 'لا يسمعون فيها لغوا ولا كذابا'),
(5708, 78, 36, 'جزاء من ربك عطاء حسابا'),
(5709, 78, 37, 'رب السماوات والأرض وما بينهما الرحمن ۖ لا يملكون منه خطابا'),
(5710, 78, 38, 'يوم يقوم الروح والملائكة صفا ۖ لا يتكلمون إلا من أذن له الرحمن وقال صوابا'),
(5711, 78, 39, 'ذلك اليوم الحق ۖ فمن شاء اتخذ إلى ربه مآبا'),
(5712, 78, 40, 'إنا أنذرناكم عذابا قريبا يوم ينظر المرء ما قدمت يداه ويقول الكافر يا ليتني كنت ترابا');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'بسم الله الرحمن الرحيم والنازعات غرقا'),
(5714, 79, 2, 'والناشطات نشطا'),
(5715, 79, 3, 'والسابحات سبحا'),
(5716, 79, 4, 'فالسابقات سبقا'),
(5717, 79, 5, 'فالمدبرات أمرا'),
(5718, 79, 6, 'يوم ترجف الراجفة'),
(5719, 79, 7, 'تتبعها الرادفة'),
(5720, 79, 8, 'قلوب يومئذ واجفة'),
(5721, 79, 9, 'أبصارها خاشعة'),
(5722, 79, 10, 'يقولون أإنا لمردودون في الحافرة'),
(5723, 79, 11, 'أإذا كنا عظاما نخرة'),
(5724, 79, 12, 'قالوا تلك إذا كرة خاسرة'),
(5725, 79, 13, 'فإنما هي زجرة واحدة'),
(5726, 79, 14, 'فإذا هم بالساهرة'),
(5727, 79, 15, 'هل أتاك حديث موسى'),
(5728, 79, 16, 'إذ ناداه ربه بالواد المقدس طوى'),
(5729, 79, 17, 'اذهب إلى فرعون إنه طغى'),
(5730, 79, 18, 'فقل هل لك إلى أن تزكى'),
(5731, 79, 19, 'وأهديك إلى ربك فتخشى'),
(5732, 79, 20, 'فأراه الآية الكبرى'),
(5733, 79, 21, 'فكذب وعصى'),
(5734, 79, 22, 'ثم أدبر يسعى'),
(5735, 79, 23, 'فحشر فنادى'),
(5736, 79, 24, 'فقال أنا ربكم الأعلى'),
(5737, 79, 25, 'فأخذه الله نكال الآخرة والأولى'),
(5738, 79, 26, 'إن في ذلك لعبرة لمن يخشى'),
(5739, 79, 27, 'أأنتم أشد خلقا أم السماء ۚ بناها'),
(5740, 79, 28, 'رفع سمكها فسواها'),
(5741, 79, 29, 'وأغطش ليلها وأخرج ضحاها'),
(5742, 79, 30, 'والأرض بعد ذلك دحاها'),
(5743, 79, 31, 'أخرج منها ماءها ومرعاها'),
(5744, 79, 32, 'والجبال أرساها'),
(5745, 79, 33, 'متاعا لكم ولأنعامكم'),
(5746, 79, 34, 'فإذا جاءت الطامة الكبرى'),
(5747, 79, 35, 'يوم يتذكر الإنسان ما سعى'),
(5748, 79, 36, 'وبرزت الجحيم لمن يرى'),
(5749, 79, 37, 'فأما من طغى'),
(5750, 79, 38, 'وآثر الحياة الدنيا'),
(5751, 79, 39, 'فإن الجحيم هي المأوى'),
(5752, 79, 40, 'وأما من خاف مقام ربه ونهى النفس عن الهوى'),
(5753, 79, 41, 'فإن الجنة هي المأوى'),
(5754, 79, 42, 'يسألونك عن الساعة أيان مرساها'),
(5755, 79, 43, 'فيم أنت من ذكراها'),
(5756, 79, 44, 'إلى ربك منتهاها'),
(5757, 79, 45, 'إنما أنت منذر من يخشاها'),
(5758, 79, 46, 'كأنهم يوم يرونها لم يلبثوا إلا عشية أو ضحاها');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'بسم الله الرحمن الرحيم عبس وتولى'),
(5760, 80, 2, 'أن جاءه الأعمى'),
(5761, 80, 3, 'وما يدريك لعله يزكى'),
(5762, 80, 4, 'أو يذكر فتنفعه الذكرى'),
(5763, 80, 5, 'أما من استغنى'),
(5764, 80, 6, 'فأنت له تصدى'),
(5765, 80, 7, 'وما عليك ألا يزكى'),
(5766, 80, 8, 'وأما من جاءك يسعى'),
(5767, 80, 9, 'وهو يخشى'),
(5768, 80, 10, 'فأنت عنه تلهى'),
(5769, 80, 11, 'كلا إنها تذكرة'),
(5770, 80, 12, 'فمن شاء ذكره'),
(5771, 80, 13, 'في صحف مكرمة'),
(5772, 80, 14, 'مرفوعة مطهرة'),
(5773, 80, 15, 'بأيدي سفرة'),
(5774, 80, 16, 'كرام بررة'),
(5775, 80, 17, 'قتل الإنسان ما أكفره'),
(5776, 80, 18, 'من أي شيء خلقه'),
(5777, 80, 19, 'من نطفة خلقه فقدره'),
(5778, 80, 20, 'ثم السبيل يسره'),
(5779, 80, 21, 'ثم أماته فأقبره'),
(5780, 80, 22, 'ثم إذا شاء أنشره'),
(5781, 80, 23, 'كلا لما يقض ما أمره'),
(5782, 80, 24, 'فلينظر الإنسان إلى طعامه'),
(5783, 80, 25, 'أنا صببنا الماء صبا'),
(5784, 80, 26, 'ثم شققنا الأرض شقا'),
(5785, 80, 27, 'فأنبتنا فيها حبا'),
(5786, 80, 28, 'وعنبا وقضبا'),
(5787, 80, 29, 'وزيتونا ونخلا'),
(5788, 80, 30, 'وحدائق غلبا'),
(5789, 80, 31, 'وفاكهة وأبا'),
(5790, 80, 32, 'متاعا لكم ولأنعامكم'),
(5791, 80, 33, 'فإذا جاءت الصاخة'),
(5792, 80, 34, 'يوم يفر المرء من أخيه'),
(5793, 80, 35, 'وأمه وأبيه'),
(5794, 80, 36, 'وصاحبته وبنيه'),
(5795, 80, 37, 'لكل امرئ منهم يومئذ شأن يغنيه'),
(5796, 80, 38, 'وجوه يومئذ مسفرة'),
(5797, 80, 39, 'ضاحكة مستبشرة'),
(5798, 80, 40, 'ووجوه يومئذ عليها غبرة'),
(5799, 80, 41, 'ترهقها قترة'),
(5800, 80, 42, 'أولئك هم الكفرة الفجرة');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'بسم الله الرحمن الرحيم إذا الشمس كورت'),
(5802, 81, 2, 'وإذا النجوم انكدرت'),
(5803, 81, 3, 'وإذا الجبال سيرت'),
(5804, 81, 4, 'وإذا العشار عطلت'),
(5805, 81, 5, 'وإذا الوحوش حشرت'),
(5806, 81, 6, 'وإذا البحار سجرت'),
(5807, 81, 7, 'وإذا النفوس زوجت'),
(5808, 81, 8, 'وإذا الموءودة سئلت'),
(5809, 81, 9, 'بأي ذنب قتلت'),
(5810, 81, 10, 'وإذا الصحف نشرت'),
(5811, 81, 11, 'وإذا السماء كشطت'),
(5812, 81, 12, 'وإذا الجحيم سعرت'),
(5813, 81, 13, 'وإذا الجنة أزلفت'),
(5814, 81, 14, 'علمت نفس ما أحضرت'),
(5815, 81, 15, 'فلا أقسم بالخنس'),
(5816, 81, 16, 'الجوار الكنس'),
(5817, 81, 17, 'والليل إذا عسعس'),
(5818, 81, 18, 'والصبح إذا تنفس'),
(5819, 81, 19, 'إنه لقول رسول كريم'),
(5820, 81, 20, 'ذي قوة عند ذي العرش مكين'),
(5821, 81, 21, 'مطاع ثم أمين'),
(5822, 81, 22, 'وما صاحبكم بمجنون'),
(5823, 81, 23, 'ولقد رآه بالأفق المبين'),
(5824, 81, 24, 'وما هو على الغيب بضنين'),
(5825, 81, 25, 'وما هو بقول شيطان رجيم'),
(5826, 81, 26, 'فأين تذهبون'),
(5827, 81, 27, 'إن هو إلا ذكر للعالمين'),
(5828, 81, 28, 'لمن شاء منكم أن يستقيم'),
(5829, 81, 29, 'وما تشاءون إلا أن يشاء الله رب العالمين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'بسم الله الرحمن الرحيم إذا السماء انفطرت'),
(5831, 82, 2, 'وإذا الكواكب انتثرت'),
(5832, 82, 3, 'وإذا البحار فجرت'),
(5833, 82, 4, 'وإذا القبور بعثرت'),
(5834, 82, 5, 'علمت نفس ما قدمت وأخرت'),
(5835, 82, 6, 'يا أيها الإنسان ما غرك بربك الكريم'),
(5836, 82, 7, 'الذي خلقك فسواك فعدلك'),
(5837, 82, 8, 'في أي صورة ما شاء ركبك'),
(5838, 82, 9, 'كلا بل تكذبون بالدين'),
(5839, 82, 10, 'وإن عليكم لحافظين'),
(5840, 82, 11, 'كراما كاتبين'),
(5841, 82, 12, 'يعلمون ما تفعلون'),
(5842, 82, 13, 'إن الأبرار لفي نعيم'),
(5843, 82, 14, 'وإن الفجار لفي جحيم'),
(5844, 82, 15, 'يصلونها يوم الدين'),
(5845, 82, 16, 'وما هم عنها بغائبين'),
(5846, 82, 17, 'وما أدراك ما يوم الدين'),
(5847, 82, 18, 'ثم ما أدراك ما يوم الدين'),
(5848, 82, 19, 'يوم لا تملك نفس لنفس شيئا ۖ والأمر يومئذ لله');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'بسم الله الرحمن الرحيم ويل للمطففين'),
(5850, 83, 2, 'الذين إذا اكتالوا على الناس يستوفون'),
(5851, 83, 3, 'وإذا كالوهم أو وزنوهم يخسرون'),
(5852, 83, 4, 'ألا يظن أولئك أنهم مبعوثون'),
(5853, 83, 5, 'ليوم عظيم'),
(5854, 83, 6, 'يوم يقوم الناس لرب العالمين'),
(5855, 83, 7, 'كلا إن كتاب الفجار لفي سجين'),
(5856, 83, 8, 'وما أدراك ما سجين'),
(5857, 83, 9, 'كتاب مرقوم'),
(5858, 83, 10, 'ويل يومئذ للمكذبين'),
(5859, 83, 11, 'الذين يكذبون بيوم الدين'),
(5860, 83, 12, 'وما يكذب به إلا كل معتد أثيم'),
(5861, 83, 13, 'إذا تتلى عليه آياتنا قال أساطير الأولين'),
(5862, 83, 14, 'كلا ۖ بل ۜ ران على قلوبهم ما كانوا يكسبون'),
(5863, 83, 15, 'كلا إنهم عن ربهم يومئذ لمحجوبون'),
(5864, 83, 16, 'ثم إنهم لصالو الجحيم'),
(5865, 83, 17, 'ثم يقال هذا الذي كنتم به تكذبون'),
(5866, 83, 18, 'كلا إن كتاب الأبرار لفي عليين'),
(5867, 83, 19, 'وما أدراك ما عليون'),
(5868, 83, 20, 'كتاب مرقوم'),
(5869, 83, 21, 'يشهده المقربون'),
(5870, 83, 22, 'إن الأبرار لفي نعيم'),
(5871, 83, 23, 'على الأرائك ينظرون'),
(5872, 83, 24, 'تعرف في وجوههم نضرة النعيم'),
(5873, 83, 25, 'يسقون من رحيق مختوم'),
(5874, 83, 26, 'ختامه مسك ۚ وفي ذلك فليتنافس المتنافسون'),
(5875, 83, 27, 'ومزاجه من تسنيم'),
(5876, 83, 28, 'عينا يشرب بها المقربون'),
(5877, 83, 29, 'إن الذين أجرموا كانوا من الذين آمنوا يضحكون'),
(5878, 83, 30, 'وإذا مروا بهم يتغامزون'),
(5879, 83, 31, 'وإذا انقلبوا إلى أهلهم انقلبوا فكهين'),
(5880, 83, 32, 'وإذا رأوهم قالوا إن هؤلاء لضالون'),
(5881, 83, 33, 'وما أرسلوا عليهم حافظين'),
(5882, 83, 34, 'فاليوم الذين آمنوا من الكفار يضحكون'),
(5883, 83, 35, 'على الأرائك ينظرون'),
(5884, 83, 36, 'هل ثوب الكفار ما كانوا يفعلون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'بسم الله الرحمن الرحيم إذا السماء انشقت'),
(5886, 84, 2, 'وأذنت لربها وحقت'),
(5887, 84, 3, 'وإذا الأرض مدت'),
(5888, 84, 4, 'وألقت ما فيها وتخلت'),
(5889, 84, 5, 'وأذنت لربها وحقت'),
(5890, 84, 6, 'يا أيها الإنسان إنك كادح إلى ربك كدحا فملاقيه'),
(5891, 84, 7, 'فأما من أوتي كتابه بيمينه'),
(5892, 84, 8, 'فسوف يحاسب حسابا يسيرا'),
(5893, 84, 9, 'وينقلب إلى أهله مسرورا'),
(5894, 84, 10, 'وأما من أوتي كتابه وراء ظهره'),
(5895, 84, 11, 'فسوف يدعو ثبورا'),
(5896, 84, 12, 'ويصلى سعيرا'),
(5897, 84, 13, 'إنه كان في أهله مسرورا'),
(5898, 84, 14, 'إنه ظن أن لن يحور'),
(5899, 84, 15, 'بلى إن ربه كان به بصيرا'),
(5900, 84, 16, 'فلا أقسم بالشفق'),
(5901, 84, 17, 'والليل وما وسق'),
(5902, 84, 18, 'والقمر إذا اتسق'),
(5903, 84, 19, 'لتركبن طبقا عن طبق'),
(5904, 84, 20, 'فما لهم لا يؤمنون'),
(5905, 84, 21, 'وإذا قرئ عليهم القرآن لا يسجدون'),
(5906, 84, 22, 'بل الذين كفروا يكذبون'),
(5907, 84, 23, 'والله أعلم بما يوعون'),
(5908, 84, 24, 'فبشرهم بعذاب أليم'),
(5909, 84, 25, 'إلا الذين آمنوا وعملوا الصالحات لهم أجر غير ممنون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'بسم الله الرحمن الرحيم والسماء ذات البروج'),
(5911, 85, 2, 'واليوم الموعود'),
(5912, 85, 3, 'وشاهد ومشهود'),
(5913, 85, 4, 'قتل أصحاب الأخدود'),
(5914, 85, 5, 'النار ذات الوقود'),
(5915, 85, 6, 'إذ هم عليها قعود'),
(5916, 85, 7, 'وهم على ما يفعلون بالمؤمنين شهود'),
(5917, 85, 8, 'وما نقموا منهم إلا أن يؤمنوا بالله العزيز الحميد'),
(5918, 85, 9, 'الذي له ملك السماوات والأرض ۚ والله على كل شيء شهيد'),
(5919, 85, 10, 'إن الذين فتنوا المؤمنين والمؤمنات ثم لم يتوبوا فلهم عذاب جهنم ولهم عذاب الحريق'),
(5920, 85, 11, 'إن الذين آمنوا وعملوا الصالحات لهم جنات تجري من تحتها الأنهار ۚ ذلك الفوز الكبير'),
(5921, 85, 12, 'إن بطش ربك لشديد'),
(5922, 85, 13, 'إنه هو يبدئ ويعيد'),
(5923, 85, 14, 'وهو الغفور الودود'),
(5924, 85, 15, 'ذو العرش المجيد'),
(5925, 85, 16, 'فعال لما يريد'),
(5926, 85, 17, 'هل أتاك حديث الجنود'),
(5927, 85, 18, 'فرعون وثمود'),
(5928, 85, 19, 'بل الذين كفروا في تكذيب'),
(5929, 85, 20, 'والله من ورائهم محيط'),
(5930, 85, 21, 'بل هو قرآن مجيد'),
(5931, 85, 22, 'في لوح محفوظ');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'بسم الله الرحمن الرحيم والسماء والطارق'),
(5933, 86, 2, 'وما أدراك ما الطارق'),
(5934, 86, 3, 'النجم الثاقب'),
(5935, 86, 4, 'إن كل نفس لما عليها حافظ'),
(5936, 86, 5, 'فلينظر الإنسان مم خلق'),
(5937, 86, 6, 'خلق من ماء دافق'),
(5938, 86, 7, 'يخرج من بين الصلب والترائب'),
(5939, 86, 8, 'إنه على رجعه لقادر'),
(5940, 86, 9, 'يوم تبلى السرائر'),
(5941, 86, 10, 'فما له من قوة ولا ناصر'),
(5942, 86, 11, 'والسماء ذات الرجع'),
(5943, 86, 12, 'والأرض ذات الصدع'),
(5944, 86, 13, 'إنه لقول فصل'),
(5945, 86, 14, 'وما هو بالهزل'),
(5946, 86, 15, 'إنهم يكيدون كيدا'),
(5947, 86, 16, 'وأكيد كيدا'),
(5948, 86, 17, 'فمهل الكافرين أمهلهم رويدا');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'بسم الله الرحمن الرحيم سبح اسم ربك الأعلى'),
(5950, 87, 2, 'الذي خلق فسوى'),
(5951, 87, 3, 'والذي قدر فهدى'),
(5952, 87, 4, 'والذي أخرج المرعى'),
(5953, 87, 5, 'فجعله غثاء أحوى'),
(5954, 87, 6, 'سنقرئك فلا تنسى'),
(5955, 87, 7, 'إلا ما شاء الله ۚ إنه يعلم الجهر وما يخفى'),
(5956, 87, 8, 'ونيسرك لليسرى'),
(5957, 87, 9, 'فذكر إن نفعت الذكرى'),
(5958, 87, 10, 'سيذكر من يخشى'),
(5959, 87, 11, 'ويتجنبها الأشقى'),
(5960, 87, 12, 'الذي يصلى النار الكبرى'),
(5961, 87, 13, 'ثم لا يموت فيها ولا يحيى'),
(5962, 87, 14, 'قد أفلح من تزكى'),
(5963, 87, 15, 'وذكر اسم ربه فصلى'),
(5964, 87, 16, 'بل تؤثرون الحياة الدنيا'),
(5965, 87, 17, 'والآخرة خير وأبقى'),
(5966, 87, 18, 'إن هذا لفي الصحف الأولى'),
(5967, 87, 19, 'صحف إبراهيم وموسى');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'بسم الله الرحمن الرحيم هل أتاك حديث الغاشية'),
(5969, 88, 2, 'وجوه يومئذ خاشعة'),
(5970, 88, 3, 'عاملة ناصبة'),
(5971, 88, 4, 'تصلى نارا حامية'),
(5972, 88, 5, 'تسقى من عين آنية'),
(5973, 88, 6, 'ليس لهم طعام إلا من ضريع'),
(5974, 88, 7, 'لا يسمن ولا يغني من جوع'),
(5975, 88, 8, 'وجوه يومئذ ناعمة'),
(5976, 88, 9, 'لسعيها راضية'),
(5977, 88, 10, 'في جنة عالية'),
(5978, 88, 11, 'لا تسمع فيها لاغية'),
(5979, 88, 12, 'فيها عين جارية'),
(5980, 88, 13, 'فيها سرر مرفوعة'),
(5981, 88, 14, 'وأكواب موضوعة'),
(5982, 88, 15, 'ونمارق مصفوفة'),
(5983, 88, 16, 'وزرابي مبثوثة'),
(5984, 88, 17, 'أفلا ينظرون إلى الإبل كيف خلقت'),
(5985, 88, 18, 'وإلى السماء كيف رفعت'),
(5986, 88, 19, 'وإلى الجبال كيف نصبت'),
(5987, 88, 20, 'وإلى الأرض كيف سطحت'),
(5988, 88, 21, 'فذكر إنما أنت مذكر'),
(5989, 88, 22, 'لست عليهم بمصيطر'),
(5990, 88, 23, 'إلا من تولى وكفر'),
(5991, 88, 24, 'فيعذبه الله العذاب الأكبر'),
(5992, 88, 25, 'إن إلينا إيابهم'),
(5993, 88, 26, 'ثم إن علينا حسابهم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'بسم الله الرحمن الرحيم والفجر'),
(5995, 89, 2, 'وليال عشر'),
(5996, 89, 3, 'والشفع والوتر'),
(5997, 89, 4, 'والليل إذا يسر'),
(5998, 89, 5, 'هل في ذلك قسم لذي حجر'),
(5999, 89, 6, 'ألم تر كيف فعل ربك بعاد'),
(6000, 89, 7, 'إرم ذات العماد'),
(6001, 89, 8, 'التي لم يخلق مثلها في البلاد'),
(6002, 89, 9, 'وثمود الذين جابوا الصخر بالواد'),
(6003, 89, 10, 'وفرعون ذي الأوتاد'),
(6004, 89, 11, 'الذين طغوا في البلاد'),
(6005, 89, 12, 'فأكثروا فيها الفساد'),
(6006, 89, 13, 'فصب عليهم ربك سوط عذاب'),
(6007, 89, 14, 'إن ربك لبالمرصاد'),
(6008, 89, 15, 'فأما الإنسان إذا ما ابتلاه ربه فأكرمه ونعمه فيقول ربي أكرمن'),
(6009, 89, 16, 'وأما إذا ما ابتلاه فقدر عليه رزقه فيقول ربي أهانن'),
(6010, 89, 17, 'كلا ۖ بل لا تكرمون اليتيم'),
(6011, 89, 18, 'ولا تحاضون على طعام المسكين'),
(6012, 89, 19, 'وتأكلون التراث أكلا لما'),
(6013, 89, 20, 'وتحبون المال حبا جما'),
(6014, 89, 21, 'كلا إذا دكت الأرض دكا دكا'),
(6015, 89, 22, 'وجاء ربك والملك صفا صفا'),
(6016, 89, 23, 'وجيء يومئذ بجهنم ۚ يومئذ يتذكر الإنسان وأنى له الذكرى'),
(6017, 89, 24, 'يقول يا ليتني قدمت لحياتي'),
(6018, 89, 25, 'فيومئذ لا يعذب عذابه أحد'),
(6019, 89, 26, 'ولا يوثق وثاقه أحد'),
(6020, 89, 27, 'يا أيتها النفس المطمئنة'),
(6021, 89, 28, 'ارجعي إلى ربك راضية مرضية'),
(6022, 89, 29, 'فادخلي في عبادي'),
(6023, 89, 30, 'وادخلي جنتي');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'بسم الله الرحمن الرحيم لا أقسم بهذا البلد'),
(6025, 90, 2, 'وأنت حل بهذا البلد'),
(6026, 90, 3, 'ووالد وما ولد'),
(6027, 90, 4, 'لقد خلقنا الإنسان في كبد'),
(6028, 90, 5, 'أيحسب أن لن يقدر عليه أحد'),
(6029, 90, 6, 'يقول أهلكت مالا لبدا'),
(6030, 90, 7, 'أيحسب أن لم يره أحد'),
(6031, 90, 8, 'ألم نجعل له عينين'),
(6032, 90, 9, 'ولسانا وشفتين'),
(6033, 90, 10, 'وهديناه النجدين'),
(6034, 90, 11, 'فلا اقتحم العقبة'),
(6035, 90, 12, 'وما أدراك ما العقبة'),
(6036, 90, 13, 'فك رقبة'),
(6037, 90, 14, 'أو إطعام في يوم ذي مسغبة'),
(6038, 90, 15, 'يتيما ذا مقربة'),
(6039, 90, 16, 'أو مسكينا ذا متربة'),
(6040, 90, 17, 'ثم كان من الذين آمنوا وتواصوا بالصبر وتواصوا بالمرحمة'),
(6041, 90, 18, 'أولئك أصحاب الميمنة'),
(6042, 90, 19, 'والذين كفروا بآياتنا هم أصحاب المشأمة'),
(6043, 90, 20, 'عليهم نار مؤصدة');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'بسم الله الرحمن الرحيم والشمس وضحاها'),
(6045, 91, 2, 'والقمر إذا تلاها'),
(6046, 91, 3, 'والنهار إذا جلاها'),
(6047, 91, 4, 'والليل إذا يغشاها'),
(6048, 91, 5, 'والسماء وما بناها'),
(6049, 91, 6, 'والأرض وما طحاها'),
(6050, 91, 7, 'ونفس وما سواها'),
(6051, 91, 8, 'فألهمها فجورها وتقواها'),
(6052, 91, 9, 'قد أفلح من زكاها'),
(6053, 91, 10, 'وقد خاب من دساها'),
(6054, 91, 11, 'كذبت ثمود بطغواها'),
(6055, 91, 12, 'إذ انبعث أشقاها'),
(6056, 91, 13, 'فقال لهم رسول الله ناقة الله وسقياها'),
(6057, 91, 14, 'فكذبوه فعقروها فدمدم عليهم ربهم بذنبهم فسواها'),
(6058, 91, 15, 'ولا يخاف عقباها');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'بسم الله الرحمن الرحيم والليل إذا يغشى'),
(6060, 92, 2, 'والنهار إذا تجلى'),
(6061, 92, 3, 'وما خلق الذكر والأنثى'),
(6062, 92, 4, 'إن سعيكم لشتى'),
(6063, 92, 5, 'فأما من أعطى واتقى'),
(6064, 92, 6, 'وصدق بالحسنى'),
(6065, 92, 7, 'فسنيسره لليسرى'),
(6066, 92, 8, 'وأما من بخل واستغنى'),
(6067, 92, 9, 'وكذب بالحسنى'),
(6068, 92, 10, 'فسنيسره للعسرى'),
(6069, 92, 11, 'وما يغني عنه ماله إذا تردى'),
(6070, 92, 12, 'إن علينا للهدى'),
(6071, 92, 13, 'وإن لنا للآخرة والأولى'),
(6072, 92, 14, 'فأنذرتكم نارا تلظى'),
(6073, 92, 15, 'لا يصلاها إلا الأشقى'),
(6074, 92, 16, 'الذي كذب وتولى'),
(6075, 92, 17, 'وسيجنبها الأتقى'),
(6076, 92, 18, 'الذي يؤتي ماله يتزكى'),
(6077, 92, 19, 'وما لأحد عنده من نعمة تجزى'),
(6078, 92, 20, 'إلا ابتغاء وجه ربه الأعلى'),
(6079, 92, 21, 'ولسوف يرضى');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'بسم الله الرحمن الرحيم والضحى'),
(6081, 93, 2, 'والليل إذا سجى'),
(6082, 93, 3, 'ما ودعك ربك وما قلى'),
(6083, 93, 4, 'وللآخرة خير لك من الأولى'),
(6084, 93, 5, 'ولسوف يعطيك ربك فترضى'),
(6085, 93, 6, 'ألم يجدك يتيما فآوى'),
(6086, 93, 7, 'ووجدك ضالا فهدى'),
(6087, 93, 8, 'ووجدك عائلا فأغنى'),
(6088, 93, 9, 'فأما اليتيم فلا تقهر'),
(6089, 93, 10, 'وأما السائل فلا تنهر'),
(6090, 93, 11, 'وأما بنعمة ربك فحدث');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'بسم الله الرحمن الرحيم ألم نشرح لك صدرك'),
(6092, 94, 2, 'ووضعنا عنك وزرك'),
(6093, 94, 3, 'الذي أنقض ظهرك'),
(6094, 94, 4, 'ورفعنا لك ذكرك'),
(6095, 94, 5, 'فإن مع العسر يسرا'),
(6096, 94, 6, 'إن مع العسر يسرا'),
(6097, 94, 7, 'فإذا فرغت فانصب'),
(6098, 94, 8, 'وإلى ربك فارغب');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'بسم الله الرحمن الرحيم والتين والزيتون'),
(6100, 95, 2, 'وطور سينين'),
(6101, 95, 3, 'وهذا البلد الأمين'),
(6102, 95, 4, 'لقد خلقنا الإنسان في أحسن تقويم'),
(6103, 95, 5, 'ثم رددناه أسفل سافلين'),
(6104, 95, 6, 'إلا الذين آمنوا وعملوا الصالحات فلهم أجر غير ممنون'),
(6105, 95, 7, 'فما يكذبك بعد بالدين'),
(6106, 95, 8, 'أليس الله بأحكم الحاكمين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'بسم الله الرحمن الرحيم اقرأ باسم ربك الذي خلق'),
(6108, 96, 2, 'خلق الإنسان من علق'),
(6109, 96, 3, 'اقرأ وربك الأكرم'),
(6110, 96, 4, 'الذي علم بالقلم'),
(6111, 96, 5, 'علم الإنسان ما لم يعلم'),
(6112, 96, 6, 'كلا إن الإنسان ليطغى'),
(6113, 96, 7, 'أن رآه استغنى'),
(6114, 96, 8, 'إن إلى ربك الرجعى'),
(6115, 96, 9, 'أرأيت الذي ينهى'),
(6116, 96, 10, 'عبدا إذا صلى'),
(6117, 96, 11, 'أرأيت إن كان على الهدى'),
(6118, 96, 12, 'أو أمر بالتقوى'),
(6119, 96, 13, 'أرأيت إن كذب وتولى'),
(6120, 96, 14, 'ألم يعلم بأن الله يرى'),
(6121, 96, 15, 'كلا لئن لم ينته لنسفعا بالناصية'),
(6122, 96, 16, 'ناصية كاذبة خاطئة'),
(6123, 96, 17, 'فليدع ناديه'),
(6124, 96, 18, 'سندع الزبانية'),
(6125, 96, 19, 'كلا لا تطعه واسجد واقترب');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'بسم الله الرحمن الرحيم إنا أنزلناه في ليلة القدر'),
(6127, 97, 2, 'وما أدراك ما ليلة القدر'),
(6128, 97, 3, 'ليلة القدر خير من ألف شهر'),
(6129, 97, 4, 'تنزل الملائكة والروح فيها بإذن ربهم من كل أمر'),
(6130, 97, 5, 'سلام هي حتى مطلع الفجر');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'بسم الله الرحمن الرحيم لم يكن الذين كفروا من أهل الكتاب والمشركين منفكين حتى تأتيهم البينة'),
(6132, 98, 2, 'رسول من الله يتلو صحفا مطهرة'),
(6133, 98, 3, 'فيها كتب قيمة'),
(6134, 98, 4, 'وما تفرق الذين أوتوا الكتاب إلا من بعد ما جاءتهم البينة'),
(6135, 98, 5, 'وما أمروا إلا ليعبدوا الله مخلصين له الدين حنفاء ويقيموا الصلاة ويؤتوا الزكاة ۚ وذلك دين القيمة'),
(6136, 98, 6, 'إن الذين كفروا من أهل الكتاب والمشركين في نار جهنم خالدين فيها ۚ أولئك هم شر البرية'),
(6137, 98, 7, 'إن الذين آمنوا وعملوا الصالحات أولئك هم خير البرية'),
(6138, 98, 8, 'جزاؤهم عند ربهم جنات عدن تجري من تحتها الأنهار خالدين فيها أبدا ۖ رضي الله عنهم ورضوا عنه ۚ ذلك لمن خشي ربه');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'بسم الله الرحمن الرحيم إذا زلزلت الأرض زلزالها'),
(6140, 99, 2, 'وأخرجت الأرض أثقالها'),
(6141, 99, 3, 'وقال الإنسان ما لها'),
(6142, 99, 4, 'يومئذ تحدث أخبارها'),
(6143, 99, 5, 'بأن ربك أوحى لها'),
(6144, 99, 6, 'يومئذ يصدر الناس أشتاتا ليروا أعمالهم'),
(6145, 99, 7, 'فمن يعمل مثقال ذرة خيرا يره'),
(6146, 99, 8, 'ومن يعمل مثقال ذرة شرا يره');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'بسم الله الرحمن الرحيم والعاديات ضبحا'),
(6148, 100, 2, 'فالموريات قدحا'),
(6149, 100, 3, 'فالمغيرات صبحا'),
(6150, 100, 4, 'فأثرن به نقعا'),
(6151, 100, 5, 'فوسطن به جمعا'),
(6152, 100, 6, 'إن الإنسان لربه لكنود'),
(6153, 100, 7, 'وإنه على ذلك لشهيد'),
(6154, 100, 8, 'وإنه لحب الخير لشديد'),
(6155, 100, 9, 'أفلا يعلم إذا بعثر ما في القبور'),
(6156, 100, 10, 'وحصل ما في الصدور'),
(6157, 100, 11, 'إن ربهم بهم يومئذ لخبير');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'بسم الله الرحمن الرحيم القارعة'),
(6159, 101, 2, 'ما القارعة'),
(6160, 101, 3, 'وما أدراك ما القارعة'),
(6161, 101, 4, 'يوم يكون الناس كالفراش المبثوث'),
(6162, 101, 5, 'وتكون الجبال كالعهن المنفوش'),
(6163, 101, 6, 'فأما من ثقلت موازينه'),
(6164, 101, 7, 'فهو في عيشة راضية'),
(6165, 101, 8, 'وأما من خفت موازينه'),
(6166, 101, 9, 'فأمه هاوية'),
(6167, 101, 10, 'وما أدراك ما هيه'),
(6168, 101, 11, 'نار حامية');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'بسم الله الرحمن الرحيم ألهاكم التكاثر'),
(6170, 102, 2, 'حتى زرتم المقابر'),
(6171, 102, 3, 'كلا سوف تعلمون'),
(6172, 102, 4, 'ثم كلا سوف تعلمون'),
(6173, 102, 5, 'كلا لو تعلمون علم اليقين'),
(6174, 102, 6, 'لترون الجحيم'),
(6175, 102, 7, 'ثم لترونها عين اليقين'),
(6176, 102, 8, 'ثم لتسألن يومئذ عن النعيم');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'بسم الله الرحمن الرحيم والعصر'),
(6178, 103, 2, 'إن الإنسان لفي خسر'),
(6179, 103, 3, 'إلا الذين آمنوا وعملوا الصالحات وتواصوا بالحق وتواصوا بالصبر');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'بسم الله الرحمن الرحيم ويل لكل همزة لمزة'),
(6181, 104, 2, 'الذي جمع مالا وعدده'),
(6182, 104, 3, 'يحسب أن ماله أخلده'),
(6183, 104, 4, 'كلا ۖ لينبذن في الحطمة'),
(6184, 104, 5, 'وما أدراك ما الحطمة'),
(6185, 104, 6, 'نار الله الموقدة'),
(6186, 104, 7, 'التي تطلع على الأفئدة'),
(6187, 104, 8, 'إنها عليهم مؤصدة'),
(6188, 104, 9, 'في عمد ممددة');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'بسم الله الرحمن الرحيم ألم تر كيف فعل ربك بأصحاب الفيل'),
(6190, 105, 2, 'ألم يجعل كيدهم في تضليل'),
(6191, 105, 3, 'وأرسل عليهم طيرا أبابيل'),
(6192, 105, 4, 'ترميهم بحجارة من سجيل'),
(6193, 105, 5, 'فجعلهم كعصف مأكول');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'بسم الله الرحمن الرحيم لإيلاف قريش'),
(6195, 106, 2, 'إيلافهم رحلة الشتاء والصيف'),
(6196, 106, 3, 'فليعبدوا رب هذا البيت'),
(6197, 106, 4, 'الذي أطعمهم من جوع وآمنهم من خوف');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'بسم الله الرحمن الرحيم أرأيت الذي يكذب بالدين'),
(6199, 107, 2, 'فذلك الذي يدع اليتيم'),
(6200, 107, 3, 'ولا يحض على طعام المسكين'),
(6201, 107, 4, 'فويل للمصلين'),
(6202, 107, 5, 'الذين هم عن صلاتهم ساهون'),
(6203, 107, 6, 'الذين هم يراءون'),
(6204, 107, 7, 'ويمنعون الماعون');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'بسم الله الرحمن الرحيم إنا أعطيناك الكوثر'),
(6206, 108, 2, 'فصل لربك وانحر'),
(6207, 108, 3, 'إن شانئك هو الأبتر');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'بسم الله الرحمن الرحيم قل يا أيها الكافرون'),
(6209, 109, 2, 'لا أعبد ما تعبدون'),
(6210, 109, 3, 'ولا أنتم عابدون ما أعبد'),
(6211, 109, 4, 'ولا أنا عابد ما عبدتم'),
(6212, 109, 5, 'ولا أنتم عابدون ما أعبد'),
(6213, 109, 6, 'لكم دينكم ولي دين');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'بسم الله الرحمن الرحيم إذا جاء نصر الله والفتح'),
(6215, 110, 2, 'ورأيت الناس يدخلون في دين الله أفواجا'),
(6216, 110, 3, 'فسبح بحمد ربك واستغفره ۚ إنه كان توابا');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'بسم الله الرحمن الرحيم تبت يدا أبي لهب وتب'),
(6218, 111, 2, 'ما أغنى عنه ماله وما كسب'),
(6219, 111, 3, 'سيصلى نارا ذات لهب'),
(6220, 111, 4, 'وامرأته حمالة الحطب'),
(6221, 111, 5, 'في جيدها حبل من مسد');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'بسم الله الرحمن الرحيم قل هو الله أحد'),
(6223, 112, 2, 'الله الصمد'),
(6224, 112, 3, 'لم يلد ولم يولد'),
(6225, 112, 4, 'ولم يكن له كفوا أحد');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'بسم الله الرحمن الرحيم قل أعوذ برب الفلق'),
(6227, 113, 2, 'من شر ما خلق'),
(6228, 113, 3, 'ومن شر غاسق إذا وقب'),
(6229, 113, 4, 'ومن شر النفاثات في العقد'),
(6230, 113, 5, 'ومن شر حاسد إذا حسد');
INSERT INTO `quran_text` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'بسم الله الرحمن الرحيم قل أعوذ برب الناس'),
(6232, 114, 2, 'ملك الناس'),
(6233, 114, 3, 'إله الناس'),
(6234, 114, 4, 'من شر الوسواس الخناس'),
(6235, 114, 5, 'الذي يوسوس في صدور الناس'),
(6236, 114, 6, 'من الجنة والناس');
